INSERT INTO [t1446] VALUES (2069,'باب وقت الدفعة من عرفة خلاف سنة أهل الكفر والأوثان كانت في الجاهلية','4722','1810','0');
INSERT INTO [t1446] VALUES (2070,'باب تباهي الله أهل السماء بأهل عرفات','4725','1810','0');
INSERT INTO [t1446] VALUES (2071,'باب ذكر الدعاء على الموقف عشية عرفة إن ثبت الخبر، ولا أخال إلا أنه ليس في الخبر حكم، وإنما هو دعاء، فخرجنا هذا الخبر وإن لم يكن ثابتا من جهة النقل إذ هذا الدعاء مباح أن يدعو به على الموقف وغيره','4728','1810','0');
INSERT INTO [t1446] VALUES (2072,'باب ذكر العلة التي من أجلها سميت عرفة عرفة','4730','1810','0');
INSERT INTO [t1446] VALUES (2073,'باب صفة السير في الدفعة من عرفة، والأمر بالسكينة في السير بلفظ عام مراده خاص','4732','1810','0');
INSERT INTO [t1446] VALUES (2074,'باب ذكر البيان أن إيجاف الخيل والإبل والإيضاع في السير في الدفعة من عرفة ليس البر، والدليل على أن البر السكينة في السير بمثل اللفظة التي ذكرت أنها لفظ عام مراده خاص','4734','1810','0');
INSERT INTO [t1446] VALUES (2075,'باب ذكر الخبر الدال على أن اللفظة التي ذكرها في السكينة في السير في الدفعة من عرفة لفظ عام مراده خاص، والبيان أن النبي صلى الله عليه وسلم إنما كان يسير سير السكينة في الوقت الذي لم يجد فجوة إذ قد نص عند وجود الفجوة في السير عند الدفعة من عرفة، وفي هذا','4736','1810','0');
INSERT INTO [t1446] VALUES (2076,'باب ذكر الدعاء والذكر والتهليل في السير من عرفة إلى مزدلفة','4738','1810','0');
INSERT INTO [t1446] VALUES (2077,'باب إباحة النزول بين عرفات وجمع للحاجة تبدو للمرء','4740','1810','0');
INSERT INTO [t1446] VALUES (2078,'باب الجمع بين الصلاتين بين المغرب والعشاء بالمزدلفة','4742','1810','0');
INSERT INTO [t1446] VALUES (2079,'باب ترك التطوع بين الصلاتين إذا جمع بينهما بالمزدلفة مع البيان أن النبي صلى الله عليه وسلم صلى بالمزدلفة صلاة المسافر لا صلاة المقيم','4744','1810','0');
INSERT INTO [t1446] VALUES (2080,'باب الأذان للمغرب والإقامة للعشاء من غير أذان، إذا جمع بينهما بالمزدلفة خلاف قول من زعم أن الصلاتين إذا جمع بينهما في وقت الآخرة منهما جمع بينهما بإقامتين من غير أذان','4746','1810','0');
INSERT INTO [t1446] VALUES (2081,'باب إباحة الفصل بين المغرب والعشاء إذا جمع بينهما بفعل ليس من عمل الصلاة في خبر ابن عيينة عن إبراهيم بن عقبة: ثم حلوا رحالهم وأعنته عليه','4748','1810','0');
INSERT INTO [t1446] VALUES (2082,'باب إباحة الأكل بين الصلاتين إذا جمع بينهما بالمزدلفة إن ثبت الخبر، فإني لا أقف على سماع أبي إسحاق هذا الخبر من عبد الرحمن بن يزيد','4750','1810','0');
INSERT INTO [t1446] VALUES (2083,'باب البيتوتة بالمزدلفة ليلة النحر','4752','1810','0');
INSERT INTO [t1446] VALUES (2084,'باب التغليس بصلاة الفجر يوم النحر بالمزدلفة','4754','1810','0');
INSERT INTO [t1446] VALUES (2085,'باب الأذان والإقامة لصلاة الفجر بالمزدلفة ثنا محمد بن يحيى، ثنا عبد الله بن محمد النفيلي، ثنا حاتم بن إسماعيل، عن جعفر بن محمد، عن أبيه، عن جابر، فذكر الحديث وقال: فصلى الفجر حين تبين له الصبح يعني بالمزدلفة قال أبو بكر: قال لنا محمد بن يحيى قال','4756','1810','0');
INSERT INTO [t1446] VALUES (2086,'باب الوقوف عند المشعر الحرام والدعاء والذكر والتهليل والتمجيد والتعظيم لله في ذلك الموقف قرأت على أحمد بن أبي سريج الرازي أن عمرو بن مجمع أخبرهم، عن موسى بن عقبة، عن نافع، عن ابن عمر قال: كان رسول الله صلى الله عليه وسلم إذا استوت به راحلته عند مسجد','4757','1810','0');
INSERT INTO [t1446] VALUES (2087,'باب إباحة الوقوف حيث شاء الحاج من المزدلفة إذ جميع المزدلفة موقف','4758','1810','0');
INSERT INTO [t1446] VALUES (2088,'باب الدفع من المشعر الحرام ومخالفة أهل الشرك والأوثان في دفعهم منه','4761','1810','0');
INSERT INTO [t1446] VALUES (2089,'باب صفة السير في الإفاضة من جمع إلى منى بلفظ عام مراده خاص','4763','1810','0');
INSERT INTO [t1446] VALUES (2090,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما سار في الإفاضة ومن جمع إلى منى على السكينة خلا بطن وادي محسر؛ فإنه أوضع فيه، وفي هذا ما دل على أن الفضل إنما أراد: وعليه السكينة حتى أتى منى، خلا إيضاعه في وادي محسر على ما ترجمت الباب أنه لفظ عام','4765','1810','0');
INSERT INTO [t1446] VALUES (2091,'باب بدء الإيضاع كان في وادي محسر','4767','1810','0');
INSERT INTO [t1446] VALUES (2092,'باب ذكر الطريق الذي يسلك فيه من المشعر الحرام إلى الجمرة في خبر جعفر بن محمد عن أبيه، عن جابر، ثم سلك الطريق الوسطى التي تخرجك إلى الجمرة الكبرى حتى أتى الجمرة التي عند الشجرة ثناه محمد بن يحيى ثنا النفيلي ثنا حاتم ثنا جعفر','4769','1810','0');
INSERT INTO [t1446] VALUES (2093,'باب فضل العمل في عشر ذي الحجة','4770','1810','0');
INSERT INTO [t1446] VALUES (2094,'باب فضل يوم النحر','4772','1810','0');
INSERT INTO [t1446] VALUES (2095,'باب التقاط الحصى لرمي الجمار من المزدلفة، والبيان أن كسر الحجارة لحصى الجمار بدعة، لما فيه من إيذاء الناس وإتعاب أبدان من يتكلف كسر الحجارة توهما أنه سنة','4774','1810','0');
