INSERT INTO [t1446] VALUES (2102,'باب ذكر الموقف الذي يرمي منه الجمار','4793','1810','0');
INSERT INTO [t1446] VALUES (2103,'باب استقبال الجمرة عند رميها والوقوف عن يسار القبلة','4795','1810','0');
INSERT INTO [t1446] VALUES (2104,'باب التكبير مع كل حصاة يرميها للجمار','4797','1810','0');
INSERT INTO [t1446] VALUES (2105,'باب الذكر عند رمي الجمار','4799','1810','0');
INSERT INTO [t1446] VALUES (2106,'باب الرخصة للنساء والضعفاء الذين رخص لهم في الإفاضة من جمع بليل في رمي الجمار قبل طلوع الشمس','4801','1810','0');
INSERT INTO [t1446] VALUES (2107,'باب الرخصة للنساء اللواتي رخص لهن في الإفاضة من جمع بليل في رمي الجمار قبل الفجر','4803','1810','0');
INSERT INTO [t1446] VALUES (2108,'باب قطع التلبية إذا رمى الحاج جمرة العقبة يوم النحر','4805','1810','0');
