INSERT INTO [t1446] VALUES (2114,'باب نحر البدن قياما معقولة ضد قول مذهب من كره ذلك وجهل السنة وسمى السنة بدعة بجهله بالسنة','4818','1810','0');
INSERT INTO [t1446] VALUES (2115,'باب التسمية والتكبير عند الذبح والنحر','4821','1810','0');
INSERT INTO [t1446] VALUES (2116,'باب إباحة الهدي من الذكران والإناث جميعا','4824','1810','0');
INSERT INTO [t1446] VALUES (2117,'باب استحباب إهداء ما قد غنم من أموال أهل الشرك والأوثان أهل الحرب منه مغايظة لهم','4826','1810','0');
INSERT INTO [t1446] VALUES (2118,'باب استحباب توجيهه الذبيحة للقبلة، والدعاء عند الذبح','4828','1810','0');
INSERT INTO [t1446] VALUES (2119,'باب إباحة اشتراك النفر في البدنة والبقرة الواحدة، وإن كان من يشترك في البقرة الواحدة أو البدنة الواحدة من قبائل شتى ليسوا من أهل بيت واحد، مع الدليل أن سبع بدنة وسبع بقرة تقوم مقام شاة في الهدي','4830','1810','0');
INSERT INTO [t1446] VALUES (2120,'باب إباحة اشتراك سبعة من المتمتعين في البدنة الواحدة والبقرة الواحدة، والدليل على أن سبع بدنة وسبع بقرة مما استيسر من الهدي، إذ الله عز وجل أوجب على المتمتع ما استيسر من الهدي إذا وجده','4833','1810','0');
INSERT INTO [t1446] VALUES (2121,'باب اشتراك النساء المتمتعات في البقرة الواحدة','4835','1810','0');
INSERT INTO [t1446] VALUES (2122,'باب إجازة الذبح والنحر عن المتمتعة بغير أمرها وعلمها','4837','1810','0');
INSERT INTO [t1446] VALUES (2123,'باب ذكر الدليل على أن اسم الضحية قد يقع على الهدي الواجب إذ نساء النبي صلى الله عليه وسلم في حجته كن متمتعات خلا عائشة التي صارت قارنة لإدخالها الحج على العمرة لما لم يمكنها الطواف والسعي لعلة الحيضة التي حاضت قبل أن تطوف وتسعى لعمرتها','4839','1810','0');
INSERT INTO [t1446] VALUES (2124,'باب ذكر الدليل على أن لا حظر في إخبار جابر: " نحرنا مع رسول الله صلى الله عليه وسلم البدنة عن سبعة " أن لا تجزئ البدنة عن أكثر من سبعة، وهذا من الجنس الذي كنت أعلمت في غير موضع من كتبنا أن العرب قد تذكر عدد الشيء لا تريد نفيا لما زاد عن ذلك العدد','4841','1810','0');
INSERT INTO [t1446] VALUES (2125,'باب استحباب المغالاة بثمن الهدي وكرائمه إن كان شهم بن الجارود ممن يجوز الاحتجاج بخبره وهذا من الجنس الذي قال المطلبي في عقب خبر أبي ذر عن النبي صلى الله عليه وسلم لما سئل أي الرقاب أفضل قال: " أغلاها ثمنا وأنفسها عند أهلها "، فقال في عقب هذا الخبر،','4845','1810','0');
INSERT INTO [t1446] VALUES (2126,'باب ذكر العيوب التي تكون في الأنعام فلا تجزئ هديا ولا ضحايا إذا كان بها بعض تلك العيوب','4847','1810','0');
INSERT INTO [t1446] VALUES (2127,'باب الزجر عن ذبح العضباء في الهدي والأضاحي زجر اختيار، أن صحيح القرن والأذن أفضل من العضباء، لا أن العضباء غير مجزية، إذ النبي صلى الله عليه وسلم لما أعلم أن أربعا لا تجزئ، دلهم بهذا القول أن ما سوى ذلك الأربع جائز','4849','1810','0');
INSERT INTO [t1446] VALUES (2128,'باب النهي عن ذبح ذات النقص في العيون والآذان في الهدي والضحايا نهي ندب وإرشاد؛ إذ صحيح العينين والأذنين أفضل لا أن النقص إذا لم يكن عورا بينا غير مجزئ، ولا أن ناقص الأذنين غير مجزئ','4851','1810','0');
INSERT INTO [t1446] VALUES (2129,'باب الرخصة في ذبح الجذعة من الضأن في الهدي والضحايا بلفظ مجمل غير مفسر','4854','1810','0');
INSERT INTO [t1446] VALUES (2130,'باب الرخصة في اقتطاع لحوم الهدي بإذن صاحبها','4856','1810','0');
INSERT INTO [t1446] VALUES (2131,'باب الدليل على أن الجذعة إنما تجزئ عند الإعسار من المسن','4858','1810','0');
INSERT INTO [t1446] VALUES (2132,'باب الصدقة بلحوم الهدي، وجلودها، وجلال البدن، بذكر خبر مجمل غير مفسر','4860','1810','0');
INSERT INTO [t1446] VALUES (2133,'باب قسم لحوم الهدي وجلوده وجلاله في المساكين، والدليل على أن خبر ابن عيينة مجمل غير مفسر، وأن النبي صلى الله عليه وسلم إنما أمر بقسم لحوم بدنه وجلودها وأجلتها على المساكين دون الأغنياء، والدليل على أن اسم الكل قد يقع على البعض','4862','1810','0');
INSERT INTO [t1446] VALUES (2134,'باب ذكر الدليل على أن اسم الكل قد يقع على البعض، والدليل على أن علي بن أبي طالب إنما أراد بقوله: أمرني أن أقسم بدنه كلها، أي خلا ما أمر من كل بدنه ببضعة فجعلت في قدر فحسيا من المرق وأكلا من اللحم قال أبو بكر: خبر جابر بن عبد الله أمر النبي صلى الله','4864','1810','0');
INSERT INTO [t1446] VALUES (2135,'باب النهي عن إعطاء الجازر أجره من الهدي بذكر خبر مجمل غير مفسر','4865','1810','0');
INSERT INTO [t1446] VALUES (2136,'باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها، والدليل على أن النبي صلى الله عليه وسلم إنما زجر عن إعطاء الجازر من لحوم هديه على جزارتها شيئا لا أن يتصدق من لحومها على الجازر، لو كان الجازر مسكينا','4867','1810','0');
INSERT INTO [t1446] VALUES (2137,'باب الأكل من لحم الهدي إذا كان تطوعا','4869','1810','0');
INSERT INTO [t1446] VALUES (2138,'باب الهدي يضل فينحر مكانه آخر، ثم يوجد الأول','4871','1810','0');
INSERT INTO [t1446] VALUES (2139,'باب صيام المتمتع إذا لم يجد الهدي','4873','1810','0');
INSERT INTO [t1446] VALUES (2140,'باب حلق الرأس بعد الفراغ من النحر أو الذبح، واستحباب التيامن في الحلق، مع الدليل على أن شعر بني آدم ليس بنجس بعد الحلق أو التقصير','4875','1810','0');
INSERT INTO [t1446] VALUES (2141,'باب فضل الحلق في الحج والعمرة واختيار الحلق على التقصير وإن كان التقصير جائزا','4877','1810','0');
INSERT INTO [t1446] VALUES (2142,'باب تسمية من حلق النبي صلى الله عليه وسلم في حجته','4879','1810','0');
INSERT INTO [t1446] VALUES (2143,'باب استحباب تقليم الأظفار مع حلق الرأس، مع الدليل على أن الأظافر إذا قصت لم يكن حكمها حكم الميتة، ولا كانت نجسا كما توهم بعض أهل العلم أن ما قطع من الحي فهو ميت، وخبر أبي واقد الليثي إنما قال النبي صلى الله عليه وسلم: " ما قطع من البهيمة وهي حية فهو','4881','1810','0');
INSERT INTO [t1446] VALUES (2144,'باب إباحة التطيب يوم النحر بعد الحلق وقبل زيارة البيت، ضد قول من زعم أن التطيب محظور حتى يزور البيت','4883','1810','0');
INSERT INTO [t1446] VALUES (2145,'باب إباحة التطيب يوم النحر قبل الزيارة بالطيب الذي فيه مسك قال أبو بكر: خبر منصور بن زاذان عن عبد الرحمن بن القاسم، قد أمليته في أول الكتاب باب الطيب عند الإحرام','4886','1810','0');
INSERT INTO [t1446] VALUES (2146,'باب الرخصة للحائض أن تنسك المناسك كلها في حيضها خلا الطواف بالبيت والصلاة','4887','1810','0');
INSERT INTO [t1446] VALUES (2147,'باب الرخصة في الاصطياد وجميع ما حرم على المحرم بعد رمي الجمرة يوم النحر قبل زيارة البيت إن ثبتت هذه اللفظة في خبر عمرة، عن عائشة، عن النبي صلى الله عليه وسلم، وإن لم تثبت هذه اللفظة عن النبي صلى الله عليه وسلم فخبر عائشة في تطييبها النبي صلى الله عليه','4889','1810','0');
INSERT INTO [t1446] VALUES (2148,'باب ذكر الدليل على أن التطيب بعد رمي الجمار والنحر والذبح والحلاق إنما هو مباح عند بعض العلماء قبل زيارة البيت لمن قد طاف بالبيت قبل الوقوف بعرفة دون من لم يطف بالبيت قبل الوقوف بعرفة','4893','1810','0');
INSERT INTO [t1446] VALUES (2149,'باب استحباب طواف الزيارة يوم النحر استنانا بالنبي صلى الله عليه وسلم ومبادرة بقضاء الواجب عن الطواف الذي به يتم حج الحاج خوف أن يعرض للمرء ما لا يمكنه طواف الزيارة معه، وإن كان تأخير الإفاضة عن يوم النحر جائزا','4895','1810','0');
INSERT INTO [t1446] VALUES (2150,'باب ذكر الدليل على أن الوطء يحل بعد ركعتي طواف الزيارة، وإن كان الطائف بمكة قبل أن يرجع إلى منى','4897','1810','0');
INSERT INTO [t1446] VALUES (2151,'باب ترك الرمل في طواف الزيارة للقارن، وحكم المفرد في هذا كحكم القارن','4899','1810','0');
INSERT INTO [t1446] VALUES (2152,'باب استحباب الشرب من ماء زمزم بعد الفراغ من طواف الزيارة','4901','1810','0');
INSERT INTO [t1446] VALUES (2153,'باب استحباب الاستقاء من ماء زمزم إذ النبي صلى الله عليه وسلم قد أعلم أنه عمل صالح، وأعلم أن لولا أن يغلب المستقي منها على الاستقاء لنزع معهم','4904','1810','0');
INSERT INTO [t1446] VALUES (2154,'باب استحباب الشرب من نبيذ السقاية إذا لم يكن النبيذ مسكرا','4906','1810','0');
INSERT INTO [t1446] VALUES (2155,'باب السعي بين الصفا والمروة مع طواف الزيارة للمتمتع','4908','1810','0');
INSERT INTO [t1446] VALUES (2156,'باب ترك السعي بين الصفا والمروة مع طواف الزيارة للمفرد والقارن قال أبو بكر: خبر يونس بن عبد الأعلى، عن ابن وهب، عن مالك في الباب قبل هذا، وقال فيه: وأما الذين جمعوا الحج والعمرة فإنهم طافوا طوافا واحدا','4910','1810','0');
INSERT INTO [t1446] VALUES (2157,'باب ذكر من قدم نسكا قبل نسك جاهلا بذكر خبر مختصر غير متقص والدليل على أن لا فدية له','4911','1810','0');
INSERT INTO [t1446] VALUES (2158,'باب خطبة الإمام بمنى يوم النحر بعد الظهر','4914','1810','0');
INSERT INTO [t1446] VALUES (2159,'باب خطبة الإمام على الراحلة','4916','1810','0');
INSERT INTO [t1446] VALUES (2160,'باب الرخصة في الجماع يوم النحر بعد الزيارة','4918','1810','0');
INSERT INTO [t1446] VALUES (2161,'باب ذكر الناسي بعض نسكه يوم النحر ثم يذكره','4920','1810','0');
INSERT INTO [t1446] VALUES (2162,'باب البيتوتة بمنى ليالي أيام التشريق','4922','1810','0');
INSERT INTO [t1446] VALUES (2163,'باب الرخصة في البيتوتة لآل العباس بمكة أيام منى من أجل سقايتهم ليقوموا بإسقاء الناس منها','4924','1810','0');
INSERT INTO [t1446] VALUES (2164,'باب النهي عن الطيب واللباس إذا أمسى الحاج يوم النحر قبل أن يفيض وكل ما زجر الحاج عنه قبل رمي الجمرة يوم النحر','4926','1810','0');
INSERT INTO [t1446] VALUES (2165,'باب النهي عن صوم يوم الفطر ويوم النحر','4928','1810','0');
INSERT INTO [t1446] VALUES (2166,'باب النهي عن صيام أيام التشريق بدلالة لا بتصريح','4930','1810','0');
INSERT INTO [t1446] VALUES (2167,'باب الزجر عن صوم أيام التشريق بتصريح لا بكناية ولا بدلالة من غير تصريح','4932','1810','0');
INSERT INTO [t1446] VALUES (2168,'باب سنة الصلاة بمنى للحاج من غير أهل مكة وغير من قد أقام بمكة إقامة يجب عليه إتمام الصلاة بذكر خبر غلط في الاحتجاج به بعض أهل العلم ممن زعم أن سنة الصلاة بمنى لأهل الآفاق وأهل مكة جميعا ركعتين كصلاة المسافر سواء','4934','1810','0');
INSERT INTO [t1446] VALUES (2169,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما صلى بها ركعتين؛ لأنه كان مسافرا غير مقيم، إذ هو صلى الله عليه وسلم كان منأهل المدينة، وإنما قدم مكة حاجا لم يقم بها إقامة يجب عليه إتمام الصلاة قال أبو بكر: خبر يحيى بن أبي إسحاق عن أنس أن النبي صلى','4937','1810','0');
INSERT INTO [t1446] VALUES (2170,'باب فضل يوم القر وهو أول أيام التشريق','4938','1810','0');
INSERT INTO [t1446] VALUES (2171,'باب بدء رمي النبي الجمار، والعلة التي رماها بدءا قبل عود','4940','1810','0');
INSERT INTO [t1446] VALUES (2172,'باب وقت رمي الجمار أيام التشريق','4942','1810','0');
INSERT INTO [t1446] VALUES (2173,'باب ذكر البيان أن رمي الجمار إنما أراد لإقامة ذكر الله لا للرمي فقط','4945','1810','0');
INSERT INTO [t1446] VALUES (2174,'باب التكبير مع كل حصاة يرمي بها رامي الجمار والوقوف عند الجمرة الأولى والثانية مع تطويل القيام والتضرع، وترك الوقوف عند جمرة العقبة بعد رميها أيام منى','4947','1810','0');
INSERT INTO [t1446] VALUES (2175,'باب الوقوف عند الجمرة الأولى والثانية بعد رميها والدليل على أن الوقوف بعد رمي الأولى منها أمامها لا خلفها، ولا عن يمينها، ولا عن شمالها، والوقوف عند الثانية ذات اليسار مما يلي الوادي مستقبل القبلة في الوقفين جميعا، ورفع اليدين في الوقفين جميعا','4949','1810','0');
INSERT INTO [t1446] VALUES (2176,'باب خطبة الإمام أوسط أيام التشريق','4951','1810','0');
INSERT INTO [t1446] VALUES (2177,'باب ذكر تعليم الإمام في خطبته يوم النفر الأول كيف ينفرون، وكيف يرمون، وتعليمهم باقي مناسكهم','4953','1810','0');
INSERT INTO [t1446] VALUES (2178,'باب الرخصة للرعاء في رمي الجمار بالليل','4955','1810','0');
INSERT INTO [t1446] VALUES (2179,'باب الرخصة للرعاة أن يرموا يوما ويدعوا يوما','4957','1810','0');
INSERT INTO [t1446] VALUES (2180,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما رخص للرعاء في ترك رمي الجمار يوما ويرعوا يوما في يومين من أيام التشريق، اليوم الأول يرعوا فيه، ويرموا يوم الثاني، ثم يرموا يوم النفر، لا أنه رخص لهم في ترك رمي الجمار يوم النحر، ولا يوم النفر الآخر','4960','1810','0');
INSERT INTO [t1446] VALUES (2181,'باب وقت النفر من منى آخر أيام التشريق','4962','1810','0');
INSERT INTO [t1446] VALUES (2182,'باب استحباب النزول بالمحصب استنانا بالنبي صلى الله عليه وسلم','4964','1810','0');
INSERT INTO [t1446] VALUES (2183,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم قد كان أعلمهم وهو بمنى أن ينزل بالأبطح، وأن أبا رافع أراد بقوله: أنا ضربت قبة رسول الله صلى الله عليه وسلم، ولم يأمرني، فجاء فنزل أي ولم يأمرني بضرب القبة في ذلك الموضع، لا أنه أراد أن النبي صلى الله','4967','1810','0');
INSERT INTO [t1446] VALUES (2184,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما نزل بالأبطح ليكون أسمع لخروجه، وإن كان قد أعلمهم وهو بمنى أنه نازل به مع الدليل على أن نزوله ليس من سنن الحج الذي يكون تاركه عاصيا، أو يوجب ترك نزوله هديا','4971','1810','0');
INSERT INTO [t1446] VALUES (2185,'باب ذكر الدليل على أن الاسم قد ينفى عن الشيء إذا لم يكن واجبا، وإن كان الفعل مباحا','4974','1810','0');
INSERT INTO [t1446] VALUES (2186,'باب استحباب النزول بالمحصب، وإن لم يكن ذلك واجبا إذ الخلفاء الراشدون المهديون الذين أمر النبي صلى الله عليه وسلم بالعض بالنواجذ على سنته وسنتهم قد اقتدوا بالنبي صلى الله عليه وسلم بالنزول به','4976','1810','0');
INSERT INTO [t1446] VALUES (2187,'باب استحباب الصلاة بالمحصب إذا نزله المرء قال أبو بكر: خبر الثوري عن عبد العزيز بن رفيع، عن أنس من هذا الباب، قد أمليته قبل','4978','1810','0');
INSERT INTO [t1446] VALUES (2188,'باب ذكر البيان أن رسول الله صلى الله عليه وسلم قصر الصلاة بالأبطح بعدما نفر من منى، ضد قول من يحكي لنا عنه من أهل عصرنا أن الحاج إذا قفل راجعا إلى بلده عليه إتمام الصلاة','4981','1810','0');
INSERT INTO [t1446] VALUES (2189,'باب استحباب الإدلاج بالارتحال من الحصبة، اقتداء بفعل المصطفى عليه السلام','4984','1810','0');
INSERT INTO [t1446] VALUES (2190,'باب الأمر بطواف الوداع بلفظ عام مراده خاص','4987','1810','0');
INSERT INTO [t1446] VALUES (2191,'باب الدليل على أن اللفظة التي ذكرتها في خبر ابن عباس لفظ عام مراده خاص والدليل على أن النبي صلى الله عليه وسلم إنما أراد بقوله: لا ينفرن أحد حتى يكون آخر عهده بالبيت، خلا الحائض، بذكر لفظة عام مرادها خاص في ذكر الحيض','4990','1810','0');
INSERT INTO [t1446] VALUES (2192,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما رخص للحيض في النفر بلا وداع إذا كن قد أفضن قبل ذلك، ثم حضن','4992','1810','0');
INSERT INTO [t1446] VALUES (2193,'باب استحباب دخول الكعبة والذكر والدعاء فيها','4994','1810','0');
INSERT INTO [t1446] VALUES (2194,'باب وضع الوجه والجبين على ما استقبل من الكعبة عند دخولها والذكر والاستغفار','4996','1810','0');
INSERT INTO [t1446] VALUES (2195,'باب التكبير والتحميد والتهليل والمسألة والاستغفار عند كل ركن من أركان الكعبة','4999','1810','0');
INSERT INTO [t1446] VALUES (2196,'باب استحباب السجود بين العمودين عند دخول الكعبة، والجلوس بعد السجدة والدعاء','5001','1810','0');
INSERT INTO [t1446] VALUES (2197,'باب ذكر البيان أن النبي صلى الله عليه وسلم قد صلى في البيت وهذا من الجنس الذي أعلمت في غير موضع من كتبنا أن الخبر الذي يجب قبوله هو خبر من يخبر برؤية الشيء وسماعه وكونه، لا من ينفي الشيء ويدفعه، والفضل بن عباس في قوله: ولم يصل ناف لصلاة النبي صلى الله','5003','1810','0');
INSERT INTO [t1446] VALUES (2198,'باب ذكر المكان الذي صلى فيه النبي صلى الله عليه وسلم من الكعبة','5005','1810','0');
INSERT INTO [t1446] VALUES (2199,'باب ذكر القدر الذي جعل النبي صلى الله عليه وسلم بين مقامه الذي صلى فيه بين الكعبة وبين الجدار','5008','1810','0');
INSERT INTO [t1446] VALUES (2200,'باب الخشوع في الكعبة إذا دخلها المرء، والنظر إلى موضع سجوده إلى الخروج منها','5010','1810','0');
INSERT INTO [t1446] VALUES (2201,'باب استحباب دخول الكعبة إذ دخولها دخولا في حسنة، وخروجا من سيئة مغفورا للداخل','5012','1810','0');
INSERT INTO [t1446] VALUES (2202,'باب ذكر الدليل على أن دخول الكعبة ليس بواجب إذ النبي صلى الله عليه وسلم قد أعلم بعد دخوله إياها أنه ود أن لم يكن دخلها مخافة إتعاب أمته بعده، وهذا كتركه صلى الله عليه وسلم بعض التطوع والذي كان يحب أن يفعله لإرادة التخفيف على أمته صلى الله عليه وسلم','5014','1810','0');
INSERT INTO [t1446] VALUES (2203,'باب استحباب الصلاة عند باب الكعبة بعد الخروج منها','5016','1810','0');
INSERT INTO [t1446] VALUES (2204,'باب ذكر الموضع الذي صلى فيه النبي صلى الله عليه وسلم الركعتين بعد خروجه من الكعبة','5018','1810','0');
INSERT INTO [t1446] VALUES (2205,'باب التزام البيت عند الخروج من الكعبة إن كان يزيد بن أبي زياد من الشرط الذي اشترطنا في أول الكتاب','5020','1810','0');
INSERT INTO [t1446] VALUES (2206,'باب استحباب الصلاة في الحجر إذا لم يمكن دخول الكعبة إذ بعض الحجر من البيت، بذكر خبر لفظه عام مراده خاص، أنا خائف أن يسمع بهذا الخبر الذي ذكرت أن لفظه لفظ عام مراده خاص بعض الناس فيتوهم أن جميع الحجر من الكعبة لا بعضه','5022','1810','0');
INSERT INTO [t1446] VALUES (2207,'باب ذكر البيان أن بعض الحجر من البيت، لا جميعه والدليل على أن النبي صلى الله عليه وسلم إنما أراد بقوله: وأخرجوا الحجر من البيت، بعضه لا جميعه، وهذا من الجنس الذي أعلمت في غير موضع من كتبنا أن الاسم باسم المعرفة بالألف واللام قد يقع على بعض الشيء','5025','1810','0');
INSERT INTO [t1446] VALUES (2208,'باب إباحة العمرة في ذي الحجة بعد مضي أيام التشريق','5030','1810','0');
INSERT INTO [t1446] VALUES (2209,'باب العمرة في ذي الحجة من التنعيم لمن قد حج ذلك العام ضد قول من زعم أن العمرة غير جائزة إلا من المواقيت التي وقت النبي صلى الله عليه وسلم حين ذكر المواقيت، فقال: يهل أهل المدينة من ذي الحليفة، الأخبار بتمامها','5032','1810','0');
INSERT INTO [t1446] VALUES (2210,'باب ذكر الدليل على أن العمرة من الميقات أفضل منها من التنعيم إذ هي أكثر نصبا وأفضل نفقة، وما كان أكثر نصبا وأفضل نفقة فالأجر على قدر النصب والنفقة','5035','1810','0');
INSERT INTO [t1446] VALUES (2211,'باب إسقاط الهدي عن المعتمر بعد مضي أيام التشريق وإن كان قد حج من عامه ذلك','5037','1810','0');
INSERT INTO [t1446] VALUES (2212,'باب إباحة الحج عمن لا يستطيع الحج عن نفسه من الكبر والدليل على أن الله عز وجل ولى نبيه بيان ما أنزل عليه من الوحي خاصا وعاما، فبين النبي صلى الله عليه وسلم أن الله لم يرد بقوله: وأن ليس للإنسان إلا ما سعى جميع الأعمال، وأن الله إنما أراد بعض السعي لا','5040','1810','0');
INSERT INTO [t1446] VALUES (2213,'باب ذكر الدليل على أن الشيخ الكبير إذا استفاد مالا بعد كبر السن وهو غني، أو استفاد مالا بعد الإسلام كان فرض الحج واجبا عليه وإن كان غير مستطيع أن يحج بنفسه، والدليل على أن الاستطاعة كما قاله مطلبينا رحمه الله استطاعتان إحداهما ببدنه مع ملك ماله، يمكنه','5042','1810','0');
INSERT INTO [t1446] VALUES (2214,'باب حج المرأة عن الرجل','5045','1810','0');
INSERT INTO [t1446] VALUES (2215,'باب الحج عن الميت بذكر خبر مجمل غير مفسر على أصلنا','5047','1810','0');
INSERT INTO [t1446] VALUES (2216,'باب الحج عمن يجب عليه الحج بالإسلام، أو ملك المال، أو هما وهو غير مستطيع للحج ببدنه من الكبر، والفرق بين العاجز عن الحج ببدنه لكبر السن وبين العاجز عن الحج لمرض قد يرجى له البرء إذ العاجز لكبر السن لا يحدث له شباب وقوة بعد، والمريض قد يصح من مرضه','5050','1810','0');
INSERT INTO [t1446] VALUES (2217,'باب حج الرجل عن المرأة التي لا تستطيع الحج من الكبر بمثل اللفظة التي ذكرت أنها مجملة غير مفسرة','5052','1810','0');
INSERT INTO [t1446] VALUES (2218,'باب النهي عن أن يحج عن الميت من لم يحج عن نفسه، والدليل على أن الأخبار التي ذكرت في أنها مجملة غير مفسرة على ما ذكرت، إذ ليس في تلك الأخبار أن النبي صلى الله عليه وسلم سأل من أمره أن يحج عن غيرها هل حج عن نفسه أم لا؟ هذا الخبر دال على أن النبي صلى','5055','1810','0');
INSERT INTO [t1446] VALUES (2219,'باب العمرة عن الذي لا يستطيع العمرة من الكبر','5057','1810','0');
INSERT INTO [t1446] VALUES (2220,'باب النذر بالحج ثم يحدث الموت قبل وفائه، والأمر بقضائه والدليل على أنه من جميع المال لتشبيه النبي صلى الله عليه وسلم نذر الحج بالدين','5059','1810','0');
INSERT INTO [t1446] VALUES (2221,'باب الدليل على أن الحج الواجب من جميع المال لا من الثلث','5061','1810','0');
INSERT INTO [t1446] VALUES (2222,'باب النذر بالحج ماشيا فيعجز الناذر عن المشي بذكر خبر مختصر غير متقصى','5063','1810','0');
INSERT INTO [t1446] VALUES (2223,'باب هدي الناذر بالحج ماشيا، فيعجز عن المشي والدليل على الخبرين اللذين ذكرتهما في الباب قبل، مختصرين على ما ذكرت','5066','1810','0');
INSERT INTO [t1446] VALUES (2224,'باب اليمين بالمشي إلى الكعبة فيعجز الحالف عن المشي','5068','1810','0');
INSERT INTO [t1446] VALUES (2225,'باب ذكر إسقاط فرض الحج عن الصبي قبل البلوغ، وعن المجنون حتى يفيق','5071','1810','0');
INSERT INTO [t1446] VALUES (2226,'باب ذكر حج الصبيان قبل البلوغ على غير الوجوب والدليل على أن قول النبي صلى الله عليه وسلم: " رفع القلم عن ثلاث "، أراد القلم مما يكون إثما ووزرا على البالغ إذا ارتكبه، لا أن القلم مرفوع عن كتابة الحسنات للصبي إذا عملها','5073','1810','0');
INSERT INTO [t1446] VALUES (2227,'باب الصبي يحج قبل البلوغ ثم يبلغ','5075','1810','0');
INSERT INTO [t1446] VALUES (2228,'باب حج الأكرياء، والدليل على أن أكر المرء نفسه في العمل طلق مباح، إذ هو من ابتغاء فضل الله لأخذه الأجرة على ذلك','5077','1810','0');
INSERT INTO [t1446] VALUES (2229,'باب حج الأجراء والدليل على أن الأجير إذا أجر نفسه بكذا، وحج عن نفسه كانت له الأجرة على مستأجره، وأداء الفرض عن نفسه جائز','5079','1810','0');
INSERT INTO [t1446] VALUES (2230,'باب إباحة التجارة في الحج والدليل على أن الاشتغال بما أباح الله من طلب المال من حله أيام الموسم في غير الأوقات الذي يشتغل المرء عن أداء المناسك لا ينقص أجر الحاج، ولا يبطل الحج، ولا يوجب عليه هديا ولا صوما ولا صدقة','5081','1810','0');
INSERT INTO [t1446] VALUES (2231,'باب ذكر عدد حجج النبي صلى الله عليه وسلم والدليل على ضد ما توهمه العامة أن النبي صلى الله عليه وسلم لم يحج إلا حجة واحدة والنبي صلى الله عليه وسلم إنما حج حجة واحدة بعد هجرته إلى المدينة فأما ما قبل الهجرة فقد حج النبي صلى الله عليه وسلم غير تلك الحجة','5084','1810','0');
INSERT INTO [t1446] VALUES (2232,'باب ذكر الدليل على صحة هذا المتن والبيان أن النبي صلى الله عليه وسلم قد حج قبل هجرته إلى المدينة لا كما من طعن في هذا الخبر، وادعى أن هذا الخبر لم يروه غير زيد بن الحباب','5086','1810','0');
INSERT INTO [t1446] VALUES (2233,'باب الرخصة في دخول مكة بغير إحرام عند العلم بحدث','5091','1810','0');
INSERT INTO [t1446] VALUES (2234,'جماع أبواب ذكر العمرة وشرائعها وسننها وفضائلها','5094','1810','0');
INSERT INTO [t1446] VALUES (2235,'باب ذكر البيان أن العمرة فرض وأنها من الإسلام كالحج سواء إلا أنها تطوع غير فريضة على ما قال بعض العلماء','5095','1810','0');
INSERT INTO [t1446] VALUES (2236,'باب ذكر عدد عمر رسول الله صلى الله عليه وسلم','5099','1810','0');
INSERT INTO [t1446] VALUES (2237,'باب فضل العمرة وتكفير الذنوب التي يرتكبها المعتمر بين العمرتين','5102','1810','0');
INSERT INTO [t1446] VALUES (2238,'باب الدليل على أن جهاد النساء الحج والعمرة، وفي الخبر - علمي - دلالة على أن العمرة واجبة كالحج، إذ النبي صلى الله عليه وسلم أعلم أن عليهن العمرة كما أن عليهن الحج','5105','1810','0');
INSERT INTO [t1446] VALUES (2239,'باب الرخصة في العمرة على الدواب المحبسة في سبيل الله','5107','1810','0');
INSERT INTO [t1446] VALUES (2240,'باب الرخصة للحاج بعد الفراغ من الحج والعمرة والإحرام بهما من أي الحل شاء','5109','1810','0');
INSERT INTO [t1446] VALUES (2241,'باب فضل العمرة في رمضان، والدليل على أنها تعدل بحجة، مع الدليل على أن الشيء قد يشبه بالشيء ويجعل عدله إذا أشبهه في بعض المعاني، لا في جميعه، إذ العمرة لو عدلت حجة في جميع أحكامها لقضي العمرة من الحج، ولكان المعتمر في رمضان إذا كان عليه حجة الإسلام','5111','1810','0');
INSERT INTO [t1446] VALUES (2242,'باب إباحة العمرة من الجعرانة','5113','1810','0');
INSERT INTO [t1446] VALUES (2243,'باب إباحة العمرة في أشهر الحج لمن لا يحج عامه ذلك، والرخصة له في الرجوع إلى وطنه بعد قضاء العمرة قبل أن يحج','5115','1810','0');
INSERT INTO [t1446] VALUES (2244,'باب إباحة العمرة قبل الحج والدليل على أن الفعلين من جنس، إذ أمر الله عز وجل بهما فبدأ بذكر أحدهما في الأمر قبل الآخر، أن جائز أن يبدأ المأمور بالفعلين بأحدهما في. . . . .','5117','1810','0');
