INSERT INTO [t145376] VALUES (1072,'(٦) مسألة في رجل مضى عليه زمن لم يصل فيه، ثم تاب ولم يتفرغ لقضاء ما فات، فهل يطالبه الله بذلك؟','1166','1002','0');
INSERT INTO [t145376] VALUES (1073,'- إذا كان ممن لا يعتقد وجوب الصلاة لا يجب عليه القضاء بعد التوبة','1167','1072','0');
INSERT INTO [t145376] VALUES (1074,'- إذا كان مؤمنا يعتقد وجوبها وتكاسل عنها بعض الأوقات يجب عليه القضاء','1168','1072','0');
