INSERT INTO [t145376] VALUES (1076,'- إذا عجز عن القضاء فلم يتفرغ حتى مات بعد التوبة فهذا مغفور له إن شاء الله','1169','1072','0');
INSERT INTO [t145376] VALUES (1077,'(٧) مسألة في رجل له عشرون سنة يشرب الخمر ولا يصلي إلا بعض الأعياد والجمع ولكنه يتصدق','1170','1002','0');
INSERT INTO [t145376] VALUES (1078,'- تارك الصلاة يجب أن يستتاب، فإن تاب وإلا عوقب','1170','1077','0');
INSERT INTO [t145376] VALUES (1079,'- إذا فعل شيئا من الخير فهذا لا يدفع عنه عقوبة ترك الصلاة','1171','1077','0');
INSERT INTO [t145376] VALUES (1080,'(٨) مسألة في رجل عنده زوجة لا تصلي، فهل يجب عليه أن يأمرها بالصلاة؟ وإذا لم تأتمر فهل يجوز له إبقاؤها زوجة أو يجب عليه أن يفارقها؟','1172','1002','0');
INSERT INTO [t145376] VALUES (1081,'- يجب عليه أن يأمرها بالصلاة ويحضها على ذلك','1172','1080','0');
INSERT INTO [t145376] VALUES (1082,'- إن أصرت على ترك الصلاة فعليه أن يطلقها','1172','1080','0');
INSERT INTO [t145376] VALUES (1083,'(٩) مسألة في رجل عمره سبعون سنة وهو مقيم في بلده مدة ثلاث سنين، ما رآه أحد صلى ولا زكى','1173','1002','0');
INSERT INTO [t145376] VALUES (1084,'- هذا يجب أن يستتاب، فإن تاب وإلا قتل','1173','1083','0');
INSERT INTO [t145376] VALUES (1085,'- إن لم يؤد الزكاة أخذت منه قهرا','1173','1083','0');
INSERT INTO [t145376] VALUES (1086,'(١٠) مسألة في البنت إذا بلغت ولم تصل، وإن قيل لها: صلي تقول: ما أنا كبيرة والمرأة الكبيرة إذا لم تصل فماذا يجب عليها؟','1175','1002','0');
INSERT INTO [t145376] VALUES (1087,'- من بلغ من الرجال والنساء فالصلاة فريضة عليه','1175','1086','0');
INSERT INTO [t145376] VALUES (1088,'- إذا بلغت المرأة ولم تقر بوجوب الصلاة عليها فهي كافرة','1175','1086','0');
INSERT INTO [t145376] VALUES (1089,'- إن كانت لا تقر بوجوبها لظنها أن الصلاة إنما تجب على العجوز، فهذا لا يحكم بكفرها حتى تعرف أنها واجبة عليها','1175','1086','0');
INSERT INTO [t145376] VALUES (1090,'- المرأة الكبيرة إذا لم تقر بوجوب الصلاة تستتاب، فإن تابت وإلا قتلت','1176','1086','0');
INSERT INTO [t145376] VALUES (1091,'(١١) مسألة في أقوام لم يكونون بالمسجد، فإذا حضرت الصلاة قاموا، فيدعون للصلاة فيأبون، فيقال لهم: من لا يصلي ما هو بمسلم، فيقولون: كل من نطق بالشهادتين مسلم','1177','1002','0');
INSERT INTO [t145376] VALUES (1092,'- من ترك الصلاة غير مقر بوجوبها عليه فهو كافر','1177','1091','0');
INSERT INTO [t145376] VALUES (1093,'- من لا يعرف الوجوب يعزف الوجوب، فإن أقر به وإلا قتل','1177','1091','0');
INSERT INTO [t145376] VALUES (1094,'- الصلاة واجبة على كل عاقل بالغ إلا الحائض والنفساء','1177','1091','0');
INSERT INTO [t145376] VALUES (1095,'- المجنون لا صلاة عليه حال جنونه، ولا قضاء عليه بعد الإفاقة','1178','1091','0');
INSERT INTO [t145376] VALUES (1096,'- إذا أقر بوجوب الصلاة وامتنع من فعلها يستتاب، فإن تاب وإلا قتل','1179','1091','0');
INSERT INTO [t145376] VALUES (1097,'(١٢) هل يجوز غيبة تارك الصلاة أم لا؟','1181','1002','0');
INSERT INTO [t145376] VALUES (1098,'- إذا قيل عنه إنه تارك الصلاة وكان تاركها فهذا جائز','1181','1097','0');
INSERT INTO [t145376] VALUES (1099,'- ينبغي أن يشاع ذلك عنه ويجهر حتى يصلي','1181','1097','0');
INSERT INTO [t145376] VALUES (1100,'(١٣) مسألة فيمن ترك الصلاة عامدا أو غير عامد ولم يزك، وقد قصد الحج، فهل يسقط هذا جميعه ومظالم العباد؟','1182','1002','0');
INSERT INTO [t145376] VALUES (1101,'- لا يسقط حقوق العباد وما وجب عليه من صلاة وزكاة وصيام وإن حج','1182','1100','0');
INSERT INTO [t145376] VALUES (1102,'(١٤) مسألة في رجل مات، وكان لا يزكي ولا يصلي إلا إن كان في رمضان، فهل يصلى على مثل هذا؟','1183','1002','0');
INSERT INTO [t145376] VALUES (1103,'- يستحب لأهل العلم والدين أن يدعوا الصلاة عليه عقوبة ونكالا لأمثاله','1183','1102','0');
INSERT INTO [t145376] VALUES (1104,'(١٥) مسألة في أقوام لم يصلوا ولم يصوموا، ومالهم حرام، ويأخذون أموال الناس، ويكرمون الجار والضيف','1184','1002','0');
INSERT INTO [t145376] VALUES (1105,'- يجب أن يؤمروا بإقامة الصلاة ويعاقبوا على تركها','1184','1104','0');
INSERT INTO [t145376] VALUES (1106,'(١٦) مسألة فيمن قال: صلاة الجماعة ليست بواجبة، وإنما كانت واجبة في زمن النبي - صلى الله عليه وسلم - ومن معه فقط، ولا يوجد اليوم منافق','1186','1002','0');
INSERT INTO [t145376] VALUES (1107,'- هذا القوم مخالف لأقوال الأئمة','1186','1106','0');
INSERT INTO [t145376] VALUES (1108,'- إطلاق النفاق على من تخلف عن الجماعة إذا كان بغير تأويل شرعي','1187','1106','0');
INSERT INTO [t145376] VALUES (1109,'- دلائل وجوب الجماعة ظاهرة بينة','1190','1106','0');
INSERT INTO [t145376] VALUES (1110,'- من قال: لا يوجد اليوم منافق، فهو مخطئ','1192','1106','0');
INSERT INTO [t145376] VALUES (1111,'- الكلام على لفظ الزنديق','1192','1106','0');
INSERT INTO [t145376] VALUES (1112,'- اسم النفاق قد يطلق على النفاق الأصغر','1193','1106','0');
INSERT INTO [t145376] VALUES (1113,'(١٧) مسألة في رجل له دكان بقرب المسجد، هل يجوز له أن يصلي منفردا، أو يؤخر الصلاة مع الجماعة ويصلي في البيت؟','1195','1002','0');
INSERT INTO [t145376] VALUES (1114,'- هو مأمور باتفاق المسلمين أن يصلي مع الجماعة','1195','1113','0');
INSERT INTO [t145376] VALUES (1115,'- الصلاة في الجماعة من أوكد ما شرعه الله ورسوله','1195','1113','0');
INSERT INTO [t145376] VALUES (1116,'- من صلى منفردا عن الجماعة يستحق العقوبة البليغة','1196','1113','0');
INSERT INTO [t145376] VALUES (1117,'- من يؤخر العشاء حتى يصليها بعد نصف الليل، لا يقر على ذلك بل يعاقب','1197','1113','0');
INSERT INTO [t145376] VALUES (1118,'(١٨) مسألة في مسلم تارك الصلاة ويصلي يوم الجمعة، هل يجب عليه اللعنة؟','1198','1002','0');
INSERT INTO [t145376] VALUES (1119,'- لعن تارك الصلاة على وجه العموم جائز، أما لعنة المعين فالأولى تركها','1198','1118','0');
INSERT INTO [t145376] VALUES (1120,'(١٩) مسألة في رجل يصوم ولا يصلي ويلعب بالنرد','1199','1002','0');
INSERT INTO [t145376] VALUES (1121,'- تاركهما يستحق العقوبة البليغة','1199','1120','0');
INSERT INTO [t145376] VALUES (1122,'- لعب النرد حرام باتفاق العلماء','1199','1120','0');
INSERT INTO [t145376] VALUES (1123,'(٢٠) مسألة فيمن عنده زوجة ما تصلي، هل تحرم عليه؟','1200','1002','0');
INSERT INTO [t145376] VALUES (1124,'- إقرار الزوجة أو غيرها ممن هو تحت طاعة الرجل على ترك الصلاة حرام','1200','1123','0');
INSERT INTO [t145376] VALUES (1125,'- المقر على ذلك آثم فاسق عاص','1200','1123','0');
INSERT INTO [t145376] VALUES (1126,'- تارك الصلاة شر من السارق والزاني باتفاق العلماء','1201','1123','0');
INSERT INTO [t145376] VALUES (1127,'- لا يحكم بانفساخ النكاح بمجرد ترك الصلاة','1202','1123','0');
INSERT INTO [t145376] VALUES (1128,'- لكن إذا دعيت إلى الصلاة وامتنعت انفسخ نكاحها عند بعض العلماء','1203','1123','0');
INSERT INTO [t145376] VALUES (1129,'(٢١) مسألة فيمن لا يصلي هل تجاب دعوته إذا دعا أحدا؟','1204','1002','0');
INSERT INTO [t145376] VALUES (1130,'- لا يسلم عليه ولا تجاب دعوته','1204','1129','0');
INSERT INTO [t145376] VALUES (1131,'(٢٢) مسألة في رجل ذكر له الصلاة فقال: قال الله (لا تقربوا الصلاة)، فقيل له: اقرأ بقية الآية، فقال: كلمة تكفي العاقل، وهو يضحك','1204','1002','0');
INSERT INTO [t145376] VALUES (1132,'- هذا مستهزئ بآيات الله، يستتاب، فإن تاب وإلا قتل','1204','1131','0');
INSERT INTO [t145376] VALUES (1133,'(٢٣) مسألة في الميت وخروجه على زمان رسول الله - صلى الله عليه وسلم -، والمرور به بالمقربين، وخروج النساء صحبة الميت، وخروجهم إلى القبر اليوم الثالث، والقراءة على القبر ثلاثة أيام، وغير ذلك','1205','1002','0');
INSERT INTO [t145376] VALUES (1134,'- السنة في الخروج بالميت إلى المقبرة','1205','1133','0');
INSERT INTO [t145376] VALUES (1135,'- خرج النساء في الجنائز منهي عنه','1206','1133','0');
INSERT INTO [t145376] VALUES (1136,'- النهي عن النياحة','1207','1133','0');
INSERT INTO [t145376] VALUES (1137,'- عمل العرس للميت من أعظم البدع، وكذلك الضرب بالدفوف في الجنائز','1208','1133','0');
INSERT INTO [t145376] VALUES (1138,'- بدع أخرى في الجنائز','1209','1133','0');
INSERT INTO [t145376] VALUES (1139,'- الصدقة عن الميت تنفعه، ولكن إخراجها مع الجنازة بدعة مكروهة','1210','1133','0');
INSERT INTO [t145376] VALUES (1140,'(٢٤) مسألة في قوم يقرءون قدام الموتى على طريقة الغناء','1212','1002','0');
INSERT INTO [t145376] VALUES (1141,'- الوقوف بالميت ليقرأ القراء مما ينهى عنه','1212','1140','0');
INSERT INTO [t145376] VALUES (1142,'- القراءة على الجنازة بدعة مكروهة','1212','1140','0');
INSERT INTO [t145376] VALUES (1143,'(٢٥) مسألة في المشاهد ومن ابتدعها، وفي زيارتها، وما صح من قبور الأنبياء والصحابة، وهل يجوز التبرك بالمشهد؟','1213','1002','0');
INSERT INTO [t145376] VALUES (1144,'- القبور ثلاثة أقسام','1213','1143','0');
INSERT INTO [t145376] VALUES (1145,'- منها ما هو حق لا ريب فيه','1213','1143','0');
INSERT INTO [t145376] VALUES (1146,'- منها ما هو كذب بلا ريب، ذكر بعض هذه القبور','1214','1143','0');
INSERT INTO [t145376] VALUES (1147,'- القسم الثالث: قبور اختلف الناس فيها','1215','1143','0');
INSERT INTO [t145376] VALUES (1148,'- القبور المختلف فيها كثيرة','1219','1143','0');
INSERT INTO [t145376] VALUES (1149,'- ضبط هذه القبور والمشاهد ليس من الدين','1220','1143','0');
INSERT INTO [t145376] VALUES (1150,'- طريقة الزيارة الشرعية للقبور','1221','1143','0');
INSERT INTO [t145376] VALUES (1151,'- حكم زيارة المشاهد والقبور لأجل الصلاة والدعاء عندها','1222','1143','0');
INSERT INTO [t145376] VALUES (1152,'- النهي عن اتخاذ القبور مساجد','1222','1143','0');
INSERT INTO [t145376] VALUES (1153,'- لا يجوز الوفاء بنذر المعصية','1225','1143','0');
INSERT INTO [t145376] VALUES (1154,'- يجب الوفاء بنذر الطاعة، وإن كان أصل عقد النذر مكروها','1226','1143','0');
INSERT INTO [t145376] VALUES (1155,'- قوله - صلى الله عليه وسلم - لا تشد الرحال إلا إلى ثلاثة مساجد، ودلالته على النهي عن السفر لزيارة القبور','1226','1143','0');
INSERT INTO [t145376] VALUES (1156,'- أمرنا الله بالعبادة في المساجد لا في المشاهد','1228','1143','0');
INSERT INTO [t145376] VALUES (1157,'- قول القائل بحرمة فلان الميت أن تقضي حاجتي أو تغفر لي ليس بمشروع','1229','1143','0');
INSERT INTO [t145376] VALUES (1158,'- المقصود بزيارة القبور','1230','1143','0');
INSERT INTO [t145376] VALUES (1159,'(٢٦) مسألة في امرأة توفيت وهي حامل في سبعة أشهر، فهل يشق بطنها أو تضع على بطنها شيئا ثقيلا أو تسطو عليه القوابل؟','1231','1002','0');
INSERT INTO [t145376] VALUES (1160,'- ينبغي أن يسعى في خروج الجنين من فرجها','1231','1159','0');
INSERT INTO [t145376] VALUES (1161,'- فإن تعذر خروج الولد فهل يشق بطنها؟ فيه قولان للفقهاء','1231','1159','0');
INSERT INTO [t145376] VALUES (1162,'- هذا النزاع إذا رجي خروجه حيا، فأما إذا ظهر موته فلا يشق بطنها','1232','1159','0');
INSERT INTO [t145376] VALUES (1163,'(٢٧) مسألة في رجل توفي إلى رحمة الله بالقاهرة، فهل يجوز الصلاة عليه غائبة في مصر أو في القلعة؟ وكم قدر مدة البعد الذي يجوز الصلاة على الغائب فيه؟ وكم مقدار بعد صلاة النبي - صلى الله عليه وسلم - للنجاشي؟','1233','1002','0');
INSERT INTO [t145376] VALUES (1164,'- في مسألة الصلاة على الغائب قولان مشهوران للعلماء','1233','1163','0');
INSERT INTO [t145376] VALUES (1165,'- سبب هذا الاختلاف','1234','1163','0');
INSERT INTO [t145376] VALUES (1166,'- أدلة المانعين ومناقشتها','1236','1163','0');
INSERT INTO [t145376] VALUES (1167,'- أعدل الأقوال في ذلك','1236','1163','0');
INSERT INTO [t145376] VALUES (1168,'- حكم الصلاة على الغائب في البلد الواحد','1236','1163','0');
INSERT INTO [t145376] VALUES (1169,'- حد البلد الواحد','1236','1163','0');
INSERT INTO [t145376] VALUES (1170,'- مقدار بعد النجاشي عن النبي - صلى الله عليه وسلم - معروف','1241','1163','0');
INSERT INTO [t145376] VALUES (1171,'- ما يفعله بعض الناس من الصلاة كل ليلة على جميع من مات من المسلمين -بدعة','1241','1163','0');
INSERT INTO [t145376] VALUES (1172,'(٢٨) مسألة في روح ابن آدم إذا خرجت منه هل تعود إليه في قبره كما كانت في دار الدنيا؟ ومعنى الروح في قوله تعالى (ويسألونك عن الروح)، وهل يموت المهدي إذا أم بعيسى قبل إتمام الصلاة؟','1242','1002','0');
INSERT INTO [t145376] VALUES (1173,'- تعود الروح إلى الميت إذا وضع في قبره','1242','1172','0');
INSERT INTO [t145376] VALUES (1174,'- مستقر أرواح المؤمنين في الجنة، وله شان آخر بعد الموت ليس لها نظير في العالم','1242','1172','0');
INSERT INTO [t145376] VALUES (1175,'- الروح المسئول عنها في الآية هي روح ابن آدم','1243','1172','0');
INSERT INTO [t145376] VALUES (1176,'- المهدي يتم الصلاة ولا يموت فيها','1243','1172','0');
INSERT INTO [t145376] VALUES (1177,'- صفة المهدي، والرد على الشيعة في تصورهم للمهدي المنتظر','1243','1172','0');
INSERT INTO [t145376] VALUES (1178,'(٢٩) مسألة في رجل مات، وأوصى أن يعمل له ختمة في أسبوعه وتمام شهره، هذا جائز أم لا؟','1245','1002','0');
INSERT INTO [t145376] VALUES (1179,'- الصدقة عن الميت أفضل من عمل ختمة','1245','1178','0');
INSERT INTO [t145376] VALUES (1180,'- في قراءة القرآن للميت نزاع','1245','1178','0');
INSERT INTO [t145376] VALUES (1181,'- الاستئجار على نفس التلاوة غير جائز','1245','1178','0');
INSERT INTO [t145376] VALUES (1182,'(٣٠) في رجل جامع زوجته ولم تغتسل، ثم ماتت، فهل يجزئها كسل الموت؟','1247','1002','0');
INSERT INTO [t145376] VALUES (1183,'- يجزئها غسل الميت عن الأمرين','1247','1182','0');
INSERT INTO [t145376] VALUES (1184,'(٣١) مسألة في رجل غسل صبيا، وأبو الصبي يسكب عليه الماء، والغاسل لا يحفظ القرآن، فهل بجوز تغسيله أم لا؟ وهل يجوز للذي لا يحفظ القرآن أن يصلي عليه؟','1248','1002','0');
INSERT INTO [t145376] VALUES (1185,'- يجوز تغسيله','1248','1184','0');
INSERT INTO [t145376] VALUES (1186,'- يجوز أن يصلي على الميت إذا كان يحفظ الفاتحة والصلاة على النبي - صلى الله عليه وسلم - والدعاء للميت','1248','1184','0');
INSERT INTO [t145376] VALUES (1187,'(٣٢) مسألة في سماع حضره رجل فصاح وخر ميتا، فقال بعضهم: هذا لا يصلى عليه، ودفن ولم يصل عليه، فماذا يجب على من أفتى بذلك؟ وهل يصلى على مثل هذا؟','1249','1002','0');
INSERT INTO [t145376] VALUES (1188,'- تجوز الصلاة عليه، ويصلى على قبره إلى شهر','1249','1187','0');
INSERT INTO [t145376] VALUES (1189,'- سماع المكاء والتصدية بدعة مكروهة','1249','1187','0');
INSERT INTO [t145376] VALUES (1190,'- الذي عمل السماع والذي أنكره وترك الصلاة عليه كلاهما مجتهد، حكمهما إلى الله','1249','1187','0');
INSERT INTO [t145376] VALUES (1191,'(٣٣) هل صح أن الأنبياء أحياء في قبورهم يصلون؟ وكيف تعرض أعمال الأمة على النبي - صلى الله عليه وسلم - في قبره؟ وإذا صلى علبه أو سلم عليه العبد هل يرد عليه السلام؟','1250','1002','0');
INSERT INTO [t145376] VALUES (1192,'- الأنبياء أحياء في قبورهم، وقد يصلون','1250','1191','0');
INSERT INTO [t145376] VALUES (1193,'- عرض الأعمال عليه حق، وأما مخل ذلك فلا يتعلق به غرض','1251','1191','0');
INSERT INTO [t145376] VALUES (1194,'(٣٤) مسألة- في حديث قيس: لابد لك من قرين يدفن معك وهو حي، فهل يبرز العمل في قبره في صورة؟','1252','1002','0');
INSERT INTO [t145376] VALUES (1195,'- هذا المعنى روي في عدة أحاديث','1252','1194','0');
INSERT INTO [t145376] VALUES (1196,'- تصوير العمل لصاحبه على الصراط، لم يبلغني فيه شيء','1253','1194','0');
INSERT INTO [t145376] VALUES (1197,'(٣٥) مسألة في أن لله ملائكة نقالة ينقلون من قبور المسلمين إلى قبور اليهود والنصارى، وكذلك العكس، هل ورد في ذلك خبر أم لا؟','1254','1002','0');
INSERT INTO [t145376] VALUES (1198,'- بعض من يكون ظاهره الإسلام منافق: يهودي أو نصراني أو زنديق','1254','1197','0');
INSERT INTO [t145376] VALUES (1199,'- ما سمعت في نقل الملائكة بأثر','1254','1197','0');
INSERT INTO [t145376] VALUES (1200,'(٣٦) مسألة فيمن يقرأ القرآن العظيم أو شيئا منه، هل الأفضل أن يهدي ثوابه لوالديه ولموتى المسلمين؟ أو يجعل ثوابه لنفسه خاصة؟','1255','1002','0');
INSERT INTO [t145376] VALUES (1201,'- أفضل العبادات ما وافق هدي الرسول - صلى الله عليه وسلم - والصحابة','1255','1200','0');
INSERT INTO [t145376] VALUES (1202,'- حكم إهداء ثواب العبادات البدنية والمالية','1258','1200','0');
INSERT INTO [t145376] VALUES (1203,'(٣٧) مسألة في رجل حفر قبرا في مصيف المسجد بقصد أن يدفن فيه، هل يجوز له ذلك؟','1260','1002','0');
INSERT INTO [t145376] VALUES (1204,'- لا يجوز أن يدفن أحد في المسجد','1260','1203','0');
INSERT INTO [t145376] VALUES (1205,'- السنة أن يدفن في مقابر المسلمين','1260','1203','0');
INSERT INTO [t145376] VALUES (1206,'- النهي عن اتخاذ القبور مساجد','1261','1203','0');
INSERT INTO [t145376] VALUES (1207,'(٣٨) مسألة في عمل طعام في الختم هل هو جائز؟ ومن يتحدث بين الناس بكلام أو حكايات مفتعلة كلها كذب هل يجوز ذلك؟','1263','1002','0');
INSERT INTO [t145376] VALUES (1208,'- هذا المتحدث ليضحك الناس أو لغرض آخر عاص لله ورسوله','1263','1207','0');
INSERT INTO [t145376] VALUES (1209,'- الصدقة عن الميت تنفعه','1264','1207','0');
INSERT INTO [t145376] VALUES (1210,'- أما اكتراء قوم يقرأون القرآن ويهدون ذلك للميت فهذه بدعة','1264','1207','0');
INSERT INTO [t145376] VALUES (1211,'- حكم الأكل من الطعام المصنوع من مال الوارث ومن تركة الميت','1265','1207','0');
INSERT INTO [t145376] VALUES (1212,'(٣٩) مسألة في رجل مات وتزوج أخوه امرأته، ثم إنها ماتت، فهل يحل أن تدفن مع زوجها الأول في قبر واحد؟','1266','1002','0');
INSERT INTO [t145376] VALUES (1213,'- يكره دفن اثنين في قبر واحد إلا لحاجة','1266','1212','0');
INSERT INTO [t145376] VALUES (1214,'(٤٠) مسألة في الصلاة على الجنازة قدام الإمام','1266','1002','0');
INSERT INTO [t145376] VALUES (1215,'- أعدل الأقوال أنها تصح عند العذر','1266','1214','0');
INSERT INTO [t145376] VALUES (1216,'(٤١) مسألة فيمن يصلي على جنازة قدام الإمام وقدام الجنازة، هل تصح أم لا؟','1267','1002','0');
INSERT INTO [t145376] VALUES (1217,'- الجواب كالسابق','1267','1216','0');
INSERT INTO [t145376] VALUES (1218,'(٤٢) مسألة في رجل كلما ختم القرآن أو قرأ شيئا منه يهدي ثوابه إلى رسول الله - صلى الله عليه وسلم - أو إلى جميع أهل الأرض، فهل يجوز ذلك أو يستحب؟ وهل يجب إنكار ذلك على فاعله؟','1268','1002','0');
INSERT INTO [t145376] VALUES (1219,'- لا نزاع في العبادات المالية كالصدقة أنها تصل إلى الميت','1268','1218','0');
INSERT INTO [t145376] VALUES (1220,'- أما العبادات البدنية ففيها قولان','1268','1218','0');
INSERT INTO [t145376] VALUES (1221,'- الكلام على إهداء ثواب القرآن إلى الميت','1269','1218','0');
INSERT INTO [t145376] VALUES (1222,'- لم يبلغنا أن أحدا من الصحابة والتابعين وتابعيهم كان يفعل ذلك','1269','1218','0');
INSERT INTO [t145376] VALUES (1223,'- من أنكر ذلك لأجل كون النبي - صلى الله عليه وسلم - أعلى من أن أحدا يهدي إليه شيئا','1269','1218','0');
INSERT INTO [t145376] VALUES (1224,'- مناقشة هذا القول','1269','1218','0');
INSERT INTO [t145376] VALUES (1225,'- ينبغي للإنسان أن يفعل المشروع من الصلاة والتسليم عليه - صلى الله عليه وسلم -','1271','1218','0');
INSERT INTO [t145376] VALUES (1226,'(٤٣) مسألة في الميت هل غسله طاهر أم نجس؟ وهل تلحد المرأة الرجل أو الرجل المرأة؟ وهل يجب أن يحج عن المرأة الرجل وعن الرجل المرأة؟','1273','1002','0');
INSERT INTO [t145376] VALUES (1227,'- غسله طاهر عند جماهير العلماء','1273','1226','0');
INSERT INTO [t145376] VALUES (1228,'- حكم إدخال المرأة والرجل في القبر ومن يقوم بذلك','1273','1226','0');
INSERT INTO [t145376] VALUES (1229,'- يجوز أن يحج الرجل عن المرأة والمرأة عن الرجل','1274','1226','0');
INSERT INTO [t145376] VALUES (1230,'(٤٤) مسألة في معنى حديث ابن مسعود ما تعدون الرقوب فيكم؟','1276','1002','0');
INSERT INTO [t145376] VALUES (1231,'- شرح معنى الرقوب','1276','1230','0');
INSERT INTO [t145376] VALUES (1232,'(٤٥) مسألة في رجل عزم على حفر قبره في حال حياته','1278','1002','0');
INSERT INTO [t145376] VALUES (1233,'- لا يجوز للرجل أن يحفر قبره قبل أن يموت','1278','1232','0');
INSERT INTO [t145376] VALUES (1234,'- من ظن أن إعداد القبر وبناءه وتعظيمه وتحسينه ينفعه فقد تمنى على الله الأماني الكاذبة','1279','1232','0');
INSERT INTO [t145376] VALUES (1235,'(٤٦) مسألة في أطفال المؤمنين الذين يموتون دون الثلاث، هل لهم صحائف أعمال بكتب فيها ما يهدى لهم من قرآن وصدقة أم لا؟ وهل يسألون في قبورهم ويحاسبون أم لا؟ وهل يدومون على حالتهم أم يكبرون؟ والبنات اللاتي يدفن أبكارا هل يزوجن في الجنة؟ و','1280','1002','0');
INSERT INTO [t145376] VALUES (1236,'(٤٧) مسألة في مقبرة للمسلمين، وأهل الذمة يدفنون فيها، هل يجب على وفي الأمر منعهم أم لا؟','1282','1002','0');
INSERT INTO [t145376] VALUES (1237,'- ليس لأهل الذمة دفن موتاهم في شيء من مقابر المسلمين','1282','1236','0');
INSERT INTO [t145376] VALUES (1238,'(٤٨) مسألة في الخلائق إذا حشروا يوم القيامة هل يحشرون جميعهم عراة أو بعضهم عراة وبعضهم بأكفانهم؟ وهل يموت إدريس من الصعقة؟','1283','1002','0');
INSERT INTO [t145376] VALUES (1239,'- معنى حديث الميت يبعث في ثيابه التي قبض فيها','1283','1238','0');
INSERT INTO [t145376] VALUES (1240,'- في الأحاديث الصحيحة أنهم يحشرون عراة','1283','1238','0');
INSERT INTO [t145376] VALUES (1241,'- أما إدريس فقد روي أنه مات في السماء','1286','1238','0');
INSERT INTO [t145376] VALUES (1242,'(٤٩) مسألة في معنى قوله من قتل دون ماله فهو شهيد، وهل يجب على الشخص أن يبذل ثلث ماله أم يجوز ذلك','1288','1002','0');
INSERT INTO [t145376] VALUES (1243,'- يجوز له ذلك ولا يجب','1289','1242','0');
INSERT INTO [t145376] VALUES (1244,'- أما الدفع عن الحرمة فهو واجب','1289','1242','0');
INSERT INTO [t145376] VALUES (1245,'- أما الدفع عن دمه فهو جائز، وفي وجوبه قولان','1289','1242','0');
INSERT INTO [t145376] VALUES (1246,'- النهي عن القتال في الفتنة','1290','1242','0');
INSERT INTO [t145376] VALUES (1247,'(٥٠) مسألة في سؤال منكر ونكير، وهل نعود الروح في جسد الميت عند ذلك؟','1294','1002','0');
INSERT INTO [t145376] VALUES (1248,'- عود الروح في بدن الميت في القبر ليس مثل عودها إليه في الدنيا','1294','1247','0');
INSERT INTO [t145376] VALUES (1249,'- الروح تتصل بالبدن وتفارقه متى شاء الله','1295','1247','0');
INSERT INTO [t145376] VALUES (1250,'- مشابهة النوم بالموت','1295','1247','0');
INSERT INTO [t145376] VALUES (1251,'(٥١) إذا أدرك الميت في أيام مرضه شهر رمضان، وتوفي وعليه صيامه والصلاة مدة مرضه، ووالداه بالحياة، فهل تسقط الصلاة والصيام عنه إذا صاما عنه وصليا؟','1298','1002','0');
INSERT INTO [t145376] VALUES (1252,'(٥٢) مسألة في الشهداء: هل يشفع الشهيد منهم في أربعين من أهل بيته أم لا؟ وهل هم سبعة أو تسعة؟ وهل يشفعون جميعهم أم لا؟ وإذا كان الشهيد عاصيا يكون منهم أم لا؟ وإذا كان الشهيد عليه دين أو مظلمة يطالب بها أم لا؟','1299','1002','0');
INSERT INTO [t145376] VALUES (1253,'(٥٣) هل يجوز نقل الموتى من تربة في مكان منقطع إلى تربة مستجدة أم لا؟','1301','1002','0');
INSERT INTO [t145376] VALUES (1254,'(٥٤) مسألة في إهداء ثواب الأعمال إلى النبي - صلى الله عليه وسلم - وإلى أزواجه وأولاده','1302','1002','0');
INSERT INTO [t145376] VALUES (1255,'- ذهب إليه طائفة من المتأخرين','1303','1254','0');
INSERT INTO [t145376] VALUES (1256,'- مستندهم في ذلك','1304','1254','0');
INSERT INTO [t145376] VALUES (1257,'- من فرق بين العبادات البدنية والمالية','1304','1254','0');
INSERT INTO [t145376] VALUES (1258,'- من سوى بينهما يقول: إن النيابة في العبادات البدنية تجوز للحاجة','1304','1254','0');
INSERT INTO [t145376] VALUES (1259,'- ويقول: إن إهداء ثواب العمل إلى الميت ليس نيابة عنه','1306','1254','0');
INSERT INTO [t145376] VALUES (1260,'- احتجاج بعض الناس لعدم وصول ثواب القرب إلى الميت بقوله تعالى (وأن ليس للإنسان إلا ما سعى)','1307','1254','0');
INSERT INTO [t145376] VALUES (1261,'- مناقشة هذا الدليل','1307','1254','0');
INSERT INTO [t145376] VALUES (1262,'- التفسير الصحيح للآية','1308','1254','0');
INSERT INTO [t145376] VALUES (1263,'- قول بعضهم: إن النبي أجل من أن يهدى له ثواب أو يفعل عنه قربة','1310','1254','0');
INSERT INTO [t145376] VALUES (1264,'- مناقشة هذا القول','1310','1254','0');
INSERT INTO [t145376] VALUES (1265,'- الدعاء مشروع من الأدنى للأعلى ومن الأعلى للأدنى','1312','1254','0');
INSERT INTO [t145376] VALUES (1266,'- حجة من لا يستحب إهداء الثواب إلى الميت ويراه بدعة: أن السلف لم يكونوا يفعلون ذلك وهم أعلم بالخير وأرغب','1314','1254','0');
INSERT INTO [t145376] VALUES (1267,'- الكلام على حديث تضحية علي عن النبي - صلى الله عليه وسلم -','1314','1254','0');
INSERT INTO [t145376] VALUES (1268,'- الكلام على حديث أبي بن كعب الذي فيه: أجعل صلاتي كلها لك','1315','1254','0');
INSERT INTO [t145376] VALUES (1269,'- الكلام على حديث الدارقطني: إن من البر بعد البر أن تصلي لهما مع صلاتك','1316','1254','0');
INSERT INTO [t145376] VALUES (1270,'- احتجاج بعض المتأخرين بأحاديث رويت فيمن مر على القبور فقرأ كذا وكذا','1317','1254','0');
INSERT INTO [t145376] VALUES (1271,'- ليس فيها ما يعتمد عليها في إثبات الأحكام الشرعية','1317','1254','0');
INSERT INTO [t145376] VALUES (1272,'- الفرق بين الأمر الديني والأمر الكوني','1321','1254','0');
INSERT INTO [t145376] VALUES (1273,'- من قال: إن النبي - صلى الله عليه وسلم - له مثل أجور أمته، فلا حاجة إلى الإهداء','1325','1254','0');
INSERT INTO [t145376] VALUES (1274,'- ما يرد على هذا القول وما يجاب به عنه','1325','1254','0');
INSERT INTO [t145376] VALUES (1275,'- لماذا لم يكن السلف يحجون ويعتمرون ويذبحون عن أئمتهم؟','1329','1254','0');
INSERT INTO [t145376] VALUES (1276,'- الفرق بين الصلاة على النبي - صلى الله عليه وسلم - وبين إهداء الثواب للوالدين ونحوهما','1332','1254','0');
INSERT INTO [t145376] VALUES (1277,'- عودة إلى الكلام على الفرق بين الحقائق الكونية والحقائق الدينية','1337','1254','0');
INSERT INTO [t145376] VALUES (1278,'- التقرب إلى الله بالأعمال الصالحة ليس من جنس طاعة المخلوق المملوك لمالكه لوجوه كثيرة','1340','1254','0');
INSERT INTO [t145376] VALUES (1279,'- كل مخلوق فهو محتاج إلى الله مفتقر إليه','1344','1254','0');
INSERT INTO [t145376] VALUES (1280,'- على العبد آن يلاحظ التوحيد والإنعام','1346','1254','0');
INSERT INTO [t145376] VALUES (1281,'- من شبه الله بخلقه فقد كفر','1347','1254','0');
INSERT INTO [t145376] VALUES (1282,'- المقصود أن الأعمال لا تعمل إلا لله، ولا يطلب أجرها إلا من الله','1351','1254','0');
INSERT INTO [t145376] VALUES (1283,'- في النصارى إشراك وغلو وابتداع، وكذا في كثير من ضلال هذه الأمة','1355','1254','0');
INSERT INTO [t145376] VALUES (1284,'- لله حقوق لا يشركه فيها غيره','1356','1254','0');
INSERT INTO [t145376] VALUES (1285,'- أصل الإسلام مبني على أصلين: أن لا نعبد إلا الله، ولا نعبده إلا بما شرع','1358','1254','0');
INSERT INTO [t145376] VALUES (1286,'- إهداء العبادات إلى النبي - صلى الله عليه وسلم - بدعة، لم يقم على استحبابه دليل شرعي','1358','1254','0');
INSERT INTO [t145376] VALUES (1287,'(٥٥) مسألة فيمن قال: إن إبليس أودع ولده لآدم، وأن آدم طرده مرتين، وبعد الثالثة ذبحه وسلقه وأكله، فلهذا يجري الشيطان في ابن آد مجرى الدم','1359','1002','0');
INSERT INTO [t145376] VALUES (1288,'- الحديث المذكور عن آدم من أقبح الكذب والبهتان','1359','1287','0');
INSERT INTO [t145376] VALUES (1289,'(٥٦) مسألة في رجل قال لزوجته: علي الطلاق ما تروحي لبيت أبيك (ثلاث مرات)، فغصبتها أمها وأخذتها، وراحت إلى دار أبيها من غير رضى منها ولا إذن الزوج، فهل تقع الثلاث أو واحدة؟','1362','1002','0');
INSERT INTO [t145376] VALUES (1290,'- لا يقع به إلا واحدة','1363','1289','0');
INSERT INTO [t145376] VALUES (1291,'(٥٧) فصل: ما ضمن بالعقد الصحيح صمن بالعقد الفاسد، والضمانات ثلاثة','1364','1002','0');
INSERT INTO [t145376] VALUES (1292,'(٥٨) مسألة في رجل قال: قال رسول الله - صلى الله عليه وسلم -: كنت نبيا وآدم بين الماء والطين، فقال له آخر: هذا ما هو صحيح','1365','1002','0');
INSERT INTO [t145376] VALUES (1293,'- ليس هذا الحديث بصحيح لفظ الحديث المعروف ومعناه','1365','1292','0');
INSERT INTO [t145376] VALUES (1294,'(٥٩) مسألة في قوله تعالى (ألا إن أولياء الله لا خوف عليهم ولا هم يحزنون) وقوله تعالى (رجال لا تلهيهم تجارة ولا بيع عن ذكر الله)، فمن هو في هؤلاء أعلى درجة؟','1368','1002','0');
INSERT INTO [t145376] VALUES (1295,'- الآية الأولى تتناول جميع الأولياء، الفاضل منهم والمفضول','1368','1294','0');
INSERT INTO [t145376] VALUES (1296,'- الآية الثانية تتناول أيضا من دخل في تلك الآية، والأفضلية بحسب الإيمان والتقوى','1369','1294','0');
INSERT INTO [t145376] VALUES (1297,'(٦٠) مسألة في غلام حلف بالطلاق الثلاث أنه لم يخدم عند إنسان، فأخذه غصبا، واستخدمه بالضرب، فلما ضربه حلف يمينا ثانيا بالطلاق الثلاث أنه ما يخدم، فما الحكم؟','1370','1002','0');
INSERT INTO [t145376] VALUES (1298,'(٦١) مسألة في رجل صلى صلاة الصبح إماما بسورة المدثر ولا أقسم بيوم القيامة في الركعتين، فقال بعض الناس: هذه الصلاة ليست من الشرع، ولا يصلى خلفه','1371','1002','0');
INSERT INTO [t145376] VALUES (1299,'- هذه الصلاة مشروعة باتفاق المسلمين','1371','1298','0');
INSERT INTO [t145376] VALUES (1300,'- بيان ذلك بالأحاديث والآثار','1371','1298','0');
INSERT INTO [t145376] VALUES (1301,'- من أنكر ما شرعه النبي - صلى الله عليه وسلم - يعزر','1373','1298','0');
INSERT INTO [t145376] VALUES (1302,'(٦٢) مسألة في رجل إمام مسجد: هل يجوز أن يكبر أحد خلفه من المؤتمين؟ أو يواظب على السجدة في فجر كل جمعة؟ أو يدعو هو والمؤتمون عقب كل صلاة؟','1373','1002','0');
INSERT INTO [t145376] VALUES (1303,'- لا يشرع الجهر بالتكبير خلف الإمام لغير حاجة','1374','1302','0');
INSERT INTO [t145376] VALUES (1304,'- دعاء الإمام والمأمومين بعد الصلاة ليس من سنن الصلاة الراتبة','1375','1302','0');
INSERT INTO [t145376] VALUES (1305,'- الناس في هذه المسالة طرفان ووسط','1375','1302','0');
INSERT INTO [t145376] VALUES (1306,'- السجدة يوم الجمعة ليست واجبة','1376','1302','0');
INSERT INTO [t145376] VALUES (1307,'(٦٣) مسألة في رجل متمسك بأحد المذاهب الأربعة، نزلت به نازلة فاستفتى بعض العلماء، فأفتاه بقول أحد الأئمة، فعارضه آخر وقال: من استفتى غير أهل مذهبه فهو زنديق فهل هذا المنكر مصيب أم مخطئ؟','1378','1002','0');
INSERT INTO [t145376] VALUES (1308,'- هذا المنكر مخطئ باتفاق الأئمة، بل هو آثم مستحق للعقوبة','1378','1307','0');
INSERT INTO [t145376] VALUES (1309,'- لا يجب على أحد أن يتبع واحدا بعينه في كل ما يقوله','1378','1307','0');
INSERT INTO [t145376] VALUES (1310,'(٦٤) مسألة في رجل لم يؤد الصلوات الفرض وتوفي، وخلف ولا صالح، وهو بعد أن يصلي الصلوات المكتوبة يصلي صلوات دائما ويحتسبها لوالده عن فرضه، فهل يجوز ذلك عن والده؟','1380','1002','0');
INSERT INTO [t145376] VALUES (1311,'- الفرض لا يسقط عنه بصلاة غيره','1380','1310','0');
INSERT INTO [t145376] VALUES (1312,'(٦٥) مسألة في رجل أوقف قطعة أرض، وشرط النظر لشخص من الفقراء، فجاء الحاكم بالناحية واجر الأرض مدة عشر سنين بدون أجرة المثل، فهل تجوز هذه الإجارة؟','1381','1002','0');
INSERT INTO [t145376] VALUES (1313,'- إذا كان لها ناظر قائم بالواجب فليس للحاكم أن يؤجرها','1381','1312','0');
INSERT INTO [t145376] VALUES (1314,'(٦٦) مسألة: متى فرض الصوم والصلاة والزكاة؟','1382','1002','0');
INSERT INTO [t145376] VALUES (1315,'(٦٧) مسألة: هل يجب للحائض أن تغسل باطن فرجها من الحيض والجنابة؟','1382','1002','0');
INSERT INTO [t145376] VALUES (1316,'- لا يجب عليها ذلك','1382','1315','0');
INSERT INTO [t145376] VALUES (1317,'مسائل وردت من الصلت','1383','992','0');
INSERT INTO [t145376] VALUES (1318,'(١) مسألة في الكلب إذا ولغ في طست لبن أو طعام أو شراب، هل يحل أكله وبيعه أم لا؟','1384','1317','0');
INSERT INTO [t145376] VALUES (1319,'(٢) مسألة في الفأرة إذا وقعت في سمن أو زيت وهو مائع، هل يحل أكله وبيعه أم لا؟','1384','1317','0');
INSERT INTO [t145376] VALUES (1320,'(٣) مسألة في رجل يدخل على امرأة أخيه وبنات أخيه وبنات عفه وبنات خاله، هل يجوز له ذلك أم لا؟','1385','1317','0');
INSERT INTO [t145376] VALUES (1321,'(٤) مسألة في التيمم، هل يجوز لأحد أن يصلي به السنن والرواتب والفريضة ويقتصر عليه إلى حين الحدث أم لا؟','1385','1317','0');
INSERT INTO [t145376] VALUES (1322,'(٥) سئل عن رجل يأمر الناس بالصلاة ولم يصل، فماذا يجب عليه؟','1386','1317','0');
INSERT INTO [t145376] VALUES (1323,'(٦) سئل فيمن يصلي الفرض خلف من يصلي نفلا','1386','1317','0');
INSERT INTO [t145376] VALUES (1324,'(٧) سئل عن الماء إذا غمس الرجل يده، هل يجوز استعماله أم لا؟','1386','1317','0');
INSERT INTO [t145376] VALUES (1325,'(٨) سئل عن صلاة التراويح، هل يجوز قبل العشاء أم لا','1387','1317','0');
INSERT INTO [t145376] VALUES (1326,'(٩) سئل عن الرجل يمس المرأة، هل ينتقض الوضوء أم لا؟','1387','1317','0');
INSERT INTO [t145376] VALUES (1327,'(١٠) سئل عن الرجل إذا اغتسل من الجنابة، ولم يتوضأ بعده ولا قبله وصلى بالغسل، فهل يجوز ذلك أم لا؟','1388','1317','0');
INSERT INTO [t145376] VALUES (1328,'(١١) سئل عن الرجل لا يواظب على السنن الرواتب','1388','1317','0');
INSERT INTO [t145376] VALUES (1329,'(١٢) سئل فيمن يحلف بالطلاق أنه لا يفعل شيئا، ثم أراد أن بفعله','1388','1317','0');
INSERT INTO [t145376] VALUES (1330,'(١٣) سئل عن الرعاف هل ينقض الوضوء أم لا؟','1389','1317','0');
INSERT INTO [t145376] VALUES (1331,'(١٤) مسألة في الفصاد في شهر رمضان، هل يفسد الصوم أم لا','1389','1317','0');
INSERT INTO [t145376] VALUES (1332,'(١٥) سئل في سفر يوم رمضان، هل يجوز له أن يقصر فيه أو يفطر أم لا؟','1390','1317','0');
INSERT INTO [t145376] VALUES (1333,'(١٦) سئل عن رجل معه مال من حرام وحلال، فهل يجوز أن يأكل من عيشه أم لا؟','1390','1317','0');
INSERT INTO [t145376] VALUES (1334,'(١٧) مسألة في رجل باع متاعا لإنسان تاجر، وكسب عليه وقسط عليه الثمن، والمديون يطلب السفر ولم يقم له كافلا، فهل لصاحب الدين أن يمنعه من السفر أم لا؟','1391','1317','0');
INSERT INTO [t145376] VALUES (1335,'(١٨) سئل عن رجل يعمل عملا يستوجب أن يبنى له قصر في الجنة ويغرس له أغراس في الجنة، ثم يعمل ذنوبا يستوجب بها النار، فإذا دخل النار كيف يكون اسمه في الجنة وهو في النار؟','1392','1317','0');
INSERT INTO [t145376] VALUES (1336,'(١٩) مسألة في رجل استلف من رجل دراهم إلى اجل على غلة، بحكم أنه إذا حل الأجل دفع إليه الغلة بأنقص مما تساوي بخمسة دراهم فهل يحل أن يتناول ذلك منه على هذه الصفة؟','1393','1317','0');
INSERT INTO [t145376] VALUES (1337,'(٢٠) مسألة في رجل فاتته صلاة العصر، فجاء إلى المسجد فوجد المغرب قد أقيمت، فهل يصلي الفائتة قبل أم لا؟','1395','1317','0');
INSERT INTO [t145376] VALUES (1338,'(٢١) مسألة في رجل خص بعض بناته، فجهزها وملكها بنحو مئتي ألف درهم، وخص بعضهم بوقف بعض ماله عليه، فهل لورثة الواقف فسخ ذلك أم لا؟','1396','1317','0');
INSERT INTO [t145376] VALUES (1339,'(٢٢) مسألة في قبور الأنبياء عليهم الصلاة والسلام، هل هذه القبور التي يزورها اليوم يصح منها شيء أم لا؟','1397','1317','0');
INSERT INTO [t145376] VALUES (1340,'(٢٣) مسألة في أكل لحم الضبع والثعلب وسنور البر وابن آوى وجلودهم، هل يحل لبس جلود الجميع وأكل لحم الجميع أم البعض؟ وهل تطهر جلودهم بالدباغ؟','1398','1317','0');
INSERT INTO [t145376] VALUES (1341,'(٢٤) مسألة في لحوم الخيل وألبانها، هل هي مباحة أم لا؟','1400','1317','0');
INSERT INTO [t145376] VALUES (1342,'(٢٥) مسألة فيمن مات وخلف بنتا وأخا لأم وابن عم','1402','1317','0');
INSERT INTO [t145376] VALUES (1343,'(٢٦) مسألة في رجل حلف بالطلاق، ثم استثنى هنيهة بقدر ما يمكن فيه الكلام','1402','1317','0');
INSERT INTO [t145376] VALUES (1344,'مسائل متفرقة','1403','992','0');
INSERT INTO [t145376] VALUES (1345,'(١) مسألة في مسجد بيت المقدس وقد جعل فيه أئمة، كل منهم يصلي في موضع منه، فإذا صلى أحد منهم في وقت ثلاة الآخر هل يدخل في النهي، فيكره له ذلك أم لا؟ وأي الأئمة أحق بالصلاة بلا كراهة؟ وهل تبطل صلاة الإمام الذي صلى بعد إقامة الصلاة لإمام','1404','1344','0');
INSERT INTO [t145376] VALUES (1346,'(٢) سئل عن رجل يشتري القمح في زمان الصيف، ثم يخزنه إلى زمان الشتاء، فيمسك يده عن بيعه حتى يكثر طالبه، فهل هذا محتكر أم لا؟ وعن رجل رأى في المنام أنه بجامع ولم تدركه اللذة الكبرى والإنزال إلا بعد أن استيقظ، فهل يفسد صومه أم لا؟','1406','1344','0');
INSERT INTO [t145376] VALUES (1347,'(٣) مسألة في سورة الأنعام: هل أنزلت على النبي - صلى الله عليه وسلم - جملة واحدة أم آيات متفرقة متتابعة؟','1408','1344','0');
INSERT INTO [t145376] VALUES (1348,'(٤) مسألة في رجل كسب جارية من ملطية وباعها، ثم اشترى بثمنها جارية، فتبين أنها مسلمة وأبواها مسلمان، فاعتقها، فهل يجب عليه الخمس أم لا؟','1410','1344','0');
INSERT INTO [t145376] VALUES (1349,'(٥) مسألة: من كان معه ختمة فله أن يحملها بين قماشه وفي خرجة','1410','1344','0');
INSERT INTO [t145376] VALUES (1350,'(٦) مسألة قراءة القرآن بقصد التلحين','1410','1344','0');
INSERT INTO [t145376] VALUES (1351,'(٧) سئل عن قوم لهم عيون ما عليها زروع، فجاء رجل فحقن الماء، وأحدث عليه سدا وطاحونا، فتضرر أرباب العيون، فهل لهم إزالة ما أحدثه؟','1411','1344','0');
INSERT INTO [t145376] VALUES (1352,'(٨) سئل عن رجل خطب ابنة رجل فركن إليه، ثم خطبها آخر، فركب عن الأول وركن إلى الثاني، فهل للثاني تزويجها؟ وهل يكون ملعونا؟','1412','1344','0');
