INSERT INTO [t14592] VALUES (622,'(٦) باب التشبه بالنبيين صلوات الله عليهم وسلامه عليهم أجمعين','2233','46','0');
INSERT INTO [t14592] VALUES (623,'١ - فمنها: العلم وطلبه، والرحلة في طلبه والاستزادة منه.','2261','622','0');
INSERT INTO [t14592] VALUES (624,'٢ - ومنها: تعليم العلم وإفادته، وإرشاد الناس إلى الخير.','2265','622','0');
INSERT INTO [t14592] VALUES (625,'٣ - ومنها: النطق بالحكمة.','2266','622','0');
INSERT INTO [t14592] VALUES (626,'٤ - ومنها: النصيحة.','2266','622','0');
INSERT INTO [t14592] VALUES (627,'٥ - ومنها -وهو من أخص أعمالهم وأغلب أحوالهم-: الدعاء إلى الله والإرشاد إليه.','2270','622','0');
INSERT INTO [t14592] VALUES (628,'٦ - ومنها: التوحيد، والإسلام، والإيمان، والإحسان.','2270','622','0');
INSERT INTO [t14592] VALUES (629,'٧ - ومنها: شهود الأفعال من الله تعالى على وجه الحكمة.','2272','622','0');
INSERT INTO [t14592] VALUES (630,'٨ - ومنها: القيام بالحقوق وتأدية الأمانات.','2272','622','0');
INSERT INTO [t14592] VALUES (631,'تنبيه','2273','630','0');
INSERT INTO [t14592] VALUES (632,'٩ - ومنها: القضاء بالحق.','2275','622','0');
INSERT INTO [t14592] VALUES (633,'١٠ - ومن أعمال الأنبياء عليهم السلام: مصابرة العبادة.','2277','622','0');
INSERT INTO [t14592] VALUES (634,'١١ - ومنها: إقامة الصلاة، والمحافظة عليها وعدم التهاون بها.','2279','622','0');
INSERT INTO [t14592] VALUES (635,'تنبيه','2280','634','0');
INSERT INTO [t14592] VALUES (636,'فائدة','2280','634','0');
INSERT INTO [t14592] VALUES (637,'فائدة أخرى','2281','634','0');
INSERT INTO [t14592] VALUES (638,'١٢ - ومنها: الفزع عند الأمور المهمة إلى الصلاة، وطلب الرزق والحاجة بها.','2283','622','0');
INSERT INTO [t14592] VALUES (639,'١٣ - ومنها: الطهارة للصلاة.','2285','622','0');
INSERT INTO [t14592] VALUES (640,'١٤ - ومنها: وضع اليد اليمنى على اليسرى في الصلاة.','2285','622','0');
INSERT INTO [t14592] VALUES (641,'١٥ - ومنها: صلاة الضحى.','2286','622','0');
INSERT INTO [t14592] VALUES (642,'١٦ - ومنها: الصلاة عند زوال الشمس.','2286','622','0');
INSERT INTO [t14592] VALUES (643,'١٧ - ومنها: تعظيم يوم الجمعة.','2287','622','0');
INSERT INTO [t14592] VALUES (644,'١٨ - ومنها: قيام الليل.','2287','622','0');
INSERT INTO [t14592] VALUES (645,'١٩ - ومنها: الصدقة، والخروج عما يشغل عن طاعة الله تعالى لوجه الله تعالى.','2290','622','0');
INSERT INTO [t14592] VALUES (646,'٢٠ - ومنها: تلاوة كتاب الله تعالى.','2292','622','0');
INSERT INTO [t14592] VALUES (647,'٢١ - ومنها: الصيام.','2293','622','0');
INSERT INTO [t14592] VALUES (648,'٢٢ - ومنها: تعجيل الفطر وتأخير السحور.','2296','622','0');
INSERT INTO [t14592] VALUES (649,'٢٣ - ومنها: إيثار الجوع.','2297','622','0');
INSERT INTO [t14592] VALUES (650,'٢٤ - ومن أعمال الأنبياء عليهم السلام: فطر يوم الفطر ويوم الأضحى.','2301','622','0');
INSERT INTO [t14592] VALUES (651,'٢٥ - ومنها: التضحية وإهداء الهدي.','2302','622','0');
INSERT INTO [t14592] VALUES (652,'٢٦ - ومنها: الاعتكاف في البيت الحرام وغيره من المساجد.','2303','622','0');
INSERT INTO [t14592] VALUES (653,'٢٧ - ومنها: الحج إلى البيت الحرام، وتأدية مناسكه كما هو مقرر في الشريعة.','2303','622','0');
INSERT INTO [t14592] VALUES (654,'تنبيه لطيف','2313','653','0');
INSERT INTO [t14592] VALUES (655,'٢٨ - ومن أخلاق الأنبياء عليهم السلام وأعمالهم: التوسل بالنبي - صلى الله عليه وسلم -.','2313','622','0');
INSERT INTO [t14592] VALUES (656,'٢٩ - ومنها: بر الوالدين.','2315','622','0');
INSERT INTO [t14592] VALUES (657,'٣٠ - ومنها: العفو والاحتمال، ومقابلة السيئة بالحسنة.','2317','622','0');
INSERT INTO [t14592] VALUES (658,'٣١ - ومنها: الحلم وحسن الخلق','2319','622','0');
INSERT INTO [t14592] VALUES (659,'٣٢ - ومنها: العود على النفس باللائمة إذا جهل أحد عليهم.','2321','622','0');
INSERT INTO [t14592] VALUES (660,'٣٣ - ومنها: السخاء.','2322','622','0');
INSERT INTO [t14592] VALUES (661,'٣٤ - ومنها: الضيافة وإكرام الضيف.','2323','622','0');
INSERT INTO [t14592] VALUES (662,'فائدة','2324','661','0');
INSERT INTO [t14592] VALUES (663,'تنبيه','2325','661','0');
INSERT INTO [t14592] VALUES (664,'٣٥ - ومنها: التواضع.','2331','622','0');
INSERT INTO [t14592] VALUES (665,'٣٦ - ومنها: أكل الحلال، وتجنب الحرام في المآكل والمشارب، والملابس، وسائر الأمور.','2332','622','0');
INSERT INTO [t14592] VALUES (666,'٣٧ - ومنها: الاهتمام بأمور الآخرة، والتفرغ عن أمور الدنيا إلا ما لابد منه.','2334','622','0');
INSERT INTO [t14592] VALUES (667,'٣٨ - ومنها: الرجاء والطمع في رحمة الله تعالى.','2337','622','0');
INSERT INTO [t14592] VALUES (668,'٣٩ - ومنها: الخوف والخشية، والهيبة والحياء','2338','622','0');
INSERT INTO [t14592] VALUES (669,'٤٠ - ومنها: الخشوع، وخصوصا في الصلاة والدعاء','2341','622','0');
INSERT INTO [t14592] VALUES (670,'٤١ - ومنها: الاستعاذة من النار، والتأوه عند ذكرها أو التأوه مطلقا.','2343','622','0');
INSERT INTO [t14592] VALUES (671,'٤٢ - ومنها: البكاء من خشية الله تعالى، وأسفا من الذنوب.','2347','622','0');
INSERT INTO [t14592] VALUES (672,'٤٣ - ومنها: الحزن.','2349','622','0');
INSERT INTO [t14592] VALUES (673,'٤٤ - ومنها: الرجاء والطمع في رحمة الله، والرغبة فيما عنده.','2353','622','0');
INSERT INTO [t14592] VALUES (674,'٤٥ - ومنها: المسارعة إلى الخيرات، والمسابقة إلى الأعمال الصالحات.','2355','622','0');
INSERT INTO [t14592] VALUES (675,'٤٦ - ومنها: التوبة والاستغفار.','2357','622','0');
INSERT INTO [t14592] VALUES (676,'٤٧ - ومنها: الورع، والحذر من الشبهات.','2361','622','0');
INSERT INTO [t14592] VALUES (677,'٤٨ - ومنها: الصيانة مع حسن الوجه وجمال الصورة.','2363','622','0');
INSERT INTO [t14592] VALUES (678,'٤٩ - ومنها: ذم الدنيا وتحقيرها.','2365','622','0');
INSERT INTO [t14592] VALUES (679,'٥٠ - ومنها: الزهد والتقلل من الدنيا، وإيثار الخشن من الثياب والعيش.','2366','622','0');
INSERT INTO [t14592] VALUES (680,'تنبيه','2370','679','0');
INSERT INTO [t14592] VALUES (681,'تنبيه آخر','2371','679','0');
INSERT INTO [t14592] VALUES (682,'ومن أخلاق الأنبياء عليهم السلام','2372','679','0');
INSERT INTO [t14592] VALUES (683,'٥١ - اليقين.','2372','622','0');
INSERT INTO [t14592] VALUES (684,'٥٢ - ومنها: التوكل والتفويض والتسليم.','2373','622','0');
INSERT INTO [t14592] VALUES (685,'٥٣ - ومنها: الاكتساب والأكل من كد اليمين وعرق الجبين','2376','622','0');
INSERT INTO [t14592] VALUES (686,'تنبيه','2381','685','0');
INSERT INTO [t14592] VALUES (687,'تنبيه أيضا','2383','685','0');
INSERT INTO [t14592] VALUES (688,'٥٤ - ومنها: الاستشارة.','2384','622','0');
INSERT INTO [t14592] VALUES (689,'تنبيه','2385','688','0');
INSERT INTO [t14592] VALUES (690,'٥٥ - ومنها: مداراة الناس ومخالقتهم بأخلاقهم من غير إثم إذا احتيج إلى مخالقتهم، وخصوصا لأجل تعليمهم وإرشادهم؛ فإن ذلك أيضا من أخلاق الأنبياء عليهم السلام.','2387','622','0');
INSERT INTO [t14592] VALUES (691,'تنبيه','2388','690','0');
INSERT INTO [t14592] VALUES (692,'٥٦ - ومنها: الصبر على جور الحكام،','2390','622','0');
INSERT INTO [t14592] VALUES (693,'٥٧ - ومنها: النصيحة للخلق، ووعظهم وتذكيرهم، ووصيتهم بالتقوى وقبول النصيحة والوصية.','2391','622','0');
INSERT INTO [t14592] VALUES (694,'٥٨ - ومنها: العزلة والانفراد عن الناس إلا في حال الدعوة والتعليم، والاختلاء بالله على كل حال.','2393','622','0');
INSERT INTO [t14592] VALUES (695,'٥٩ - ومنها: الصمت إلا عن خير.','2394','622','0');
INSERT INTO [t14592] VALUES (696,'٦٠ - ومنها: التنزه عن خائنة الأعين.','2396','622','0');
INSERT INTO [t14592] VALUES (697,'٦١ - ومنها: الحب في الله تعالى، والبغض في الله، والاجتماع على الله، والهجرة في الله سبحانه وتعالى.','2398','622','0');
INSERT INTO [t14592] VALUES (698,'٦٢ - ومنها: الرحمة والشفقة على خلق الله تعالى، وخصوصا الضعفاء كاليتيم والمسكين والأرملة والخادم.','2400','622','0');
INSERT INTO [t14592] VALUES (699,'٦٣ - ومنها: العدل والقضاء بالحق.','2402','622','0');
INSERT INTO [t14592] VALUES (700,'٦٤ - ومنها: قول الحق عند من يخاف أو يرجى بحيث لا تأخذهم في الله لومة لائم.','2403','622','0');
INSERT INTO [t14592] VALUES (701,'٦٥ - ومنها: القوة في دين الله تعالى، وأعمال الخير والأمانة والعفة.','2403','622','0');
INSERT INTO [t14592] VALUES (702,'٦٦ - ومنها: الغضب لله وعدم الغضب للنفس.','2405','622','0');
INSERT INTO [t14592] VALUES (703,'٦٧ - ومنها: النكاح، خصوصا للمرأة الصالحة، والإنكاح، خصوصا للرجل الكامل الصالح.','2406','622','0');
INSERT INTO [t14592] VALUES (704,'وإنما خصت الأنبياء عليهم السلام بكثرة النساء والنكاح لما استودعه الله تعالى في النكاح من الفوائد والأسرار التي الأنبياء بها أحرى','2409','703','0');
INSERT INTO [t14592] VALUES (705,'تنبيهان','2422','704','0');
INSERT INTO [t14592] VALUES (706,'- ومن أخلاقهم أيضا','2428','703','0');
INSERT INTO [t14592] VALUES (707,'٦٨ - المحافظة على سائر الآداب في سائر الأمور؛ كآداب الطهارة، والصلاة، والسفر، والجهاد، والحج، والأكل والشرب، واللباس، والنوم، وغير ذلك.','2428','622','0');
INSERT INTO [t14592] VALUES (708,'٦٩ - ومنها: التعطر واستعمال الطيب.','2429','622','0');
INSERT INTO [t14592] VALUES (709,'٧٠ - ومنها: الاكتحال وسائر أنواع الزينة الشرعية.','2430','622','0');
INSERT INTO [t14592] VALUES (710,'٧١ - ومنها: المحافظة على خصال الفطرة.','2430','622','0');
INSERT INTO [t14592] VALUES (711,'٧٢ - الاستغفار عند الخروج من الخلاء، وحمد الله على إذهاب الأذى.','2435','622','0');
INSERT INTO [t14592] VALUES (712,'٧٣ - ومنها: بقية آداب قضاء الحاجة؛ كالاستتار، والإبعاد، وقعود القرفصاء.','2436','622','0');
INSERT INTO [t14592] VALUES (713,'٧٤ - ومنها: الاغتسال من الجنابة، والتستر عند الاغتسال وعند قضاء الحاجة حياء، والحياء في سائر الأحوال، بل كذلك الاستتار مطلقا، وحفظ العورة، والوضوء والتثليث فيه والمحافظة عليه، والخضاب في محله بالصفرة والحمرة.','2437','622','0');
INSERT INTO [t14592] VALUES (714,'٧٥ - ومنها: لبس القميص، والسراويل، والكساء، والجبة، والقلنسوة، والنعل، وسائر أنواع اللباس مما لا يكون فيه إسراف ولا مخيلة.','2440','622','0');
INSERT INTO [t14592] VALUES (715,'٧٦ - ومن أخلاق الأنبياء عليهم السلام: التؤدة والتأني إلا في أمور الآخرة، والاقتصاد في المعيشة، والسمت الحسن.','2443','622','0');
INSERT INTO [t14592] VALUES (716,'تنبيه','2444','715','0');
INSERT INTO [t14592] VALUES (717,'وهنا لطيفتان','2446','715','0');
INSERT INTO [t14592] VALUES (718,'الأولى','2446','717','0');
INSERT INTO [t14592] VALUES (719,'اللطيفة الثانية','2447','717','0');
INSERT INTO [t14592] VALUES (720,'٧٧ - ومن أخلاق الأنبياء عليهم السلام: الرضا بقضاء الله تعالى.','2448','622','0');
INSERT INTO [t14592] VALUES (721,'٧٨ - ومنها: إيثار محبة الفقراء والصحبة معهم على صحبة الأغنياء لهذا الحديث المذكور.','2449','622','0');
INSERT INTO [t14592] VALUES (722,'٧٩ - وكذلك من أعمالهم: تشييع الجنائز، وتعزية الحي بالميت.','2451','622','0');
INSERT INTO [t14592] VALUES (723,'٨٠ - ومنها: مساعدة الضعفاء وقضاء حوائج المسلمين.','2452','622','0');
INSERT INTO [t14592] VALUES (724,'٨١ - ومنها: أن الأنبياء عليهم السلام لا يتطلعون في عمل صالح إلى عرض من الدنيا - قل أو جل - سواء في ذلك التبليغ وغيره.','2453','622','0');
INSERT INTO [t14592] VALUES (725,'٨٢ - ومنها: أنهم حيث لا يريدون بأعمالهم إلا وجه الله تبارك وتعالى فلا يتقربون من كل نوع من أنواع القربات إليه إلا بأحسنها وأحبها إليه، ولا يؤثرون أنفسهم عليه بشيء لأن رغبتهم إليه وحرصهم عليه؛ فإنهم أخيار الأبرار.','2456','622','0');
INSERT INTO [t14592] VALUES (726,'٨٣ - ومنها: البداءة بالسلام ورده.','2457','622','0');
INSERT INTO [t14592] VALUES (727,'٨٤ - ومنها: مصافحة الأخ والقريب عند اللقاء، ومعانقته وإظهار البشاشة والبشر والهشاشة.','2458','622','0');
INSERT INTO [t14592] VALUES (728,'٨٥ - ومنها: التبسم في محله من غير قهقهة ولا رفع صوت.','2462','622','0');
INSERT INTO [t14592] VALUES (729,'فائدة زائدة','2466','728','0');
INSERT INTO [t14592] VALUES (730,'٨٦ - ومنها: الخطبة، والتذكير والتحذير من الدجال والفتن والأمور المحذورة.','2466','622','0');
INSERT INTO [t14592] VALUES (731,'٨٧ - ومنها: اتخاذ المنبر والعصا.','2468','622','0');
INSERT INTO [t14592] VALUES (732,'٨٨ - ومنها: اتخاذ الكلب للحراسة ونحوها.','2469','622','0');
INSERT INTO [t14592] VALUES (733,'٨٩ - ومنها: اتخاذ القذافة.','2469','622','0');
INSERT INTO [t14592] VALUES (734,'٩٠ - ومنها: اتخاذ القوس، والرمي عنها بالسهام، وتعلم الرماية للحرب.','2470','622','0');
INSERT INTO [t14592] VALUES (735,'٩١ - ومنها: ارتباط الخيل في سبيل الله، وركوبها لذلك، وإعدادها للحرب.','2471','622','0');
INSERT INTO [t14592] VALUES (736,'٩٢ - ومنها: الجهاد في مجيل الله تعالى.','2472','622','0');
INSERT INTO [t14592] VALUES (737,'٩٣ - ومنها: التفكر والاعتبار، والمسافرة لذلك، والضرب في الأرض لمطالعة آيات الله والتماس رزقه، وتنفيذ أقضيته وأحكامه.','2473','622','0');
INSERT INTO [t14592] VALUES (738,'٩٤ - ومنها: المهاجرة خوفا من الفتنة في الدين.','2474','622','0');
INSERT INTO [t14592] VALUES (739,'٩٥ - سكنى الشام.','2478','622','0');
INSERT INTO [t14592] VALUES (740,'٩٦ - ومنها: المجاورة بمكة المشرفة.','2482','622','0');
INSERT INTO [t14592] VALUES (741,'٩٧ - ومنها: زيارة بيت المقدس.','2484','622','0');
INSERT INTO [t14592] VALUES (742,'فائدة لطيفة','2485','741','0');
INSERT INTO [t14592] VALUES (743,'٩٨ - ومنها: بناء المساجد.','2486','622','0');
INSERT INTO [t14592] VALUES (744,'٩٩ - ملازمة المساجد للصلاة والعلم والتعليم والخير.','2494','622','0');
INSERT INTO [t14592] VALUES (745,'تنبيه','2495','744','0');
INSERT INTO [t14592] VALUES (746,'١٠٠ - ومنها: تعظيم المساجد وتجهيزها وتنظيفها.','2496','622','0');
INSERT INTO [t14592] VALUES (747,'١٠١ - ومنها: السفر للحج، والجهاد، والهجرة، والزيارة، وطلب الصالحين والعلماء، وطلب العلم، وإعداد الزاد، وحسن النية، وصحبة الرفيق، واتباع الطريق، وحفظ الأوقات وسائر آداب السفر.','2497','622','0');
INSERT INTO [t14592] VALUES (748,'١٠٢ - ومنها: قراءة القرآن، وتحسين الصوت به، والتخشع والتحزن عند قراءته.','2498','622','0');
INSERT INTO [t14592] VALUES (749,'١٠٣ - ومنها: صلاة الضحى، والمحافظة على الذكر في الصباح والمساء.','2499','622','0');
INSERT INTO [t14592] VALUES (750,'١٠٤ - ومنها: كثرة الذكر على كل حال وفي كل حين والجلوس في مجالس الذكر.','2501','622','0');
INSERT INTO [t14592] VALUES (751,'١٠٥ - ومنها: الصلاة على النبي - صلى الله عليه وسلم -','2504','622','0');
INSERT INTO [t14592] VALUES (752,'١٠٦ - ومنها: تصديق النبي - صلى الله عليه وسلم - والإيمان به وبما جاء به.','2505','622','0');
INSERT INTO [t14592] VALUES (753,'١٠٧ - ومنها: كتابة العلم.','2505','622','0');
INSERT INTO [t14592] VALUES (754,'تنبيه','2507','753','0');
INSERT INTO [t14592] VALUES (755,'فائدة','2507','753','0');
INSERT INTO [t14592] VALUES (756,'١٠٨ - ومن خصال الأنبياء عليهم السلام: الشكر.','2515','622','0');
INSERT INTO [t14592] VALUES (757,'١٠٩ - ومن خصال الأنبياء عليهم السلام: الصبر بأقسامه الثلاثة.','2521','622','0');
INSERT INTO [t14592] VALUES (758,'تنبيه','2524','757','0');
INSERT INTO [t14592] VALUES (759,'١١٠ - ومن أعمال الأنبياء عليهم السلام: الدعاء.','2525','622','0');
INSERT INTO [t14592] VALUES (760,'فائدة','2556','759','0');
INSERT INTO [t14592] VALUES (761,'فائدة أخرى','2557','759','0');
INSERT INTO [t14592] VALUES (762,'تنبيه','2557','759','0');
INSERT INTO [t14592] VALUES (763,'١١١ - ترصد أوقات الإجابة، والأحوال الشريفة، والأمكنة العظيمة للدعاء، والمحافظة على آداب الدعاء','2559','622','0');
INSERT INTO [t14592] VALUES (764,'١١٢ - ومن آدابهم: رفع اليدين وبسطهما في الدعاء.','2561','622','0');
INSERT INTO [t14592] VALUES (765,'١١٣ - ومن آدابهم: تصدير الدعاء باسم من أسماء الله تعالى يليق بالترحم والتلطف، أو بما يوافق الدعاء المدعو به.','2562','622','0');
INSERT INTO [t14592] VALUES (766,'١١٤ - ومن آدابهم: الإشارة إلى الحاجة دون التصريح في الدعاء إلا أن يكون الحال يقتضى الانبساط إلى الله تعالى، كما في قول أيوب عليه السلام: {مسني الضر وأنت أرحم الراحمين} [الأنبياء: ٨٣].','2563','622','0');
INSERT INTO [t14592] VALUES (767,'١١٥ - ومن آدابهم: الاختصار في الدعاء والاختيار لجوامعه، وأدعية الأنبياء عليهم السلام في القرآن كلها جوامع.','2565','622','0');
INSERT INTO [t14592] VALUES (768,'١١٦ - ومن آدابهم: تكرار الدعاء ثلاثا كما في هذا الأثر.','2566','622','0');
INSERT INTO [t14592] VALUES (769,'١١٧ - ومن آدابهم: أنهم يسألون الحاجات عند الاضطرار.','2566','622','0');
INSERT INTO [t14592] VALUES (770,'١١٨ - ومن آدابهم: الإسرار في الدعاء والتملق إلى الله تعالى بضعف الحال، والتقرب إلى الله تعالى في الدعاء بما له عليهم من سوابق.','2567','622','0');
INSERT INTO [t14592] VALUES (771,'١١٩ - ومن آدابهم: التوسل إلى الله تعالى بصالح أعمالهم إذا كان الوقت يقتضي الادلال والانبساط','2567','622','0');
INSERT INTO [t14592] VALUES (772,'١٢٠ - ومن آدابهم: البداءة في الدعاء بنفس الداعي، وتعميم الدعاء للمؤمنين.','2568','622','0');
INSERT INTO [t14592] VALUES (773,'١٢١ - ومن آدابهم: التأمين على الدعاء.','2568','622','0');
INSERT INTO [t14592] VALUES (774,'تنبيه لطيف','2569','773','0');
INSERT INTO [t14592] VALUES (775,'١٢٢ - ومن خصال الأنبياء عليهم السلام: الاستمطار والاستسقاء لكافة الخلق.','2571','622','0');
INSERT INTO [t14592] VALUES (776,'١٢٣ - ومنها: الاستسقاء بالصالحين كما تقدم قريبا عن عيسى عليه السلام.','2573','622','0');
INSERT INTO [t14592] VALUES (777,'١٢٤ - ومن أخلاق الأنبياء عليهم السلام: ترك التداوي ثقة بالله تعالى، واعتمادا عليه، وفعل التداوي تنفيذا لحكم الله تعالى، وإظهارا لما استودعه في الأدوية من المنافع من غير اعتماد عليها ولا على من يشير بها من طبيب ونحوه.','2575','622','0');
INSERT INTO [t14592] VALUES (778,'تنبيه','2579','777','0');
INSERT INTO [t14592] VALUES (779,'١٢٥ - ومن أخلاق الأنبياء عليهم السلام: ترك التضجر والتأوه في المرض.','2580','622','0');
INSERT INTO [t14592] VALUES (780,'١٢٦ - ومنها: قصر الأمل، وتوقع الموت خصوصا للمريض.','2581','622','0');
INSERT INTO [t14592] VALUES (781,'١٢٧ - ومنها: الوصية عند الموت للأولاد والأهل بالمحافظة على الدين.','2581','622','0');
INSERT INTO [t14592] VALUES (782,'١٢٨ - ومنها: الحذر من الموت على غرة، والدعاء بتهوين سكرات الموت.','2583','622','0');
INSERT INTO [t14592] VALUES (783,'١٢٩ - ومنها: إخراج ما عسى أن يكون عندهم من أمتعة الدنيا قبل الموت، وخصوصا عند الموت.','2584','622','0');
INSERT INTO [t14592] VALUES (784,'١٣٠ - ومنها: تفريغ القلب لملاقاة الله من كل ما سواه من زوجة وولد ومال وسائر أمور الدنيا.','2584','622','0');
INSERT INTO [t14592] VALUES (785,'فائدة','2586','784','0');
INSERT INTO [t14592] VALUES (786,'وهنا تنبيهات','2588','784','0');
INSERT INTO [t14592] VALUES (787,'الأول','2588','786','0');
INSERT INTO [t14592] VALUES (788,'الثاني','2590','786','0');
INSERT INTO [t14592] VALUES (789,'الثالث','2591','786','0');
INSERT INTO [t14592] VALUES (790,'الرابع','2591','786','0');
INSERT INTO [t14592] VALUES (791,'وهذه خاتمة لطيفة لهذا الكتاب','2593','784','0');
INSERT INTO [t14592] VALUES (792,'فائدتان','2610','791','0');
INSERT INTO [t14592] VALUES (793,'الأولى','2610','792','0');
INSERT INTO [t14592] VALUES (794,'الثانية','2610','792','0');
INSERT INTO [t14592] VALUES (795,'تتمة','2611','791','0');
INSERT INTO [t14592] VALUES (796,'(٧) باب ذكر أخلاق رسول الله - صلى الله عليه وسلم -','2612','46','0');
INSERT INTO [t14592] VALUES (797,'تنبيه','2621','796','0');
INSERT INTO [t14592] VALUES (798,'ومن هنا نذكر أخلاق النبي - صلى الله عليه وسلم -','2622','796','0');
INSERT INTO [t14592] VALUES (799,'القسم الثاني من الكتاب في النهي عن التشبه بمن ورد النهي عن التشبه بهم, والنهي عن طرقهم','2716','0','0');
INSERT INTO [t14592] VALUES (800,'النوع الأول من القسم الثاني في النهي عن التشبه بالشيطان "لعنه الله تعالى"','2721','799','0');
INSERT INTO [t14592] VALUES (801,'١ - فمنها: الكفر بكل أنواعه','2737','800','0');
INSERT INTO [t14592] VALUES (802,'تنبيه','2739','801','0');
INSERT INTO [t14592] VALUES (803,'٢ - ومن أعمال فجرة الجن: مسيس بني آدم بالصرع، والقتل، والأمراض، وغير ذلك.','2758','800','0');
INSERT INTO [t14592] VALUES (804,'تنبيه','2767','803','0');
INSERT INTO [t14592] VALUES (805,'تتمة','2777','803','0');
INSERT INTO [t14592] VALUES (806,'٣ - ومن أخلاق الشيطان - لعنه الله تعالى - الدعاء إلى الكفر، بل هو أشد من الكفر.','2777','800','0');
INSERT INTO [t14592] VALUES (807,'٤ - ومن أخلاق الشيطان الرجيم: نية السوء، وإضماره للعباد.','2781','800','0');
INSERT INTO [t14592] VALUES (808,'٥ - ومنها - وهو أخصها وأجمعها -: الإغراء، والأمر بالمعاصي.','2781','800','0');
INSERT INTO [t14592] VALUES (809,'٦ - ومنها: الاستزلال، والتغرير.','2782','800','0');
INSERT INTO [t14592] VALUES (810,'تنبيه','2784','809','0');
INSERT INTO [t14592] VALUES (811,'٧ - ومن أخلاق الشيطان لعنه الله تعالى: الرضا بالمعصية، والسخط بالطاعات، والغيظ منها.','2785','800','0');
INSERT INTO [t14592] VALUES (812,'٨ - ومنها: الابتداع في الدين.','2786','800','0');
INSERT INTO [t14592] VALUES (813,'٩ - ومنها: إنكار البعث، والجنة، والنار.','2786','800','0');
INSERT INTO [t14592] VALUES (814,'١٠ - ومنها: التكذيب بالقضاء والقدر.','2787','800','0');
INSERT INTO [t14592] VALUES (815,'١١ - ومنها: اعتقاد كون الأسباب مؤثرة بأنفسها في المسببات، وأنها هي الفاعلة، واعتقاد المسلمين أن الله تعالى يوجد الأشياء عند','2787','800','0');
INSERT INTO [t14592] VALUES (816,'١٢ - ومنها: إنكار قدرة الله تعالى على كل الممكنات، كما يفهم من اقتراح اللعين على عيسى عليه السلام أن يرد الله الجبل خبزا.','2788','800','0');
INSERT INTO [t14592] VALUES (817,'تنبيه','2789','816','0');
INSERT INTO [t14592] VALUES (818,'١٣ - ومنها: الحيلولة بين العبد وبين التفكر في آيات الله تعالي، ومخلوقاته، ومصنوعاته.','2790','800','0');
INSERT INTO [t14592] VALUES (819,'١٤ - ومنها: التشكيك في الدين.','2790','800','0');
INSERT INTO [t14592] VALUES (820,'١٥ - ومن أخلاق الشيطان: كفران النعم.','2793','800','0');
INSERT INTO [t14592] VALUES (821,'١٦ - ومنها: التكبر الحامل للعبد على الامتناع، والخروج عن الطاعة، واتباع الحق.','2795','800','0');
INSERT INTO [t14592] VALUES (822,'لطيفتان','2796','821','0');
INSERT INTO [t14592] VALUES (823,'الأولى','2796','822','0');
INSERT INTO [t14592] VALUES (824,'الثانية','2797','822','0');
INSERT INTO [t14592] VALUES (825,'١٧ - ومن أخلاق الشيطان: رؤية النفس وتزكيتها، والأعجاب بها،','2797','800','0');
INSERT INTO [t14592] VALUES (826,'تنبيه','2799','825','0');
INSERT INTO [t14592] VALUES (827,'١٨ - ومن أخلاق اللعين: دعاء الغير إلى تزكية النفس، ورؤيتها، والإعجاب بها.','2800','800','0');
INSERT INTO [t14592] VALUES (828,'١٩ - ومنها -وهو من جنس ما تقدم -: دعوى الأحوال الشريفة والمقامات العالية، وهو على خلافها.','2800','800','0');
INSERT INTO [t14592] VALUES (829,'٢٠ - ومنها: تسخط المقدور، وعدم الرضا بالقسمة، والحسد.','2801','800','0');
INSERT INTO [t14592] VALUES (830,'٢١ - ومنها: الحقد؛ وهو غاية الحسد.','2804','800','0');
INSERT INTO [t14592] VALUES (831,'٢٢ - ومنها: اللجاج؛ وهو ملازمة الأمر وعدم الانصراف عنه.','2805','800','0');
INSERT INTO [t14592] VALUES (832,'تنبيه','2806','831','0');
INSERT INTO [t14592] VALUES (833,'لطيفة','2810','831','0');
INSERT INTO [t14592] VALUES (834,'٢٣ - ومن أخلاق الشيطان: العجلة، والطيش، والإنسان بطبعه عجول.','2811','800','0');
INSERT INTO [t14592] VALUES (835,'تنبيه','2813','834','0');
INSERT INTO [t14592] VALUES (836,'٢٤ - ومن أفعال الشيطان الرجيم: قتل النفس التي حرم الله، والدعاء إليه، والمعاونة فيه.','2813','800','0');
INSERT INTO [t14592] VALUES (837,'تنبيه','2816','836','0');
INSERT INTO [t14592] VALUES (838,'٢٥ - ومن أخلاق اللعين: كراهية النكاح والتزوج، ومحبة العزوبة من كل أحد؛ لأنه يتمكن بالخواطر الشهوانية إذا لم يكن له حليلة.','2816','800','0');
INSERT INTO [t14592] VALUES (839,'٢٦ - ومن قبائح الشيطان: الزنا والأمر به.','2818','800','0');
INSERT INTO [t14592] VALUES (840,'لطيفة','2819','839','0');
INSERT INTO [t14592] VALUES (841,'٢٧ - ومن قبائح الشيطان: التلوط به، والدعاء إلى نكاح نفسه.','2822','800','0');
INSERT INTO [t14592] VALUES (842,'٢٨ - ومنها: العبث بمذاكير نفسه، أو بمذاكير غيره اجتلابا للمني.','2822','800','0');
INSERT INTO [t14592] VALUES (843,'٢٩ - ومنها: العبث بدبر نفسه أو بدبر غيره بقصد الشهوة.','2823','800','0');
INSERT INTO [t14592] VALUES (844,'٣٠ - ومن قبائح الشيطان: التشبه بالنساء.','2827','800','0');
INSERT INTO [t14592] VALUES (845,'٣١ - ومنها: القيادة بين الرجال والنساء، وبين الرجال والمرد، وكلاهما من الكبائر، والثاني أقبح.','2827','800','0');
INSERT INTO [t14592] VALUES (846,'٣٢ - ومنها: صحبة الأحداث، والنظر إلى الجميل منهم،','2829','800','0');
INSERT INTO [t14592] VALUES (847,'٣٣ - ومن أخلاق الشيطان لعنه الله: الكذب.','2833','800','0');
INSERT INTO [t14592] VALUES (848,'تنبيه','2834','847','0');
INSERT INTO [t14592] VALUES (849,'٣٤ - ومنها: التلبس بزي غيره إيهاما أنه غيره.','2835','800','0');
INSERT INTO [t14592] VALUES (850,'تنبيه','2835','849','0');
INSERT INTO [t14592] VALUES (851,'٣٥ - ومنها: الكذب على رسول الله - صلى الله عليه وسلم -، أو على غيره من الأنبياء عليهم السلام وهو -وإن كان داخلا في الكذب- إلا أني نبهت عليه على حدة لمزيد الاعتناء بالزجر عنه، والتنفير منه.','2836','800','0');
INSERT INTO [t14592] VALUES (852,'٣٦ - ومنها: التكذيب بالحق.','2837','800','0');
INSERT INTO [t14592] VALUES (853,'٣٧ - ومنها: مجادلة الناس بغير حق.','2838','800','0');
INSERT INTO [t14592] VALUES (854,'٣٨ - ومنها: مصادمة النص بالقياس، وتقديم الرأي على النص.','2839','800','0');
INSERT INTO [t14592] VALUES (855,'٣٩ - ومنها: محبة البدعة، والدعاء إليها، وذلك من الكبائر، ومجالسة أهل البدعة ومعاشرتهم لغير ضرورة.','2839','800','0');
INSERT INTO [t14592] VALUES (856,'٤٠ - ومنها: محبة الفتنة، والإشارة بها وبقتل المؤمن، والسعي في إهلاك خيار العباد والمكر بهم.','2841','800','0');
INSERT INTO [t14592] VALUES (857,'٤١ - ومنها: الغش.','2844','800','0');
INSERT INTO [t14592] VALUES (858,'تنبيه','2845','857','0');
INSERT INTO [t14592] VALUES (859,'٤٢ - ومنها: الخديعة والمكر.','2846','800','0');
INSERT INTO [t14592] VALUES (860,'٤٣ - ومنها: اليمين الغموس.','2848','800','0');
INSERT INTO [t14592] VALUES (861,'٤٤ - ومنها: الحلف بغير الله تعالى.','2850','800','0');
INSERT INTO [t14592] VALUES (862,'تنبيه','2852','861','0');
INSERT INTO [t14592] VALUES (863,'٤٥ - ومنها: التصميم على اليمين وغيرها خير منها، فإن كانت اليمين على محرم فتركها واجب، وإلا فسنة، والكفارة فيهما.','2853','800','0');
INSERT INTO [t14592] VALUES (864,'٤٦ - ومنها: قلة المبالاة بحنث اليمين إلا في خير، وترك تكفير اليمين المحنوث فيها.','2854','800','0');
INSERT INTO [t14592] VALUES (865,'٤٧ - ومنها: إيقاع الناس في الكذب والحنث.','2854','800','0');
INSERT INTO [t14592] VALUES (866,'٤٨ - ومنها: أن يحول بين العبد وبين الوفاء بالعهد أو باليمين أو بالنذر، وبينه وبين إخراج ما نوى أن يتصدق به، وإذا خلا رجل بامرأة كان ثالثهما، ويلازم المعجبين بآرائهم، ويصوب إعجابهم، فالمؤمن عليه أن يحذر هذه الأخلاق الشيطانية.','2855','800','0');
INSERT INTO [t14592] VALUES (867,'٤٩ - ومنها: النذر في المعصية.','2856','800','0');
INSERT INTO [t14592] VALUES (868,'٥٠ - ومنها: الجهل بالله تعالى وبعظمته، وهذه أخص أوصاف الرجيم،','2857','800','0');
INSERT INTO [t14592] VALUES (869,'٥١ - ومنها: الفحش، والبذاء، والوقاحة، وقلة الحياء، بل عدمه بالكلية، وهذا مما لا شك فإن الشيطان أول من ألقى جلباب الحياء من الله ومن الناس.','2858','800','0');
INSERT INTO [t14592] VALUES (870,'تنبيه','2859','869','0');
INSERT INTO [t14592] VALUES (871,'٥٢ - ومن أخلاق الشيطان: الامتناع من السجود لله تعالى، وإن شئت فقل: الامتناع من الصلاة، فتارك الصلاة أشبه الناس بالشيطان لأنه أمر بالسجود فلم يسجد.','2860','800','0');
INSERT INTO [t14592] VALUES (872,'تنبيه','2862','871','0');
INSERT INTO [t14592] VALUES (873,'فائدة','2863','871','0');
INSERT INTO [t14592] VALUES (874,'٥٣ - ومن أخلاق اللعين: كراهية السجود من غيره، وعيبه واستقباحه.','2864','800','0');
INSERT INTO [t14592] VALUES (875,'٥٤ - ومن أعماله لعنه الله تعالى: الصد عن ذكر الله تعالى، وعن الصلاة، أو عن غيرها من الطاعات وأعمال الخير.','2865','800','0');
INSERT INTO [t14592] VALUES (876,'محذرة','2867','875','0');
INSERT INTO [t14592] VALUES (877,'٥٥ - ومنها -وهو نوع مما تقدم-: القعود على عقيصة شعر المصلي، ولذلك كره أن يصلي الرجل وشعره معقوص.','2867','800','0');
INSERT INTO [t14592] VALUES (878,'٥٦ - ومنها: المرور بين يدي المصلي، وهو حرام على من مر بين المصلي وبين سترته، إذا كان بينه وبين السترة قدر ثلاثة أذرع فما دونها.','2868','800','0');
INSERT INTO [t14592] VALUES (879,'تنبيه','2871','878','0');
INSERT INTO [t14592] VALUES (880,'٥٧ - ومن أعمال الشيطان -لعنه الله- العبث بكل طائع في كل طاعة أمكنه العبث به ليشغله عن طاعته أو يفسدها.','2872','800','0');
INSERT INTO [t14592] VALUES (881,'٥٨ - ومن أخلاقه -لعنه الله-: الغفلة عن ذكر الله تعالى، ولا سيما إذا استقلت الشمس.','2873','800','0');
INSERT INTO [t14592] VALUES (882,'٥٩ - ومنها: الفرار من الأماكن التي يقرأ فيها القرآن العظيم، مع الإقبال على المجالس التي يضرب فيها بالآلات، ويتغنى فيها بأنواع التلحينات.','2874','800','0');
INSERT INTO [t14592] VALUES (883,'٦٠ - ومنها: الفرار من الأذان وعدم إجابة المؤذن واستماعه، والتلهي عن سماعه، والخروج من المسجد بعد الأذان وقبل الصلاة لغير ضرورة، وكل ذلك مكروه، وفاعله متشبه بالشيطان.','2875','800','0');
INSERT INTO [t14592] VALUES (884,'تنبيه','2876','883','0');
INSERT INTO [t14592] VALUES (885,'٦١ - ومن أخلاق اللعين: إنساء العبد أن يذكر ربه في شدائده وحاجاته، فيلقي في قلب العبد طلب الغوث والحاجة من العبد لما له من الجاه أو الكلمة أو القوة.','2878','800','0');
INSERT INTO [t14592] VALUES (886,'٦٢ - ومن قبائح أخلاق الشيطان: حب الدنيا، والدرهم والدينار، وتحبيبها إلى الخلق','2887','800','0');
INSERT INTO [t14592] VALUES (887,'تنبيه','2889','886','0');
INSERT INTO [t14592] VALUES (888,'٦٣ - ومن قبائح أخلاق الشيطان: البخل، وحمل الناس عليه.','2891','800','0');
INSERT INTO [t14592] VALUES (889,'تنبيه','2895','888','0');
INSERT INTO [t14592] VALUES (890,'٦٤ - ومن أخلاق الشيطان - وهو من جنس ما تقدم -: النهي عن الصدقة لمن تطلب منه الصدقة لا سيما الزكاة.','2896','800','0');
INSERT INTO [t14592] VALUES (891,'تنبيه','2898','890','0');
INSERT INTO [t14592] VALUES (892,'٦٥ - ومن أعمال الشيطان: التبذير والإسراف، والأمر بذلك.','2899','800','0');
INSERT INTO [t14592] VALUES (893,'تنبيهان','2902','892','0');
INSERT INTO [t14592] VALUES (894,'٦٦ - ومن أعمال الشيطان: شرب الخمر، وتناول المسكرات، والقمار، واللعب بالنرد ونحوه مطلقا، واللعب بالشطرنج إذا اقترن بمحرم، والتكهن، والتنجيم، والتطير، والزجر، والطرق، والعيافة، ونحو ذلك.','2909','800','0');
INSERT INTO [t14592] VALUES (895,'تنبيهان','2918','894','0');
INSERT INTO [t14592] VALUES (896,'٦٧ - ومن أعمال الشياطين لعنهم الله: عمل السحر، وعلمه وتعلمه وتعليمه، وهي منه.','2922','800','0');
INSERT INTO [t14592] VALUES (897,'٦٨ - ومنها: النشرة.','2925','800','0');
INSERT INTO [t14592] VALUES (898,'٦٩ - ومنها: سائر أنواع الرقى إلا الرقية بذكر الله تعالى وما يعرف معناه مما يسوغ، وكذلك الإشارة بالرقية إلا ما ذكر.','2926','800','0');
INSERT INTO [t14592] VALUES (899,'تنبيه','2928','898','0');
INSERT INTO [t14592] VALUES (900,'٧٠ - ومن أعمال الشيطان: تصوير ما فيه روح، والأمر بذلك؛ وهو من الكبائر','2931','800','0');
INSERT INTO [t14592] VALUES (901,'٧١ - ومن أخلاق الشيطان: إيقاع العداوة والبغضاء بين الناس، والنميمة، وإتيان هؤلاء بوجه وهؤلاء بوجه.','2933','800','0');
INSERT INTO [t14592] VALUES (902,'تنبيهان','2934','901','0');
INSERT INTO [t14592] VALUES (903,'الأول','2934','902','0');
INSERT INTO [t14592] VALUES (904,'الثاني','2935','902','0');
INSERT INTO [t14592] VALUES (905,'٧٢ - ومنها: اعتياد الشر والأذى.','2936','800','0');
INSERT INTO [t14592] VALUES (906,'٧٣ - ومنها: التشاتم والتساب.','2937','800','0');
INSERT INTO [t14592] VALUES (907,'فائدة','2937','906','0');
INSERT INTO [t14592] VALUES (908,'٧٤ - ومنها: عدم المبالاة بما قال، وما قيل له.','2938','800','0');
INSERT INTO [t14592] VALUES (909,'٧٥ - ومنها: حضور مجالس أهل الجور من القضاة والولاة.','2939','800','0');
INSERT INTO [t14592] VALUES (910,'٧٦ - ومنها: حضور مجالس الغضب والخصومات التي لا خير فيها.','2941','800','0');
INSERT INTO [t14592] VALUES (911,'٧٧ - ومنها: الدخول على الملوك والسلاطين والأمراء بغير ضرورة، والتأويل في ذلك، والإشارة بذلك.','2942','800','0');
INSERT INTO [t14592] VALUES (912,'٧٨ - ومنها: دلالة أعداء المسلمين على عوراتهم، والسعي في أذيتهم؛ وكل ذلك من الكبائر.','2943','800','0');
INSERT INTO [t14592] VALUES (913,'٧٩ - ومنها: تثبيت أعداء المسلمين على قتالهم واستثارتهم لذلك.','2946','800','0');
INSERT INTO [t14592] VALUES (914,'٨٠ - ومن أعمال الرجيم: تخبيب الولد على أبيه، والعبد على سيده، والمرأة على سيدها، والرجل على زوجته؛ وكل ذلك حرام.','2949','800','0');
INSERT INTO [t14592] VALUES (915,'٨١ - ومن أعمال الرجيم وأخلاقه: مصادقة من أصر على مصارمة أخيه المسلم وهجره بغير حق، ورد التحية على من لم يستحقها.','2953','800','0');
INSERT INTO [t14592] VALUES (916,'٨٢ - ومنها: التجسس والاستماع إلى حديث قوم يكرهون سماعه.','2954','800','0');
INSERT INTO [t14592] VALUES (917,'٨٣ - ومنها: إيقاع الناس في التهمة وسوء الظن فيمن لا يساء به الظن.','2954','800','0');
INSERT INTO [t14592] VALUES (918,'٨٤ - ومنها: إساءة الظن بالله تعالى، وبأوليائه ومن لا يساء به الظن','2955','800','0');
INSERT INTO [t14592] VALUES (919,'٨٥ - ومنها: حمل الإنسان على الأشر والبطر، والفخر والخيلاء، والكبر واتباع الهوى.','2956','800','0');
INSERT INTO [t14592] VALUES (920,'٨٦ - ومنها: تمنية الإنسان بما لا يليق به، أو ما يليق به ولا يستطيعه خصوصا من أمور الدنيا.','2957','800','0');
INSERT INTO [t14592] VALUES (921,'٨٧ - ومنها: تحزين المؤمن، وإدخال الهم والغم عليه.','2957','800','0');
INSERT INTO [t14592] VALUES (922,'فائدة','2961','921','0');
INSERT INTO [t14592] VALUES (923,'٨٨ - ومن أعماله قبحه الله: تخويف المؤمن وازعاجه وترويعه؛ وكل ذلك حرام.','2964','800','0');
INSERT INTO [t14592] VALUES (924,'٨٩ - ومن أعمال اللعين وأخلاقه: إيذاء المؤمن في بدنه وأهله وولده وماله، والتصرف في ملك الغير بغير إذنه خصوصا بالإتلاف والإفساد، وقسوة القلب على خلق الله تعالى، وعدم الرحمة والشفقة.','2969','800','0');
INSERT INTO [t14592] VALUES (925,'فائدة','2974','924','0');
INSERT INTO [t14592] VALUES (926,'فائدة أخرى','2977','924','0');
INSERT INTO [t14592] VALUES (927,'تنبيهان','2978','924','0');
INSERT INTO [t14592] VALUES (928,'٩٠ - ومن أخلاق اللعين: الظلم والجور والعسف كما يدل عليه فعله بأيوب عليه السلام.','2980','800','0');
INSERT INTO [t14592] VALUES (929,'٩١ - ومن أخلاق الشيطان لعنه الله تعالى: السعي في أذى المسلم والسعاية به، والمعاونة عليه في باطل.','2981','800','0');
INSERT INTO [t14592] VALUES (930,'٩٢ - ومنها: التزوير كما تقدم عن ابن عباس رضي الله تعالى عنهما في تزوير الشيطان على سليمان عليه السلام علم السحر.','2981','800','0');
INSERT INTO [t14592] VALUES (931,'٩٣ - ومنها: تغليط العلماء، والتزوير عليهم، ونسبة الاعتقاد السيئ إليهم.','2982','800','0');
INSERT INTO [t14592] VALUES (932,'٩٤ - ومنها: التزوير على ولاية القضاء والحكم، والحكم بين الناس بالباطل؛ وذلك كله من الكبائر.','2983','800','0');
INSERT INTO [t14592] VALUES (933,'٩٥ - ومنها: استحلال الحرام وتحريم الحلال.','2984','800','0');
INSERT INTO [t14592] VALUES (934,'٩٦ - ومنها: أكل الحرام.','2985','800','0');
INSERT INTO [t14592] VALUES (935,'٩٧ - ومنها: غضب أثواب الناس وأمتعتهم؛ وهو من الكبائر.','2985','800','0');
INSERT INTO [t14592] VALUES (936,'٩٨ - ومنها: السرقة؛ وهي كبيرة.','2986','800','0');
INSERT INTO [t14592] VALUES (937,'٩٩ - ومنها: الاعتذار بكثرة العيال وغلبة الدين عن الدخول في الحرام والشبهات.','2987','800','0');
INSERT INTO [t14592] VALUES (938,'١٠٠ - ومن أخلاق الشيطان قبحه الله: منع فضل الماء عن ابن السبيل؛ وهو كبيرة أيضا.','2990','800','0');
INSERT INTO [t14592] VALUES (939,'١٠١ - ومنها: قطع الطريق، وإضلال المسافرين في طاعة الله تعالى.','2991','800','0');
INSERT INTO [t14592] VALUES (940,'١٠٢ - ومنها: السفر وحده أو مع ثان.','2992','800','0');
INSERT INTO [t14592] VALUES (941,'تنبيه','2993','940','0');
INSERT INTO [t14592] VALUES (942,'١٠٣ - ومنها: تلبية الجاهلية؛ وهي كفر صراح.','2994','800','0');
INSERT INTO [t14592] VALUES (943,'١٠٤ - ومنها: استيطان الشيطان الأمكن المستقذرة كالكنف، والحمامات، والمزابل، والحانات - بالمهملة - وفي معناها بيوت القهوة المتخذة من البن - وإن كانت القهوة في نفسها مباحة - فإن بيوتها مأوى الشياطين.','2995','800','0');
INSERT INTO [t14592] VALUES (944,'١٠٥ - ومنها: إطالة المكث في الحمام لغير ضرورة، بل لمجرد التلهي والبطالة، وهو مضر من جهة الطب.','2997','800','0');
INSERT INTO [t14592] VALUES (945,'١٠٦ - ومنها: القعود في الأسواق لغير غرض صحيح.','2998','800','0');
INSERT INTO [t14592] VALUES (946,'تنبيهان','2999','945','0');
INSERT INTO [t14592] VALUES (947,'الأول','2999','946','0');
INSERT INTO [t14592] VALUES (948,'الثاني','3000','946','0');
INSERT INTO [t14592] VALUES (949,'١٠٧ - ومن أخلاق الشيطان وأعمالة: التبكير إلى الأسواق، والتأخر في الانصراف منها.','3002','800','0');
INSERT INTO [t14592] VALUES (950,'تنبيه','3004','949','0');
INSERT INTO [t14592] VALUES (951,'١٠٨ - ومن أخلاق الشيطان: ترك القيلولة؛ وهي النوم وسط النهار، وهي مستحبة لقيام الليل.','3005','800','0');
INSERT INTO [t14592] VALUES (952,'١٠٩ - ومنها: الانتشار من غروب الشمس إلى أن تذهب فحمة العشا - أي: ظلمتها - من غير ضرورة.','3006','800','0');
INSERT INTO [t14592] VALUES (953,'١١٠ - ومنها: السهر في غير فائدة.','3007','800','0');
INSERT INTO [t14592] VALUES (954,'١١١ - ومنها: تسهير أهل المعصية والغفلة، وكراهية نومهم.','3008','800','0');
INSERT INTO [t14592] VALUES (955,'تنبيه','3008','954','0');
INSERT INTO [t14592] VALUES (956,'١١٢ - ومنها: تنويم أهل الطاعة عن الطاعة.','3009','800','0');
INSERT INTO [t14592] VALUES (957,'فائدة لها مناسبة تامة بهذا المحل','3014','956','0');
INSERT INTO [t14592] VALUES (958,'تنبيه','3018','956','0');
INSERT INTO [t14592] VALUES (959,'١١٣ - ومن أعمال الشيطان وأخلاقه: افتتاح المجالس والأمور وختمها بالشر، ومحبة ذلك من غيره.','3021','800','0');
INSERT INTO [t14592] VALUES (960,'١١٤ - ومن أخلاقه لعنه الله: بغض العلماء والصالحين.','3022','800','0');
INSERT INTO [t14592] VALUES (961,'١١٥ - ومن أخلاقه لعنه الله تعالى: تطويل أمل العالم حتى يدع العمل معتذرا عنه بطلب العلم، وهذا من جملة أغلاط العلماء.','3025','800','0');
INSERT INTO [t14592] VALUES (962,'١١٦ - ومنها: الفرح بموت العلماء العاملين، والفقهاء الزاهدين، والصلحاء العابدين، ولكن فرحه بموت العلماء والفقهاء أشد، وهذا من لازم بغضهم.','3026','800','0');
INSERT INTO [t14592] VALUES (963,'١١٧ - ومنها: إطالة الأمل للعاصي حتى يسوف بالتوبة والطاعة، وللغني حتى يسوف بالحج، والصدقة، والإنفاق في وجوه الخير.','3027','800','0');
