INSERT INTO [t14592] VALUES (1090,'٩ - ومنها: المكر، وهو كبيرة.','3325','1081','0');
INSERT INTO [t14592] VALUES (1091,'١٠ - ومنها: إضلال الناس، وإغواؤهم، ومنعهم عن الإيمان بالله تعالى، وعن طاعته، والدعوة إلى معصيته، واتباع الأئمة المضلين.','3326','1081','0');
INSERT INTO [t14592] VALUES (1092,'١١ - ومنها: الإعراض عن سماع الموعظة.','3328','1081','0');
INSERT INTO [t14592] VALUES (1093,'١٢ - ومنها: بغض النصحاء.','3329','1081','0');
INSERT INTO [t14592] VALUES (1094,'١٣ - ومنها: الإصرار على المعصية، وترك التوبة والاستغفار.','3329','1081','0');
INSERT INTO [t14592] VALUES (1095,'١٤ - ومنها: الاستكبار.','3330','1081','0');
INSERT INTO [t14592] VALUES (1096,'١٥ - ومنها: مقابلة الإحسان بالإساءة.','3331','1081','0');
INSERT INTO [t14592] VALUES (1097,'١٦ - ومنها: الوقاحة، والتجري على الأكابر، وعدم توقيرهم، وتجرئة الصغار عليهم، وحمل الأطفال على قبائح الأعمال.','3334','1081','0');
INSERT INTO [t14592] VALUES (1098,'١٧ - ومنها: استبعاد اختصاص الله تعالى بعض عباده بفضيلة العلم والحكمة، أو نحو ذلك.','3336','1081','0');
INSERT INTO [t14592] VALUES (1099,'١٨ - ومنها: النظر إلى ظاهر الهيئة، واعتبار أن خسة الحرفة أو رثاثة الهيئة مانع من الاختصاص بالفضيلة.','3338','1081','0');
INSERT INTO [t14592] VALUES (1100,'١٩ - ومنها: إنكار فضل ذوي الفضل.','3340','1081','0');
INSERT INTO [t14592] VALUES (1101,'٢٠ - ومنها: الاستنكاف عن مجالسة الفقراء، وأداني الناس من حيث الحرفة وظاهر الهيئة لا في الدين، وإزعاجهم من المجالس،','3340','1081','0');
INSERT INTO [t14592] VALUES (1102,'تنبيه','3342','1101','0');
INSERT INTO [t14592] VALUES (1103,'(٣) باب النهي عن التشبه بكنعان بن نوح','3344','1043','0');
INSERT INTO [t14592] VALUES (1104,'١ - فمنها: النفاق.','3351','1103','0');
INSERT INTO [t14592] VALUES (1105,'٢ - ومنها: مخالفة الوالد في الدين والاعتقاد الحق.','3352','1103','0');
INSERT INTO [t14592] VALUES (1106,'تنبيه','3352','1105','0');
INSERT INTO [t14592] VALUES (1107,'٣ - ومنها: عدم المحافظة على ود الوالد والأستاذ.','3353','1103','0');
INSERT INTO [t14592] VALUES (1108,'٤ - ومنها: الاستعداد بالرأي، والإعجاب به، وإيثار رأي النفس على الرأي الصواب، وعلى رأي الوالد والأستاذ والمرشد.','3356','1103','0');
INSERT INTO [t14592] VALUES (1109,'٥ - ومنها: إيثار تدبير نفسه على تدبير الله تعالى،','3357','1103','0');
INSERT INTO [t14592] VALUES (1110,'٦ - ومنها: الالتجاء إلى غير الله تعالى في الشدة.','3358','1103','0');
INSERT INTO [t14592] VALUES (1111,'تتمة','3359','1110','0');
INSERT INTO [t14592] VALUES (1112,'(٤) باب النهي عن التشبه بعاد','3363','1043','0');
INSERT INTO [t14592] VALUES (1113,'١ - فمنها: الكفر، وعبادة الأوثان، وتقليد الآباء في ذلك.','3378','1112','0');
INSERT INTO [t14592] VALUES (1114,'٢ - ومنها: الابتداع في الدين أعم من أن يكون كفرا أو دونه.','3378','1112','0');
INSERT INTO [t14592] VALUES (1115,'٣ - ومنها: الكذب، والتكذيب لأهل الصدق.','3379','1112','0');
INSERT INTO [t14592] VALUES (1116,'٤ - ومنها: العناد، والتصميم على الباطل بعد ظهور الحق.','3380','1112','0');
INSERT INTO [t14592] VALUES (1117,'٥ - ومنها: الأصرار على المعصية، وترك التوبة والاستغفار.','3381','1112','0');
INSERT INTO [t14592] VALUES (1118,'٦ - ومن أعمال عاد، وأخلاقهم: عصيان أولياء الأمور في طاعة الله تعالى، وبغض العلماء وأولياء الله تعالى، والرغبة عنهم، والاستنكاف عن مجالستهم ومعاشرتهم، والرغبة في عشرة الأشرار والمترفين، واتباعهم والانهماك معهم فيما هم فيه.','3385','1112','0');
INSERT INTO [t14592] VALUES (1119,'٧ - ومنها: أذية أنبياء الله وأوليائه، وانتقاصهم، ورميهم بالسوء،','3385','1112','0');
INSERT INTO [t14592] VALUES (1120,'فائدة لطيفة','3387','1119','0');
INSERT INTO [t14592] VALUES (1121,'٨ - ومنها: الإعجاب بالشباب والقوة، والفخر والخيلاء، والتطاول على الناس.','3388','1112','0');
INSERT INTO [t14592] VALUES (1122,'٩ - ومنها: ظلم الناس، والبغي عليهم، وتمكيس أموالهم،','3389','1112','0');
INSERT INTO [t14592] VALUES (1123,'١٠ - ومن أخلاق عاد: تسفيه ذوي الأحلام والعقول، وتجهيل أهل العلم، وتخطئة أهل الصواب.','3400','1112','0');
INSERT INTO [t14592] VALUES (1124,'١١ - ومن أخلاقهم وأعمالهم: البطر، والإكباب على اللهو واللعب، وشرب الخمر، واستماع الغناء، واتخاذ القيان.','3403','1112','0');
INSERT INTO [t14592] VALUES (1125,'١٢ - ومن أعمال عاد، وأخلاقهم: الكيد.','3405','1112','0');
INSERT INTO [t14592] VALUES (1126,'تنبيه','3405','1125','0');
INSERT INTO [t14592] VALUES (1127,'١٣ - ومن أخلاق عاد: الغفلة عن الموت والعقوبة، واستبعاد موعود الله تعالى.','3408','1112','0');
INSERT INTO [t14592] VALUES (1128,'١٤ - ومن أخلاق عاد: انتظار المحبوب والثواب اعتمادا على حسن الظن بالنفس، ونسيان العقوبة على سوء العمل.','3409','1112','0');
INSERT INTO [t14592] VALUES (1129,'١٥ - ومن أخلاق عاد: مكابرتهم، وتصميمهم على ما كانوا عليه من المعاصي مع مشاهدة الآيات، وملاحظة العقوبة، وعدم اتعاظهم بها.','3412','1112','0');
INSERT INTO [t14592] VALUES (1130,'تتمة','3415','1129','0');
INSERT INTO [t14592] VALUES (1131,'(٥) باب النهي عن التشبه بثمود','3421','1043','0');
INSERT INTO [t14592] VALUES (1132,'١ - فمنها: الكفر، والتكذيب، وعبادة الأوثان، والزنا.','3429','1131','0');
INSERT INTO [t14592] VALUES (1133,'٢ - ومنها: محاجة أهل الحق في أصول الديانات ميلا مع الهوى.','3429','1131','0');
INSERT INTO [t14592] VALUES (1134,'٣ - ومنها: الأخذ بالرأي في مصادمة النص.','3431','1131','0');
INSERT INTO [t14592] VALUES (1135,'٤ - ومنها: بغض الناصحين، والأنفة من قول النصيحة.','3432','1131','0');
INSERT INTO [t14592] VALUES (1136,'٥ - ومنها: طاعة المترفين والمفسدين، وموافقتهم على ما هم عليه.','3432','1131','0');
INSERT INTO [t14592] VALUES (1137,'٦ - ومن أخلاق ثمود: التطير بأهل الخير واليمن، أو مطلق الطيرة والتشاؤم.','3435','1131','0');
INSERT INTO [t14592] VALUES (1138,'٧ - ومنها: طاعة النساء.','3436','1131','0');
INSERT INTO [t14592] VALUES (1139,'٨ - ومنها: الوقوع في المعصية والإثم والبلاء رغبة في ذوات الجمال.','3438','1131','0');
INSERT INTO [t14592] VALUES (1140,'٩ - ومنها: القيادة، ودعوة المرأة الرجل إلى نفسها أو إلى غيرها.','3439','1131','0');
INSERT INTO [t14592] VALUES (1141,'١٠ - ومنها: الاغترار بالدنيا، والتأنق في جمعها وبنيانها، وإتقان البنيان وإحكامه أملا وأشرا.','3440','1131','0');
INSERT INTO [t14592] VALUES (1142,'١١ - ومن قبائح ثمود: سوء الأعمال مع طول الأعمار.','3441','1131','0');
INSERT INTO [t14592] VALUES (1143,'١٢ - ومنها: الأشر والبطر، والفرح بالدنيا، والبخل بها، والتأنق في تحصيلها وتحصينها، والشره، والإعجاب بالنفس، وبما لها أو','3442','1131','0');
INSERT INTO [t14592] VALUES (1144,'تنبيه','3446','1143','0');
INSERT INTO [t14592] VALUES (1145,'١٣ - ومن أعمال ثمود، وأخلاقهم: تعيير أهل الدين بحرفتهم ونحوها مما تعده النفوس الطاغية نقصانا.','3447','1131','0');
INSERT INTO [t14592] VALUES (1146,'١٤ - ومنها: اكتساب الإثم، ورمي البريء به.','3448','1131','0');
INSERT INTO [t14592] VALUES (1147,'١٥ - ومنها: الاستكثار من الشر.','3448','1131','0');
INSERT INTO [t14592] VALUES (1148,'١٦ - ومنها: الطغيان.','3449','1131','0');
INSERT INTO [t14592] VALUES (1149,'١٧ - ومنها: نقض عهد الله وميثاقه.','3452','1131','0');
INSERT INTO [t14592] VALUES (1150,'١٨ - ومنها: تضييع الأمانة، والتعدي عليها.','3454','1131','0');
INSERT INTO [t14592] VALUES (1151,'١٩ - ومنها: إقرار أهل المعاصي على معصيتهم، وترك الأمر بالمعروف والنهي عن المنكر.','3456','1131','0');
INSERT INTO [t14592] VALUES (1152,'٢٠ - ومنها: ذبح الحيوان الموقوف','3457','1131','0');
INSERT INTO [t14592] VALUES (1153,'٢١ - ومنها: الاعتداء في الصدقة.','3457','1131','0');
INSERT INTO [t14592] VALUES (1154,'فائدة','3459','1153','0');
INSERT INTO [t14592] VALUES (1155,'(٦) باب النهي عن التشبه بالرهط التسعة من ثمود','3465','1043','0');
INSERT INTO [t14592] VALUES (1156,'١ - فمنها: المكر والفتك.','3470','1155','0');
INSERT INTO [t14592] VALUES (1157,'٢ - ومنها: قرض الدينار والدرهم، وكسرهما.','3471','1155','0');
INSERT INTO [t14592] VALUES (1158,'٣ - ومنها: اتباع عورات الناس، وتقصد فضيحتهم.','3472','1155','0');
INSERT INTO [t14592] VALUES (1159,'٤ - ومنها: التعاون على الإثم، وخصوصا على قتل المؤمن، والتحاض على ذلك، والتحالف عليه.','3472','1155','0');
INSERT INTO [t14592] VALUES (1160,'٥ - ومنها: العزم على القتل، والحلف عليه، والعزم على الكذب والجحود، والحلف عليهما.','3473','1155','0');
INSERT INTO [t14592] VALUES (1161,'تنبيه','3475','1160','0');
INSERT INTO [t14592] VALUES (1162,'(٧) باب النهي عن التشبه بنمرود وقومه','3479','1043','0');
INSERT INTO [t14592] VALUES (1163,'١ - فمنها: لباس ما هو من زي النساء من التاج المتخذ من الحرير المكلل بالدر والياقوت وغير ذلك، ومن الأردية والأقبية المتخذة من ذلك مما يحرم على الرجال.','3487','1162','0');
INSERT INTO [t14592] VALUES (1164,'٢ - ومنها: الدعوة إلى عبادة النفس، وإعطائها فوق حقها.','3488','1162','0');
INSERT INTO [t14592] VALUES (1165,'٣ - ومن أخلاق نمرود: التجبر - وهو التكبر - وقهر الغير، والاستيلاء عليه، أو على ماله، أو عرضه.','3490','1162','0');
INSERT INTO [t14592] VALUES (1166,'٤ - ومن أعمال نمرود وقومه ما لم يرد الشرع به من العقوبات، وخصوصا لمن لا يستحق عقوبة، والمثلة، وتعذيب الناس في غير قصاص ولا تأديب مأذون فيه.','3503','1162','0');
INSERT INTO [t14592] VALUES (1167,'٥ - ومنها: أخذ الرجل بذنب غيره.','3504','1162','0');
INSERT INTO [t14592] VALUES (1168,'٦ - ومنها: اتخاذ الشرط والجلاوزة، ومن يأخذ الناس ويروعهم.','3505','1162','0');
INSERT INTO [t14592] VALUES (1169,'٧ - ومنها: التنجيم والتكهن، وتصديق المنجم والكاهن.','3506','1162','0');
INSERT INTO [t14592] VALUES (1170,'٨ - ومنها: منع أحد الزوجين عن الآخر خشية حصول الولد،','3508','1162','0');
INSERT INTO [t14592] VALUES (1171,'٩ - ومنها: قتل الأطفال، والأمر بقتلهم.','3509','1162','0');
INSERT INTO [t14592] VALUES (1172,'١٠ - ومنها: القتل من حيث هو، والأمر به ما لم يكن قصاصا ولا حدا.','3509','1162','0');
INSERT INTO [t14592] VALUES (1173,'١١ - ومنها: عبادة الكوكب، واعتقاد أنها مؤثرة، وأنها تضر وتنفع.','3509','1162','0');
INSERT INTO [t14592] VALUES (1174,'١٢ - ومنها: اتخاذ الأصنام، وعبادتها.','3510','1162','0');
INSERT INTO [t14592] VALUES (1175,'١٣ - ومنها: اعتقاد أن الحذر يدفع القدر.','3510','1162','0');
INSERT INTO [t14592] VALUES (1176,'١٤ - ومنها: الفرار من الطاعون مع أنه لا يرد شيئا من قدر الله تعالى.','3510','1162','0');
INSERT INTO [t14592] VALUES (1177,'١٥ - ومن قبائح قوم نمرود: تسمية الحق والعدل ظلما في قولهم: {من فعل هذا بآلهتنا إنه لمن الظالمين (٥٩)} [الأنبياء: ٥٩].','3515','1162','0');
INSERT INTO [t14592] VALUES (1178,'١٦ - ومنها: حضور من يضرب، أو يقتل، أو يهان ظلما حيث قالوا: {فأتوا به على أعين الناس لعلهم يشهدون (٦١)} [الأنبياء: ٦١] كما تقدم عن ابن إسحاق.','3515','1162','0');
INSERT INTO [t14592] VALUES (1179,'١٧ - ومن أعمال النماردة: الردة وجحود الحق بعد الاعتراف به.','3517','1162','0');
INSERT INTO [t14592] VALUES (1180,'١٨ - ومنها: العقوبة بحرق النار.','3517','1162','0');
INSERT INTO [t14592] VALUES (1181,'١٩ - ومنها: الإشارة بالأمر من غير روية ولا تأمل، والإشارة بالسوء وبما لا يحل فعله.','3518','1162','0');
INSERT INTO [t14592] VALUES (1182,'٢٠ - ومنها: التقليد لغير من هو قدوة، وضعف الرأي،','3519','1162','0');
INSERT INTO [t14592] VALUES (1183,'٢١ - ومنها: الجهل، والحيرة، والحماقة.','3520','1162','0');
INSERT INTO [t14592] VALUES (1184,'٢٢ - ومنها: الاحتكار.','3521','1162','0');
INSERT INTO [t14592] VALUES (1185,'لطيفة','3523','1184','0');
INSERT INTO [t14592] VALUES (1186,'٢٣ - ومنها: السجود لغير الله تعالى.','3524','1162','0');
INSERT INTO [t14592] VALUES (1187,'لطيفة','3524','1186','0');
INSERT INTO [t14592] VALUES (1188,'(٨) باب النهي عن التشبه بقوم لوط عليه السلام','3526','1043','0');
INSERT INTO [t14592] VALUES (1189,'١ - منها: الكفر بالله تعالى.','3528','1188','0');
INSERT INTO [t14592] VALUES (1190,'فائدة زائدة وتنبيه لطيف','3539','1189','0');
INSERT INTO [t14592] VALUES (1191,'تنبيه','3541','1189','0');
INSERT INTO [t14592] VALUES (1192,'٢ - ومن أخلاق قوم لوط: البخل بالحقوق الواجبة، ومنع أبناء السبيل حقوقهم، وترك الصدقة.','3546','1188','0');
INSERT INTO [t14592] VALUES (1193,'٣ - ومنها: النكاية باللواط، والسطوة بالأعراض.','3546','1188','0');
INSERT INTO [t14592] VALUES (1194,'٤ - ومن أعمال قوم لوط القبيحة، ودأبهم الخبيث','3547','1188','0');
INSERT INTO [t14592] VALUES (1195,'٥ - ومنها: التجاهر باللواط فعلا أو حكاية،','3549','1188','0');
INSERT INTO [t14592] VALUES (1196,'تنبيه','3550','1195','0');
INSERT INTO [t14592] VALUES (1197,'٦ - ومنها: تعييب وتعيير من يتحرج عن إتيان الذكران، وشتمه بالإعراض عن الصبيان إلى النسوان، واستقلال عقله واستضعاف رأيه.','3551','1188','0');
INSERT INTO [t14592] VALUES (1198,'٧ - ومنها: قطع الطريق، والظلم، وتغريم المال بغير حق، والإكراه على الفاحشة، والحكم بالباطل، وإلزام من دعي إلى فاحشة أن يجيب داعيه إليها.','3552','1188','0');
INSERT INTO [t14592] VALUES (1199,'٨ - ومن أعمال قوم لوط: إتيان المرأة في دبرها.','3554','1188','0');
INSERT INTO [t14592] VALUES (1200,'٩ - ومن أعمال قوم لوط: إتيان المرأة المرأة.','3557','1188','0');
INSERT INTO [t14592] VALUES (1201,'١٠ - ومن أعمال قوم لوط','3562','1188','0');
INSERT INTO [t14592] VALUES (1202,'١١ - ومن أخلاق قوم لوط وأعمالهم: النميمة، وبها هلكت امرأة لوط.','3569','1188','0');
INSERT INTO [t14592] VALUES (1203,'١٢ - ومنها: إقرار المنكر، وترك النهي عنه وترك الأمر بالمعروف، بل كانوا ينهون عنه ويأمرون بالمنكر.','3570','1188','0');
INSERT INTO [t14592] VALUES (1204,'(٩) باب النهي عن التشبه بقوم شعيب عليه السلام','3573','1043','0');
INSERT INTO [t14592] VALUES (1205,'١ - منها: الكفر بالله تعالى','3579','1204','0');
INSERT INTO [t14592] VALUES (1206,'٢ - ومنها: كفران النعم','3579','1204','0');
INSERT INTO [t14592] VALUES (1207,'٣ - ومنها: الخيانة في المكيال والميزان - وهو من الكبائر - ومثله الخيانة في الذرع، ونحوه.','3580','1204','0');
INSERT INTO [t14592] VALUES (1208,'لطيفة','3581','1207','0');
INSERT INTO [t14592] VALUES (1209,'تنبيه','3582','1207','0');
INSERT INTO [t14592] VALUES (1210,'٤ - ومن أعمال قوم شعيب عليه السلام: البخس.','3584','1204','0');
INSERT INTO [t14592] VALUES (1211,'٥ - ومنها: الإفساد في الأرض.','3585','1204','0');
INSERT INTO [t14592] VALUES (1212,'٦ - ومنها - وهو من جنس ما قبله -: قطع الطريق.','3586','1204','0');
INSERT INTO [t14592] VALUES (1213,'٧ - ومنها: الجلوس في طرقات المسلمين وممارهم بقصد أذيتهم،','3587','1204','0');
INSERT INTO [t14592] VALUES (1214,'٨ - ومنها: المكس، وأخذ العشور التي لم يأت بها الشرع.','3589','1204','0');
INSERT INTO [t14592] VALUES (1215,'٩ - ومن أعمال قوم شعيب: تلقي الركبان للبيع، وتغرير الجلابين والغرباء.','3592','1204','0');
INSERT INTO [t14592] VALUES (1216,'١٠ - ومنها: قرض الدرهم والدينار، وكسرها بغير غرض صحيح.','3593','1204','0');
INSERT INTO [t14592] VALUES (1217,'تنبيه','3594','1216','0');
INSERT INTO [t14592] VALUES (1218,'١١ - ومنها: السخرية والاستهزاء بالمؤمنين، وبالمصلين وحملة القرآن، وأهل العلم، والتهكم عليهم، والتكبر عليهم، واحتقارهم.','3595','1204','0');
