INSERT INTO [t14592] VALUES (1502,'١٨٦ - ومن أعمال اليهود والنصارى قبحهم الله تعالى: قتل النفس التي حرم الله تعالى بغير الحق.','4243','1278','0');
INSERT INTO [t14592] VALUES (1503,'١٨٧ - ومن أخلاق اليهود: أن كل واحد منهم لم يخل بمسلم إلا حدثته نفسه بقتله.','4245','1278','0');
INSERT INTO [t14592] VALUES (1504,'١٨٨ - ومنها: الظلم في القصاص، وفي الدية.','4246','1278','0');
INSERT INTO [t14592] VALUES (1505,'١٨٩ - ومن أخلاق اليهود: أنهم لا يعفون عن القاتل على مال.','4247','1278','0');
INSERT INTO [t14592] VALUES (1506,'١٩٠ - ومن أخلاق اليهود: السحر، وتعلمه، وتعليمه، والكهانة، وإتيان الكاهن، وتصديقه.','4249','1278','0');
INSERT INTO [t14592] VALUES (1507,'فائدة','4249','1506','0');
INSERT INTO [t14592] VALUES (1508,'١٩١ - ومن أعمال اليهود والنصارى: الزنا، واللواط.','4250','1278','0');
INSERT INTO [t14592] VALUES (1509,'١٩٢ - ومن أعمال اليهود والنصارى: الوقوع على المحارم، والتجاهر بالزنا والفواحش.','4252','1278','0');
INSERT INTO [t14592] VALUES (1510,'فائدة','4253','1509','0');
INSERT INTO [t14592] VALUES (1511,'١٩٣ - ومن أعمال بني إسرائيل: القذف.','4259','1278','0');
INSERT INTO [t14592] VALUES (1512,'١٩٤ - ودل هذا الحديث على أن من أخلاق بني إسرائيل','4261','1278','0');
INSERT INTO [t14592] VALUES (1513,'١٩٥ - ومن أخلاق بني إسرائيل: المحاباة في الحدود.','4265','1278','0');
INSERT INTO [t14592] VALUES (1514,'١٩٦ - ومن أعمال اليهود والنصارى: الكذب، والأيمان الفاجرة.','4269','1278','0');
INSERT INTO [t14592] VALUES (1515,'١٩٧ - ومن أعمال بني إسرائيل: القتال على الملك، والقتال على التأويل، وهو دون الأول لتمحض الأول للدنيا والثاني كالمقدمة له.','4270','1278','0');
INSERT INTO [t14592] VALUES (1516,'١٩٨ - ومنها: الولاية، والقضاء لأجل الدنيا لا لوجه الله تعالى، والتقرب إليه بالفصل بين الحق والباطل، وإيصال الحق إلى أهله،','4271','1278','0');
INSERT INTO [t14592] VALUES (1517,'١٩٩ - ومن أعمال اليهود والنصارى: اتخاذ الولاة الشرط.','4284','1278','0');
INSERT INTO [t14592] VALUES (1518,'٢٠٠ - ومن أعمال اليهود، والنصارى، وعباد الشمس: تولية الملك والحكم للنساء كما في قصة بلقيس.','4286','1278','0');
INSERT INTO [t14592] VALUES (1519,'٢٠١ - ودلت قصة لاجب وامرأته على أن من أعمال بني إسرائيل','4289','1278','0');
INSERT INTO [t14592] VALUES (1520,'٢٠٢ - ومن أخلاق اليهود والنصارى: الاحتفال لأعيادهم.','4289','1278','0');
INSERT INTO [t14592] VALUES (1521,'١ - فمن ذلك: احتفالهم للخميس الحقير هو وأهله،','4296','1520','0');
INSERT INTO [t14592] VALUES (1522,'٢ - ومن ذلك: ما يفعله النساء من طبخ العصيدة ونحوها في صبيحة اليوم المعروف عند النصارى بميلاد عيسى،','4298','1520','0');
INSERT INTO [t14592] VALUES (1523,'٣ - ومن ذلك: الاغتسال لغير ضرورة في يوم غطاس النصارى،','4298','1520','0');
INSERT INTO [t14592] VALUES (1524,'٤ - ومن ذلك: احتفال أهل مصر بعيد الزيتونة؛','4299','1520','0');
INSERT INTO [t14592] VALUES (1525,'٥ - ومن ذلك: ما يفعله النساء من الامتناع عن شراء السمك وأكله يوم السبت.','4299','1520','0');
INSERT INTO [t14592] VALUES (1526,'٢٠٣ - ومن أخلاق اليهود والنصارى: الطيرة من حيث هي.','4304','1278','0');
INSERT INTO [t14592] VALUES (1527,'٢٠٤ - ومنها: حب الحياة، وإطالة الأمل.','4306','1278','0');
INSERT INTO [t14592] VALUES (1528,'٢٠٥ - ومنها: الادخار شحا وبخلا.','4307','1278','0');
INSERT INTO [t14592] VALUES (1529,'٢٠٦ - ومن أخلاق اليهود والنصارى: الوقاحة، وعدم الحياء من الله تعالى.','4312','1278','0');
INSERT INTO [t14592] VALUES (1530,'٢٠٧ - ومنها: سخط المقدور، والتدبير والاختيار لغير ما يختاره الله، وعدم الرضا بالقضاء، والجزع، وترك الصبر على البلاء.','4312','1278','0');
INSERT INTO [t14592] VALUES (1531,'تنبيهات','4313','1530','0');
INSERT INTO [t14592] VALUES (1532,'الأول','4313','1531','0');
INSERT INTO [t14592] VALUES (1533,'الثاني','4314','1531','0');
INSERT INTO [t14592] VALUES (1534,'الثالث','4315','1531','0');
INSERT INTO [t14592] VALUES (1535,'٢٠٨ - ومن أخلاق اليهود والنصارى: كفران النعم، وترك الشكر.','4317','1278','0');
INSERT INTO [t14592] VALUES (1536,'تنبيه','4319','1535','0');
INSERT INTO [t14592] VALUES (1537,'٢٠٩ - ومن أخلاق اليهود والنصارى: الظلم بجميع أنواعه، والعدوان، وولاية الظالمين والفاسقين والكافرين.','4320','1278','0');
INSERT INTO [t14592] VALUES (1538,'٢١٠ - ومن أخلاق اليهود والنصارى: الرياء.','4324','1278','0');
INSERT INTO [t14592] VALUES (1539,'٢١١ - ومنها: عدم الاستقامة على الأمر من الدين، والروغان عنه، والطغيان في النعمة.','4324','1278','0');
INSERT INTO [t14592] VALUES (1540,'٢١٢ - ومن أخلاق اليهود والنصارى: إقرار المنكر، والسكوت عن الحق، وترك الأمر بالمعروف والنهي عن المنكر.','4330','1278','0');
INSERT INTO [t14592] VALUES (1541,'٢١٣ - ومنها: الاسترسال في المعاصي، والانهماك فيها، والإصرار عليها.','4332','1278','0');
INSERT INTO [t14592] VALUES (1542,'٢١٤ - ومنها: أنهم كانوا مع انهماكهم في المعاصي يتمنون على الله المغفرة.','4333','1278','0');
INSERT INTO [t14592] VALUES (1543,'(١٢) باب النهي عن التشبه بالأعاجم والمجوس','4378','1043','0');
INSERT INTO [t14592] VALUES (1544,'١ - فمنها -وهو أقبح ما هم فيه-: الشرك والكفر، وعبادة النار والأضواء.','4404','1543','0');
INSERT INTO [t14592] VALUES (1545,'تنبيه','4406','1544','0');
INSERT INTO [t14592] VALUES (1546,'٢ - ومن أخلاق المجوس: إنكار القضاء والقدر.','4408','1543','0');
INSERT INTO [t14592] VALUES (1547,'٣ - ومن عوائد العجم وأخلاقهم: الخروج على السلطان، وإرادة خلعه أو قتله.','4409','1543','0');
INSERT INTO [t14592] VALUES (1548,'٤ - ومنها: استخلاف السلطان، أو الأمير ولده وغيره أمثل منه إيثارا، أو تقديما للبنوة على حقوق الرعية.','4409','1543','0');
INSERT INTO [t14592] VALUES (1549,'٥ - ومنها: ضرب المكوس والضرائب على الناس، وأخذها منهم وجبايتها، واعتقاد أنها حق مأخوذ لا يسامح فيها، وأخذها بالعنف؛ وكل ذلك حرام.','4410','1543','0');
INSERT INTO [t14592] VALUES (1550,'٦ - ومن أخلاق العجم: الرفض، وبغض الشيخين وغيرهما من الصحابة.','4413','1543','0');
INSERT INTO [t14592] VALUES (1551,'٧ - ومن قبائح المجوس: استباحة أكل الميتة في غير حالة الاضطرار.','4415','1543','0');
INSERT INTO [t14592] VALUES (1552,'٨ - ومن قبائحهم: نكاح المحارم.','4416','1543','0');
INSERT INTO [t14592] VALUES (1553,'٩ - ومن قبائح العجم: العشق الشيطاني والهوى الحيواني،','4418','1543','0');
INSERT INTO [t14592] VALUES (1554,'١٠ - ومن قبائح العجم: أكل الحشيش المسكر.','4425','1543','0');
INSERT INTO [t14592] VALUES (1555,'تنبيه','4426','1554','0');
INSERT INTO [t14592] VALUES (1556,'١١ - ومن أخلاق العجم: الدهاء -بضم المهملة والمد-.','4427','1543','0');
INSERT INTO [t14592] VALUES (1557,'١٢ - ومن أعمال العجم وعوائدهم القبيحة: الضرب بالعود والطنبور، وآلات اللهو، وشرب الخمور.','4427','1543','0');
INSERT INTO [t14592] VALUES (1558,'١٣ - ومنها: اللعب بالنرد والشطرنج.','4428','1543','0');
INSERT INTO [t14592] VALUES (1559,'١٤ - ومنها: لباس الحرير للرجال، وافتراشه والاتكاء عليه، وتجليل السروج والرحال به، واتخاذ الأسرة والآنية من الذهب والفضة للرجال والنساء جميعا، وكل ذلك حرام.','4431','1543','0');
INSERT INTO [t14592] VALUES (1560,'١٥ - ومنها: كثرة التنعم والترفه، والمظاهرة في اللباس والطعام.','4434','1543','0');
INSERT INTO [t14592] VALUES (1561,'١٦ - ومنها: الخروج يوم النيروز.','4436','1543','0');
INSERT INTO [t14592] VALUES (1562,'١٧ - ومنها: عمل الأراجيح يوم العيد.','4438','1543','0');
INSERT INTO [t14592] VALUES (1563,'١٨ - ومنها: الدعكسة.','4439','1543','0');
INSERT INTO [t14592] VALUES (1564,'١٩ - ومنها: حفظ أخبار العجم على وجه الاستحسان لها وبثها، والعناية بكتب الأعاجم التي لا تتعلق بعلوم الشرع.','4440','1543','0');
INSERT INTO [t14592] VALUES (1565,'٢٠ - ومنها: التكلم بالأعجمية','4441','1543','0');
INSERT INTO [t14592] VALUES (1566,'تنبيه','4444','1565','0');
INSERT INTO [t14592] VALUES (1567,'٢١ - ومن عوائد العجم وأعمالهم: غمغمة الكلام وطمطمته.','4445','1543','0');
INSERT INTO [t14592] VALUES (1568,'٢٢ - ومنها: الألقاب التي تشعر بتزكية النفس.','4446','1543','0');
INSERT INTO [t14592] VALUES (1569,'٢٣ - ومنها: التسمية: شاهان شاه، وما كان في معناه كملك الأملاك.','4446','1543','0');
INSERT INTO [t14592] VALUES (1570,'٢٤ - ومنها: التطير بشيء من الأشياء من ذات، أو حال، أو زمان، أو مكان','4447','1543','0');
INSERT INTO [t14592] VALUES (1571,'فائدة','4447','1570','0');
INSERT INTO [t14592] VALUES (1572,'٢٥ - ومنها: الرقية بغير اللسان العربي.','4448','1543','0');
INSERT INTO [t14592] VALUES (1573,'٢٦ - ومنها: الاشتغال بعلم الفلسفة وعلم المنطق.','4449','1543','0');
INSERT INTO [t14592] VALUES (1574,'٢٧ - ومنها: البداءة في الكتاب باسم المكتوب إليه.','4449','1543','0');
INSERT INTO [t14592] VALUES (1575,'تنبيه','4450','1574','0');
INSERT INTO [t14592] VALUES (1576,'٢٨ - ومن عوائد العجم والروم: تحجب ملوكهم وحكامهم','4451','1543','0');
INSERT INTO [t14592] VALUES (1577,'٢٩ - ومنها: وطء أعقابهم، ومشي الخدام خلفهم وبين أيديهم.','4453','1543','0');
INSERT INTO [t14592] VALUES (1578,'٣٠ - من أخلاق الأعاجم والأروام: قيام بعضهم لبعض على سبيل الإعظام، ومحبتهم لأن يقام لهم، وقيام الخدم بين يدي المخدوم وعلى رأسه، وكل ذلك مكروه.','4454','1543','0');
INSERT INTO [t14592] VALUES (1579,'٣١ - ومن آداب الأعاجم: الأكل على الخوان والأواني الرفيعة.','4459','1543','0');
INSERT INTO [t14592] VALUES (1580,'٣٢ - ومنها: قطع اللحم النضيج بالسكين.','4461','1543','0');
INSERT INTO [t14592] VALUES (1581,'٣٣ - ومنها: سكوت الجماعة على الطعام.','4462','1543','0');
INSERT INTO [t14592] VALUES (1582,'٣٤ - ومنها: الاستنكاف عن أكل اللقمة إذا سقطت، ونحو ذلك.','4463','1543','0');
INSERT INTO [t14592] VALUES (1583,'٣٥ - ومنها: التنعم والتأنق في ألوان الأطعمة وطيباتها، وغير ذلك.','4464','1543','0');
INSERT INTO [t14592] VALUES (1584,'٣٦ - ومنها: غسل اليدين قبل الطعام ما لم تكونا متقذرتين.','4465','1543','0');
INSERT INTO [t14592] VALUES (1585,'فائدة','4467','1584','0');
INSERT INTO [t14592] VALUES (1586,'٣٧ - ومن آداب الأعاجم إذا غسلوا أيديهم أن يراق ماء كل واحد منهم من الطست، ثم يوضع بين يدي الآخر.','4468','1543','0');
INSERT INTO [t14592] VALUES (1587,'٣٨ - ومن آداب الأعاجم والأروام التي يتداولونها في هذه الأيام: قيام قوم عن الطعام قبل أن يرفع وقعود آخرين.','4468','1543','0');
INSERT INTO [t14592] VALUES (1588,'٣٩ - ومن أخلاق الأعاجم: أنهم كانوا لا يساكنون الحيض، ولا يؤاكلونهن.','4469','1543','0');
INSERT INTO [t14592] VALUES (1589,'٤٠ - ومن أخلاق الأعاجم: ترك الشعر أبيض من غير خضاب، وهو خلاف الأولى.','4470','1543','0');
INSERT INTO [t14592] VALUES (1590,'٤١ - ومنها: عقد اللحية.','4471','1543','0');
INSERT INTO [t14592] VALUES (1591,'٤٢ - ومنها: حلق القفا لغير ضرورة.','4472','1543','0');
INSERT INTO [t14592] VALUES (1592,'٤٣ - ومنها: توفير الشوارب، والأخذ من اللحى.','4473','1543','0');
INSERT INTO [t14592] VALUES (1593,'٤٤ - ومنها: ما ذكره صاحب "قلائد الشرف": أن من عادة الفرس أنهم كانت الأصوات تستر عنهم إلا غناء النساء، وصوت الجوارح، وصهيل المراكب.','4475','1543','0');
INSERT INTO [t14592] VALUES (1594,'٤٥ - ومنها: ما ذكره صاحب "قلائد الشرف" أيضا','4477','1543','0');
INSERT INTO [t14592] VALUES (1595,'٤٦ - ومنها: وضع الأموات في النواويس والتوابيت التي اعتاد الروم والعجم وضع أمواتهم فيها، ومثله الفساقي التي اعتادوها أهل مصر.','4477','1543','0');
INSERT INTO [t14592] VALUES (1596,'٤٧ - ومن أخلاق الأعاجم: حب الدنيا.','4478','1543','0');
INSERT INTO [t14592] VALUES (1597,'٤٨ - ومنها: محبة طول العمر حتى إن من تحية الملوك: عش ألف سنة.','4479','1543','0');
INSERT INTO [t14592] VALUES (1598,'لطيفة','4479','1597','0');
INSERT INTO [t14592] VALUES (1599,'لطائف أخرى','4480','1597','0');
INSERT INTO [t14592] VALUES (1600,'تنبيه','4484','1597','0');
INSERT INTO [t14592] VALUES (1601,'تتمة','4492','1597','0');
INSERT INTO [t14592] VALUES (1602,'(١٣) باب النهي عن التشبه بأهل الجاهلية والمشركين','4494','1043','0');
INSERT INTO [t14592] VALUES (1603,'١ - فمن قبائح الجاهلية -وهو أقبحها وأفحشها-: الكفر، وعبادة الأصنام، واعتقاد أنها تنفع وتشفع، وتقرب إلى الله زلفى.','4505','1602','0');
INSERT INTO [t14592] VALUES (1604,'تنبيهان','4515','1603','0');
INSERT INTO [t14592] VALUES (1605,'الأول','4515','1604','0');
INSERT INTO [t14592] VALUES (1606,'الثاني','4516','1604','0');
INSERT INTO [t14592] VALUES (1607,'٢ - ومن أخلاق الجاهلية: التكذيب بالقدر.','4516','1602','0');
INSERT INTO [t14592] VALUES (1608,'٣ - ومن أخلاق الجاهلية: الطعن على كتاب الله تعالى، أو على أحاديث رسوله - صلى الله عليه وسلم - الثابتة عنه، ودعوى معارضة القرآن.','4519','1602','0');
INSERT INTO [t14592] VALUES (1609,'٤ - ومنها: الإعراض عن كتاب الله تعالى، وعن تدبر آياته، وإيثار اللهو واللعب.','4519','1602','0');
INSERT INTO [t14592] VALUES (1610,'٥ - ومن أخلاق الجاهلية: التكذيب بلقاء الله تعالى ومصيرهم إليه،','4522','1602','0');
INSERT INTO [t14592] VALUES (1611,'٦ - ومنها: إنكار المعاد.','4523','1602','0');
INSERT INTO [t14592] VALUES (1612,'٧ - ومنها: إنكار السمعيات كالحشر، والنشر، والصراط، والميزان، والحساب، والقصاص، والحوض، والشفاعة، والرؤية، والجنة والنار وما فيهما.','4525','1602','0');
INSERT INTO [t14592] VALUES (1613,'٨ - ومنها: تثبيط الناس عن اتباع السنة، وصدهم عن الهدى.','4525','1602','0');
INSERT INTO [t14592] VALUES (1614,'٩ - ومنها: تعظيم شجرة مخصوصة، أو بقعة مخصوصة، أو حجر مخصوص لم يعظمه الشرع الشريف.','4527','1602','0');
INSERT INTO [t14592] VALUES (1615,'١٠ - ومنها: أنهم كانوا لا يتطهرون.','4528','1602','0');
INSERT INTO [t14592] VALUES (1616,'١١ - ومنها: عمل المعاصي مطلقا، وإساءة الأعمال والأخلاق، وتكديرها بالرياء، والمن والأذى، وطلب العوض.','4528','1602','0');
INSERT INTO [t14592] VALUES (1617,'١٢ - ومن عوائد الجاهلية وأعمالهم وأخلاقهم: اتخاذ المواسم والأعياد التي لم ترد بها الشريعة.','4533','1602','0');
INSERT INTO [t14592] VALUES (1618,'١٣ - ومنها: الاشتغال مطلقا بغرور الدنيا، والاعتزاز بها، والفخر والخيلاء، والأشر والرياء،','4535','1602','0');
INSERT INTO [t14592] VALUES (1619,'تنبيه','4538','1618','0');
INSERT INTO [t14592] VALUES (1620,'١٤ - ومن قبائح الجاهلية: الذبائح التي كانوا يذبحونها ويتقربون بها بغير شريعة واردة.','4538','1602','0');
INSERT INTO [t14592] VALUES (1621,'١٥ - ومنها: المباراة والمعاقرة.','4539','1602','0');
INSERT INTO [t14592] VALUES (1622,'١٦ - ومن أخلاق الجاهلية: التحرج عن الأكل من الهدي والأضحية.','4541','1602','0');
INSERT INTO [t14592] VALUES (1623,'١٧ - ومنها: الذبح لغير الله، أو له ولغيره على وجه الإشراك.','4542','1602','0');
INSERT INTO [t14592] VALUES (1624,'١٨ - ومنها: تضريج الكعبة بالدماء، واعتقاد أن ذلك قربة.','4542','1602','0');
INSERT INTO [t14592] VALUES (1625,'١٩ - ومنها: تلطيخ رأس الغلام بدم عقيقته.','4543','1602','0');
INSERT INTO [t14592] VALUES (1626,'٢٠ - ومنها: الوأد - وهو من أشد الكبائر - وزيادة الفرح إذا بشر بالغلام، وزيادة الترح إذا بشر بالأنثى حياء من الناس، وخوفا من الفقر، وكانوا يئدون البنات لذلك.','4543','1602','0');
INSERT INTO [t14592] VALUES (1627,'تنبيه','4545','1626','0');
INSERT INTO [t14592] VALUES (1628,'٢١ - ومن عوائد الجاهلية: العزل عن النساء مخافة الولد فرارا من العيلة والفقر، أو حذرا من ولادة الإناث','4547','1602','0');
INSERT INTO [t14592] VALUES (1629,'٢٢ - ومن أفعال الجاهلية: قتل النفس التي حرم الله تعالى إلا بالحق.','4548','1602','0');
INSERT INTO [t14592] VALUES (1630,'٢٣ - ومنها: أنهم كانوا إذا قتل لهم قتيل لم يرضوا بقتل قاتله حتى يتجاوزوا، أو يقتلوا غير قاتله.','4549','1602','0');
INSERT INTO [t14592] VALUES (1631,'٢٤ - ومنها: أخذ الإنسان بجريرة.','4550','1602','0');
INSERT INTO [t14592] VALUES (1632,'٢٥ - ومنها: إعانة القاتل والظالم على ظلمه.','4553','1602','0');
INSERT INTO [t14592] VALUES (1633,'٢٦ - ومنها: قتل القاتل بعد قبول الدية منه لما قاله الحسن.','4554','1602','0');
INSERT INTO [t14592] VALUES (1634,'٢٧ - ومنها: البغي مطلقا في القتل وغيره.','4554','1602','0');
INSERT INTO [t14592] VALUES (1635,'٢٨ - ومنها أنهم كانوا إذا قتل لأحدهم قتيل استوفى ذلك بنفسه.','4556','1602','0');
INSERT INTO [t14592] VALUES (1636,'٢٩ - ومنها: الزنا سرا وجهرا، ونكاح المحارم، وتعاطي الأنكحة الفاسدة كالشغار','4556','1602','0');
INSERT INTO [t14592] VALUES (1637,'تنبيه','4562','1636','0');
INSERT INTO [t14592] VALUES (1638,'٣٠ - ومن أعمال الجاهلية الفاحشة - وهو من جنس ما سبق -: المبادلة','4562','1602','0');
INSERT INTO [t14592] VALUES (1639,'٣١ - ومنها: أكل الأولياء مهور مولياتهم.','4563','1602','0');
INSERT INTO [t14592] VALUES (1640,'٣٢ - ومنها - وهو من أفعال المجوس أيضا -: كثرة الوقيد في الأعراس ونحوها.','4564','1602','0');
INSERT INTO [t14592] VALUES (1641,'٣٣ - ومنها: قولهم لمن تزوج: بالرفاء والبنين، وقولهم للعنب: كرم، وتسميتهم المحرم: صفر، والعشاء: عتمة، والمغرب: عشاء.','4565','1602','0');
INSERT INTO [t14592] VALUES (1642,'٣٤ - ومن عوائد الجاهلية [قولهم]: أنعم الله بك عينا، وأنعم صباحا.','4568','1602','0');
INSERT INTO [t14592] VALUES (1643,'٣٥ - ومن قبائح الجاهلية: إدمان شرب الخمر.','4570','1602','0');
INSERT INTO [t14592] VALUES (1644,'تنبيه','4574','1643','0');
INSERT INTO [t14592] VALUES (1645,'٣٦ - ومن أعمال أهل الجاهلية','4575','1602','0');
INSERT INTO [t14592] VALUES (1646,'٣٧ - ومن أعمال الجاهلية: قول الشعر المشتمل على هجاء من لا يجوز هجاؤه','4583','1602','0');
INSERT INTO [t14592] VALUES (1647,'تنبيه','4584','1646','0');
INSERT INTO [t14592] VALUES (1648,'٣٨ - ومن قبائح الجاهلية: الخوض في الباطل.','4585','1602','0');
INSERT INTO [t14592] VALUES (1649,'٣٩ - ومن أخلاق أهل الجاهلية: ترك الصلاة بما لا يعني، ومنع المصلي من الصلاة، ونهيه عنها، وإيذاء المصلين باللفظ وغيره.','4588','1602','0');
INSERT INTO [t14592] VALUES (1650,'٤٠ - ومن عادات أهل الجاهلية: التحلق في المساجد والمعابد لأجل السمر وحديث الدنيا، لا لأجل الصلاة والعبادة، واللغو عند سماع القرآن، واللغط في مجالس الذكر.','4590','1602','0');
INSERT INTO [t14592] VALUES (1651,'٤١ - ومن أخلاقهم: منع الحقوق الواجبة كالزكاة، وصلة الأرحام، والديون، والنهي عن فعل ذلك، والحيلولة بين المتصدق والصدقة.','4592','1602','0');
INSERT INTO [t14592] VALUES (1652,'٤٢ - ومن أخلاقهم: التعبد والتقرب إلى الله تعالى بما لم يرد به الشرع، بل لمجرد الرأي والهوى','4594','1602','0');
INSERT INTO [t14592] VALUES (1653,'تنبيه','4596','1652','0');
INSERT INTO [t14592] VALUES (1654,'تنبيه آخر','4597','1652','0');
INSERT INTO [t14592] VALUES (1655,'٤٣ - ومن أخلاق الجاهلية: التقرب إلى الله تعالى بالسكوت؛ وإن كان هذا شرعا سابقا.','4598','1602','0');
INSERT INTO [t14592] VALUES (1656,'٤٤ - ومن أخلاقهم: الغزو لأجل المعصية والتجبر، وطلب الدنيا كما هو معلوم من أعمالهم، والأعراب الآن على دعواهم الإسلام يفعلون ذلك، وإن اعتقد بعضهم حرمته.','4601','1602','0');
INSERT INTO [t14592] VALUES (1657,'٤٥ - ومنها: حمل الرؤوس المقطوعة من بلد إلى بلد.','4601','1602','0');
INSERT INTO [t14592] VALUES (1658,'٤٦ - ومن أعمال أهل الجاهلية','4604','1602','0');
INSERT INTO [t14592] VALUES (1659,'٤٧ - وكان من عادة الجاهلية','4608','1602','0');
INSERT INTO [t14592] VALUES (1660,'٤٨ - ومن أفعال أهل الجاهلية: الفسوق، والرفث، والجدال في الحج.','4619','1602','0');
INSERT INTO [t14592] VALUES (1661,'٤٩ - ومنها: الحكم بغير ما أنزل الله تعالى.','4621','1602','0');
INSERT INTO [t14592] VALUES (1662,'٥٠ - ومنها: ترك العدل في عطية أولاده؛ فإن السنة أن يساوي بينهم.','4623','1602','0');
INSERT INTO [t14592] VALUES (1663,'٥١ - ومنها: التبني بحيث يعتقد أنه يؤثر في إرثه أو محرمية.','4624','1602','0');
INSERT INTO [t14592] VALUES (1664,'٥٢ - ومنها: الحلف بغير الله تعالى كالآباء، ورأس الأمير بحياته وحياة غيره، وهو مكروه.','4626','1602','0');
INSERT INTO [t14592] VALUES (1665,'٥٣ - ومنها: الإيلاء أكثر من أربعة أشهر إضرارا بالنساء.','4627','1602','0');
INSERT INTO [t14592] VALUES (1666,'٥٤ - ومنها: الظهار.','4628','1602','0');
INSERT INTO [t14592] VALUES (1667,'٥٥ - ومنها: أنهم كانوا لا يرون على المطلقة عدة.','4628','1602','0');
INSERT INTO [t14592] VALUES (1668,'٥٦ - ومنها: أكل الأولياء مهور مولياتهن.','4629','1602','0');
INSERT INTO [t14592] VALUES (1669,'٥٧ - ومنها: ما ذكره القاضي أبو بكر بن العربي في "القبس" قال: وكانت أهل الجاهلية تحلق رأس المولود وتلطخه بالدم، فشرع النبي - صلى الله عليه وسلم - التصدق بزنته - يعني: الشعر -.','4629','1602','0');
INSERT INTO [t14592] VALUES (1670,'٥٨ - ومنها: ما ذكره الجوهري في "صحاحه"، وغيره من أنهم كانوا في الجاهلية إذا قتل لهم قتيل عقوا بسهم؛','4630','1602','0');
INSERT INTO [t14592] VALUES (1671,'٥٩ - ومن عوائدهم وأحكامهم - وهو من تهاونهم بما كان عليه صالحوا آبائهم من التمسك بملة إبراهيم عليه الصلاة والسلام من تعظيم الحرم، والإحرام بالنسك -','4631','1602','0');
INSERT INTO [t14592] VALUES (1672,'٦٠ - ومنها: التشديد على من يقتل الصيد بالمجاوزة في الحكم عليه عن الكفارة إلى العقوبة.','4632','1602','0');
INSERT INTO [t14592] VALUES (1673,'تنبيه','4633','1672','0');
INSERT INTO [t14592] VALUES (1674,'٦١ - ومن أخلاق الجاهلية: تحريم الحلال، وتحليل الحرام.','4636','1602','0');
INSERT INTO [t14592] VALUES (1675,'تنبيه','4639','1674','0');
INSERT INTO [t14592] VALUES (1676,'تتمة','4641','1674','0');
INSERT INTO [t14592] VALUES (1677,'٦٢ - ومن عوائد أهل الجاهلية: أنهم كانوا لا يورثون النساء ولا الصغار، ويأكلون أموال اليتامى والأرامل والضعفاء.','4643','1602','0');
INSERT INTO [t14592] VALUES (1678,'٦٣ - ومنها: تناول الأثمان المحرمة والأعواض المؤثمة؛ كثمن الكلب، والخمر، ومهر البغي، وحلوان الكاهن، والعقود الفاسدة؛ كبيع الخمر، والكلب، والميتة، وبيع الغرر، وبيع حبل الحبلة، وعسب الفحل.','4645','1602','0');
INSERT INTO [t14592] VALUES (1679,'٦٤ - ومنها: الربا وأكل ما يحصل منه، وتناوله، والتصرف فيه.','4647','1602','0');
INSERT INTO [t14592] VALUES (1680,'٦٥ - ومنها: القمار، وهو المراهنة على ما يأخذه الغالب؛ إلا المسابقة والمناضلة الشرعيتين، والنرد.','4650','1602','0');
INSERT INTO [t14592] VALUES (1681,'٦٦ - ومنها: الجلب والجنب - وهما بفتحتين -.','4652','1602','0');
INSERT INTO [t14592] VALUES (1682,'٦٧ - ومنها: المكس.','4654','1602','0');
INSERT INTO [t14592] VALUES (1683,'٦٨ - ومنها: المغالقة على الخيل.','4656','1602','0');
INSERT INTO [t14592] VALUES (1684,'٦٩ - ومنها: غلق الرهن.','4658','1602','0');
INSERT INTO [t14592] VALUES (1685,'٧٠ - ومنها: التعزي بعزاء الجاهلية.','4661','1602','0');
INSERT INTO [t14592] VALUES (1686,'٧١ - ومن أعمال أهل الجاهلية: الاستقسام بالأزلام.','4664','1602','0');
INSERT INTO [t14592] VALUES (1687,'٧٢ - ومنها: تعليق التمائم والحروز، وتقليد الدابة الوثر والجرس، ونحو ذلك.','4665','1602','0');
INSERT INTO [t14592] VALUES (1688,'٧٣ - ومنها: السحر والكهانة - وهما معروفان من فعل الجاهلية - والخط، والتنجيم.','4667','1602','0');
INSERT INTO [t14592] VALUES (1689,'٧٤ - ومن أخلاقهم: الطيرة.','4669','1602','0');
INSERT INTO [t14592] VALUES (1690,'تنبيه','4672','1689','0');
INSERT INTO [t14592] VALUES (1691,'تنبيه','4676','1689','0');
INSERT INTO [t14592] VALUES (1692,'٧٥ - ومن أخلاق أهل الجاهلية: اعتقاد أن غير الله يضر أو ينفع حقيقة، والاستعانة بغير الله تعالى.','4679','1602','0');
INSERT INTO [t14592] VALUES (1693,'٧٦ - ومن أخلاق أهل الجاهلية: الاستمطار بالأنواء، واعتقاد أنها ممطرة حقيقة، وقولهم: مطرنا بنوء كذا.','4681','1602','0');
INSERT INTO [t14592] VALUES (1694,'٧٧ - ومن أعمال الجاهلية: الاستسقاء بالنيران، أو بنار شجر مخصوص، أو بغير ذلك ما سوى الدعاء والطلب من الله تعالى، والتوبة، والأعمال الصالحة، وكان التسليع في الجاهلية كما ذكره صاحب "الصحاح".','4685','1602','0');
INSERT INTO [t14592] VALUES (1695,'٧٨ - ومن أعمال أهل الجاهلية: سب الدهر، والتشكي منه، ونسبة التقلبات إليه.','4687','1602','0');
INSERT INTO [t14592] VALUES (1696,'٧٩ - ومن أخلاقهم: أنهم كانوا يعتقدون أن الشمس والقمر لا تكسفان إلا لموت عظيم.','4688','1602','0');
INSERT INTO [t14592] VALUES (1697,'٨٠ - ومنها: دخول الإنسان على غيره، أو منزله بغير سلام ولا استئذان، واستبدال غيره به كقولهم: أنعم صباحا، وأنعم مساء، وحييتم صباحا، ومثل قول الناس الآن بعضهم لبعض: صباح الخير، مساء الخير من غير سلام، وكل ذلك مخالف للسنة.','4690','1602','0');
INSERT INTO [t14592] VALUES (1698,'٨١ - ومنها: التحرج عن الأكل مع الضيف بخلا،','4691','1602','0');
INSERT INTO [t14592] VALUES (1699,'٨٢ - ومنها: الفخر بالآباء والأنساب والأحساب.','4692','1602','0');
INSERT INTO [t14592] VALUES (1700,'٨٣ - ومنها: الطعن في الأنساب.','4694','1602','0');
INSERT INTO [t14592] VALUES (1701,'٨٤ - ومنها: التنابز بالألقاب المشعرة بالذم.','4694','1602','0');
INSERT INTO [t14592] VALUES (1702,'٨٥ - ومنها: التمادح لا على وجه التقرب إلى الله تعالى بالإنصاف، وإنزال الناس منازلهم، بل على وجه المداهنة والمراءاة، والتقرب إلى قلوب الناس تحصيلا للأغراض الفاسدة.','4695','1602','0');
INSERT INTO [t14592] VALUES (1703,'٨٦ - ومنها: التمدح وتزكية النفس.','4697','1602','0');
INSERT INTO [t14592] VALUES (1704,'٨٧ - ومنها: المشي في القميص من غير رداء، ونحوه مما يتم به التستر؛ لأن ذلك يؤدي إلى كشف العورة أو بدوها، وروح هذه الخصلة يرجع إلى قلة الحياء والتستر، وهما من الإيمان، وضدهما من الجهل.','4697','1602','0');
INSERT INTO [t14592] VALUES (1705,'٨٨ - ومنها: تبرج النساء.','4698','1602','0');
INSERT INTO [t14592] VALUES (1706,'٨٩ - ومنها: اعتزال الحائض في المسكن والمؤاكلة، وتقدم أن اليهود كذلك.','4699','1602','0');
INSERT INTO [t14592] VALUES (1707,'٩٠ - ومنها: اعتقاد العدوى.','4700','1602','0');
INSERT INTO [t14592] VALUES (1708,'٩١ - ومنها: النياحة على الميت، والنعي، ولطم الخدود، وخمش الوجوه، وحلق الشعور ونشرها، وشق الجيوب في المصائب، والإسعاد في ذلك.','4701','1602','0');
INSERT INTO [t14592] VALUES (1709,'٩٢ - ومن أعمال أهل الجاهلية: الوصية بالنوح، والتعديد، واللطم، وما ذكر معه.','4704','1602','0');
INSERT INTO [t14592] VALUES (1710,'٩٣ - ومنها: الإحداد على الميت أكثر من ثلاث لغير الزوجة، وإحداد الزوجة أكثر من أربعة أشهر وعشر، أو من مدة الحمل.','4705','1602','0');
INSERT INTO [t14592] VALUES (1711,'٩٤ - ومنها - وهو من جنس الإحداد -: أن يغير هيئته لموت أبيه، أو ابنه، أو قريبه، ويخرج بهيئة مزرية خلاف عادته إظهارا للحزن.','4706','1602','0');
INSERT INTO [t14592] VALUES (1712,'٩٥ - ومنها: أن يدفن مع الميت شيء من مال صامت، أو حيوان، أو يجعل عند قبره ليستأنس به، أو ليقيه من الأرض، أو لغير ذلك من الاعتقادات الفاسدة.','4707','1602','0');
INSERT INTO [t14592] VALUES (1713,'٩٦ - ومن أخلاق أهل الجاهلية: معاداة أولياء الله تعالى، وإيذاؤهم، وإخراجهم من أوطانهم.','4710','1602','0');
INSERT INTO [t14592] VALUES (1714,'٩٧ - ومنها: الغل، والحقد، والحسد، والعداوة، والبغضاء، والحمية.','4710','1602','0');
INSERT INTO [t14592] VALUES (1715,'٩٨ - ومنها: الأمن من مكر الله تعالى، واليأس من رحمته، وكفران نعمه وشدة الفرح بالنعمة مع نسيان المنعم بها، والفخر بما لا يملك، والبطر والاغترار بالله تعالى، والجزع من الضراء والمصاب.','4713','1602','0');
INSERT INTO [t14592] VALUES (1716,'٩٩ - ومنها: الإصرار والتمادي في الضلال، وعدم الاتعاظ بالآيات، وعدم الاستكانة والتضرع عند نزول البليات تجلدا على الله تعالى، ومكابرة في المعاندة لأمر الله تعالى.','4715','1602','0');
INSERT INTO [t14592] VALUES (1717,'تتمة','4717','1716','0');
INSERT INTO [t14592] VALUES (1718,'(١٤) باب النهي عن التشبه بالمنافقين','4721','1043','0');
INSERT INTO [t14592] VALUES (1719,'١ - فمن أخلاق المنافقين -وهو أقبحها-','4726','1718','0');
INSERT INTO [t14592] VALUES (1720,'٢ - ومنها: الاستهزاء بالدين وأهله.','4727','1718','0');
INSERT INTO [t14592] VALUES (1721,'٣ - ومنها: إظهار الإيمان, والصلاح، والزهد والورع مع إبطان أضدادها.','4729','1718','0');
INSERT INTO [t14592] VALUES (1722,'٤ - ومنها: الإفساد في الأرض.','4730','1718','0');
INSERT INTO [t14592] VALUES (1723,'٥ - ومنها: الظلم في الولاية، أو مطلقا، ولا سيما في الولاية والعدوان.','4731','1718','0');
INSERT INTO [t14592] VALUES (1724,'٦ - ومنها: أن المنافق يدعي أن إفساده إصلاح، وأن ظلمه عدل،','4732','1718','0');
INSERT INTO [t14592] VALUES (1725,'تنبيه','4733','1724','0');
INSERT INTO [t14592] VALUES (1726,'٧ - ومنها: أن المنافق يسفه المؤمن، ويرى أنه على ضلال وباطل.','4734','1718','0');
INSERT INTO [t14592] VALUES (1727,'٨ - ومنها: التلدد في الخصومة، وكثرة الخصومات -فإن كانت في غير الحق كانت أقبح-','4735','1718','0');
INSERT INTO [t14592] VALUES (1728,'٩ - ومنها: الفجور في الخصومة أو مطلقا.','4737','1718','0');
INSERT INTO [t14592] VALUES (1729,'١٠ - ومنها: التكبر عن امتثال الأمر بالتقوى، والأنفة من قبول الحق إذا دعي إليه.','4738','1718','0');
INSERT INTO [t14592] VALUES (1730,'تنبيه','4739','1729','0');
INSERT INTO [t14592] VALUES (1731,'١١ - ومن أخلاق المنافقين: اتباع الهوى.','4741','1718','0');
INSERT INTO [t14592] VALUES (1732,'تنبيه','4743','1731','0');
INSERT INTO [t14592] VALUES (1733,'١٢ - ومن أخلاق المنافقين','4744','1718','0');
INSERT INTO [t14592] VALUES (1734,'١٣ - ومنها: الخوض في الباطل واللعب.','4745','1718','0');
INSERT INTO [t14592] VALUES (1735,'١٤ - ومنها: الوقيعة في أهل العلم، وحملة القرآن.','4745','1718','0');
INSERT INTO [t14592] VALUES (1736,'١٥ - ومنها: السرور بمصيبة المؤمن، والشماتة له، والحزن والمساءة بنعمته وحسنته.','4746','1718','0');
INSERT INTO [t14592] VALUES (1737,'تنبيه','4747','1736','0');
INSERT INTO [t14592] VALUES (1738,'١٦ - ومنها: التكذيب بالخارق الذي يظهر على يد الصادق كمعجزة النبي، وكرامة الولي.','4748','1718','0');
INSERT INTO [t14592] VALUES (1739,'١٧ - ومنها: التهاون بالصلوات الخمس، أو بواحدة منها، والتشاغل عند القيام إليها، وتأخيرها إلى وقت لا يسعها، وترك الطمأنينة فيها.','4749','1718','0');
INSERT INTO [t14592] VALUES (1740,'تنبيه','4750','1739','0');
INSERT INTO [t14592] VALUES (1741,'تنبيه ثان','4751','1739','0');
INSERT INTO [t14592] VALUES (1742,'١٨ - ومن أخلاق المنافقين: القعود عن الجماعة، وعن شهود الجماعة في المسجد.','4752','1718','0');
INSERT INTO [t14592] VALUES (1743,'تنبيه','4755','1742','0');
INSERT INTO [t14592] VALUES (1744,'١٩ - ومن أخلاق المنافقين','4756','1718','0');
INSERT INTO [t14592] VALUES (1745,'٢٠ - ومنها: ترك الصف الأول رغبة عنه إلا لعذر.','4757','1718','0');
