INSERT INTO [t1459] VALUES (1,'سرنا يومنا وليلتنا، حتى قام قائم الظهيرة، وخلا الطريق، فلم يمر أحد، فرفعت لنا صخرة لها','1','0','0');
INSERT INTO [t1459] VALUES (2,'بعثنا رسول الله صلى الله عليه وسلم إلى النجاشي ثمانين رجلا، منهم عبد الله بن مسعود،','2','0','0');
INSERT INTO [t1459] VALUES (3,'لما بلغه موت النجاشي، استغفر له','3','0','0');
INSERT INTO [t1459] VALUES (4,'ولد آدم كلهم تحت رايتي يوم القيامة، وأنا أول من تفتح له أبواب الجنة','4','0','0');
INSERT INTO [t1459] VALUES (5,'لا عليك يا قيلة ألا تفعلي، إذا أردت أن تشتري الشيء فأعط به الذي تريدين أن تأخذي به، أعطيت','5','0','0');
INSERT INTO [t1459] VALUES (6,'أتتها امرأة فسألتها عن شيء من الطلاق، قالت: فذكرت ذلك لرسول الله صلى الله عليه وسلم،','6','0','0');
INSERT INTO [t1459] VALUES (7,'قال رجل من أهل الكتاب فجعل يذكر عنه، فجئت وهو يذكر ذاك، فذاك الذي يمنعني من الحديث عن','7','0','0');
INSERT INTO [t1459] VALUES (8,'لا تعقل العاقلة الصلح، ولا العهد، ولا ما جنى المملوك، ولا الاعتراف','8','0','0');
INSERT INTO [t1459] VALUES (9,'وما أنفقتم من شيء فهو يخلفه} [سبأ: ٣٩] قال: في غير إسراف ولا إقتار','9','0','0');
INSERT INTO [t1459] VALUES (10,'خير الصحابة أربعة، وخير السرايا أربعمائة، وخير الجيوش أربعة آلاف، ولن تهزم اثنا عشر ألفا','10','0','0');
INSERT INTO [t1459] VALUES (11,'نهى أن يتلقى الجلب. قال: فإن تلقاه متلق فصاحبه فيها بالخيار إذا دخل','11','0','0');
INSERT INTO [t1459] VALUES (12,'صلى الله عليك، وعلى زوجك','12','0','0');
INSERT INTO [t1459] VALUES (13,'ردوا القتلى إلى مصارعهم','13','0','0');
INSERT INTO [t1459] VALUES (14,'إذا سجدوا وضعوا جباههم بين أكفهم، ودون ذلك، وأمام ذلك','14','0','0');
INSERT INTO [t1459] VALUES (15,'زره ولو لم تجد إلا بشوكة','15','0','0');
INSERT INTO [t1459] VALUES (16,'الإسلام بدأ غريبا وسيعود غريبا كما بدأ، فطوبى للغرباء','16','0','0');
INSERT INTO [t1459] VALUES (17,'روح القدس مع حسان ما دام ينافح عن رسول الله صلى الله عليه وسلم','17','0','0');
INSERT INTO [t1459] VALUES (18,'أصدق كلمة تكلمت بها العرب , كلمة لبيد:','18','0','0');
INSERT INTO [t1459] VALUES (19,'من الشعر حكمة','19','0','0');
INSERT INTO [t1459] VALUES (20,'خير الدهقان بين أن يعطيه عشرين ألفا ويأخذ الفرس، وبين أن يغرم ربع الثمن فقال لي الدهقان:','20','0','0');
INSERT INTO [t1459] VALUES (21,'كنا إذا أتينا النبي صلى الله عليه وسلم جلس أحدنا حيث انتهى','21','0','0');
INSERT INTO [t1459] VALUES (22,'إذا انتهى أحدكم إلى المجلس، فإن وسع له فليجلس , وإلا فلينظر ببصره أوسع مكان يراه فليجلس','22','0','0');
INSERT INTO [t1459] VALUES (23,'أبينك وبينها قرابة؟ قال: لا. قال: فتعرفها؟ قال: لا. قال: فرحمتها، رحمك الله قالها','23','0','0');
INSERT INTO [t1459] VALUES (24,'قولوا: لا إله إلا الله تفلحوا. ورجل من خلفه يرميه، يقول: لا تسمعوا منه، هذا الكذاب. قال:','24','0','0');
INSERT INTO [t1459] VALUES (25,'ما هو بلعين، قد جاهد مع رسول الله صلى الله عليه وسلم بلسانه ونفسه','25','0','0');
INSERT INTO [t1459] VALUES (26,'أليس هو الذي يقول: [البحر الوافر] هجوت محمدا فأجبت عنه ... وعند الله في ذاك الجزاء أتهجوه','26','0','0');
INSERT INTO [t1459] VALUES (27,'ادن بني، وسم الله، وكل بيمينك، وكل مما يليك','27','0','0');
INSERT INTO [t1459] VALUES (28,'ألم أنهكم قالوا: إنما هم أولاد المشركين يا رسول الله. قال: أوليس خياركم أولاد المشركين؟','28','0','0');
INSERT INTO [t1459] VALUES (29,'هم من آبائهم','29','0','0');
INSERT INTO [t1459] VALUES (30,'إن شئت أسمعتك تضاغيهم في النار. قالت: قلت يا رسول الله، كيف ذلك ولم يبلغوا الحنث قال: ربك','30','0','0');
INSERT INTO [t1459] VALUES (31,'هم خدم أهل الجنة','31','0','0');
INSERT INTO [t1459] VALUES (32,'وجب أجرك على الله، ورد عليك الميراث','32','0','0');
INSERT INTO [t1459] VALUES (33,'أتيت وأنا نائم، في شهر رمضان فقيل لي: الليلة ليلة القدر قال: فقمت وأنا ناعس، فتعلقت','33','0','0');
INSERT INTO [t1459] VALUES (34,'الليلة ليلة القدر','34','0','0');
INSERT INTO [t1459] VALUES (35,'تسبل إزارك وتوفر شعرك حدثنا أبو المعطل الزهري، عن أبي إسحاق، عن شمر بن عطية، عن خريم بن','35','0','0');
INSERT INTO [t1459] VALUES (36,'رجم يهوديا ويهودية','36','0','0');
INSERT INTO [t1459] VALUES (37,'رجم يهوديا ويهودية حين تحاكما إليه','37','0','0');
INSERT INTO [t1459] VALUES (38,'اليوم عاشوراء، يوم صامه رسول الله صلى الله عليه وسلم , فقال: من لم يكن صامه منكم','38','0','0');
INSERT INTO [t1459] VALUES (39,'ما رأيت أحدا آمر بصوم عاشوراء من علي، وأبي موسى رضي الله عنهما','39','0','0');
INSERT INTO [t1459] VALUES (40,'تسحروا، فإن في السحور بركة','40','0','0');
INSERT INTO [t1459] VALUES (41,'من لم يشكر الناس لم يشكر الله عز وجل','41','0','0');
INSERT INTO [t1459] VALUES (42,'اثلغيه كما يثلغ الأفعى، فإن اليتيم أحق بالثلغ من الأفعى قال أبو جعفر: الثلغ:','42','0','0');
INSERT INTO [t1459] VALUES (43,'ادع كاتبك يقرأ على الناس في المسجد. قال أبو موسى: إنه نصراني , لا يدخل المسجد. قال عمر','43','0','0');
INSERT INTO [t1459] VALUES (44,'أسلم، حتى نستعين بك على بعض أمور المسلمين , لأنه لا ينبغي لنا أن نستعين على أمورهم بمن','44','0','0');
INSERT INTO [t1459] VALUES (45,'أعتق غلاما له مجوسيا يقال له: مأبورا','45','0','0');
INSERT INTO [t1459] VALUES (46,'من ارتبط كلبا نقص من أجره كل يوم قيراطان. قال: لم يا أبا هريرة؟ . قال: إنه لا يزال يؤذي','46','0','0');
INSERT INTO [t1459] VALUES (47,'رضيت لكم عمر فبايعوه','47','0','0');
INSERT INTO [t1459] VALUES (48,'لعن رسول الله صلى الله عليه وسلم الرجلة من النساء','48','0','0');
INSERT INTO [t1459] VALUES (49,'إذا قام الرجل من مجلسه فهو أحق به إذا رجع إليه','49','0','0');
INSERT INTO [t1459] VALUES (50,'إني لأرى رد جواب الكتاب علي حقا كرد السلام','50','0','0');
INSERT INTO [t1459] VALUES (51,'قيدوا العلم بالكتابة قال أبو جعفر: لم يكن يرفعه أحد غير هذا الرجل','51','0','0');
INSERT INTO [t1459] VALUES (52,'استأذنت رسول الله صلى الله عليه وسلم أن يأذن لي أن أكتب الحديث فأبى أن يأذن','52','0','0');
INSERT INTO [t1459] VALUES (53,'ويأتيك بالأخبار من لم تزود','53','0','0');
INSERT INTO [t1459] VALUES (54,'يأمر بناته أن يذبحن نسائكهن بأيديهن','54','0','0');
INSERT INTO [t1459] VALUES (55,'يأمر بناته أن يذبحن نسائكهن بأيديهن','55','0','0');
INSERT INTO [t1459] VALUES (56,'يزكي ما خرج من الثمرة منها. وقال ابن عمر: يقضي ما أنفق على ثمرته وأهله ثم يزكي ما','56','0','0');
INSERT INTO [t1459] VALUES (57,'تربص أربع سنين. وقال ابن عمر: ينفق عليها من جميع المال وقال ابن عباس: إذا تجحف بالورثة،','57','0','0');
INSERT INTO [t1459] VALUES (58,'إن شئتم فتوضئوا وإلا فلا تتوضئوا قالوا: يا رسول الله، أنتوضأ من لحوم الإبل؟ . قال: نعم.','58','0','0');
INSERT INTO [t1459] VALUES (59,'نحرنا يوم الحديبية مع النبي صلى الله عليه وسلم سبعين بدنة، البدنة عن','59','0','0');
INSERT INTO [t1459] VALUES (60,'الندم توبة؟ قال: نعم','60','0','0');
INSERT INTO [t1459] VALUES (61,'خياركم من أطعم الطعام','61','0','0');
INSERT INTO [t1459] VALUES (62,'خياركم من أطعم الطعام','62','0','0');
INSERT INTO [t1459] VALUES (63,'لن يدخل أحدكم عمله الجنة قالوا: ولا أنت يا رسول الله؟ قال: ولا أنا، إلا أن يتغمدني الله','63','0','0');
INSERT INTO [t1459] VALUES (64,'رآه بال قائما، ثم توضأ ومسح على خفيه، قلت له: انزع الخفين قال: لا أنزعهما، قد رأيت من هو','64','0','0');
INSERT INTO [t1459] VALUES (65,'لا تدمنوا النظر إلى المجذومين','65','0','0');
INSERT INTO [t1459] VALUES (66,'فطرده وأخرجه','66','0','0');
INSERT INTO [t1459] VALUES (67,'المسجد يومئذ لمغرز بالقصب وأبو موسى قائم علينا يعلمنا آية آية، فقال أبو موسى: قال رسول','67','0','0');
INSERT INTO [t1459] VALUES (68,'من اشترى خادما فليضع يده على ناصيته، ثم يقول: اللهم، إني أسألك من خيره، وخير ما جبلته','68','0','0');
INSERT INTO [t1459] VALUES (69,'من أحدث في ديننا ما ليس فيه فهو رد','69','0','0');
INSERT INTO [t1459] VALUES (70,'مضطجعا في المسجد واضعا إحدى رجليه على الأخرى وإن أبا بكر، وعمر، وعثمان , رضي الله عنهم ,','70','0','0');
INSERT INTO [t1459] VALUES (71,'ساعتان تفتح فيهما أبواب السماء، وقلما ترد فيهما دعوة: عند الأذان، وعند الصف في سبيل الله','71','0','0');
INSERT INTO [t1459] VALUES (72,'ليس على أهل القرى جمعة , ولا على من على رأس ميل جمعة , إنما الجمعة على أهل الأمصار مثل','72','0','0');
INSERT INTO [t1459] VALUES (73,'الجمعة واجبة على من آواه الليل قال أبو جعفر: سمعت رجلا يذكره لحماد بن زيد، فعجب منه ,','73','0','0');
INSERT INTO [t1459] VALUES (74,'المؤذن إذا بلغ حي على الصلاة. قال: نادى: الصلاة في الرحال، فأنكر ذلك بعض القوم، فقال: قد','74','0','0');
INSERT INTO [t1459] VALUES (75,'رخص لنا إذا كان مطر وابل أن نصلي في الرحال','75','0','0');
INSERT INTO [t1459] VALUES (76,'إذا كان يوم غيم ومطر أذن وأقام , ثم قال: الصلاة في الرحال قال أبو جعفر لوين: هذه الرخصة','76','0','0');
INSERT INTO [t1459] VALUES (77,'زين الحديث الصدق، وأعظم الخطايا اللسان الكذاب، وشر العدة، يعني: عدة أحدكم نفسه حتى يأتيه','77','0','0');
INSERT INTO [t1459] VALUES (78,'بارك الله لك فيه، أولم ولو بشاة قال لوين: الوقية: أربعون، والنش: عشرون، والنواة: وزن خمسة','78','0','0');
INSERT INTO [t1459] VALUES (79,'من مات في بيت المقدس فكأنما مات في السماء قال لوين: ليس يعني بيت المقدس نفسه إنما يعني','79','0','0');
INSERT INTO [t1459] VALUES (80,'لا تسبوه قال أبو جعفر: إنما كان قبل هذا الحديث، ذكر ماعز','80','0','0');
INSERT INTO [t1459] VALUES (81,'صلى نحو بيت المقدس خمسة عشر شهرا أو ستة عشر شهرا فجعل رسول الله صلى الله عليه وسلم يقلب','81','0','0');
INSERT INTO [t1459] VALUES (82,'صلى رسول الله صلى الله عليه وسلم قبل بيت المقدس ستة عشر شهرا، ثم أمر أن يصلي قبل الكعبة،','82','0','0');
INSERT INTO [t1459] VALUES (83,'وما كان الله ليضيع إيمانكم. .} [البقرة: ١٤٣] . قال: صلاتكم. قيل لشريك: صلاتكم إيمانكم؟','83','0','0');
INSERT INTO [t1459] VALUES (84,'اهد ثقيفا','84','0','0');
INSERT INTO [t1459] VALUES (85,'لقد هممت ألا أتهب هبة إلا من قرشي، أو أنصاري، أو ثقفي قال ابن عيينة وقال غيره: هؤلاء أهل','85','0','0');
INSERT INTO [t1459] VALUES (86,'اسكت فإن أمه قصوية. فقال له ابن صفوان: والله، لقد ولدتني أمي ثقفية، ما يسرني بها عشر','86','0','0');
INSERT INTO [t1459] VALUES (87,'لا يملين أحدكم في مصاحفنا إلا فتيان قريش وثقيف','87','0','0');
INSERT INTO [t1459] VALUES (88,'هل معكم هدية أم صدقة فإن الصدقة يبتغى بها وجه الله تبارك وتعالى، وإن الهدية يبتغى بها وجه','88','0','0');
INSERT INTO [t1459] VALUES (89,'كأن هذا الكبش الذي ذبح إبراهيم عليه السلام فعمد معاذ بن عفراء رضي الله عنه، فاشترى كبشا','89','0','0');
INSERT INTO [t1459] VALUES (90,'انطلق فادفع هذه الدراهم إلى امرأتك، ومرها تشتري كل يوم بدرهم لحما، فهو أفضل مما','90','0','0');
INSERT INTO [t1459] VALUES (91,'إذا بايعت صاحبك فلا تفارقه وبينك وبينه شيء','91','0','0');
INSERT INTO [t1459] VALUES (92,'ما زاد من ذهب أو فضة على مثل بمثل فهو ربا','92','0','0');
INSERT INTO [t1459] VALUES (93,'صلاته في رمضان ثلاث عشرة ركعة، منها ركعتا الفجر','93','0','0');
INSERT INTO [t1459] VALUES (94,'شيطان الردهة علامة في قوم ظلمة، راعي الخيل، أو راع للخيل يحتدره رجل من بني بجيلة، يقال','94','0','0');
INSERT INTO [t1459] VALUES (95,'كتب علي النحر فلم تكتب عليكم، وأمرت بصلاة الضحى ولم تؤمروا بها','95','0','0');
INSERT INTO [t1459] VALUES (96,'سم الله وكل بيمينك، وكل مما يليك فما زالت تلك طعمتي','96','0','0');
INSERT INTO [t1459] VALUES (97,'سم الله، وكل بيمينك، وكل مما يليك','97','0','0');
INSERT INTO [t1459] VALUES (98,'لم يكتب الأضحى، من شاء ضحى','98','0','0');
INSERT INTO [t1459] VALUES (99,'تجاوز لأمتي ما حدثت به أنفسها ما لم يتكلموا به أو يعملوا','99','0','0');
INSERT INTO [t1459] VALUES (100,'إذا صليتما في رحالكما ثم أتيتما مسجد جماعة فصليا معهم، فإنها لكم نافلة','100','0','0');
INSERT INTO [t1459] VALUES (101,'يطلع من تحت هذه الصور رجل من أهل الجنة فطلع أبو بكر رضي الله عنه , فهنيناه بما قال رسول','101','0','0');
INSERT INTO [t1459] VALUES (102,'من أشراط الساعة سوء الجوار , وقطيعة الأرحام، وتعطيل السيف عن الجهاد، وأن تختل الدنيا','102','0','0');
INSERT INTO [t1459] VALUES (103,'المسلم أخو المسلم، لا يظلمه، ولا يخذله، والتقوى هاهنا. وأشار بيده إلى','103','0','0');
INSERT INTO [t1459] VALUES (104,'ما من امرئ يتصدق بصدقة من كسب طيب، ولا يقبل الله إلا طيبا، حتى ولو بتمرة إلا أخذها الله','104','0','0');
INSERT INTO [t1459] VALUES (105,'ما أطعمت نفسك فهو لك صدقة , وما أطعمت ولدك فهو لك صدقة، وما أطعمت زوجك فهو لك صدقة، وما','105','0','0');
INSERT INTO [t1459] VALUES (106,'كيلوا طعامكم يبارك لكم فيه','106','0','0');
INSERT INTO [t1459] VALUES (107,'المؤمن من أهل الإيمان بمنزلة الرأس من الجسد، يألم المؤمن لما يصيب أهل الإيمان كما يألم','107','0','0');
INSERT INTO [t1459] VALUES (108,'إنما مثل المسلمين في تواصلهم وتراحمهم والذي جعل الله بينهم، كمثل الجسد إذا وجع بعضه , وجع','108','0','0');
INSERT INTO [t1459] VALUES (109,'رأيت على ابن عباس رضي الله عنهما، عمامة سوداء حرقانية قد أرسلها من بين يديه شبرا، ومن','109','0','0');
INSERT INTO [t1459] VALUES (110,'أقم عندها فإن لك من الأجر مثل ما تريد. ثم أتاه رجل، فقال: يا رسول الله، إني نذرت أن أنحر','110','0','0');
INSERT INTO [t1459] VALUES (111,'ثلاث، وثلاث، وثلاث: ثلاث لا يمين فيهن، وثلاث الملعون فيهن، وثلاث أشك فيهن، أما الثلاث','111','0','0');
INSERT INTO [t1459] VALUES (112,'الولاء لمن أعتق','112','0','0');
INSERT INTO [t1459] VALUES (113,'يسجد على جبهته وأرنبته','113','0','0');
INSERT INTO [t1459] VALUES (114,'ظهر الفساد في البر والبحر بما كسبت أيدي الناس} [الروم: ٤١] قال: أما البحر فما كان من','114','0','0');
INSERT INTO [t1459] VALUES (115,'هذه نومة يبغضها الله عز وجل','115','0','0');
INSERT INTO [t1459] VALUES (116,'إن شئتم أن ترقدوا هاهنا وإن شئتم فالمسجد قال: فخرجنا إلى المسجد، قال: فلما كان في آخر','116','0','0');
INSERT INTO [t1459] VALUES (117,'هذه نومة يبغضها الله عز وجل قال أبو جعفر لوين: قد اختلفوا في هذين الحديثين، وأحدهما عندي','117','0','0');
