INSERT INTO [t1462] VALUES (1,'يصلي علي أحد من أمتي إلا ردها الله عليه عشر أمثالها','1','0','0');
INSERT INTO [t1462] VALUES (2,'يصلي عليك أحد من أمتك إلا صليت عليه عشرا ولا سلم عليك إلا سلمت عليه','2','0','0');
INSERT INTO [t1462] VALUES (3,'من صلى علي واحدة صلى الله عليه عشرا فليكثر عدد ذلك أو ليقل','3','0','0');
INSERT INTO [t1462] VALUES (4,'من صلى عليك واحدة صلى الله عليه عشرا ورفعه عشر درجات','4','0','0');
INSERT INTO [t1462] VALUES (5,'من صلى عليك صلاة صلى الله عليه عشرا ورفعه عشر درجات','5','0','0');
INSERT INTO [t1462] VALUES (6,'ما من عبد يصلي علي إلا صلت عليه الملائكة ما صلى علي، فليقل من ذلك أو','6','0','0');
INSERT INTO [t1462] VALUES (7,'من صلى عليك صليت عليه، ومن سلم عليك سلمت عليه فسجدت لله شكرا','7','0','0');
INSERT INTO [t1462] VALUES (8,'من صلى علي، صلى الله عليه عشرا','8','0','0');
INSERT INTO [t1462] VALUES (9,'من صلى علي واحدة، صلى الله عليه عشرا','9','0','0');
INSERT INTO [t1462] VALUES (10,'من صلى علي صلاة كتب الله له عشر حسنات','10','0','0');
INSERT INTO [t1462] VALUES (11,'من صلى علي مرة واحدة كتب الله له عشر حسنات','11','0','0');
INSERT INTO [t1462] VALUES (12,'من صلى على النبي صلى الله عليه وسلم كتب الله له عشر حسنات ومحا عنه عشر سيئات ورفع له عشر','12','0','0');
INSERT INTO [t1462] VALUES (13,'يصلي عليك صلاة إلا صلى الله عليه بها عشرا. فقام إليه رجل فقال: يا رسول الله أجعل نصف','13','0','0');
INSERT INTO [t1462] VALUES (14,'جاءت الراجفة تتبعها الرادفة، جاء الموت بما فيه، وقال أبي: يا رسول الله إني أصلي من الليل','14','0','0');
INSERT INTO [t1462] VALUES (15,'رغم أنف امرئ ذكرت عنده فلم يصل عليك، فقلت: آمين، فقال: رغم أنف امرئ أدرك أبويه فلم يدخل','15','0','0');
INSERT INTO [t1462] VALUES (16,'رغم أنف رجل ذكرت عنده فلم يصل علي، ورغم أنف رجل أدرك أبويه عند الكبر فلم يدخلاه الجنة،','16','0','0');
INSERT INTO [t1462] VALUES (17,'رغم أنف عبد ذكرت عنده فلم يصل عليك فقلت: آمين','17','0','0');
INSERT INTO [t1462] VALUES (18,'من ذكرت عنده فلم يصل عليك فقلت: آمين فلما رقيت الثالثة قال: بعد من أدرك أبويه الكبر أو','18','0','0');
INSERT INTO [t1462] VALUES (19,'لا تجعلوا قبري عيدا ولا تجعلوا بيوتكم قبورا، وصلوا علي وسلموا حيثما كنتم فسيبلغني سلامكم','19','0','0');
INSERT INTO [t1462] VALUES (20,'لله في الأرض ملائكة سياحين يبلغوني من أمتي السلام','20','0','0');
INSERT INTO [t1462] VALUES (21,'من أفضل أيامكم يوم الجمعة؛ فيه خلق آدم، وفيه قبض، وفيه النفخة، وفيه الصعقة، فأكثروا علي','21','0','0');
INSERT INTO [t1462] VALUES (22,'لا تأكل الأرض جسد من كلمه روح القدس','22','0','0');
INSERT INTO [t1462] VALUES (23,'ملكا موكل بكل من صلى على النبي حتى يبلغه النبي صلى الله عليه وسلم','23','0','0');
INSERT INTO [t1462] VALUES (24,'حياتي خير لكم تحدثون ويحدث لكم، فإذا أنا مت كانت وفاتي خيرا لكم، تعرض علي أعمالكم فإن','24','0','0');
INSERT INTO [t1462] VALUES (25,'حياتي خير لكم، ووفاتي لكم خير، تحدثون فيحدث لكم فإذا أنا مت عرضت علي أعمالكم، فإن رأيت','25','0','0');
INSERT INTO [t1462] VALUES (26,'ملكا موكل يوم الجمعة من صلى على النبي يبلغ النبي صلى الله عليه وسلم يقول: إن فلانا من','26','0','0');
INSERT INTO [t1462] VALUES (27,'أكثروا علي الصلاة يوم الجمعة','27','0','0');
INSERT INTO [t1462] VALUES (28,'أكثروا علي الصلاة يوم الجمعة فإنها تعرض علي','28','0','0');
INSERT INTO [t1462] VALUES (29,'صلوا في بيوتكم ولا تجعلوا بيوتكم مقابر، لعن الله يهود اتخذوا قبور أنبيائهم مساجد، وصلوا','29','0','0');
INSERT INTO [t1462] VALUES (30,'البخيل لمن ذكرت عنده فلم يصل علي','30','0','0');
INSERT INTO [t1462] VALUES (31,'البخيل من ذكرت عنده فلم يصل علي. صلى الله عليه وسلم تسليما قال القاضي: اختلف يحيى الحماني','31','0','0');
INSERT INTO [t1462] VALUES (32,'البخيل من ذكرت عنده فلم يصل علي. قال: هكذا رواه عمرو بن الحارث أرسله عن علي بن حسين، عن','32','0','0');
INSERT INTO [t1462] VALUES (33,'البخيل الذي إذا ذكرت عنده لم يصل علي. هكذا رواه الدراوردي، أرسله عن عبد الله بن علي بن','33','0','0');
INSERT INTO [t1462] VALUES (34,'البخيل من ذكرت عنده فلم يصل علي حدثنا به علي بن عبد الله بن جعفر بن نجيح قال: قال أبي:','34','0','0');
INSERT INTO [t1462] VALUES (35,'أبخل الناس من ذكرت عنده فلم يصل علي صلى الله عليه وسلم','35','0','0');
INSERT INTO [t1462] VALUES (36,'بحسب امرئ في البخل أن أذكر عنده فلا يصلي علي','36','0','0');
INSERT INTO [t1462] VALUES (37,'كفى به شحا أن يذكرني قوم فلا يصلون علي صلى الله عليه وسلم','37','0','0');
INSERT INTO [t1462] VALUES (38,'أكثروا علي من الصلاة يوم الجمعة','38','0','0');
INSERT INTO [t1462] VALUES (39,'من ينسى الصلاة علي خطئ أبواب الجنة','39','0','0');
INSERT INTO [t1462] VALUES (40,'من ينسى الصلاة علي خطئ طريق الجنة قال سفيان: قال رجل بعد عمرو: سمعت محمد بن علي يقول: قال','40','0','0');
INSERT INTO [t1462] VALUES (41,'من نسي الصلاة علي خطئ طريق الجنة','41','0','0');
INSERT INTO [t1462] VALUES (42,'من ذكرت عنده فلم يصل علي فقد خطئ طريق الجنة','42','0','0');
INSERT INTO [t1462] VALUES (43,'صلوا على أنبياء الله ورسله؛ فإن الله بعثهم كما بعثني. صلى الله عليه وسلم وعليهم','43','0','0');
INSERT INTO [t1462] VALUES (44,'صلوا علي فإن صلاتكم علي زكاة لكم قال: وسلوا الله لي الوسيلة. - قال: فإما حدثنا وإما','44','0','0');
INSERT INTO [t1462] VALUES (45,'صلوا علي فإن صلاتكم علي زكاة لكم، وسلوا الله لي الوسيلة - فإما أن يكونوا سألوه، وإما أن','45','0','0');
INSERT INTO [t1462] VALUES (46,'سلوا الله لي الوسيلة؛ لا يسألها لي مسلم أو مؤمن إلا كنت له شهيدا أو شفيعا أو شفيعا أو','46','0','0');
INSERT INTO [t1462] VALUES (47,'الوسيلة درجة عند الله ليس فوقها درجة، فسلوا الله أن يؤتيني الوسيلة على','47','0','0');
INSERT INTO [t1462] VALUES (48,'من صلى علي أو سأل لي الوسيلة حقت عليه شفاعتي يوم القيامة','48','0','0');
INSERT INTO [t1462] VALUES (49,'فسلوا الله لي الوسيلة','49','0','0');
INSERT INTO [t1462] VALUES (50,'تقبل شفاعة محمد الكبرى، وارفع درجته العليا وأعطه سؤله في الآخرة والأولى كما آتيت إبراهيم','50','0','0');
INSERT INTO [t1462] VALUES (51,'من قال: اللهم صل على محمد وأنزله المقعد المقرب منك يوم القيامة، وجبت له','51','0','0');
INSERT INTO [t1462] VALUES (52,'ما جلس قوم مجلسا لم يذكروا الله، ولم يصلوا على نبيهم، إلا كان مجلسهم عليهم ترة يوم','52','0','0');
INSERT INTO [t1462] VALUES (53,'ما من قوم يقعدون ثم يقومون ولا يصلون على النبي صلى الله عليه وسلم إلا كان عليهم يوم','53','0','0');
INSERT INTO [t1462] VALUES (54,'صل على محمد وعلى آل محمد كما صليت على آل إبراهيم إنك حميد مجيد','54','0','0');
INSERT INTO [t1462] VALUES (55,'صل على محمد وعلى آل محمد كما صليت على إبراهيم وآل إبراهيم إنك حميد مجيد وبارك على محمد','55','0','0');
INSERT INTO [t1462] VALUES (56,'صل على محمد وعلى آل محمد كما صليت على إبراهيم وآل إبراهيم إنك حميد مجيد. قال: ونحن نقول:','56','0','0');
INSERT INTO [t1462] VALUES (57,'صل على محمد النبي الأمي وعلى آل محمد كما صليت على إبراهيم وعلى آل إبراهيم وبارك على محمد','57','0','0');
INSERT INTO [t1462] VALUES (58,'صل على محمد النبي الأمي، عليه السلام','58','0','0');
INSERT INTO [t1462] VALUES (59,'صل على محمد وعلى آل محمد كما صليت على إبراهيم وعلى آل إبراهيم إنك حميد مجيد، اللهم بارك','59','0','0');
INSERT INTO [t1462] VALUES (60,'وصل على محمد وعلى آل محمد كما صليت على إبراهيم وعلى آل إبراهيم','60','0','0');
INSERT INTO [t1462] VALUES (61,'صل على محمد وعلى آل محمد كما صليت على آل إبراهيم وبارك على محمد وعلى آل محمد كما باركت','61','0','0');
INSERT INTO [t1462] VALUES (62,'صل على عبدك ورسولك وأهل بيته كما صليت على آل إبراهيم إنك حميد مجيد , وبارك عليه وأهل بيته','62','0','0');
INSERT INTO [t1462] VALUES (63,'اجعل صلواتك وبركاتك على آل محمد كما جعلتها على آل إبراهيم إنك حميد','63','0','0');
INSERT INTO [t1462] VALUES (64,'صل على محمد عبدك ورسولك كما صليت على آل إبراهيم وبارك على محمد وعلى آل محمد كما باركت على','64','0','0');
INSERT INTO [t1462] VALUES (65,'صل على محمد عبدك ورسولك كما صليت على إبراهيم وبارك على محمد وآل محمد كما باركت على','65','0','0');
INSERT INTO [t1462] VALUES (66,'صل على محمد كما صليت على إبراهيم إنك حميد مجيد، وبارك على محمد وعلى آل محمد كما باركت على','66','0','0');
INSERT INTO [t1462] VALUES (67,'صلوا علي وقولوا: اللهم بارك على محمد وعلى آل محمد كما باركت على إبراهيم وآل إبراهيم إنك','67','0','0');
INSERT INTO [t1462] VALUES (68,'اللهم صل على محمد وأزواجه وذريته كما صليت على آل إبراهيم، وبارك على محمد وأزواجه كما','68','0','0');
INSERT INTO [t1462] VALUES (69,'صل على آل محمد، كما صليت على آل إبراهيم، وبارك على آل محمد، كما باركت على آل','69','0','0');
INSERT INTO [t1462] VALUES (70,'صل على محمد، كما صليت على آل إبراهيم، اللهم بارك على محمد، كما باركت على آل','70','0','0');
INSERT INTO [t1462] VALUES (71,'صل على آل محمد، كما صليت على آل إبراهيم، اللهم بارك على محمد، كما باركت على آل','71','0','0');
INSERT INTO [t1462] VALUES (72,'ما من دعوة لا يصلى على النبي صلى الله عليه وسلم قبلها إلا كانت معلقة بين السماء','72','0','0');
INSERT INTO [t1462] VALUES (73,'لا تصلوا صلاة على أحد إلا على النبي صلى الله عليه وسلم، ولكن يدعى للمسلمين والمسلمات','73','0','0');
INSERT INTO [t1462] VALUES (74,'صلاتهم على النبيين ودعاؤهم للمسلمين عامة، ويدعوا ما سوى ذلك','74','0','0');
INSERT INTO [t1462] VALUES (75,'صلى الله عليك وعلى زوجك','75','0','0');
INSERT INTO [t1462] VALUES (76,'غفر الله له ما تقدم من ذنبه وما تأخر، وقد أمرت أن أصلي عليه','76','0','0');
INSERT INTO [t1462] VALUES (77,'يستحب للرجل إذا فرغ من تلبيته أن يصلي على النبي صلى الله عليه وسلم','77','0','0');
INSERT INTO [t1462] VALUES (78,'إذا مررتم بالمساجد فصلوا على النبي صلى الله عليه وسلم','78','0','0');
INSERT INTO [t1462] VALUES (79,'فطوفوا بالبيت سبعا، وصلوا عند المقام ركعتين، ثم ائتوا الصفا، فقوموا من حيث ترون البيت،','79','0','0');
INSERT INTO [t1462] VALUES (80,'صل على محمد، وعلى آل محمد، واغفر لنا، وسهل لنا أبواب رحمتك، فإذا فرغت، فقولي مثل ذلك، غير','80','0','0');
INSERT INTO [t1462] VALUES (81,'صل على محمد، وعلى آل محمد، اللهم اغفر لنا وارحمنا وافتح لنا أبواب رحمتك. حدثنا يحيى بن','81','0','0');
INSERT INTO [t1462] VALUES (82,'صلى الله وملائكته على محمد، السلام عليك أيها النبي ورحمة الله وبركاته','82','0','0');
INSERT INTO [t1462] VALUES (83,'صلى الله وملائكته على محمد، السلام عليك أيها النبي ورحمة الله، قلت من حدثك؟ أنت سمعته؟','83','0','0');
INSERT INTO [t1462] VALUES (84,'لا إله إلا الله وحده لا شريك له، له الملك وله الحمد، وهو على كل شيء قدير، ثم يصلي على','84','0','0');
INSERT INTO [t1462] VALUES (85,'فتكبر تكبيرة تفتتح بالصلاة، وتحمد ربك، وتصلي على النبي محمد صلى الله عليه وسلم، ثم تدعو','85','0','0');
INSERT INTO [t1462] VALUES (86,'فحمد الله وأثنى عليه، وصلى على النبي صلى الله عليه وسلم، ودعا بدعوات، ثم قام فصلى','86','0','0');
INSERT INTO [t1462] VALUES (87,'أول تكبيرة من الصلاة على الجنازة ثناء على الله عز وجل، والثانية صلاة على النبي، والثالثة','87','0','0');
INSERT INTO [t1462] VALUES (88,'بارك فيه وصل عليه واغفر له وأورده حوض نبيك صلى الله عليه وسلم','88','0','0');
INSERT INTO [t1462] VALUES (89,'كبرت وحمدت الله وصليت على نبيه صلى الله عليه وسلم ثم أقول: اللهم هذا عبدك ابن عبدك وابن','89','0','0');
INSERT INTO [t1462] VALUES (90,'يقرأ بفاتحة الكتاب، ويصلي على النبي صلى الله عليه وسلم، ثم يخلص الدعاء للميت متى يفرغ،','90','0','0');
INSERT INTO [t1462] VALUES (91,'صلاة الله عز وجل عليه ثناؤه عليه، وصلاة الملائكة عليه الدعاء','91','0','0');
INSERT INTO [t1462] VALUES (92,'صلاة الله: رحمته، وصلاة الملائكة: الدعاء','92','0','0');
INSERT INTO [t1462] VALUES (93,'صلاة الله: مغفرته، وصلاة الملائكة: الدعاء','93','0','0');
INSERT INTO [t1462] VALUES (94,'يقف على قبر النبي ويصلي على النبي صلى الله عليه وسلم وأبي بكر وعمر رضي الله','94','0','0');
INSERT INTO [t1462] VALUES (95,'السلام عليك يا رسول الله، السلام على أبي بكر، السلام على أبي، ويصلي','95','0','0');
INSERT INTO [t1462] VALUES (96,'السلام عليك يا رسول الله، السلام عليك يا أبا بكر، السلام عليك يا أبتاه','96','0','0');
INSERT INTO [t1462] VALUES (97,'يسلم على النبي صلى الله عليه وسلم ثم على أبي بكر وعمر رضي الله عنهما','97','0','0');
INSERT INTO [t1462] VALUES (98,'وينزل سبعون ألفا من الملائكة حتى يحفوا بالقبر يضربون بأجنحتهم ويصلون على النبي صلى الله','98','0','0');
INSERT INTO [t1462] VALUES (99,'ورفعنا لك ذكرك} [الشرح: ٤] قال: لا أذكر إلا ذكرت أشهد أن لا إله إلا الله، أشهد أن محمدا','99','0','0');
INSERT INTO [t1462] VALUES (100,'ورفعنا لك ذكرك} [الشرح: ٤] فقال النبي: ابدءوا بالعبودية وثنوا بالرسالة قال معمر: أشهد أن','100','0','0');
INSERT INTO [t1462] VALUES (101,'يستقبلون الإمام إذا خطب ولكنهم كانوا لا يسعون إنما هو قصص وصلاة على النبي صلى الله عليه','101','0','0');
INSERT INTO [t1462] VALUES (102,'إذا صلى أحدكم فليبدأ بتمجيد الله والثناء عليه ثم يصلي على النبي صلى الله عليه وسلم ثم','102','0','0');
INSERT INTO [t1462] VALUES (103,'يصلي على النبي صلى الله عليه وسلم في القنوت','103','0','0');
