INSERT INTO [t147663] VALUES (20,'والشرط الثاني: أن يكون بشيء (مباح)','20','18','0');
INSERT INTO [t147663] VALUES (21,'والشرط الثالث: أن يكون بشيء (يابس)','20','18','0');
INSERT INTO [t147663] VALUES (22,'والشرط الرابع: أن يكون بشيء (منق)','20','18','0');
INSERT INTO [t147663] VALUES (23,'(و) الشرط السادس: (عدم تعدي خارج موضع العادة)','21','18','0');
INSERT INTO [t147663] VALUES (24,'(و) الشرط السابع: ألا يكون روثا أو عظما، ولو طاهرين','21','18','0');
INSERT INTO [t147663] VALUES (25,'(و) الشرط الثامن: ألا يكون بـ (طعام ولو لبهيمة)','22','18','0');
INSERT INTO [t147663] VALUES (26,'مسألة: (ولا يصح وضوء ولا تيمم قبله)','22','16','0');
INSERT INTO [t147663] VALUES (27,'مسألة: (وحرم) عند قضاء الحاجة','22','16','0');
INSERT INTO [t147663] VALUES (28,'(فصل) في السواك وما ألحق به من الادهان وسنن الفطرة','24','2','0');
INSERT INTO [t147663] VALUES (29,'مسألة: (والسواك) له وقتان','24','28','0');
