INSERT INTO [t147663] VALUES (31,'مسألة: يسن السواك كل وقت، (إلا لصائم)','24','30','0');
INSERT INTO [t147663] VALUES (32,'مسألة: يسن السواك بالعود،','25','30','0');
INSERT INTO [t147663] VALUES (33,'الوقت الثاني: وقت مقيد','26','29','0');
INSERT INTO [t147663] VALUES (34,'مسألة: (وسن بداءة بـ) الجانب (الأيمن في سواك)','27','28','0');
INSERT INTO [t147663] VALUES (35,'مسألة: (و) يسن (ادهان) في البدن، وشعر الرأس، واللحية؛ غبا','27','28','0');
INSERT INTO [t147663] VALUES (36,'مسألة: (و) يسن (اكتحال) في كل عين ثلاثا بالإثمد المطيب','28','28','0');
INSERT INTO [t147663] VALUES (37,'مسألة: (و) يسن (نظر في مرآة)','28','28','0');
INSERT INTO [t147663] VALUES (38,'مسألة: (و) يسن (تطيب)','29','28','0');
INSERT INTO [t147663] VALUES (39,'مسألة: (و) يسن (استحداد)','29','28','0');
INSERT INTO [t147663] VALUES (40,'مسألة: (ويجب ختان ذكر وأنثى) وخنثى','29','28','0');
INSERT INTO [t147663] VALUES (41,'- مسألة: وقت الختان ينقسم إلى أربعة أقسام','30','28','0');
