INSERT INTO [t147663] VALUES (44,'مسألة: (والتسمية)','31','42','0');
INSERT INTO [t147663] VALUES (45,'مسألة: (ويجب غسل يدي القائم من نوم الليل)','32','42','0');
INSERT INTO [t147663] VALUES (46,'مسألة: (وشروط الوضوء) ولو مستحبا (ثمانية)','32','42','0');
INSERT INTO [t147663] VALUES (47,'مسألة: (وفروضه ستة)','34','42','0');
INSERT INTO [t147663] VALUES (48,'مسألة: ضابط الموالاة','36','42','0');
INSERT INTO [t147663] VALUES (49,'مسألة: (ويسقطان) أي: الترتيب والموالاة (مع غسل) عن حدث أكبر','37','42','0');
INSERT INTO [t147663] VALUES (50,'(فصل) في المسح على الخفين وغيرهما من الحوائل','37','2','0');
INSERT INTO [t147663] VALUES (51,'مسألة: (يجوز المسح على الخفين)','37','50','0');
INSERT INTO [t147663] VALUES (52,'- مسألة: يجوز المسح على الخفين ونحوهما (بسبعة شروط)','38','50','0');
INSERT INTO [t147663] VALUES (53,'- مسألة: (فيمسح مقيم) يوما وليلة، (و) يمسح (عاص بسفره من حدث بعد لبس يوما وليلة) كمقيم، فلا يستبيح الرخصة بمعصيته، ويأتي الكلام عن ابتداء المسح.','40','50','0');
INSERT INTO [t147663] VALUES (54,'- مسألة: (و) يمسح (مسافر سفر قصر لم يعص به) أي: بالسفر، بأن كان غير محرم ولا مكروه، ولو عصى فيه: (ثلاثة) أيام (بلياليهن)؛ لحديث علي رضي الله عنه: «جعل رسول الله صلى الله عليه وسلم ثلاثة أيام ولياليهن للمسافر، ويوما وليلة للمقيم» [رواه مسلم ٢٧٦].','41','50','0');
INSERT INTO [t147663] VALUES (55,'- مسألة: تبتدئ مدة المسح من حدث بعد لبس؛ لحديث صفوان بن عسال رضي الله عنه قال: «كان رسول الله صلى الله عليه وسلم يأمرنا إذا كنا سفرا أن لا ننزع خفافنا ثلاثة أيام ولياليهن، إلا من جنابة، ولكن من غائط وبول ونوم» [أحمد ١٨٠٩١، والترمذي ٩٦]، فدل بمفهومه: أنها تنزع لثلاث مضين من الغائط.','41','50','0');
INSERT INTO [t147663] VALUES (56,'- مسألة: (فلو مسح في سفر ثم أقام) أتم مسح مقيم إن بقي منه','41','50','0');
INSERT INTO [t147663] VALUES (57,'- مسألة: (أو) مسح (في حضر ثم سافر) أتم مسح مقيم؛ تغليبا لجانب الحضر.','42','50','0');
INSERT INTO [t147663] VALUES (58,'- مسألة: (أو شك) ماسح بسفر (في ابتداء المسح) بأن لم يدر: أمسح مقيما أو مسافرا؟ ؛ (لم يزد على مسح مقيم)؛ لأنه اليقين، وما زاد عليه لم يتحقق شرطه، والأصل عدمه.','42','50','0');
INSERT INTO [t147663] VALUES (59,'- مسألة: (ويجوز المسح على جبيرة) مشدودة على كسر أو جرح ونحوهما؛ لحديث جابر رضي الله عنه في صاحب الشجة: أن النبي صلى الله عليه وسلم قال: «إنما كان يكفيه أن يتيمم، ويعصب على جرحه خرقة، ثم يمسح عليها، ويغسل سائر جسده» [أبو داود ٣٣٦]، وصح عن ابن عمر وابن عباس رضي الله عنهم [الأوسط لابن المنذر ٥٢٦ - ٥٢٧].','42','50','0');
INSERT INTO [t147663] VALUES (60,'- مسألة: (و) يبطل المسح على الخف ونحوه بأمور، منها','44','50','0');
INSERT INTO [t147663] VALUES (61,'(فصل) في (نواقض الوضوء)','45','2','0');
INSERT INTO [t147663] VALUES (62,'أي: مفسداته، وهي (ثمانية)','45','61','0');
INSERT INTO [t147663] VALUES (63,'أحدها: (خارج من سبيل) أي: مخرج بول أو غائط','45','62','0');
INSERT INTO [t147663] VALUES (64,'(و) الناقض الثاني: خارج من بقية البدن من غير السبيلين','46','62','0');
INSERT INTO [t147663] VALUES (65,'(و) الناقض الثالث: (زوال عقل) بجنون، أو تغطيته بإغماء أو نوم','47','62','0');
INSERT INTO [t147663] VALUES (66,'(و) الناقض الرابع: (غسل ميت)، مسلما كان أو كافرا، ذكرا كان أو أنثى، صغيرا كان أو كبيرا، (أو) غسل (بعضه) أي: الميت','48','62','0');
INSERT INTO [t147663] VALUES (67,'(و) الناقض الخامس: (أكل لحم إبل) خاصة، (ولو نيئا)','48','62','0');
INSERT INTO [t147663] VALUES (68,'(و) الناقض السادس: (مس فرج)، لا العانة ولا الأنثيين','50','62','0');
INSERT INTO [t147663] VALUES (69,'- مسألة: (لا) ينقض (مس الخصيتين)؛ لأنه ليس بذكر، (ولا) ينقض مس (محل الفرج البائن)؛ لأنه ليس بفرج.','52','68','0');
INSERT INTO [t147663] VALUES (70,'(و) الناقض السابع: (لمس ذكر) أنثى لشهوة، سواء كان اللمس باليد أو بغيرها، ولو بزائد','52','62','0');
INSERT INTO [t147663] VALUES (71,'(و) الناقض الثامن: (الردة) عن الإسلام','53','62','0');
INSERT INTO [t147663] VALUES (72,'- مسألة: (وكل ما أوجب غسلا) كإسلام، وانتقال مني ونحوهما، (أوجب وضوءا)','53','61','0');
INSERT INTO [t147663] VALUES (73,'- مسألة: (ولا نقض)','54','61','0');
INSERT INTO [t147663] VALUES (74,'- مسألة: (ومن شك في طهارة) وتيقن الحدث، بنى على اليقين؛ وهو الحدث','54','61','0');
INSERT INTO [t147663] VALUES (75,'(فصل) في الغسل','55','2','0');
INSERT INTO [t147663] VALUES (76,'- مسألة: (موجبات الغسل) أي: الأشياء التي توجبه (سبعة)','55','75','0');
INSERT INTO [t147663] VALUES (77,'- مسألة: (ومصلى العيد لا) مصلى (الجنائز مسجد)؛ لحديث أم عطية رضي الله عنها قالت: قال النبي صلى الله عليه وسلم: «ويعتزل الحيض المصلى» [البخاري ٣٢٤، ومسلم ٨٩٠].','59','75','0');
INSERT INTO [t147663] VALUES (78,'- مسألة: (ويحرم تكسب بصنعة فيه) أي: في المسجد؛ لأنه لم يبن لذلك، ولحديث أبي هريرة رضي الله عنه قال: قال النبي صلى الله عليه وسلم: «إذا رأيتم من يبيع، أو يبتاع في المسجد، فقولوا: لا أربح الله تجارتك» [الترمذي ١٣٢١].','59','75','0');
INSERT INTO [t147663] VALUES (79,'(فصل)','60','2','0');
INSERT INTO [t147663] VALUES (80,'- مسألة: (وشروط الغسل سبعة)','60','79','0');
INSERT INTO [t147663] VALUES (81,'- مسألة: تنقسم صفة الغسل إلى قسمين','60','79','0');
INSERT INTO [t147663] VALUES (82,'- مسألة: (ومن) اجتمع له غسل مسنون وغسل واجب لم يخل من أحوال','61','79','0');
INSERT INTO [t147663] VALUES (83,'- مسألة: يسن لجنب الوضوء لنوم؛ لما جاء عن عمر رضي الله عنه: أنه سأل رسول الله صلى الله عليه وسلم: أيرقد أحدنا وهو جنب؟ قال: «نعم إذا توضأ أحدكم فليرقد وهو جنب» [البخاري: ٢٨٧، ومسلم: ٣٠٦]، ولم يجب؛ لأنه جواب سؤال، (وكره نوم جنب بلا وضوء)؛ لظاهر الحديث السابق.','62','79','0');
INSERT INTO [t147663] VALUES (84,'- مسألة: (ويكره)','62','79','0');
INSERT INTO [t147663] VALUES (85,'(فصل) في التيمم','65','2','0');
INSERT INTO [t147663] VALUES (86,'- مسألة: (التيمم) لغة: القصد، وشرعا: التعبد لله بـ (استعمال تراب مخصوص، لـ) مسح (وجه ويدين) على وجه مخصوص.','65','85','0');
INSERT INTO [t147663] VALUES (87,'- مسألة: التيمم (بدل طهارة ماء، لكل ما يفعل به) أي: بالماء (عند عجز عنه شرعا)، فيقوم التيمم مقام استعمال الماء في ثلاثة أمور','65','85','0');
INSERT INTO [t147663] VALUES (88,'- مسألة: (وشروطه) أي: التيمم (ثلاثة)','67','85','0');
INSERT INTO [t147663] VALUES (89,'- مسألة: (ومن وجد ماء) يكفي بعض طهره، من حدث أكبر أو أصغر، و (لا يكفي طهارته) كاملة، (استعمله وجوبا) فيما يقدر عليه، (ثم تيمم) للباقي؛ لحديث أبي هريرة رضي الله عنه: أن النبي صلى الله عليه وسلم قال: «وإذا أمرتكم بأمر فأتوا منه ما استطعتم» [البخاري ٧٢٨٨، ومسلم ١٣٣٠]، ويكون التيمم بعد استعمال الماء ليتحقق الشرط الذي هو عدم الماء.','68','85','0');
INSERT INTO [t147663] VALUES (90,'- مسألة: (فإن لم يجد ذلك) أي: الماء والتراب؛ كمن حبس بمحل لا ماء فيه، ولا تراب، أو لم يمكنه استعمالهما؛ كمن به جراحات لا يمكن مسها','71','85','0');
INSERT INTO [t147663] VALUES (91,'- مسألة: (وفروضه) أي: فروض التيمم أربعة','72','85','0');
INSERT INTO [t147663] VALUES (92,'- مسألة: (ويبطله) أي: التيمم بخمسة أمور','74','85','0');
INSERT INTO [t147663] VALUES (93,'(فصل) في النجاسات وكيفية تطهيرها','76','2','0');
INSERT INTO [t147663] VALUES (94,'- مسألة: تطهير النجاسات على قسمين','76','93','0');
INSERT INTO [t147663] VALUES (95,'- مسألة: يشترط في إزالة النجاسة الحكمية: أن تكون الإزالة (بماء طهور)، فلا تطهر أرض متنجسة بغير الماء من المائعات، ولا بالشمس، ولا بالريح، ولا بالجفاف؛ لحديث أنس - رضي الله عنه - في بول الأعرابي السابق،','76','93','0');
INSERT INTO [t147663] VALUES (96,'- مسألة: (ويضر بقاء طعم) مطلقا؛ لدلالته على بقاء العين، ولسهولة إزالته.','78','93','0');
INSERT INTO [t147663] VALUES (97,'- مسألة: (ولا تطهر أرض) متنجسة ولا غيرها (بشمس، و) لا (ريح، و) لا (جفاف)؛ لأنه صلى الله عليه وسلم أمر بغسل بول الأعرابي، ولو كان ذلك يطهر لاكتفى به.','80','93','0');
INSERT INTO [t147663] VALUES (98,'- مسألة: (ولا) تطهر (نجاسة) عينية بالاستحالة، فلا تطهر (بنار، فرمادها) أي: النجاسة، وغبارها، وبخارها، (نجس)؛ لقول ابن عمر رضي الله عنهما: «نهى رسول الله - صلى الله عليه وسلم - عن أكل الجلالة وألبانها» [أبو داود: ٣٧٨٥، والترمذي: ١٨٢٤]، لأنها تأكل النجاسة، ولو طهرت بالاستحالة لم ينه عنه.','81','93','0');
INSERT INTO [t147663] VALUES (99,'- مسألة: (وإن خفيت نجاسة) في بدن أو ثوب (غسل) ما احتمل أن النجاسة أصابته (حتى يتيقن غسلها)؛ ليخرج من العهدة بيقين، فإن جهل جهتها من بدن أو ثوب، غسله كله.','82','93','0');
INSERT INTO [t147663] VALUES (100,'(فصل) في بيان النجاسات','83','2','0');
INSERT INTO [t147663] VALUES (101,'- مسألة: (المسكر المائع) نجس، خمرا كان أو غيره؛ لقول الله تعالى: {إنما الخمر والميسر والأنصاب والأزلام رجس من عمل الشيطان فاجتنبوه} [المائدة: ٩٠]، واختاره شيخ الإسلام.','83','100','0');
INSERT INTO [t147663] VALUES (102,'- مسألة: (و) كل (ما لا يؤكل) لحمه (من الطير) كصقر، (والبهائم) كأسد، (مما) هو (فوق الهر خلقة نجس)؛ لحديث ابن عمر رضي الله عنهما قال: سئل رسول الله صلى الله عليه وسلم عن الماء وما ينوبه من الدواب والسباع؟ فقال: «إذا كان الماء قلتين لم يحمل الخبث» [أحمد: ٤٦٠٥، وأبو داود: ٦٣، والترمذي: ٦٧، والنسائي: ٥٢، وابن ماجه: ٥١٧]، فمفهومه أنه ينجس إذا لم يبلغهما.','83','100','0');
INSERT INTO [t147663] VALUES (103,'- مسألة: (وكل ميتة نجسة)؛ لقول الله عز وجل: (قل لا أجد في ما أوحي','83','100','0');
INSERT INTO [t147663] VALUES (104,'- مسألة: (ويعفى عن يسير طين شارع عرفا إن علمت نجاسته)؛ لمشقة التحرز منه، (وإلا) تتحقق نجاسته، بأن علم طهارته أو شك في نجاسته (فهو طاهر)؛ لأن الأصل فيه الطهارة.','85','100','0');
INSERT INTO [t147663] VALUES (105,'- مسألة: (ولا يكره) استعمال (سؤر حيوان طاهر، وهو فضلة طعامه وشرابه)؛ لحديث أبي قتادة رضي الله عنه السابق: «إنها ليست بنجس، إنها من الطوافين عليكم والطوافات»، (غير)','85','100','0');
INSERT INTO [t147663] VALUES (106,'- مسألة: (ولو أكل هر ونحوه) كفأر، (أو) أكل (طفل نجاسة ثم','85','100','0');
INSERT INTO [t147663] VALUES (107,'(فصل) في الحيض، والنفاس','86','2','0');
INSERT INTO [t147663] VALUES (108,'- مسألة: (وأقل سن حيض) يمكن أن تحيض فيه المرأة: (تمام تسع سنين)؛ لأنه لم يثبت في الوجود والعادة لأنثى حيض قبل استكمالها، (وأكثره: خمسون سنة)؛ لقول عائشة رضي الله عنها: (إذا بلغت المرأة خمسين سنة خرجت من حد الحيض) [ذكره أحمد، ولم نجد من خرجه].','86','107','0');
INSERT INTO [t147663] VALUES (109,'- مسألة: (والحامل لا تحيض)، فإذا رأت الحامل دما فهو دم فساد، تغتسل عند انقطاعه استحبابا، إلا أن تراه قبل ولادتها بيومين أو ثلاثة مع أمارة فنفاس؛ لحديث أبي سعيد رضي الله عنه: أن النبي صلى الله عليه وسلم قال في سبايا أوطاس: «لا توطأ حامل حتى تضع، ولا غير ذات حمل حتى تحيض حيضة» [أحمد ١١٥٩٦، وأبو داود ٢١٥٧]، فجعل الحيض علما على براءة الرحم، فدل على أنه لا يجتمع معه.','87','107','0');
INSERT INTO [t147663] VALUES (110,'- مسألة: (وأقله) أي: أقل زمن يصلح أن يكون الدم فيه دم حيض: (يوم وليلة)؛ لقول علي رضي الله عنه: (أقل الحيض يوم وليلة) [ذكره الأصحاب، ولم نجده مسندا، قال ابن حجر: كأنه يشير إلى ما ذكره البخاري تعليقا عن علي وشريح وسيأتي قريبا]، ولأن الشرع علق على الحيض أحكاما ولم يبينه، فعلم أنه رده إلى العرف، كالقبض والحرز، وقد وجد حيض معتاد يوما، ولم يوجد أقل منه، (وأكثره: خمسة عشر يوما) بلياليها؛ لما روي عن ابن عمر رضي الله عنهما مرفوعا: «النساء ناقصات عقل ودين»، قيل: وما نقصان دينهن؟ قال: «تمكث إحداهن شطر عمرها لا تصلي» [ذكره الأصحاب، ولم نجده مسندا].','87','107','0');
INSERT INTO [t147663] VALUES (111,'- مسألة: (وغالبه) أي: غالب الحيض: (ست أو سبع) ليال بأيامها، اتفاقا؛ لقوله صلى الله عليه وسلم لحمنة بنت جحش لما سألته: «فتحيضي ستة أيام أو سبعة أيام في علم الله، ثم اغتسلي، فإذا رأيت أنك قد طهرت واستنقأت فصلي أربعا وعشرين ليلة، أو ثلاثا وعشرين ليلة وأيامها، وصومي وصلي، فإن ذلك يجزئك، وكذلك فافعلي كما تحيض النساء وكما يطهرن» [أحمد ٢٧٤٧٤، وأبوداود ٢٨٧، والترمذي ١٢٨، وابن ماجه ٦٢٧]','88','107','0');
INSERT INTO [t147663] VALUES (112,'- مسألة: (وأقل الطهر بين الحيضتين ثلاثة عشر يوما، وغالبه) أي: الطهر بين الحيضتين: (بقية الشهر) أي: أربعة وعشرون يوما، أو ثلاثة وعشرون يوما؛ لما روى أحمد واحتج به، عن علي رضي الله عنه: أن امرأة جاءته وقد طلقها زوجها، فزعمت أنها حاضت في شهر ثلاث حيض، فقال علي لشريح: «قل فيها»، فقال شريح: إن جاءت ببينة من بطانة أهلها ممن يرجى دينه وأمانته فشهدت بذلك، وإلا فهي كاذبة، فقال علي: «قالون». أي: جيد، بالرومية. [ابن أبي شيبة ١٩٦٤١]، ووجود ثلاث حيض في شهر، دليل على أن الثلاثة عشر طهر صحيح يقينا.','88','107','0');
INSERT INTO [t147663] VALUES (113,'- مسألة: (ولا حد لأكثره) أي: أكثر الطهر بين الحيضتين، اتفاقا؛ لأن المرأة قد لا تحيض أصلا، وقد تحيض في السنة مرة واحدة.','89','107','0');
INSERT INTO [t147663] VALUES (114,'- مسألة: (ويحرم عليها) أي: الحائض، وكذا النفساء: (فعل صلاة، ولا تقضيها، و) يحرم (فعل صوم، وتقضيه) إجماعا؛ لقول عائشة رضي الله عنها: «كان يصيبنا ذلك، فنؤمر بقضاء الصوم، ولا نؤمر بقضاء الصلاة» [البخاري ٣٢١، ومسلم ٣٣٥].','89','107','0');
INSERT INTO [t147663] VALUES (115,'- مسألة: (و) الاستمتاع بالحائض لا يخلو من ثلاثة أقسام','89','107','0');
INSERT INTO [t147663] VALUES (116,'- مسألة: (والنفاس) وهو: دم ترخيه الرحم للولادة وبعدها، وهو بقية الدم الذي احتبس في الرحم مدة الحمل لأجله.','90','107','0');
INSERT INTO [t147663] VALUES (117,'- مسألة: النفاس (لا حد لأقله)؛ لأنه لم يرد تحديده، فرجع فيه إلى الوجود، وقد وجد قليلا وكثيرا،','90','107','0');
INSERT INTO [t147663] VALUES (118,'- مسألة: (ويثبت حكمه) أي: النفاس (بوضع ما يتبين فيه خلق إنسان)، ولو خفيا فلو وضعت علقة، أو مضغة لا تخطيط فيها، لم يثبت لها بذلك حكم النفاس، وأقل ما يتبين فيه خلق الإنسان: واحد وثمانون يوما، وغالبها: ثلاثة أشهر.','91','107','0');
INSERT INTO [t147663] VALUES (119,'- مسألة: (والنقاء زمنه) أي: زمن النفاس (طهر)؛ لأن الله تعالى وصف الحيض بكونه أذى، فإذا ذهب الأذى زال حكمه، والنفاس مثله.','91','107','0');
INSERT INTO [t147663] VALUES (120,'- مسألة: (ويكره الوطء فيه) أي: في النقاء من النفاس قبل الأربعين بعد الغسل، وهو من المفردات؛ لأن عثمان بن أبي العاص رضي الله عنه كان يقول','91','107','0');
INSERT INTO [t147663] VALUES (121,'- مسألة: (وهو) أي: النفاس (كحيض في) جميع (أحكامه)، فهو كالحيض','92','107','0');
INSERT INTO [t147663] VALUES (122,'- مسألة: (تجب) الصلوات (الخمس) في اليوم والليلة (على كل)','93','107','0');
INSERT INTO [t147663] VALUES (123,'(كتاب الصلاة)','93','0','0');
INSERT INTO [t147663] VALUES (124,'الصلاة في اللغة: الدعاء','93','123','0');
INSERT INTO [t147663] VALUES (125,'- مسألة: (ومن تركها) أي: الصلاة لم يخل من أمرين','94','123','0');
INSERT INTO [t147663] VALUES (126,'(فصل) في الأذان والإقامة','95','123','0');
INSERT INTO [t147663] VALUES (127,'- مسألة: (الأذان والإقامة فرضا كفاية)؛ لحديث مالك بن الحويرث رضي الله عنه: قال - صلى الله عليه وسلم -: «فإذا حضرت الصلاة فليؤذن لكم أحدكم، وليؤمكم أكبركم» [البخاري: ٦٢٨، ومسلم: ٦٧٤]، وهذا أمر يفيد الوجوب، وقوله (أحدكم) يدل على أنه على الكفاية، وهو من المفردات.','95','126','0');
INSERT INTO [t147663] VALUES (128,'- مسألة: يجب الأذان والإقامة (على) من اجتمعت فيه ثلاثة شروط','95','126','0');
INSERT INTO [t147663] VALUES (129,'- مسألة: (ويسنان) أي: الأذان والإقامة، (لمنفرد)، ولا يجبان، وهو اختيار شيخ الإسلام؛ لحديث عقبة بن عامر رضي الله عنه مرفوعا: «يعجب ربك من راعي غنم في رأس شظية الجبل يؤذن بالصلاة ويصلي، فيقول الله عز وجل: انظروا إلى عبدي هذا يؤذن ويقيم الصلاة يخاف مني، قد غفرت لعبدي وأدخلته الجنة» [أحمد: ١٧٤٤٢، وأبو داود: ١٢٠٣ والنسائي: ٦٦٦].','96','126','0');
INSERT INTO [t147663] VALUES (130,'- مسألة: (و) يسنان، أي: الأذان والإقامة (سفرا)، ولا يجبان عليه؛ لما ورد عن ابن عمر رضي الله عنهما أنه كان لا يؤذن في السفر ويقول: «إنما الأذان للإمام الذي يجتمع إليه الناس» [مالك: ٢٣٧].','96','126','0');
INSERT INTO [t147663] VALUES (131,'- مسألة: (ولا يصحان) أي: الأذان والإقامة (إلا) بأحد عشر شرطا','97','126','0');
INSERT INTO [t147663] VALUES (132,'- مسألة: (وهو) أي: الأذان المختار أذان بلال، وهو: (خمس عشرة كلمة، بلا ترجيع، وهي) أي: الإقامة المختارة هي إقامة بلال','99','126','0');
INSERT INTO [t147663] VALUES (133,'- مسألة: (وحرم) على من وجبت عليه الصلاة (خروج من مسجد بعده) أي: بعد الأذان؛ لحديث أبي الشعثاء قال: كنا قعودا في المسجد مع أبي هريرة رضي الله عنه، فأذن المؤذن، فقام رجل من المسجد يمشي، فأتبعه','100','126','0');
INSERT INTO [t147663] VALUES (134,'- مسألة: (وسن أذان في يمنى أذني مولود) ذكرا كان أو أنثى (حين يولد)؛ لحديث أبي رافع رضي الله عنه قال: «رأيت رسول الله صلى الله عليه وسلم أذن في أذن الحسن بن علي حين ولدته فاطمة بالصلاة» [أحمد ٢٧١٨٦، وأبوداود: ٥١٠٥].','101','126','0');
INSERT INTO [t147663] VALUES (135,'(فصل) في شروط صحة الصلاة','102','123','0');
INSERT INTO [t147663] VALUES (136,'- مسألة: (وشروط صحة الصلاة ستة)','102','135','0');
INSERT INTO [t147663] VALUES (137,'- مسألة: (وشرطها) أي: النية','104','135','0');
INSERT INTO [t147663] VALUES (138,'- مسألة: (وزمنها) أي: النية على أقسام','104','135','0');
INSERT INTO [t147663] VALUES (139,'فصل','105','123','0');
INSERT INTO [t147663] VALUES (140,'- مسألة: (وأركان الصلاة أربعة عشر) بالاستقراء، وهي','105','139','0');
INSERT INTO [t147663] VALUES (141,'- مسألة: (وواجباتها) أي: الصلاة (ثمانية)','109','139','0');
INSERT INTO [t147663] VALUES (142,'- مسألة: (وسننها): أي: الصلاة ضربان: الأول: (أقوال، و) الثاني: (أفعال لا تبطل بترك شيء منها) أي: السنن (مطلقا)، أي: سواء تركه عمدا، أو جهلا، أو سهوا.','111','139','0');
INSERT INTO [t147663] VALUES (143,'- مسألة: (فسنن الأقوال إحدى عشرة، وهي)','111','139','0');
INSERT INTO [t147663] VALUES (144,'- مسألة: (وسنن الأفعال) كرفع اليدين في مواضعه، ووضع اليمنى على اليسرى ونحو ذلك، (مع الهيئات) سميت: «هيئة»؛ لأنها صفة في غيرها، كجهر الإمام في القراءة، وترتيل القراءة، ونحو ذلك: (خمس وأربعون)، مذكورة في المطولات.','114','139','0');
INSERT INTO [t147663] VALUES (145,'فصل','115','123','0');
INSERT INTO [t147663] VALUES (146,'- مسألة: (ويكره للمصلي) أمور، منها','115','145','0');
INSERT INTO [t147663] VALUES (147,'(فصل) في أحكام سجود السهو','116','123','0');
INSERT INTO [t147663] VALUES (148,'- مسألة: سجود السهو، بالنسبة إلى حكمه ينقسم إلى ثلاثة أقسام','116','147','0');
INSERT INTO [t147663] VALUES (149,'- مسألة: (وتبطل الصلاة بتعمد ترك سجود السهو الواجب الذي محله قبل السلام)؛ لأنه تعمد ترك واجب من واجبات الصلاة.','117','147','0');
INSERT INTO [t147663] VALUES (150,'- مسألة: محل سجود السهو قبل السلام في جميع الأحوال التي يشرع لها سجود السهو؛ لفعل النبي صلى الله عليه وسلم في حديث عبد الله بن بحينة رضي الله عنه لما ترك التشهد الأول ناسيا. [البخاري: ١٢٢٤، ومسلم: ٥٧٠].','117','147','0');
INSERT INTO [t147663] VALUES (151,'- مسألة: إن ترك المصلي التشهد الأول ناسيا لم يخل من أربعة أقسام','118','147','0');
INSERT INTO [t147663] VALUES (152,'- مسألة: من سلم قبل إتمام صلاته لم يخل من حالتين','120','147','0');
INSERT INTO [t147663] VALUES (153,'- مسألة: الكلام في الصلاة لا يخلو من أمرين','121','147','0');
INSERT INTO [t147663] VALUES (154,'- مسألة: (و) من شك في الصلاة هل زاد فيها أو نقص؛ فإنه (يبني على اليقين -وهو الأقل-)؛ لحديث أبي سعيد رضي الله عنه مرفوعا: «إذا شك أحدكم في صلاته، فلم يدر كم صلى ثلاثا أم أربعا، فليطرح الشك وليبن على ما استيقن، ثم يسجد سجدتين قبل أن يسلم، فإن كان صلى خمسا شفعن له صلاته، وإن كان صلى إتماما لأربع كانتا ترغيما للشيطان» [مسلم: ٥٧١].','123','147','0');
INSERT INTO [t147663] VALUES (155,'(فصل) في صلاة التطوع','125','123','0');
INSERT INTO [t147663] VALUES (156,'- مسألة: (أفضل تطوع البدن) الجهاد، قال أحمد: (لا أعلم شيئا بعد الفرائض أفضل من الجهاد)، ثم العلم، تعلمه وتعليمه؛ لما جاء في فضله، ثم (بعد الجهاد والعلم: صلاة التطوع)؛ لأن فرضها آكد الفروض، فتطوعها آكد التطوعات.','125','155','0');
INSERT INTO [t147663] VALUES (157,'- مسألة: (وآكدها) أي: صلاة التطوع: (كسوف)؛ لأنه صلى الله عليه وسلم أمر بها في حديث عائشة رضي الله عنها وغيره [البخاري ١٠٥٨، ومسلم ٩٠١]، ولم يتركها عند وجود سببها، بخلاف الاستسقاء، فإنه كان يستسقي تارة، ويترك أخرى.','125','155','0');
INSERT INTO [t147663] VALUES (158,'- مسألة: حكم الوتر: سنة مؤكدة؛ لمداومة النبي صلى الله عليه وسلم عليها حضرا وسفرا، وليس بواجب؛ لقول علي رضي الله عنه: «الوتر ليس بحتم كصلاتكم المكتوبة، ولكن سنة سنها رسول الله صلى الله عليه وسلم» [أحمد ١٢٦١، والترمذي ٤٥٣، والنسائي ١٦٧٥، وابن ماجه ١١٦٩].','126','155','0');
INSERT INTO [t147663] VALUES (159,'- مسألة: عدد ركعات الوتر','126','155','0');
INSERT INTO [t147663] VALUES (160,'- مسألة: (ووقته) أي: الوتر، لا يخلو من أمرين','128','155','0');
INSERT INTO [t147663] VALUES (161,'- مسألة: (ويقنت فيه) أي: في الوتر، (بعد الركوع ندبا)؛ لحديث أبي هريرة رضي الله عنه: «أن رسول الله صلى الله عليه وسلم كان إذا أراد أن يدعو على أحد أو','128','155','0');
INSERT INTO [t147663] VALUES (162,'- مسألة: يسن أن يدعو في قنوته بما ثبت عن النبي صلى الله عليه وسلم وأصحابه، (فيقول) الإمام، والمنفرد نصا (جهرا)، -وقياس المذهب: يخير المنفرد بين الجهر بالقنوت وعدمه، وظاهر كلام جماعة: يختص الجهر بالإمام فقط','129','155','0');
INSERT INTO [t147663] VALUES (163,'- مسألة: (ويؤمن مأموم) إن سمعه؛ فيقول: آمين، قال ابن قدامة: لا نعلم فيه خلافا، لحديث ابن عباس رضي الله عنهما في قنوت النبي صلى الله عليه وسلم في النوازل: «ويؤمن من خلفه» [أحمد ٢٧٤٦، وأبو داود ١٤٤٣].','131','155','0');
INSERT INTO [t147663] VALUES (164,'- مسألة: ويجمع الإمام الضمير؛ لأنه يؤمن على دعائه، (ويفرد منفرد الضمير)؛ لأن المحفوظ من أدعيته صلى الله عليه وسلم في الصلاة أنها بلفظ الإفراد.','131','155','0');
INSERT INTO [t147663] VALUES (165,'- مسألة: (ثم يمسح) الداعي (وجهه بيديه هنا) أي: عقب القنوت، (وخارج الصلاة) إذا دعا؛ لما روى السائب عن أبيه: «أن النبي صلى الله عليه وسلم كان إذا دعا فرفع يديه، مسح وجهه بيديه» [أحمد ١٧٩٤٣، وأبو داود ١٤٩٢].','131','155','0');
INSERT INTO [t147663] VALUES (166,'- مسألة: (و) السنن (الرواتب المؤكدة) تفعل مع الفرائض، ويكره تركها في الحضر؛ لمداومة النبي صلى الله عليه وسلم عليها في الحضر، قال شيخ الإسلام: (من أصر على تركها - أي السنن - دل ذلك على قلة دينه، وردت شهادته في مذهب أحمد والشافعي وغيرهما).','132','155','0');
INSERT INTO [t147663] VALUES (167,'- مسألة: السنن الرواتب: ) عشر (ركعات: (ركعتان قبل الظهر، وركعتان بعدها، وركعتان بعد المغرب، وركعتان بعد العشاء، وركعتان قبل الفجر)؛ لقول ابن عمر رضي الله عنهما: «حفظت من النبي صلى الله عليه وسلم عشر ركعات: ركعتين قبل الظهر، وركعتين بعدها، وركعتين بعد المغرب في بيته، وركعتين بعد العشاء في بيته، وركعتين قبل صلاة الصبح» [البخاري ١١٨٠، ومسلم ٧٢٩].','132','155','0');
INSERT INTO [t147663] VALUES (168,'- مسألة: (وآكدها) أي: أفضل الرواتب (الفجر)؛ لقول عائشة رضي الله عنها','132','155','0');
INSERT INTO [t147663] VALUES (169,'- مسألة: (والتراويح) سنة مؤكدة، فعلها النبي صلى الله عليه وسلم بأصحابه، فعن عائشة رضي الله عنها: أن رسول الله صلى الله عليه وسلم صلى ذات ليلة في المسجد، فصلى بصلاته ناس، ثم صلى من القابلة، فكثر الناس، ثم اجتمعوا من الليلة الثالثة أو الرابعة، فلم يخرج إليهم رسول الله صلى الله عليه وسلم، فلما أصبح قال: «قد رأيت الذي صنعتم ولم يمنعني من الخروج إليكم إلا أني خشيت أن تفرض عليكم» وذلك في رمضان [البخاري ١١٢٩، ومسلم ٧٦١]، وجمع عمر الناس على أبي بن كعب رضي الله عنهم [البخاري ٢٠١٠]، واتفق عليها الصحابة.','133','155','0');
INSERT INTO [t147663] VALUES (170,'- مسألة: عدد ركعات التراويح (عشرون ركعة برمضان)؛ لما روى السائب بن يزيد قال: (كانوا يقومون على عهد عمر بن الخطاب رضي الله عنه في شهر رمضان بعشرين ركعة) [البيهقي في الكبرى ٤٣٩٣]، ولا بأس بالزيادة عليها.','133','155','0');
INSERT INTO [t147663] VALUES (171,'- مسألة: تسن التراويح (جماعة) قال أحمد: (كان علي وجابر وعبدالله يصلونها في الجماعة)، (يسلم منه كل ثنتين)؛ لحديث ابن عمر السابق: «صلاة الليل مثنى مثنى»، (بنية أول كل ركعتين)، فينوي أنهما من التراويح أو من قيام رمضان.','134','155','0');
INSERT INTO [t147663] VALUES (172,'- مسألة: (ووقتها) أي: التراويح، على قسمين','134','155','0');
INSERT INTO [t147663] VALUES (173,'(فصل) في صلاة الليل وغيرها','135','123','0');
INSERT INTO [t147663] VALUES (174,'- مسألة: صلاة التطوع نوعان','135','173','0');
INSERT INTO [t147663] VALUES (175,'- مسألة: (ويسن)','136','173','0');
INSERT INTO [t147663] VALUES (176,'- مسألة: (وكثرة الركوع والسجود أفضل من طول القيام) في غير ما ورد عن النبي صلى الله عليه وسلم تطويله، كصلاة الكسوف؛ لحديث أبي هريرة رضي الله عنه: أن النبي صلى الله عليه وسلم قال: «أقرب ما يكون العبد من ربه، وهو ساجد» [مسلم ٤٨٢].','137','173','0');
INSERT INTO [t147663] VALUES (177,'- مسألة: (وتسن صلاة الضحى)؛ لحديث أبي هريرة رضي الله عنه قال: «أوصاني خليلي صلى الله عليه وسلم بثلاث: صيام ثلاثة أيام من كل شهر، وركعتي الضحى، وأن أوتر قبل أن أنام» [البخاري ١٩٨١، ومسلم ٧٢١].','137','173','0');
INSERT INTO [t147663] VALUES (178,'- مسألة: (وتسن)','139','173','0');
INSERT INTO [t147663] VALUES (179,'- مسألة: (ويسن سجود تلاوة) واختاره ابن عثيمين؛ للأوامر الواردة فيه، ولا يجب؛ لما روى زيد بن ثابت رضي الله عنه قال: «قرأت على النبي صلى الله عليه وسلم (والنجم) فلم يسجد فيها» [البخاري ١٠٧٣، ومسلم ٥٧٧].','141','173','0');
INSERT INTO [t147663] VALUES (180,'- مسألة: يسن سجود التلاوة (لقارئ ومستمع) وهو الذي يقصد الاستماع؛ لقول ابن عمر رضي الله عنهما: «كان النبي صلى الله عليه وسلم يقرأ علينا السورة فيها السجدة فيسجد ونسجد، حتى ما يجد أحدنا موضعا لجبهته»، ولمسلم: «في غير صلاة» [البخاري ١٠٧٩، ومسلم ٥٧٥].','142','173','0');
INSERT INTO [t147663] VALUES (181,'- مسألة: (ويسن) في غير صلاة (سجود شكر) لله تعالى، (عند تجدد نعمة، أو اندفاع نقمة) مطلقا، سواء كانت النعمة عامة أو خاصة، دينية أو دنيوية؛ لحديث أبي بكرة رضي الله عنه: «أن النبي صلى الله عليه وسلم كان إذا أتاه أمر يسره أو بشر به، خر ساجدا، شكرا لله تبارك وتعالى» [أحمد ٢٠٤٥٥، وأبوداود ٢٧٧٤، والترمذي ١٥٧٨، وابن ماجه ١٣٩٤].','143','173','0');
INSERT INTO [t147663] VALUES (182,'- مسألة: (وأوقات النهي خمسة)','143','173','0');
INSERT INTO [t147663] VALUES (183,'- مسألة: (فيحرم فيها ابتداء) صلاة (نفل) في هذه الأوقات، ولا تنعقد، (مطلقا) أي: سواء كان عالما أو ناسيا أو جاهلا، حتى ما له سبب؛ كسنة راتبة، وسنة وضوء، ويستثنى من ذلك ما أشار إليه بقوله: (لا)','145','173','0');
INSERT INTO [t147663] VALUES (184,'(فصل) في صلاة الجماعة','147','123','0');
INSERT INTO [t147663] VALUES (185,'- مسألة: (صلاة الجماعة واجبة للـ) ـصلوات الـ (ـخمس) المكتوبة وجوبا عينيا؛ لقوله تعالى: {وإذا كنت فيهم فأقمت لهم الصلاة فلتقم طائفة منهم معك} [النساء: ١٠٢] الآية، فأمر بالجماعة حال الخوف، ففي غيره أولى.','147','184','0');
INSERT INTO [t147663] VALUES (186,'- مسألة: يشترط لوجوب صلاة الجماعة شروط','147','184','0');
INSERT INTO [t147663] VALUES (187,'- مسألة: (وتنعقد) صلاة الجماعة (باثنين)، قال في «الشر» ح: (بغير خلاف علمناه)؛ لحديث مالك بن الحويرث رضي الله عنه: «وليؤمكما أكبركما» [البخاري ٢٨٤٨، ومسلم ٦٧٤]، (في غير جمعة وعيد)؛ لاشتراط العدد فيهما، ويأتي في موضعه.','149','184','0');
INSERT INTO [t147663] VALUES (188,'- مسألة: تصح الجماعة في فرض ونفل (ولو بأنثى) والإمام رجل، أو خنثى، أو أنثى، (أو) كانت بـ (عبد) والإمام حر، أو عبد؛ لعموم ماسبق.','149','184','0');
INSERT INTO [t147663] VALUES (189,'- مسألة: (وحرم أن يؤم بمسجد له إمام راتب) قبله بغير إذنه؛ لقوله صلى الله عليه وسلم: «ولا يؤمن الرجل الرجل في سلطانه، ولا يقعد في بيته على تكرمته إلا بإذنه» [مسلم: ٦٧٣]، وإمام المسجد سلطان في مسجده.','150','184','0');
INSERT INTO [t147663] VALUES (190,'- مسألة: (ومن كبر) مأموما (قبل تسليمة الإمام) التسليمة (الأولى؛ أدرك الجماعة) ولو لم يجلس؛ لحديث عائشة رضي الله عنها مرفوعا: «من أدرك من العصر سجدة قبل أن تغرب الشمس، أو من الصبح قبل أن تطلع، فقد أدركها» [مسلم: ٦٠٨].','151','184','0');
INSERT INTO [t147663] VALUES (191,'- مسألة: (ومن أدرك الركوع) مع الإمام فقد (أدرك الركعة) اتفاقا؛ لحديث أبي بكرة رضي الله عنه: أنه انتهى إلى النبي صلى الله عليه وسلم وهو راكع، فركع قبل أن يصل إلى الصف، فذكر ذلك للنبي صلى الله عليه وسلم فقال: «زادك الله حرصا ولا تعد» [البخاري: ٧٨٣]، وإنما فعل ذلك ليدرك الركعة، ولم يأمره النبي - صلى الله عليه وسلم - بقضاء الركعة، ولثبوته عن ابن مسعود [البيهقي: ٢٥٧٨]، وابن عمر [ابن أبي شيبة: ٢٥٢٠].','152','184','0');
INSERT INTO [t147663] VALUES (192,'- مسألة: (وسن دخوله) أي: المأموم (مع إمامه كيف أدركه)، وإن لم يعتد بما أدركه فيه، لحديث أبي هريرة رضي الله عنه مرفوعا: «إذا جئتم إلى الصلاة ونحن سجود فاسجدوا، ولا تعدوها شيئا، ومن أدرك الركعة، فقد أدرك الصلاة» [أبوداود ٨٩٣].','152','184','0');
INSERT INTO [t147663] VALUES (193,'- مسألة: (وما أدرك) المسبوق (معه) أي: مع إمامه فهو (آخرها) أي: آخر صلاته، (وما يقضيه) بعد سلام إمامه فهو (أولها) أي: أول صلاته، فلو أدرك ركعة من الصبح -مثلا-أطال قراءة ما لم يدرك على التي أدركها، وراعى ترتيب السور؛ لحديث أبي هريرة رضي الله عنه أن النبي صلى الله عليه وسلم قال «ما أدركتم فصلوا، وما فاتكم فاقضوا» [أحمد: ٧٦٦٤]، والمقضي هو الفائت فيكون على صفته.','152','184','0');
INSERT INTO [t147663] VALUES (194,'- مسألة: (ويتحمل) إمام (عن مأموم) أشياء، بحيث تسقط عن المأموم لإتيان الإمام بها، منها','153','184','0');
INSERT INTO [t147663] VALUES (195,'- مسألة: أحوال المأموم مع الإمام من حيث الاقتداء ثلاثة أحوال','155','184','0');
INSERT INTO [t147663] VALUES (196,'- مسألة: (وسن لإمام التخفيف) في الصلاة (مع الإتمام)، لحديث أنس رضي الله عنه مرفوعا: «أن رسول الله صلى الله عليه وسلم كان من أخف الناس صلاة في تمام» [مسلم: ٤٦٩]، قال في «المبدع»: (ومعناه أن يقتصر على أدنى الكمال من التسبيح وسائر أجزاء الصلاة، إلا أن يؤثر المأموم التطويل وعددهم ينحصر).','158','184','0');
INSERT INTO [t147663] VALUES (197,'- مسألة: (و) سن للإمام (تطويل قراءة) الركعة (الأولى عن) قراءة الركعة (الثانية)؛ لقول أبي قتادة رضي الله عنه: «كان النبي صلى الله عليه وسلم يقرأ في الركعتين الأوليين من صلاة الظهر بفاتحة الكتاب وسورتين، يطول في الأولى، ويقصر في الثانية» [البخاري: ٧٥٩، ومسلم: ٤٥١]، ويستثنى من ذلك','158','184','0');
INSERT INTO [t147663] VALUES (198,'- مسألة: (و) سن للإمام (انتظار داخل) في ركوع وغيره؛ لأن الانتظار ثبت عن النبي صلى الله عليه وسلم في صلاة الخوف لإدراك الجماعة [البخاري: ٤١٢٩، ومسلم: ٨٤٢]، وذلك موجود هنا، (إن لم يشق على مأموم) فيكره؛ لأن حرمة الذي معه أعظم من حرمة الذي لم يدخل معه.','159','184','0');
INSERT INTO [t147663] VALUES (199,'(فصل) في الإمامة','159','123','0');
INSERT INTO [t147663] VALUES (200,'- مسألة: الأولى بالإمامة: لا يخلو من أمرين','159','199','0');
INSERT INTO [t147663] VALUES (201,'- مسألة: (وحر أولى) بالإمامة (من عبد و) من (مبعض)؛ لأنه أكمل وأشرف، (ومبعض أولى) بالإمامة (من عبد)؛ لحصول بعض الأكملية والأشرفية فيه.','162','199','0');
INSERT INTO [t147663] VALUES (202,'- مسألة: إمامة الفاسق: وهو من فعل كبيرة، أو داوم على صغيرة، على قسمين','164','199','0');
INSERT INTO [t147663] VALUES (203,'- مسألة: (وتصح) صلاة فرض ونفل (خلف)','165','199','0');
INSERT INTO [t147663] VALUES (204,'- مسألة: (لا) تصح صلاة (خلف أخرس) ولو بأخرس؛ لأنه لم يأت بفرض القراءة، ولا بد منه.','165','199','0');
INSERT INTO [t147663] VALUES (205,'- مسألة: (و) لا تصح الصلاة خلف (كافر)، سواء علم بكفره في الصلاة أو بعد الفراغ منها؛ لأن صلاته لا تصح لنفسه، فلا تصح لغيره.','166','199','0');
INSERT INTO [t147663] VALUES (206,'- مسألة: إمامة العاجز عن ركن أو شرط من شروط الصلاة، ولا تخلو من أقسام','166','199','0');
INSERT INTO [t147663] VALUES (207,'- مسألة: إمامة المرأة لا تخلو من أحوال','168','199','0');
INSERT INTO [t147663] VALUES (208,'- مسألة: إمامة الصبي المميز، ولا تخلو من أحوال','169','199','0');
INSERT INTO [t147663] VALUES (209,'- مسألة: إمامة المحدث، ولا تخلو من أقسام','169','199','0');
INSERT INTO [t147663] VALUES (210,'- مسألة: إمامة المتنجس، وأشار إليه بقوله: (أو نجس)، ولا تخلو إمامته من أقسام','170','199','0');
INSERT INTO [t147663] VALUES (211,'- مسألة: (ولا) تصح (إمامة أمي) بقارئ؛ لحديث أبي مسعود السابق: «يؤم القوم أقرؤهم لكتاب الله»، ولأن القراءة ركن مقصود في الصلاة فلم يصح اقتداء القادر عليه بالعاجز عنه، كالطهارة والسترة.','172','199','0');
INSERT INTO [t147663] VALUES (212,'- مسألة: (و) الأمي: نسبة إلى الأم، كأنه على الحالة التي ولدته أمه عليها، وفي الاصطلاح: (هو)','172','199','0');
INSERT INTO [t147663] VALUES (213,'فصل في موقف الإمام والمأمومين','173','123','0');
INSERT INTO [t147663] VALUES (214,'- مسألة: موقف المأموم من الإمام ينقسم إلى قسمين','173','213','0');
INSERT INTO [t147663] VALUES (215,'- مسألة: إن خالفوا في موقفهم من الإمام، فلا يخلو من أربعة أقسام','173','213','0');
INSERT INTO [t147663] VALUES (216,'- مسألة: (ومن صلى) مأموما (عن يساره) أي: يسار الإمام (مع خلو يمينه)؛ لم تصح صلاته، وسبق.','175','213','0');
INSERT INTO [t147663] VALUES (217,'- مسألة: (أو) صلى مأموم خلف الإمام، أو خلف الصف (ركعة) كاملة (منفردا؛ لم تصح صلاته)، عامدا أو ناسيا، عالما أو جاهلا، وهو من المفردات؛ لحديث وابصة بن معبد رضي الله عنه قال: «صلى رجل خلف الصف وحده، فأمره النبي صلى الله عليه وسلم أن يعيد» [أحمد ١٨٠٠٢، وأبو داود ٦٨٢، والترمذي ٢٣١، وابن ماجه ١٠٠٤].','176','213','0');
INSERT INTO [t147663] VALUES (218,'فصل في أحكام الاقتداء','176','123','0');
INSERT INTO [t147663] VALUES (219,'- مسألة: اقتداء المأموم بالإمام لا يخلو من قسمين','176','218','0');
INSERT INTO [t147663] VALUES (220,'- مسألة: (وكره علو إمام على مأموم ذراعا فأكثر)؛ لأن عمار بن ياسر رضي الله عنهما صلى بالمدائن على دكان والناس أسفل منه، فتقدم حذيفة فأخذ على يديه فاتبعه عمار، حتى أنزله حذيفة، فلما فرغ عمار من صلاته قال له حذيفة رضي الله عنهما: ألم تسمع رسول الله صلى الله عليه وسلم يقول: «إذا أم الرجل القوم فلا يقم في مكان أرفع من مقامهم»، قال عمار: «لذلك اتبعتك حين أخذت على يدي» [أبو داود ٥٩٨]، ولا تبطل؛ لأن عمارا أتم صلاته؛ ولو كانت فاسدة، لاستأنفها.','178','218','0');
INSERT INTO [t147663] VALUES (221,'فصل في الأعذار المسقطة للجمعة والجماعة','179','123','0');
INSERT INTO [t147663] VALUES (222,'- مسألة: (ويعذر) المصلي (بترك جمعة وجماعة) بأحد الأعذار الآتية','179','221','0');
INSERT INTO [t147663] VALUES (223,'(فصل) في صلاة أهل الأعذار','182','123','0');
INSERT INTO [t147663] VALUES (224,'أولا: المريض','182','223','0');
INSERT INTO [t147663] VALUES (225,'- مسألة: مراتب صلاة المريض','182','224','0');
INSERT INTO [t147663] VALUES (226,'المرتبة الأولى: (يلزم المريض أن يصلي) الصلاة المكتوبة (قائما) إجماعا','182','225','0');
INSERT INTO [t147663] VALUES (227,'المرتبة الثانية: (فإن لم يستطع) القيام','182','225','0');
INSERT INTO [t147663] VALUES (228,'المرتبة الثالثة: (فإن لم يستطع) القعود أو شق عليه','183','225','0');
INSERT INTO [t147663] VALUES (229,'- مسألة: (ويومئ بركوع وسجود عاجز عنهما ما أمكنه)؛ لحديث أبي هريرة السابق: «وإذا أمرتكم بأمر فأتوا منه ما استطعتم»، (ويجعل) أي','183','228','0');
INSERT INTO [t147663] VALUES (230,'المرتبة الرابعة: (فإن عجز) عن الإيماء برأسه (أومأ بطرفه)','184','225','0');
INSERT INTO [t147663] VALUES (231,'- مسألة: (فإن قدر) مصل قاعدا (على قيام) في أثناء الصلاة انتقل إليه، (أو) قدر مصل مضطجعا على (قعود في أثنائها)','184','230','0');
INSERT INTO [t147663] VALUES (232,'المرتبة الخامسة: فإن عجز عن الإيماء بطرفه','184','225','0');
INSERT INTO [t147663] VALUES (233,'- مسألة: (ولا تصح) صلاة (مكتوبة في سفينة قاعدا لقادر على قيام)؛ لقدرته على ركن الصلاة، كمن بغير سفينة.','185','224','0');
INSERT INTO [t147663] VALUES (234,'- مسألة: (وتصح) صلاة مكتوبة (على راحلة واقفة أو سائرة)','185','224','0');
INSERT INTO [t147663] VALUES (235,'- مسألة: (ويعتبر المقر لأعضاء السجود)','186','224','0');
INSERT INTO [t147663] VALUES (236,'(فصل) في قصر الصلاة','186','123','0');
INSERT INTO [t147663] VALUES (237,'- مسألة: (يسن قصر الصلاة الرباعية) وهي الظهر والعصر والعشاء، بخلاف المغرب والصبح فلا يقصران إجماعا، (لمن نوى سفرا)؛ لأنه صلى الله عليه وسلم','186','236','0');
INSERT INTO [t147663] VALUES (238,'- مسألة: (ولا يكره إتمام) رباعية لمن له قصرها؛ لقوله تعالى: {فليس عليكم جناح أن تقصروا من الصلاة}، ونفي الجناح يدل على الرخصة، ولحديث يعلى قال: قالت عائشة رضي الله عنها: «كان يقصر في السفر ويتم» [الدارقطني وصححه: ٢٢٩٨]، (والقصر أفضل)، من الإتمام؛ لحديث ابن عمر رضي الله عنهما: أن النبي صلى الله عليه وسلم قال: «إن الله يحب أن تؤتى رخصه، كما يكره أن تؤتى معصيته» [أحمد: ٥٨٦٦].','189','236','0');
INSERT INTO [t147663] VALUES (239,'- مسألة: (ولا يعيد من قصر) بشرطه (ثم رجع قبل استكمال المسافة)؛ لوجود نية المسافة المعتبرة.','190','236','0');
INSERT INTO [t147663] VALUES (240,'- مسألة: (و) يستثنى من جواز القصر بعد وجود الشروط السابقة صورا، منها','190','236','0');
INSERT INTO [t147663] VALUES (241,'- مسألة: (وإن حبس) المسافر في مكان (ظلما)، أو بسبب مرض، (أو بمطر)، ولم ينو الإقامة، قصر أبدا؛ لما تقدم من الآثار عن ابن عمر وأنس رضي الله عنهم.','192','236','0');
INSERT INTO [t147663] VALUES (242,'- مسألة: (أو أقام) المسافر بمكان (لـ) قضاء (حاجة بلا نية إقامة فوق أربعة أيام، ولا يدري متى تنقضي) حاجته، أقبل مدة القصر أم بعدها، (قصر أبدا) إجماعا.','192','236','0');
INSERT INTO [t147663] VALUES (243,'(فصل) في الجمع','192','123','0');
INSERT INTO [t147663] VALUES (244,'- مسألة: (يباح جمع بين ظهر وعصر) في وقت إحداهما، (و) بين (عشاءين بوقت إحداهما، وتركه أفضل)؛ للاختلاف فيه، (غير جمعي عرفة ومزدلفة فيسن) بشرطه، للاتفاق عليهما؛ لفعله صلى الله عليه وسلم.','192','243','0');
INSERT INTO [t147663] VALUES (245,'- مسألة: (ويجمع في ثمان حالات)','193','243','0');
INSERT INTO [t147663] VALUES (246,'- مسألة: (ويختص بجواز جمع العشاءين) دون الظهرين ست حالات يأتي بيانها؛ واختصت هذه بالعشاءين؛ لأنه لم يرد إلا فيهما، ومشقتهما أكثر من حيث إنهما يفعلان في الظلمة، بخلاف مشقة السفر فإنها لأجل السير وفوات الرفقة.','195','243','0');
INSERT INTO [t147663] VALUES (247,'- مسألة: يختص الجمع بين العشاءين دون الظهرين في ست حالات','195','243','0');
INSERT INTO [t147663] VALUES (248,'- مسألة: (والأفضل) لمن له الجمع في الحالات السابقة (فعل الأرفق) به (من تقديم جمع) بأن يقدم الثانية فيصليها مع الأولى،','197','243','0');
INSERT INTO [t147663] VALUES (249,'- مسألة: (ويشترط له) أي: الجمع تقديما كان، أو تأخيرا (ترتيب مطلقا)، أي: سواء ذكره، أو نسيه، بخلاف قضاء الفوائت، فإنه يسقط بالنسيان.','198','243','0');
INSERT INTO [t147663] VALUES (250,'- مسألة: (و) يشترط (لـ) جواز (جمع بوقت أولى) أي: جمع التقديم ثلاثة شروط','199','243','0');
INSERT INTO [t147663] VALUES (251,'- مسألة: (ويشترط لـ) جواز (جمع بوقت ثانية) أي: جمع التأخير شرطان','202','243','0');
INSERT INTO [t147663] VALUES (252,'- مسألة: (ولا يشترط للصحة) أي: لصحة الجمع تقديما كان أو','202','243','0');
INSERT INTO [t147663] VALUES (253,'(فصل) في صلاة الخوف','203','123','0');
INSERT INTO [t147663] VALUES (254,'- مسألة: (تصح صلاة الخوف) عند وجود سببها اتفاقا، لقوله تعالى: {وإذا كنت فيهم فأقمت لهم الصلاة} [النساء: ١٠٢] الآية، وما ثبت في حقه ثبت في حق أمته؛ ما لم يقم دليل على اختصاصه.','203','253','0');
INSERT INTO [t147663] VALUES (255,'- مسألة: تصح صلاة الخوف (على ستة أوجه) وقيل: سبعة، كلها جائزة، والصفة المختارة عند الإمام أحمد ما ورد في حديث سهل بن أبي حثمة رضي الله عنه: «أن رسول الله صلى الله عليه وسلم يوم ذات الرقاع صلى صلاة الخوف: أن طائفة صفت معه، وطائفة وجاه العدو، فصلى بالتي معه ركعة، ثم ثبت قائما، وأتموا لأنفسهم ثم انصرفوا، فصفوا وجاه العدو، وجاءت الطائفة الأخرى فصلى بهم الركعة التي بقيت من صلاته ثم ثبت جالسا، وأتموا لأنفسهم، ثم سلم بهم» [البخاري: ٤١٢٩، ومسلم: ٨٤٢]، لأنها أشبه بكتاب الله، وأحوط للصلاة والحرب، وأنكى للعدو، وأقل في الأفعال.','204','253','0');
INSERT INTO [t147663] VALUES (256,'- مسألة: (وإذا اشتد الخوف) أي: تواصل الطعن، والضرب، والكر، والفر، ولم يمكن تفريق القوم وصلاتهم؛ (صلوا) إذا حضرت','204','253','0');
INSERT INTO [t147663] VALUES (257,'- مسألة: (ولمصل) في خوف (كر) على العدو، (وفر) منه (لمصلحة، ولا تبطل بطوله)؛ لأنه موضع ضرورة، بخلاف الصياح فإنه لا حاجة به إلىه.','205','253','0');
INSERT INTO [t147663] VALUES (258,'- مسألة: (وسن له) أي: لمصل خائف (فيها) أي: في صلاة الخوف (حمل ما يدفع به عن نفسه) من سلاح، (ولا يثقله كسيف وسكين)؛ لقوله','205','253','0');
INSERT INTO [t147663] VALUES (259,'- مسألة: (وجاز) في صلاة خوف (لحاجة حمل نجس) لا يعفى عنه في غيرها، (ولا يعيد) ما صلاه في الخوف مع النجس الكثير؛ للعذر.','206','253','0');
INSERT INTO [t147663] VALUES (260,'(فصل) في صلاة الجمعة','206','123','0');
INSERT INTO [t147663] VALUES (261,'- مسألة: (تجب) صلاة (الجمعة) وجوب عين بالإجماع؛ لقوله تعالى: (يا أيها الذين آمنوا إذا نودي للصلاة من يوم الجمعة فاسعوا إلى ذكر الله) [الجمعة: ٩]','206','260','0');
INSERT INTO [t147663] VALUES (262,'- مسألة: تجب صلاة الجمعة (على كل)','206','260','0');
INSERT INTO [t147663] VALUES (263,'- مسألة: (ولا تجب على من يباح له القصر)، لا بنفسه ولا بغيره، وتقدم، (ولا) تجب على (عبد)؛ لما سبق، (ولا) على (مبعض)؛ لأنه عبد، (ولا) على (امرأة)؛ لما سبق، (ولا) على (خنثى) مشكل؛ لأنه لم تتحقق ذكوريته، لكن يستحب للخنثى حضورها احتياطا.','210','260','0');
INSERT INTO [t147663] VALUES (264,'فصل','212','123','0');
INSERT INTO [t147663] VALUES (265,'- مسألة: (وشرط لصحتها) أي: الجمعة (أربعة شروط).','212','264','0');
INSERT INTO [t147663] VALUES (266,'- مسألة: لا تنعقد الجمعة بأقل من أربعين، (فإن نقصوا) عن العدد المشترط (قبل إتمامها) لم يتموها جمعة؛ لفقد شرط العدد، واستأنفوا جمعة إن أمكن إعادتها جمعة بشروطها؛ لأنها فرض الوقت، وإن لم يمكن استئنافها لفقد بعض شروطها، (استأنفوا ظهرا).','215','264','0');
INSERT INTO [t147663] VALUES (267,'- مسألة: الخطبتان (بدل ركعتين)؛ لقول عمر رضي الله عنه: «الخطبة موضع الركعتين، من فاتته الخطبة صلى أربعا» [عبدالرزاق ٥٤٨٥]، لا أن الخطبتين بدل ركعتين من الظهر؛ لأن الجمعة ليست بدلا عن الظهر، بل مستقلة، واختاره ابن القيم؛ لقول عمر رضي الله عنه: «الجمعة ركعتان، تمام غير قصر على لسان محمد صلى الله عليه وسلم» [أحمد ٢٥٧، والنسائي ١٤٠٢، وابن ماجه ١٠٦٣]، ولعدم جواز زيادتها على ركعتين، بل الظهر بدل عنها إذا فاتت.','215','264','0');
INSERT INTO [t147663] VALUES (268,'- مسألة: (من شرطهما) أي: من شرط صحة الخطبتين (خمسة أشياء)','216','264','0');
INSERT INTO [t147663] VALUES (269,'- مسألة: (وأركانهما) أي: الخطبتين (ستة)','216','264','0');
INSERT INTO [t147663] VALUES (270,'- مسألة: (ويبطلها) أي: الخطبة (كلام محرم) في أثنائها، (ولو يسيرا)؛ كما يبطل الأذان بذلك وأولى.','218','264','0');
INSERT INTO [t147663] VALUES (271,'- مسألة: (وهي) أي: الخطبة (بغير العربية) مع القدرة (كقراءة) القرآن؛ فلا تجزئ بغير العربية، لأن النبي صلى الله عليه وسلم كان يخطب بالعربية، (فلا تصح إلا مع العجز) عن العربية، (غير القراءة) أي: قراءة آية فصاعدا، فلا تجزئ بغير العربية؛ فإنه دليل النبوة، وعلامة الرسالة، ولا يحصل بالعجمية، فإن عجز عن القراءة وجب بدلها ذكرا؛ قياسا على الصلاة.','218','264','0');
INSERT INTO [t147663] VALUES (272,'- مسألة: (وتسن) في الخطبة سنن منها','219','264','0');
INSERT INTO [t147663] VALUES (273,'- مسألة: (ويحرم الكلام والإمام يخطب وهو) أي: المتكلم (منه) أي: من الإمام (بحيث يسمعه)؛ لحديث أبي هريرة رضي الله عنه مرفوعا: «إذا قلت لصاحبك: أنصت، يوم الجمعة، والإمام يخطب، فقد لغوت» [مسلم ٨٥١]، بخلاف البعيد الذي لا يسمعه؛ لأن وجوب الإنصات للاستماع، وهذا ليس بمستمع.','221','264','0');
INSERT INTO [t147663] VALUES (274,'(فصل)','222','123','0');
INSERT INTO [t147663] VALUES (275,'- مسألة: (و) صلاة (الجمعة ركعتان) إجماعا؛ قال عمر رضي الله عنه: «صلاة الجمعة ركعتان، تمام غير قصر، على لسان محمد صلى الله عليه وسلم» [أحمد ٢٥٧، والنسائي ١٤١٩، وابن ماجه ١٠٦٣].','222','274','0');
INSERT INTO [t147663] VALUES (276,'- مسألة: (وحرم إقامتها) أي: صلاة جمعة، (و) كذا صلاة (عيد في أكثر من موضع) واحد (من البلد) الواحد؛ لأنه صلى الله عليه وسلم وأصحابه لم يقيموها في أكثر من موضع واحد، قال في «المبدع»: (لا نعلم فيه خلافا إلا عن عطاء)، (إلا لحاجة، كضيق) مسجد، (وبعد)؛ بأن يكون البلد واسعا وتتباعد أقطاره، فيشق على من منزله بعيد عن محل الجمعة مجيئها، (وخوف فتنة) لعداوة بين أهل البلد يخشى باجتماعهم في محل إثارتها، (ونحوه)؛ مما يدعو لتعدد الجمع فيجوز بقدر الحاجة فقط؛ لأنها تفعل في الأمصار العظيمة في مواضع من غير نكير، فكان إجماعا.','222','274','0');
INSERT INTO [t147663] VALUES (277,'- مسألة: (وسن) يوم الجمعة','224','274','0');
INSERT INTO [t147663] VALUES (278,'- مسألة: (ومن دخل والإمام يخطب؛ لم يجلس حتى يركع ركعتين خفيفتين) تحية المسجد، استحبابا، ولو كان وقت نهي؛ لحديث جابر رضي الله عنهما قال: قال النبي صلى الله عليه وسلم: «إذا جاء أحدكم والإمام يخطب فليصل ركعتين»، وزاد مسلم: «وليتجوز فيهما» [البخاري ١١٧٠، ومسلم ٨٧٥].','225','274','0');
INSERT INTO [t147663] VALUES (279,'(فصل) في صلاة العيدين','225','123','0');
INSERT INTO [t147663] VALUES (280,'- مسألة: (وصلاة العيدين فرض كفاية)؛ لقوله تعالى: {فصل لربك وانحر} [الكوثر: ٢]، قال عكرمة وغيره: هي صلاة العيد، والأمر يقتضي الوجوب، وكونها على الكفاية؛ لحديث طلحة رضي الله عنه مرفوعا: «خمس صلوات في اليوم والليلة»، فقال: هل علي غيرها؟ قال: «لا، إلا أن تطوع» [البخاري: ٤٦، ومسلم: ١١].','225','279','0');
INSERT INTO [t147663] VALUES (281,'- مسألة: (و) أول (وقتها) أي: صلاة العيد (كـ) ـأول وقت (صلاة الضحى)؛ لأنه صلى الله عليه وسلم ومن بعده لم يصلوها إلا بعد ارتفاع الشمس، ذكره في «المبدع»، فعن عبد الله بن بسر رضي الله عنه: أنه خرج مع الناس في يوم عيد فطر أو أضحى، فأنكر إبطاء الإمام، وقال: «إنا كنا قد فرغنا ساعتنا هذه»، وذلك حين التسبيح. [البخاري معلقا بصيغة الجزم ٢/ ١٩، وأبو داود: ١١٣٥، وابن ماجه: ١٣١٧].','226','279','0');
INSERT INTO [t147663] VALUES (282,'- مسألة: (وشروطها) أي: شروط صحة صلاة العيد (كـ) شروط صحة صلاة (الجمعة)، من دخول وقت؛ كسائر المؤقتات، واستيطان؛ لأنه صلى الله عليه وسلم وافق في حجه عيدا ولم يصل، وعدد الجمعة، فلا تقام إلا حيث تقام الجمعة، لأنها ذات خطبة راتبة أشبهتها.','226','279','0');
INSERT INTO [t147663] VALUES (283,'- مسألة: (فإن لم يعلم بالعيد إلا بعد الزوال) أي: بعد زوال الشمس (صلوا) العيد (من الغد قضاء)؛ لما روى أبو عمير بن أنس عن عمومة له من الأنصار، قالوا: «غم علينا هلال شوال، فأصبحنا صياما، فجاء ركب من آخر النهار، فشهدوا عند رسول الله صلى الله عليه وسلم، أنهم رأوا الهلال بالأمس، فأمر رسول الله صلى الله عليه وسلم الناس أن يفطروا من يومهم، وأن يخرجوا لعيدهم من الغد» [أحمد: ٢٠٥٨٤، وأبو داود: ١١٥٧، والنسائي: ١٥٥٧، وابن ماجه: ١٦٥٣].','227','279','0');
INSERT INTO [t147663] VALUES (284,'- مسألة: (و) من سنن صلاة العيد','227','279','0');
INSERT INTO [t147663] VALUES (285,'- مسألة: (و) صفة صلاة العيد','229','279','0');
INSERT INTO [t147663] VALUES (286,'- مسألة: (والتكبيرات الزوائد) سنة؛ قال في «الشرح»: (بغير خلاف علمناه).','233','279','0');
INSERT INTO [t147663] VALUES (287,'- مسألة: (وكره تنفل وقضاء فائتة قبل الصلاة بموضعها)، أي: موضع صلاة العيد، صحراء كان أو مسجدا.','233','279','0');
INSERT INTO [t147663] VALUES (288,'- مسألة: (وسن لمن فاتته) صلاة العيد (قضاؤها في يومها)، قبل','233','279','0');
INSERT INTO [t147663] VALUES (289,'(فصل)','234','123','0');
INSERT INTO [t147663] VALUES (290,'- مسألة: (وسن التكبير) في العيدين، وهو على قسمين','234','289','0');
INSERT INTO [t147663] VALUES (291,'- مسألة: (ويكبر الإمام مستقبل الناس)، أي: يلتفت إلى المأمومين ثم يكبر؛ لحديث جابر رضي الله عنه: كان رسول الله صلى الله عليه وسلم إذا صلى الصبح من غداة عرفة يقبل على أصحابه فيقول: «على مكانكم»، ويقول: «الله أكبر الله أكبر الله أكبر لا إله إلا الله، والله أكبر الله أكبر ولله الحمد» [الدارقطني ١٧٣٧]، ولأنه ذكر مختص بوقت العيد فأشبه الخطبة.','238','289','0');
INSERT INTO [t147663] VALUES (292,'- مسألة: (ولا يسن) التكبير (عقب صلاة عيد)؛ لأنها ليست من الصلوات الخمس، أشبهت النوافل.','238','289','0');
INSERT INTO [t147663] VALUES (293,'- مسألة: (وصفته) أي: التكبير (شفعا: الله أكبر الله أكبر لا إله إلا الله، والله أكبر الله أكبر ولله الحمد)؛ لوروده عن علي وابن مسعود رضي الله عنهما [ابن أبي شيبة: ٥٦٥٣].','239','289','0');
INSERT INTO [t147663] VALUES (294,'- مسألة: (ولا بأس بقوله) أي: المصلي (لغيره) من المصلين: (تقبل الله منا ومنك)، قال أحمد: (لا بأس به، يرويه أهل الشام عن أبي أمامة، وواثلة بن الأسقع [البيهقي ٦٢٩٤] رضي الله عنهم)، ولعموم التهنئة لما يحدث الله من نعمة ويدفع من نقمة، بمشروعية سجود الشكر، وتبشير النبي صلى الله عليه وسلم بقدوم رمضان، وتهنئة طلحة لكعب بحضرة النبي صلى الله عليه وسلم.','239','289','0');
INSERT INTO [t147663] VALUES (295,'- مسألة: (ولا) بأس (بالتعريف عشية عرفة بالأمصار)، وهو الاجتماع في المساجد يوم عرفة عشية حتى تغرب الشمس للذكر والدعاء، قال أحمد: (إنما هو دعاء وذكر، وأول من فعله ابن عباس وعمرو بن حريث رضي الله عنهم [مصنف ابن أبي شيبة ٣/ ٢٨٧])، وقيل له: تفعله أنت؟ قال: لا.','240','289','0');
INSERT INTO [t147663] VALUES (296,'(فصل) في صلاة الكسوف','241','123','0');
INSERT INTO [t147663] VALUES (297,'- مسألة: (صلاة الكسوف سنة) عند وجود سببها','241','296','0');
INSERT INTO [t147663] VALUES (298,'- مسألة: تصلى صلاة الكسوف (من غير خطبة)؛ لأن النبي صلى الله عليه وسلم أمر بها دون الخطبة، وإنما خطب بعد الصلاة ليعلمهم حكمها، وهذا مختص به.','242','296','0');
INSERT INTO [t147663] VALUES (299,'- مسألة: (ووقتها) أي: صلاة الكسوف: (من ابتدائه) أي: الكسوف (إلى) حين (التجلي)؛ لحديث المغيرة بن شعبة رضي الله عنه مرفوعا: «فإذا رأيتموهما، فادعوا الله وصلوا حتى ينجلي» [البخاري: ١٠٦٠]، فإن تجلى الكسوف وهو في الصلاة أتمها خفيفة، على صفتها؛ لحديث أبي مسعود الأنصاري رضي الله عنه مرفوعا: «فإذا رأيتم منها شيئا فصلوا، وادعوا الله حتى يكشف ما بكم» [البخاري: ١٠٤١، ومسلم: ٩١١]، ولا يقطعها؛','242','296','0');
INSERT INTO [t147663] VALUES (300,'- مسألة: (وهي) أي: صلاة الكسوف: (ركعتان)، على أن تكون (كل ركعة بقيامين وركوعين)؛ لحديث ابن عباس رضي الله عنهما قال: «انخسفت الشمس على عهد رسول الله صلى الله عليه وسلم، فصلى رسول الله صلى الله عليه وسلم، فقام قياما طويلا نحوا من قراءة سورة البقرة، ثم ركع ركوعا طويلا، ثم رفع، فقام قياما طويلا وهو دون القيام الأول، ثم ركع ركوعا طويلا وهو دون الركوع الأول، ثم سجد، ثم قام قياما طويلا وهو دون القيام الأول، ثم ركع ركوعا طويلا وهو دون الركوع الأول، ثم رفع، فقام قياما طويلا وهو دون القيام الأول، ثم ركع ركوعا طويلا وهو دون الركوع الأول، ثم سجد، ثم انصرف وقد تجلت الشمس» [البخاري: ١٠٥٢، ومسلم: ٩٠٧].','243','296','0');
INSERT INTO [t147663] VALUES (301,'- مسألة: (وسن تطويل) قراءة الـ (سورة) في كل الركعات من غير تقدير؛ لحديث ابن عباس السابق، وفيه: «فقام قياما طويلا نحوا من قراءة سورة البقرة».','243','296','0');
INSERT INTO [t147663] VALUES (302,'- مسألة: (و) سن تطويل الـ (تسبيح) في الركوع والسجود؛ لما تقدم في الحديث.','244','296','0');
INSERT INTO [t147663] VALUES (303,'- مسألة: (و) سن (كون أولى كل) ركوع من الركوعين (أطول) من ركوع الركعة التي بعدها، للحديث السابق.','244','296','0');
INSERT INTO [t147663] VALUES (304,'- مسألة: ما بعد الركوع الأول من كل ركعة سنة؛ لا تدرك به الركعة للمسبوق، (و) لهذا (تصح) صلاة الكسوف لو صلاها (كالنافلة)، بركوع واحد؛ لحديث سمرة رضي الله عنه في صلاة النبي صلى الله عليه وسلم الكسوف، وفيه: «فصلى، فقام بنا كأطول ما قام بنا في صلاة قط، لا نسمع له صوتا» [أحمد ٢٠١٧٨، وأبوداود ١١٨٤، والنسائي ١٤٨٣].','244','296','0');
INSERT INTO [t147663] VALUES (305,'- مسألة: (ولا يصلى لآية غيره) أي: غير الكسوف من الآيات؛ (كظلمة نهارا، وضياء ليلا، وريح شديدة، وصواعق)؛ لعدم نقله عنه وعن أصحابه صلى الله عليه وسلم، مع أنه وجد في زمانهم انشقاق القمر، وهبوب الرياح، والصواعق، (إلا لزلزلة دائمة) فيصلى لها كصلاة الكسوف؛ لثبوته ابن عباس [مصنف عبد الرزاق: ٤٩٢٩]، وعن علي رضي الله عنهم [البيهقي في الكبرى: ٦٣٨٢].','244','296','0');
INSERT INTO [t147663] VALUES (306,'(فصل) في صلاة الاستسقاء','245','123','0');
INSERT INTO [t147663] VALUES (307,'- مسألة: الاستسقاء: الدعاء بطلب السقيا على صفة مخصوصة، على ثلاثة أنواع','245','306','0');
INSERT INTO [t147663] VALUES (308,'- مسألة: تشرع صلاة الاستسقاء (إذا أجدبت الأرض) أي: أمحلت، والجدب نقيض الخصب، (وقحط المطر) أي: احتبس فلم ينزل، وضر ذلك، أو غار ماء الأنهار، أو نقص ماء العيون؛ لحديث عائشة رضي الله عنها قالت: شكا الناس إلى رسول الله صلى الله عليه وسلم قحوط المطر، فأمر بمنبر، فوضع له في المصلى، ووعد الناس يوما يخرجون فيه، ثم قال: «إنكم شكوتم جدب دياركم، واستئخار المطر عن إبان زمانه عنكم» الحديث [أبو داود: ١١٧٣].','246','306','0');
INSERT INTO [t147663] VALUES (309,'- مسألة: (وصفتها) أي: صلاة الاستسقاء (وأحكامها)، وموضعها ووقتها (كـ) صلاة وأحكام وموضع (صلاة عيد)؛ قال ابن عباس رضي الله عنهما أيضا: «صلى رسول الله صلى الله عليه وسلم ركعتين كما كان يصلي في العيدين» [أحمد: ٢٠٣٩، وأبو داود: ١١٦٥، والترمذي: ٥٥٨، والنسائي: ١٥٠٨، وابن ماجه: ١٢٦٦].','246','306','0');
INSERT INTO [t147663] VALUES (310,'- مسألة: (وهي) أي: صلاة الاستسقاء، كونها تصلى في جماعة أفضل؛ لظاهر حديث عبد الله بن زيد السابق، (و) كذا (التي قبلها) أي: صلاة الكسوف، كونها تصلى في (جماعة أفضل)؛ لقول عائشة رضي الله عنها','246','306','0');
INSERT INTO [t147663] VALUES (311,'- مسألة: (وإذا أراد الإمام الخروج) لصلاة الاستسقاء','247','306','0');
INSERT INTO [t147663] VALUES (312,'- مسألة: (وسن وقوف في أول المطر، وتوضؤ، واغتسال منه)؛ لحديث أنس رضي الله عنه قال: أصابنا ونحن مع رسول الله صلى الله عليه وسلم مطر، قال: فحسر رسول الله صلى الله عليه وسلم ثوبه، حتى أصابه من المطر، فقلنا: يا رسول الله؛ لم صنعت هذا؟ قال: «لأنه حديث عهد بربه تعالى» [مسلم ٨٩٨].','251','306','0');
INSERT INTO [t147663] VALUES (313,'- مسألة: (وإن كثر حتى خيف منه سن قول: (اللهم حوالينا) أي: أنزله حوالي المدينة في مواضع النبات، (ولا علينا) في المدينة، ولا في غيرها من المباني، (اللهم على الظراب) أي: الروابي الصغار، (والآكام) على وزن آصال وجبال، قال مالك: هي الجبال الصغار، (وبطون الأودية) أي الأمكنة المنخفضة، (ومنابت الشجر) أي: أصولها؛ لأنه أنفع لها؛ لما روى أنس أنه صلى الله عليه وسلم كان يقول ذلك [البخاري: ١٠١٤،','251','306','0');
INSERT INTO [t147663] VALUES (314,'- مسألة: (وسن) لمن مطر (قول: مطرنا بفضل الله ورحمته)؛ لحديث زيد بن خالد رضي الله عنه قال: صلى بنا رسول الله صلى الله عليه وسلم صلاة الصبح بالحديبية في إثر سماء كانت من الليل، فلما انصرف أقبل على الناس فقال: «هل تدرون ماذا قال ربكم؟ » قالوا: الله ورسوله أعلم، قال: «قال: أصبح من عبادي مؤمن بي وكافر، فأما من قال: مطرنا بفضل الله ورحمته، فذلك مؤمن بي كافر بالكوكب، وأما من قال: مطرنا بنوء كذا وكذا، فذلك كافر بي مؤمن بالكوكب» [البخاري: ٨٤٦، ومسلم: ٧١].','252','306','0');
INSERT INTO [t147663] VALUES (315,'- مسألة: (يسن الاستعداد للموت) بالتوبة من المعاصي، والخروج من المظالم، لقوله تعالى: (فمن كان يرجوا لقاء ربه فليعمل عملا صالحا) [الكهف: ١١٠].','253','306','0');
INSERT INTO [t147663] VALUES (316,'- مسألة: (و) سن (الإكثار من ذكره) أي: الموت؛ لحديث أبي هريرة رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: «أكثروا ذكر هادم اللذات»، يعني: الموت، وفي رواية: «هاذم اللذات» [أحمد ٤٢٥٨، والترمذي ٢٣٠٧، وابن ماجه ٤٢٥٨].','253','306','0');
INSERT INTO [t147663] VALUES (317,'- مسألة: (وتسن عيادة) كل (مريض مسلم)، ولو من وجع ضرس ونحوه؛ لحديث أبي هريرة رضي الله عنه مرفوعا: «حق المسلم على المسلم خمس: رد السلام، وعيادة المريض، واتباع الجنائز، وإجابة الدعوة، وتشميت العاطس» [البخاري ١٢٤٠، ومسلم ٢١٦٢].','253','306','0');
INSERT INTO [t147663] VALUES (318,'(كتاب الجنائز)','253','0','0');
INSERT INTO [t147663] VALUES (319,'- مسألة: (و) سن (تذكيره) أي: المريض -سواء كان مرضه مخوفا أم لا- بأمور','254','318','0');
INSERT INTO [t147663] VALUES (320,'- مسألة: آداب المحتضر، وأشار إليها بقوله: (وإذا نزل به)، أي: نزل الملك بالمريض لقبض روحه، (سن لأرفق أهله به تعاهد)','257','318','0');
INSERT INTO [t147663] VALUES (321,'- مسألة: (و) ينبغي للمريض أن (يعتمد على الله فيمن يحب) من بنيه وغيرهم، (و) أن (يوصي) بقضاء ديونه، وتفرقة وصيته، (للأرجح في نظره) من قريب وأجنبي؛ لأنه المصلحة.','259','318','0');
INSERT INTO [t147663] VALUES (322,'- مسألة: (فإذا مات) المحتضر (سن)','259','318','0');
INSERT INTO [t147663] VALUES (323,'(فصل) في غسل الميت','261','318','0');
INSERT INTO [t147663] VALUES (324,'- مسألة: (وغسله) أي: الميت (فرض كفاية) إجماعا على من أمكنه؛ لحديث ابن عباس رضي الله عنهما في الذي وقصته دابته فمات: قال صلى الله عليه وسلم: «اغسلوه بماء وسدر، وكفنوه في ثوبيه» [البخاري ١٢٦٥، ومسلم ١٢٠٦].','261','323','0');
INSERT INTO [t147663] VALUES (325,'- مسألة: (وشرط في ماء) لصحة غسله: (طهورية، وإباحة)؛ كباقي الأغسال.','262','323','0');
INSERT INTO [t147663] VALUES (326,'- مسألة: (و) شرط (في غاسل: إسلام، وعقل)؛ لاعتبار نيته، ولا تصح من كافر ومجنون، (وتمييز) فلا يشترط بلوغه؛ لصحة غسله لنفسه.','262','323','0');
INSERT INTO [t147663] VALUES (327,'- مسألة: في صفة غسل الميت، قال رحمه لله: (وإذا أخذ) أي: شرع الغاسل (في غسله) فعل أمورا','262','323','0');
INSERT INTO [t147663] VALUES (328,'- مسألة: (وكره اقتصار على غسله) أي: الميت (مرة)؛ لحديث أم عطية رضي الله عنها: «اغسلنها ثلاثا، أو خمسا، أو أكثر من ذلك إن رأيتن ذلك، بماء وسدر»، ويجزئ مرة؛ لحديث ابن عباس رضي الله عنهما في الذي وقصته','265','323','0');
INSERT INTO [t147663] VALUES (329,'- مسألة: (وسقط) بتثليث السين، وهو المولود قبل تمامه، لا يخلو من أمرين','267','323','0');
INSERT INTO [t147663] VALUES (330,'(فصل) في تكفين الميت','267','318','0');
INSERT INTO [t147663] VALUES (331,'- مسألة: (وتكفينه) أي: الميت (فرض كفاية) لحديث ابن عباس رضي الله عنهما السابق في الذي وقصته ناقته، وفيه: «وكفنوه في ثوبيه».','267','330','0');
INSERT INTO [t147663] VALUES (332,'- مسألة: عدد الأثواب التي يكفن فيها الميت تنقسم إلى قسمين','267','330','0');
INSERT INTO [t147663] VALUES (333,'(فصل) في الصلاة على الميت','271','318','0');
INSERT INTO [t147663] VALUES (334,'- مسألة: (والصلاة عليه) أي: الميت (فرض كفاية)؛ للأمر بها كما في حديث زيد بن خالد الجهني رضي الله عنه في الغال، وفيه أن النبي صلى الله عليه وسلم قال: «صلوا على صاحبكم» [أحمد: ١٧٠٣١، وأبو داود: ٢٧١٠، والنسائي: ١٩٥٩، وابن ماجه: ٢٨٤٨].','271','333','0');
INSERT INTO [t147663] VALUES (335,'- مسألة: (وتسن) الصلاة على الميت في (جماعة) إجماعا، ولو لنساء؛ لأن النبي صلى الله عليه وسلم كان يفعلها هو وأصحابه جماعة.','272','333','0');
INSERT INTO [t147663] VALUES (336,'- مسألة: (وشروطها) أي: الصلاة على الميت (ثمانية) شروط','272','333','0');
INSERT INTO [t147663] VALUES (337,'- مسألة: (وأركانها) أي: الصلاة على الميت (سبعة)','273','333','0');
INSERT INTO [t147663] VALUES (338,'- مسألة: (وسن قيام إمام ومنفرد) في الصلاة (عند صدر رجل)؛ رواه','274','333','0');
INSERT INTO [t147663] VALUES (339,'- مسألة: (و) يسن قيام الإمام والمنفرد عند (وسط امرأة)؛ لما روى سمرة بن جندب رضي الله عنه قال: «صليت وراء النبي صلى الله عليه وسلم على امرأة ماتت في نفاسها، فقام عليها وسطها» [البخاري: ١٣٣١، ومسلم: ٩٦٤].','275','333','0');
INSERT INTO [t147663] VALUES (340,'- مسألة: (وصفتها) أي: الصلاة على الميت','275','333','0');
INSERT INTO [t147663] VALUES (341,'(فصل)','278','318','0');
INSERT INTO [t147663] VALUES (342,'- مسألة: (وحمله) أي: الميت (ودفنه فرض كفاية) إجماعا؛ لأنه لو ترك لأنتن، وتأذى الناس برائحته.','278','341','0');
INSERT INTO [t147663] VALUES (343,'- مسألة: (وسن) عند حمل الجنازة أمور، منها','278','341','0');
INSERT INTO [t147663] VALUES (344,'- مسألة: (وكره) عند الدفن أمور، منها','279','341','0');
INSERT INTO [t147663] VALUES (345,'- مسألة: (ويجب أن يستقبل به) أي: الميت (القبلة) عند دفنه؛ لحديث عبيد بن عمير عن أبيه مرفوعا -وكانت له صحبة: «البيت الحرام قبلتكم أحياء وأمواتا» [أبو داود: ٢٨٧٥]، وعمل المسلمين على ذلك.','281','341','0');
INSERT INTO [t147663] VALUES (346,'- مسألة: (ويسن) لحد الميت (على جنبه الأيمن)؛ لأنه يشبه النائم، وهذه سنته.','281','341','0');
INSERT INTO [t147663] VALUES (347,'- مسألة: (وحرم دفن اثنين فأكثر) معا، أو واحدا بعد آخر قبل بلى السابق (في قبر) واحد؛ لأنه صلى الله عليه وسلم كان يدفن كل ميت في قبر، وعلى هذا استمر فعل الصحابة ومن بعدهم.','281','341','0');
INSERT INTO [t147663] VALUES (348,'- مسألة: (وسن) عند دفن الميت أمور، منها','282','341','0');
INSERT INTO [t147663] VALUES (349,'- مسألة: (وإن ماتت حامل) بمن يرجى حياته، بأن كان يتحرك حركة قوية، وانفتحت المخارج، وله ستة أشهر فأكثر، (حرم شق بطنها) للحمل؛ لأنه هتك حرمة متيقنة لإبقاء حياة متوهمة، إذ الغالب أن الولد لا يعيش.','285','341','0');
INSERT INTO [t147663] VALUES (350,'(فصل) في أحكام التعزية وزيارة القبور','287','318','0');
INSERT INTO [t147663] VALUES (351,'- مسألة: (وتعزية مسلم) مصاب (ولو) كان المعزى (صغيرا) أو جارا أو صديقا، (إلى ثلاثة أيام سنة) اتفاقا، قبل الدفن وبعده، وسواء كان الميت مسلما أو كافرا؛ لفعل النبي صلى الله عليه وسلم حين عزى ابنته بفقد ابنها، فقال لها: «إن لله ما أخذ، وله ما أعطى، وكل عنده بأجل مسمى، فلتصبر، ولتحتسب» [البخاري: ١٢٨٤، ومسلم: ٩٢٣].','287','350','0');
INSERT INTO [t147663] VALUES (352,'- مسألة: إهداء القرب للميت: وهي على قسمين','288','350','0');
INSERT INTO [t147663] VALUES (353,'- مسألة: (وتسن زيارة القبور للرجال)، وحكاه النووي إجماعا؛ لحديث أبي هريرة رضي الله عنه مرفوعا: «نهيتكم عن زيارة القبور، فزوروها» [مسلم: ٩٧٧]، وفي رواية: «فإنها تذكر الآخرة» [أبو داود: ٣٢٣٥، والترمذي: ١٠٥٤، وابن ماجه: ١٥٧١].','289','350','0');
INSERT INTO [t147663] VALUES (354,'- مسألة: (وتكره للنساء) زيارة القبور؛ لحديث أم عطية رضي الله عنها: «نهينا عن اتباع الجنائز، ولم يعزم علينا» [البخاري: ١٢٧٨، ومسلم: ٩٣٨]، (وإن علمن) أي: النساء (أنه يقع منهن محرم) بزيارتهن (حرمت) زيارتهن لها؛ وعليه يحمل حديث أبي هريرة رضي الله عنه: «لعن رسول الله صلى الله عليه وسلم زوارات القبور» [الترمذي: ١٠٥٦، وابن ماجه: ١٥٧٥]، غير قبر النبي صلى الله عليه وسلم وصاحبيه فيسن.','289','350','0');
INSERT INTO [t147663] VALUES (355,'- مسألة: (ويجوز البكاء على الميت)؛ لحديث ابن عمر رضي الله عنه: أن النبي صلى الله عليه وسلم أتى سعد بن عبادة يوم مات، فبكى، وقال: «إن الله لا يعذب بدمع العين، ولا بحزن القلب، ولكن يعذب بهذا -وأشار إلى لسانه- أو يرحم» [البخاري: ١٣٠٤، ومسلم: ٩٢٤].','290','350','0');
INSERT INTO [t147663] VALUES (356,'- مسألة: (ويحرم ندب) وهو البكاء مع تعداد محاسن الميت بلفظ النداء، كقوله: واسيداه؛ لأنه من النياحة المنهي عنها.','290','350','0');
INSERT INTO [t147663] VALUES (357,'- مسألة: (و) حرم (نياحة) وهي رفع الصوت بالندب؛ لحديث أم عطية رضي الله عنها قالت: «أخذ علينا النبي صلى الله عليه وسلم عند البيعة ألا ننوح» [البخاري: ١٣٠٦، ومسلم: ٩٣٦].','290','350','0');
INSERT INTO [t147663] VALUES (358,'- مسألة: (و) حرم (شق ثوب، ولطم خد ونحوه)؛ كصراخ، ونتف شعر؛ لحديث عبد الله بن مسعود رضي الله عنه مرفوعا: «ليس منا من لطم الخدود، وشق الجيوب، ودعا بدعوى الجاهلية» [البخاري: ١٢٩٤، ومسلم: ١٠٣].','291','350','0');
INSERT INTO [t147663] VALUES (359,'- مسألة: (ويعرف الميت زائره)؛ ويدل عليه: تسمية المسلم عليه: «زائرا»، ولولا أنهم يشعرون به لما صح تسميته: «زائرا»، فإن المزور إذا لم يعلم بزيارة من زاره لم يصح أن يقال: زاره، (يوم الجمعة قبل طلوع الشمس) قاله أحمد، وقال في «الغنية»: يعرفه كل وقت، وهذا الوقت آكد.','291','350','0');
INSERT INTO [t147663] VALUES (360,'- مسألة: (شروط وجوبها): أي: الزكاة (خمسة أشياء)','292','350','0');
INSERT INTO [t147663] VALUES (361,'(كتاب الزكاة)','292','0','0');
INSERT INTO [t147663] VALUES (362,'- مسألة: (وتجب) الزكاة (في مال الصغير والمجنون)؛ لعموم أدلة الزكاة، ولقول عمر رضي الله عنه: «ابتغوا في أموال اليتامى قبل أن تأكلها الزكاة» [مصنف عبدالرزاق ٤/ ٦٦ - ٦٩].','293','361','0');
INSERT INTO [t147663] VALUES (363,'- مسألة: (وهي) أي: الزكاة لا تجب في شيء من الأموال إلا (في خمسة أشياء)','293','361','0');
INSERT INTO [t147663] VALUES (364,'- مسألة: (ويمنع وجوبها) أي: الزكاة (دين ينقص النصاب)، ولو كان المال ظاهرا، كالمواشي والحبوب والثمار، فيسقط من المال بقدر الدين، كأنه غير مالك له، ثم يزكي ما بقي؛ لقول عثمان رضي الله عنه: «إن هذا شهر زكاتكم، فمن كان عليه دين فليؤده، ثم ليؤد زكاة ما فضل» [مصنف ابن أبي شيبة ٧٠٨٦، وصححه الحافظ].','294','361','0');
INSERT INTO [t147663] VALUES (365,'- مسألة: (ومن مات وعليه زكاة أخذت من تركته) ولو لم يوص بها؛ لحديث ابن عباس رضي الله عنهما مرفوعا: «اقضوا الله فالله أحق بالوفاء» [البخاري: ١٨٥٢، ومسلم: ١١٤٨].','294','361','0');
INSERT INTO [t147663] VALUES (366,'فصل في زكاة بهيمة الأنعام','294','361','0');
INSERT INTO [t147663] VALUES (367,'- مسألة: (وشرط في) زكاة (بهيمة الأنعام) من إبل، وبقر، وغنم، ثلاثة شروط','294','366','0');
INSERT INTO [t147663] VALUES (368,'- مسألة: في نصاب زكاة الإبل: (فأقل نصاب الإبل) بخاتي - وهي المتولدة من العربي والعجمي- أو عرابا: (خمس) من الإبل.','295','366','0');
INSERT INTO [t147663] VALUES (369,'(فصل) في زكاة البقر','297','361','0');
INSERT INTO [t147663] VALUES (370,'- مسألة: (وأقل نصاب البقر: ثلاثون) ولا يجب شيء فيما دون ذلك.','297','369','0');
INSERT INTO [t147663] VALUES (371,'فصل في زكاة الغنم','298','361','0');
INSERT INTO [t147663] VALUES (372,'- مسألة: (والخلطة) أي: الشركة في بهيمة الأنعام تنقسم إلى قسمين','299','371','0');
INSERT INTO [t147663] VALUES (373,'(فصل) في زكاة الخارج من الأرض','301','361','0');
INSERT INTO [t147663] VALUES (374,'- مسألة: تجب الزكاة في الخارج من الأرض في الجملة بإجماع أهل العلم؛ والأصل فيها قول الله تعالى: (يا أيها الذين آمنوا أنفقوا من طيبات ما كسبتم ومما أخرجنا لكم من الأرض) [البقرة: ٢٦٧]، والزكاة تسمى: «نفقة»','301','373','0');
INSERT INTO [t147663] VALUES (375,'- مسألة: (تجب الزكاة في كل) ما اجتمع فيه أمران','301','373','0');
INSERT INTO [t147663] VALUES (376,'- مسألة: (و) المقدار الواجب من زكاة الخارج من الأرض يختلف باختلاف أحواله في السقي والمؤنة، ولذا فهو على خمسة أقسام','305','373','0');
INSERT INTO [t147663] VALUES (377,'- مسألة: (ويجتمع عشر وخراج في أرض خراجية)؛ لعموم أدلة وجوب زكاة الخارج من الأرض، فالخراج في رقبتها والعشر في غلتها.','306','373','0');
INSERT INTO [t147663] VALUES (378,'- مسألة: (و) تجب الزكاة (في العسل) من النحل؛ لما روى عبد الله بن','306','373','0');
INSERT INTO [t147663] VALUES (379,'- مسألة: (ومن استخرج) من الأرض (من) أي (معدن) كان، سواء من ذهب، أو فضة، أو نحاس، أو حديد، أو غير ذلك -والمعدن: هو كل متولد في الأرض من غير جنسها-، وبلغ ذلك المعدن (نصابا) إن كان من ذهب أو فضة، أو ما يبلغ قيمة أحدهما من غيرهما (بعد سبك وتصفية، فـ) ـتجب (فيه) الزكاة؛ لعموم قوله تعالى: (أنفقوا من طيبات ما كسبتم ومما أخرجنا لكم من الأرض) [البقرة: ٢٦٧].','308','373','0');
INSERT INTO [t147663] VALUES (380,'- مسألة: (و) يجب (في الركاز -وهو الكنز-) المدفون من دفن الجاهلية إذا كان عليه علامة من علاماتها؛ كتواريخهم، أو أسماء ملوكهم،','308','373','0');
INSERT INTO [t147663] VALUES (381,'(فصل) في زكاة الذهب والفضة','309','361','0');
INSERT INTO [t147663] VALUES (382,'- مسألة: (ويجب في الذهب والفضة) وما يقوم مقاهما من عروض التجارة والأوراق النقدية؛ (ربع العشر إذا بلغا نصابا)؛ لحديث أنس رضي الله عنه في','309','381','0');
INSERT INTO [t147663] VALUES (383,'- مسألة: زكاة الحلي على أقسام','311','381','0');
INSERT INTO [t147663] VALUES (384,'فصل في أحكام التحلي','313','361','0');
INSERT INTO [t147663] VALUES (385,'- مسألة: (ويحرم أن يحلى مسجد أو محراب) بذهب وفضة، (أو) أن (يموه سقف أو حائط) من مسجد أو دار أو غيرهما (بنقد) أي: ذهب وفضة؛ لأنه سرف يفضي إلى الخيلاء، فهو كالآنية، (وتجب إزالته)؛ كسائر المنكرات.','313','384','0');
INSERT INTO [t147663] VALUES (386,'- مسألة: المتحلي لا يخلو من أمرين','313','384','0');
INSERT INTO [t147663] VALUES (387,'- مسألة: (و) يجب أن (يقوم عرض التجارة، وهو) أي: عرض التجارة: (ما يعد للبيع والشراء لأجل الربح)، فيقومها صاحبها (بالأحظ للفقراء من ذهب أو فضة) فإذا بلغ العرض بأحدهما نصابا وجبت فيه الزكاة؛ لأن التقويم لحظ أهل الزكاة، فتقوم بالأحظ لهم.','317','384','0');
INSERT INTO [t147663] VALUES (388,'(فصل) في زكاة الفطر','318','361','0');
INSERT INTO [t147663] VALUES (389,'- مسألة: (وزكاة الفطر صدقة واجبة بالفطر من) آخر (رمضان)؛ طهرة للصائم من الرفث واللغو، وطعمة للمساكين، (وتسمى) زكاة الفطر (فرضا)؛ لقول ابن عمر رضي الله عنهما: «فرض رسول الله صلى الله عليه وسلم زكاة الفطر» [البخاري ١٥٠٣، ومسلم ٩٨٤]، قال ابن المنذر: (وأجمع عوام أهل العلم على أن صدقة الفطر فرض).','318','388','0');
INSERT INTO [t147663] VALUES (390,'- مسألة: (ولا يمنع وجوبها) أي: زكاة الفطر (دين)، واختاره شيخ الإسلام؛ لتأكدها، بدليل وجوبها على الفقير، (إلا مع طلب) بالدين، فإن كان عليه دين يطالب به صاحبه؛ قدم وسقط وجوب الزكاة؛ لأن الزكاة وجبت مواساة، وقضاء الدين أهم.','318','388','0');
INSERT INTO [t147663] VALUES (391,'- مسألة: (وتجب) زكاة الفطر بشرطين','319','388','0');
INSERT INTO [t147663] VALUES (392,'- مسألة: (فـ) إن فضل له شيء عن ذلك وجب عليه أن (يخرج) زكاة الفطر (عن)','320','388','0');
INSERT INTO [t147663] VALUES (393,'- مسألة: (وتسن) زكاة الفطر (عن جنين)؛ لوردوه عن عثمان رضي الله عنه [مصنف ابن أبي شيبة ١٠٧٣٧، وفيه انقطاع]، وقال أبو قلابة: «كانوا يعطون صدقة الفطر حتى يعطون عن الحبل» [مصنف ابن أبي شيبة ١٠٧٣٨].','321','388','0');
INSERT INTO [t147663] VALUES (394,'- مسألة: أوقات إخراج زكاة الفطر','321','388','0');
INSERT INTO [t147663] VALUES (395,'- مسألة: (وهي) أي: الفطرة: (صاع من بر، أو) صاع من (شعير، أو) صاع من (تمر، أو) صاع من (زبيب، أو) صاع من (أقط)، ولا يجزئ غيرها مع قدرته على تحصيلها؛ لقول أبي سعيد رضي الله عنه: «كنا نخرج زكاة الفطر صاعا من طعام، أو صاعا من شعير، أو صاعا من تمر، أو صاعا من أقط، أو صاعا من زبيب» [البخاري ١٥٠٦، ومسلم ٩٨٥]، فلما عدد','323','388','0');
INSERT INTO [t147663] VALUES (396,'- مسألة: (فإن عدمت) الأصناف الخمسة: (أجزأ كل حب يقتات)، وثمر يقتات، إذا كان مكيلا؛ كالذرة، والأرز ونحوها؛ لأنه أشبه بالمنصوص عليه، فكان أولى.','324','388','0');
INSERT INTO [t147663] VALUES (397,'- مسألة: (ويجوز أن تعطي الجماعة فطرتهم لـ) شخص (واحد)، واختاره شيخ الإسلام؛ لأنها صدقة لغير معين، فجاز صرفها لواحد؛ كالزكاة.','324','388','0');
INSERT INTO [t147663] VALUES (398,'(فصل) في إخراج الزكاة','325','361','0');
INSERT INTO [t147663] VALUES (399,'- مسألة: (يجب إخراج الزكاة) أي: زكاة المال (فورا؛ كـ) ـإخراج (نذر وكفارة إن أمكن)؛ لقوله تعالى: {وآتوا الزكاة} [البقرة: ٤٣]، والأمر المطلق يقتضي الفورية.','325','398','0');
INSERT INTO [t147663] VALUES (400,'- مسألة: تارك الزكاة لا يخلو من أمرين','326','398','0');
INSERT INTO [t147663] VALUES (401,'- مسألة: نقل الزكاة من بلد إلى بلد آخر لا يخلو من أمرين','327','398','0');
INSERT INTO [t147663] VALUES (402,'- مسألة: (وإن كان المزكي في بلد وماله في) بلد (آخر) فلا يخلو من أمرين','328','398','0');
INSERT INTO [t147663] VALUES (403,'- مسألة: (ويجوز تعجيلها) أي: الزكاة؛ لحديث علي رضي الله عنه: «أن العباس سأل النبي صلى الله عليه وسلم في تعجيل صدقته قبل أن تحل، فرخص له في ذلك»','328','398','0');
INSERT INTO [t147663] VALUES (404,'فصل في أهل الزكاة','330','361','0');
INSERT INTO [t147663] VALUES (405,'- مسألة: (ولا) يجوز أن (تدفع) الزكاة (إلا إلى الأصناف الثمانية)؛ الذين ذكرهم الله تعالى في قوله: (إنما الصدقات للفقراء والمساكين والعاملين عليها والمؤلفة قلوبهم وفي الرقاب والغارمين وفي سبيل الله وابن السبيل فريضة من الله والله عليم حكيم) [سورة التوبة: ٦٠]، فدلت على حصر الزكاة في هؤلاء.','330','404','0');
INSERT INTO [t147663] VALUES (406,'- مسألة: (ويجوز الاقتصار) في الزكاة (على) صنف (واحد) من الأصناف الثمانية؛ لقوله تعالى: (وإن تخفوها وتؤتوها الفقراء فهو خير لكم) [البقرة: ٢٧١]، فاقتصر على صنف واحد.','332','404','0');
INSERT INTO [t147663] VALUES (407,'- مسألة: يجوز الاقتصار على واحد (من صنف) واحد من تلك الأصناف الثمانية؛ كأن تعطى الزكاة لفقير واحد، أو غارم واحد؛ لحديث سلمة بن صخر رضي الله عنه لما ظاهر من امرأته وعجز عن الكفارة، قال له النبي صلى الله عليه وسلم: «انطلق إلى صاحب صدقة بني زريق فليدفعها إليك» [أحمد: ١٦٤٢١، وأبو داود: ٢٢١٣، والترمذي: ٣٢٩٩، وابن ماجه: ٢٠٦٢].','333','404','0');
INSERT INTO [t147663] VALUES (408,'- مسألة: (وتسن) الزكاة (إلى من لا تلزمه) أي: لا تجب عليه (مؤنته) أي: نفقته (من أقاربه)، كذوي رحمه، ومن لا يرثه؛ من نحو أخ وعم؛ لحديث سلمة بن عامر رضي الله عنه: «إن الصدقة على المسكين صدقة، وعلى ذي الرحم اثنتان: صدقة وصلة» [أحمد: ١٦٢٣٣، والترمذي: ٦٥٨، والنسائي: ٢٥٨٢، وابن ماجه: ١٨٤٤].','333','404','0');
INSERT INTO [t147663] VALUES (409,'- مسألة: (ومن أبيح له أخذ شيء) من زكاة، أو كفارة، أو نذر، أوغيرها (أبيح له سؤاله)؛ لأنه يطلب حقه الذي جعل له.','333','404','0');
INSERT INTO [t147663] VALUES (410,'- مسألة: (ويجب قبول مال طيب) من زكاة، أو كفارة، أو صدقة تطوع، أو هبة، مما يجوز له أخذه إذا (أتى بلا مسألة ولا استشراف نفس)؛ لحديث عمر رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: «إذا جاءك من هذا المال شيء وأنت غير مشرف ولا سائل، فخذه وما لا فلا تتبعه نفسك» [البخاري: ١٤٧٣، مسلم: ١٠٤٥].','334','404','0');
INSERT INTO [t147663] VALUES (411,'- مسألة: (وإن تفرغ قادر على التكسب) تفرغا كليا (للعلم الشرعي لا) إن تفرغ القادر على التكسب (للعبادة، وتعذر الجمع بين التكسب والاشتغال بالعلم؛ أعطي من زكاة لحاجته؛ وإن لم يكن العلم لازما له)؛ لأن العلم نفعه متعد، بخلاف العبادة، لقصور نفعها عليه.','334','404','0');
INSERT INTO [t147663] VALUES (412,'(فصل) في موانع الزكاة','335','361','0');
INSERT INTO [t147663] VALUES (413,'- مسألة: (ولا يجزئ دفعها) أي: الزكاة لأصناف','335','412','0');
INSERT INTO [t147663] VALUES (414,'- مسألة: (وإن دفعها) أي: الزكاة (لغير مستحقها لجهل)؛ كمن ظنه مكاتبا، أو غارما، (ثم علم حاله) بأنه غير مستحق (لم تجزئه)؛ لأنه ليس بمستحق، ولا يخفى حاله غالبا، فلم يعذر بجهالته؛ كدين الآدمي.','336','412','0');
INSERT INTO [t147663] VALUES (415,'- مسألة: (وتسن صدقة التطوع) بالفاضل عن كفايته وكفاية من ينفق عليه (كل وقت)؛ لأن الله حث عليها في آيات كثيرة، ولحديث أبي هريرة رضي الله عنه مرفوعا: «خير الصدقة ما كان عن ظهر غنى، وابدأ بمن تعول» [البخاري: ١٤٢٦، ومسلم: ١٠٣٤].','337','412','0');
INSERT INTO [t147663] VALUES (416,'- مسألة: (وكونها) أي: الصدقة','337','412','0');
INSERT INTO [t147663] VALUES (417,'- مسألة: (و) تتأكد الصدقة في مواطن','338','412','0');
INSERT INTO [t147663] VALUES (418,'- مسألة: (والمن بالصدقة) وغيرها (كبيرة)، والكبيرة: ما فيه حد في الدنيا، أو وعيد في الآخرة، وزاد شيخ الإسلام: أو غضب، أو لعنة، أو نفي إيمان، (ويبطل الثواب به) أي: المن؛ لقوله تعالى: {لا تبطلوا صدقاتكم بالمن والأذى} [البقرة: ٢٦٤].','339','412','0');
INSERT INTO [t147663] VALUES (419,'- مسألة: (وصوم رمضان يجب) بواحد من ثلاثة أمور','340','412','0');
INSERT INTO [t147663] VALUES (420,'(كتاب الصيام)','340','0','0');
INSERT INTO [t147663] VALUES (421,'الصيام لغة','340','420','0');
INSERT INTO [t147663] VALUES (422,'- مسألة: (والهلال المرئي نهارا)، قبل الزوال أو بعده فهو (لليلة المقبلة)؛ لما روى شقيق بن سلمة، قال: أتأنا كتاب عمر بن الخطاب رضي الله عنه ونحن بخانقين: «إن الأهلة بعضها أعظم من بعض، فإذا رأيتم الهلال من أول النهار فلا تفطروا حتى يشهد شاهدان أنهما رأياه بالأمس» [الدارقطني ٢١٩٧، وصححه إسناده الحافظ].','342','420','0');
INSERT INTO [t147663] VALUES (423,'- مسألة: (وإذا ثبتت رؤيته) أي: هلال رمضان (ببلد لزم الصوم جميع الناس)؛ لحديث ابن عمر رضي الله عنهما السابق: «إذا رأيتموه فصوموا»، وهو خطاب للأمة كافة.','343','420','0');
INSERT INTO [t147663] VALUES (424,'- مسألة: (وإن ثبتت) رؤية هلال رمضان (نهارا) ولم يكونوا بيتوا النية لنحو غيم (أمسكوا) عن مفسدات الصوم لحرمة الوقت، (وقضوا) ذلك اليوم؛ لأنهم لم يصوموه.','343','420','0');
INSERT INTO [t147663] VALUES (425,'- مسألة: (ويقبل فيه) أي: هلال رمضان (وحده) دون غيره من الشهور (خبر) واحد، فلا يشترط التعدد؛ لحديث ابن عمر رضي الله عنهما قال: «تراءى الناس الهلال، فأخبرت رسول الله صلى الله عليه وسلم أني رأيته، فصامه، وأمر الناس بصيامه» [أبو داود ٢٣٣٨، وصححه الألباني].','344','420','0');
INSERT INTO [t147663] VALUES (426,'- مسألة: (ومن رآه) أي: الهلال (وحده لشوال لم يفطر) واختاره شيخ الإسلام؛ لحديث أبي هريرة رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم «الفطر يوم تفطرون، والأضحى يوم تضحون» [أبو داود: ٢٣٢٤، والترمذي: ٦٩٧، وابن ماجه: ١٦٦٠].','345','420','0');
INSERT INTO [t147663] VALUES (427,'- مسألة: (و) من رآى الهلال وحده (لرمضان، وردت شهادته؛ لزمه الصوم، وجميع أحكام الشهر، من طلاق، وعتاق، وغيرهما)، كظهار معلق به؛ لأنه يوم علمه من رمضان، فلزمه حكمه.','345','420','0');
INSERT INTO [t147663] VALUES (428,'(فصل)','346','420','0');
INSERT INTO [t147663] VALUES (429,'- مسألة: (ويجب) صوم شهر رمضان (على كل)','346','428','0');
INSERT INTO [t147663] VALUES (430,'- مسألة: (ومن عجز عنه) أي: الصوم (لكبر)؛ كشيخ هرم وعجوز يجهدهما الصوم، ويشق عليهما مشقة شديدة، (أو) عجز عنه لـ (ـمرض لا يرجى برؤه أفطر، وعليه) إن كان فطره (لا مع عذر معتاد كسفر) إطعام مسكين (عن كل يوم)؛ لقول ابن عباس رضي الله عنهما في قوله تعالى: (وعلى الذين يطيقونه فدية طعام مسكين) [البقرة: ١٨٤]: «ليست بمنسوخة، هو الشيخ الكبير، والمرأة الكبيرة لا يستطيعان أن يصوما، فيطعمان مكان كل يوم مسكينا» [البخاري ٤٥٠٥].','348','428','0');
INSERT INTO [t147663] VALUES (431,'- مسألة: (وسن فطر، وكره صوم) لمسافر (بسفر قصر، ولو بلا مشقة)؛ لحديث جابر رضي الله عنه مرفوعا: «ليس من البر الصوم في السفر» [البخاري ١٩٤٦، ومسلم ١١١٥].','349','428','0');
INSERT INTO [t147663] VALUES (432,'- مسألة: (وكره صوم حامل ومرضع خافتا على أنفسهما أو) خافتا على (الولد)؛ كالمريض، ويجوز لهما الفطر اتفاقا.','350','428','0');
INSERT INTO [t147663] VALUES (433,'- مسألة: (وشرط لـ) صوم (كل يوم واجب نية معينة) مفردة له؛ بأن يعتقد أنه يصوم من رمضان، أو قضائه، أو نذر، أو كفارة؛ لأن كل يوم من رمضان عبادة مفردة، لا يفسد صومه بفساد صوم يوم آخر، وكقضاء رمضان.','352','428','0');
INSERT INTO [t147663] VALUES (434,'- مسألة: يشترط في صوم الفرض أن ينوي (من الليل)، ولا فرق بين أول الليل ووسطه وآخره؛ لحديث حفصة رضي الله عنها قالت: قال رسول الله صلى الله عليه وسلم: «من لم يجمع الصيام قبل الفجر، فلا صيام له» [أحمد ٢٦٤٥٧، أبو داود ٢٤٥٤، والترمذي ٧٣٠، والنسائي ٢٣٣٠، وابن ماجه ١٧٠٠].','352','428','0');
INSERT INTO [t147663] VALUES (435,'- مسألة: (ويصح صوم نفل ممن لم يفعل مفسدا)؛ من أكل، وشرب، ونحوهما، (بنيته نهارا؛ ولو) كانت النية (بعد الزوال)؛ لحديث عائشة رضي الله عنها قالت: دخل علي النبي صلى الله عليه وسلم ذات يوم فقال: «هل عندكم شيء؟ » فقلنا: لا، قال: «فإني إذن صائم» [مسلم ١١٥٤].','353','428','0');
INSERT INTO [t147663] VALUES (436,'- مسألة: (ومن خطر بقلبه ليلا أنه صائم غدا فقد نوى، وكذا الأكل والشرب بنية الصوم)؛ لأن محل النية القلب.','353','428','0');
INSERT INTO [t147663] VALUES (437,'(فصل) في المفطرات','354','420','0');
INSERT INTO [t147663] VALUES (438,'- مسألة: مفطرات الصائم','354','437','0');
INSERT INTO [t147663] VALUES (439,'- مسألة: (ولا) يفسد الصوم (إن دخل ماء مضمضة أو استنشاق حلقه، و) كذا (لو بالغ) في المضمضة والاستنشاق، (أو) تمضمض أو استنشق و (زاد على ثلاث) غرفات، فدخل الماء حلقه؛ لعدم القصد في الكل.','359','437','0');
INSERT INTO [t147663] VALUES (440,'(فصل) في الجماع في نهار رمضان وغير ذلك','360','420','0');
INSERT INTO [t147663] VALUES (441,'- مسألة: (ومن جامع) بتغييب حشفة أصلية في فرج أصلي، (في نهار رمضان في قبل أو دبر، ولو) كان ذلك الفرج (لميت أو بهيمة)، وهو صائم أو (في حالة يلزمه فيها الإمساك)؛ كما لو ثبتت','360','440','0');
INSERT INTO [t147663] VALUES (442,'- مسألة: من تكرر منه الجماع في رمضان فلا يخلو من أحوال','362','440','0');
INSERT INTO [t147663] VALUES (443,'- مسألة: (وهي) أي: كفارة الوطء في نهار رمضان على الترتيب','364','440','0');
INSERT INTO [t147663] VALUES (444,'- مسألة: (وسن) للصائم أمور','365','440','0');
INSERT INTO [t147663] VALUES (445,'- مسألة: (ومن فاته رمضان) كله (قضى عدد أيامه) تاما كان أو ناقصا؛ كأعداد الصلوات الفائتة.','366','440','0');
INSERT INTO [t147663] VALUES (446,'- مسألة: يحرم (ولا يصح ابتداء تطوع من عليه قضاء رمضان) قبل الفرض، واختاره ابن باز؛ قياسا على الحج، ولأن المبادرة إلى إبراء الذمة من أكبر العمل الصالح.','366','440','0');
INSERT INTO [t147663] VALUES (447,'- مسألة: (فإن) صام و (نوى) به (صوما واجبا أو قضاء ثم قلبه نفلا صح) النفل؛ قياسا على قلب فرض الصلاة نفلا.','366','440','0');
INSERT INTO [t147663] VALUES (448,'- مسألة: إن أخر قضاء رمضان حتى دخل رمضان آخر فلا يخلو من حالتين','367','440','0');
INSERT INTO [t147663] VALUES (449,'(فصل) في صوم التطوع وما يكره منه','369','420','0');
INSERT INTO [t147663] VALUES (450,'- مسألة: (يسن صوم التطوع، وأفضله)','369','449','0');
INSERT INTO [t147663] VALUES (451,'- مسألة: (و) يسن (صوم)','369','449','0');
INSERT INTO [t147663] VALUES (452,'- مسألة: (وكره) من الصيام','372','449','0');
INSERT INTO [t147663] VALUES (453,'- مسألة: يحرم (ولا يصح صوم أيام التشريق)، وهي الحادي عشر، والثاني عشر، والثالث عشر من ذي الحجة، ولا يصح فرضا ولا نفلا،','374','449','0');
INSERT INTO [t147663] VALUES (454,'- مسألة: (ولا) يصح (صوم) يوم (عيد مطلقا)، أي: سواء فرضا أو نفلا (ويحرم) بالإجماع؛ لحديث أبي هريرة رضي الله عنه: «أن رسول الله صلى الله عليه وسلم نهى عن صيام يومين: يوم الأضحى، ويوم الفطر» [البخاري ١٩٩٣، ومسلم ١١٣٨]، والنهي يقتضي الفساد؛ لأنه يعود إلى ذات العبادة.','375','449','0');
INSERT INTO [t147663] VALUES (455,'- مسألة: (ومن دخل في تطوع) فأراد قطعه لم يخل من ثلاثة أقسام','375','449','0');
INSERT INTO [t147663] VALUES (456,'- مسألة: (ويجب) بغير خلاف (إتمام فرض مطلقا) أي: بأصل الشرع أو بالنذر، وسواء كان فرض كفاية؛ كصلاة الجنازة، أو فرض عين، (ولو) كان وقته (موسعا؛ كصلاة، وقضاء رمضان، و) كذا (نذر مطلق، وكفارة) -إن قلنا: هما غير واجبين على الفور، والمذهب خلافه- لأن الخروج من عهدة الواجب متعين، ودخلت التوسعة في وقته رفقا ومظنة للحاجة، فإذا شرع فيها تعينت المصلحة في إتمامه، (وإن بطل) الفرض (فلا مزيد) عليه، فيعيده، أو يقضيه فقط، (ولا كفارة) مطلقا، غير الوطء في نهار رمضان، وتقدم.','376','449','0');
INSERT INTO [t147663] VALUES (457,'- مسألة: (وأفضل الأيام يوم الجمعة)، قال شيخ الإسلام: (هو أفضل أيام الأسبوع إجماعا)؛ لحديث أبي هريرة رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: «خير يوم طلعت عليه الشمس يوم الجمعة، فيه خلق آدم،','376','449','0');
INSERT INTO [t147663] VALUES (458,'- مسألة: (وأفضل الليالي ليلة القدر)؛ لقوله تعالى: {ليلة القدر خير من ألف شهر} [القدر: ٣].','377','449','0');
INSERT INTO [t147663] VALUES (459,'(فصل) في الاعتكاف','379','420','0');
INSERT INTO [t147663] VALUES (460,'- مسألة: (والاعتكاف سنة) كل وقت إجماعا؛','379','459','0');
INSERT INTO [t147663] VALUES (461,'- مسألة: (ويجب) اعتكاف (بنذر)؛ لحديث عائشة رضي الله عنها قال: قال رسول الله صلى الله عليه وسلم: «من نذر أن يطيع الله فليطعه» [البخاري: ٦٦٩٦].','379','459','0');
INSERT INTO [t147663] VALUES (462,'- مسألة: (وشرط له) أي: لصحة الاعتكاف شروط','380','459','0');
INSERT INTO [t147663] VALUES (463,'- مسألة: (ومن نذر الاعتكاف أو) نذر (الصلاة في مسجد) من المساجد، فلا يخلو من حالين','381','459','0');
INSERT INTO [t147663] VALUES (464,'- مسألة: الخروج من المسجد مبطل للاعتكاف في الجملة؛ لأنه ترك لركن الاعتكاف، وهو اللبث في المسجد، وخروج المعتكف من المسجد لا يخلو من حالين','383','459','0');
INSERT INTO [t147663] VALUES (465,'- مسألة: (ويبطل) الاعتكاف بأمور','386','459','0');
INSERT INTO [t147663] VALUES (466,'- مسألة: (وحيث بطل) الاعتكاف المنذور بسكر، أو ردة، أو خروج لما له منه بد؛ لم يخل ذلك من ثلاثة أحوال','387','459','0');
INSERT INTO [t147663] VALUES (467,'- مسألة: (ولا يبطل إن خرج) المعتكف (من المسجد) لما لا بد منه طبعا؛ كخروجه (لبول، أو غائط، أو إتيان بمأكل ومشرب، أو) كان خروجه لما لا بد منه شرعا؛ كخروجه (لجمعة تلزمه، أو طهارة واجبة) كوضوء وغسل، (ونحو ذلك) كخروجه لغسل متنجس يحتاجه، وقيء بغتة، وتقدم تفصيله.','388','459','0');
INSERT INTO [t147663] VALUES (468,'- مسألة: (ويسن) للمعتكف (تشاغله بالقرب) جمع قربة، وهي: كل ما يتقرب به إلى الله تعالى، من صلاة، وقراءة قرآن، وذكر، ونحوها؛ لأن المقصود من الاعتكاف صلاح القلب واستقامته.','388','459','0');
INSERT INTO [t147663] VALUES (469,'- مسألة: (و) يسن له أيضا (اجتناب ما لا يعنيه) أي: يهمه، كفضول الكلام، والنظر، والضحك، والنوم، وسائر المباحات؛ لقوله صلى الله عليه وسلم: «من حسن إسلام المرء تركه ما لا يعنيه» [الترمذي: ٢٣١٧، وابن ماجه: ٣٩٧٦].','388','459','0');
INSERT INTO [t147663] VALUES (470,'- مسألة: (ويحرم جعل القرآن بدلا عن الكلام)، كقولك لمن اسمه','388','459','0');
INSERT INTO [t147663] VALUES (471,'- مسألة: (وينبغي لمن قصد المسجد أن ينوي الاعتكاف مدة لبثه فيه)؛ لأن أقل الاعتكاف هو ما يسمى به معتكفا لابثا؛ لأنه يصدق عليه اسم الاعتكاف لغة.','389','459','0');
INSERT INTO [t147663] VALUES (472,'- مسألة: (وهو) أي: الحج (فرض كفاية كل عام) على من لم يجب عليه عينا.','390','459','0');
INSERT INTO [t147663] VALUES (473,'- مسألة: (وهو) أي الحج لغة: القصد إلى من تعظمه، وشرعا: (قصد مكة لعمل مخصوص، في زمن مخصوص).','390','459','0');
INSERT INTO [t147663] VALUES (474,'(كتاب الحج)','390','0','0');
INSERT INTO [t147663] VALUES (475,'- مسألة: (وهو): أي الحج (أحد أركان الإسلام)؛ لحديث ابن عمر رضي الله عنهما: قال النبي صلى الله عليه وسلم: «بني الإسلام على خمس: شهادة أن لا إله إلا الله، وأن محمدا عبده ورسوله، وإقام الصلاة، وإيتاء الزكاة، وحج البيت، وصوم رمضان» [البخاري ٨، ومسلم ١٦].','391','474','0');
INSERT INTO [t147663] VALUES (476,'- مسألة: (والعمرة) لغة: الزيارة، وشرعا: (زيارة البيت) الحرام (على وجه مخصوص).','391','474','0');
INSERT INTO [t147663] VALUES (477,'- مسألة: (ويجبان) أي: الحج والعمرة.','391','474','0');
INSERT INTO [t147663] VALUES (478,'- مسألة: يجب الحج والعمرة (في العمر مرة) واحدة، إجماعا؛ لحديث ابن عباس رضي الله عنهما: قال رسول الله صلى الله عليه وسلم: «الحج مرة فمن زاد فتطوع» [أحمد ٢٣٠٤، وأبو داود ١٧٢١، والنسائي ٢٦١٩، وابن ماجه ٢٨٨٦].','392','474','0');
INSERT INTO [t147663] VALUES (479,'- مسألة: يجب الحج والعمرة (بخمسة شروط)','392','474','0');
INSERT INTO [t147663] VALUES (480,'- مسألة: (فإن بلغ الصغير أو عتق الرقيق) في الحج، فلا يخلو من أمرين','393','474','0');
INSERT INTO [t147663] VALUES (481,'- مسألة: (فمن كملت له هذه الشروط) أي: شروط السعي للحج والعمرة (لزمه السعي فورا إن كان في الطريق أمن)، بناء على أن الأمر المطلق للفور، ويؤيده: حديث ابن عباس رضي الله عنهما: قال رسول الله صلى الله عليه وسلم: «من أراد الحج فليتعجل» [أحمد ١٨٣٤، وأبو داود ١٧٣٢، وابن ماجه ٢٨٨٣].','396','474','0');
INSERT INTO [t147663] VALUES (482,'- مسألة: القدرة في الحج والعمرة على أربعة أقسام','397','474','0');
INSERT INTO [t147663] VALUES (483,'- مسألة: (وتزيد الأنثى) سواء كانت شابة أو عجوزا (شرطا سادسا: وهو أن تجد لها) محرما؛ فإن لم تجد المحرم لم يجب عليها الحج، ولا الإنابة فيه؛ لحديث ابن عباس رضي الله عنهما: قال رسول الله صلى الله عليه وسلم: «لا تسافر المرأة إلا مع ذي محرم، ولا يدخل عليها رجل إلا ومعها محرم»، فقال رجل: يا رسول الله؛ إني أريد أن أخرج في جيش كذا وكذا، وامرأتي تريد الحج، فقال: «اخرج معها» [البخاري ١٨٥٥، ومسلم ١٣٣٤]، وهو عام في كل سفر.','399','474','0');
INSERT INTO [t147663] VALUES (484,'- مسألة: (فإن) وجدت المحرم وفرطت بالتأخير، ثم (أيست منه) أي: من وجود المحرم (استنابت) من يحج عنها ويعتمر؛ كالكبير العاجز.','401','474','0');
INSERT INTO [t147663] VALUES (485,'(فصل) في المواقيت','402','474','0');
INSERT INTO [t147663] VALUES (486,'- مسألة: (والمواقيت) لغة: الحد، وشرعا: (مواضع وأزمنة معينة لعبادة مخصوصة).','402','485','0');
INSERT INTO [t147663] VALUES (487,'- مسألة: مواقيت الحج والعمرة على قسمين','402','485','0');
INSERT INTO [t147663] VALUES (488,'(فصل) في الإحرام','405','474','0');
INSERT INTO [t147663] VALUES (489,'- مسألة: (وسن لمريد الإحرام وهو) في اللغة: نية الدخول في التحريم، وفي الشرع: (نية) الدخول في (النسك)، لا نية أن يحج أو يعتمر، فيسن لمريده','405','488','0');
INSERT INTO [t147663] VALUES (490,'- مسألة: (ونيته) أي: نية الإحرام (شرط)، فلا يصير محرما بمجرد التجرد أو التلبية من غير نية الدخول في النسك؛ لحديث: «إنما الأعمال بالنيات» [البخاري: ١، ومسلم: ١٩٠٧].','408','488','0');
INSERT INTO [t147663] VALUES (491,'- مسألة: (وأفضل الأنساك) الثلاثة (التمتع)؛ لأن النبي صلى الله عليه وسلم أمر أصحابه لما طافوا وسعوا أن يجعلوها عمرة إلا من ساق هديا، وثبت على إحرامه لسوقه الهدي، وتأسف بقوله: «لو استقبلت من أمري ما استدبرت ما سقت الهدي، ولحللت مع الناس حين حلوا» [البخاري ١٦٥١، ومسلم ١٢١٦]، ثم الإفراد؛ لقول عمر رضي الله عنه: «فافصلوا حجكم من عمرتكم، فإنه أتم لحجكم، وأتم لعمرتكم» [مسلم ١٢٢٤]، ولإتيانه بالحج تاما من غير احتياج إلى جبر فكان أولى، ثم القران.','409','488','0');
INSERT INTO [t147663] VALUES (492,'- مسألة: (و) صفة التمتع: (هو) ما اجتمعت فيه أربعة شروط','409','488','0');
INSERT INTO [t147663] VALUES (493,'- مسألة: (والإفراد أن يحرم بالحج) فقط مفردا، (ثم بعد فراغه منه) أي: من الحج؛ (يحرم بالعمرة)، وهذا إن كان وجوب العمرة باقيا عليه، بأن لم يكن أتى بها من قبل، وإلا فليست العمرة قيدا في الإفراد.','410','488','0');
INSERT INTO [t147663] VALUES (494,'- مسألة: (والقران) له ثلاث صور','410','488','0');
INSERT INTO [t147663] VALUES (495,'- مسألة: (وسن) لمريد الإحرام (أن يعين نسكا) في ابتداء إحرامه؛ من عمرة، أو حج، أو قران، ويلفظ بما عينه؛ لقول عائشة رضي الله عنها: «خرجنا مع رسول الله صلى الله عليه وسلم، فمنا من أهل بعمرة، ومنا من أهل بحجة، ومنا من أهل بحج وعمرة» [البخاري: ٣١٩، ومسلم: ١٢١١].','412','488','0');
INSERT INTO [t147663] VALUES (496,'- مسألة: (و) يسن (أن يشترط) في الإحرام؛ لحديث عائشة رضي الله عنها قالت: دخل رسول الله صلى الله عليه وسلم على ضباعة بنت الزبير رضي الله عنها فقال لها: «أردت الحج؟ » قالت: والله، ما أجدني إلا وجعة، فقال لها: «حجي واشترطي، وقولي: اللهم محلي حيث حبستني» [البخاري ٥٠٨٩، ومسلم ١٢٠٧].','412','488','0');
INSERT INTO [t147663] VALUES (497,'- مسألة: (وإذا انعقد) الإحرام (لم يبطل) بجنون، أو إغماء، أو سكر، أو موت؛ لحديث ابن عباس في الذي وقصته ناقته وهو محرم، وفيه: «فإن الله يبعثه يوم القيامة يلبي» [البخاري: ١٨٤٩، ومسلم: ١٢٠٦].','413','488','0');
INSERT INTO [t147663] VALUES (498,'(فصل) في محظورات الإحرام','414','474','0');
INSERT INTO [t147663] VALUES (499,'- مسألة: يحرم على المحرم فعل شيء من المحظورات لغير عذر؛ لأن الأصل في النهي أنه للتحريم.','414','498','0');
INSERT INTO [t147663] VALUES (500,'- مسألة: (ومحظورات الإحرام تسع)','414','498','0');
INSERT INTO [t147663] VALUES (501,'- مسألة: (وفي جميع المحظورات) السابقة (الفدية، إلا) محظورين','419','498','0');
INSERT INTO [t147663] VALUES (502,'- مسألة: (وفي) إفساد (البيض) أي: بيض الصيد الضمان؛ لأنه كجزئه، فيضمنه بقيمته في مكانه؛ لأن البيض لا مثل له، فتجب فيه القيمة كصغار الطير.','421','498','0');
INSERT INTO [t147663] VALUES (503,'- مسألة: (وفي) إزالة (الشعرة) الواحدة، (أو الظفر) الواحد: (إطعام مسكين) واحد، (وفي) إزالة (الاثنين) أي: شعرتين أو ظفرين: (إطعام اثنين) أي: مسكينين، وفي الثلاثة: فدية أذى؛ لقول عطاء بن أبي رباح: «في الشعرة مد، وفي الشعرتين مدان، وفي الثلاث فصاعدا دم» [البيهقي: ٩١٢٤]، وخصت فدية الأذى في حلق ثلاث شعرات فأكثر؛ لأن أقل ما يصدق عليه اسم الحلق حلق ثلاث شعرات.','421','498','0');
INSERT INTO [t147663] VALUES (504,'(فصل في الفدية)','422','474','0');
INSERT INTO [t147663] VALUES (505,'- مسألة: (وهي) أي: الفدية لغة: ما يعطى في افتكاك الأسير، أو إنقاذ من هلكة، واصطلاحا: (ما يجب بسبب إحرام أو حرم) مكي.','422','504','0');
INSERT INTO [t147663] VALUES (506,'- مسألة: الفدية على قسمين','422','504','0');
INSERT INTO [t147663] VALUES (507,'- مسألة: من فعل محظورا من محظورات الإحرام لا يخلو من ثلاثة أحوال','426','504','0');
INSERT INTO [t147663] VALUES (508,'- مسألة: مصرف الفدية ينقسم إلى قسمين','428','504','0');
INSERT INTO [t147663] VALUES (509,'- مسألة: (ويجزئ الصوم) والحلق (بكل مكان)، قال في «المبدع»: (لا نعلم فيه خلافا)؛ لقول ابن عباس رضي الله عنهما السابق: «الهدي والإطعام بمكة، والصوم حيث شاء».','429','504','0');
INSERT INTO [t147663] VALUES (510,'- مسألة: (والدم) المطلق','429','504','0');
INSERT INTO [t147663] VALUES (511,'(فصل في جزاء الصيد)','430','474','0');
INSERT INTO [t147663] VALUES (512,'- مسألة: (وهو) أي: الصيد (ضربان)','430','511','0');
INSERT INTO [t147663] VALUES (513,'(فصل) في حكم صيد الحرم','433','474','0');
INSERT INTO [t147663] VALUES (514,'- مسألة: (وحرم) على المحرم والحلال (صيد حرم مكة) إجماعا','433','513','0');
INSERT INTO [t147663] VALUES (515,'- مسألة: (وحرم قطع شجره) أي: شجر الحرم إجماعا','433','513','0');
INSERT INTO [t147663] VALUES (516,'- مسألة: (وكره إخراج تراب الحرم، و) إخراج (حجارته إلى الحل)؛ لما ورد عن عطاء عن ابن عباس وابن عمر رضي الله عنه: «أنهما كرها أن ينقل من تراب الحرم إلى الحل، أو يدخل تراب الحل إلى الحرم» [أخبار مكة للفاكهي: ٢٢٧٣].','436','513','0');
INSERT INTO [t147663] VALUES (517,'- مسألة: (وتستحب المجاورة بمكة) لما يأتي في بيان فضلها.','436','513','0');
INSERT INTO [t147663] VALUES (518,'- مسألة: (وتضاعف الحسنة) بالكمية في مكان فاضل؛ كمكة والمدينة، وزمان فاضل؛ كالأشهر الحرم ورمضان، بلا خلاف بين العلماء؛ للحديث القدسي الذي رواه ابن عباس رضي الله عنهما مرفوعا: «إن الله كتب الحسنات والسيئات ثم بين ذلك، فمن هم بحسنة فلم يعملها كتبها الله له عنده حسنة كاملة، فإن هو هم بها فعملها كتبها الله له عنده عشر حسنات إلى سبع مائة ضعف إلى أضعاف كثيرة» [البخاري: ٦٤٩١، ومسلم: ١٣١].','437','513','0');
INSERT INTO [t147663] VALUES (519,'- مسألة: (وحرم صيد حرم المدينة)؛ لحديث عامر بن سعد عن أبيه رضي الله عنه: قال رسول الله صلى الله عليه وسلم: «إني أحرم ما بين لابتي المدينة أن يقطع عضاهها، أو يقتل صيدها» [مسلم ١٣٦٣].','437','513','0');
INSERT INTO [t147663] VALUES (520,'- مسألة: (و) حرم (قطع شجره) أي: شجر المدينة، (وحشيشه)؛','437','513','0');
INSERT INTO [t147663] VALUES (521,'- مسألة: (ولا جزاء) في صيد حرم المدينة، ولا في قطع شجرها وحشيشها، قال أحمد: (لم يبلغنا أن النبي صلى الله عليه وسلم ولا أحدا من أصحابه حكموا فيه بجزاء).','438','513','0');
INSERT INTO [t147663] VALUES (522,'- مسألة: (يسن) عند دخول مكة','439','513','0');
INSERT INTO [t147663] VALUES (523,'- مسألة: (فإذا رأى البيت رفع يديه)؛ لقول ابن عباس رضي الله عنهما: «ترفع الأيدي في سبع مواطن: إذا رأى البيت، وعلى الصفا والمروة، وفي جمع، والعرفات، وعند الجمار» [مصنف ابن أبي شيبة ١٥٧٤٨، وفيه ضعف]، (وقال ما ورد)، ومنه','440','513','0');
INSERT INTO [t147663] VALUES (524,'- مسألة: (ثم يطوف متمتع للعمرة، و) يطوف (مفرد وقارن للقدوم، وهو الورود)، وهو تحية الكعبة؛ لما ورد عن عائشة رضي الله عنها: «أن أول شيء','440','513','0');
INSERT INTO [t147663] VALUES (525,'- مسألة: (ويضطبع) في هذا الطواف، بأن يجعل وسط الرداء تحت عاتقه الأيمن، ويجعل طرفيه على عاتقه الأيسر؛ (غير حامل) شخص (معذور) بردائه؛ كمريض أو صغير، فلا يضطبع؛ لعذر، (في كل أسبوعه) أي: في الأشواط السبعة كلها، استحبابا باتفاق الأئمة؛ لما ورد عن ابن عباس رضي الله عنهما: «أن رسول الله صلى الله عليه وسلم وأصحابه اعتمروا من الجعرانة، فرملوا بالبيت، وجعلوا أرديتهم تحت آباطهم قد قذفوها على عواتقهم اليسرى» [أحمد ٢٧٩٢، وأبو داود ١٨٨٤].','441','513','0');
INSERT INTO [t147663] VALUES (526,'- مسألة: (ويبتدئه) أي: الطواف (من الحجر الأسود)؛ لفعله صلى الله عليه وسلم كما سيأتي، (فيحاذيه) أي: الحجر (أو) يحاذي (بعضه بكل بدنه)؛ لأن ما لزم استقباله لزم بجميع البدن كالقبلة، (ويستلمه) أي: الحجر الأسود، واستلامه على مراتب','441','513','0');
INSERT INTO [t147663] VALUES (527,'- مسألة: (ويرمل) وهو إسراع المشي مع تقارب الخطى من غير وثب، (الأفقي) وهو المحرم من بعيد من مكة، (في هذا الطواف) فقط؛ لما ورد أن ابن عمر رضي الله عنهما: «أن رسول الله صلى الله عليه وسلم كان إذا طاف في الحج والعمرة أول ما يقدم فإنه يسعى ثلاثة أطواف بالبيت، ثم يمشي أربعة» [البخاري ١٦٠٣، ومسلم ١٢٦١].','443','513','0');
INSERT INTO [t147663] VALUES (528,'- مسألة: (فإذا فرغ) من الطواف (صلى ركعتين) نفلا؛ لما لحديث','443','513','0');
INSERT INTO [t147663] VALUES (529,'- مسألة: (ثم) بعد الصلاة يعود و (يستلم الحجر) الأسود؛ لقول جابر رضي الله عنه: «ثم رجع إلى الركن فاستلمه»، ولا يستلمه إلا في طواف يعقبه سعي؛ لأنه الوارد.','444','513','0');
INSERT INTO [t147663] VALUES (530,'(فصل في صفة الحج والعمرة)','446','474','0');
INSERT INTO [t147663] VALUES (531,'- مسألة: (يسن لمحل بمكة) وقربها (الإحرام بالحج) قبل الزوال من (يوم التروية، وهو الثامن من ذي الحجة)','446','530','0');
INSERT INTO [t147663] VALUES (532,'- مسألة: (و) يسن (المبيت بمنى) يوم التروية','446','530','0');
INSERT INTO [t147663] VALUES (533,'- مسألة: (فإذا طلعت الشمس) من يوم عرفة (سار) من منى (فأقام بنمرة إلى الزوال، ثم يأتي عرفة)؛ لحديث جابر رضي الله عنه، وفيه: «ثم مكث','446','530','0');
INSERT INTO [t147663] VALUES (534,'- مسألة: (وكلها) أي: كل عرفة (موقف) اتفاقا؛ لحديث جابر رضي الله عنه مرفوعا: «ووقفت هاهنا، وعرفة كلها موقف» [مسلم: ١٢١٨]، (إلا بطن عرنة) فليست من عرفة اتفاقا؛ لحديث جابر مرفوعا: «كل عرفة موقف، وارتفعوا عن بطن عرنة» [ابن ماجه: ٣٠١٢].','447','530','0');
INSERT INTO [t147663] VALUES (535,'- مسألة: (ويجمع) استحبابا من يباح له الجمع (فيها) أي: عرفة (بين الظهر والعصر تقديما) إجماعا في الجملة؛ لفعل النبي صلى الله عليه وسلم.','447','530','0');
INSERT INTO [t147663] VALUES (536,'- مسألة: (ويرفع) واقف بعرفة (يديه) ندبا، (ويكثر) الحاج (الدعاء) في عرفة، (ومما ورد): ما في حديث عبد الله بن عمرو رضي الله عنهما مرفوعا: «خير الدعاء دعاء يوم عرفة، وخير ما قلت أنا والنبيون من قبلي: لا إله إلا الله وحده لا شريك له، له الملك وله الحمد وهو على كل شيء قدير»، وفي لفظ: «كان أكثر دعاء رسول الله صلى الله عليه وسلم يوم عرفة: لا إله إلا الله وحده لا شريك له، له الملك وله الحمد، بيده الخير، وهو على كل شيء قدير» [أحمد: ٦٩٦١، والترمذي: ٣٥٨٥]، ويكثر الاستغفار والتضرع والخشوع، وإظهار الضعف والافتقار.','448','530','0');
INSERT INTO [t147663] VALUES (537,'- مسألة: (و) بداية (وقت الوقوف) بعرفة: (من فجر) يوم (عرفة)؛ لحديث عروة بن مضرس، وفيه: «وقد وقف بعرفة قبل ذلك ليلا أو نهارا، فقد أتم حجه، وقضى تفثه»، فقوله: «نهارا» يشمل ما قبل الزوال وما بعده.','449','530','0');
INSERT INTO [t147663] VALUES (538,'- مسألة: (ثم يدفع) الحاج مع الإمام أونائبه (بعد الغروب إلى مزدلفة)؛ لحديث جابر السابق، وفيه: «فلم يزل واقفا حتى غربت الشمس، وذهبت الصفرة قليلا، حتى غاب القرص، وأردف أسامة خلفه، ودفع رسول الله صلى الله عليه وسلم وقد شنق للقصواء الزمام، حتى إن رأسها ليصيب مورك رحله، ويقول بيده اليمنى: «أيها الناس، السكينة السكينة» كلما أتى حبلا من الحبال أرخى لها قليلا، حتى تصعد، حتى أتى المزدلفة».','449','530','0');
INSERT INTO [t147663] VALUES (539,'- مسألة: (ويجمع فيها) أي: في مزدلفة (بين العشاءين) أي: المغرب والعشاء (تأخيرا) إن وصلها وقت العشاء، فإن وصلها وقت المغرب فيجمع ولا يؤخرها؛ لحديث جابر السابق، وفيه: «حتى أتى المزدلفة، فصلى بها المغرب والعشاء بأذان واحد وإقامتين، ولم يسبح بينهما شيئا».','450','530','0');
INSERT INTO [t147663] VALUES (540,'- مسألة: (ويبيت بها) أي: في مزدلفة وجوبا؛ لحديث جابر السابق، وفيه: «ثم اضطجع رسول الله صلى الله عليه وسلم حتى طلع الفجر، وصلى الفجر حين تبين له الصبح، بأذان وإقامة، ثم ركب القصواء، حتى أتى المشعر الحرام»، وقال في حديث جابر الآخر: «لتأخذوا عني مناسككم»، ولحديث عروة بن مضرس، وفيه: «من شهد صلاتنا هذه، ووقف معنا حتى ندفع».','451','530','0');
INSERT INTO [t147663] VALUES (541,'- مسألة: (فإذا صلى) الحاج (الصبح) بغلس (أتى المشعر الحرام)، وهو جبل صغير بالمزدلفة سمي بذلك؛ لأنه من علامات الحج، (فرقيه) إن أمكنه، (و) إلا (وقف عنده، وحمد الله تعالى وهلل وكبر)، (ودعا بما ورد)، ولم نقف على ذكر مخصوص في هذا الموطن عن النبي صلى الله عليه وسلم ولا عن أصحابه، وعبارة الأصحاب: فيقول: (اللهم كما وقفتنا فيه، وأريتنا إياه، فوفقنا لذكرك كما هديتنا، واغفر لنا، وارحمنا كما وعدتنا بقولك، وقولك الحق: {فإذا أفضتم من عرفات فاذكروا الله، ولذلك قال: (وقرأ) قوله تعالى: ("فإذا أفضتم من عرفات" الآيتين [البقرة: ١٩٨ - ١٩٩]) وتمامهما: (فاذكروا الله عند','452','530','0');
INSERT INTO [t147663] VALUES (542,'- مسألة: (ويدعو) عند المشعر الحرام (حتى يسفر جدا)؛ لحديث جابر السابق، وفيه: «فلم يزل واقفا حتى أسفر جدا، فدفع قبل أن تطلع الشمس».','453','530','0');
INSERT INTO [t147663] VALUES (543,'- مسألة: (ثم يدفع) من المشعر الحرام قبل طلوع الشمس (إلى منى)؛ لحديث جابر السابق.','453','530','0');
INSERT INTO [t147663] VALUES (544,'- مسألة: يدفع الحاج من المشعر الحرام إلى منى بسكينة؛ لحديث ابن عباس رضي الله عنهما، وفيه: ثم أردف الفضل بن عباس من جمع إلى منى، وهو يقول: «أيها الناس إن البر ليس بإيجاف الخيل والإبل فعليكم بالسكينة» [أحمد: ٢٤٢٧، وأبو داود: ١٩٢٠]، (فإذا بلغ محسرا)، وهو واد بين مزدلفة ومنى، سمي بذلك؛ لأنه يحسر سالكه، (أسرع) قدر (رمية حجر)، وهي بمقدار خمسمائة وخمسة وأربعين ذراعا تقريبا، والذراع نصف متر تقريبا، فصار المجموع (٢٧٢.٥) متر، وذلك إن كان ماشيا، وإلا حرك','453','530','0');
INSERT INTO [t147663] VALUES (545,'- مسألة: (و) إذا دفع الحاج من مزدلفة (أخذ حصى الجمار)، فيأخذ (سبعين حصاة)؛ ليرمي بها جمرة العقبة بسبع حصيات، وأيام التشريق الثلاثة كل يوم إحدى وعشرين حصاة، فإن كان متعجلا تخلص من حصى اليوم الثالث.','454','530','0');
INSERT INTO [t147663] VALUES (546,'- مسألة: (و) كره (تكسيره) أي: الحصى؛ لئلا يطير إلى وجهه منه شيء يؤذيه.','455','530','0');
INSERT INTO [t147663] VALUES (547,'- مسألة: (ولا يسن غسله) أي: الحصى، قال أحمد: لم يبلغنا أن النبي صلى الله عليه وسلم فعله.','455','530','0');
INSERT INTO [t147663] VALUES (548,'- مسألة: إذا وصل منى (فـ) ـإنه (يرمي جمرة العقبة وحدها) دون الوسطى والصغرى، (بسبع) حصيات؛ واحدة بعد أخرى؛ لحديث جابر في صفة الحج، وفيه: «حتى أتى الجمرة التي عند الشجرة، فرماها بسبع حصيات، يكبر مع كل حصاة منها».','456','530','0');
INSERT INTO [t147663] VALUES (549,'- مسألة: (ثم ينحر) الهدي إن كان معه، واجبا كان أو تطوعا؛ لحديث جابر السابق، وفيه: «رمى من بطن الوادي، ثم انصرف إلى المنحر، فنحر ثلاثا وستين بيده، ثم أعطى عليا، فنحر ما غبر».','457','530','0');
INSERT INTO [t147663] VALUES (550,'- مسألة: (و) يجب على الحاج أن (يحلق أو يقصر) من جميع شعره، وهو نسك من المناسك يجب بتركه دم؛ لحديث ابن عمر رضي الله عنهما مرفوعا، وفيه: «وليقصر وليحلل» [البخاري: ١٦٩١، ومسلم: ١٢٢٧]، فلو لم يكن نسكا لم يتوقف الحل عليه.','457','530','0');
INSERT INTO [t147663] VALUES (551,'- مسألة: (ثم) إذا رمى وحلق أو قصر فـ (قد حل له كل شيء) حرم عليه بالإحرام (إلا النساء) وطئا، ومباشرة لشهوة، وعقد نكاح، وهو التحلل الأول؛ لحديث عائشة رضي الله عنها مرفوعا: «إذا رميتم وحلقتم، فقد حل','457','530','0');
INSERT INTO [t147663] VALUES (552,'فصل','458','474','0');
INSERT INTO [t147663] VALUES (553,'- مسألة: (ثم يفيض) الحاج (إلى مكة فيطوف طواف الزيارة) ويقال: طواف الإفاضة، (الذي هو ركن) بالإجماع؛ لأمر الله به بقوله: (وليطوفوا بالبيت العتيق) [الحج: ٢٩].','458','552','0');
INSERT INTO [t147663] VALUES (554,'- مسألة: (وسن) له بعد طواف الزيارة (أن يشرب من زمزم)؛ لحديث جابر السابق، وفيه: فأفاض إلى البيت، فصلى بمكة الظهر، فأتى بني عبد المطلب يسقون على زمزم، فقال: «انزعوا بني عبد المطلب، فلولا أن','459','552','0');
INSERT INTO [t147663] VALUES (555,'(فصل)','461','474','0');
INSERT INTO [t147663] VALUES (556,'- مسألة: (ثم يرجع) من مكة بعد الطواف والسعي (فيصلي ظهر يوم النحر بمنى)','461','555','0');
INSERT INTO [t147663] VALUES (557,'- مسألة: (و) يجب أن (يرمي الجمرات الثلاث) الصغرى، ثم الوسطى، ثم الكبرى اتفاقا','461','555','0');
INSERT INTO [t147663] VALUES (558,'- مسألة: (وطواف الوداع) ويسمى: «طواف الصدر»: (واجب، يفعله كل من أراد الخروج من مكة) إذا فرغ من جميع أموره؛ لقول ابن عباس رضي الله عنهما: «أمر الناس أن يكون آخر عهدهم بالبيت، إلا أنه خفف عن الحائض» [البخاري: ١٧٥٥، ومسلم: ١٣٢٨].','463','555','0');
INSERT INTO [t147663] VALUES (559,'- مسألة: (وسن دخوله البيت) أي: الكعبة، لما ورد عن ابن عمر رضي الله عنهما أنه قال: «دخل النبي صلى الله عليه وسلم وبلال، وأسامة بن زيد، وعثمان بن طلحة، وأمر بالباب فأغلق، فلبثوا فيه مليا، ثم فتح الباب، فقال عبد الله: فبادرت الناس فتلقيت رسول الله صلى الله عليه وسلم خارجا وبلال على إثره، فقلت لبلال: «هل صلى فيه رسول الله صلى الله عليه وسلم؟ » قال: «نعم»، قلت: «أين؟ » قال: «بين العمودين تلقاء وجهه»، قال: ونسيت أن أسأله: كم صلى؟ » [البخاري: ٥٠٤، ومسلم: ١٣٢٩]، فيدخلها (بلا خف، ولا نعل، ولا سلاح)؛ تعظيما لها.','465','555','0');
INSERT INTO [t147663] VALUES (560,'- مسألة: (و) إذا قضى الحاج نسكه (تستحب) له (زيارة قبر النبي صلى الله عليه وسلم، وقبري صاحبيه، رضي الله عنهما) أي: زيارة مسجده، أو زيارة مسجده وقبره معا؛ لعموم حديث بريدة رضي الله عنه: «نهيتكم عن زيارة القبور فزوروها» [مسلم: ١٩٧٧].','465','555','0');
INSERT INTO [t147663] VALUES (561,'- مسألة: (وصفة العمرة)','467','555','0');
INSERT INTO [t147663] VALUES (562,'- مسألة: (ولا بأس بها) أي: العمرة (في السنة مرارا)؛ لعموم حديث أبي هريرة رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: «العمرة إلى العمرة كفارة لما بينهما، والحج المبرور ليس له جزاء إلا الجنة» [البخاري: ١٧٧٣، مسلم: ١٣٤٩].','468','555','0');
INSERT INTO [t147663] VALUES (563,'- مسألة: (وهي) أي: العمرة (في غير أشهر الحج) أفضل منها في أشهر الحج، خروجا من خلاف من كرهها فيه، ولا تكره في أشهر الحج.','469','555','0');
INSERT INTO [t147663] VALUES (564,'- مسألة: (و) العمرة (في رمضان أفضل)؛ لحديث ابن عباس رضي الله عنهما: أن رسول الله صلى الله عليه وسلم قال: «فإذا جاء رمضان فاعتمري، فإن عمرة فيه تعدل حجة» [البخاري: ١٧٨٢، ومسلم: ١٢٥٦].','469','555','0');
INSERT INTO [t147663] VALUES (565,'(فصل)','470','474','0');
INSERT INTO [t147663] VALUES (566,'- مسألة: (أركان الحج أربعة)','470','565','0');
INSERT INTO [t147663] VALUES (567,'- مسألة: (وواجباته) أي: الحج (سبعة)','470','565','0');
INSERT INTO [t147663] VALUES (568,'- مسألة: (وأركان العمرة ثلاثة)','472','565','0');
INSERT INTO [t147663] VALUES (569,'- مسألة: (وواجباتها) أي: العمرة (شيئان)','472','565','0');
INSERT INTO [t147663] VALUES (570,'- مسألة: (والمسنون) من أفعال الحج وأقواله؛ (كالمبيت بمنى ليلة عرفة، وطواف القدوم، والرمل، والاضطباع) في موضعهما (ونحو ذلك)؛ كاستلام الركنين وتقبيل الحجر، وصعوده على الصفا والمروة، والتلبية والأذكار والدعاء في مواضعهما.','472','565','0');
INSERT INTO [t147663] VALUES (571,'- مسألة: (فمن ترك ركنا) غير الإحرام (لم يتم حجه إلا به)، وإن ترك الإحرام لم ينعقد نسكه؛ لحديث عمر رضي الله عنه مرفوعا: «إنما الأعمال','472','565','0');
INSERT INTO [t147663] VALUES (572,'- مسألة: (ومن ترك واجبا) عمدا أو سهوا أو جهلا أو لعذر، (فعليه دم) بتركه؛ لقول ابن عباس رضي الله عنهما: «من نسي من نسكه شيئا أو تركه فليهرق دما» [البيهقي ٨٩٢٥]، (وحجه صحيح).','473','565','0');
INSERT INTO [t147663] VALUES (573,'- مسألة: (ومن ترك مسنونا) من مسنونات الحج، (فلا شيء عليه).','473','565','0');
INSERT INTO [t147663] VALUES (574,'فصل في الفوات والإحصار','473','474','0');
INSERT INTO [t147663] VALUES (575,'- مسألة: (ومن فاته الوقوف بعرفة)، بأن طلع عليه فجر يوم النحر ولم يقف بعرفة؛ لقول جابر رضي الله عنه: «لا يفوت الحج حتى ينفجر الفجر من ليلة جمع» [البيهقي: ٩٨١٧]، ترتب عليه الأحكام التالية، ولو كان معذورا','473','574','0');
INSERT INTO [t147663] VALUES (576,'- مسألة: الإحصار أقسام، منها','475','574','0');
INSERT INTO [t147663] VALUES (577,'(فصل في الهدي والأضحية والعقيقة)','477','474','0');
INSERT INTO [t147663] VALUES (578,'- مسألة: (الهدي ما يهدى للحرم من نعم وغيره)، سمي بذلك؛ (لأنه يهدى إلى الله تعالى).','477','577','0');
INSERT INTO [t147663] VALUES (579,'- مسألة: (والأضحية ما يذبح من إبل، وبقر، وغنم أهلية، أيام النحر، بسبب العيد، تقربا إلى الله تعالى)، وهي مشروعة إجماعا، لقوله تعالى: {فصل لربك وانحر} [الكوثر: ٢]، قال ابن عباس رضي الله عنهما: (اذبح يوم النحر) [تفسير الطبري ٢٤/ ٦٥٤].','477','577','0');
INSERT INTO [t147663] VALUES (580,'- مسألة: (وهي) أي: الأضحية (سنة مؤكدة)؛ لحديث أم سلمة رضي الله عنها قالت: قال رسول الله صلى الله عليه وسلم: «من أراد أن يضحي فدخل العشر، فلا يأخذ من شعره ولا بشرته شيئا» [مسلم ١٩٧٧]، فعلقه على الإرادة، والواجب لا','477','577','0');
INSERT INTO [t147663] VALUES (581,'- مسألة: (والأفضل) في هدي وأضحية (إبل، فبقر) إن أخرج كاملا، (فغنم)؛ جذع ضأن، ثم ثني معز؛ لحديث أبي هريرة رضي الله عنه: أن رسول الله صلى الله عليه وسلم قال: «من اغتسل يوم الجمعة غسل الجنابة ثم راح، فكأنما قرب بدنة، ومن راح في الساعة الثانية، فكأنما قرب بقرة، ومن راح في الساعة الثالثة، فكأنما قرب كبشا أقرن» الحديث [البخاري: ٨٨١، ومسلم: ٨٥٠]','478','577','0');
INSERT INTO [t147663] VALUES (582,'- مسألة: (ولا تجزئ) أضحية (من غيرهن) أي: الإبل والبقر والغنم الأهلية اتفاقا؛ لعدم وروده، قال ابن القيم: (ولم يعرف عنه صلى الله عليه وسلم ولا عن الصحابة هدي، ولا أضحية، ولا عقيقة من غيرها).','478','577','0');
INSERT INTO [t147663] VALUES (583,'- مسألة: (وتجزئ شاة عن واحد وأهل بيته وعياله)؛ لحديث أبي أيوب رضي الله عنه: «كان الرجل في عهد النبي صلى الله عليه وسلم يضحي بالشاة عنه وعن أهل','478','577','0');
INSERT INTO [t147663] VALUES (584,'- مسألة: (و) تجزئ (بدنة أو بقرة عن سبعة)؛ لقول جابر رضي الله عنه: «خرجنا مع رسول الله صلى الله عليه وسلم مهلين بالحج، فأمرنا رسول الله صلى الله عليه وسلم أن نشترك في الإبل والبقر، كل سبعة منا في بدنة» [مسلم ١٣١٨].','479','577','0');
INSERT INTO [t147663] VALUES (585,'- مسألة: (ولا يجزئ) في الأضحية، وكذا دم تمتع ونحوه (إلا)','479','577','0');
INSERT INTO [t147663] VALUES (586,'- مسألة: العيوب المانعة من الإجزاء على قسمين','480','577','0');
INSERT INTO [t147663] VALUES (587,'- مسألة: (وسن) في (نحر إبل): أن تكون (قائمة، معقولة يدها اليسرى، بأن يطعنها) بالحربة أو نحوها (في الوهدة) التي (بين) أصل (العنق والصدر)؛ لقول عبد الرحمن بن سابط رضي الله عنه: «أن النبي صلى الله عليه وسلم وأصحابه كانوا ينحرون البدنة معقولة اليسرى قائمة على ما بقي من قوائمها» [أبو داود ١٧٦٧، وصححه الألباني]، وفي قوله تعالى: (فإذا وجبت جنوبها) [الحج: ٣٦] دليل على أنها تنحر قائمة.','482','577','0');
INSERT INTO [t147663] VALUES (588,'- مسألة: (ويسمي وجوبا حين يحرك يده بالفعل) أي: النحر أو الذبح؛ لحديث رافع بن خديج رضي الله عنه أن رسول الله صلى الله عليه وسلم قال: «ما أنهر الدم','482','577','0');
INSERT INTO [t147663] VALUES (589,'- مسألة: (ويكبر) الذابح استحبابا حين يحرك يده بالنحر أو الذبح، (ويقول: اللهم هذا منك ولك) استحبابا؛ لحديث جابر رضي الله عنه قال: ذبح النبي صلى الله عليه وسلم يوم الذبح كبشين أقرنين أملحين موجوءين، فلما وجههما قال: «إني وجهت وجهي للذي فطر السموات والأرض على ملة إبراهيم حنيفا، وما أنا من المشركين، إن صلاتي ونسكي ومحياي ومماتي لله رب العالمين لا شريك له، وبذلك أمرت وأنا من المسلمين، اللهم منك ولك، وعن محمد وأمته، باسم الله والله أكبر» ثم ذبح. [أبو داود ٢٧٩٥، وضعفه الألباني].','483','577','0');
INSERT INTO [t147663] VALUES (590,'- مسألة: (ووقت ذبح أضحية، وهدي نذر أو تطوع، ومتعة، وقران)','483','577','0');
INSERT INTO [t147663] VALUES (591,'- مسألة: (ووقت ذبح هدي واجب بفعل محظور من حينه) أي: من حين فعل المحظور؛ كالكفارة بالحنث. وكذا دم وجب لترك واجب في حج أو عمرة؛ فيدخل وقته من تركه.','485','577','0');
INSERT INTO [t147663] VALUES (592,'(فصل)','485','474','0');
INSERT INTO [t147663] VALUES (593,'- مسألة: (ويتعين هدي) بأمور','485','592','0');
INSERT INTO [t147663] VALUES (594,'- مسألة: (و) تتعين (أضحية بـ) ـقوله: (هذه أضحية)؛ لما تقدم في','485','592','0');
INSERT INTO [t147663] VALUES (595,'- مسألة: (ولا يجوز إعطاء الجازر أجرته منها) أي: من الأضحية، اتفاقا؛ لقول علي رضي الله عنه: «أمرني رسول الله صلى الله عليه وسلم أن أقوم على بدنه، وأن أتصدق بلحمها وجلودها وأجلتها، وأن لا أعطي الجزار منها» [البخاري ١٧١٦، ومسلم ١٣١٧].','486','592','0');
INSERT INTO [t147663] VALUES (596,'- مسألة: (ولا يباع جلدها، ولا شيء منها)، سواء كانت واجبة أو تطوعا؛ لحديث علي السابق، (بل ينتفع به) أي: بجلدها، أو يتصدق به استحبابا، قال في «الشرح»: (لا خلاف في جواز الانتفاع بجلودها وجلالها -وهو ما يطرح على ظهر البعير من كساء ونحوه-لأن الجلد جزء منها، فجاز للمضحي الانتفاع كاللحم).','486','592','0');
INSERT INTO [t147663] VALUES (597,'- مسألة: (وسن أن يأكل) من الهدي والأضحية، (و) أن (يهدي) منها، (و) أن (يتصدق) منها؛ (أثلاثا)، أي: يأكل هو وأهل بيته الثلث، ويهدي الثلث، ويتصدق بالثلث؛ قال الإمام أحمد: (نحن نذهب إلى','486','592','0');
INSERT INTO [t147663] VALUES (598,'- مسألة: ما يشرع ذبحه من الدماء ينقسم من حيث جواز الأكل منه إلى ثلاثة أقسام','487','592','0');
INSERT INTO [t147663] VALUES (599,'- مسألة: يجوز أن يأكل المضحي أكثر أضحيته؛ لإطلاق الأمر بالأكل والإطعام، (و) لا يجوز أن يأكلها كلها، بل (يجب أن يتصدق بما يقع عليه اسم اللحم)؛ لأنه يجب الصدقة ببعضها؛ لعموم قوله تعالى: (فكلوا منها وأطعموا البائس الفقير)، وهذا مطلق، فيتناول أقل ما يقع عليه اسم اللحم.','488','592','0');
INSERT INTO [t147663] VALUES (600,'- مسألة: (وإذا دخل العشر) أي: عشر ذي الحجة (حرم على من يضحي أو يضحى عنه أخذ شيء من شعره أو ظفره أو بشرته إلى الذبح)؛ لحديث أم سلمة رضي الله عنها قالت: قال رسول الله صلى الله عليه وسلم: «إذا دخلت العشر، وأراد أحدكم أن يضحي، فلا يمس من شعره وبشره شيئا»، وفي رواية: «فليمسك عن شعره وأظفاره» [مسلم ١٩٧٧].','489','592','0');
INSERT INTO [t147663] VALUES (601,'(فصل) في العقيقة','490','474','0');
INSERT INTO [t147663] VALUES (602,'- مسألة: (والعقيقة سنة مؤكدة في حق الأب)؛ لقول ابن عباس رضي الله عنهما: «أن رسول الله صلى الله عليه وسلم عق عن الحسن، والحسين كبشا كبشا» [أبو داود ٢٨٤١، والنسائي ٤٢١٩ بلفظ: «كبشين كبشين»].','490','601','0');
INSERT INTO [t147663] VALUES (603,'- مسألة: وقت ذبح العقيقة ينقسم إلى ثلاثة أقسام','491','601','0');
INSERT INTO [t147663] VALUES (604,'- مسألة: (وحكمها) أي: العقيقة (كأضحية فيما يجزئ) منها، (و) ما (يستحب، و) ما (يكره) أي: في أكثر أحكامها، كالأكل والهدية والصدقة، وما يجوز من الحيوان، وما يجتنبه من العيوب ونحو ذلك؛ لاشتراكهما في تعلق حق الفقراء بهما.','492','601','0');
INSERT INTO [t147663] VALUES (605,'- مسألة: (وإن اتفق وقت عقيقة وأضحية)؛ بأن يكون اليوم السابع من الولادة أو نحوه من أيام النحر؛ (أجزأت إحداهما عن الأخرى)؛ كما لو','492','601','0');
INSERT INTO [t147663] VALUES (606,'- مسألة: (وهو) أي: الجهاد (فرض كفاية)، إذا قام به من يكفي سقط عن الباقي، وإلا أثم الكل؛ لقوله تعالى: (وما كان المؤمنون لينفروا كافة) [التوبة: ١٢٢]؛ ولأن النبي صلى الله عليه وسلم كان يبعث السرايا ويقيم هو وأصحابه.','494','601','0');
INSERT INTO [t147663] VALUES (607,'(كتاب الجهاد)','494','0','0');
INSERT INTO [t147663] VALUES (608,'- مسألة: (وأفضل متطوع به) من العبادات هو (الجهاد) قال أحمد: لا أعلم شيئا من العمل بعد الفرائض أفضل من الجهاد؛ لحديث أبي سعيد قال: قيل يا رسول الله: أي الناس أفضل؟ قال: «مؤمن يجاهد في سبيل الله بنفسه وماله» [البخاري: ٢٧٨٦، ومسلم: ١٨٨٨].','496','607','0');
INSERT INTO [t147663] VALUES (609,'- مسألة: (وسن رباط، وهو: لزوم ثغر لجهاد)، والثغر هو: كل مكان يخيف أهله العدو ويخيفهم؛ لحديث سهل بن سعد رضي الله عنه: قال رسول الله صلى الله عليه وسلم «رباط يوم في سبيل الله خير من الدنيا وما عليها» [البخاري: ٢٨٩٢]، (ولو) كان الرباط (ساعة)؛ لإطلاق الأدلة.','496','607','0');
INSERT INTO [t147663] VALUES (610,'- مسألة: (ولا يتطوع به) أي: الجهاد (مدين لا وفاء له)، حالا كان الدين أو مؤجلا؛ لأن الجهاد يقصد منه الشهادة، فتفوت به النفس فيفوت الحق، (إلا) في حالات','497','607','0');
INSERT INTO [t147663] VALUES (611,'- مسألة: (ولا) يتطوع بالجهاد (من أحد أبويه حر) لا عبد؛ لعدم ولايته، أشبه المجنون، (مسلم) لا كافر، فلا يشترط إذنه حينئذ؛ لأن الكافر لا يسعى في مصلحة المسلمين، وكثير من الصحابة جاهد وأحد أبويه كافر ولم يرد عنهم استئذانهم، (إلا بإذنه) أي: الوالد الحر المسلم العاقل؛ لحديث عبد الله بن عمرو رضي الله عنهما قال: جاء رجل إلى النبي صلى الله عليه وسلم، فاستأذنه في الجهاد، فقال: «أحي والداك؟ »، قال: نعم، قال: «ففيهما فجاهد» [البخاري: ٣٠٠٤، ومسلم: ٢٥٤٩]، ولأن برهما فرض عين، والجهاد فرض كفاية، والأول مقدم.','498','607','0');
INSERT INTO [t147663] VALUES (612,'- مسألة: (ولا يحل للمسلمين الفرار من مثليهم؛ ولو) كان الفار (واحدا من اثنين) كافرين إلا في ثلاثة أحوال','498','607','0');
INSERT INTO [t147663] VALUES (613,'- مسألة: (ولا يجوز قتل)','499','607','0');
INSERT INTO [t147663] VALUES (614,'- مسألة: (ويخير الإمام في أسير، حر، مقاتل بين) أربعة أمور','500','607','0');
INSERT INTO [t147663] VALUES (615,'(فصل) فيما يلزم الإمام في الغزو','501','607','0');
INSERT INTO [t147663] VALUES (616,'- مسألة: (ويلزم) كلا من (الإمام أو الجيش إخلاص النية لله تعالى في الطاعات) كلها من جهاد وغيره؛ لقوله تعالى: {وما أمروا إلا ليعبدوا الله مخلصين له الدين} [البينة: ٥].','501','615','0');
INSERT INTO [t147663] VALUES (617,'- مسألة: (وعليه) أي: الإمام (عند المسير) أي: مسير الجيش','501','615','0');
INSERT INTO [t147663] VALUES (618,'- مسألة: (ويحرم استعانة بكافر) في الغزو، واختاره شيخ الإسلام؛ لحديث عائشة رضي الله عنها قالت: خرج رسول الله صلى الله عليه وسلم قبل بدر، فلما كان بحرة الوبرة أدركه رجل قد كان يذكر منه جرأة ونجدة، ففرح أصحاب رسول الله صلى الله عليه وسلم حين رأوه، فلما أدركه قال لرسول الله صلى الله عليه وسلم: جئت لأتبعك وأصيب معك، قال له رسول الله صلى الله عليه وسلم: «تؤمن بالله ورسوله؟ » قال: لا، قال: «فارجع، فلن أستعين بمشرك» [مسلم: ١٨١٧]، (إلا لضرورة) ككون الكفار أكثر عددا ونحوه، بشرط أن يكون حسن الرأي في المسلمين، وأن يكون مأمونا، لما ورد من دخول النبي صلى الله عليه وسلم في جوار المطعم بن عدي بعد عودته من الطائف [سيرة ابن هشام ٢/ ٢٢٥]، واستعانته بعبد الله بن أريقط في الهجرة [البخاري: ٢٢٦٣].','503','615','0');
INSERT INTO [t147663] VALUES (619,'- مسألة: (ويمنع) الإمام (جيشه من) أمور، منها','503','615','0');
INSERT INTO [t147663] VALUES (620,'- مسألة: (ويعد) الإمام (الصابر) في القتال (بأجر ونفل)؛ ترغيبا له فيه.','503','615','0');
INSERT INTO [t147663] VALUES (621,'- مسألة: (ويشاور) الإمام (ذا رأي)؛ لقوله تعالى: {وشاورهم في الأمر} [آل عمران: ١٥٩].','504','615','0');
INSERT INTO [t147663] VALUES (622,'- مسألة: (ومن قتل قتيلا) من الكفار، أو أثخنه بالجراح (في حالة الحرب) لا إن انهزم الكفار كلهم فأدرك إنسانا منهزما فقتله؛ (فله) أي: المسلم (سلبه) قال في «المبدع»: (بغير خلاف نعلمه)، سواء شرطه له الإمام أم لا؛ لحديث أنس رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم: «من قتل كافرا فله سلبه» [أحمد: ١٢١٣١، وأبو داود: ٢٧١٨].','504','615','0');
INSERT INTO [t147663] VALUES (623,'(فصل) في الغنيمة وغيرها','505','607','0');
INSERT INTO [t147663] VALUES (624,'- مسألة: (وتملك الغنيمة) وهي: ما أخذ من مال حربي قهرا بقتال وما ألحق به؛ كفداء الأسرى، مشتقة من الغنم وهو الربح، (بالاستيلاء عليها في دار الحرب)، ولو لم يقسمها أو يحوزها إلى بلاد المسلمين؛ لأنها مال مباح، فملكت بالاستيلاء عليها كسائر المباحات.','505','623','0');
INSERT INTO [t147663] VALUES (625,'- مسألة: (وشرط فيمن يسهم له) من الغنيمة (أربعة شروط)','509','623','0');
INSERT INTO [t147663] VALUES (626,'- مسألة: (وإذا فتحوا أرضا بالسيف) أي: قهرا وغلبة؛ (خير الإمام) تخيير مصلحة؛ لأنه نائب المسلمين فلا يفعل إلا ما فيه صلاحهم، (بين) أمرين','510','623','0');
INSERT INTO [t147663] VALUES (627,'مسألة: (وما أخذ من مال مشرك) أي: كافر بحق الكفر، لا ما أخذ من ذمي غصبا أو ببيع ونحوه، (بلا قتال)، ليخرج الغنيمة، (كجزية، وخراج، وعشر تجارة من الحربي) الذي اتجر إلينا، (ونصفه من الذمي) الذي اتجر إلى غير بلده ثم عاد، (وما تركوه فزعا) منا، (أو) تخلف (عن ميت ولا وارث له، فيء، ومصرفه في مصالح المسلمين)؛ لقوله تعالى: (ما أفاء الله على رسوله من أهل القرى فلله وللرسول، ولذي القربى، واليتامى والمساكين، وابن السبيل ... ) الآيتين [الحشر: ٧ - ٨]، قال عمر رضي الله عنه: «استوعبت هذه الآية الناس فلم يبق أحد من المسلمين إلا له فيها حق، إلا بعض من تملكون من أرقائكم» [أبو داود ٢٩٦٦، والنسائي ٤١٩٥].','511','623','0');
INSERT INTO [t147663] VALUES (628,'(فصل) في عقد الذمة وأحكامها','512','607','0');
INSERT INTO [t147663] VALUES (629,'- مسألة: (و) لا (ويجوز) أي: يصح (عقد الذمة) إلا بشرطين','512','628','0');
INSERT INTO [t147663] VALUES (630,'- مسألة: (ويجب) على الإمام عقد الذمة (إن أمن مكرهم)؛ لحديث عبادة بن الصامت رضي الله عنه مرفوعا: «لا ضرر ولا ضرار» [أحمد: ٢٢٧٧٨، وابن ماجه: ٢٣٤٠]، (والتزموا لنا بأربعة أحكام)','513','628','0');
INSERT INTO [t147663] VALUES (631,'- مسألة: (ولا تؤخذ الجزية) إلا مـ (ـمن) توفرت في خمسة شروط','514','628','0');
INSERT INTO [t147663] VALUES (632,'- مسألة: (ويلزمهم) أي: أهل الذمة (التمييز عن المسلمين)، فيشترطه الإمام عليهم، ويكون التمييز في أمور؛ كاللباس، والكلام، وركوب الدواب، والكنى، غير ذلك؛ لكتاب عبد الرحمن بن غنم إلى عمر رضي الله عنه [أحكام أهل الملل للخلال: ١٠٠٠].','515','628','0');
INSERT INTO [t147663] VALUES (633,'- مسألة: (ويمنعون) أي: أهل الذمة (من)','515','628','0');
INSERT INTO [t147663] VALUES (634,'- مسألة: (و) يمنع أهل الذمة (من إحداث كنائس)، وبيع، ومجتمع للصلاة فيه، في شيء من أرض المسلمين، سواء مصره المسلمون؛ كالبصرة، أو فتح عنوة؛ كمصر والشام؛ لما ورد عن ابن عباس رضي الله عنهما أنه','516','628','0');
INSERT INTO [t147663] VALUES (635,'- مسألة: (و) يمنعون (من إظهار منكر)؛ كنكاح محرم، (و) من إظهار (عيد، و) إظهار (صليب، و) إظهار (أكل وشرب نهار رمضان، و) إظهار (خمر، وخنزير)؛ لأنه يؤذينا، (و) يمنعون من (رفع صوت على ميت، و) من (قراءة قرآن، و) من ضرب (ناقوس، وجهر بكتابهم)؛ لأن في كتاب ابن غنم لعمر: «وأن لا نضرب ناقوسا إلا ضربا خفيفا في جوف كنائسنا»، (و) يمنعون من (شراء مصحف وفقه وحديث)؛ لأنه يتضمن ابتذال ذلك بأيديهم.','517','628','0');
INSERT INTO [t147663] VALUES (636,'- مسألة: (و) يجب (على الإمام حفظهم) أي: أهل الذمة، (ومنع من يؤذيهم) من مسلم، وذمي، وحربي، لأنهم إنما بذلوا الجزية لحفظهم وحفظ أموالهم.','517','628','0');
INSERT INTO [t147663] VALUES (637,'(فصل) في نقض العهد وما يتعلق به','518','607','0');
INSERT INTO [t147663] VALUES (638,'- مسألة: (و) ينتقض عهد أهل الذمة بأمور، منها','518','637','0');
