INSERT INTO [t147811] VALUES (36,'٢٠ - باب قول الله - عز وجل -: {ياأيها الناس اتقوا ربكم إن زلزلة الساعة شيء عظيم','248','0','0');
INSERT INTO [t147811] VALUES (37,'٢١ - باب قول الله - عز وجل -: {يوم يجمع الله الرسل فيقول ماذا أجبتم قالوا لا علم لنا إنك أنت علام الغيوب (١٠٩)}','264','0','0');
INSERT INTO [t147811] VALUES (38,'٢٢ - باب قول الله - عز وجل -: {وكل إنسان ألزمناه طائره في عنقه ونخرج له يوم القيامة كتابا يلقاه منشورا (١٣) اقرأ كتابك كفى بنفسك اليوم عليك حسيبا (١٤)} [الإسراء].','269','0','0');
