INSERT INTO [t147811] VALUES (45,'٢٩ - باب إثبات الشفاعة للمصطفى - صلى الله عليه وسلم -، وقول الله - عز وجل -','329','0','0');
INSERT INTO [t147811] VALUES (46,'٣٠ - باب شفاعة المصطفى - صلى الله عليه وسلم - لأهل الجمع يوم القيامة بعد طول القيام ليريحهم الله  ́ من مكانهم ويحاسبهم','344','0','0');
INSERT INTO [t147811] VALUES (47,'٣١ - باب إثبات النوع الثاني من الشفاعة لغير النبي - صلى الله عليه وسلم -','356','0','0');
INSERT INTO [t147811] VALUES (48,'٣٢ - باب من يشفع له رسول الله - صلى الله عليه وسلم - أو غيره من ملائكة الله تعالي، ورسله وعباده، ومن يخرجه الله من النار برحمته','365','0','0');
INSERT INTO [t147811] VALUES (49,'٣٣ - باب قوله - عز وجل -: {ولا يشفعون إلا لمن ارتضى وهم من خشيته مشفقون (٢٨)} [الأنبياء]. مع سائر ما يحتج به من أنكر الشفاعة','397','0','0');
INSERT INTO [t147811] VALUES (50,'٣٤ - باب قول الله - عز وجل -: {إن الله لا يغفر أن يشرك به ويغفر ما دون ذلك لمن يشاء}','411','0','0');
INSERT INTO [t147811] VALUES (51,'٣٥ - باب قول الله - عز وجل -: {ثم أورثنا الكتاب الذين اصطفينا من عبادنا فمنهم ظالم لنفسه ومنهم مقتصد ومنهم سابق بالخيرات بإذن الله}','434','0','0');
INSERT INTO [t147811] VALUES (52,'٣٦ - باب قول الله - عز وجل -: {ربما يود الذين كفروا لو كانوا مسلمين}','442','0','0');
INSERT INTO [t147811] VALUES (53,'٣٧ - باب ما جاء في المؤمن يفدى بالكافر، فيقال هذا فداؤك من النار، والكافر لا يؤخذ منه فدية، ولا تنفعه شفاعة','448','0','0');
