INSERT INTO [t151017] VALUES (1,'مقدمة المصنف','5','0','0');
INSERT INTO [t151017] VALUES (2,'(١) معلقة امرئ القيس','7','0','0');
INSERT INTO [t151017] VALUES (3,'معلقة امرئ القيس','12','2','0');
INSERT INTO [t151017] VALUES (4,'نسبه','12','2','0');
INSERT INTO [t151017] VALUES (5,'أمرؤ القيس بن حجر بن الحارث الملك، بن عمرو المقصور -الذي اقتصر على ملك أبيه- بن حجر بن آكل المرار بن عمرو بن معاوية بن الحارث بن معاوية بن مرتع، وقال قوم: ابن معاوية بن ثور بن مرتع، وإنما سمي مرتعا لأنه كان من أتاه من قوه رتعه، أي جعل له مرتعا لماش','12','2','0');
INSERT INTO [t151017] VALUES (6,'طبقته في الشعراء','13','2','0');
INSERT INTO [t151017] VALUES (7,'حال أمرئ القيس في ريعان شبابه','13','2','0');
INSERT INTO [t151017] VALUES (8,'مقتل حجر والد أمرئ القيس','14','2','0');
INSERT INTO [t151017] VALUES (9,'خبر أمرئ القيس بعد مقتل أبيه','14','2','0');
INSERT INTO [t151017] VALUES (10,'خبره مع بني أسد','16','2','0');
INSERT INTO [t151017] VALUES (11,'مطاردة المنذر له وخبر موته','17','2','0');
INSERT INTO [t151017] VALUES (12,'شيء من سيرته','18','2','0');
INSERT INTO [t151017] VALUES (13,'تنبيه','21','2','0');
INSERT INTO [t151017] VALUES (14,'١ - قفا نبك من ذكرى حبيب ومنزل ... بسقط اللوى بين الدخول فحومل','22','2','0');
INSERT INTO [t151017] VALUES (15,'٢ - فتوضح فالمقراة لم يعف رسمها ... لما نسجتها من جنوب وشمأل','24','2','0');
INSERT INTO [t151017] VALUES (16,'٣ - رخاء تسح الريح في جنباتها ... كساها الصبا سحق الملاء المذيل','28','2','0');
INSERT INTO [t151017] VALUES (17,'٤ - ترى بعر الأرام في عرصاتها ... وقيعانها كأنه حب فلفل','29','2','0');
INSERT INTO [t151017] VALUES (18,'٥ - كأني غداة البين يوم تحملوا ... لدى سمرات الحي ناقف حنظل','31','2','0');
INSERT INTO [t151017] VALUES (19,'٦ - وقوفا بها صحبي علي مطيهم ... يقولون: لا تهلك أسى وتجمل','33','2','0');
INSERT INTO [t151017] VALUES (20,'٧ - فدع عنك شيئا، قد مضى لسبيله ... ولكن على ما غالك اليوم أقبل','36','2','0');
INSERT INTO [t151017] VALUES (21,'٨ - وقفت بها حتى إذا ما ترددت ... عماية محزون بشوق موكل','39','2','0');
INSERT INTO [t151017] VALUES (22,'٩ - وإن شفائي عبرة إن سفحتها ... وهل عند رسم دارس من معول؟','40','2','0');
INSERT INTO [t151017] VALUES (23,'١٠ - كدأبك من أم الحويرث قبلها ... وجارتها أم الرباب بمأسل','42','2','0');
INSERT INTO [t151017] VALUES (24,'١١ - إذا قامتا تضوع المسك منهما ... نسيم الصبا جاءت بريا القرنفل','43','2','0');
INSERT INTO [t151017] VALUES (25,'١٢ - ففاضت دموع العين مني صبابة ... على النحر حتى بل دمعي محملي','44','2','0');
INSERT INTO [t151017] VALUES (26,'١٣ - ألا رب يوم لك منهن صالح ... ولا سيما يوم بدارة جلجل','45','2','0');
INSERT INTO [t151017] VALUES (27,'١٤ - ويوم عقرت للعذارى مطيتي ... فيا عجبا من رحلها المتحمل','48','2','0');
INSERT INTO [t151017] VALUES (28,'١٥ - ويا عجبا من حلها بعد رحلها ... ويا عجبا للجازر المتبذل','50','2','0');
INSERT INTO [t151017] VALUES (29,'١٦ - فظل العذارى يرتمين بلحمها ... وشحم كهداب الدمقس المفتل','51','2','0');
INSERT INTO [t151017] VALUES (30,'١٧ - تدار علينا بالسديف صحافنا ... ويؤتى إلينا بالعبيط المثمل','52','2','0');
INSERT INTO [t151017] VALUES (31,'١٨ - ويوم دخلت الخدر خدر عنيزة ... فقالت: لك الويلات، إنك مرجلي','53','2','0');
INSERT INTO [t151017] VALUES (32,'١٩ - تقول، وقد مال الغبيط بنا معا ... عقرت بعيري، يا امرأ القيس، فانزل','55','2','0');
INSERT INTO [t151017] VALUES (33,'٢٠ - فقلت لها سيري وأرخي زمامه ... ولا تبعديني من جناك المعلل','58','2','0');
INSERT INTO [t151017] VALUES (34,'٢١ - دعي البكر، لا ترثي له من ردافنا ... وهاتي أذيقينا جناة القرنفل','59','2','0');
INSERT INTO [t151017] VALUES (35,'٢٢ - بثغر كمثل الأحوان منور ... نقي الثنايا أشنب غير أثعل','61','2','0');
INSERT INTO [t151017] VALUES (36,'٢٣ - فمثلك حبلى قد طرقت ومرضع ... فألهيتها عن ذي تمائم محول','62','2','0');
INSERT INTO [t151017] VALUES (37,'٢٤ - إذا ما بكى من خلفها انصرفت له ... بشق وتحتي شقها لم يحول','65','2','0');
INSERT INTO [t151017] VALUES (38,'٢٥ - ويوما على ظهر الكثيب تعذرت ... علي، وآلت حلفة لم تحلل','66','2','0');
INSERT INTO [t151017] VALUES (39,'٢٦ - أفاطم مهلا بعض هذا التدلل ... وإن كنت قد أزمعت صرمي فأجملي','68','2','0');
INSERT INTO [t151017] VALUES (40,'٢٧ - وإن كنت قد ساءتك مني خليقة ... فسلي ثيابي من ثيابك تنسل','70','2','0');
INSERT INTO [t151017] VALUES (41,'٢٨ - أغرك مني أن حبك قاتلي ... وأنك مهما تأمري القلب يفعل؟','73','2','0');
INSERT INTO [t151017] VALUES (42,'٢٩ - وأنك قسمت الفؤاد، فنصفه ... قتيل، ونصف بالحديد مكبل','74','2','0');
INSERT INTO [t151017] VALUES (43,'٣٠ - وما ذرفت عيناك إلا لتضربي ... بسهميك في أعشار قلب مقتل','76','2','0');
INSERT INTO [t151017] VALUES (44,'٣١ - وبيضة خدر لا يرام خباؤها ... تمتعت من لهو بها غير معجل','77','2','0');
INSERT INTO [t151017] VALUES (45,'٣٢ - تجاوزت أحراسا إليها ومعشرا ... على حراصا لو يسرون مقتلي','78','2','0');
INSERT INTO [t151017] VALUES (46,'٣٣ - إذا ما الثريا في السماء تعرضت ... تعرض أثناء الوشاح المفصل','80','2','0');
INSERT INTO [t151017] VALUES (47,'٣٤ - فجئت، وقد نضت لنوم ثيابها ... لدي الستر إلا لبسة المتفضل','81','2','0');
INSERT INTO [t151017] VALUES (48,'٣٥ - فقالت: يمين الله، مالك حيلة ... وما إن أري عنك الغواية تنجلي','83','2','0');
INSERT INTO [t151017] VALUES (49,'٣٦ - خرجت بها أمشي تجر وراءنا ... على أثرينا ذيل مرط مرحل','84','2','0');
INSERT INTO [t151017] VALUES (50,'٣٧ - فلما أجزنا ساحة الحي وانتحى ... بنا بطن خبت ذي قفاف عقنقل','85','2','0');
INSERT INTO [t151017] VALUES (51,'٣٨ - هصرت بفودي رأسها فتمايلت ... على هضيم الكشح ريا المخلخل','87','2','0');
INSERT INTO [t151017] VALUES (52,'٣٩ - إذا التفتت نحوي تضوع ريحها ... نسيم الصبا جاءت بريا القرنفل','88','2','0');
INSERT INTO [t151017] VALUES (53,'٤٠ - إذا قلت: هاتي نوليني تمايلت ... على هضيم الكشح ريا المخلخل','90','2','0');
INSERT INTO [t151017] VALUES (54,'٤١ - مهفهفة بيضاء غير مفاضة ... ترائبها مصقولة كالسجنجل','91','2','0');
INSERT INTO [t151017] VALUES (55,'٤٢ - كبكر المقاناة البياض بصفرة ... غذاها نمير الماء غير محلل','92','2','0');
INSERT INTO [t151017] VALUES (56,'٤٣ - تصد وتبدي عن أسيل وتتقي ... بناظرة من وحش وجرة مطفل','94','2','0');
INSERT INTO [t151017] VALUES (57,'٤٤ - وجيد كجيد الريم ليس بفاحش ... إذا هي نصته ولا بمعطل','95','2','0');
INSERT INTO [t151017] VALUES (58,'٤٥ - وفرع يزين المتن أسود فاحم ... أثيث كقنو النخلة المتعثكل','97','2','0');
INSERT INTO [t151017] VALUES (59,'٤٦ - غدائره مستشزرات إلى العلا ... تضل العقاص في مثنى ومرسل','98','2','0');
INSERT INTO [t151017] VALUES (60,'٤٧ - وكشح لطيف كالجديل مخصر ... وساق كأنبوب السقي المذلل','99','2','0');
INSERT INTO [t151017] VALUES (61,'٤٨ - ويضحي فتيت المسك فوق فراشها ... نؤوم الضحى، لم تنتطق عن تفضل','100','2','0');
INSERT INTO [t151017] VALUES (62,'٤٩ - وتعطو برخص غير شثن كأنه ... أساريع ظبي أو مساويك إسحل','101','2','0');
INSERT INTO [t151017] VALUES (63,'٥٠ - تضيء الظلام العشاء كأنها ... منارة ممسى راهب متبل','102','2','0');
INSERT INTO [t151017] VALUES (64,'٥٠ - تضيء الظلام العشاء كأنها ... منارة ممسى راهب متبل','102','2','0');
INSERT INTO [t151017] VALUES (65,'٥١ - إلى مثلها يرنو الحليم صبابة ... إذا ما استكبرت بين درع ومجول','103','2','0');
INSERT INTO [t151017] VALUES (66,'٥٢ - تسلت عمايات الرجال عن الصبا ... وليس فؤادي عن هواك بمنسل','104','2','0');
INSERT INTO [t151017] VALUES (67,'٥٣ - ألا رب خصم ألوى رددته ... نصيح على تعذاله غير مؤتلي','106','2','0');
INSERT INTO [t151017] VALUES (68,'٥٤ - وليل كموج البحر أرخى سدوله ... علي بأنواع الهموم ليبتلي','107','2','0');
INSERT INTO [t151017] VALUES (69,'٥٥ - فقلت له تمطى بصلبه ... وأردف أعجازا، وناء بكلكل','109','2','0');
INSERT INTO [t151017] VALUES (70,'٥٦ - ألا أيها الليل الطويل، ألا انجلي ... بصبح، وما الإصباح منك بأمثل','111','2','0');
INSERT INTO [t151017] VALUES (71,'٥٧ - فيا من ليل كان نجومه ... بكل مغار القتل شدت بيذبل','113','2','0');
INSERT INTO [t151017] VALUES (72,'٥٨ - كأن الثريا علقت في مصامها ... بأمراس كتان إلى صم جندل','115','2','0');
INSERT INTO [t151017] VALUES (73,'٥٩ - وقربة أقوام جعلت عصامها ... على كاهل مني ذلول مرحل','116','2','0');
INSERT INTO [t151017] VALUES (74,'٦٠ - وواد كجوف العير قفر قطعته ... به الذئب يعوي كالخليع المعيل','117','2','0');
INSERT INTO [t151017] VALUES (75,'٦١ - فقلت له عوى إن شأننا ... قليل الغنى إن كنت لما تمول','119','2','0');
INSERT INTO [t151017] VALUES (76,'٦٢ - كلانا إذا ما نال شيئا أفاته ... ومن يحترث حرثي وحرثك يهزل','121','2','0');
INSERT INTO [t151017] VALUES (77,'٦٣ - وقد اغتدي والطير في وكناتها ... بمنجرد، قيد الأوابد، هيكل','123','2','0');
INSERT INTO [t151017] VALUES (78,'٦٤ - مكر، مفر، مقبل، مدبر، معا ... كجلمود صخر حطه السيل من عل','124','2','0');
INSERT INTO [t151017] VALUES (79,'٦٥ - كميت يزل اللبد عن حال متنه ... كما زلت الصفواء بالمتنزل','126','2','0');
INSERT INTO [t151017] VALUES (80,'٦٦ - على الذبل جياش كان اهتزامه ... إذا جاش فيه حمية غلى مرجل','127','2','0');
INSERT INTO [t151017] VALUES (81,'٦٧ - مسح إذا ما السابحات على الونى ... أثرن الغبار بالكديد المركل','128','2','0');
INSERT INTO [t151017] VALUES (82,'٦٩ - درير كحذروف الوليد أمره ... تتابع كفيه بخيط موصل','131','2','0');
INSERT INTO [t151017] VALUES (83,'٧٠ - له أيطلا ظبي، وساقا نعامة ... وإرخاء سرحان وتقريب تنفل','132','2','0');
INSERT INTO [t151017] VALUES (84,'٧١ - ضليع إذا استدبرته سد فرجه ... بضاف فويق الأرض ليس بأعزل','133','2','0');
INSERT INTO [t151017] VALUES (85,'٧٢ - كأن على المتنين منه إذا انتحي ... مداك عروس، أو صلابة حنظل','134','2','0');
INSERT INTO [t151017] VALUES (86,'٧٣ - كأن دماء الهاديات بنحره ... عصارة حناء بشيب مرجل','136','2','0');
INSERT INTO [t151017] VALUES (87,'٧٤ - فعن لنا سرب، كأن نعاجه ... عذاري دوار في ملاء مذيل','137','2','0');
INSERT INTO [t151017] VALUES (88,'٧٥ - فادبرن كالجزع المفصل بينه ... بجيد معم في العشيرة مخول','138','2','0');
INSERT INTO [t151017] VALUES (89,'٧٦ - فألحقنا بالهاديات، ودونه ... جواحرها في صرة لم تزيل','139','2','0');
INSERT INTO [t151017] VALUES (90,'٧٧ - فعادى عداء بين ثور ونعجة ... دراكا، ولم ينضح بماء، فيغسل','140','2','0');
INSERT INTO [t151017] VALUES (91,'٧٨ - فظل طهاه اللحم من بين منضح ... صفيف شواء، أو قدير معجل','142','2','0');
INSERT INTO [t151017] VALUES (92,'٧٩ - ورحنا يكاد الطر يقصر دونه ... متي ما ترق العين فيه تسهل','143','2','0');
INSERT INTO [t151017] VALUES (93,'٨٠ - فبات عليه سرجه ولجامه ... وبات بعيني قائما غير مرسل','145','2','0');
INSERT INTO [t151017] VALUES (94,'٨١ - أصاح تري برقا أريك وميضة ... كلمع اليدين في حبي مكلل؟','146','2','0');
INSERT INTO [t151017] VALUES (95,'٨٢ - يضئ سناه، أو مصابيح راهب ... أمال السليط بالذبال المفتل','148','2','0');
INSERT INTO [t151017] VALUES (96,'٨٣ - قعدت له وصحبتي بين ضارج ... وبين العذيب بعدما متأملي','149','2','0');
INSERT INTO [t151017] VALUES (97,'٨٤ - علا قطنا بالشيم أيمن صوبه ... وأيسره على الستار، فيذبل','150','2','0');
INSERT INTO [t151017] VALUES (98,'٨٥ - فأضحى يسح الماء حول كتيفة ... يكب على الأذقان دوح الكنهبل','151','2','0');
INSERT INTO [t151017] VALUES (99,'٨٦ - ومر على القنان من نفيانه ... فأنزل منه العصم من كل منزل','153','2','0');
INSERT INTO [t151017] VALUES (100,'٨٧ - وتيماء لم يترك بها جذع نخلة ... ولا أجما إلا مشيدا بجندل','154','2','0');
INSERT INTO [t151017] VALUES (101,'٨٨ - كأن ثبيرا في عرانين وبله ... كبير أناس في بجاد مزمل','155','2','0');
INSERT INTO [t151017] VALUES (102,'٨٩ - كأن ذرى رأس المجيمر غدوة ... من السيل والاغثاء فلكة مغزل','156','2','0');
INSERT INTO [t151017] VALUES (103,'٩٠ - وألقى بصحراء الغبيط بعاعه ... نزول اليماني، ذي العياب المحمل','157','2','0');
INSERT INTO [t151017] VALUES (104,'٩١ - كأن مكاكي الجواء غدية ... صبحن سلافا من رحيق مفلفل','159','2','0');
INSERT INTO [t151017] VALUES (105,'٩٢ - كأن السباع فيه غرقي عشية ... بأرجائه القصوى أنابيش عنصل','160','2','0');
INSERT INTO [t151017] VALUES (106,'(٢) معلقة طرفة بن العبد','162','0','0');
INSERT INTO [t151017] VALUES (107,'فهرست أبيات معلقة طرفة بن العبد','163','106','0');
INSERT INTO [t151017] VALUES (108,'معلقة طرفة بن العبد','168','106','0');
INSERT INTO [t151017] VALUES (109,'١ - لخولة أطلال ببرقة ثمهد ... تلوح كباقي الوشم في ظاهر اليد','171','106','0');
INSERT INTO [t151017] VALUES (110,'٢ - بروضة دعمي، فأكناف حائل ... ظللت لها أبكي، وأبكي إلى الغد','173','106','0');
INSERT INTO [t151017] VALUES (111,'٣ - وقوفا بها صحبي علي مطيهم ... يقولون: لا تهلك أسى وتجلد','174','106','0');
INSERT INTO [t151017] VALUES (112,'٤ - كأن حدوج المالكية غدوة ... خلايا سفين بالنواصف من دد','174','106','0');
INSERT INTO [t151017] VALUES (113,'٥ - عدولية، أو من سفين ابن يامن ... يجور بها الملاح طورا، ويهتدي','175','106','0');
INSERT INTO [t151017] VALUES (114,'٦ - يشق حباب الماء حيزومها بها ... كما قسم الترب المفايل باليد','176','106','0');
INSERT INTO [t151017] VALUES (115,'٧ - وفي الحي أحوى ينفض المردشادن ... مظاهر سمطي لؤلؤ وزبرجد','177','106','0');
INSERT INTO [t151017] VALUES (116,'٨ - خذول تراعي ربربا بخميلة ... تناول أطراف البرير وترتدي','178','106','0');
INSERT INTO [t151017] VALUES (117,'٩ - وتبسم عن ألمى، كأن منورا ... تخلل حر الرمل دعص له ندي','180','106','0');
INSERT INTO [t151017] VALUES (118,'١٠ - سقته إياة الشمس إلا لثاته ... أسف، ولم تكدم عليه بإثمد','181','106','0');
INSERT INTO [t151017] VALUES (119,'١١ - ووجه كأن الشمس ألقت رداءها ... عليه نقي اللون، لم يتخدد','183','106','0');
INSERT INTO [t151017] VALUES (120,'١٢ - وإني لأمضي الهم عند احتضاره ... بعوجاء مرقال، تروح وتعتدي','184','106','0');
INSERT INTO [t151017] VALUES (121,'١٣ - أمون، كألواح الإران نسأتها ... على لاحب، كأنه ظهر برجد','186','106','0');
INSERT INTO [t151017] VALUES (122,'١٤ - جمالية وجناء، تزدي كأنها ... سفنجة تبري لأزعر أربد','187','106','0');
INSERT INTO [t151017] VALUES (123,'١٥ - تباري عتاقا ناجيات، وأتبعت ... وظيفا وظيفأ فوق مور معبد','188','106','0');
INSERT INTO [t151017] VALUES (124,'١٦ - تربعت القفين في الشول ترتعي ... حدائق مولي الأسرة أغيد','189','106','0');
INSERT INTO [t151017] VALUES (125,'١٧ - تريع إلى صوت المهيب، وتتقي ... بذي خصل، روعات أكلف ملبد','191','106','0');
INSERT INTO [t151017] VALUES (126,'١٨ - كأن جناحي مضرحي تكنفا ... حفافيه شكا في العسيب بمسرد','192','106','0');
INSERT INTO [t151017] VALUES (127,'١٩ - فطورا به خلف الزميل، وتارة ... على حشف كالشن ذاو مجدد','193','106','0');
INSERT INTO [t151017] VALUES (128,'٢٠ - لها فخذان أكمل النحض فيهما ... كأنهما بابا منيف ممرد','194','106','0');
INSERT INTO [t151017] VALUES (129,'٢١ - وطي محال كالحني خلوفه ... وأجرنة لزت بدأي منضد','195','106','0');
INSERT INTO [t151017] VALUES (130,'٢٢ - كأن كناسي ضالة يكنفانها ... وأطر قسي تحت صلب مؤيد','196','106','0');
INSERT INTO [t151017] VALUES (131,'٢٣ - لها مرفقان أفتلان كأنما ... تمر بسلمي دالج متشدد','197','106','0');
INSERT INTO [t151017] VALUES (132,'٢٤ - كقنطرة الرومي أقسم ربها ... لتكتنفن حتى تشاد بقرمد','199','106','0');
INSERT INTO [t151017] VALUES (133,'٢٥ - صهابية العثنون، موجودة القرا ... بعيدة وخد الرجل، مواراة اليد','200','106','0');
INSERT INTO [t151017] VALUES (134,'٢٦ - أمرت يداها فتل شرر، وأجنحت ... لها عضداها في سقيف مسند','201','106','0');
INSERT INTO [t151017] VALUES (135,'٢٧ - جنوح، دفاق، عندل، ثم أفرغت ... لها كتفاها في معالى مصعد','202','106','0');
INSERT INTO [t151017] VALUES (136,'٢٨ - كأن علوب النسع في دأياتها ... موارد من خلقاء في ظهر قردد','203','106','0');
INSERT INTO [t151017] VALUES (137,'٢٩ - تلاقى، وأحيانا تبين كأنها ... بنائق غر في قميص مقدد','204','106','0');
INSERT INTO [t151017] VALUES (138,'٣٠ - وأتلع نهاض إذا صعدت به ... كسكان بوصي بدجلة مصعد','205','106','0');
INSERT INTO [t151017] VALUES (139,'٣١ - وجمجمة مثل العلاة كأنما ... وعى الملتقى منها إلى حرف مبرد','206','106','0');
INSERT INTO [t151017] VALUES (140,'٣٢ - وخذ كقرطاس الشامي، ومشفر ... كسبت اليماني، قده لم يجرد','207','106','0');
INSERT INTO [t151017] VALUES (141,'٣٣ - وعينان كالماويتين استكنتا ... بكهفي حجاجي صخرة قلت مورد','208','106','0');
INSERT INTO [t151017] VALUES (142,'٣٤ - طحوران عوار القذى، فتراهما ... كمكحولتي مذعورة أم فرقد','209','106','0');
INSERT INTO [t151017] VALUES (143,'٣٥ - وصادقتا سمع التوجس للسرى ... لهجس خفي، أو لصوت مندد','210','106','0');
INSERT INTO [t151017] VALUES (144,'٣٦ - مؤللتان تعرف العتق فيهما ... كسامعتي شاة بحومل مفرد','211','106','0');
INSERT INTO [t151017] VALUES (145,'٣٧ - وأروع نباض، أحذ، ململم ... كمرداة ضخر في صفيح مصمد','212','106','0');
INSERT INTO [t151017] VALUES (146,'٣٨ - وإن شئت سامى واسط الكور رأسها ... وعامت بضبعيها نجاء الخفيدد','213','106','0');
INSERT INTO [t151017] VALUES (147,'٣٩ - وإن شئت لم ترقل، وإن شئت أرقلت ... مخافة ملوي من القد محصد','214','106','0');
INSERT INTO [t151017] VALUES (148,'٤٠ - وأعلم مخروت من لأنف مارن ... عتيق، متى ترجم به الأرض تردد','215','106','0');
INSERT INTO [t151017] VALUES (149,'٤١ - إذا أقبلت، قالوا: تأخر رحلها ... وإن أدبرت، قالوا: تقدم، فاشدد','216','106','0');
INSERT INTO [t151017] VALUES (150,'٤٢ - وتضحي الجبال الحمر خلفي، كأنها ... من البعد حفت بالملاء المعضد','217','106','0');
INSERT INTO [t151017] VALUES (151,'٤٣ - وتشرب بالقعب الصغير وإن تقد ... بمشفرها يوما إلى الليل تنقد','218','106','0');
INSERT INTO [t151017] VALUES (152,'٤٤ - على مثلها أمضي، إذا قال صاحبي: ... ألا ليتني أفديك منها، وافتدي','219','106','0');
INSERT INTO [t151017] VALUES (153,'٤٥ - وجاشت إليه النفس خوفا وخاله ... مصابا، ولو أمسى على غير مرصد','221','106','0');
INSERT INTO [t151017] VALUES (154,'٤٦ - إذا القوم قالوا: من فتى؟ خلت أنتي ... عنيت، فلم أكسل، ولم أتبلد','223','106','0');
INSERT INTO [t151017] VALUES (155,'٤٧ - أحلت عليها بالقطيع فأجذمت ... وقد خب آل الأمعز المتوقد','225','106','0');
INSERT INTO [t151017] VALUES (156,'٤٨ - فذالت، كما ذالت وليدة مجلس ... تري ربها أذيال سحل ممدد','226','106','0');
INSERT INTO [t151017] VALUES (157,'٤٩ - ولست بحلال التلاع مخافة ... ولكن متى يسترفد القوم أرفد','227','106','0');
INSERT INTO [t151017] VALUES (158,'٥٠ - وإن تبغني في حلقة القوم تلقني ... وإن تقتنصني في الحوانيت تصطد','228','106','0');
INSERT INTO [t151017] VALUES (159,'٥١ - متى تأتني أصبحك كأسا روية ... وإن كنت عنها غانيا، فاغن وازدد','229','106','0');
INSERT INTO [t151017] VALUES (160,'٥٢ - وإن يلتق الحي الجميع تلاقني ... إلى ذروة البيت الكريم المصمد','231','106','0');
INSERT INTO [t151017] VALUES (161,'٥٣ - نداماي بيض كالنجوم، وقينة ... تروح علينا بين برد ومجسد','232','106','0');
INSERT INTO [t151017] VALUES (162,'٥٤ - رحيب قطاب الجيب منها رفيقة ... بجس الندامى بضة المتجرد','233','106','0');
INSERT INTO [t151017] VALUES (163,'٥٥ - إذا نحن قلنا: أسمعينا انبرت لنا ... على رسلها، مطروفة لم تشدد','235','106','0');
INSERT INTO [t151017] VALUES (164,'٥٦ - إذا رجعت في صوتها خلت صوتها ... تجاوب أظار على ربع رد','236','106','0');
INSERT INTO [t151017] VALUES (165,'٥٧ - وما زال تشرابي الخمور ولذتي ... وبيعي وإنفاقي طريفي ومتلدي','237','106','0');
INSERT INTO [t151017] VALUES (166,'٥٨ - إلى أن تحامتني العشيرة كلها ... وأفردت إفراد البعير المعبد','238','106','0');
INSERT INTO [t151017] VALUES (167,'٥٩ - رأيت بني غبراء لا يذكرونني ... ولا أهل هذاك الطراف الممدد','239','106','0');
INSERT INTO [t151017] VALUES (168,'٦٠ - ألا أيهذا اللائمي أحضر الوغى ... وأن أشهد اللذات، هل أنت مخلدي؟','241','106','0');
INSERT INTO [t151017] VALUES (169,'٦١ - فإن كنت لا تستطيع دفع منيتي ... فدعني أبادرها بما ملكت يدي','243','106','0');
INSERT INTO [t151017] VALUES (170,'٦٢ - فلولا ثلاث هن من عيشة الفتى ... وجدك لم أحفل متى قام عودي','245','106','0');
INSERT INTO [t151017] VALUES (171,'٦٣ - فمنهن سبق العاذلات بشربة ... كميت متى تعل بالماء تزبد','246','106','0');
INSERT INTO [t151017] VALUES (172,'٦٤ - وكري إذا نادى المضاف محنبا ... كسيد الغضا نبهته المتورد','248','106','0');
INSERT INTO [t151017] VALUES (173,'٦٥ - وتقصير يوم الدجن، والدجن معجب ... ببهكنة تحت الطراف المعمد','249','106','0');
INSERT INTO [t151017] VALUES (174,'٦٦ - كأن البرين والدماليج علقت ... على عشر أو خروع لم يخضد','250','106','0');
INSERT INTO [t151017] VALUES (175,'٦٧ - ذريني أروي هامتي في حياتها ... مخافة شرب في الحياة مصرد','251','106','0');
INSERT INTO [t151017] VALUES (176,'٦٨ - كريم يروي نفسه في حياته ... ستعلم، إن متنا غدا، أينا الصدي؟','252','106','0');
INSERT INTO [t151017] VALUES (177,'٦٩ - أرى قبر نحام بخيل بماله ... كقبر غوي في البطالة مفسد','253','106','0');
INSERT INTO [t151017] VALUES (178,'٧٠ - ترى جثوتين من تراب عليهما ... صفائح صم من صفيح منضد','254','106','0');
INSERT INTO [t151017] VALUES (179,'٧١ - أرى الموت يعتام الكرام، ويصطفي ... عقلية مال الفاحش المتشدد','255','106','0');
INSERT INTO [t151017] VALUES (180,'٧٢ - أرى الموت يعتاد النفوس، ولا أرى ... بعيدا غدا، ما أقرب اليوم من غد!','256','106','0');
INSERT INTO [t151017] VALUES (181,'٧٣ - أرى الدهر كنزا ناقصا كل ليلة ... وما تنقص الأيام والدهر ينفد','257','106','0');
INSERT INTO [t151017] VALUES (182,'٧٤ - لعمرك، إن الموت ما أخطأ الفتى ... لكالطول المرخى، وثنياه باليد','259','106','0');
INSERT INTO [t151017] VALUES (183,'٧٥ - كتى ما يشأ يوما يقده لحتفه ... ومن يك في حبل المنية ينقد','260','106','0');
INSERT INTO [t151017] VALUES (184,'٧٦ - فما لي أراني وابن عمي مالكا؟ ... متى أدن منه ينأ عني، ويبعد','261','106','0');
INSERT INTO [t151017] VALUES (185,'٧٧ - يلوم، وما أدري علام يلومني؟ ... كما لامني في الحي قرط بن اعبد','263','106','0');
INSERT INTO [t151017] VALUES (186,'٧٨ - وأيأسني من كل خير طلبته ... كأنا وضعناه إلى رمس ملحد','264','106','0');
INSERT INTO [t151017] VALUES (187,'٧٩ - على غير ذنب قتلته أنني ... نشدت، فلم أغفل حمولة معبد','265','106','0');
INSERT INTO [t151017] VALUES (188,'٨٠ - وقربت بالقربى وجدك إنني ... متى يك أمر للنكيثة أشهد','267','106','0');
INSERT INTO [t151017] VALUES (189,'٨١ - وإن أدع في الجلى أكن من حماتها ... وإن يأتك الأعداء بالجهد أجهد','268','106','0');
INSERT INTO [t151017] VALUES (190,'٨٢ - وإن يقذفوا يالقذع عرضك أسقهم ... بكأس حياض الموت قبل التهدد','270','106','0');
INSERT INTO [t151017] VALUES (191,'٨٣ - بلا حدث أحدثته، وكمحدث ... هجائي وقذفي بالشكاة ومطردي','271','106','0');
INSERT INTO [t151017] VALUES (192,'٨٤ - فلو كان مولاي امرءا هو غيره ... لفرج كربي، أو لانظرني غدي','272','106','0');
INSERT INTO [t151017] VALUES (193,'٨٥ - ولكن مولاي امرؤ هو خانقي ... على الشكر والتسآل، أو أنا مفتدي','274','106','0');
INSERT INTO [t151017] VALUES (194,'٨٦ - وظلم ذوي القربى أشد مضاضة ... على المرء من وقع الحسام المهند','275','106','0');
INSERT INTO [t151017] VALUES (195,'٨٧ - فذرني وخلقي، إنني لك شاكر ... ولو حل بيتي نائيا عند ضرغد','276','106','0');
INSERT INTO [t151017] VALUES (196,'٨٨ - فلو شاء ربي كنت قيس بن خالد ... ولو شاء ربي كنت عمرو بن مرثد','277','106','0');
INSERT INTO [t151017] VALUES (197,'٨٩ - فأصبحت ذا مال كثير، وزارني ... بنون كرام سادة لمسود','279','106','0');
INSERT INTO [t151017] VALUES (198,'٩٠ - أنا الرجل الضرب الذي تعرفونه ... خشاش كرأس الحية المتوقد','280','106','0');
INSERT INTO [t151017] VALUES (199,'٩١ - فآليت لا تنفط كشحي بطانة ... لغضب رقيق الشفرتين مهند','281','106','0');
INSERT INTO [t151017] VALUES (200,'٩٢ - حسام إذا ما قمت منتصرا به ... كفى العود منه البدء ليس بمعضد','282','106','0');
INSERT INTO [t151017] VALUES (201,'٩٣ - أخي ثقة لا ينثني عن ضريبة ... إذا قيل: مهلا، قال حاجزه: قدي','283','106','0');
INSERT INTO [t151017] VALUES (202,'٩٤ - إذا ابتدر القوم السلاح وجدتني ... منيعا إذا بلت بقائمه يدي','285','106','0');
INSERT INTO [t151017] VALUES (203,'٩٥ - وبرك هجود قد أثارت مخافتي ... نواديها أمشي بعضب مجرد','286','106','0');
INSERT INTO [t151017] VALUES (204,'٩٦ - فمرت كهاة ذات خيف جلالة ... عقيلة شيخ كالوبيل يلندد','287','106','0');
INSERT INTO [t151017] VALUES (205,'٩٧ - يقول وقد تر الوظيف وساقها ... ألست ترى أن قد أتيت بمؤيد؟','289','106','0');
INSERT INTO [t151017] VALUES (206,'٩٨ - وقال: ألا ماذا ترون بشارب ... شديد علينا بغيه متعمد؟','290','106','0');
INSERT INTO [t151017] VALUES (207,'٩٩ - وقال: ذروه، إنما نفعها له ... وإلا تردوا قاصي البرك يزدد','291','106','0');
INSERT INTO [t151017] VALUES (208,'١٠٠ - فظل الإماء يمتللن حوارها ... ويسعى علينا بالسديف المسرهد','292','106','0');
INSERT INTO [t151017] VALUES (209,'١٠١ - فإذ مت فانعيني بما أنا أهله ... وشقي علي الحبيب يا ابنة معبد','293','106','0');
INSERT INTO [t151017] VALUES (210,'١٠٢ - ولا تجعليني كامرئ ليس همه ... كهمي، ولا يغني غنائي ومشهدي','295','106','0');
INSERT INTO [t151017] VALUES (211,'١٠٣ - بطئ عن الجلى، سريع إلى الخنا ... ذلول بأجماع الرجال ملهد','297','106','0');
INSERT INTO [t151017] VALUES (212,'١٠٤ - فلو كنت وغلا في الرجال لضرني ... عداوة ذي الأصحاب والمتوحد','298','106','0');
INSERT INTO [t151017] VALUES (213,'١٠٥ - ولكن نفى الأعادي جرأتي ... عليهم وإقدامي وصدقي، ومحتدي','298','106','0');
INSERT INTO [t151017] VALUES (214,'١٠٦ - لعمرك ما امري علي بغمة ... نهاري ولا ليلي علي بسرمد','299','106','0');
INSERT INTO [t151017] VALUES (215,'١٠٧ - ويوم حبست النفس عند عراكه ... حفاظا على عوراته والتهدد','300','106','0');
INSERT INTO [t151017] VALUES (216,'١٠٨ - على موطن يخشى الفتى عنده الردى ... متى تعترك فيه الفرائض ترعد','301','106','0');
INSERT INTO [t151017] VALUES (217,'١٠٩ - أرى الموت لا يرعى على ذي جلالة ... وإن كان في الدنيا عزيزا بمقعد','303','106','0');
INSERT INTO [t151017] VALUES (218,'١١٠ - وأصفر مضبوح نظرت حواره ... على النار، واستودعته كف مجمد','304','106','0');
INSERT INTO [t151017] VALUES (219,'١١١ - ستبدي لك الأيام ما كنت جاهلا ... ويأتيك بالأخبار من لم تزود','305','106','0');
INSERT INTO [t151017] VALUES (220,'١١٢ - ويأتيك بالأخبار من لم تبع له ... بتاتا، ولم تضرب له وقت موع','307','106','0');
INSERT INTO [t151017] VALUES (221,'١١٣ - لعمرك ما الأيام إلا معارة ... فما استطعت من معروفها، فتزود','308','106','0');
INSERT INTO [t151017] VALUES (222,'١١٤ - ولا خير في خير ترى الشر دونه ... ولا نائل يأتيك بعد التلدد','309','106','0');
INSERT INTO [t151017] VALUES (223,'١١٥ - عن المرء لا تسأل، وأبصر قرينه ... فإن القرين بالمقارن يقتدي','310','106','0');
INSERT INTO [t151017] VALUES (224,'١١٦ - لعمرك ما أدري، وإني لواجل ... أفي اليوم إقدام المنية أم غد؟','312','106','0');
INSERT INTO [t151017] VALUES (225,'١١٧ - فإن تك خلفي لا يفتها سواديا ... وإن تك قدامي أجدها بمرصد','313','106','0');
INSERT INTO [t151017] VALUES (226,'١١٨ - إذا أنت لم تنفع بودك أهله ... ولم تنك بالبوسى عدوك فابعد','314','106','0');
INSERT INTO [t151017] VALUES (227,'١١٩ - لا يرهب ابن العم ما عشت صولتي ... ولا أختني من صولة المتهدد','315','106','0');
INSERT INTO [t151017] VALUES (228,'١٢٠ - وإني وإن أوعدته، أو وعدته ... لمخلف إيعادي، ومنجز موعدي','316','106','0');
INSERT INTO [t151017] VALUES (229,'(٣) معلقة عمرو بن كلثوم التغلبي','319','0','0');
INSERT INTO [t151017] VALUES (230,'فهرست أبيات معلقة عمرو بن كلثوم التغلبي','320','229','0');
INSERT INTO [t151017] VALUES (231,'معلقة عمرو بن كلثوم التغلبي','325','229','0');
INSERT INTO [t151017] VALUES (232,'نسبه','325','229','0');
INSERT INTO [t151017] VALUES (233,'عمرو بن كلثوم بن مالك، بن عتاب بن صعد بن زهير؛ بن جشم بن بكر بن حبيب بن عمرو بن غنم بن تغلب، بن وائل بن قاسط بن هنب بن أفصى بن دعمي بن جديلة بن أسد بن ربيعة بن نزار بن معد بن عدنان، وأم عمرو بن كلثوم هي ليلى بنت مهلهل أخي كليب،','325','232','0');
INSERT INTO [t151017] VALUES (234,'سبب إنشاء المعلقة','325','229','0');
INSERT INTO [t151017] VALUES (235,'١ - ألا هبي بصحنك، فاصبحينا ... ولا تبقي خمور الأندرينا','328','229','0');
INSERT INTO [t151017] VALUES (236,'٢ - مشعشعة كأن الحص فيها ... إذا ما الماء خالطها سخينا','330','229','0');
INSERT INTO [t151017] VALUES (237,'٣ - تجوز بذي اللبانة عن هواه ... إذا ما ذاقها حتى يلينا','332','229','0');
INSERT INTO [t151017] VALUES (238,'٤ - ترى اللحز الشحيح إذا أمرت ... عليه لماله فيها مهينا','333','229','0');
INSERT INTO [t151017] VALUES (239,'٥ - كأن الشهب في الآذان منها ... إذا قرعوا بحافتها الجبينا','334','229','0');
INSERT INTO [t151017] VALUES (240,'٦ - صددت الكأس عنا أم عمرو ... وكأن الكأس مجراها اليمينا','335','229','0');
INSERT INTO [t151017] VALUES (241,'٧ - وما شر الثلاثة أم عمرو ... بصاحبك الذي لا تصبحينا','336','229','0');
INSERT INTO [t151017] VALUES (242,'٨ - وكأس قد شربت ببعلبك ... وأخرى في دمشق وقاصرينا','337','229','0');
INSERT INTO [t151017] VALUES (243,'٩ - إذا صمدت حمياها أريبا ... من الفتيان خلت به جنونا','338','229','0');
INSERT INTO [t151017] VALUES (244,'١٠ - فما برحت مجال الشرب حتى ... تغالوها، وقالوا: قد روينا','339','229','0');
INSERT INTO [t151017] VALUES (245,'١١ - وإنا سوف تدركنا المنايا ... مقدرة لنا ومقدرينا','340','229','0');
INSERT INTO [t151017] VALUES (246,'١٢ - قفي قبل التفرق يا ظعينا ... نخبرك اليقين، وتخبرينا','341','229','0');
INSERT INTO [t151017] VALUES (247,'١٣ - بيوم كرهة ضربا وطعنا ... أقربه مواليك العيونا','342','229','0');
INSERT INTO [t151017] VALUES (248,'١٤ - قفي نسألك، هل أحدثت صرما؟ ... لوشك البين، أم خنت الأمينا؟','343','229','0');
INSERT INTO [t151017] VALUES (249,'١٥ - أفي ليلى يعاتبني أبوها ... وإخوتها، وهم لي ظالمونا؟','344','229','0');
INSERT INTO [t151017] VALUES (250,'١٦ - تريك إذا دخلت على خلاء ... وقد أمنت عيون الكاشحينا','346','229','0');
INSERT INTO [t151017] VALUES (251,'١٧ - ذراعي عيطل أدماء بكر ... تربعت الأجارع والمتونا','347','229','0');
INSERT INTO [t151017] VALUES (252,'١٨ - وثديا مثل حق العاج رخصا ... حصانا من أكف اللامسنا','348','229','0');
INSERT INTO [t151017] VALUES (253,'١٩ - ووجها مثل ضوء البدر وافى ... بإتمام أناسا مدجينينا','349','229','0');
INSERT INTO [t151017] VALUES (254,'٢٠ - ومتني لدنة طالت ولانت ... روادفها تنوء بما يلينا','350','229','0');
INSERT INTO [t151017] VALUES (255,'٢١ - ومأكمة يضيق الباب عنها ... وكشحا، قد جننت به جنونا','351','229','0');
INSERT INTO [t151017] VALUES (256,'٢٢ - وسالفتي رخام، أو بلنط ... يرن خشاش حليهما رنينا','352','229','0');
INSERT INTO [t151017] VALUES (257,'٢٣ - تذكرت الصبا، واشتقت لما ... رأيت حمولها أصلا حدينا','353','229','0');
INSERT INTO [t151017] VALUES (258,'٢٤ - وأعرضت اليمامة، واشمخرت ... كأسياف بأيدي مصلتينا','354','229','0');
INSERT INTO [t151017] VALUES (259,'٢٥ - فما وجدت كوجدي أم سقب ... أضلته، فرجعت الحنينا','355','229','0');
INSERT INTO [t151017] VALUES (260,'٢٦ - ولا شمطاء لم يترك شقاها ... لها من تسعة إلا جنينا','356','229','0');
INSERT INTO [t151017] VALUES (261,'٢٧ - وإن غدا، وإن اليوم رهن ... وبعد غد بما لا تعلمينا','357','229','0');
INSERT INTO [t151017] VALUES (262,'٢٨ - أبا هند، فلا تعجل علينا ... وأنظرنا نخبرك اليقينا','358','229','0');
INSERT INTO [t151017] VALUES (263,'٢٩ - بأنا نورد الرايات بيضا ... ونصدرهن جمرا قد روينا','360','229','0');
INSERT INTO [t151017] VALUES (264,'٣٠ - وأيام لنا غر طوال ... عصينا الملك فيها أن ندينا','361','229','0');
INSERT INTO [t151017] VALUES (265,'٣١ - وسيد معشر قد توجوه ... بتاج الملك يحمي المحجرينا','362','229','0');
INSERT INTO [t151017] VALUES (266,'٣٢ - تركنا الخيل عاكفة عليه ... مقلدة أعنتها صفوفنا','363','229','0');
INSERT INTO [t151017] VALUES (267,'٣٣ - وأنزلنا البيوت بذي طلوح ... إلى الشامان تنفي الموعدينا','364','229','0');
INSERT INTO [t151017] VALUES (268,'٣٤ - وقد هرت كلاب الحي منا ... وشذبنا قتادة من يلينا','365','229','0');
INSERT INTO [t151017] VALUES (269,'٣٥ - متى ننقل إلى قوم رحانا ... يكونوا في اللقاء لها طحينا','366','229','0');
INSERT INTO [t151017] VALUES (270,'٣٦ - يكون ثفالها شرقي نجد ... ولهوتها قضاعة أجمعينا','367','229','0');
INSERT INTO [t151017] VALUES (271,'٣٧ - وإن الضغن بعد الضغن يبدو ... عليك، ويخرج الداء الدفينا','367','229','0');
INSERT INTO [t151017] VALUES (272,'٣٨ - ورثنا المجد، قد علمت معد ... نطاعن دونه حتى يبينا','368','229','0');
INSERT INTO [t151017] VALUES (273,'٣٩ - ونحن إذا عماد الحي خرت ... على الأحفاض نمنع من يلينا','369','229','0');
INSERT INTO [t151017] VALUES (274,'٤٠ - ندافع عنهم الأعداء قدما ... ونحمل عنهمو ما حملونا','371','229','0');
INSERT INTO [t151017] VALUES (275,'٤١ - نطاعن ما تراخى الناس عنا ... ونضرب بالسيوف إذا غشينا','372','229','0');
INSERT INTO [t151017] VALUES (276,'٤٣ - نشق بها رؤوس القوم شقا ... ونخليها الرقاب، فيختلينا','373','229','0');
INSERT INTO [t151017] VALUES (277,'٤٤ - تخال جماجم الأبطال فيها ... وسوقا بالأماعز يرتمينا','374','229','0');
INSERT INTO [t151017] VALUES (278,'٤٥ - نحز رؤوسهم في غير بر ... فما يدرون ماذا يتقونا؟','375','229','0');
INSERT INTO [t151017] VALUES (279,'٤٦ - كأن سيوفنا فينا وفيهم ... مخاريق بأيدي لاعبينا','376','229','0');
INSERT INTO [t151017] VALUES (280,'٤٧ - كأن ثيابنا منا ومنهم ... خضبن بأرجوان، أو طلينا','377','229','0');
INSERT INTO [t151017] VALUES (281,'٤٨ - إذا ما عي بالإسناف حي ... من الهول المشبه، أن يكونا','378','229','0');
INSERT INTO [t151017] VALUES (282,'٤٩ - نصبنا مثل رهوة ذات حد ... محافظة، وكنا السابقينا','379','229','0');
INSERT INTO [t151017] VALUES (283,'٥٠ - بفتيان يرون القتل مجدا ... وشيب في الحروب مجربينا','380','229','0');
INSERT INTO [t151017] VALUES (284,'٥١ - يدهدون الرؤوس كما تدهدي ... حزاورة بأبطحها الكرينا','381','229','0');
INSERT INTO [t151017] VALUES (285,'٥٢ - حديا الناس كلهمو جميعا ... مقارعة بنيهم عن بنينا','381','229','0');
INSERT INTO [t151017] VALUES (286,'٥٣ - فأما يوم خشيتنا عليهم ... فنصبح غارة متلببينا','383','229','0');
INSERT INTO [t151017] VALUES (287,'٥٤ - وأما يوم لا نخشى عليهم ... فنصبح في مجالسنا ثبينا','384','229','0');
INSERT INTO [t151017] VALUES (288,'٥٥ - برأس من بني جشم بن بكر ... ندق به السهولة والحزونا','385','229','0');
INSERT INTO [t151017] VALUES (289,'٥٦ - بأي مشيئة عمرو بن هند ... نكون لقيلكم فيها قطينا؟','386','229','0');
INSERT INTO [t151017] VALUES (290,'٥٧ - بأي مشيئة عمرو بن هند ... ترى أنا نكون الأرذلينا','387','229','0');
INSERT INTO [t151017] VALUES (291,'٥٨ - بأي مشيئة عمرو بن هند ... تطيع بنا الوشاة وتزدرينا','387','229','0');
INSERT INTO [t151017] VALUES (292,'٥٩ - تهددنا، وأوعدنا رويدا ... متى كنا لأمك مقتوينا؟','388','229','0');
INSERT INTO [t151017] VALUES (293,'٦٠ - فإن قناتنا يا عمرو أعيت ... على الأعداء قبلك أن تلينا','389','229','0');
INSERT INTO [t151017] VALUES (294,'٦١ - إذا عض الثقاف بها اشمأزت ... وولتهم عشوزنة زبونا','390','229','0');
INSERT INTO [t151017] VALUES (295,'٦٢ - عشوزنة إذا انقلبت أرنت ... تدق قفا المثقف والجبينا','392','229','0');
INSERT INTO [t151017] VALUES (296,'٦٣ - فهل حدثت في جشم بن بكر ... بنقص في خطوب الأولينا؟','392','229','0');
INSERT INTO [t151017] VALUES (297,'٦٤ - ورثنا مجد علقمة بن سيف ... أباح لنا حصون المجد دينا','393','229','0');
INSERT INTO [t151017] VALUES (298,'٦٥ - ورثت مهلهلا والخير ... منه زهيرا، نعم ذخر الذاخرينا','394','229','0');
INSERT INTO [t151017] VALUES (299,'٦٦ - وعتابا وكلثوما جميعا ... بهم نلنا تراث الأكرمينا','395','229','0');
INSERT INTO [t151017] VALUES (300,'٦٧ - وذا البرة الذي حدثت عنه ... به نحمى ونحمي الملجئينا','396','229','0');
INSERT INTO [t151017] VALUES (301,'٦٨ - ومنا قبله الساعي كليب ... فأي المجد إلا قد ولينا؟','398','229','0');
INSERT INTO [t151017] VALUES (302,'٦٩ - متى نعقد قرينتنا بحبل ... نجذ الوصل، أو نقص القرينا','398','229','0');
INSERT INTO [t151017] VALUES (303,'٧٠ - ونوجد نحن أمنعهم دمارا ... وأوفاهم إذا عقدوا يمينا','400','229','0');
INSERT INTO [t151017] VALUES (304,'٧١ - ونحن غداة أوقد في خزاز ... رفدنا فوق رفد الرافدينا','401','229','0');
INSERT INTO [t151017] VALUES (305,'٧٢ - ونحن الحابسون بذي أراطى ... تسف الجلة الخور الدرينا','402','229','0');
INSERT INTO [t151017] VALUES (306,'٧٣ - ونحن الحاكمون إذا أطعنا ... ونحن العازمون إذا عصينا','402','229','0');
INSERT INTO [t151017] VALUES (307,'٧٤ - ونحن التاركون لما سخطنا ... ونحن الآخذون لما رضينا','403','229','0');
INSERT INTO [t151017] VALUES (308,'٧٥ - وكنا الأيمنين إذا التقينا ... وكان الأيسرين بنو أبينا','404','229','0');
INSERT INTO [t151017] VALUES (309,'٧٦ - فصالوا صولة فيمن يليهم ... وصلنا صولة فيمن يلينا','405','229','0');
INSERT INTO [t151017] VALUES (310,'٧٧ - فآبوا بالنهاب، وبالسبايا ... وأبنا بالملوك مصفدينا','406','229','0');
INSERT INTO [t151017] VALUES (311,'٧٨ - إليكم يا بني بكر إليكم ... ألما تعرفوا منا اليقينا؟','407','229','0');
INSERT INTO [t151017] VALUES (312,'٧٩ - ألما تعرفوا منا ومنكم ... كتائب يطعن ويرتمينا','408','229','0');
INSERT INTO [t151017] VALUES (313,'٨٠ - علينا البيض، واليلب اليماني ... وأسياف يقمن، وينحنينا','409','229','0');
INSERT INTO [t151017] VALUES (314,'٨١ - علينا كل سابغة دلاص ... ترى فوق النجاد لها غضونا','410','229','0');
INSERT INTO [t151017] VALUES (315,'٨٢ - إذا وضعت عن الأبطال يوما ... رأيت لها جلود القوم جونا','410','229','0');
INSERT INTO [t151017] VALUES (316,'٨٣ - كأن متونهن متون غدر ... تصفقها الرياح إذا جرينا','411','229','0');
INSERT INTO [t151017] VALUES (317,'٨٤ - وتحملنا غداة الروع جرد ... عرفن لنا نقائذ، وافتلينا','412','229','0');
INSERT INTO [t151017] VALUES (318,'٨٥ - وردن دوارعا، وخرجن شعثا ... كأمثال الرصائع، قد بلينا','413','229','0');
INSERT INTO [t151017] VALUES (319,'٨٦ - ورثناهن عن آباء صدق ... ونورثها إذا متنا بنينا','414','229','0');
INSERT INTO [t151017] VALUES (320,'٨٧ - وقد علم القبائل من معد ... إذا قبب بأبطحها بنينا','415','229','0');
INSERT INTO [t151017] VALUES (321,'٨٨ - بأنا العاصمون بكل كحل ... وأنا الباذلون لمجتدينا','416','229','0');
INSERT INTO [t151017] VALUES (322,'٨٩ - وأنا المطعمون إذا قدرنا ... وأنا المهلكون إذا ابتلينا','417','229','0');
INSERT INTO [t151017] VALUES (323,'٩٠ - وأنا المانعون لما يلينا ... إذا ما البيض زايلت الجفونا','418','229','0');
INSERT INTO [t151017] VALUES (324,'٩١ - وأنا المنعمون إذا قدرنا ... وأنا المهلكون إذا أتينا','419','229','0');
INSERT INTO [t151017] VALUES (325,'٩٢ - وأنا الحاكمون بما أردنا ... وأنا النازلون بحيث شينا','419','229','0');
INSERT INTO [t151017] VALUES (326,'٩٣ - وأنا الطالبون إذا نقمنا ... وأنا الضاربون إذا ابتلينا','420','229','0');
INSERT INTO [t151017] VALUES (327,'٩٤ - وأنا النازلون بكل ثغر ... يخاف النازلون به المنونا','421','229','0');
INSERT INTO [t151017] VALUES (328,'٩٥ - وأنا الشاربون الماء صفوا ... ويشرب غيرنا كدرا وطينا','422','229','0');
INSERT INTO [t151017] VALUES (329,'٩٦ - ألا أبلغ بني الطماح عنا ... ودعميا، فكيف وجدتمونا؟','423','229','0');
INSERT INTO [t151017] VALUES (330,'٩٧ - نزلتم منزل الأضياف منا ... فعجلنا القرى أن تشتمونا','424','229','0');
INSERT INTO [t151017] VALUES (331,'٩٨ - قريناكم فعجلنا قراكم ... قبيل الصبح مرداة طحونا','425','229','0');
INSERT INTO [t151017] VALUES (332,'٩٩ - على آثارنا بيض حسان ... نحاذر أن تقسم، أو تهونا','426','229','0');
INSERT INTO [t151017] VALUES (333,'١٠٠ - ظعائن من بني جشم بن بكر ... خلطن بميسم حسبا ودينا','427','229','0');
INSERT INTO [t151017] VALUES (334,'١٠١ - أخذن على بعولتهن عهدا ... إذا لاقوا كتائب معلمينا','428','229','0');
INSERT INTO [t151017] VALUES (335,'١٠٢ - ليستلبن أبدانا وبيضا ... وأسرى في الحديد مقرنينا','429','229','0');
INSERT INTO [t151017] VALUES (336,'١٠٣ - إذا ما رحن يمشين الهويني ... كما اضطربت متون الشاربينا','430','229','0');
INSERT INTO [t151017] VALUES (337,'١٠٤ - يقتن جيادنا, ويقلن: لستم ... بعولتنا إذا لم تمنعونا','432','229','0');
INSERT INTO [t151017] VALUES (338,'١٠٥ - إذا لم نحمهن، فلا بقينا ... لشيء بعدهن, ولا حيينا','433','229','0');
INSERT INTO [t151017] VALUES (339,'١٠٦ - وما منع الظعائن مثل ضرب ... ترى منه السواعد كالقلينا','433','229','0');
INSERT INTO [t151017] VALUES (340,'١٠٧ - لنا الدنيا، ومن أضحى عليها ... ونبطش حين نبطش قادرينا','434','229','0');
INSERT INTO [t151017] VALUES (341,'١٠٨ - إذا ما الملك سام الناس خسفا ... أبينا أن نقر الذل فينا','435','229','0');
INSERT INTO [t151017] VALUES (342,'١٠٩ - نسمى ظالمين, وما ظلمنا ... ولكنا سنبدأ ظالمينا','436','229','0');
INSERT INTO [t151017] VALUES (343,'١١٠ - تنادى المصعبان، وآل بكر ... ونادوا يا لكندة أجمعينا','437','229','0');
INSERT INTO [t151017] VALUES (344,'١١١ - فإن نغلب فغلابون قدما ... وإن نغلب فغير مغلبينا','439','229','0');
INSERT INTO [t151017] VALUES (345,'١١٢ - إذا بلغ الفطام لنا صبي ... تخر له الجبابر ساجدينا','440','229','0');
INSERT INTO [t151017] VALUES (346,'١١٣ - ملأنا البر حتى ضاق عنا ... وظهر البحر نملؤه سفينا','441','229','0');
INSERT INTO [t151017] VALUES (347,'١١٤ - ألا لا يجهلن أحد علينا ... فنجهل فوق جهل الجاهلينا','442','229','0');
INSERT INTO [t151017] VALUES (348,'١١٥ - ونعدو حيث لا يعدى علينا ... ونضرب بالمواسي من يلينا','444','229','0');
INSERT INTO [t151017] VALUES (349,'١١٦ - ألا لا يحسب الأعداء أنا ... تضغضغنا، وأنا قد فنينا','445','229','0');
INSERT INTO [t151017] VALUES (350,'١١٧ - ترانا بارزين, وكل حي ... قد اتخذوا مخافتنا قرينا','446','229','0');
INSERT INTO [t151017] VALUES (351,'١١٨ - كأنا, والسيوف مسللات ... ولدنا الناس طرا أجمعينا','447','229','0');
INSERT INTO [t151017] VALUES (352,'(٤) معلقة الحارث بن حلزة اليشكري','448','0','0');
INSERT INTO [t151017] VALUES (353,'فهرست أبيات معلقة الحارث بن حلزة اليشكري','449','352','0');
INSERT INTO [t151017] VALUES (354,'معلقة الحارث بن حلزة اليشكري','453','352','0');
INSERT INTO [t151017] VALUES (355,'نسبه','453','352','0');
INSERT INTO [t151017] VALUES (356,'الحارث بن حلزة بن مكروه بن بديد بن عبد الله بن مالك بن عبد سعد بن جشم بن ذبيان، بن كنانة بن يشكر بن وائل بن قاسط بن هنب بن أفصى بن دعمي بن جديلة، بن أسد بن ربيعة، بن نزار بن معد بن عدنان بن أدد.','453','355','0');
INSERT INTO [t151017] VALUES (357,'١ - آذنتنا ببينها أسماء ... رب ثاو يمل منه الثواء','455','352','0');
INSERT INTO [t151017] VALUES (358,'٢ - آذنتنا ببينها, ثم ولت ... ليت شعري متى يكون اللقاء؟','456','352','0');
INSERT INTO [t151017] VALUES (359,'٣ - بعد عهد لها برقة شماء ... فأدنى ديارها الخلصاء','457','352','0');
INSERT INTO [t151017] VALUES (360,'٤ - فالمحياة فالصفاح, فأعنا ... ق فتاق فعاذب فالوفاء','458','352','0');
INSERT INTO [t151017] VALUES (361,'٥ - فرياض القطا, فأودية الشر ... بب, فالشعبتان فالأبلاء','458','352','0');
INSERT INTO [t151017] VALUES (362,'٦ - لا أرى من عهدت فيها, فأبكي الـ ... ـيوم دلها وما يرد البكاء؟','459','352','0');
INSERT INTO [t151017] VALUES (363,'٧ - وبعينيك أوقدت هند النا ... ر أخيرا تلوي بها العلياء','460','352','0');
INSERT INTO [t151017] VALUES (364,'٨ - أوقدتها بين العقيق فشخصيـ ... ـن بعود كما يلوح الضياء','461','352','0');
INSERT INTO [t151017] VALUES (365,'٩ - فتنورت نارها من بعيد ... بخزاز هيهات منك الصلاء','462','352','0');
INSERT INTO [t151017] VALUES (366,'١٠ - غير أني قد أستعين على الهمـ ... ـم إذا خف بالثوي النجاء','463','352','0');
INSERT INTO [t151017] VALUES (367,'١١ - بزفوف كأنها هقلة أمـ ... ـم رئال دوية سقفاء','464','352','0');
INSERT INTO [t151017] VALUES (368,'١٢ - آنست نبأة, وأفزعها القنـ ... ـناص عصرا, وقد دنا الإمساء','465','352','0');
INSERT INTO [t151017] VALUES (369,'١٣ - فترى خلفها من الرجع, والوقـ ... ـع منينا كأنه إهباء','466','352','0');
INSERT INTO [t151017] VALUES (370,'١٤ - وطراقا من خلفهن طراق ... ساقطات تلوي بها الصحراء','467','352','0');
INSERT INTO [t151017] VALUES (371,'١٥ - أتلهى بها الهواجر, إذ كـ ... ـل ابن هم بلية عمياء','468','352','0');
INSERT INTO [t151017] VALUES (372,'١٦ - وأتانا عن الأراقم أنبا ... ء وخطب نعنى به ونساء','469','352','0');
INSERT INTO [t151017] VALUES (373,'١٧ - إن إخواننا الأراقم يغلو ... ن علينا في قولهم إحفاء','470','352','0');
INSERT INTO [t151017] VALUES (374,'١٨ - يخلطون البريء منا بذي الذنـ ... ـب, ولا ينفع الخلي الخلاء','471','352','0');
INSERT INTO [t151017] VALUES (375,'١٩ - زعموا أن كل من ضرب العيـ ... ـر موال لنا, وأنا الولاء','472','352','0');
INSERT INTO [t151017] VALUES (376,'٢٠ - أجمعوا أمرهم عشاء، فلما ... أصبحوا أصبحت لهم ضوضاء','474','352','0');
INSERT INTO [t151017] VALUES (377,'٢١ - من مناد، ومن مجيب، ومن تصـ ... ـهال خيل! خلال ذاك رغاء','475','352','0');
INSERT INTO [t151017] VALUES (378,'٢٢ - أيها الناطق المرقش عنا ... عند عمرو، وهل لذاك بقاء؟','475','352','0');
INSERT INTO [t151017] VALUES (379,'٢٣ - لا تخلنا على غراتك إنا ... قبل ما قد وشى بنا الأعداء','476','352','0');
INSERT INTO [t151017] VALUES (380,'٢٤ - فبقينا على الشناءة تنميـ ... ـنا حصون وعزة قعساء','477','352','0');
INSERT INTO [t151017] VALUES (381,'٢٥ - قبل ما اليوم بيضت بعيون النـ ... ـاس فيها تغيظ وإباء','478','352','0');
INSERT INTO [t151017] VALUES (382,'٢٦ - وكأن المنون تردي بنا أر ... عن جونا ينجاب عنه العماء','479','352','0');
INSERT INTO [t151017] VALUES (383,'٢٧ - مكفهرا على الحوادث لا تر ... توه للدهر مؤيد صماء','480','352','0');
INSERT INTO [t151017] VALUES (384,'٢٨ - أيما خطة أردتم فأدو ... ها إلينا تمشي بها الأملاء','481','352','0');
INSERT INTO [t151017] VALUES (385,'٢٩ - إن نبشتم ما بين ملحة فالصا ... قب، فيه الأموات والأحياء','482','352','0');
INSERT INTO [t151017] VALUES (386,'٣٠ - أو نقشتم، فالنقش تجشمه النا ... س، وفيه الصلاح والإبراء','483','352','0');
INSERT INTO [t151017] VALUES (387,'٣١ - أو سكتم عنا، فكنا كمن أغـ ... ـمض عينا في جفنها الأقذاء','484','352','0');
INSERT INTO [t151017] VALUES (388,'٣٢ - أو منعتم ما تسألون فمن حد ... ثتموه له علينا العلاء؟','485','352','0');
INSERT INTO [t151017] VALUES (389,'٣٣ - هل علمتم أيام ينتهب النا ... س غوارا لكل حي عواء؟','486','352','0');
INSERT INTO [t151017] VALUES (390,'٣٤ - إذ رفعنا الجمال من سعف البحـ ... ـرين سيرا حتى نهاها الحساء','487','352','0');
INSERT INTO [t151017] VALUES (391,'٣٥ - ثم ملنا على تميم، فأحرمـ ... ـنا وفينا بنات مر إماء','488','352','0');
INSERT INTO [t151017] VALUES (392,'٣٦ - لا يقيم العزيز في البلد السهـ ... ـل، ولا ينفع الذليل النجاء','490','352','0');
INSERT INTO [t151017] VALUES (393,'٣٧ - ليس ينجي موائلا من حذار ... رأس طود، وحرة رجلاء','490','352','0');
INSERT INTO [t151017] VALUES (394,'٣٨ - فملكنا بذلك الناس حتى ... ملك المنذر بن ماء السماء','491','352','0');
INSERT INTO [t151017] VALUES (395,'٣٩ - وهو الرب والشهيد على يو ... م الحيارين، والبلاء بلاء','492','352','0');
INSERT INTO [t151017] VALUES (396,'٤٠ - ملك أضلع البرية لا يو ... جد فيها لما لديه كفاء','493','352','0');
INSERT INTO [t151017] VALUES (397,'٤١ - فاتركوا البغي والتعدي، وإما ... تتعاشوا ففي التعاشي الداء','494','352','0');
INSERT INTO [t151017] VALUES (398,'تنبيه','495','352','0');
INSERT INTO [t151017] VALUES (399,'٤٢ - واذكروا حلف ذي المجاز، وما قد ... دم فيه العهود والكفلاء','496','352','0');
INSERT INTO [t151017] VALUES (400,'٤٣ - حذر الخون، والتعدي، وهل ينـ ... ـقض ما في المهارق الأهواء؟','497','352','0');
INSERT INTO [t151017] VALUES (401,'٤٤ - واعلموا أننا وإياكم فيـ ... ـما اشترطنا يوم اختلفنا سواء','498','352','0');
INSERT INTO [t151017] VALUES (402,'٤٥ - أعلينا جناح كندة أن يغـ ... ـنم غازيهم، ومنا الجزاء؟','499','352','0');
INSERT INTO [t151017] VALUES (403,'٤٦ - أم علينا جرى حنيفة، أو ما ... جمعت من محارب غبراء؟','500','352','0');
INSERT INTO [t151017] VALUES (404,'٤٧ - أم جنايا بني عتيق؟ فمن يغـ ... ـدر فإنا من حربهم براء','501','352','0');
INSERT INTO [t151017] VALUES (405,'٤٨ - أم علينا جرى العباد كما نيـ ... ـط بجوز المحمل الأعباء','503','352','0');
INSERT INTO [t151017] VALUES (406,'٤٩ - أم علينا جرى قضاعة أم ليـ ... ـس علينا فيما جنوا أنداء','503','352','0');
INSERT INTO [t151017] VALUES (407,'٥٠ - أم علينا جرى إياد كما قيـ ... ـل لطسم: أخوكم الأباء','504','352','0');
INSERT INTO [t151017] VALUES (408,'٥١ - ليس منا المضربون، ولا قيـ ... ـس، ولا جندل، ولا الحداء','506','352','0');
INSERT INTO [t151017] VALUES (409,'٥٢ - عننا باطلا، وظلما كما تعـ ... ـتر عن حجرة الربيض الظباء','507','352','0');
INSERT INTO [t151017] VALUES (410,'٥٣ - وثمانون من تميم بأيد يـ ... ـهم رماح صدروهن القضاء','508','352','0');
INSERT INTO [t151017] VALUES (411,'٥٤ - لم يخلوا بني رزاح ببرقا ... ء نطاع لهم عليهم دعاء','510','352','0');
INSERT INTO [t151017] VALUES (412,'٥٥ - تركوهم ملحبين، وآبوا ... بنهاب يصم منه الحداء','511','352','0');
INSERT INTO [t151017] VALUES (413,'٥٦ - ثم جاؤوا يسترجعون، فلم تر ... جع لهم شامة ولا زهراء','512','352','0');
INSERT INTO [t151017] VALUES (414,'٥٧ - ثم فاؤوا منهم بقاصمة الظهـ ... ـر، ولا يبرد الغليل الماء','513','352','0');
INSERT INTO [t151017] VALUES (415,'٥٨ - ثم خيل من بعد ذاك مع الغلا ... ق، لا رأفة! ولا إبقاء','513','352','0');
INSERT INTO [t151017] VALUES (416,'٥٩ - ما أصابوا من تغلبي، فمطلو ... ل عليه إذا تولى العفاء','514','352','0');
INSERT INTO [t151017] VALUES (417,'٦٠ - كتكاليف قومنا، إذ غزا المنـ ... ـذر، هل نحن لابن هند رعاء؟','516','352','0');
INSERT INTO [t151017] VALUES (418,'٦١ - إذ أحل العلاة قبة ميسو ... ن، فأدنى دارها العوصاء','517','352','0');
INSERT INTO [t151017] VALUES (419,'٦٢ - فتأوت له قراضبة من ... كل حي كأنهم ألقاء','518','352','0');
INSERT INTO [t151017] VALUES (420,'٦٣ - فهداهم بالأسودين، وأمر اللـ ... ـه بلغ يشقى به الأشقياء','519','352','0');
INSERT INTO [t151017] VALUES (421,'٦٤ - إذ تمنونهم غرورا، فساقتـ ... ـهم إليكم أمنية أشراء','520','352','0');
INSERT INTO [t151017] VALUES (422,'٦٥ - لم يغروكم غرورا، ولكن ... رفع الآل شخصهم والضحاء','521','352','0');
INSERT INTO [t151017] VALUES (423,'٦٦ - أيها الشانئ المبلغ عنا ... عند عمرو، وهل لذاك انتهاء؟','522','352','0');
INSERT INTO [t151017] VALUES (424,'٦٧ - إن عمرا لنا لديه خلال ... غير شك في كلهن البلاء','523','352','0');
INSERT INTO [t151017] VALUES (425,'٦٨ - ملك مقسط، وأكمل من يمـ ... ـشي، ومن دون ما لديه الثناء','524','352','0');
INSERT INTO [t151017] VALUES (426,'٦٩ - إرمي بمثله جالت الجـ ... ـن، فآبت لخصمها الأجلاء','525','352','0');
INSERT INTO [t151017] VALUES (427,'٧٠ - من لنا عنده من الخير آيا ... ت ثلاث في كلهن القضاء','526','352','0');
INSERT INTO [t151017] VALUES (428,'٧١ - آية شارق الشقيقة إذ جا ... ءت معد لكل حي لواء','527','352','0');
INSERT INTO [t151017] VALUES (429,'٧٢ - حول قيس مستلئمين بكبش ... قرظي كأنه عبلاء','528','352','0');
INSERT INTO [t151017] VALUES (430,'٧٣ - وصتيت من العواتك ما تنـ ... ـهاه إلا مبيضة رعلاء','529','352','0');
INSERT INTO [t151017] VALUES (431,'٧٤ - فرددناهم بطعن كما يخـ ... ـرج من خربة المزاد الماء','530','352','0');
INSERT INTO [t151017] VALUES (432,'٧٥ - وحملناهم على حزن ثهلا ... ن شلالا، ودمي الأنساء','531','352','0');
INSERT INTO [t151017] VALUES (433,'٧٩ - وفعلنا بهم كما علم اللـ ... ـه، ومما إن للحائنين دماء','531','352','0');
INSERT INTO [t151017] VALUES (434,'٧٧ - ثم حجرا أعني ابن أم قطام ... وله فارسية خضراء','532','352','0');
INSERT INTO [t151017] VALUES (435,'٧٨ - أسد في اللقاء، ورد، هموس ... وربيع إن شنعت غبراء','533','352','0');
INSERT INTO [t151017] VALUES (436,'٧٩ - فجبهناهم بطعن كما تنـ ... ـهز عن جمة الطوي الدلاء','534','352','0');
INSERT INTO [t151017] VALUES (437,'٨٠ - وفككنا غل امرئ القيس عنـ ... ـه بعدما طال حبسه والعناء','535','352','0');
INSERT INTO [t151017] VALUES (438,'٨١ - وأقدناه رب غسان بالمنـ ... ـذر كرها، إذ لا تكال الدماء','536','352','0');
INSERT INTO [t151017] VALUES (439,'٨٢ - وأتيناهم بتسعة أملا ... ك كرام أسلابهم أغلاء','536','352','0');
INSERT INTO [t151017] VALUES (440,'٨٣ - ومع الجون حون آل بني الأو ... س عنود كأنها دفواء','538','352','0');
INSERT INTO [t151017] VALUES (441,'٨٤ - ما جزعنا تحت العجاجة، إذ ولـ ... ـت بأقفائها وحر الصلاء','539','352','0');
INSERT INTO [t151017] VALUES (442,'٨٥ - وولدنا عمرو بن أم أناس ... من قريب لما أتانا الحباء','540','352','0');
INSERT INTO [t151017] VALUES (443,'٨٦ - مثلها يخرج النصيحة للقو ... م فلاة من دونها أفلاء','541','352','0');
INSERT INTO [t151017] VALUES (444,'(٥) معلقة لبيد بن ربيعة','547','0','0');
INSERT INTO [t151017] VALUES (445,'فهرست أبيات معلقة لبيد بن ربيعة الصحابي رضي الله عنه','548','444','0');
INSERT INTO [t151017] VALUES (446,'معلقة لبيد بن ربيعة','552','444','0');
INSERT INTO [t151017] VALUES (447,'لبيد ونسبه','552','444','0');
INSERT INTO [t151017] VALUES (448,'أبو عقيل لبيد بن ربيعة بن جعفر بن كلاب بن ربيعة بن عامر بن صعصعة بن معاوية بن بكر بن هوازن بن منصور بن عكرمة بن خصفة بن قيس بن عيلان بن مضر بن نزار بن معد بن عدنان','552','447','0');
INSERT INTO [t151017] VALUES (449,'شيء من أخباره','552','444','0');
INSERT INTO [t151017] VALUES (450,'١ - عفت الديار محلها فمقامها ... بمنى تأبد غولها فرجامها','557','444','0');
INSERT INTO [t151017] VALUES (451,'٢ - مدافع الريان عري رسمها ... خلقا كما ضمن الوحي سلامها','558','444','0');
INSERT INTO [t151017] VALUES (452,'٣ - دمن تجرم بعد عهد أنيسها ... حجج خلون، حلالها وحرامها','560','444','0');
INSERT INTO [t151017] VALUES (453,'٤ - رزقت مرابيع النجوم وصابها ... ودق الرواعد، جودها فرهامها','561','444','0');
INSERT INTO [t151017] VALUES (454,'٥ - من كل سارية، وغاد مدجن ... وغشية، متجاوب إرزامها','562','444','0');
INSERT INTO [t151017] VALUES (455,'٦ - فعلا فروع الأيهقان، وأطفلت ... بالجلهتين ظباؤها ونعامها','563','444','0');
INSERT INTO [t151017] VALUES (456,'٧ - العين ساكنة على أطلائها ... عوذا تأجل بالقضاء بهامها','564','444','0');
INSERT INTO [t151017] VALUES (457,'٨ - وجلا السيول عن الطلول كأنها ... زبر تجد متونها أقلامها','566','444','0');
INSERT INTO [t151017] VALUES (458,'٩ - أو رجع واشمة أسف نؤورها ... كففا تعرض فوقهن وشامها','566','444','0');
INSERT INTO [t151017] VALUES (459,'١٠ - فوقفت أسألها، وكيف سؤالنا ... صما خوالد ما يبين كلامها؟','567','444','0');
INSERT INTO [t151017] VALUES (460,'١١ - عريت، وكان بها الجميع، فأنكروا ... منها، وغودر نؤيها وثمامها','569','444','0');
INSERT INTO [t151017] VALUES (461,'١٢ - شاقتك ظعن الحي حين تحملوا ... فتكنسوا قطنا تصر خيامها','570','444','0');
INSERT INTO [t151017] VALUES (462,'١٣ - من كل مخفوف يظل عصي ... زوج عليه كله وقرامها','571','444','0');
INSERT INTO [t151017] VALUES (463,'١٤ - زجلا كأن نعاج توضح فوقها ... وظباء وجرة عطفا أرامها','572','444','0');
INSERT INTO [t151017] VALUES (464,'١٥ - حفزت، وزايلها السراب كأنها ... أجزاع بيشة، أثلها ورضامها','573','444','0');
INSERT INTO [t151017] VALUES (465,'١٦ - بل ما تذكر من نوار، وقد نأت ... وتقطعت أسبابها ورمامها','574','444','0');
INSERT INTO [t151017] VALUES (466,'١٧ - مرية حلت بفيد، وجاوزت ... أهل الحجاز، فأين منك مرامها؟','575','444','0');
INSERT INTO [t151017] VALUES (467,'١٨ - بمشارق الجبلين، أو بمحجر ... فتضمنتها فردة فرخامها','577','444','0');
INSERT INTO [t151017] VALUES (468,'١٩ - فصوائق إن أيمنت فمظنة ... منها وحاف القهر، أو طلخامها','578','444','0');
INSERT INTO [t151017] VALUES (469,'٢٠ - فاقطع لبانة من تعرض وصله ... ولشر واصل خلة صرامها','578','444','0');
INSERT INTO [t151017] VALUES (470,'٢١ - واحب المجامل بالجزيل، وصرمه ... باق، إذا ضلعت، وزاغ قوامها','579','444','0');
INSERT INTO [t151017] VALUES (471,'٢٢ - بطليح أسفار تركن بقية ... منها فأحنق صلبها وسنامها','581','444','0');
INSERT INTO [t151017] VALUES (472,'٢٣ - فإذا تغالي لحمها فتحسرت ... وتقطعت بعد الكلال خدامها','582','444','0');
INSERT INTO [t151017] VALUES (473,'٢٤ - فلها هباب في الزمام كأنها ... صهباء خف مع الجنوب جهامها','583','444','0');
INSERT INTO [t151017] VALUES (474,'٢٥ - أو ملمع، وسقت لأحقب لاحه ... طرد الفحول وضربها وكدامها','584','444','0');
INSERT INTO [t151017] VALUES (475,'٢٦ - يغلو بها حدب الإكام مسحجا ... قد رابه عصيانها ووحامها','585','444','0');
INSERT INTO [t151017] VALUES (476,'٢٧ - بأحزة الثلبوت يربأ فوقها ... قفر المراقب خوفها آرامها','587','444','0');
INSERT INTO [t151017] VALUES (477,'٢٨ - حتى إذا سلخا جمادى ستة ... جزآ، فطال صيامه وصيامها','588','444','0');
INSERT INTO [t151017] VALUES (478,'٢٩ - رجعا بأمرهما إلى ذي مرة ... حصد، ونجح صريمة إبرامها','590','444','0');
INSERT INTO [t151017] VALUES (479,'٣٠ - ورمى دوابرها السفا، وتهيجت ... ريح المصايف، سومها وسهامها','591','444','0');
INSERT INTO [t151017] VALUES (480,'٣١ - فتنازعا سبطا، يطير ظلاله ... كدخان مشعلة يشب ضرامها','592','444','0');
INSERT INTO [t151017] VALUES (481,'٣٢ - مشمولة غلثت بنابت عرفج ... كدخان نار ساطع أسنامها','593','444','0');
INSERT INTO [t151017] VALUES (482,'٣٣ - فمضى وقدمها، وكانت عادة ... منه إذا هي عردت إقدامها','594','444','0');
INSERT INTO [t151017] VALUES (483,'٣٤ - فتوسطا عرض السري وصدعا ... مسجورة متجاورا قلامها','595','444','0');
INSERT INTO [t151017] VALUES (484,'٣٥ - محفوفة وسط اليراع، يظلها ... منه مصرع غابة، وقيامها','596','444','0');
INSERT INTO [t151017] VALUES (485,'٣٦ - أفتلك، أم وحشية مسبوعة ... خذلت، وهادية الصوار قوامها؟','597','444','0');
INSERT INTO [t151017] VALUES (486,'٣٧ - خنساء ضيعت الفرير، فلم يرم ... عرض الشقائق طوفها وبغامها','598','444','0');
INSERT INTO [t151017] VALUES (487,'٣٨ - لمعفر قهد تنازع شلوه ... غبس كواسب، لا يمن طعامها','600','444','0');
INSERT INTO [t151017] VALUES (488,'٣٩ - صادفن منها غرة، فأصبنها ... إلى المنايا لا تطيش سهامها','601','444','0');
INSERT INTO [t151017] VALUES (489,'٤٠ - باتت، وأسبل واكف من ديمة ... يروي الخمائل، دائما تسجامها','602','444','0');
INSERT INTO [t151017] VALUES (490,'٤١ - تجتاف أصلا قالصا متنبذا ... بعجوب أنقاء يميل هيامها','603','444','0');
INSERT INTO [t151017] VALUES (491,'٤٧ - يعلو طريقة منها متواتر ... في ليلة كفر النجوم غمامها','604','444','0');
INSERT INTO [t151017] VALUES (492,'٤٣ - وتضيئ في وجه الظلام منيرة ... كجمانة البحري سل نظامها','605','444','0');
INSERT INTO [t151017] VALUES (493,'٤٤ - حتى إذا انحسر الظلام، وأسفرت ... بكرت تزل عن الثرى أزلامها','606','444','0');
INSERT INTO [t151017] VALUES (494,'٤٥ - علهت تردد في نهاء صعائد ... سبعا تؤاما كاملا أيامها','607','444','0');
INSERT INTO [t151017] VALUES (495,'٤٦ - حتى إذا يئست، وأسحق حالق ... لم يبله إرضاعها، وفطامها','608','444','0');
INSERT INTO [t151017] VALUES (496,'٤٧ - وتسمعت رز الأنيس فراعها ... عن ظهر غيب، والأنيس سقامها','609','444','0');
INSERT INTO [t151017] VALUES (497,'٤٨ - فغدت كلا الفرجين تحسب أنه ... مولى المخافة خلفها وأمامها','610','444','0');
INSERT INTO [t151017] VALUES (498,'٤٩ - حتى إذا يئس الرماة وأرسلوا ... غضفا دواجن قافلا أعصامها','612','444','0');
INSERT INTO [t151017] VALUES (499,'٥٠ - فلحقن واعتكرت لها مدرية ... كالشمهري حدها وتمامها','613','444','0');
INSERT INTO [t151017] VALUES (500,'٥١ - لتذودهن، وأيقنت إن لم تذد ... أن قد أحم مع الحتوف حمامها','614','444','0');
INSERT INTO [t151017] VALUES (501,'٥٢ - فتقصدت منها كساب، فضرجت ... بدم، وغودر في المكر سحامها','615','444','0');
INSERT INTO [t151017] VALUES (502,'٥٣ - فبتلك، إذ رقص اللوامع بالضحي ... واجتاب أردية السراب إكامها','617','444','0');
INSERT INTO [t151017] VALUES (503,'٥٤ - أقضي اللبانة لا أفرط ريبة ... أو أن يلوم بحاجة لوامها','618','444','0');
INSERT INTO [t151017] VALUES (504,'٥٥ - أو لم تكن تدري نوار بأنني ... وصال عقد حبائل جذامها','619','444','0');
INSERT INTO [t151017] VALUES (505,'٥٦ - تراك أمكنة إذا لم أرضها ... أو يعتلق بعض النفوس حمامها','620','444','0');
INSERT INTO [t151017] VALUES (506,'٥٧ - بل أنت لا تدرين: كم من ليلة ... طلق لذيذ لهوها وندامها','621','444','0');
INSERT INTO [t151017] VALUES (507,'٥٨ - قدبت سامرها وغاية تاجر ... وافيت، إذ رفعت، وعز مدامها','622','444','0');
INSERT INTO [t151017] VALUES (508,'٥٩ - أغلى السباء بكل أدكن عاتق ... أو جونة قدحت، وفض ختامها','624','444','0');
INSERT INTO [t151017] VALUES (509,'٦٠ - باكرت حاجتها الدجاج بسحرة ... لأعل منها حين هب نيامها','625','444','0');
INSERT INTO [t151017] VALUES (510,'٦١ - وغداة ريح قد ورغت وقرة ... إذ أصبحت بيد الشمال زمامها','626','444','0');
INSERT INTO [t151017] VALUES (511,'٦٢ - بصبوح صافية وجذب كرينة ... بموتر تأتاله إبهامها','628','444','0');
INSERT INTO [t151017] VALUES (512,'٦٤ - فعلوت مرتقبا على ذي هبوة ... خرج إلى أعلامهن قتامها','630','444','0');
INSERT INTO [t151017] VALUES (513,'٦٥ - حتى إذا ألقت يدا في كافر ... وأجن عورات الثغور ظلامها','631','444','0');
INSERT INTO [t151017] VALUES (514,'٦٦ - أسهلت، وانتصبت كجذع منيفة ... جرداء يحصر دونها جرامها','632','444','0');
INSERT INTO [t151017] VALUES (515,'٦٧ - رفعتها طرد النعام، وفوقه ... حتى إذا سخنت، وخف عظامها','633','444','0');
INSERT INTO [t151017] VALUES (516,'٦٨ - قلقت رحالتها، وأسبل نحرها ... وابتل من زبد الحميم حزامها','635','444','0');
INSERT INTO [t151017] VALUES (517,'٧٠ - وكثيرة غرباؤها مجهولة ... ترجي نوافلها، ويخشى ذامها','637','444','0');
INSERT INTO [t151017] VALUES (518,'٧١ - غلب تشذر بالذحول كأنها ... جن البدي رواسيا أقدامها','639','444','0');
INSERT INTO [t151017] VALUES (519,'٧٢ - أنكرت باطلها، وبؤت بحقها ... يوما، ولم يفخر علي كرامها','640','444','0');
INSERT INTO [t151017] VALUES (520,'٧٣ - وجزور أيسار دعوت لحتفها ... بمغالق متشابه أجسامها','641','444','0');
INSERT INTO [t151017] VALUES (521,'٧٤ - أدعو بهن لعاقر، أو مطفل ... بدلت لجيران الجميع لحامها','642','444','0');
INSERT INTO [t151017] VALUES (522,'٧٥ - فالضيف والجار الغريب كأنما ... هبطا تبالة مخصبا أهضامها','644','444','0');
INSERT INTO [t151017] VALUES (523,'٧٦ - تأوي إلى الأطناب كل رذية ... مثل البلية، قالص أهدامها','645','444','0');
INSERT INTO [t151017] VALUES (524,'٧٧ - ويكللون إذا الرياح تناوحت ... خلجا تمد شوارعا أيتامها','646','444','0');
INSERT INTO [t151017] VALUES (525,'٧٨ - إنا إذا التقت المجامع لم يزل ... منا لزاز عظيمة جشامها','647','444','0');
INSERT INTO [t151017] VALUES (526,'٧٩ - ومقسم يعطي العشيرة حقها ... ومغذمر لحقوقها هضامها','648','444','0');
INSERT INTO [t151017] VALUES (527,'٨٠ - فضلا، وذو كرم يعين على الندى ... سمح كسوب رغائب غنامها','650','444','0');
INSERT INTO [t151017] VALUES (528,'٨١ - من معشر سنت لهم آباؤهم ... ولكل قوم سنة وإمامها','651','444','0');
INSERT INTO [t151017] VALUES (529,'٨٢ - إن يفزعوا تلق المغافر عندهم ... والسن يلمع كالكواكب لامها','652','444','0');
INSERT INTO [t151017] VALUES (530,'٨٣ - لا يطبعون، ولا يبور فعالهم ... إذ لا تميل مع الهوى أحلامها','653','444','0');
INSERT INTO [t151017] VALUES (531,'٨٤ - فاقنع بما قسم المليك، فإنما ... قسم الخلائق بيننا علامها','655','444','0');
INSERT INTO [t151017] VALUES (532,'٨٥ - وإذا الأمانة قسمت في معشر ... أوفى بأعظم حظنا قسامها','656','444','0');
INSERT INTO [t151017] VALUES (533,'٨٦ - فبنى لنا بيتا رفيعا سمكه ... فسما إليه كهلها وغلامها','657','444','0');
INSERT INTO [t151017] VALUES (534,'٨٧ - وهم السعادة إذا العشيرة أفظعت ... وهمو فوارسها، وهم حكامها','659','444','0');
INSERT INTO [t151017] VALUES (535,'٨٨ - وهمو ربيع للمجاور فيهمو ... والمرملات إذا تطاول عامها','660','444','0');
INSERT INTO [t151017] VALUES (536,'٨٩ - وهم العشيرة أن يبطئ حاسد ... أو أن يلوم مع العدى لوامها','661','444','0');
INSERT INTO [t151017] VALUES (537,'(٦) معلقة عنترة بن شداد العبسي','664','0','0');
INSERT INTO [t151017] VALUES (538,'فهرست أبيات معلقة عنترة بن شداد العبسي','665','537','0');
INSERT INTO [t151017] VALUES (539,'معلقة عنترة بن شداد العبسي','670','537','0');
INSERT INTO [t151017] VALUES (540,'اسمه ونسبه','670','537','0');
INSERT INTO [t151017] VALUES (541,'عنترة بن شداد الذي ينتهي نسبة إلى قبيلة بني عبس، فبني غطفان، فبني قيس، أمه أمة حبشية، يقال لها: زبيبة،','670','540','0');
INSERT INTO [t151017] VALUES (542,'١ - هل غادر الشعراء من متردم؟ ... أم هل عرفت الدار بعد توهم؟','674','537','0');
INSERT INTO [t151017] VALUES (543,'٢ - إلا رواكد بينهن خصائص ... وبقية من نؤيها المجرنثم','676','537','0');
INSERT INTO [t151017] VALUES (544,'٣ - أعياك رسم الدار لم يتكلم ... حتى تكلم كالأصم الأعجم','677','537','0');
INSERT INTO [t151017] VALUES (545,'٤ - ولقد حبست بها طويلا ناقتي ... ترغو إلى سفع الرواكد جثم','678','537','0');
INSERT INTO [t151017] VALUES (546,'٥ - يا دار عبلة بالجواء تكلمي ... وعمي صباحا، دار عبلة، واسلمي','679','537','0');
INSERT INTO [t151017] VALUES (547,'٦ - دار لانسة غضيض طرفها ... طوع العناق، لذيذة المتبسم','680','537','0');
INSERT INTO [t151017] VALUES (548,'٧ - فوقفت فيها ناقتي، وكأنها ... فدن لأقضي حاجة المتلوم','681','537','0');
INSERT INTO [t151017] VALUES (549,'٨ - وتحل عبلة بالجواء، وأهلنا ... بالحزن، فالصمان، فالمتثلم','682','537','0');
INSERT INTO [t151017] VALUES (550,'٩ - وتظل عبلة في الخزوز تجرها ... وأظل في حلق الحديد المبهم','683','537','0');
INSERT INTO [t151017] VALUES (551,'١٠ - حييت من طلل تقادم عهده ... أقوى وأقفر بعد أم الهيثم','684','537','0');
INSERT INTO [t151017] VALUES (552,'١١ - حلت بأرض الزائرين، فأصبحت ... عسرا علي طلابك، ابنة مخرم','686','537','0');
INSERT INTO [t151017] VALUES (553,'١٢ - علقتها عرضا، وأقتل قومها ... زعما -لعمر أبيك- ليس بمزعم','687','537','0');
INSERT INTO [t151017] VALUES (554,'١٣ - ولقد نزلت، فلا تظني غيره ... مني بمنزلة المحب المكرم','689','537','0');
INSERT INTO [t151017] VALUES (555,'١٤ - كيف المزار وقد تربع أهلها ... بعنيزتين، وأهلنا بالغيلم','690','537','0');
INSERT INTO [t151017] VALUES (556,'١٥ - إن كنت أزمعت الفراق، فإنما ... زمت ركابكمو بليل مظلم','691','537','0');
INSERT INTO [t151017] VALUES (557,'١٦ - ما راعني إلا حمولة أهلها ... وسط الديار تسف حب الخمخم','692','537','0');
INSERT INTO [t151017] VALUES (558,'١٧ - فيها اثنتان وأربعون حلوبة ... سوداء كخافية الغراب الأسحم','693','537','0');
INSERT INTO [t151017] VALUES (559,'١٨ - فصغارها مثل الدبى، وكبارها ... مثل الضفادع في غدير مفعم','696','537','0');
INSERT INTO [t151017] VALUES (560,'١٩ - ولقد نظرت غداة فارق أهلها ... نظر المحب بطرف عيني مغرم','697','537','0');
INSERT INTO [t151017] VALUES (561,'٢٠ - وأحب لو أسقيك غير تملق ... والله، من سقم أصابك من دمي','698','537','0');
INSERT INTO [t151017] VALUES (562,'٢١ - إذ تستبيك بذي غروب واضح ... عذب مقبله لذيذ المطعم','699','537','0');
INSERT INTO [t151017] VALUES (563,'٢٢ - وكأنما نظرت بعيني شادن ... رشأ من الغزلان ليس بتوءم','700','537','0');
INSERT INTO [t151017] VALUES (564,'٢٣ - وكأن فارة تاجر بقسيمة ... سبقت عوارضها إليك من الفم','701','537','0');
INSERT INTO [t151017] VALUES (565,'٢٤ - أو روضة أنفا تضمن نبتها ... غيث قليل الدمن ليس بمعلم','702','537','0');
INSERT INTO [t151017] VALUES (566,'٢٥ - أو عاتقا من أذرعات معتقا ... مما تعتقه ملوك الأعجم','704','537','0');
INSERT INTO [t151017] VALUES (567,'٢٦ - نظرت إليه بمقلة مكحولة ... نظر المليل بطرفه المتقسم','705','537','0');
INSERT INTO [t151017] VALUES (568,'٢٨ - ولقد مررت بدار عبلة بعدما ... لعب الربيع بربعها المتوسم','706','537','0');
INSERT INTO [t151017] VALUES (569,'٢٩ - جادت عليه كل بكر حرة ... فتركن كل حديقة كالدرهم','707','537','0');
INSERT INTO [t151017] VALUES (570,'٣٠ - سحا وتساكبا، فكل عشية ... يجري عليها الماء لم يتصرم','708','537','0');
INSERT INTO [t151017] VALUES (571,'٣١ - وخلا الذباب بها فليس ببارح ... غردا كفعل الشارب المترنم','709','537','0');
INSERT INTO [t151017] VALUES (572,'٣٢ - هزجا يحك ذراعه بذراعه ... قدح المكب على الزناد الأجذم','711','537','0');
INSERT INTO [t151017] VALUES (573,'٣٣ - تمسي وتصبح فوق ظهر حشية ... وأبيت فوق سراة أدهم ملجم','712','537','0');
INSERT INTO [t151017] VALUES (574,'٣٤ - وحشيتي سرج على عبل الشوى ... نهد مراكله نبيل المحزم','713','537','0');
INSERT INTO [t151017] VALUES (575,'٣٥ - هل تبلغني دارها شدنية ... لعنت بمحروم الشراب مصرم؟','714','537','0');
INSERT INTO [t151017] VALUES (576,'٣٦ - خطارة غب السرى زيافة ... تطس الإكام بوخد خف ميثم','715','537','0');
INSERT INTO [t151017] VALUES (577,'٣٧ - وكأنما أقص الإكام عشية ... بقريب بين المنسمين مصلم','716','537','0');
INSERT INTO [t151017] VALUES (578,'٣٨ - تأوي له قلص النعام كما أوت ... حزق يمانية لأعجم طمطم','717','537','0');
INSERT INTO [t151017] VALUES (579,'٣٩ - يتبعن قلة رأسه، وكأنه ... حرج على نعش لهن مخيم','718','537','0');
INSERT INTO [t151017] VALUES (580,'٤٠ - صعل يعود بذي العشيرة بيضه ... كالعبد ذي الفرو الطويل الأصلم','719','537','0');
INSERT INTO [t151017] VALUES (581,'٤١ - شربت بماء الدحرضين، فأصبحت ... زوراء تنفر عن حياض الديلم','720','537','0');
INSERT INTO [t151017] VALUES (582,'٤٢ - وكأنما تنأى بجانب دفها الـ ... ـوحشي من هزج العشي مؤوم','721','537','0');
INSERT INTO [t151017] VALUES (583,'٤٣ - هر جنيب كلما عطفت له ... غضبى اتقاها باليدين وبالفم','722','537','0');
INSERT INTO [t151017] VALUES (584,'٤٤ - أبقى لها طول السفار مقرمدا ... سندا، ومثل دعائم المتخيم','723','537','0');
INSERT INTO [t151017] VALUES (585,'٤٥ - بركت على ماء الرداع كأنما ... بركت على قصب أجش مهضم','724','537','0');
INSERT INTO [t151017] VALUES (586,'٤٦ - وكأن ربا، أو كحيلا معقدا ... حش الوقود به جوانب قمقم','724','537','0');
INSERT INTO [t151017] VALUES (587,'٤٨ - إن تغدفي دوني القناع، فإنني ... طب بأخذ الفارس المستلئم','727','537','0');
INSERT INTO [t151017] VALUES (588,'٤٩ - أثني علي بما علمت فإنني ... سهل مخالقتي، إذا لم أظلم','728','537','0');
INSERT INTO [t151017] VALUES (589,'٥٠ - وإذا ظلمت فإن ظلمي باسل ... مر مذاقته كطعم العلقم','729','537','0');
INSERT INTO [t151017] VALUES (590,'٥١ - ولقد شربت من المدامة بعدما ... ركد الهواجر بالمشوف المعلم','731','537','0');
INSERT INTO [t151017] VALUES (591,'٥٢ - بزجاجة صفراء ذات أسرة ... قرنت بأزهر في الشمال مفدم','732','537','0');
INSERT INTO [t151017] VALUES (592,'٥٣ - فإذا شربت، فإنني مستهلك ... مالي، وعرضي وافر لم يكلم','734','537','0');
INSERT INTO [t151017] VALUES (593,'٥٤ - وإذا صحوت، فما أقصر عن ندى ... وكما علمت شمائلي وتكرمي','735','537','0');
INSERT INTO [t151017] VALUES (594,'٥٥ - وحليل غانية تركت مجدلا ... تمكو فريصته كشدق الأعلم','737','537','0');
INSERT INTO [t151017] VALUES (595,'٥٦ - سبقت يداي له بعاجل طعنة ... ورشاش نافذة كلون العندم','738','537','0');
INSERT INTO [t151017] VALUES (596,'٥٧ - هلا سالت الخيل، يا ابنة مالك ... إن كنت جاهلة بما لم تعلمي','739','537','0');
INSERT INTO [t151017] VALUES (597,'٥٨ - لا تسأليني، واسألي بي صحبتي ... يملأ يديك تعففي وتكرمي','740','537','0');
INSERT INTO [t151017] VALUES (598,'٥٩ - إذ لا أزال على رحالة سابح ... نهد تعاوره الكماه مكلم','742','537','0');
INSERT INTO [t151017] VALUES (599,'٦٠ - طورا يجرد للطعان، وتارة ... يأوي إلى حصد القسي عرمرم','743','537','0');
INSERT INTO [t151017] VALUES (600,'٦١ - يخبرك من شهد الوقيعة أنني ... أغشى الوغى، وأعف عند المغنم','743','537','0');
INSERT INTO [t151017] VALUES (601,'٦٢ - فأرى مغانم، لو أشاء حويتها ... ويصدني عنها الحيا وتكرمي','745','537','0');
INSERT INTO [t151017] VALUES (602,'٦٣ - ومدجج كره الكماه نزاله ... لا ممعن هربا، ولا مستسلم','746','537','0');
INSERT INTO [t151017] VALUES (603,'٦٤ - جادت يداي له بعاجل طعنة ... بمثقف صدق الكعوب مقوم','747','537','0');
INSERT INTO [t151017] VALUES (604,'٦٥ - برحيبة الفرغين يهدي جرسها ... بالليل معتس الذئاب الضرم','748','537','0');
INSERT INTO [t151017] VALUES (605,'٦٦ - فشككت بالرمح الأصم ثيابه ... ليس الكريم على القنا بمحرم','749','537','0');
INSERT INTO [t151017] VALUES (606,'٦٧ - فتركته جزر السباع ينشنه ... ما بين قلة رأسه والمعصم','750','537','0');
INSERT INTO [t151017] VALUES (607,'٦٨ - ومسك سابغة هتكت فروجها ... بالسيف عن حامي الحقيقة معلم','751','537','0');
INSERT INTO [t151017] VALUES (608,'٦٩ - ربذ يداه بالقداح إذا شتا ... هتاك غايات التجار ملوم','753','537','0');
INSERT INTO [t151017] VALUES (609,'٧٠ - لما رآني قد نزلت أريده ... أبدى نواجذه لغير تبسم','754','537','0');
INSERT INTO [t151017] VALUES (610,'٧١ - فطعنته بالرمح، ثم علوته ... بمهند صافي الحديدة مخذم','755','537','0');
INSERT INTO [t151017] VALUES (611,'٧٢ - عهدي به مد النهار كأنما ... خضب البنان، ورأسه بالعظلم','756','537','0');
INSERT INTO [t151017] VALUES (612,'٧٣ - بطل كأن ثيابه في سرحة ... يحذي نعال السبت ليس بتوءم','757','537','0');
INSERT INTO [t151017] VALUES (613,'٧٤ - ولقد ذكرتك، والرماح نواهل ... مني، وبيض الهند، تقطر من دمي','759','537','0');
INSERT INTO [t151017] VALUES (614,'٧٥ - فوددت تقبيل السيوف لأنها ... لمعت كبارق ثغرك المتبسم','761','537','0');
INSERT INTO [t151017] VALUES (615,'٧٦ - يا شاة ما قنص لمن حلت له ... حرمت علي، وليتها لم تحرم','761','537','0');
INSERT INTO [t151017] VALUES (616,'٧٧ - فبعثت جاريتي، فقلت لها: اذهبي ... فتجسسي أخبارها لي، واعلمي','763','537','0');
INSERT INTO [t151017] VALUES (617,'٧٨ - قالت: رأيت من الأعادي غرة ... والشاة ممكنة لمن هو مرتمي','764','537','0');
INSERT INTO [t151017] VALUES (618,'٧٩ - وكأنها التفتت بجيد جداية ... رشأ من الغزلان حر أرثم','766','537','0');
INSERT INTO [t151017] VALUES (619,'٨٠ - نبئت عمرا غير شاكر نعمتي ... والكفر مخبثة لنفس المنعم','767','537','0');
INSERT INTO [t151017] VALUES (620,'٨١ - ولقد حفظت وصاه عمي بالضحى ... إذ تقلص الشفتان عن وضح الفم','768','537','0');
INSERT INTO [t151017] VALUES (621,'٨٢ - في حومة الموت التي لا تشتكي ... غمرتها الأبطال غير تغمغم','769','537','0');
INSERT INTO [t151017] VALUES (622,'٨٣ - إذ يتقون بي الأسنة لم أخم ... عنها، ولكني تضايق مقدمي','770','537','0');
INSERT INTO [t151017] VALUES (623,'٨٤ - لما سمعت نداء مرة قد علا ... وابني ربيعة في الغبار الأقتم','771','537','0');
INSERT INTO [t151017] VALUES (624,'٨٥ - ومحلم يسعون تحت لوائهم ... والموت تحت لواء آل محلم','772','537','0');
INSERT INTO [t151017] VALUES (625,'٨٦ - أيقنت أن سيكون عند لقائهم ... ضرب يطير عن الفراخ الجثم','773','537','0');
INSERT INTO [t151017] VALUES (626,'٨٧ - لما رأيت القوم أقبل جمعهم ... يتذامرون، كررت غير مذمم','774','537','0');
INSERT INTO [t151017] VALUES (627,'٨٨ - ولقد هممت بغارة في ليلة ... سوداء حالكة كلون الأدلم','775','537','0');
INSERT INTO [t151017] VALUES (628,'٨٩ - يدعون عنتر، والرماح كأنها ... أشطان بئر في لبان الأدهم','776','537','0');
INSERT INTO [t151017] VALUES (629,'٩٠ - كيف التقدم، والرماح كأنها ... برق تلألأ في السحاب الأركم؟','777','537','0');
INSERT INTO [t151017] VALUES (630,'٩١ - كيف التقدم، والسيوف كأنها ... غوغا جراد في كثيب أهيم','778','537','0');
INSERT INTO [t151017] VALUES (631,'٩٢ - فإذا اشتكى وقع القنا بلبانه ... أدنيته من سل عضب مخذم','778','537','0');
INSERT INTO [t151017] VALUES (632,'٩٣ - ما زلت أرميهم بغرة وجهه ... ولبانه حتى تسربل بالدم','779','537','0');
INSERT INTO [t151017] VALUES (633,'٩٤ - قازور من وقع القنا بلبانه ... وشكا إلي بعبرة وتحمحم','780','537','0');
INSERT INTO [t151017] VALUES (634,'٩٥ - لو كان يدري ما المحاورة؟ اشتكى ... ولكان، لو علم الكلام، مكلمي','781','537','0');
INSERT INTO [t151017] VALUES (635,'٩٦ - آسيته في كل أمر نابنا ... هل بعد أسوة صاحب من مذمم؟','783','537','0');
INSERT INTO [t151017] VALUES (636,'٩٧ - فتركت سيدهم لأول طعنة ... يكبو صريعا لليدين، وللفم','784','537','0');
INSERT INTO [t151017] VALUES (637,'٩٨ - ركبت فيه صعدة، هندية ... سحماء، تلمع، ذات حد لهذم','785','537','0');
INSERT INTO [t151017] VALUES (638,'٩٩ - ولقد شفى نفسي، وأبرأ سقمها ... قيل الفوارس: ويك عنتر أقدم','786','537','0');
INSERT INTO [t151017] VALUES (639,'١٠٠ - والخيل تقتحم الخبار عوابسا ... من بين شيظمة وأجرد شيظم','788','537','0');
INSERT INTO [t151017] VALUES (640,'١٠١ - ذلل ركابي حيث شئت مشايعي ... قلبي، وأحفزه بأمر مبرم','789','537','0');
INSERT INTO [t151017] VALUES (641,'١٠٢ - ولقد خشيت بأن أموت ولم تكن ... للحرب دائرة على ابني ضمضم','790','537','0');
INSERT INTO [t151017] VALUES (642,'١٠٣ - الشاتمي عرضي، ولم اشتمها ... والناذرين إذا لم ألقهما دمي','791','537','0');
INSERT INTO [t151017] VALUES (643,'١٠٤ - أسد علي، وفي العدو أذلة ... هذا، لعمرك، فعل مولى الأشأم','793','537','0');
INSERT INTO [t151017] VALUES (644,'١٠٥ - إن يفعلا فلقد تركت أباهما ... جزر السباع، وكل نسر قشعم','794','537','0');
INSERT INTO [t151017] VALUES (645,'١٠٦ - إني عداني أن أزورك، فاعلمي ... ما قد علمت، وبعض ما لم تعلمي','795','537','0');
INSERT INTO [t151017] VALUES (646,'١٠٧ - حالت رماح ابني بغيض دونكم ... وزوت جواني الحرب من لم يجرم','796','537','0');
INSERT INTO [t151017] VALUES (647,'١٠٨ - يا عبل لو أبصرتني لرأيتني ... في الحرب أقدم كالهزبر الضيغم','797','537','0');
INSERT INTO [t151017] VALUES (648,'١٠٩ - ولقد كررت المهر يدمى نحره ... حتى اتقتني الخيل بابني حذلم','798','537','0');
INSERT INTO [t151017] VALUES (649,'١١٠ - إذ يتقي عمرو، وأذعن غدوة ... حذر الأسنة، إذ شرعن لدلهم','799','537','0');
INSERT INTO [t151017] VALUES (650,'١١١ - يحمي كتيبه، ويسعى خلفها ... يفري عواقبها كلدغ الأرقم','800','537','0');
INSERT INTO [t151017] VALUES (651,'١١٢ - ولقد كشفت الخدر عن مربوبة ... ولقد رقدت على نواشر معصم','801','537','0');
INSERT INTO [t151017] VALUES (652,'١١٣ - ولرب يوم قد لهوت وليلة ... بمسور ذي بارقين مسوم','802','537','0');
INSERT INTO [t151017] VALUES (653,'(٧) معلقة زهير بن أبي سلمى المزني','804','0','0');
INSERT INTO [t151017] VALUES (654,'فهرست أبيات معلقة زهير بن أبي سلمى المزني','805','653','0');
INSERT INTO [t151017] VALUES (655,'(معلقة زهير) نسب زهير وأسباب نظمه المعلقة','808','653','0');
INSERT INTO [t151017] VALUES (656,'١ - أمن أم أوفى دمنة لكم تكلم ... بحومانة الدراج فالمتثلم؟','809','653','0');
INSERT INTO [t151017] VALUES (657,'٢ - ديار بالرقمتين كأنها ... مراجع وشم في نواشر معصم','811','653','0');
INSERT INTO [t151017] VALUES (658,'٣ - بها الهين، والأرام يمشين خلفة ... وأطلاؤها ينهضن من كل مجثم','813','653','0');
INSERT INTO [t151017] VALUES (659,'٤ - وقفت بها من بعد عشرين حجة ... فلأيا عرفت الدار بعد توهم','814','653','0');
INSERT INTO [t151017] VALUES (660,'فائدة','815','659','0');
INSERT INTO [t151017] VALUES (661,'٥ - أثافي سفعا في معرس مرجل ... ونؤيا كجذم الحوض لم يتثلم','816','653','0');
INSERT INTO [t151017] VALUES (662,'٦ - فلما عرفت الدار قلت لربعها ... ألا انعم صباحا، أيها الربع، وأسلم','817','653','0');
INSERT INTO [t151017] VALUES (663,'٧ - تبصر خليلي، هل ترى من ظعائن ... تحملن بالعلياء من فوق جرثم؟','819','653','0');
INSERT INTO [t151017] VALUES (664,'٨ - جعلن القنان عن يمين، وحزنه ... وكم بالقنان من محل ومحرم','822','653','0');
INSERT INTO [t151017] VALUES (665,'٩ - وعالين أنماطا عتاقا وكلة ... وراد الحواشي لونها لون عندم','823','653','0');
INSERT INTO [t151017] VALUES (666,'١٠ - ظهرن من السوبان، ثم جزعنه ... على كل قيني قشيب ومفأم','825','653','0');
INSERT INTO [t151017] VALUES (667,'١١ - ووركن في السوبان يعلون متنه ... عليهن دل الناعم المتنعم','826','653','0');
INSERT INTO [t151017] VALUES (668,'١٢ - كأن فتات العهن في كل منزل ... نزلن به حب الفنا لم يحطم','826','653','0');
INSERT INTO [t151017] VALUES (669,'١٣ - بطلان بكورا، واستحرن بسحرة ... فهن ووادي الرس كاليد في الفم','827','653','0');
INSERT INTO [t151017] VALUES (670,'١٤ - فلما وردن الماء زرقا جمامه ... وضعن عصى الحاضر المتخيم','828','653','0');
INSERT INTO [t151017] VALUES (671,'١٥ - تذكرنب الأحلام ليلى، ومن تطف ... عليه خيالات الأحبة يحلم','829','653','0');
INSERT INTO [t151017] VALUES (672,'١٦ - وفيهن ملهى للطيف، ومنظر ... أنيق لعين الناظر المتوسم','831','653','0');
INSERT INTO [t151017] VALUES (673,'١٧ - سعى ساعيا غيظ بن مرة بعدما ... تبزل ما بين العشيرة بالدم','832','653','0');
INSERT INTO [t151017] VALUES (674,'١٨ - فأقسمت بالبيت الذي طاف حوله ... رجل بنوه من قريش وجرهم','833','653','0');
INSERT INTO [t151017] VALUES (675,'١٩ - يمينا لنعم السيدان وجدتما ... على كل حال من سحيل ومبرم','836','653','0');
INSERT INTO [t151017] VALUES (676,'١٩ - يمينا لنعم السيدان وجدتما ... على كل حال من سحيل ومبرم','836','675','0');
INSERT INTO [t151017] VALUES (677,'٢٠ - تداركتما عبسا وذبيان بعدما ... تفانوا، ودقوا بينهم عطر منشم','837','653','0');
INSERT INTO [t151017] VALUES (678,'٢١ - وقد قلتما: إن ندرك السلم واسعا ... بمال ومعروف من القول نسلم','839','653','0');
INSERT INTO [t151017] VALUES (679,'٢٢ - فأصبحتما منها على خير موطن ... بعيدين فيها من عقوق ومأثم','840','653','0');
INSERT INTO [t151017] VALUES (680,'٢٣ - عظيمين في عليا معد هديتما ... ومن يستبح كنزا من المجد يعظم','842','653','0');
INSERT INTO [t151017] VALUES (681,'٢٤ - وأصبح يحدى فيهمو من تلادكم ... مغانم شتى من إفال مزنم','843','653','0');
INSERT INTO [t151017] VALUES (682,'٢٥ - تعفى الكلوم بالمئين، فأصبحت ... ينجمها من ليس فيها بمجرم','845','653','0');
INSERT INTO [t151017] VALUES (683,'٢٦ - ينجمها قوم لقوم غرامة ... وبم يهريقوا بينهم ملء محجم','846','653','0');
INSERT INTO [t151017] VALUES (684,'٢٧ - ألا أبلغ الأحلاف عني رسالة ... وذبيان هل أقسمتمو كل مقسم؟','847','653','0');
INSERT INTO [t151017] VALUES (685,'٢٨ - فلا تكتمن الله ما في صدوركم ... ليخفى، ومهما يكتم الله يعلم','848','653','0');
INSERT INTO [t151017] VALUES (686,'٢٩ - يؤخر، فيوضع في كتاب فيدخر ... ليوم الحساب! أو يعجل فينقم','850','653','0');
INSERT INTO [t151017] VALUES (687,'٣٠ - وما الحرب إلا ما علمتم وذقتمو ... وما هو عنها بالحديث المرجم','850','653','0');
INSERT INTO [t151017] VALUES (688,'٣١ - متى تبعثوها تبعثوها ذميمة ... وتضر إذا ضريتموها فتضرم','852','653','0');
INSERT INTO [t151017] VALUES (689,'٣٢ - فتعرككمو عرك الرحى بثفالها ... وتلقح كشافا، ثم تنتج، فتتئم','853','653','0');
INSERT INTO [t151017] VALUES (690,'٣٣ - فتنتج لكم غلمان أشأم كلهم ... كأحمر عاد، ثم ترضع فتفطم','855','653','0');
INSERT INTO [t151017] VALUES (691,'٣٤ - فتغلل لكم ما لا تغل لأهلها ... قرى بالعراق من قفيز ودرهم','856','653','0');
INSERT INTO [t151017] VALUES (692,'٣٥ - لحي حلال يعصم الماس أمرهم ... إذا طرقت إحدى الليالي بمعظم','857','653','0');
INSERT INTO [t151017] VALUES (693,'٣٦ - كرام، فلا ذو الضغن يدرك تبله ... ولا الجارم الجاني عليهم بمسلم','859','653','0');
INSERT INTO [t151017] VALUES (694,'٣٧ - رعوا ظمأهم حتى إذا تم أوردوا ... غمارا تسيل بالسلاح وبالدم','860','653','0');
INSERT INTO [t151017] VALUES (695,'٣٨ - فقضوا منايا بينهم، ثم أصدروا ... إلى كلأ مستوبل متوخم','861','653','0');
INSERT INTO [t151017] VALUES (696,'٣٩ - لعمري لنعم الحي جر عليهمو ... بما لا يواتيهم حصين بن ضمضم','862','653','0');
INSERT INTO [t151017] VALUES (697,'٤٠ - وكان طوى كشحا على مستكنة ... فلا هو أبداها، ولم يتقدم','864','653','0');
INSERT INTO [t151017] VALUES (698,'٤١ - وقال: سأقضي حاجتي، ثم أتقي ... عدوي بألف من ورائي ملجك','865','653','0');
INSERT INTO [t151017] VALUES (699,'٤٢ - فشد ولم يفزع بيوتا كثيرة ... لدى حيث ألقت رحلها أم قشعم','866','653','0');
INSERT INTO [t151017] VALUES (700,'٤٣ - لدى أسد شاكي السلاح مقذف ... له لبد أظفاره لم تقلم','867','653','0');
INSERT INTO [t151017] VALUES (701,'٤٤ - جريء متى يظلم يعاقب بظلمه ... سريعا، وإلا يبد بالظلم يظلم','869','653','0');
INSERT INTO [t151017] VALUES (702,'٤٥ - لعمرك ما جرت عليهم رماحهم ... دم ابن نهيك، أو قتيل المثلم','871','653','0');
INSERT INTO [t151017] VALUES (703,'٤٦ - ولا شاركت في الموت في دم نوفل ... ولا وهب منها ولا ابن المخزم','871','653','0');
INSERT INTO [t151017] VALUES (704,'٤٧ - فكلا أراهم أصبحوا يعقلونه ... صحيحات ألف بعد ألف مصتم','872','653','0');
INSERT INTO [t151017] VALUES (705,'٤٨ - ومن يعص أطراف الزجاجة فإنه ... يطيع العوالي ركبت كل لهذم','874','653','0');
INSERT INTO [t151017] VALUES (706,'٤٩ - ومن يوف لا يذمم، ومن يفض قلبه ... إلى مطمئن البر لا يتجمجم','875','653','0');
INSERT INTO [t151017] VALUES (707,'٥٠ - ومن هاب أسباب المنايا ينلنه ... ولو رام أسباب السماء بسلم','876','653','0');
INSERT INTO [t151017] VALUES (708,'٥١ - ومن يك ذا فضل، فيبخل بفضله ... على قومه يستغن عنه ويذمم','878','653','0');
INSERT INTO [t151017] VALUES (709,'٥٢ - ومن لا يزل يسترحل الناس نفسه ... ولا يعفها يوما من الذم يندم','879','653','0');
INSERT INTO [t151017] VALUES (710,'٥٣ - ومن يغترب يحسب عدوا صديقه ... ومن لا يكرم نفسه لا يكرم','880','653','0');
INSERT INTO [t151017] VALUES (711,'٥٤ - ومن لا يذد عن حوضه بسلاحه ... يهدم، ومن لا يظلم الناس يظلم','881','653','0');
INSERT INTO [t151017] VALUES (712,'٥٥ - ومن لا يصانع في أمور كثيرة ... يضرس بأنياب ويوطأ بمنسم','882','653','0');
INSERT INTO [t151017] VALUES (713,'٥٦ - ومن يجعل المعروف من دون عرضه ... يفره ومن لا يتق الشتم يشتم','883','653','0');
INSERT INTO [t151017] VALUES (714,'٥٧ - ومن يجعل المعروف في غير أهله ... يكن حمده ذما عليه ويندم','884','653','0');
INSERT INTO [t151017] VALUES (715,'٥٨ - سئمت تكاليف الحياة ومن يعيش ... ثمانين حولا- لا أبالك- يسأم','886','653','0');
INSERT INTO [t151017] VALUES (716,'٥٩ - رأيت المنايا خبط عشواء من تصب ... تمته، ومن تخطئ يعمر فيهرم','887','653','0');
INSERT INTO [t151017] VALUES (717,'٦٠ - ومهما تكن عند امرئ من خليقة ... وإن خالها تخفى على الناس تعلم','890','653','0');
INSERT INTO [t151017] VALUES (718,'٦١ - وأعلم ما في اليوم والأمس قبله ... ولكنني عن علم ما في غد عمى','892','653','0');
INSERT INTO [t151017] VALUES (719,'٦٢ - وكائن ترى من صامت لك معجب ... زيادته، أو نقصه في التكلم','893','653','0');
INSERT INTO [t151017] VALUES (720,'٦٣ - لسان الفتى نصف، ونصف فؤاده ... فلم يبق إلا صورة اللحم والدم','896','653','0');
INSERT INTO [t151017] VALUES (721,'٦٤ - وإن سفاه الشيخ لا حلم بعده ... وإن الفتى بعد السفاهة يحلم','897','653','0');
INSERT INTO [t151017] VALUES (722,'٦٥ - سألنا فأعطيتم وعدنا فعدتمو ... ومن أكثر التسال يوما سيحرم','898','653','0');
INSERT INTO [t151017] VALUES (723,'(٨) معلقة الأعشى','902','0','0');
INSERT INTO [t151017] VALUES (724,'فهرس أبيات معلقة الأعشى','903','723','0');
INSERT INTO [t151017] VALUES (725,'معلقة الأعشى','907','723','0');
INSERT INTO [t151017] VALUES (726,'نسبه','907','723','0');
INSERT INTO [t151017] VALUES (727,'ميمون بن قيس بن جندل بن شراحيل بن سعد بن مالك بن ضبيعة بن قيس بن ثعلبة بن عكابة بن صعب بن علي بن بكر بن وائل بن قاسط بن هنب بن أفصى بن دعمي بن جديلة بن أسد بن ربيعة بن نزار بن معد بن عدنان.','907','726','0');
INSERT INTO [t151017] VALUES (728,'من أخباره','907','723','0');
INSERT INTO [t151017] VALUES (729,'فائدة','908','723','0');
INSERT INTO [t151017] VALUES (730,'١ - ودع هريرة، إن الركب مرتحل ... وهل تطيق وداعا أيها الرجل؟','910','723','0');
INSERT INTO [t151017] VALUES (731,'٢ - غراء، فرعاء، مصقول عوارضها ... تمشي الهويني كما يمشي الوجي الوحل','911','723','0');
INSERT INTO [t151017] VALUES (732,'٣ - كأن مشيتها من بيت جاراتها ... مر السحابة، لا ريث، ولا عجل','912','723','0');
INSERT INTO [t151017] VALUES (733,'٤ - تسمع للحلي وسواسا إذا انصرفت ... كما استعان بريح عشرق زجل','913','723','0');
INSERT INTO [t151017] VALUES (734,'٥ - ليست كمن يكره الجيران طلعتها ... ولا تراها لسر الجار تختتل','914','723','0');
INSERT INTO [t151017] VALUES (735,'٦ - يكاد يصرعها، لولا تشددها ... إذا تقوم إلى جاراتها الكسل','915','723','0');
INSERT INTO [t151017] VALUES (736,'٧ - إذا تلاعب قرنا ساعة فترت ... وارتج منها ذنوب المتن والكفل','917','723','0');
INSERT INTO [t151017] VALUES (737,'٨ - صفر الوشاح، وملء الدرع بهكنة ... إذا تأتي يكاد الخصر ينخزل','918','723','0');
INSERT INTO [t151017] VALUES (738,'٩ - نعم الضجيع غداة الدجن يصرعها ... للذة المرء لا جاف ولا تفل','919','723','0');
INSERT INTO [t151017] VALUES (739,'١٠ - هركولة، فنق، درم مرافقها ... كأن أخمصها بالشوك منتعل','920','723','0');
INSERT INTO [t151017] VALUES (740,'١١ - إذا تقوم يضوع المسك أصورة ... والزنبق الورد من أردانها شمل','921','723','0');
INSERT INTO [t151017] VALUES (741,'١٢ - ما روضة من رياض الحزن معشبة ... خضراء جاد عليها مسبل هطل','922','723','0');
INSERT INTO [t151017] VALUES (742,'١٣ - يضاحك الشمس منها كوكب شرق ... مؤزر بعميم النبت مكتهل','923','723','0');
INSERT INTO [t151017] VALUES (743,'١٤ - يوما بأطيب منها نشر رائحة ... ولا بأحسن منها إذ دنا الأصل','923','723','0');
INSERT INTO [t151017] VALUES (744,'١٥ - علقتها عرضا، وعلقت رجلا ... غيري، وعلق أخرى غيرها الرجل','924','723','0');
INSERT INTO [t151017] VALUES (745,'١٦ - وعلقته فتاة ما يحاولها ... ومن بني عمها ميت بها وهل','926','723','0');
INSERT INTO [t151017] VALUES (746,'١٧ - وعلقتني أخيرى ما تلائمني ... فاجتمع الحب حب كله تبل','927','723','0');
INSERT INTO [t151017] VALUES (747,'١٨ - فكلنا مغرم يهذي بصاحبه ... ناء ودان ومخبول ومختبل','928','723','0');
INSERT INTO [t151017] VALUES (748,'١٩ - صدت هريرة عنا ما تكلمنا ... جهلا بأم خليد حبل من تصل؟','929','723','0');
INSERT INTO [t151017] VALUES (749,'٢٠ - أأن رأت رجلا أعشى أضر به ... ريب المنون، ودهر مفند خبل؟','931','723','0');
INSERT INTO [t151017] VALUES (750,'٢١ - قالت هريرة لما جئت زائرها: ... ويلي عليك، وويلي منك يا رجل','932','723','0');
INSERT INTO [t151017] VALUES (751,'٢٢ - لم تمش ميلا، ولم تركب على جمل ... ولم تر الشمس إلا دونها الكلل','934','723','0');
INSERT INTO [t151017] VALUES (752,'٢٣ - تمشي الهويني كأن الريح ترجعها ... مشي اليعافير، في جيئاتها الوهل','935','723','0');
INSERT INTO [t151017] VALUES (753,'٢٤ - إما ترينا حفاة لا نعال لنا ... إنا كذلك ما نحفى وننتعل','936','723','0');
INSERT INTO [t151017] VALUES (754,'٢٥ - وقد أخالس رب البيت غفلته ... وقد يحاذر مني ثم مايئل','937','723','0');
INSERT INTO [t151017] VALUES (755,'٢٦ - وقد أقود الصبا يوما، فيتبعني ... وقد يصاحبني ذو الشرة الغزل','938','723','0');
INSERT INTO [t151017] VALUES (756,'٢٧ - وقد غدوت إلى الحانوت يتبعني ... شاو مشل شلول شلشل شول','939','723','0');
INSERT INTO [t151017] VALUES (757,'٢٨ - في فتية كسيوف الهند قد علموا ... أن هالك كل من يحفي وينتعل','940','723','0');
INSERT INTO [t151017] VALUES (758,'٢٩ - نازعتهم قضب الريحان متكئا ... وقهوة مزة راووقها خضل','941','723','0');
INSERT INTO [t151017] VALUES (759,'٣٠ - لا يستفيقون منها، وهي راهنة ... إلا بهات، وإن علوا، وإن نهلوا','942','723','0');
INSERT INTO [t151017] VALUES (760,'٣١ - يسعي بها ذو زجاجات له نطف ... مقلص أسفل السربال معتمل','943','723','0');
INSERT INTO [t151017] VALUES (761,'٣٢ - ومستجيب تخال الصنج يسمعه ... إذا ترجع فيه القينة الفضل','944','723','0');
INSERT INTO [t151017] VALUES (762,'٣٣ - والساحبات ذيول الريط آونة ... والرافلات على أعجازها العجل','945','723','0');
INSERT INTO [t151017] VALUES (763,'٣٤ - من كل ذلك يوم، قد لهوت به ... وفي التجارب طول اللهو، والغزل','946','723','0');
INSERT INTO [t151017] VALUES (764,'٣٥ - وبلدة مثل ظهر الترس موحشة ... للجن بالليل في حافاتها رجل','947','723','0');
INSERT INTO [t151017] VALUES (765,'٣٦ - لا يتنمى لها بالقيظ يركبها ... إلا الذين لهم فيما أتوا مهل','948','723','0');
INSERT INTO [t151017] VALUES (766,'٣٧ - جاوزتها بطليح جسرة سرح ... في مرفقيها إذا استعرضتها فتل','949','723','0');
INSERT INTO [t151017] VALUES (767,'٣٨ - بل هل ترى عارضا، قدبت أرمقه ... كأنما البرق في حافاته شعل؟','950','723','0');
INSERT INTO [t151017] VALUES (768,'٣٩ - له رداف وجوز مقام عمل ... منطق بسجال الماء متصل','951','723','0');
INSERT INTO [t151017] VALUES (769,'٤٠ - لم يلهني اللهو عنه، حين أرقبه ... ولا اللذاذة من كأس، ولا شغل','952','723','0');
INSERT INTO [t151017] VALUES (770,'٤١ - فقلت للشرب في درنى، وقد ثملوا: ... شيموا وكيف يشيم الشارب الثمل؟','953','723','0');
INSERT INTO [t151017] VALUES (771,'٤٢ - برقا يضئ على أجزاع مسقطه ... وبالخبية فيه عارض هطل','954','723','0');
INSERT INTO [t151017] VALUES (772,'٤٣ - قالوا: نمار فبطن الخال جادهما ... فالعسجدية فالأبلاء فالرجل','954','723','0');
INSERT INTO [t151017] VALUES (773,'٤٤ - فالسفح يجري، فخنزير فبرقته ... حتى تدافع منه الربو فالحبل','955','723','0');
INSERT INTO [t151017] VALUES (774,'٤٥ - حتى تحمل منه الماء تكلفة ... روض القطا، فكثيب الغينة السهل','956','723','0');
INSERT INTO [t151017] VALUES (775,'٤٦ - يسقي ديارا لها، قد أصبحت غرضا ... زورا تجانف عنها القود والرسل','957','723','0');
INSERT INTO [t151017] VALUES (776,'٤٧ - أبلغ يزيد بني شيبان مألكة ... أبا ثبيت أما تنفك تأتكل','958','723','0');
INSERT INTO [t151017] VALUES (777,'٤٨ - ألست منتهيا عن نحت أثلتنا ... ولست ضائرها ما أطت الإبل؟','959','723','0');
INSERT INTO [t151017] VALUES (778,'٤٩ - كناطح صخرة يوما ليفلقها ... فلم يضرها، وأوهي قرنه الوعل','960','723','0');
INSERT INTO [t151017] VALUES (779,'٥٠ - لأعرفنك إن جد النفير بنا ... وشبت الحرب بالطواف، واحتملوا','961','723','0');
INSERT INTO [t151017] VALUES (780,'٥١ - تغري بنا رهط مسعود وإخوته ... عند اللقاء، فتردي، ثم تعتزل','962','723','0');
INSERT INTO [t151017] VALUES (781,'٥٢ - لا أعرفنك إن جدت عداوتنا ... والتمس النصر منكم عوض تحتمل','963','723','0');
INSERT INTO [t151017] VALUES (782,'٥٦ - تلزم أرماح ذي الجدين سورتنا ... عند اللقاء، فترديهم، وتعتزل','965','723','0');
INSERT INTO [t151017] VALUES (783,'٥٤ - لا تقعدن، وقد أكلتها حطبا ... تعوذ من شرها يوما وتبتهل','965','723','0');
INSERT INTO [t151017] VALUES (784,'٥٥ - سائل بني أسد عنا، فقد علموا ... أن سوف يأتيك من أنبائنا شكل','966','723','0');
INSERT INTO [t151017] VALUES (785,'٥٦ - واسأل قشيرا وعبد الله كلهمو ... وأسأل ربيعة عنا: كيف نفتعل؟','967','723','0');
INSERT INTO [t151017] VALUES (786,'٥٧ - إنا نقاتلهم حتى نقتلهم ... عند اللقاء، وإن جاروا، وإن جهلوا','968','723','0');
INSERT INTO [t151017] VALUES (787,'٥٨ - قد كان في آل كهف، إن هم احتربوا ... والجاشرية ما تسعي وتنتضل','969','723','0');
INSERT INTO [t151017] VALUES (788,'٥٩ - إني لعمر الذي حطت مناسمها ... تخدي وسيق إليه الباقر الغيل','970','723','0');
INSERT INTO [t151017] VALUES (789,'٦٠ - لئن قتلتم عميدا، لم يكن صددا ... لنقتلن مثله منكم فنمتثل','971','723','0');
INSERT INTO [t151017] VALUES (790,'٦١ - لئن منيت بنا عن غب معركة ... لا تلفنا من دماء القوم ننتفل','972','723','0');
INSERT INTO [t151017] VALUES (791,'٦٢ - لا تنتهون، ولن ينهي ذوي شطط ... كالطعن يهلك فيه الزيت والفتل','974','723','0');
INSERT INTO [t151017] VALUES (792,'٦٣ - حتى يظل عميد القوم مرتفقا ... يدفع بالراح عنه نسوة عجل','975','723','0');
INSERT INTO [t151017] VALUES (793,'٦٤ - أصابه هندواني، فأقصده ... أو ذابل من رماح الخط معتدل','976','723','0');
INSERT INTO [t151017] VALUES (794,'٦٥ - كلا زعمتم بأنا لا نقاتلكم ... إنا لأمثالكم يا قومنا قتل','976','723','0');
INSERT INTO [t151017] VALUES (795,'٦٦ - نحن الفوارس، يوم الحنو ضاحية ... جنبي فطيمة لا ميل، ولا عزل','977','723','0');
INSERT INTO [t151017] VALUES (796,'٦٧ - قالوا: الطراد، فقلنا، تلك عادتنا ... أو تنز لون، فإنا معشر نزل','979','723','0');
INSERT INTO [t151017] VALUES (797,'٦٨ - قد نخضب العير من مكنون فائله ... وقد يشيط على أرماحنا البطل','979','723','0');
INSERT INTO [t151017] VALUES (798,'(٩) معلقة النابغة الزبياني','981','0','0');
INSERT INTO [t151017] VALUES (799,'فهرست أبيات معلقة النابغة الذبياني','982','798','0');
INSERT INTO [t151017] VALUES (800,'معلقة النابغة الذبياني','985','798','0');
INSERT INTO [t151017] VALUES (801,'نسبه','985','798','0');
INSERT INTO [t151017] VALUES (802,'زياد بن عمرو بن معاوية بن ضباب بن جابر بن يربوع، بن غيظ بن مرة بن عوف بن سعد بن ذبيان بن بغيض بن الريث بن غطفان بن سعد بن قيس بن عيلان بن مضر بن نزار بن معد بن عدنان','985','801','0');
INSERT INTO [t151017] VALUES (803,'١ - يا دار مية بالعلياء فالسند ... أقوت وطال عليها سالف الأمد','986','798','0');
INSERT INTO [t151017] VALUES (804,'٢ - وقفت فيها أصيلا كي أسائلها ... عيت جوابا، وما بالربع من أحد','987','798','0');
INSERT INTO [t151017] VALUES (805,'٣ - إلا أواري لأيا ما أبينها ... والنؤي كالحوض بالمظلومة الجلد','988','798','0');
INSERT INTO [t151017] VALUES (806,'٤ - ردت عليه أقاصيه، ولبده ... ضرب الوليدة بالمسحاة في الثأد','990','798','0');
INSERT INTO [t151017] VALUES (807,'٥ - خلت سبيل أتى كان يحبسه ... ورفعته إلى السجفين فالنضد','991','798','0');
INSERT INTO [t151017] VALUES (808,'٦ - أصحت خلاء، وأضحى أهلها احتملوا ... أخنى عليها الذي أخنى على لبد','991','798','0');
INSERT INTO [t151017] VALUES (809,'٧ - فعد عما ترى إذ لا ارتجاع له ... وانم القتود على عيرانة أجد','993','798','0');
INSERT INTO [t151017] VALUES (810,'٨ - مقذوفة بدخيس النحض بازلها ... له صريف صريف القعو بالمسد','994','798','0');
INSERT INTO [t151017] VALUES (811,'٩ - كأن رحلي، وقد زال النهار بنا ... بذي الجليل على مستأنس وحد','996','798','0');
INSERT INTO [t151017] VALUES (812,'١٠ - من وحش وجرة، موشي أكارعه ... طاوي المصير، كسيف الصيقل الفرد','997','798','0');
INSERT INTO [t151017] VALUES (813,'١١ - سرت عليه من الجوزاء سارية ... تزجي الشمال عليه جامد البرد','998','798','0');
INSERT INTO [t151017] VALUES (814,'١٢ - فارتاع من صوت كلاب فبات له ... طوع الشوامت من خوف، ومن صرد','999','798','0');
INSERT INTO [t151017] VALUES (815,'١٣ - فبثهن عليه، واستمر به ... صمع الكعوب بريئات من الحرد','1000','798','0');
INSERT INTO [t151017] VALUES (816,'١٤ - فهاب ضمران منه حيث يوزعه ... طعن المعارك عند المحجر النجد','1001','798','0');
INSERT INTO [t151017] VALUES (817,'١٥ - شك الفريصة بالمدرى، فأنفذها ... شك المبيطر إذ يشفي من العضد','1002','798','0');
INSERT INTO [t151017] VALUES (818,'١٦ - كأنه، خارجا من جنب صفحته ... سفود شرب نسوه عند مفتأد','1003','798','0');
INSERT INTO [t151017] VALUES (819,'١٧ - فظل يعجم أعلى الروق منقبضا في حالك اللون صدق، غير ذي أود','1004','798','0');
INSERT INTO [t151017] VALUES (820,'١٨ - لما رأى واشق إقعاص صاحبه ... ولا سبيل إلى عقل، ولا قود','1005','798','0');
INSERT INTO [t151017] VALUES (821,'١٩ - قالت له النفس: إني لا أرى طمعا ... وإن مولاك لم يسلم، ولم يصد','1006','798','0');
INSERT INTO [t151017] VALUES (822,'٢٠ - فتلك تبلغني النعمان، إن له ... فضلا على الناس، في الأدنى، وفي البعد','1007','798','0');
INSERT INTO [t151017] VALUES (823,'٢١ - ولا أرى فاعلا في الناس يشبهه ... وما أحاشي من الأقوام من أحد','1008','798','0');
INSERT INTO [t151017] VALUES (824,'٢٢ - إلا سليمان إذ قال الإله له ... قم في البرية فأحددها عن الفند','1009','798','0');
INSERT INTO [t151017] VALUES (825,'٢٣ - وخيس الجن، إني قد أذنت لهم ... يبنون تدمر بالصفاح والعمد','1010','798','0');
INSERT INTO [t151017] VALUES (826,'٢٤ - فمن أطاع فأعقبه بطاعته ... كما أطاعك، وأدلله على الرشد','1011','798','0');
INSERT INTO [t151017] VALUES (827,'٢٥ - ومن عصاك، فعاقبه معاقبة ... تنهى الظلوم، ولا تقعد على ضمد','1012','798','0');
INSERT INTO [t151017] VALUES (828,'٢٦ - إلا لمثلك، أو من أنت سابقه ... سبق الجواد إذا استولى على الأمد','1013','798','0');
INSERT INTO [t151017] VALUES (829,'٢٧ - وأحكم كحكم فتاة الحي، إذ ... نظرت إلى حمام سراع وارد الثمد','1014','798','0');
INSERT INTO [t151017] VALUES (830,'٢٨ - قالت: ألا ليتما هذا الحمام لنا ... إلى حمامتنا، ونصفه فقد','1015','798','0');
INSERT INTO [t151017] VALUES (831,'٢٩ - يحفه جانبا نيق، وتتبعه ... مثل الزجاجة، لم تكحل من الرمد','1017','798','0');
INSERT INTO [t151017] VALUES (832,'٣٠ - فحسبوه، فألفوه كما حسبت ... تسعا وتسعين، لم تنقص ولم تزد','1018','798','0');
INSERT INTO [t151017] VALUES (833,'٣١ - فكملت مائة فيها حمامتها ... وأسرعت حسبة في ذلك العدد','1019','798','0');
INSERT INTO [t151017] VALUES (834,'٣٢ - أعطى لفارهة، حلو توابعها ... من المواهب، لا تعطى على نكد','1020','798','0');
INSERT INTO [t151017] VALUES (835,'٣٣ - الواهب المائة الأبكار زينها ... سعدان توضح في أوبارها اللبد','1021','798','0');
INSERT INTO [t151017] VALUES (836,'٣٤ - والساحبات ذيول المرط فنقها ... برد الهواجر كالغزلان بالجرد','1022','798','0');
INSERT INTO [t151017] VALUES (837,'٣٥ - والخيل تمزع غربا في أعنتها ... كالطير تنجو من الشؤبوب ذي البرد','1023','798','0');
INSERT INTO [t151017] VALUES (838,'٣٦ - والأدم قد خيست فتلا مرافقها ... مشدودة برحال الحيرة الجدد','1024','798','0');
INSERT INTO [t151017] VALUES (839,'٣٧ - فلا لعمر الذي قد زرته حججا ... وما هريق على الأنصاب من جسد','1025','798','0');
INSERT INTO [t151017] VALUES (840,'٣٨ - والمؤمن العائذات الطير تمسحها ... ركبان مكة بين الغيل والسند','1026','798','0');
INSERT INTO [t151017] VALUES (841,'٣٩ - ما إن أتيت بشيء أنت تكرهه ... إ ذن فلا رفعت سوطي إلى يدي','1027','798','0');
INSERT INTO [t151017] VALUES (842,'٤٠ - إذن فعاقبني ربي معاقبة ... قرت بها عين من يأتيك بالحسد','1028','798','0');
INSERT INTO [t151017] VALUES (843,'٤١ - هذا لأبرأ من قول قذفت به ... طارت نوافذه حرا على كبدي','1029','798','0');
INSERT INTO [t151017] VALUES (844,'٤٢ - مهلا، فداء لك الأقوام كلهمو ... وما أثمر من مال، ومن ولد','1030','798','0');
INSERT INTO [t151017] VALUES (845,'٤٣ - لا تقذفني بركن، لاكفاء له ... ولو تأثفك الأعداء بالرفد','1032','798','0');
INSERT INTO [t151017] VALUES (846,'٤٤ - فما الفرات، إذا جاشت غواربه ... ترمى أواذيه العبرين بالزبد','1033','798','0');
INSERT INTO [t151017] VALUES (847,'٤٥ - يمده كل واد مزبد لجب ... فيه حطام من الينبوت والخضد','1034','798','0');
INSERT INTO [t151017] VALUES (848,'٤٦ - يظل من خوفه الملاح معتصما ... بالخير رانة بعد الاين والنجد','1034','798','0');
INSERT INTO [t151017] VALUES (849,'٤٧ - يوما بأجود منه سيب نافلة ... ولا يحول عطاء اليوم دون غد','1035','798','0');
INSERT INTO [t151017] VALUES (850,'٤٨ - أنبئت أن أبا قابوس أوعدني ... ولا قرار على زأر من الأسد','1036','798','0');
INSERT INTO [t151017] VALUES (851,'٤٩ - هذا الثناء، فإن تسمع لقائله ... فما عرضت، أبيت اللعن، بالصفد','1038','798','0');
INSERT INTO [t151017] VALUES (852,'٥٠ - ها إن تا عذرة إلا تكن نفعت ... فإن صاحبها قد تاه في البلد','1039','798','0');
INSERT INTO [t151017] VALUES (853,'خاتمة','1040','798','0');
INSERT INTO [t151017] VALUES (854,'(١٠) معلقة عبيد بن الأبرص','1042','0','0');
INSERT INTO [t151017] VALUES (855,'فهرس أبيات معلقة عبيد بن الأبرص','1043','854','0');
INSERT INTO [t151017] VALUES (856,'معلقة عبيد عبيد بن الأبرص الأسدي','1046','854','0');
INSERT INTO [t151017] VALUES (857,'عبيد بن الأبرص، بن حنتم، وقيل: ابن جشم من بني أسد، ويتصل نسبه بمضر','1046','854','0');
INSERT INTO [t151017] VALUES (858,'١ - أقفر من أهله ملحوب ... قالقطبيات فالذنوب','1049','854','0');
INSERT INTO [t151017] VALUES (859,'٢ - فراكس، فتعالبات ... فذات فرقين، فالقليب','1049','854','0');
INSERT INTO [t151017] VALUES (860,'٣ - فعردة، فقفاجبر ... ليس بها منهمو عريب','1050','854','0');
INSERT INTO [t151017] VALUES (861,'٤ - وبلت من أهلها وحوشا ... وغيرت حالها الخطوب','1051','854','0');
INSERT INTO [t151017] VALUES (862,'٥ - أرض توارثها شعوب ... وكل من حلها محروب','1051','854','0');
INSERT INTO [t151017] VALUES (863,'٦ - إما قتيل، وإما هالك ... والشيب شين لمن يشيب','1052','854','0');
INSERT INTO [t151017] VALUES (864,'٧ - عيناك دفعهما سروب ... كأن شأنيهما شعيب','1053','854','0');
INSERT INTO [t151017] VALUES (865,'٨ - واهية، أو معين ممعن ... من هضبة، دونها لهوب','1054','854','0');
INSERT INTO [t151017] VALUES (866,'٩ - أو فلج ببطن واد ... واد للماء من تحته قسيب','1054','854','0');
INSERT INTO [t151017] VALUES (867,'١٠ - أو جدول في ظلال نخل ... للماء من تخته سكوب','1055','854','0');
INSERT INTO [t151017] VALUES (868,'١١ - تصبو، وأني لك التصابي ... أني، وقد راعك المشبب؟','1056','854','0');
INSERT INTO [t151017] VALUES (869,'١٢ - إن يك خول منها أهلها ... فلا بدئ، ولا عجيب','1057','854','0');
INSERT INTO [t151017] VALUES (870,'١٣ - أويك قد أقفر منها جوها ... وعادها المحل والجدوب','1058','854','0');
INSERT INTO [t151017] VALUES (871,'١٤ - فكل ذي نعمة مخلوسها ... وكل ذي أمل مكذوب','1059','854','0');
INSERT INTO [t151017] VALUES (872,'١٥ - وكل ذي إبل موروث ... وكل ذي سلب مسلوب','1060','854','0');
INSERT INTO [t151017] VALUES (873,'١٦ - وكل ذي غيبة يؤوب ... وغائب المؤت لا يؤوب','1060','854','0');
INSERT INTO [t151017] VALUES (874,'١٧ - أعاقر مثل ذات رحم ... أو غانم مثل من يخيب؟','1061','854','0');
INSERT INTO [t151017] VALUES (875,'١٨ - من يسأل الناس يحرموه ... وسائل الله لا يخيب','1062','854','0');
INSERT INTO [t151017] VALUES (876,'١٩ - بالله يدرك كل خير ... والقول في بعضه تلغيب','1063','854','0');
INSERT INTO [t151017] VALUES (877,'٢٠ - والله ليس له شريك ... علام ما أخفت القلوب','1064','854','0');
INSERT INTO [t151017] VALUES (878,'٢١ - أفلح بما شئت، فقد يبلغ بالـ ... ـضعف، وقد يخدع الأريب','1064','854','0');
INSERT INTO [t151017] VALUES (879,'٢٢ - لا يعظ الناس من لا يعظ الـ ... ـذهر، ولا ينفع التلبيب','1066','854','0');
INSERT INTO [t151017] VALUES (880,'٢٣ - لا ينفع اللب عن تعلم ... إلا السجيات والقلوب','1066','854','0');
INSERT INTO [t151017] VALUES (881,'٢٤ - فقد يعودن حبيبا شانئ ... ويرجعن شانئا حبيب','1067','854','0');
INSERT INTO [t151017] VALUES (882,'٢٥ - ساعد بأرض إذا كنت بها ... ولا تقل: إنني غريب','1068','854','0');
INSERT INTO [t151017] VALUES (883,'٢٦ - قد يوصل النازح النائي، وقد ... يقطع ذو السهمة القريب','1068','854','0');
INSERT INTO [t151017] VALUES (884,'٢٧ - والمرء ما عاش في تكذيب ... طول الحياة له ئعذيب','1069','854','0');
INSERT INTO [t151017] VALUES (885,'٢٨ - بل رب ماء وردته آجن ... سبيله خائف جديب','1070','854','0');
INSERT INTO [t151017] VALUES (886,'٢٩ - ريش الحمام على أرجائه ... القلب من خوفه وجيب','1071','854','0');
INSERT INTO [t151017] VALUES (887,'٣٠ - قطعته غدوة مشيحا ... وصاحبي بادن حبوب','1072','854','0');
INSERT INTO [t151017] VALUES (888,'٣١ - عيرانة مؤجد فقارها ... كأن حاركها كثيب','1072','854','0');
INSERT INTO [t151017] VALUES (889,'٣٢ - أخلف ما بازلا سديسها ... لاحقة هي، ولا نيوب','1073','854','0');
INSERT INTO [t151017] VALUES (890,'٣٣ - كأنها من حمير غانات ... جون بخته ندوب','1074','854','0');
INSERT INTO [t151017] VALUES (891,'٣٤ - أو شبب يرتعي الرخامى ... تلفه شمأل هبوب','1075','854','0');
INSERT INTO [t151017] VALUES (892,'٣٥ - فذاك عصر، وقد أراني ... تحملني نهدة سرحوب','1075','854','0');
INSERT INTO [t151017] VALUES (893,'٣٦ - مضبر خلقها تضبيرا ... ينشق عن وجهها السبيب','1076','854','0');
INSERT INTO [t151017] VALUES (894,'٣٧ - زيتية نائم عروقها ... ولين أسرها رطيب','1077','854','0');
INSERT INTO [t151017] VALUES (895,'٣٨ - كأنها لقوة طلوب ... تخر في وكرها القلوب','1078','854','0');
INSERT INTO [t151017] VALUES (896,'٣٩ - باتت على إرم عذوبا ... كأنها شيخة رقوب','1078','854','0');
INSERT INTO [t151017] VALUES (897,'٤٠ - فأصبحت في غداة قرة ... يسقط عن ريشها الضريب','1079','854','0');
INSERT INTO [t151017] VALUES (898,'٤١ - فأبصرت تعلبا سريعا ... ودونه سبسب جديب','1079','854','0');
INSERT INTO [t151017] VALUES (899,'٤٢ - فنفضت ريشها وولت ... فداك من نهضة قريب','1080','854','0');
INSERT INTO [t151017] VALUES (900,'٤٣ - فاشتال، وارتاع من حسيس ... وفعله يفعل المذؤوب','1081','854','0');
INSERT INTO [t151017] VALUES (901,'٤٤ - نهضت نحوه حثيثة ... وحردت حرده تسيب','1082','854','0');
INSERT INTO [t151017] VALUES (902,'٤٥ - فدب من رأيها دبيبا ... والعين حملاقها مقلوب','1082','854','0');
INSERT INTO [t151017] VALUES (903,'٤٦ - فأدركته فطرحته ... والصيد من تختها مكروب','1083','854','0');
INSERT INTO [t151017] VALUES (904,'٤٧ - فجدلته، فطرحته ... فكدحت وجهه الجبوب','1084','854','0');
INSERT INTO [t151017] VALUES (905,'٤٨ - فعاودته، فرفعته ... فأرسلته، وهو مكروب','1085','854','0');
INSERT INTO [t151017] VALUES (906,'٤٩ - يضغو ومخلبها في دفه ... لابد حيزومه منقوب','1085','854','0');
INSERT INTO [t151017] VALUES (907,'خاتمة','1087','854','0');
