INSERT INTO [t151157] VALUES (1,'المقدمة','1','0','0');
INSERT INTO [t151157] VALUES (2,'هل سورة يس مكية أم مدنية','2','0','0');
INSERT INTO [t151157] VALUES (3,'تفسير قوله تعالى: {بسم الله الرحمن الرحيم}','3','0','0');
INSERT INTO [t151157] VALUES (4,'تفسير قوله تعالى: {يس}','7','0','0');
INSERT INTO [t151157] VALUES (5,'تفسير قوله تعالى: {والقرآن الحكيم}','8','0','0');
INSERT INTO [t151157] VALUES (6,'تفسير قوله تعالى: {إنك لمن المرسلين}','11','0','0');
INSERT INTO [t151157] VALUES (7,'تفسير قوله تعالى: {على صراط مستقيم}','13','0','0');
INSERT INTO [t151157] VALUES (8,'تفسير قوله تعالى: {تنزيل العزيز الرحيم}','15','0','0');
INSERT INTO [t151157] VALUES (9,'تفسير قوله تعالى: {لتنذر قوما ما أنذر آباؤهم فهم غافلون}','19','0','0');
INSERT INTO [t151157] VALUES (10,'تفسير قوله تعالى: {لقد حق القول على أكثرهم فهم لا يؤمنون}','23','0','0');
INSERT INTO [t151157] VALUES (11,'تفسير قوله تعالى: {إنا جعلنا في أعناقهم أغلالا فهي إلى الأذقان فهم مقمحون}','25','0','0');
INSERT INTO [t151157] VALUES (12,'تفسير قوله تعالى: {وجعلنا من بين أيديهم سدا ومن خلفهم سدا فأغشيناهم فهم لا يبصرون}','27','0','0');
INSERT INTO [t151157] VALUES (13,'تفسير قوله تعالى: {وسواء عليهم أأنذرتهم أم لم تنذرهم لا يؤمنون}','28','0','0');
INSERT INTO [t151157] VALUES (14,'تفسير قوله تعالى: {إنما تنذر من اتبع الذكر وخشي الرحمن بالغيب فبشره بمغفرة وأجر كريم}','32','0','0');
INSERT INTO [t151157] VALUES (15,'تفسير قوله تعالى: {إنا نحن نحي الموتى ونكتب ما قدموا وآثارهم وكل شيء أحصيناه في إمام مبين}','40','0','0');
INSERT INTO [t151157] VALUES (16,'تفسير قوله تعالى: {واضرب لهم مثلا أصحاب القرية إذ جاءها المرسلون}','52','0','0');
INSERT INTO [t151157] VALUES (17,'تفسير قوله تعالى: {إذ أرسلنا إليهم اثنين فكذبوهما فعززنا بثالث فقالوا إنا إليكم مرسلون}','55','0','0');
INSERT INTO [t151157] VALUES (18,'تفسير قوله تعالى: {قالوا ما أنتم إلا بشر مثلنا وما أنزل الرحمن من شيء إن أنتم إلا تكذبون}','57','0','0');
INSERT INTO [t151157] VALUES (19,'تفسير قوله تعالى: {قالوا ربنا يعلم إنا إليكم لمرسلون}','58','0','0');
INSERT INTO [t151157] VALUES (20,'تفسير قوله تعالى: {وما علينا إلا البلاغ المبين}','59','0','0');
INSERT INTO [t151157] VALUES (21,'تفسير قوله تعالى: {قالوا إنا تطيرنا بكم لئن لم تنتهوا لنرجمنكم وليمسنكم منا عذاب أليم}','63','0','0');
INSERT INTO [t151157] VALUES (22,'تفسير قوله تعالى: {قالوا طائركم معكم أئن ذكرتم بل أنتم قوم مسرفون}','65','0','0');
INSERT INTO [t151157] VALUES (23,'تفسير قوله تعالى: {وجاء من أقصى المدينة رجل يسعى قال يا قوم اتبعوا المرسلين}','70','0','0');
INSERT INTO [t151157] VALUES (24,'تفسير قوله تعالى: {اتبعوا من لا يسألكم أجرا وهم مهتدون}','72','0','0');
INSERT INTO [t151157] VALUES (25,'تفسير قوله تعالى: {وما لي لا أعبد الذي فطرني وإليه ترجعون}','74','0','0');
INSERT INTO [t151157] VALUES (26,'تفسير قوله تعالى: {أأتخذ من دونه آلهة إن يردن الرحمن بضر لا تغن عني شفاعتهم شيئا ولا ينقذون}','79','0','0');
INSERT INTO [t151157] VALUES (27,'تفسير قوله تعالى: {إني إذا لفي ضلال مبين}','87','0','0');
INSERT INTO [t151157] VALUES (28,'تفسير قوله تعالى: {إني آمنت بربكم فاسمعون}','88','0','0');
INSERT INTO [t151157] VALUES (29,'تفسير قوله تعالى: {قيل ادخل الجنة قال يا ليت قومي يعلمون}','89','0','0');
INSERT INTO [t151157] VALUES (30,'تفسير قوله تعالى: {بما غفر لي ربي وجعلني من المكرمين}','91','0','0');
INSERT INTO [t151157] VALUES (31,'تفسير قوله تعالى: {وما أنزلنا على قومه من بعده من جند من السماء وما كنا منزلين}','97','0','0');
INSERT INTO [t151157] VALUES (32,'تفسير قوله تعالى: {إن كانت إلا صيحة واحدة فإذا هم خامدون}','98','0','0');
INSERT INTO [t151157] VALUES (33,'تفسير قوله تعالى: {يا حسرة على العباد ما يأتيهم من رسول إلا كانوا به يستهزئون}','104','0','0');
INSERT INTO [t151157] VALUES (34,'تفسير قوله تعالى: {ألم يروا كم أهلكنا قبلهم من القرون أنهم إليهم لا يرجعون}','110','0','0');
INSERT INTO [t151157] VALUES (35,'تفسير قوله تعالى: {وإن كل لما جميع لدينا محضرون}','113','0','0');
INSERT INTO [t151157] VALUES (36,'تفسير قوله تعالى: {وآية لهم الأرض الميتة أحييناها وأخرجنا منها حبا فمنه يأكلون}','115','0','0');
INSERT INTO [t151157] VALUES (37,'تفسير قوله تعالى: {وجعلنا فيها جنات من نخيل وأعناب وفجرنا فيها من العيون}','118','0','0');
INSERT INTO [t151157] VALUES (38,'تفسير قوله تعالى: {ليأكلوا من ثمره وما عملته أيديهم أفلا يشكرون}','119','0','0');
INSERT INTO [t151157] VALUES (39,'تفسير قوله تعالى: {سبحان الذي خلق الأزواج كلها مما تنبت الأرض ومن أنفسهم ومما لا يعلمون}','129','0','0');
INSERT INTO [t151157] VALUES (40,'تفسير قوله تعالى: {وآية لهم الليل نسلخ منه النهار فإذا هم مظلمون}','132','0','0');
INSERT INTO [t151157] VALUES (41,'تفسير قوله تعالى: {والشمس تجري لمستقر لها ذلك تقدير العزيز العليم}','134','0','0');
INSERT INTO [t151157] VALUES (42,'تفسير قوله تعالى: {والقمر قدرناه منازل حتى عاد كالعرجون القديم}','140','0','0');
INSERT INTO [t151157] VALUES (43,'تفسير قوله تعالى: {لا الشمس ينبغي لها أن تدرك القمر ولا الليل سابق النهار وكل في فلك يسبحون}','144','0','0');
INSERT INTO [t151157] VALUES (44,'تفسير قوله تعالى: {وآية لهم أنا حملنا ذريتهم في الفلك المشحون}','149','0','0');
INSERT INTO [t151157] VALUES (45,'تفسير قوله تعالى: {وخلقنا لهم من مثله ما يركبون}','153','0','0');
INSERT INTO [t151157] VALUES (46,'تفسير قوله تعالى: {وإن نشأ نغرقهم فلا صريخ لهم ولا هم ينقذون * إلا رحمة منا ومتاعا إلى حين}','155','0','0');
INSERT INTO [t151157] VALUES (47,'تفسير قوله تعالى: {وإذا قيل لهم اتقوا ما بين أيديكم وما خلفكم لعلكم ترحمون}','157','0','0');
INSERT INTO [t151157] VALUES (48,'تفسير قوله تعالى: {وما تأتيهم من آية من آيات ربهم إلا كانوا عنها معرضين}','162','0','0');
INSERT INTO [t151157] VALUES (49,'تفسير قوله تعالى: {وإذا قيل لهم أنفقوا مما رزقكم الله قال الذين كفروا للذين آمنوا أنطعم من لو يشاء الله أطعمه إن أنتم إلا في ضلال مبين}','164','0','0');
INSERT INTO [t151157] VALUES (50,'تفسير قوله تعالى: {ويقولون متى هذا الوعد إن كنتم صادقين}','174','0','0');
INSERT INTO [t151157] VALUES (51,'تفسير قوله تعالى: {ما ينظرون إلا صيحة واحدة تأخذهم وهم يخصمون}','175','0','0');
INSERT INTO [t151157] VALUES (52,'تفسير قوله تعالى: {فلا يستطيعون توصية ولا إلى أهلهم يرجعون}','178','0','0');
INSERT INTO [t151157] VALUES (53,'تفسير قوله تعالى: {ونفخ في الصور فإذا هم من الأجداث إلى ربهم ينسلون}','179','0','0');
INSERT INTO [t151157] VALUES (54,'تفسير قوله تعالى: {قالوا يا ويلنا من بعثنا من مرقدنا هذا ما وعد الرحمن وصدق المرسلون}','183','0','0');
INSERT INTO [t151157] VALUES (55,'تفسير قوله تعالى: {فاليوم لا تظلم نفس شيئا ولا تجزون إلا ما كنتم تعملون}','190','0','0');
INSERT INTO [t151157] VALUES (56,'تفسير قوله تعالى: {إن أصحاب الجنة اليوم في شغل فاكهون}','195','0','0');
INSERT INTO [t151157] VALUES (57,'تفسير قوله تعالى: {هم وأزواجهم في ظلال على الأرائك متكئون}','199','0','0');
INSERT INTO [t151157] VALUES (58,'تفسير قوله تعالى: {لهم فيها فاكهة ولهم ما يدعون}','202','0','0');
INSERT INTO [t151157] VALUES (59,'تفسير قوله تعالى: {سلام قولا من رب رحيم}','204','0','0');
INSERT INTO [t151157] VALUES (60,'تفسير قوله تعالى: {وامتازوا اليوم أيها المجرمون}','209','0','0');
INSERT INTO [t151157] VALUES (61,'تفسير قوله تعالى: {ألم أعهد إليكم يا بني آدم أن لا تعبدوا الشيطان إنه لكم عدو مبين * وأن اعبدوني هذا صراط مستقيم}','211','0','0');
INSERT INTO [t151157] VALUES (62,'تفسير قوله تعالى: {ولقد أضل منكم جبلا كثيرا أفلم تكونوا تعقلون}','219','0','0');
INSERT INTO [t151157] VALUES (63,'تفسير قوله تعالى: {هذه جهنم التي كنتم توعدون}','223','0','0');
INSERT INTO [t151157] VALUES (64,'تفسير قوله تعالى: {اصلوها اليوم بما كنتم تكفرون}','226','0','0');
INSERT INTO [t151157] VALUES (65,'تفسير قوله تعالى: {اليوم نختم على أفواههم وتكلمنا أيديهم وتشهد أرجلهم بما كانوا يكسبون}','228','0','0');
INSERT INTO [t151157] VALUES (66,'تفسير قوله تعالى: {ولو نشاء لطمسنا على أعينهم فاستبقوا الصراط فأنى يبصرون}','233','0','0');
INSERT INTO [t151157] VALUES (67,'تفسير قوله تعالى: {ولو نشاء لمسخناهم على مكانتهم فما استطاعوا مضيا ولا يرجعون}','236','0','0');
INSERT INTO [t151157] VALUES (68,'تفسير قوله تعالى: {ومن نعمره ننكسه في الخلق أفلا يعقلون}','238','0','0');
INSERT INTO [t151157] VALUES (69,'تفسير قوله تعالى: {وما علمناه الشعر وما ينبغي له إن هو إلا ذكر وقرآن مبين}','241','0','0');
INSERT INTO [t151157] VALUES (70,'تفسير قوله تعالى: {لينذر من كان حيا ويحق القول على الكافرين}','253','0','0');
INSERT INTO [t151157] VALUES (71,'تفسير قوله تعالى: {أولم يروا أنا خلقنا لهم مما عملت أيدينا أنعاما فهم لها مالكون}','255','0','0');
INSERT INTO [t151157] VALUES (72,'تفسير قوله تعالى: {وذللناها لهم فمنها ركوبهم ومنها يأكلون}','270','0','0');
INSERT INTO [t151157] VALUES (73,'تفسير قوله تعالى: {ولهم فيها منافع ومشارب أفلا يشكرون}','276','0','0');
INSERT INTO [t151157] VALUES (74,'تفسير قوله تعالى: {واتخذوا من دون الله آلهة لعلهم ينصرون}','278','0','0');
INSERT INTO [t151157] VALUES (75,'تفسير قوله تعالى: {لا يستطيعون نصرهم وهم لهم جند محضرون}','282','0','0');
INSERT INTO [t151157] VALUES (76,'تفسير قوله تعالى: {فلا يحزنك قولهم إنا نعلم ما يسرون وما يعلنون}','285','0','0');
INSERT INTO [t151157] VALUES (77,'تفسير قوله تعالى: {أولم ير الإنسان أنا خلقناه من نطفة فإذا هو خصيم مبين}','287','0','0');
INSERT INTO [t151157] VALUES (78,'تفسير قوله تعالى: {وضرب لنا مثلا ونسي خلقه قال من يحي العظام وهي رميم}','291','0','0');
INSERT INTO [t151157] VALUES (79,'تفسير قوله تعالى: {قل يحييها الذي أنشأها أول مرة وهو بكل خلق عليم}','293','0','0');
INSERT INTO [t151157] VALUES (80,'تفسير قوله تعالى: {الذي جعل لكم من الشجر الأخضر نارا فإذا أنتم منه توقدون}','297','0','0');
INSERT INTO [t151157] VALUES (81,'تفسير قوله تعالى: {أوليس الذي خلق السماوات والأرض بقادر على أن يخلق مثلهم بلى وهو الخلاق العليم}','300','0','0');
INSERT INTO [t151157] VALUES (82,'تفسير قوله تعالى: {إنما أمره إذا أراد شيئا أن يقول له كن فيكون}','304','0','0');
INSERT INTO [t151157] VALUES (83,'تفسير قوله تعالى: {فسبحان الذي بيده ملكوت كل شيء وإليه ترجعون}','309','0','0');
