INSERT INTO [t151] VALUES (734,'تسمية الصيام الصبر','1498','732','0');
INSERT INTO [t151] VALUES (735,'مسألة: (ويجب صيام رمضان على كل مسلم بالغ عاقل قادر على الصوم، ويؤمر به الصبي إذا أطاقه)','1499','732','0');
INSERT INTO [t151] VALUES (736,'الفصل الأول: أن صيام رمضان فرض في الجملة','1499','735','0');
INSERT INTO [t151] VALUES (737,'الأفضل أن يقال: جاء «شهر رمضان»، وذكر ما ورد من الكراهة في إطلاق «رمضان» دون إضافته','1502','736','0');
INSERT INTO [t151] VALUES (738,'المعروف من كلام أحمد أنه لا يكره تسميته رمضان، وهو مقتضى الأحاديث الصحيحة','1504','736','0');
INSERT INTO [t151] VALUES (739,'قال بعض أهل اللغة: ما كان في أوله راء من الشهور، فالغالب إضافة الشهر إليه','1507','736','0');
INSERT INTO [t151] VALUES (740,'وجه اشتقاق «رمضان» من الرمض','1507','736','0');
INSERT INTO [t151] VALUES (741,'فصل (ما ورد من الدعاء عند رؤية الهلال)','1509','736','0');
INSERT INTO [t151] VALUES (742,'الفصل الثاني: أنه يجب على كل مسلم عاقل بالغ قادر','1513','735','0');
INSERT INTO [t151] VALUES (743,'الفصل الثالث: أنه لا يجب على الكافر، بمعنى أنه لا يخاطب بفعله','1513','735','0');
INSERT INTO [t151] VALUES (744,'الفصل الرابع: أنه لا يجب على المجنون في المشهور من المذهب','1514','735','0');
INSERT INTO [t151] VALUES (745,'إن نوى الصوم وجن في بعض اليوم، هل يبطل صومه؟','1516','744','0');
INSERT INTO [t151] VALUES (746,'أما الصرع الذي يعرض وقتا ثم يزول، فيلحق بالإغماء والغشي','1516','744','0');
INSERT INTO [t151] VALUES (747,'فصل (من زال عقله بغير جنون من إغماء أو غيره، فإنه يجب عليه الصوم)','1517','744','0');
INSERT INTO [t151] VALUES (748,'الفصل الخامس: أنه لا يجب على الصبي حتى يبلغ في إحدى الروايتين','1518','735','0');
INSERT INTO [t151] VALUES (749,'الفصل السادس: أنه لا يجب الصوم إلا على القادر','1521','735','0');
INSERT INTO [t151] VALUES (750,'فصل (إن صار من أهل الوجوب في أثناء النهار، فهل يمسك بقية يومه ويقضي؟ على روايتين)','1522','749','0');
INSERT INTO [t151] VALUES (751,'ولو أكل يعتقد بقاء الليل، ثم تبين أنه كان نهارا أمسك بقية يومه، ويقضيه بعد خروج الشهر','1525','749','0');
INSERT INTO [t151] VALUES (752,'فصل (من يجب عليه القضاء إذا زال عذره في أثناء اليوم، فالقضاء يجب عليه، وفي وجوب الإمساك روايتان)','1527','749','0');
INSERT INTO [t151] VALUES (753,'فصل (إذا وجد سبب الفطر في أثناء النهار مثل أن تحيض المرأة، فإنها تصير مفطرة، وكذلك إذا مرض فله الفطر)','1531','749','0');
INSERT INTO [t151] VALUES (754,'إذا سافر الصائم أثناء النهار، فهل له الفطر؟ روايتان، أصحهما الجواز','1532','749','0');
INSERT INTO [t151] VALUES (755,'إذا نوى المسافر الصوم في السفر، فله الفطر إذا أراد، إلا أن يريد الفطر بالجماع، ففيه روايتان','1539','749','0');
INSERT INTO [t151] VALUES (756,'وإذا أراد المقيم السفر حيلة للفطر، لم يبح الفطر','1543','749','0');
INSERT INTO [t151] VALUES (757,'مسألة: (ويجب بأحد ثلاثة أشياء: كمال شعبان، ورؤية هلال رمضان، ووجود غيم أو قتر ليلة الثلاثين يحول دونه)','1544','732','0');
INSERT INTO [t151] VALUES (758,'إن كان ليلة الثلاثين حائل يمنع من رؤية من سحاب أو قتر، فهل يسمى يوم الشك، وهل يصام على أنه من رمضان؟ روايتان','1545','757','0');
INSERT INTO [t151] VALUES (759,'ذكر أدلة عدم صوم يوم الغيم','1547','757','0');
INSERT INTO [t151] VALUES (760,'بيان وجوه الدلالة من الأحاديث على عدم صومه','1553','757','0');
INSERT INTO [t151] VALUES (761,'دليل من جعل الناس تبعا للسلطان','1555','757','0');
INSERT INTO [t151] VALUES (762,'ذكر أدلة من يرى صوم يوم الغيم','1556','757','0');
INSERT INTO [t151] VALUES (763,'بيان وجوه الدلالة من الأحاديث على صومه','1557','757','0');
INSERT INTO [t151] VALUES (764,'آثار الصحابة في صوم يوم الغيم','1561','757','0');
INSERT INTO [t151] VALUES (765,'آثار الصحابة أنفسهم في النهي عن صوم يوم الشك والأمر بإكمال العدة','1566','757','0');
INSERT INTO [t151] VALUES (766,'تقرير أنه يجب حمل آثار الصوم على حال الغيم وآثار الفطر على حال الصحو، من وجوه','1569','757','0');
INSERT INTO [t151] VALUES (767,'ترجيح الصوم في يوم الغيم من وجهين آخرين','1572','757','0');
INSERT INTO [t151] VALUES (768,'ما ورد من الأمر بصيام سرر الشهر','1572','757','0');
INSERT INTO [t151] VALUES (769,'لا يحكم بدخول شهر من الشهور بمجرد الإغمام إلا شهر رمضان','1579','757','0');
INSERT INTO [t151] VALUES (770,'الجواب عن أدلة المانعين من صوم يوم الغيم','1582','757','0');
INSERT INTO [t151] VALUES (771,'جواب ثان','1587','770','0');
INSERT INTO [t151] VALUES (772,'جواب ثالث','1590','770','0');
INSERT INTO [t151] VALUES (773,'فصل (إذا أوجبنا صومه ترتب عليه جميع أحكام الصوم من تبييت النية وغيره)','1591','757','0');
INSERT INTO [t151] VALUES (774,'هل تصلى التراويح ليلتئذ؟ على وجهين','1592','757','0');
INSERT INTO [t151] VALUES (775,'أما وقوع الطلاق المعلق بدخول رمضان، أو حلول الدين المؤجل إليه، ونحوها من الأحكام، فلا تثبت بيوم الغيم','1594','757','0');
INSERT INTO [t151] VALUES (776,'مسألة: (وإذا رأى الهلال وحده، صام)','1595','732','0');
INSERT INTO [t151] VALUES (777,'الرواية الثانية: لا يصوم إذا انفرد برؤيته وردت شهادته','1596','776','0');
INSERT INTO [t151] VALUES (778,'مسألة: (فإن كان عدلا صام الناس بقوله)','1599','732','0');
INSERT INTO [t151] VALUES (779,'وفي رواية: لا يقبل إلا عدلان كسائر الشهور','1600','778','0');
INSERT INTO [t151] VALUES (780,'أدلة قبول شهادة الواحد','1601','778','0');
INSERT INTO [t151] VALUES (781,'وتصلى التراويح ليلتئذ','1605','778','0');
INSERT INTO [t151] VALUES (782,'فصل (يقبل فيه شهادة الواحد سواء كان حرا أو عبدا، رجلا أو امرأة)','1605','778','0');
INSERT INTO [t151] VALUES (783,'مسألة: (ولا يفطر إلا بشهادة عدلين)','1606','732','0');
INSERT INTO [t151] VALUES (784,'مسألة: (وإذا صاموا بشهادة اثنين ثلاثين يوما، أفطروا. وإن كان بغيم أو قول واحد، لم يفطروا إلا أن يروه أو يكملوا العدة)','1609','732','0');
INSERT INTO [t151] VALUES (785,'إن صاموا ثمانية وعشرين يوما، وكانوا قد أكملوا عدة شعبان لإصحاء السماء وكونهم لم يروه، فإنهم يقضون يوما','1611','784','0');
INSERT INTO [t151] VALUES (786,'إذا رأى هلال الفطر وحده لم يجز له أن يفطر','1613','784','0');
INSERT INTO [t151] VALUES (787,'فصل (وإذا شهد بالرؤية واحد أو اثنان أو أكثر عند بعض الناس ولم يثبت عند الإمام؛ فإنه يجب على من سمع خبرهم وعرف عدالتهم أن يصوم بخبرهم)','1617','784','0');
INSERT INTO [t151] VALUES (788,'مسألة: (وإذا اشتبهت الأشهر على الأسير تحرى وصام، فإن وافق الشهر أو بعده أجزأه، وإن وافق قبله لم يجزئه)','1618','732','0');
INSERT INTO [t151] VALUES (789,'فصل (وإذا رئي الهلال بعد الزوال فهو لليلة المقبلة، وإن رئي قبل الزوال فاختلف هل هو لليلة المقبلة أو الماضية)','1621','788','0');
INSERT INTO [t151] VALUES (790,'الرواية الثالثة: إن رئي قبل الزوال في أول الشهر فهو لليلة الماضية، وإن رئي كذلك في آخر الشهر فهو للمقبلة','1622','788','0');
INSERT INTO [t151] VALUES (791,'فصل (إذا رأى الهلال أهل بلد لزم سائر البلدان الصوم، وإن لم يروه)','1626','788','0');
INSERT INTO [t151] VALUES (792,'فصل (ولا يصح الصوم إلا بنية كسائر العبادات)','1630','788','0');
INSERT INTO [t151] VALUES (793,'وجوب تبييت النية','1631','788','0');
INSERT INTO [t151] VALUES (794,'الصوم التطوع يجزئ بنية من النهار','1638','788','0');
INSERT INTO [t151] VALUES (795,'فصل (من صام فرضا أو قضاء أو نذرا أجمع عليه من الليل)','1642','788','0');
INSERT INTO [t151] VALUES (796,'فصل (تصح النية في جميع ليلة الصوم، ولا يجب استصحاب ذكرها)','1644','788','0');
INSERT INTO [t151] VALUES (797,'إن تردد في قطع الصوم، أو نوى أنه يقطعه فيما بعد؛ فهو على الخلاف في الصلاة','1645','788','0');
INSERT INTO [t151] VALUES (798,'إن نوى نهارا قبل يوم الصوم بليلة، ففيه روايتان','1646','788','0');
INSERT INTO [t151] VALUES (799,'فصل (هل يشترط أن ينوي نية الفريضة؟ على وجهين)','1647','788','0');
INSERT INTO [t151] VALUES (800,'فصل (لا يجزئ الواجب من الكفارة والقضاء والنذر المطلق إلا بتعيين النية)','1650','788','0');
INSERT INTO [t151] VALUES (801,'إذا صام رمضان ينوي به تطوعا هل يجزئه؟ ثلاث روايات','1650','788','0');
INSERT INTO [t151] VALUES (802,'إن قال ليلة الغيم: إن كان من رمضان فأنا صائم، وإلا فأنا مفطر= لم يجزئه بحال','1653','788','0');
INSERT INTO [t151] VALUES (803,'باب أحكام المفطرين في رمضان','1655','731','0');
INSERT INTO [t151] VALUES (804,'مسألة: (ويباح الفطر في رمضان لأربعة أقسام: أحدها: المريض الذي يتضرر به، والمسافر الذي له القصر، فالفطر لهما أفضل)','1655','803','0');
INSERT INTO [t151] VALUES (805,'الفصل الأول: أن المريض والمسافر يباح لهما الفطر','1655','804','0');
INSERT INTO [t151] VALUES (806,'الفصل الثاني: أن السفر المبيح للفطر هو السفر المبيح للقصر، والمرض المبيح للفطر هو الذي لا يطاق معه الصيام أو الذي يزيده الصيام','1656','804','0');
INSERT INTO [t151] VALUES (807,'في معنى المريض: الصحيح الذي يخاف من الصوم مرضا أو جهدا شديدا','1657','806','0');
INSERT INTO [t151] VALUES (808,'الفصل الثالث: المريض والمسافر يستحب لهما الفطر، فإن صاما أجزأهما','1658','804','0');
INSERT INTO [t151] VALUES (809,'وجه اختيار الفطر على الصيام للمسافر','1661','804','0');
INSERT INTO [t151] VALUES (810,'عامة الصحابة اختاروا الفطر في السفر','1670','804','0');
INSERT INTO [t151] VALUES (811,'والصحيح: أنه من شق عليه الصوم، أو يصير كلا على رفقائه، أو صام تنطعا واعتقادا أن الفطر نقص في الدين = كره له الصوم، وإلا فلا','1675','804','0');
INSERT INTO [t151] VALUES (812,'مسألة: (والثاني: الحائض والنفساء يفطران ويقضيان، وإن صامتا لم يجزئهما)','1679','803','0');
INSERT INTO [t151] VALUES (813,'مسألة: (والثالث: الحامل والمرضع إذا خافتا على ولديهما، أفطرتا وقضتا وأطعمتا عن كل يوم مسكينا، وإن صامتا أجزأهما)','1680','803','0');
INSERT INTO [t151] VALUES (814,'الفصل الأول: أن المرأة الحامل إذا خافت من الصوم على ولدها، جاز لها أن تفطر، وعليها مع القضاء فدية طعام مسكين عن كل يوم','1680','813','0');
INSERT INTO [t151] VALUES (815,'أما إن خافت على نفسها، فتفطر وتقضي ولا فدية عليها','1683','814','0');
INSERT INTO [t151] VALUES (816,'الفصل الثاني: في المرضع (وهي كالحامل في وجوب الفدية)','1686','813','0');
INSERT INTO [t151] VALUES (817,'فصل (لو أحاط العدو ببلد، وكان الصوم يضعفهم، فهل لهم الفطر؟ روايتان)','1688','816','0');
INSERT INTO [t151] VALUES (818,'مسألة: (الرابع: العاجز عن الصوم لكبر أو مرض لا يرجى برؤه، فإنه يطعم عنه لكل يوم مسكين)','1688','803','0');
INSERT INTO [t151] VALUES (819,'حكم من كان به الشبق الذي يخاف من الصوم معه أن ينشق أنثياه','1689','818','0');
INSERT INTO [t151] VALUES (820,'ليس المراد بالشيخ الكبير من بلغ حدا إن ترك الأكل هلك، وإنما الذي يلحقه مشقة شديدة في الصيام','1691','818','0');
INSERT INTO [t151] VALUES (821,'الاستدلال على المسألة بقوله تعالى: {وعلى الذين يطيقونه فدية طعام مسكين} والخلاف في نسخه','1691','818','0');
INSERT INTO [t151] VALUES (822,'فصل (إن قوي الشيخ ونحوه على القضاء بعد إخراج الفدية، فلا قضاء عليه)','1699','818','0');
INSERT INTO [t151] VALUES (823,'مسألة: (وعلى سائر من أفطر القضاء لا غير، إلا من أفطر بجماع في الفرج، فإنه يقضي ويعتق رقبة، فإن لم يجد فصيام شهرين متتابعين، فإن لم يستطع فإطعام ستين مسكينا، فإن لم يجد سقطت عنه)','1699','803','0');
INSERT INTO [t151] VALUES (824,'الفصل الأول: أن المفطرين قسمان','1700','823','0');
INSERT INTO [t151] VALUES (825,'أحدهما: من يباح له الفطر، وهم أربعة: المريض والمسافر، والحائض والنفساء، والمرضع والحامل، والعاجز عن الصوم','1700','824','0');
INSERT INTO [t151] VALUES (826,'الثاني: من أفطر بغير هذه الأعذار، فهؤلاء يجب عليهم القضاء عن كل يوم يوما','1700','824','0');
INSERT INTO [t151] VALUES (827,'الفصل الثاني: أنه لا كفارة بالفطر في رمضان إلا بالجماع وأسبابه','1703','823','0');
INSERT INTO [t151] VALUES (828,'من قال: كل من أفطر عامدا عالما يجب عليه الكفارة','1704','827','0');
INSERT INTO [t151] VALUES (829,'بيان أن الصحيح هو القول الأول','1706','827','0');
INSERT INTO [t151] VALUES (830,'الفصل الثالث: أن الجماع في الفرج يوجب الكفارة، وهذا كالمجمع عليه','1711','823','0');
INSERT INTO [t151] VALUES (831,'كفارة الجماع على الترتيب في الرواية المنصورة','1714','830','0');
INSERT INTO [t151] VALUES (832,'فصل (إن عجز عن الكفارات الثلاثة، فهل تسقط عنه أو تبقى في ذمته؟ روايتان)','1716','830','0');
INSERT INTO [t151] VALUES (833,'إذا عجز عن الكفارة وأطعم عنه غيره، فهل يجوز أن يصرفه إلى نفسه وعياله؟','1718','830','0');
INSERT INTO [t151] VALUES (834,'فصل (يجب العتق إذا وجد الرقبة أو ثمنها فاضلا عن حوائجه الأصلية)','1721','830','0');
INSERT INTO [t151] VALUES (835,'الفصل الرابع: أن الكفارة تجب بالجماع في الفرج، سواء كان قبلا أو دبرا، وسواء أنزل أو لم ينزل','1721','823','0');
INSERT INTO [t151] VALUES (836,'أما المباشرة فيما دون الفرج إذا لم ينزل بها، فلا قضاء عليه ولا كفارة','1722','835','0');
INSERT INTO [t151] VALUES (837,'وإن أنزل الماء الأعظم بالمباشرة فيما دون الفرج فسد صومه، وفي الكفارة ثلاث روايات','1723','835','0');
INSERT INTO [t151] VALUES (838,'وإن أمذى بالمباشرة، فعليه القضاء دون الكفارة','1725','835','0');
INSERT INTO [t151] VALUES (839,'إن كرر النظر فأمنى لزمه القضاء، وفي الكفارة روايتان','1727','835','0');
INSERT INTO [t151] VALUES (840,'الخلاف إن تفكر فأمذى أو أنزل','1728','835','0');
INSERT INTO [t151] VALUES (841,'فصل (لا تجب الكفارة إلا في شهر رمضان، فلو جامع في القضاء أو النذر أو الكفارة، لم تجب عليه الكفارة)','1730','835','0');
INSERT INTO [t151] VALUES (842,'مسألة: (فإن جامع ولم يكفر حتى جامع ثانية، فكفارة واحدة. وإن كفر ثم جامع فكفارة ثانية، وكل من لزمه الإمساك في رمضان فجامع، فعليه كفارة)','1731','823','0');
INSERT INTO [t151] VALUES (843,'إن وطئ في يومين ولم يكفر، فهل عليه كفارة واحدة أو كفرتان؟','1733','823','0');
INSERT INTO [t151] VALUES (844,'فصل (لا فرق في إيجاب الكفارة بالإجماع بين المعذور وغير المعذور)','1734','823','0');
INSERT INTO [t151] VALUES (845,'والرواية الثانية: أن من وطء ناسيا عليه القضاء دون الكفارة','1735','823','0');
INSERT INTO [t151] VALUES (846,'وأما مقدمات الجماع التي لا توجب الكفارة إذا فعلها ناسيا فأمنى أو أمذى، فهو على صيامه، ولا قضاء عليه','1740','823','0');
INSERT INTO [t151] VALUES (847,'وإن أكره الرجل على الجماع، فعليه القضاء والكفارة','1740','823','0');
INSERT INTO [t151] VALUES (848,'فصل (أما المرأة، فإن كانت مطاوعة، فسد صومها وفي الكفارة ثلاث روايات)','1741','823','0');
INSERT INTO [t151] VALUES (849,'الرواية الثالثة: عليها الكفارة في الحج دون الصوم','1743','823','0');
INSERT INTO [t151] VALUES (850,'فصل (إن كانت مستكرهة، هل يفسد صومها؟ روايتان)','1749','823','0');
INSERT INTO [t151] VALUES (851,'وهل عليها الكفارة ترجع بها عليه؟ روايتان','1751','823','0');
INSERT INTO [t151] VALUES (852,'فصل (إذا جامع ونزع قبل الفجر، ثم أمنى بذلك بعد طلوع الفجر، فصومه صحيح)','1756','823','0');
INSERT INTO [t151] VALUES (853,'وإن استدام الجماع بعد طلوع الفجر ولم يعلم ونزع قبل أن يعلم وجب عليه القضاء، وفي الكفارة روايتان','1757','823','0');
INSERT INTO [t151] VALUES (854,'فصل (ولو احتلم الصائم في النهار في المنام، لم يفطر)','1760','823','0');
INSERT INTO [t151] VALUES (855,'مسألة: (ومن أخر القضاء لعذر حتى أدركه رمضان آخر، فليس عليه غيره، وإن فرط أطعم مع القضاء لكل يوم مسكينا)','1761','803','0');
INSERT INTO [t151] VALUES (856,'الفصل الأول: أنه يجوز تأخير قضاء رمضان إلى شعبان','1761','855','0');
INSERT INTO [t151] VALUES (857,'يستحب أن يقضي رمضان متتابعا، وإن قضاه مفرقا جاز ولم يكره','1762','856','0');
INSERT INTO [t151] VALUES (858,'الفصل الثاني: أنه ليس له أن يؤخره إلى رمضان آخر إلا لعذر','1768','855','0');
INSERT INTO [t151] VALUES (859,'وإن أخره إلى الثاني لغير عذر أثم، وعليه أن يصوم الذي أدركه، ثم يقضي الأول، ويطعم لكل يوم مسكينا','1768','858','0');
INSERT INTO [t151] VALUES (860,'تقرير أن قضاء الرمضان مؤقت بما بين الرمضانين من خمسة وجوه','1771','858','0');
INSERT INTO [t151] VALUES (861,'فصل (إن كان قد أمكنه قضاء بعض ما فاته دون بعض، لزمه الإطعام عن قدر ما أمكنه قضاؤه. وإن أخره إلى رمضان ثالث لم يلزمه أكثر من كفارة مع الإثم)','1775','858','0');
INSERT INTO [t151] VALUES (862,'فصل ومن عليه قضاء رمضان، لا يجوز أن يصوم تطوعا','1776','858','0');
INSERT INTO [t151] VALUES (863,'الرواية الثانية: يجوز أن يتطوع قبل القضاء','1778','858','0');
INSERT INTO [t151] VALUES (864,'مسألة: (وإن ترك القضاء حتى مات لعذر، فلا شيء عليه، وإن كان لغير عذر أطعم عنه لكل يوم مسكينا، إلا أن يكون الصوم منذورا فإنه يصام عنه، وكذلك كل نذر طاعة)','1781','803','0');
INSERT INTO [t151] VALUES (865,'المسألة الأولى: من استمر به العذر من سفر أو مرض حتى مات، فإنه لا شيء عليه من قضاء ولا كفارة','1781','864','0');
INSERT INTO [t151] VALUES (866,'المسألة الثانية: إذا فرط في القضاء حتى مات قبل أن يدركه الرمضان الثاني، فإنه يطعم عنه لكل يوم مسكين','1784','864','0');
INSERT INTO [t151] VALUES (867,'فصل (فإن فرط حتى أدركه رمضان الثاني قبل أن يصوم، ومات في أثناء ذلك الرمضان أو بعده قبل أن يصوم؛ هل يطعم عنه لكل يوم مسكين أو مسكينان؟)','1788','866','0');
INSERT INTO [t151] VALUES (868,'المسألة الثالثة: أن الصوم المنذور إذا مات قبل فعله فإنه يصام عنه، بخلاف صوم رمضان وصوم الكفارة','1789','864','0');
INSERT INTO [t151] VALUES (869,'فصل (ويصام النذر عنه، سواء تركه لعذر أو لغير عذر)','1794','868','0');
INSERT INTO [t151] VALUES (870,'فصل (هل يجوز أن يصوم عنه أكثر من واحد في يوم؟)','1797','868','0');
INSERT INTO [t151] VALUES (871,'المسألة الرابعة: إذا نذر غير الصوم من عتق أو صدقة أو هدي أو حج، فإنه يجوز أن يفعله عنه وليه، أوصى أو لم يوص','1798','864','0');
INSERT INTO [t151] VALUES (872,'وأما الصلاة المنذورة والقرآن والذكر والدعاء، فهل يفعل بعد الموت؟ على روايتين','1799','871','0');
INSERT INTO [t151] VALUES (873,'وإذا نذر أن يعتكف فمات قبل أن يعتكف، ينبغي لأهله أن يعتكفوا عنه','1801','871','0');
INSERT INTO [t151] VALUES (874,'باب ما يفسد الصوم','1802','731','0');
INSERT INTO [t151] VALUES (875,'مسألة: (ومن أكل أو شرب، أو استعط، أو أوصل إلى جوفه شيئا من أي موضع كان، أو استقاء، أو استمنى، أو قبل أو لمس فأمنى أو أمذى، أو كرر النظر حتى أنزل، أو حجم أو احتجم، عامدا ذاكرا لصومه فسد، وإن فعله ناسيا أو مكرها لم يفسد)','1802','874','0');
INSERT INTO [t151] VALUES (876,'الفصل الأول: أن الصوم يفسد بالجماع وبالأكل والشرب','1802','875','0');
INSERT INTO [t151] VALUES (877,'الفصل الثاني: أن الواصل إلى الجوف يفطر من أي موضع دخل','1804','875','0');
