INSERT INTO [t151] VALUES (904,'الفصل الثاني: إذا تمضمض أو استنشق ولم يزد على الثلاث ولم يبالغ، فسبقه الماء فدخل في جوفه، فإنه لا يفطر','1869','902','0');
