INSERT INTO [t151] VALUES (971,'فصل (يكره إفراد يوم الجمعة بالصوم)','2030','966','0');
INSERT INTO [t151] VALUES (972,'فصل (يكره إفراد يوم السبت بالصيام عند أكثر أصحابنا، وعنه ما يدل على أنه لا يكره)','2034','966','0');
INSERT INTO [t151] VALUES (973,'فصل (قال أصحابنا: ويكره إفراد يوم النيروز ويوم المهرجان)','2038','966','0');
INSERT INTO [t151] VALUES (974,'مسألة: (وليلة القدر في الوتر من العشر الأواخر من رمضان)','2039','936','0');
INSERT INTO [t151] VALUES (975,'ذكر الأحاديث الواردة في تعيين ليلة القدر','2041','974','0');
INSERT INTO [t151] VALUES (976,'دلالة الأحاديث على أنها في العشر الأواخر، وأن السبع الأواخر أرجى، وأن أرجاها ليالي الوتر. ثم الوتر قد يكون باعتبار ما بقي أو باعتبار ما مضى','2049','974','0');
INSERT INTO [t151] VALUES (977,'توهم بعض الناس أنها رفعت، وهو خطأ، وإنما رفع علمها ومعرفتها في ذلك العام','2052','974','0');
INSERT INTO [t151] VALUES (978,'ما ورد مما يدل على أنها ليلة سبع وعشرين','2053','974','0');
INSERT INTO [t151] VALUES (979,'لا نجزم لليلة بعينها أنها ليلة القدر على الإطلاق، بل هي مبهمة في العشر','2064','974','0');
INSERT INTO [t151] VALUES (980,'فصل (وعلامتها: أن الشمس تطلع يومئذ لا شعاع لها)','2064','974','0');
INSERT INTO [t151] VALUES (981,'فصل (يستحب الاجتهاد في العشر مطلقا)','2066','974','0');
INSERT INTO [t151] VALUES (982,'باب الاعتكاف','2069','731','0');
INSERT INTO [t151] VALUES (983,'مسألة: (وهو لزوم المسجد لطاعة الله فيه)','2069','982','0');
INSERT INTO [t151] VALUES (984,'معنى الاعتكاف لغة: الاحتباس والوقوف والمقام','2069','983','0');
INSERT INTO [t151] VALUES (985,'الاعتكاف شرعا: لزوم المسجد لطاعة الله فيه','2071','983','0');
INSERT INTO [t151] VALUES (986,'مسألة: (وهو سنة، لا يجب إلا بالنذر)','2072','982','0');
INSERT INTO [t151] VALUES (987,'الفصل الأول: أن الاعتكاف سنة وقربة بالكتاب والسنة والإجماع','2072','986','0');
INSERT INTO [t151] VALUES (988,'الفصل الثاني: أنه ليس بواجب في الشرع، بل يجب بالنذر','2075','986','0');
INSERT INTO [t151] VALUES (989,'إذا شرع في الاعتكاف، لم يلزم بالشروع، ولو قطعه مدة لم يلزمه قضاؤه','2077','988','0');
INSERT INTO [t151] VALUES (990,'فإن قيل: إذا كان له الخروج منه، وأن يدخل فيه متى شاء، فما معنى قولهم: يحرم على المعتكف كذا، ويجب عليه كذا؟','2078','988','0');
INSERT INTO [t151] VALUES (991,'فصل (لا يصح الاعتكاف إلا من مسلم عاقل)','2080','988','0');
INSERT INTO [t151] VALUES (992,'فصل (ليس للرقيق الاعتكاف بغير إذن السيد، ولا للزوجة بدون إذن الزوج)','2080','988','0');
INSERT INTO [t151] VALUES (993,'مسألة: (ويصح من المرأة في كل مسجد، ولا يصح من الرجل إلا في مسجد تقام فيه الجماعة. واعتكافه في مسجد تقام فيه الجمعة أفضل)','2082','982','0');
INSERT INTO [t151] VALUES (994,'الفصل الأول: أن الاعتكاف لا يصح إلا في مسجد، ويصح في كل مسجد في الجملة','2082','993','0');
INSERT INTO [t151] VALUES (995,'الفصل الثاني: أن المسجد هو المكان المبني للصلوات الخمس، وبيت قناديله وسطحه، وحوائطه، والمنارة المبنية في حيطانه= جزء منه','2084','993','0');
INSERT INTO [t151] VALUES (996,'أما الرحبة: ففيها روايتان','2085','995','0');
INSERT INTO [t151] VALUES (997,'الفصل الثالث: أنه لا يصح اعتكاف الرجل إلا في مسجد تقام فيه الصلوات الخمس جماعة','2086','993','0');
INSERT INTO [t151] VALUES (998,'إنه إجماع الصحابة، وقول عامة التابعين','2089','997','0');
INSERT INTO [t151] VALUES (999,'أما من لا تجب عليه الجماعة كالمريض والعبد، ففيه وجهان','2092','997','0');
INSERT INTO [t151] VALUES (1000,'الفصل الرابع: أن المرأة لا يصح اعتكافها إلا في المسجد المتخذ للصلوات الخمس','2093','993','0');
INSERT INTO [t151] VALUES (1001,'هل يجوز اعتكافها في مسجد لا تقام فيه الجماعة؟','2097','1000','0');
INSERT INTO [t151] VALUES (1002,'لا يكره الاعتكاف للعجوز مطلقا، والمنصوص أنه لا يكره للشابة أيضا','2099','1000','0');
INSERT INTO [t151] VALUES (1003,'يستحب الاستتار للمعتكف بأن يضرب خباء في موضع لا تنقطع فيه الصفوف، ولا سيما النساء فالأفضل أن لا يرين الرجال ولا يراهن الرجال','2102','1000','0');
INSERT INTO [t151] VALUES (1004,'الفصل الخامس: أن الاعتكاف في المسجد الجامع الذي تقام فيه الجمعة أفضل، فإذا اعتكف في غيره وجب عليه الخروج إلى صلاة الجمعة','2103','993','0');
INSERT INTO [t151] VALUES (1005,'ركن الاعتكاف شيئان: لزوم المسجد والنية، وأما الصوم فسنة','2105','1004','0');
INSERT INTO [t151] VALUES (1006,'إن اعتكف بدون الصوم، فهل يصح؟ على روايتين','2106','1004','0');
INSERT INTO [t151] VALUES (1007,'عدم اشتراط الصوم للاعتكاف هو اختيار الأصحاب، وسياق الأدلة على ترجيحه','2109','1004','0');
INSERT INTO [t151] VALUES (1008,'ليس لأقله حد، بل يجزئ ما يقع عليه الاسم من اللبث في المسجد','2117','1004','0');
INSERT INTO [t151] VALUES (1009,'وإذا نذر أن يعتكف صائما أو وهو صائم، لزمه ذلك','2118','1004','0');
INSERT INTO [t151] VALUES (1010,'مسألة: (ومن نذر الاعتكاف أو الصلاة في مسجد، فله فعل ذلك في غيره، إلا المساجد الثلاثة، فإذا نذر ذلك في المسجد الحرام لزمه، وإن نذره في مسجد المدينة فله فعله في المسجد الحرام وحده، وإن نذره في المسجد الأقصى فله فعله فيهما)','2120','982','0');
INSERT INTO [t151] VALUES (1011,'المسألة الأولى: أنه إذا نذر الصلاة أو الاعتكاف في مسجد بعينه غير المساجد الثلاثة، فله فعل ذلك فيه وفي غيره من المساجد','2120','1010','0');
INSERT INTO [t151] VALUES (1012,'إذا صلى واعتكف في غير المسجد الذي عينه، فهل يلزمه كفارة يمين؟ على وجهين','2121','1011','0');
INSERT INTO [t151] VALUES (1013,'المسألة الثانية: أنه إذا نذر الصلاة أو الاعتكاف في المسجد الحرام، لم يجزئه إلا فيه، وإن نذره في مسجد النبي - صلى الله عليه وسلم -لم يجزئه إلا فيه أو في المسجد الحرام، وإن نذره في المسجد الأقصى لم يجزئه إلا في أحد الثلاثة','2123','1010','0');
INSERT INTO [t151] VALUES (1014,'فصل (إذا نذر المشي إلى بيت المقدس أو إلى مسجد النبي - صلى الله عليه وسلم -، انعقد نذره ولزمه ذلك، وكان موجبه الصلاة فيه)','2128','1013','0');
INSERT INTO [t151] VALUES (1015,'فصل (إن نذر الصوم بمكان بعينه أجزأه الصوم بكل مكان)','2128','1013','0');
INSERT INTO [t151] VALUES (1016,'فصل (إذا نذر صوما أو صلاة أو اعتكافا في وقت بعينه)','2128','1013','0');
INSERT INTO [t151] VALUES (1017,'فصل (إذا أراد أن يعتكف العشر الأواخر، فإنه يدخل معتكفه قبل غروب الشمس من أول ليلة)','2129','1013','0');
INSERT INTO [t151] VALUES (1018,'فصل (من نذر اعتكاف ليلة لم يلزمه يومها، وإن نذر اعتكاف يوم لم تلزمه ليلته)','2132','1013','0');
INSERT INTO [t151] VALUES (1019,'وإن نذر اعتكاف شهر بعينه، دخل معتكفه قبل غروب الشمس من أول ليلة من الشهر، فإذا طلع هلال الشهر الثاني خرج من معتكفه','2134','1013','0');
INSERT INTO [t151] VALUES (1020,'وإن نذر اعتكاف عشرة أيام متتابعة؛ فهل تدخل الليلة الأولى؟ على روايتين، أصرحهما أنها لا تدخل','2135','1013','0');
INSERT INTO [t151] VALUES (1021,'فصل (إذا نذر اعتكاف شهر مطلق، أجزأه ما بين الهلالين وإن كان ناقصا، وإن كان شرع في أثناء شهر، لزمه استيفاء ثلاثين يوما متتابعا)','2137','1013','0');
INSERT INTO [t151] VALUES (1022,'إذا نذر صيام شهر، فهو متتابع، وإذا قال: ثلاثين يوما، فله أن يفرق','2138','1013','0');
INSERT INTO [t151] VALUES (1023,'فصل (إذا نذر اعتكاف يوم يقدم فلان، انعقد نذره)','2139','1013','0');
INSERT INTO [t151] VALUES (1024,'مسألة: (ويستحب للمعتكف الاشتغال بالقرب، واجتناب ما لا يعنيه من قول أو فعل)','2141','982','0');
INSERT INTO [t151] VALUES (1025,'الفصل الأول: ينبغي للمعتكف أن يشتغل بالعبادات المحضة التي بينه وبين الله تعالى مثل: القرآن، وذكر الله، والدعاء، والاستغفار، والصلاة، والتفكر، ونحو ذلك','2141','1024','0');
INSERT INTO [t151] VALUES (1026,'هل الأفضل للمعتكف أن يشتغل بإقراء القرآن والفقه أو يشتغل بنفسه؟ على روايتين، والمشهور عنه أنه يشتغل بنفسه','2142','1025','0');
INSERT INTO [t151] VALUES (1027,'وأما كون النفع المتعدي أفضل فعنه ثلاثة أجوبة','2143','1025','0');
INSERT INTO [t151] VALUES (1028,'فصل (ينام المعتكف متربعا، ويكون ماء طهارته معدا معلوما)','2144','1025','0');
INSERT INTO [t151] VALUES (1029,'الفصل الثاني: ينبغي له اجتناب ما لا يعنيه من القول والعمل','2145','1024','0');
INSERT INTO [t151] VALUES (1030,'أما الصمت عن كل كلام، فليس بمشروع في دين الإسلام','2147','1029','0');
INSERT INTO [t151] VALUES (1031,'لا يجوز أن يجعل القرآن بدلا عن الكلام','2149','1029','0');
INSERT INTO [t151] VALUES (1032,'فصل (يعود المريض ولا يجلس، ولا يتجر ولا يصنع، إلا أن يشتري ما لابد له منه)','2151','1029','0');
INSERT INTO [t151] VALUES (1033,'فصل (يجوز للمعتكف أن يغسل رأسه ويرجله حال الاعتكاف)','2153','1029','0');
INSERT INTO [t151] VALUES (1034,'مسألة: (ولا يخرج من المسجد إلا لما لابد له منه، إلا أن يشترط)','2155','982','0');
INSERT INTO [t151] VALUES (1035,'يجوز الخروج لكل ما يخاف من تركه ضررا في دينه أو دنياه، فيدخل في ذلك الخروج لفعل واجب وترك محرم وإزالة ضرر','2157','1034','0');
INSERT INTO [t151] VALUES (1036,'فصل (أما عيادة المريض وشهود الجنازة، ففيه روايتان منصوصتان)','2159','1034','0');
INSERT INTO [t151] VALUES (1037,'الرواية الثانية، وهي اختيار عامة الأصحاب: لا يجوز ذلك إلا بشرط','2160','1034','0');
INSERT INTO [t151] VALUES (1038,'فصل (الخروج ثلاثة أقسام: ما يجوز مطلقا، ما يجوز بالشرط، ما لا يجوز مطلقا)','2165','1034','0');
INSERT INTO [t151] VALUES (1039,'فصل (إن قال: علي أن أعتكف شهر رمضان إن لم أكن مريضا أو مسافرا، جاز؛ لأن النذر يصح تعليقه بشرط)','2168','1034','0');
INSERT INTO [t151] VALUES (1040,'مسألة: (ولا يباشر امرأة)','2169','982','0');
INSERT INTO [t151] VALUES (1041,'يبطل الاعتكاف بالوطء، سواء كان عامدا أو ناسيا، عالما أو جاهلا','2170','1040','0');
INSERT INTO [t151] VALUES (1042,'إن خرج من المسجد ناسيا، فهل يبطل؟ وجهان','2171','1040','0');
INSERT INTO [t151] VALUES (1043,'فصل (إذا أبطل اعتكافا لزمه قضاؤه، وهل عليه كفارة؟ على روايتين)','2172','1040','0');
INSERT INTO [t151] VALUES (1044,'على الرواية الثانية، هل تجب الكفارة إذا وطئ ليلا، أو نهارا فقط؟ فتكون في المسألة ثلاث روايات','2174','1040','0');
INSERT INTO [t151] VALUES (1045,'الرواية الثالثة: إن وطئ نهارا وجبت عليه كفارة الظهار لأجل رمضان، وإذا وطئ ليلا وليس هو واجبا عليه فلا كفارة عليه، وإن كان واجبا وجبت عليه كفارة ترك النذر','2176','1040','0');
INSERT INTO [t151] VALUES (1046,'فصل (ويبطل الاعتكاف أيضا بالردة، وبالسكر)','2178','1040','0');
INSERT INTO [t151] VALUES (1047,'فصل (إذا ترك الاعتكاف بالخروج من المعتكف؛ فإما أن يكون نذرا أو تطوعا)','2178','1040','0');
INSERT INTO [t151] VALUES (1048,'أما النذر فأربعة أقسام','2179','1040','0');
INSERT INTO [t151] VALUES (1049,'أحدها: أن يكون نذرا معينا، فهل يبطل ما مضى بالخروج أو لا؟ روايتان','2179','1048','0');
INSERT INTO [t151] VALUES (1050,'الثاني: أن ينذر اعتكاف الوقت المعين متتابعا، فإذا ترك بعضه كان عليه استئناف الاعتكاف، فيعتكف ما بقي، ويصله بالقضاء','2180','1048','0');
INSERT INTO [t151] VALUES (1051,'الثالث: أن ينذر اعتكافا متتابعا غير معين، فإذا ترك بعضه كان عليه أن يستأنف الاعتكاف في أي وقت كان، ولا كفارة عليه','2180','1048','0');
INSERT INTO [t151] VALUES (1052,'الرابع: أن ينذر اعتكافا مطلقا غير متتابع، فإذا ترك اعتكاف بعضها لم يبطل غير ذلك اليوم','2180','1048','0');
INSERT INTO [t151] VALUES (1053,'فصل (لو نذر اعتكاف العشر الأواخر من رمضان، ثم أفسده، لزمه أن يقضيه من قابل في مثل وقته)','2182','1040','0');
INSERT INTO [t151] VALUES (1054,'فصل (إن كان الاعتكاف الذي أفسده تطوعا، فلا قضاء عليه)','2184','1040','0');
INSERT INTO [t151] VALUES (1055,'مسألة: (وإن سأل عن المريض أو غيره في طريقه، ولم يعرج عليه جاز)','2186','982','0');
INSERT INTO [t151] VALUES (1056,'فصل في تفصيل الأسباب المبيحة للخروج وأحكامها','2187','1055','0');
INSERT INTO [t151] VALUES (1057,'أحدها: الخروج لحاجة الإنسان من البول والغائط، وهو في خروجه في حكم المعتكف','2187','1055','0');
INSERT INTO [t151] VALUES (1058,'إذا خرج لحاجة الإنسان، فدخل في طريقه إلى مسجد آخر ليتم فيه بقية اعتكافه، جاز','2190','1055','0');
INSERT INTO [t151] VALUES (1059,'فصل (جواز خروجه للجمعة، والخلاف في قدر مكثه في المسجد الجامع)','2191','1055','0');
INSERT INTO [t151] VALUES (1060,'فصل (إذا جوزنا له الخروج لعيادة المريض وتشييع الجنازة بغير شرط أو كان قد اشترطه، فإنه لا يزيد على الأمر المسنون)','2193','1055','0');
INSERT INTO [t151] VALUES (1061,'فصل (قال أحمد: يجب على المعتكف أن لا يؤويه إلا سقف المسجد)','2193','1055','0');
INSERT INTO [t151] VALUES (1062,'فصل (لا يخرج من معتكفه إلا لحاجة الإنسان أو صلاة الجمعة)','2194','1055','0');
INSERT INTO [t151] VALUES (1063,'فصل (وأما الأكل فعليه أن يأكل في المسجد، إلا أن يشترط الأكل في أهله)','2194','1055','0');
INSERT INTO [t151] VALUES (1064,'إذا أراد أن يأكل في المسجد، وضع مائدة أو غيرها لئلا يقع من طعامه ما يلوث المسجد','2197','1055','0');
INSERT INTO [t151] VALUES (1065,'فصل (إذا تعينت عليه شهادة أو أحضره سلطان بحق، لم يبطل اعتكافه)','2197','1055','0');
INSERT INTO [t151] VALUES (1066,'فصل (إذا حاضت المرأة أو نفست، وجب عليها الخروج من المسجد)','2198','1055','0');
INSERT INTO [t151] VALUES (1067,'الحيض لا يبطل ما مضى من الاعتكاف، فإذا طهرت بنت على ما قبل الحيض','2200','1055','0');
INSERT INTO [t151] VALUES (1068,'أما المستحاضة، فإنها تقيم في المسجد','2201','1055','0');
INSERT INTO [t151] VALUES (1069,'فصل (إذا وجبت عليها عدة وفاة، فإنها تخرج لتعتد في منزلها ولو كان الاعتكاف منذورا)','2202','1055','0');
INSERT INTO [t151] VALUES (1070,'فصل (إذا وقعت فتنة خاف منها على نفسه أو ماله أو أهله، فله أن يخرج)','2204','1055','0');
INSERT INTO [t151] VALUES (1071,'فصل (إذا تعين عليه الخروج للجهاد، بأن يحضر عدو، أو يستنفر الإمام استنفارا عاما، فإنه يخرج ويدع اعتكافه)','2206','1055','0');
INSERT INTO [t151] VALUES (1072,'فصل (يستحب لمن اعتكف العشر الأواخر من رمضان أن يبيت ليلة العيد في معتكفه ويخرج منه إلى المصلى في ثياب اعتكافه)','2208','1055','0');
INSERT INTO [t151] VALUES (1073,'كتاب الحج','2210','0','0');
INSERT INTO [t151] VALUES (1074,'جماع معنى الحج في أصل اللغة','2210','1073','0');
INSERT INTO [t151] VALUES (1075,'وجوب الحج','2213','1073','0');
INSERT INTO [t151] VALUES (1076,'مسألة: (يجب الحج والعمرة مرةً في العمر على المسلم العاقل البالغ الحرِّ).','2213','1075','0');
INSERT INTO [t151] VALUES (1077,'الفصل الأول: أن الحج واجب في الجملة، وهو أحد مباني الإسلام','2213','1076','0');
INSERT INTO [t151] VALUES (1078,'الفصل الثاني: أن العمرة أيضا واجبة','2220','1076','0');
INSERT INTO [t151] VALUES (1079,'عنه رواية أخرى: أنها سنة','2221','1078','0');
INSERT INTO [t151] VALUES (1080,'أدلة الوجوب','2225','1078','0');
INSERT INTO [t151] VALUES (1081,'أن العمرة هي الحج الأصغر','2229','1078','0');
INSERT INTO [t151] VALUES (1082,'فصل (أهل مكة ليس عليهم عمرة)','2232','1078','0');
INSERT INTO [t151] VALUES (1083,'الفصل الثالث: أنهما إنما يجبان مرة في العمر بإيجاب الشرع، فأما إيجاب المرء على نفسه فيجب في الذمة بالنذر','2236','1076','0');
INSERT INTO [t151] VALUES (1084,'الفصل الرابع: أنه لا يجب الوجوب المقتضي للفعل وصحته إلا على مسلم','2238','1076','0');
INSERT INTO [t151] VALUES (1085,'إنما يجب على الكفار بمعنى أنهم يؤمرون به بشرطه، وأن الله يعاقبهم على تركه','2239','1084','0');
INSERT INTO [t151] VALUES (1086,'إن حج ثم ارتد ثم أسلم، فهل عليه أن يحج؟ روايتان','2241','1084','0');
INSERT INTO [t151] VALUES (1087,'الفصل الخامس: أنه لا حج على مجنون كسائر العبادات','2241','1076','0');
INSERT INTO [t151] VALUES (1088,'الفصل السادس: أنه لا حج على الصبي قبل البلوغ','2242','1076','0');
INSERT INTO [t151] VALUES (1089,'الفصل السابع: أنه لا يجب إلا على حر كامل الحرية','2243','1076','0');
INSERT INTO [t151] VALUES (1090,'فصل (تنقسم شروط وجوب الحج إلى نوعين: ما يشترط لصحة الحج، وإلى ما لا يشترط لصحته)','2244','1076','0');
INSERT INTO [t151] VALUES (1091,'مسألة: (إذا استطاع إليه سبيلا، وهو أن يجد زادا وراحلة بآلتها مما يصلح لمثله، فاضلا عما يحتاج إليه لقضاء ديونه ومؤنة نفسه وعياله على الدوام)','2245','1075','0');
INSERT INTO [t151] VALUES (1092,'الفصل الأول (استطاعة السبيل: ملك الزاد والراحلة)','2245','1091','0');
INSERT INTO [t151] VALUES (1093,'الفصل الثاني (من كان قادرا على تحصيل ثمن الزاد والراحلة بصنعة أو قبول هبة أو نحوه، لم يجب عليه ذلك)','2250','1091','0');
INSERT INTO [t151] VALUES (1094,'فصل (إذا بذل له ابنه أو غيره مالا يحج به، لم يلزمه قبوله)','2250','1093','0');
INSERT INTO [t151] VALUES (1095,'فصل (من لم يجد زادا وراحلة: إذا اكتسب حتى حصلهما فقد أحسن بذلك)','2261','1093','0');
INSERT INTO [t151] VALUES (1096,'فصل (إنما تعتبر الراحلة في حق من بينه وبين مكة مسافة القصر)','2262','1093','0');
INSERT INTO [t151] VALUES (1097,'الفصل الثالث (يعتبر أن يكون الزاد مما يقتاته مثله، والراحلة مما تصلح لمثله)','2263','1091','0');
INSERT INTO [t151] VALUES (1098,'هل يكره الحج في المحمل؟ روايتان','2265','1097','0');
INSERT INTO [t151] VALUES (1099,'إن كان يجد الزاد في المنازل في طريقه، لم يلزمه حمله من مصره','2266','1097','0');
INSERT INTO [t151] VALUES (1100,'الفصل الرابع (أن يجد ذلك بعد ما يحتاج إليه من قضاء دينه ومؤنة نفسه وعياله على الدوام)','2267','1091','0');
INSERT INTO [t151] VALUES (1101,'لا بد أن يرجع إلى كفاية له ولعياله على الدوام، من طعام وكسوة ومسكن','2269','1100','0');
INSERT INTO [t151] VALUES (1102,'إن كان له كتب علم يحتاج إليها لم يلزمه بيعها','2269','1100','0');
INSERT INTO [t151] VALUES (1103,'إذا لم يكن له صبر عن الزواج، والمال لا يكفي لهما، تزوج وترك الحج','2271','1100','0');
INSERT INTO [t151] VALUES (1104,'فصل (لا يجب عليه المسير إلا إذا اتسع الوقت للسير والأداء)','2272','1091','0');
INSERT INTO [t151] VALUES (1105,'إن كان في طريقه من يصده من قطاع الطريق أو نحوهم، لم يجب عليه السعي إلى الحج','2272','1104','0');
INSERT INTO [t151] VALUES (1106,'هل يجب بذل خفارة لهم إن أمكن؟ وجهان','2272','1104','0');
INSERT INTO [t151] VALUES (1107,'فصل (لا يجب عليه أن يحج بنفسه حتى يقدر على الركوب)','2274','1091','0');
INSERT INTO [t151] VALUES (1108,'إحجاجه عن نفسه واجب، سواء بلغ وهو معضوب، أو عضب بعد ذلك','2275','1107','0');
INSERT INTO [t151] VALUES (1109,'إذا أحج عن نفسه أجزأ عنه وإن عوفي','2277','1107','0');
INSERT INTO [t151] VALUES (1110,'فصل (إن كان العاجز عن الحج يرجو القدرة عليه، كالمريض والمحبوس ونحوهما، لم تجز له الاستنابة في فرض الحج)','2277','1091','0');
INSERT INTO [t151] VALUES (1111,'فصل (إمكان المسير والأداء بسعة الوقت، وخلو الطريق، والصحة: هل هو شرط للوجوب أو للزوم الأداء فقط؟ على روايتين)','2278','1091','0');
INSERT INTO [t151] VALUES (1112,'مسألة: (ويعتبر للمرأة وجود محرمها، وهو زوجها، ومن تحرم عليه على التأبيد بنسب أو سبب مباح)','2282','1075','0');
INSERT INTO [t151] VALUES (1113,'الفصل الأول (أن المرأة لا يجب عليها أن تسافر للحج، ولا يجوز لها ذلك إلا مع زوج أو ذي محرم)','2282','1112','0');
INSERT INTO [t151] VALUES (1114,'لا يجوز لها أن تسافر بغير محرم إلا في الهجرة','2286','1113','0');
INSERT INTO [t151] VALUES (1115,'الفصل الثاني في المحرم','2287','1112','0');
INSERT INTO [t151] VALUES (1116,'وجه تسمية الزوج محرما','2287','1115','0');
INSERT INTO [t151] VALUES (1117,'السبب قسمان: صهر، ورضاع','2288','1115','0');
INSERT INTO [t151] VALUES (1118,'مسألة: (فمن فرط حتى مات أخرج عنه من ماله حجة وعمرة)','2289','1075','0');
INSERT INTO [t151] VALUES (1119,'كذلك من وجب عليه ولم يفرط، مثل من كان به مرض يرجى برؤه، فإنه أيضا يخرج من ماله','2289','1118','0');
INSERT INTO [t151] VALUES (1120,'الحج دين يدخل في عموم قوله تعالى: {من بعد وصية يوصي بها أو دين}','2291','1118','0');
INSERT INTO [t151] VALUES (1121,'الحج عن المعضوب لا يجزئ عنه بدون إذنه','2299','1118','0');
INSERT INTO [t151] VALUES (1122,'من زعم أن العمل لا ينفع غير عامله في جميع المواضع فقد خرج عن دين الإسلام','2300','1118','0');
INSERT INTO [t151] VALUES (1123,'فصل (يجب الحج عن الميت والعاجز من حيث وجب عليه)','2300','1118','0');
INSERT INTO [t151] VALUES (1124,'فصل (متى ملك الزاد والراحلة وجب عليه أن يحج على الفور، فإن أخره عصى بذلك)','2304','1118','0');
INSERT INTO [t151] VALUES (1125,'اختيار أبي يعلى الصغير وغيره: وجوبه على التراخي','2305','1124','0');
INSERT INTO [t151] VALUES (1126,'وجوبه على الفور هو المذهب المعروف لمسلكين عام وخاص','2309','1124','0');
INSERT INTO [t151] VALUES (1127,'أما العام، فهو أن الأمر المطلق يقتضي امتثال على الفور','2309','1124','0');
INSERT INTO [t151] VALUES (1128,'أما الخاص، فأمر النبي صلى الله عليه وسلم من أراد الحج أن يتعجل','2310','1124','0');
INSERT INTO [t151] VALUES (1129,'وأيضا: ما ورد من الوعيد فيمن مات قبل أن يحج','2313','1124','0');
INSERT INTO [t151] VALUES (1130,'الجواب عن قولهم: إن الحج فرض متقدما، والنبي صلى الله عليه وسلم أخر أداءه','2319','1124','0');
INSERT INTO [t151] VALUES (1131,'فصل (الميت يحج عنه وليه،، فإن حج عنه أجنبي بدون إذن الوارث، فوجهان)','2330','1118','0');
INSERT INTO [t151] VALUES (1132,'فصل (إذا مات وعليه دين لآدمي ودين لله تعالى، فهل يتحاصان أو يقدم دين الآدمي؟ روايتان)','2331','1118','0');
INSERT INTO [t151] VALUES (1133,'فصل (الأولى أن يحج متبرعا بمال نفسه، ولا يأخذ دراهم يحج بها)','2332','1118','0');
INSERT INTO [t151] VALUES (1134,'فصل (يجوز الاستنابة في حج التطوع في الحياة وبعد الموت من المعضوب، والقادر في إحدى الروايتين)','2334','1118','0');
INSERT INTO [t151] VALUES (1135,'إن حج عن نفسه ثم أهدى ثوابها للميت، جاز','2336','1134','0');
INSERT INTO [t151] VALUES (1136,'فصل (من حج عن غيره، فإن حجه يقع عن المحجوج عنه كأنه هو الذي فعله بنفسه)','2336','1118','0');
INSERT INTO [t151] VALUES (1137,'فصل (يجوز حج الرجل عن المرأة، والعكس)','2337','1118','0');
INSERT INTO [t151] VALUES (1138,'فصل (لا يجوز الاستئجار على الحج وغيره من القرب المحضة)','2337','1118','0');
INSERT INTO [t151] VALUES (1139,'معنى قول أحمد: «يكري نفسه ويحج»','2339','1138','0');
INSERT INTO [t151] VALUES (1140,'فصل (على القول بجواز الاستئجار على الحج، فإنه يعتبر له شروط الإجارة)','2343','1138','0');
INSERT INTO [t151] VALUES (1141,'إذا لم يقدر له النفقة، فإنه ينفق بالمعروف، ويرد ما فضل','2345','1138','0');
INSERT INTO [t151] VALUES (1142,'فصل (أما الأجير الذي يكري نفسه لخدمة الجمال ونحوه، ويحج عن نفسه، فهو جائز)','2346','1118','0');
INSERT INTO [t151] VALUES (1143,'فصل (ما لزم النائب من الدماء بفعل محظور، فهو في ماله)','2348','1118','0');
INSERT INTO [t151] VALUES (1144,'فصل (ما أنفق زيادة على القدر المعتاد أو على ما لا بد منه فهو في ماله)','2348','1118','0');
INSERT INTO [t151] VALUES (1145,'فصل (إذا أمر بالحج فتمتع أو قرن جاز ذلك، والدم على النائب)','2349','1118','0');
INSERT INTO [t151] VALUES (1146,'مسألة: (ولا يصح الحج من كافر ولا مجنون)','2351','1075','0');
INSERT INTO [t151] VALUES (1147,'المجنون قسمان: أحدهما: الجنون المطبق، فهذا لا يصح حجه','2352','1146','0');
INSERT INTO [t151] VALUES (1148,'الثاني: أن يجن بعد إحرامه، فهذا إن كان صرعا وخنقا لم يبطل إحرامه','2353','1146','0');
INSERT INTO [t151] VALUES (1149,'مسألة: (ويصح من العبد والصبي، ولا يجزئهما)','2353','1075','0');
INSERT INTO [t151] VALUES (1150,'الفصل الأول (أن العبد يصح حجه، ولا يجزئه عن حجة الإسلام)','2354','1149','0');
INSERT INTO [t151] VALUES (1151,'وإن عتق أو بلغ الصبي أثناء الوقوف أجزأتهما تلك الحجة عن حجة الإسلام','2355','1150','0');
INSERT INTO [t151] VALUES (1152,'فصل (إذا أحرم العبد بإذن سيده لم يملك تحليله؛ لأنها عبادة تلزم بالشروع)','2357','1150','0');
INSERT INTO [t151] VALUES (1153,'فصل (إذا نذر العبد الحج معينا أو مطلقا فإنه ينعقد نذره)','2359','1150','0');
INSERT INTO [t151] VALUES (1154,'فصل (إذا أفسد العبد إحرامه فعليه المضي فيه، وعليه القضاء، سواء كان الإحرام مأذونا فيه أو غير مأذون فيه)','2360','1150','0');
INSERT INTO [t151] VALUES (1155,'فصل (كل محظور فعله بإذن سيده فجزاؤه على سيده، وإن كان بغير إذنه فهو على العبد)','2362','1150','0');
INSERT INTO [t151] VALUES (1156,'فصل (إذا حج الأعرابي ثم هاجر هل يجب عليه إعادة الحج؟ قولان)','2364','1150','0');
INSERT INTO [t151] VALUES (1157,'الفصل الثاني (أن حج الصبي صحيح، سواء كان مميزا أو طفلا)','2365','1149','0');
INSERT INTO [t151] VALUES (1158,'هل يصح إحرام المميز بدون إذن الولي؟ وجهان','2367','1157','0');
INSERT INTO [t151] VALUES (1159,'إن كان غير مميز عقد الإحرام له وليه','2367','1157','0');
INSERT INTO [t151] VALUES (1160,'لا يصح إحرام الأجانب عنه وجها واحدا','2370','1157','0');
INSERT INTO [t151] VALUES (1161,'فصل (لا يجوز للمرأة أن تسافر بدون إذن الزوج في حج التطوع، ويستحب لها أن تستأذنه في الفرض تطييبا لنفسه)','2372','1157','0');
INSERT INTO [t151] VALUES (1162,'مسألة: (ويصح من غير المستطيع والمرأة بغير محرم، ويجزئهما)','2373','1075','0');
INSERT INTO [t151] VALUES (1163,'مسألة: (ومن حج عن غيره ولم يكن حج عن نفسه، أو عن نذره ونفله قبل حجة الإسلام، وقع عن فرض نفسه دون غيره)','2373','1075','0');
INSERT INTO [t151] VALUES (1164,'الفصل الأول (أن من عليه حجة واجبة، فليس له أن يحج عن غيره حتى يحج عن نفسه)','2374','1163','0');
INSERT INTO [t151] VALUES (1165,'يجوز لغير المستطيع الذي لا يجب عليه الحج أن يحج عن غيره','2375','1164','0');
INSERT INTO [t151] VALUES (1166,'إذا خالف وأحرم عن غيره، ففيه روايتان','2378','1164','0');
INSERT INTO [t151] VALUES (1167,'الفصل الثاني (إذا كان عليه فرض ونفل، لم يجز أن يحرم إلا بالفرض، وإن كان عليه فرضان لم يجز أن يبدأ إلا بأوكدهما)','2380','1163','0');
INSERT INTO [t151] VALUES (1168,'باب المواقيت','2385','1073','0');
INSERT INTO [t151] VALUES (1169,'مسألة: (وميقات أهل المدينة ذو الحليفة، والشام ومصر والمغرب الجحفة، واليمن يلملم، ولنجد قرن، وللمشرق ذات عرق)','2385','1168','0');
INSERT INTO [t151] VALUES (1170,'توقيت النبي صلى الله عليه وسلم للمواقيت كان في ثلاث مراحل','2386','1169','0');
INSERT INTO [t151] VALUES (1171,'هل تحديد ذات عرق باجتهاد عمر، أو بتوقيت النبي صلى الله عليه وسلم؟','2391','1169','0');
INSERT INTO [t151] VALUES (1172,'فصل (أبعد المواقيت ذو الحليفة، ثم جحفة، ثم الثلاثة على مسافة متقاربة)','2393','1169','0');
INSERT INTO [t151] VALUES (1173,'مسألة: (وهذه المواقيت لأهلها، ولكل من مر عليها)','2396','1168','0');
INSERT INTO [t151] VALUES (1174,'ومن مر على ميقاتين فعليه أن يحرم من أبعدهما من مكة','2396','1173','0');
INSERT INTO [t151] VALUES (1175,'مسألة: (ومن كان منزله دون الميقات فميقاته من موضعه، حتى أهل مكة يهلون منها لحجهم، ويهلون للعمرة من الحل)','2398','1168','0');
INSERT INTO [t151] VALUES (1176,'الفصل الأول: في غير المكي إذا كان مسكنه دون الميقات إلى مكة، فإنه يهل من أهله','2398','1175','0');
INSERT INTO [t151] VALUES (1177,'الفصل الثاني: في أهل مكة، وهم ثلاثة أقسام: مستوطن بها، ومقيم بها غير مستوطن، ومسافر','2400','1175','0');
INSERT INTO [t151] VALUES (1178,'فصل (وأما المكي إذا أراد أن يعتمر فإنه يخرج إلى الحل)','2402','1175','0');
INSERT INTO [t151] VALUES (1179,'الإحرام بالعمرة من أقصى الحل أفضل من أدناه','2405','1175','0');
INSERT INTO [t151] VALUES (1180,'فأما الاعتمار من الحديبية فلا فضل فيه على غيره','2408','1175','0');
INSERT INTO [t151] VALUES (1181,'ليس في خروج المكي إلى الجعرانة بخصوصها سنة','2409','1175','0');
INSERT INTO [t151] VALUES (1182,'إن أحرم الحرمي بالعمرة من الحرم، فعليه دم لتركه بعض نسكه','2409','1175','0');
INSERT INTO [t151] VALUES (1183,'مسألة: (ومن لم يكن طريقه على ميقات فميقاته حذو أقربها إليه)','2411','1168','0');
INSERT INTO [t151] VALUES (1184,'مسألة: (ولا يجوز لمن أراد دخول مكة تجاوز الميقات غير محرم إلا لقتال مباح، أو حاجة تتكرر كالحطاب ونحوه. ثم إن أراد النسك أحرم من موضعه، وإن تجاوزه غيره رجع فأحرم من الميقات، فإن أحرم من دونه فعليه دم، سواء رجع إلى الميقات أو لم يرجع)','2413','1168','0');
INSERT INTO [t151] VALUES (1185,'الفصل الأول (من أراد مكة للحج أو العمرة لم يجز له تجاوز الميقات إلا محرما)','2413','1184','0');
INSERT INTO [t151] VALUES (1186,'من أراد دخول مكة لغير النسك مثل تجارة أو زيارة، يجب عليه أن يدخل محرما بعمرة في أشهر الروايتين','2414','1185','0');
INSERT INTO [t151] VALUES (1187,'إن دخل مكة غير محرم لزمه قضاء هذا الإحرام','2419','1185','0');
INSERT INTO [t151] VALUES (1188,'الفصل الثاني (أن من دخل مكة لقتال مباح فإنه لا إحرام عليه)','2422','1184','0');
INSERT INTO [t151] VALUES (1189,'وكذلك من دخلها خائفا لفتنة عرضت ونحو ذلك','2424','1188','0');
INSERT INTO [t151] VALUES (1190,'وكذلك من يتكرر دخوله إلى مكة كل يوم مثل الحطابين والرعاء','2425','1188','0');
INSERT INTO [t151] VALUES (1191,'فصل (يجوز للعبد والصبي والمجنون الدخول بغير إحرام)','2426','1188','0');
INSERT INTO [t151] VALUES (1192,'الفصل الثالث (أن من جاز له مجاوزة الميقات بغير إحرام، إذا أراد النسك بعد ذلك فإنه يحرم من موضعه)','2426','1184','0');
INSERT INTO [t151] VALUES (1193,'فصل (أما الصبي والمجنون والعبد إذا دخلوا مكة بغير إحرام، ثم أرادوا الحج فإنهم يحرمون بالحج من حيث أنشأوه)','2426','1192','0');
INSERT INTO [t151] VALUES (1194,'إذا جاوز الكافر الميقات، ثم أسلم وأراد الحج، هل يرجع إلى الميقات للإحرام؟ روايتان','2427','1192','0');
INSERT INTO [t151] VALUES (1195,'الفصل الرابع (إن جاوز الميقات من أراد النسك أو التجارة ونحوها، لزمهم أن يرجعوا إلى الميقات فيحرموا منه)','2429','1184','0');
INSERT INTO [t151] VALUES (1196,'مسألة: (والأفضل أن لا يحرم قبل الميقات، فإن فعل فهو محرم)','2431','1168','0');
INSERT INTO [t151] VALUES (1197,'من رأى الفضل في الإحرام قبل الميقات يخاف عليه الفتنة','2433','1196','0');
INSERT INTO [t151] VALUES (1198,'تفسير الحديث: «أن تحرم من دويرة أهلك»','2438','1196','0');
INSERT INTO [t151] VALUES (1199,'إنكار الصحابة على من أحرم قبل الميقات','2441','1196','0');
INSERT INTO [t151] VALUES (1200,'مسألة: (وأشهر الحج: شوال وذو القعدة وعشر من ذي الحجة)','2443','1168','0');
INSERT INTO [t151] VALUES (1201,'يوم النحر من عشر ذي الحجة، لأنه اسم لمجموع الليالي وأيامها','2445','1200','0');
INSERT INTO [t151] VALUES (1202,'توجيه ما روي أن أشهر الحج: شوال وذو القعدة وذو الحجة','2447','1200','0');
INSERT INTO [t151] VALUES (1203,'فصل (الإحرام بالحج قبل أشهره مكروه)','2449','1200','0');
INSERT INTO [t151] VALUES (1204,'فإن خالف وأحرم بالحج قبل أشهره فإنه ينعقد الإحرام بالحج في أشهر الروايتين','2452','1200','0');
INSERT INTO [t151] VALUES (1205,'خطأ الاستدلال بقوله تعالى: {يسألونك عن الأهلة قل هي مواقيت للناس والحج} على أن جميع الأهلة ميقات للحج','2458','1200','0');
INSERT INTO [t151] VALUES (1206,'فصل (متى أحرم بالحج فعليه أن يحج تلك السنة، وليس له أن يؤخره إلى العام المقبل)','2461','1200','0');
INSERT INTO [t151] VALUES (1207,'فصل (أما العمرة فيحرم بها متى شاء لا تختص بوقت)','2461','1200','0');
INSERT INTO [t151] VALUES (1208,'هل تصح العمرة في أيام التشريق؟','2462','1200','0');
INSERT INTO [t151] VALUES (1209,'باب الإحرام','2463','1073','0');
INSERT INTO [t151] VALUES (1210,'مسألة: (من أراد الإحرام استحب له أن يغتسل، ويتنظف، ويتطيب، ويتجرد عن المخيط في إزار ورداء أبيضين نظيفين)','2463','1209','0');
INSERT INTO [t151] VALUES (1211,'وليس هذا الغسل واجبا، نص عليه','2466','1210','0');
INSERT INTO [t151] VALUES (1212,'فصل (المراد بالتنظف أن يجز شاربه، ويقلم أظفاره، وينتف إبطه ونحو ذلك)','2466','1210','0');
INSERT INTO [t151] VALUES (1213,'فصل (التطيب مستحب غير مؤكد بحيث لا يكره تركه)','2468','1210','0');
INSERT INTO [t151] VALUES (1214,'يستحب أن يتطيب في بدنه دون ثيابه','2471','1210','0');
INSERT INTO [t151] VALUES (1215,'ويستحب للمرأة أن تتطيب كالرجل، وأن تختضب','2472','1210','0');
INSERT INTO [t151] VALUES (1216,'فصل (في التجرد عن المخيط ولباس إزار ورداء نظيفين أبيضين)','2472','1210','0');
INSERT INTO [t151] VALUES (1217,'مسألة: (ثم يصلي ركعتين ويحرم عقيبهما؛ وهو أن ينوي الإحرام، ويستحب أن ينطق به ويشترط)','2473','1209','0');
INSERT INTO [t151] VALUES (1218,'الفصل الأول: أنه يستحب أن يكون الإحرام بعد صلاة','2474','1217','0');
INSERT INTO [t151] VALUES (1219,'الفصل الثاني: في الوقت الذي يستحب فيه الإحرام','2475','1217','0');
INSERT INTO [t151] VALUES (1220,'إذا أحرم دبر الصلاة ففي أول أوقات التلبية ثلاثة أوجه','2478','1219','0');
INSERT INTO [t151] VALUES (1221,'ما روي من الاختلاف في وقت إحرام النبي صلى الله عليه وسلم وإهلاله','2479','1219','0');
INSERT INTO [t151] VALUES (1222,'التلبية والإهلال والإحرام وفرض الحج بمعنى واحد','2485','1219','0');
INSERT INTO [t151] VALUES (1223,'المتمع يهل إذا أخذ في الخروج من المسجد بعد طوافه بالبيت يوم التروية','2486','1219','0');
INSERT INTO [t151] VALUES (1224,'الفصل الثالث: أن الإحرام ينعقد بمجرد النية عند أصحابنا','2487','1217','0');
INSERT INTO [t151] VALUES (1225,'فصل (وقد استحب أصحابنا أن ينطق بما أحرم به، وأن يشترط)','2488','1224','0');
INSERT INTO [t151] VALUES (1226,'مسألة: (وهو مخير بين التمتع والإفراد والقران، وأفضلها التمتع، وهو أن يحرم بالعمرة في أشهر الحج، ويفرغ منها، ثم يشرع في الحج في عامه، ثم الإفراد، وهو أن يحرم بالحج مفردا، ثم القران)','2489','1209','0');
INSERT INTO [t151] VALUES (1227,'الفصل الأول: أن من أراد النسك فهو مخير بين الأنساك الثلاثة، فإذا أراد أن يجمع بين النسكين، فالأفضل لمن لم يسق الهدي: التمتع، ثم الإفراد، ثم القران','2489','1226','0');
INSERT INTO [t151] VALUES (1228,'وجوه تفضيل المتعة على الحجة المفردة، والقران','2492','1227','0');
INSERT INTO [t151] VALUES (1229,'سرد للأحاديث المروية في أمر النبي صلى الله عليه وسلم المسلمين بالمتعة في حجة الوداع','2494','1227','0');
INSERT INTO [t151] VALUES (1230,'بيان أن متعة النبي صلى الله عليه وسلم كانت متعة قران، وذكر الروايات المفسرة في ذلك','2529','1227','0');
INSERT INTO [t151] VALUES (1231,'إيراد بأن فسخ الحج إلى العمرة واستحباب التمتع كان خاصا لأصحاب النبي صلى الله عليه وسلم','2532','1227','0');
INSERT INTO [t151] VALUES (1232,'الجواب عن الإيراد من أوجه','2540','1227','0');
INSERT INTO [t151] VALUES (1233,'لا يصح القول: إن الفسخ كان للوفد خاصة، وذلك لوجوه','2544','1227','0');
INSERT INTO [t151] VALUES (1234,'إفتاء أحمد بالتمتع، وإنكاره على من اعترض عليه بأن فيه تقوية لقول الروافض','2562','1227','0');
INSERT INTO [t151] VALUES (1235,'توجيه نهي عمر وعثمان وغيرهما عن المتعة، مع ثبوت المتعة عنهما قولا وفعلا','2564','1227','0');
INSERT INTO [t151] VALUES (1236,'إن الرجل إذا أنشأ للعمرة سفرا من مصره كان أفضل من عمرة التمتع','2569','1227','0');
INSERT INTO [t151] VALUES (1237,'مخالفة الصحابة لعمر وعثمان ومن بعدهما في منعهم الناس من التمتع','2572','1227','0');
INSERT INTO [t151] VALUES (1238,'أما كون النبي صلى الله عليه وسلم أفرد الحج ولم يعتمر في أشهره، فعنه ثلاثة أجوبة','2574','1227','0');
INSERT INTO [t151] VALUES (1239,'كان ابن عباس يرى وجوب المتعة على من لم يسق الهدي','2583','1227','0');
INSERT INTO [t151] VALUES (1240,'فصل (إذا اعتمر قبل أشهر الحج وأفرد الحج من سنته فهو أفضل من التمتع)','2588','1227','0');
INSERT INTO [t151] VALUES (1241,'فصل (يجوز الإحرام بنسك معين، ويجوز أن يحرم مطلقا من غير أن ينوي عمرة أو حجة، ويجوز أن يحرم بمثل ما أحرم به فلان)','2590','1227','0');
INSERT INTO [t151] VALUES (1242,'المسألة الثانية: أنه يجوز أن يحرم بالعمرة، ثم يدخل عليها الحج، ويصير قارنا','2593','1209','0');
INSERT INTO [t151] VALUES (1243,'إذا وقف بعرفة قبل أن يطوف بالبيت فهو باق على قرانه، كما حصل لعائشة','2596','1242','0');
INSERT INTO [t151] VALUES (1244,'وجه من قال: إن النبي صلى الله عليه وسلم أمر عائشة برفض العمرة وأن تصير مفردة للحج','2598','1242','0');
INSERT INTO [t151] VALUES (1245,'وجه من قال: إنها كانت قارنة، وطوافها يوم النحر بالبيت وبين الصفا والمروة يسعها لحجها وعمرتها','2603','1242','0');
INSERT INTO [t151] VALUES (1246,'المسألة الثالثة: إذا أحرم بالحج لم يجز أن يدخل عليه العمرة، فإن أدخلها عليه لم تنعقد','2605','1209','0');
INSERT INTO [t151] VALUES (1247,'فصل (إذا أحرم بحجتين أو عمرتين فإنه ينعقد بإحداهما، ولا يلزمه قضاء الآخر)','2607','1246','0');
INSERT INTO [t151] VALUES (1248,'فصل (إذا نسي المحرم ما أحرم به، أو أحرم بمثل فلان وتعذر معرفته، يجعلها عمرة، ثم يلبي من مكة)','2608','1246','0');
INSERT INTO [t151] VALUES (1249,'وأما القاضي وأكثر أصحابه فإنهم يخيرونه بين العمرة والحج','2609','1246','0');
INSERT INTO [t151] VALUES (1250,'في وجوب الدم عليه وجهان','2612','1246','0');
INSERT INTO [t151] VALUES (1251,'مسألة: (وإذا استوى على راحلته لبى، فيقول: لبيك اللهم لبيك، لبيك لا شريك لك لبيك، إن الحمد والنعمة لك والملك، لا شريك لك)','2613','1209','0');
INSERT INTO [t151] VALUES (1252,'سبب التلبية ومعناها','2616','1251','0');
INSERT INTO [t151] VALUES (1253,'بيان اشتقاق التلبية، وجماع مادة «لبب»','2619','1251','0');
INSERT INTO [t151] VALUES (1254,'المستحب كسر همزة «إن الحمد»، ويجوز فتحها','2622','1251','0');
INSERT INTO [t151] VALUES (1255,'فصل (الأفضل أن يلبي تلبية رسول الله صلى الله عليه وسلم، فإن زاد شيئا فهو جائز غير مكروه)','2622','1251','0');
INSERT INTO [t151] VALUES (1256,'مسألة: (ويستحب الإكثار منها ورفع الصوت بها لغير النساء)','2627','1209','0');
INSERT INTO [t151] VALUES (1257,'أما المرأة فيستحب لها أن تسمع رفيقتها','2630','1256','0');
INSERT INTO [t151] VALUES (1258,'مسألة: (وهي آكد فيما إذا علا نشزا، أو هبط واديا، أو سمع ملبيا، أو فعل محظورا ناسيا، أو التقت الرفاق، وفي أدبار الصلاة، وبالأسحار، وإقبال الليل والنهار)','2631','1209','0');
INSERT INTO [t151] VALUES (1259,'يستحب أن يبدأ قبل التلبية بذكر الركوب','2633','1258','0');
INSERT INTO [t151] VALUES (1260,'فصل (يكفيه أن يلبي لهذه الأسباب مرة واحدة؛ بحيث يكون دعاؤه عقيب تلك المرة)','2635','1258','0');
INSERT INTO [t151] VALUES (1261,'حقيقة المذهب أن استدامتها وتكرارها على كل حال مستحب من غير تقييد بعدد','2637','1258','0');
INSERT INTO [t151] VALUES (1262,'فصل (لا يستحب أن يتخللها غيرها من الكلام ليأتي بها نسقا، ولا يجوز أن يلبي بغير العربية وهو يقدر على التلبية بالعربية)','2638','1258','0');
INSERT INTO [t151] VALUES (1263,'فصل (تشرع التلبية من حين الإحرام إلى الشروع في الإحلال)','2639','1258','0');
INSERT INTO [t151] VALUES (1264,'يكره إظهار التلبية في الأمصار والحلل','2642','1258','0');
INSERT INTO [t151] VALUES (1265,'فصل (لا يستحب تسمية ما أحرم به في تلبيته)','2645','1258','0');
INSERT INTO [t151] VALUES (1266,'استحب أحمد أن يسمي في تلبيته العمرة والحج أول مرة','2646','1258','0');
INSERT INTO [t151] VALUES (1267,'فصل (لا بأس بتلبية الحلال، ولا يصير محرما بذلك إذا لم ينوي الإحرام)','2648','1258','0');
INSERT INTO [t151] VALUES (1268,'باب محظورات الإحرام','2650','1073','0');
INSERT INTO [t151] VALUES (1269,'مسألة: (وهي تسع: حلق الشعر، وقلم الظفر)','2650','1268','0');
INSERT INTO [t151] VALUES (1270,'مسألة: (ففي ثلاث منها دم، وفي كل واحد مما دونها مد طعام، وهو ربع الصاع)','2652','1268','0');
INSERT INTO [t151] VALUES (1271,'عدد ما يوجب الدم من الشعرات أوالأظفار فيه ثلاث روايات','2654','1270','0');
INSERT INTO [t151] VALUES (1272,'إذا نتف شعرة أو اثنتين أطعم مدا عن كل شعرة، أو قبضة من طعام عنهما؛ روايتان','2656','1270','0');
INSERT INTO [t151] VALUES (1273,'مسألة: (وإن خرج في عينه شعر فقلعه، أو نزل شعره فغطى عينيه، أو انكسر ظفره فقصه= فلا شيء فيه)','2657','1268','0');
INSERT INTO [t151] VALUES (1274,'فصل (لا بأس أن يحلق المحرم رأس الحلال ويقلم أظفاره، ولا فدية عليه)','2658','1273','0');
INSERT INTO [t151] VALUES (1275,'مسألة: (الثالث: لبس المخيط إلا أن لا يجد إزارا فيلبس سراويل، أو لا يجد نعلين فيلبس خفين، ولا فدية عليه)','2658','1268','0');
INSERT INTO [t151] VALUES (1276,'الفصل الأول (أن المحرم يحرم عليه أن يلبس المخيط المصنوع على قدر العضو)','2659','1275','0');
INSERT INTO [t151] VALUES (1277,'الفصل الثاني (إذا لم يجد إزارا فإنه يلبس السراويل دون فتقها، وإذا لم يجد نعلين فإنه يلبس الخفين دون قطعها)','2664','1275','0');
INSERT INTO [t151] VALUES (1278,'توجيه حديث ابن عمر في الأمر بقطع الخفين','2671','1277','0');
INSERT INTO [t151] VALUES (1279,'يتعين أن يكون النبي صلى الله عليه وسلم أمر أولا بقطعها، ثم رخص في لبسها من غير قطع، وذلك لثمانية وجوه','2674','1277','0');
INSERT INTO [t151] VALUES (1280,'فصل (معنى كونه لا يجده: أن لا يباع، أو يجده يباع وليس معه ثمن فاضل عن حوائجه الأصلية)','2685','1277','0');
INSERT INTO [t151] VALUES (1281,'حكم من وجد النعلين ولم يمكنه لبسهما','2687','1277','0');
INSERT INTO [t151] VALUES (1282,'فصل (حكم لبس المداس والحذاء ونحو ذلك مما يصنع على مقدار القدم)','2689','1277','0');
INSERT INTO [t151] VALUES (1283,'فصل (كل ما عمل على هيئة المخيط فله حكمه)','2695','1277','0');
INSERT INTO [t151] VALUES (1284,'فصل (يجوز أن يرتدي بالقباء والدواج والفرجية ونحو ذلك، فلا يدخل منكبيه فيه)','2696','1277','0');
INSERT INTO [t151] VALUES (1285,'مسألة: (الرابع: تغطية الرأس، والأذنان منه)','2697','1268','0');
INSERT INTO [t151] VALUES (1286,'أما الوجه ففيه ثلاث روايات، الثالثة: يجوز أن يخمر أسفل من الأنف','2698','1285','0');
INSERT INTO [t151] VALUES (1287,'فصل (يجوز للمحرم أن يحمل فوق رأسه شيئا مثل المكتل أو المتاع)','2702','1285','0');
INSERT INTO [t151] VALUES (1288,'فصل (أما إذا غطى رأسه بشيء منفصل عنه فهو أقسام:)','2703','1285','0');
INSERT INTO [t151] VALUES (1289,'أحدها: أن يستظل بسقف أو بخيمة أو بشجرة، ونحو ذلك فهذا جائز','2703','1288','0');
INSERT INTO [t151] VALUES (1290,'الثاني: المحمل والعمارية والهودج ونحو ذلك مما يصنع على المراكب لأجل الاستظلال، فالمشهور عن أحمد الكراهة','2706','1288','0');
INSERT INTO [t151] VALUES (1291,'أما إن تظلل زمنا يسيرا من حر أو مطر، برفع يده أو ثوبه، فجائز بلا كراهة','2715','1285','0');
INSERT INTO [t151] VALUES (1292,'من أحرم وعليه قميصه خلعه ولم يشقه، مع أن فيه تظليلا لرأسه وتخميرا له','2717','1285','0');
INSERT INTO [t151] VALUES (1293,'وحيث كره له التظليل فهل تجب الفدية؟ على روايتين منصوصتين','2723','1285','0');
INSERT INTO [t151] VALUES (1294,'مسألة: (الخامس: الطيب في بدنه وثيابه)','2724','1268','0');
INSERT INTO [t151] VALUES (1295,'إن تطيب قبل الإحرام، ثم استدام، لم يحرم ذلك ولم يكره','2725','1294','0');
INSERT INTO [t151] VALUES (1296,'فصل (يحرم عليه أن يتطيب في بدنه وثيابه، سواء مس الطيب بدنه أو لم يمسه)','2728','1294','0');
INSERT INTO [t151] VALUES (1297,'افتراش الفرش المطيب بمنزلة لبسه','2729','1294','0');
INSERT INTO [t151] VALUES (1298,'حكم المصبوغ بماء الفواكه التي يشم ريحها','2732','1294','0');
INSERT INTO [t151] VALUES (1299,'فصل (إذا مس بيده من الطيب ما يعلق لرطوبته أو نحوه ذلك، فهو حرام وعليه الفدية)','2732','1294','0');
INSERT INTO [t151] VALUES (1300,'فصل (لا يجوز اشتمام الطيب ولو لم يتصل ببدنه أو بثوبه)','2733','1294','0');
INSERT INTO [t151] VALUES (1301,'وليس له أن يستصحب ما يجد ريحه لتجارة ولا غيرها، وإن لم يقصد شمه','2735','1294','0');
INSERT INTO [t151] VALUES (1302,'فصل (النباتات التي لها رائحة طيبة ولا يتطيب بها، على قسمين)','2736','1294','0');
INSERT INTO [t151] VALUES (1303,'أحدهما: ما يقصد طعمه دون ريحه، كالأترج والتفاح، فهذا لا بأس بشمه','2736','1294','0');
INSERT INTO [t151] VALUES (1304,'والثاني: ما استنبت لريحه، وهو الريحان، ففيه روايتان','2736','1294','0');
INSERT INTO [t151] VALUES (1305,'فصل (فأما الثياب المصبوغة بغير طيب؛ فلا يكره منها في الإحرام إلا ما يكره في الحل)','2740','1294','0');
INSERT INTO [t151] VALUES (1306,'للمرأة أن تلبس ما شاءت من ألوان الثياب من معصفر وغيره','2741','1294','0');
INSERT INTO [t151] VALUES (1307,'ذكر الآثار في عدم كراهة المعصفر للرجال، وبيان أنها تحمل على غير المشبع بحيث يكون رقيق الحمرة','2744','1294','0');
INSERT INTO [t151] VALUES (1308,'لا بأس بالحلي والحرير ونحو ذلك للمحرمة','2748','1294','0');
INSERT INTO [t151] VALUES (1309,'فصل (حكم الكحل والخضاب ونحوهما للمحرم)','2748','1294','0');
INSERT INTO [t151] VALUES (1310,'أما الكحل إذا كان فيه طيب فإنه لا يجوز إلا لضرورة','2750','1294','0');
INSERT INTO [t151] VALUES (1311,'أما الخضاب بالحناء فهو زينة، ولا يكره إذا كان لحاجة','2753','1294','0');
INSERT INTO [t151] VALUES (1312,'أما النظر في المرآة، فيجوز ولكن لا يصلح شيئا','2755','1294','0');
INSERT INTO [t151] VALUES (1313,'فصل (يجوز للمحرم أن يغسل رأسه وبدنه وثيابه، وأن يبدل ثياب الإحرام، وأن يدخل الحمام)','2756','1294','0');
INSERT INTO [t151] VALUES (1314,'قول الإمام أحمد: حديث ابن عباس «أن رجلا وقصت به ناقته وهو محرم» فيه خمس سنن','2763','1294','0');
INSERT INTO [t151] VALUES (1315,'فصل (لا يتفلى المحرم ولا يقتل القمل، ولا يدهن رأسه، ويتداوى بما يأكل)','2766','1294','0');
INSERT INTO [t151] VALUES (1316,'إن احتاج إلى الادهان، مثل أن يكون برجله شقوق، جاز بغير كراهة ولا فدية','2768','1294','0');
INSERT INTO [t151] VALUES (1317,'مسألة: (السادس: قتل صيد البر، وهو ما كان وحشيا مباحا، فأما صيد البحر والأهلي وما حرم أكله فلا شيء فيه، إلا ما كان متولدا من مأكول وغيره)','2773','1268','0');
INSERT INTO [t151] VALUES (1318,'والصيد الذي يضمن بالجزاء يشترط فيه ثلاث صفات','2775','1317','0');
INSERT INTO [t151] VALUES (1319,'هل في قتل ما لا يؤكل لحمه ولا يؤذي جزاء؟ روايتان','2776','1317','0');
INSERT INTO [t151] VALUES (1320,'القول بوجوب الجزاء فيما تعارض فيه دليل الحظر والإباحة','2779','1317','0');
INSERT INTO [t151] VALUES (1321,'ما آذى الناس أو آذى أموالهم فإن قتله مباح','2782','1317','0');
INSERT INTO [t151] VALUES (1322,'ذكر الأحاديث في قتل الفواسق الخمسة في الإحرام','2784','1317','0');
INSERT INTO [t151] VALUES (1323,'هل يقتل السبع إذا لم يعد عليه بالفعل؟ روايتان','2787','1317','0');
INSERT INTO [t151] VALUES (1324,'أصح الروايتين: أنه يقتل ما يعدو على المحرم ويريد عقره، لثمانية وجوه','2789','1317','0');
INSERT INTO [t151] VALUES (1325,'تقسيم القاضي وابن عقيل للحيوانات التي لا تؤكل على ثلاثة أقسام','2793','1317','0');
INSERT INTO [t151] VALUES (1326,'فصل (ما حرم قتله، فإنه يحرم قصد قتله بمباشرة أو تسبب، ويحرم أذاه بأنواع الأذى، ويحرم عليه تملكه)','2796','1317','0');
INSERT INTO [t151] VALUES (1327,'فصل (إذا ذبح المحرم صيدا فهو حرام، وهو بمنزلة الميتة)','2800','1317','0');
INSERT INTO [t151] VALUES (1328,'قول الله تعالى: {لا تقتلوا الصيد وأنتم حرم} يقتضي كونه حراما لوجوه','2800','1317','0');
INSERT INTO [t151] VALUES (1329,'صيد المدينة ايضا حرام أكله وحرام صيده','2805','1317','0');
INSERT INTO [t151] VALUES (1330,'إن أعان المحرم حلالا بدلالة أو نحوها، فهو ذكي مباح للحلال','2805','1317','0');
INSERT INTO [t151] VALUES (1331,'إذا وجد المضطر ميتة وصيدا فإنه يأكل الميتة ويدع الصيد','2807','1317','0');
INSERT INTO [t151] VALUES (1332,'فصل (ما صاده الحلال بغير معونة من المحرم وذكاه، فإنه مباح للمحرم إذا لم يصده لأجله)','2809','1317','0');
INSERT INTO [t151] VALUES (1333,'المراد بالصيد في قوله تعالى: {وحرم عليكم صيد البر ما دمتم حرما} هو نفس الحيوان المصيد، لستة وجوه','2820','1317','0');
INSERT INTO [t151] VALUES (1334,'فسرت السنة أن المراد فعل يكون سببا إلى هلاك الصيد، يدل على ذلك خمسة أشياء','2822','1317','0');
INSERT INTO [t151] VALUES (1335,'إذا أكل الصيد من صيد لأجله من المحرمين وجب عليه الجزاء','2826','1317','0');
INSERT INTO [t151] VALUES (1336,'فصل (يحرم على المحرم الإعانة على قتل الصيد بدلالة أو إشارة أو إعارة آلة لصيده أو لذبحه)','2827','1317','0');
INSERT INTO [t151] VALUES (1337,'مسألة: (السابع: عقد النكاح لا يصح منه، ولا فدية فيه)','2830','1268','0');
INSERT INTO [t151] VALUES (1338,'ذكر ما روي عن ابن عباس أن النبي صلى الله عليه وسلم تزوج ميمونة وهو محرم، والجواب عنه','2834','1337','0');
INSERT INTO [t151] VALUES (1339,'لو تيقنا أنه تزوجها محرما لكان حديث النهي هو الذي يجب أن يعمل به لأوجه','2847','1337','0');
INSERT INTO [t151] VALUES (1340,'فصل (لا كفارة في النكاح؛ لأنه يقع باطلا فلم يوجب كفارة)','2852','1337','0');
INSERT INTO [t151] VALUES (1341,'فصل (أما تزويجه للحلال، فلا يصح أيضا في أشهر الروايتين)','2852','1337','0');
INSERT INTO [t151] VALUES (1342,'أما بالولاية العامة وهي ولاية السلطان، ففيه وجهان','2853','1337','0');
INSERT INTO [t151] VALUES (1343,'فصل (وأما ارتجاع زوجته المطلقة، ففيه روايتان)','2855','1337','0');
INSERT INTO [t151] VALUES (1344,'يجوز أن يفيء المولي باللسان وهو محرم، ويجوز أن يصالح الناشز، ويجوز أن يكفر المظاهر وهو محرم','2858','1337','0');
INSERT INTO [t151] VALUES (1345,'فصل (قياس المذهب: لا يحل للمحرم أن يخطب ولا يشهد)','2858','1337','0');
INSERT INTO [t151] VALUES (1346,'مسألة: (الثامن: المباشرة لشهوة فيما دون الفرج، فإن أنزل بها ففيها بدنة، وإلا ففيها شاة)','2860','1268','0');
INSERT INTO [t151] VALUES (1347,'المسألة الأولى: أن المحرم لا يجوز له أن يباشر لشهوة، ولا النظر لشهوة','2860','1346','0');
INSERT INTO [t151] VALUES (1348,'من باشر لشهوة ولم ينزل لم يفسد حجه، قولا واحدا','2861','1347','0');
INSERT INTO [t151] VALUES (1349,'إن كانت المباشرة وطأ دون الفرج ففيها بدنة، وإن كانت قبلة أو غمزا ففيها شاة','2862','1347','0');
INSERT INTO [t151] VALUES (1350,'المسألة الثانية: إذا أنزل المني بالمباشرة، فهل يفسد نسكه؟ ثلاث روايات','2863','1346','0');
INSERT INTO [t151] VALUES (1351,'وإن نظر لشهوة فأمنى فعليه دم','2865','1350','0');
INSERT INTO [t151] VALUES (1352,'مسألة: (التاسع: الوطء في الفرج، فإن كان قبل التحلل الأول أفسد الحج، ووجب المضي في فاسده والحج من قابل، وعليه بدنة، وإن كان بعد التحلل الأول ففيه شاة، ويحرم من التنعيم ليطوف محرما)','2869','1268','0');
INSERT INTO [t151] VALUES (1353,'الفصل الأول: أن الجماع حرام في الإحرام وهو من الكبائر','2869','1352','0');
INSERT INTO [t151] VALUES (1354,'الفصل الثاني: أن المحرم إذا وطئ في الإحرام فسد حجه والإحرام باق عليه، وعليه أن يمضي فيه فيتمه، ثم عليه قضاء الحج من قابل، وعليه أن يهدي بدنة','2869','1352','0');
INSERT INTO [t151] VALUES (1355,'الفصل الثالث: أنه لا فرق بين الوطء قبل الوقوف بعرفة أو بعده إذا وقع قبل التحلل الأول','2874','1352','0');
INSERT INTO [t151] VALUES (1356,'الفصل الرابع: إذا وطئ بعد التحلل الأول لم يبطل حجه','2877','1352','0');
INSERT INTO [t151] VALUES (1357,'فصل (وهل عليه بدنة أو شاة؟ على روايتين)','2880','1356','0');
INSERT INTO [t151] VALUES (1358,'فصل (الواجب عليه إذا وطئ بعد التحلل الأول: أن يأتي مسجد عائشة فيحرم بعمرة)','2882','1356','0');
INSERT INTO [t151] VALUES (1359,'مسألة: (وإن وطئ في العمرة أفسدها، وعليه شاة)','2888','1268','0');
INSERT INTO [t151] VALUES (1360,'وإن وطئها بعد السعي وقبل الحلق، لم تبطل عمرته بحال','2890','1359','0');
INSERT INTO [t151] VALUES (1361,'مسألة: (ولا يفسد النسك بغيره)','2893','1268','0');
INSERT INTO [t151] VALUES (1362,'فصل (كل وطء في الفرج فإنه يفسد، سواء كان قبلا أو دبرا، من آدمي أو بهيمة)','2894','1361','0');
INSERT INTO [t151] VALUES (1363,'فصل (ويفسد به الإحرام سواء فعله عامدا أو ساهيا، عالما أو جاهلا)','2895','1361','0');
INSERT INTO [t151] VALUES (1364,'محظورات الإحرام أغلظ من محظورات الصيام لوجهين','2896','1361','0');
INSERT INTO [t151] VALUES (1365,'فصل (يجب أن يقضي مثل الذي أفسده حجا كان عمرة، وعليه أن يحرم من أبعد الموضعين: المكان الذي أحرم منه أولا، وميقات بلده)','2902','1361','0');
INSERT INTO [t151] VALUES (1366,'فصل (ليس عليه إلا قضاء واحد)','2903','1361','0');
INSERT INTO [t151] VALUES (1367,'فصل (ينحر هدي الفساد في عام القضاء)','2904','1361','0');
INSERT INTO [t151] VALUES (1368,'إن كان قد وجب عليه دم بترك واجب، فهل يسقط عنه بفعل القضاء؟ روايتان','2906','1361','0');
INSERT INTO [t151] VALUES (1369,'إن كان متمتعا أو قارنا قد وجب عليه دم بسبب ذلك، ثم وطئ، فهل يسقط عنه دم المتعة والقران؟ روايتان','2908','1361','0');
INSERT INTO [t151] VALUES (1370,'فصل (الدم الواجب بالوطء ونحوه أربعة أقسام)','2909','1361','0');
INSERT INTO [t151] VALUES (1371,'فصل (على الزوجين أن يتفرقا في نسك القضاء في النزول والمحمل والفسطاط وما أشبه ذلك)','2909','1361','0');
INSERT INTO [t151] VALUES (1372,'هل يفترقان من حين الإحرام أو إذا بلغا مكان الإصابة؟ روايتان','2913','1361','0');
INSERT INTO [t151] VALUES (1373,'مسألة: (والمرأة كالرجل، إلا أن إحرامها في وجهها، ولها لبس المخيط)','2914','1268','0');
INSERT INTO [t151] VALUES (1374,'الفصل الأول: أن المرأة في تحريم الطيب وقتل الصيد وتقليم الأظافر والحلق والمباشرة كالرجل','2914','1373','0');
INSERT INTO [t151] VALUES (1375,'الفصل الثاني: أنها لا يحرم عليها لبس المخيط ولا تخمير الرأس','2915','1373','0');
INSERT INTO [t151] VALUES (1376,'الفصل الثالث: أن إحرامها في وجهها، فلا يجوز لها أن تلبس النقاب والبرقع','2915','1373','0');
INSERT INTO [t151] VALUES (1377,'فصل (لا يجوز للمحرمة لبس القفازين ونحوهما)','2918','1268','0');
INSERT INTO [t151] VALUES (1378,'باب الفدية','2921','1073','0');
INSERT INTO [t151] VALUES (1379,'مسألة: (وهي على ضربين؛ أحدهما: على التخيير، وهي فدية الأذى واللبس والطيب، فله الخيار بين صيام ثلاثة أيام، أو إطعام ثلاثة آصع من تمر لستة مساكين، أو ذبح شاة)','2921','1378','0');
INSERT INTO [t151] VALUES (1380,'فصل (إن فعل المحظور لعذر ففديته على التخيير، وإن فعله لغير عذر ففيه روايتان)','2922','1379','0');
INSERT INTO [t151] VALUES (1381,'إنما ذكر الله التخيير في المريض ومن به أذى، وذلك يقتضي أن غير المعذور بخلاف ذلك لوجوه','2923','1379','0');
INSERT INTO [t151] VALUES (1382,'فصل (إذا أراد الحلق أو اللبس أو الطيب لعذر جاز له إخراج الفدية بعد وجود السبب المبيح وقبل فعل المحظور)','2926','1379','0');
INSERT INTO [t151] VALUES (1383,'فصل (يجوز إخراج الفدية حيث وجبت من حل أو حرم، وكذا حيث جازت)','2926','1379','0');
INSERT INTO [t151] VALUES (1384,'مسألة: (وكذلك الحكم في كل دم وجب لترك واجب)','2927','1378','0');
INSERT INTO [t151] VALUES (1385,'مسألة: (وجزاء الصيد مثل ما قتل من النعم، إلا الطائر فإن فيه قيمته، إلا الحمامة فيها شاة، والنعامة فيها بدنة)','2927','1378','0');
INSERT INTO [t151] VALUES (1386,'الفصل الأول: أن ما وجب ضمانه من الصيد إما بالحرم أو بالإحرام فإنه يضمن بمثله من بهيمة الأنعام، وهو ما شابهه في الخلقة والصفة تقريبا','2928','1385','0');
INSERT INTO [t151] VALUES (1387,'دلالة القرآن على أن المراد بالمثل: ما ماثل الصيد من جهة الخلقة والصورة، وذلك من خمسة أوجه','2928','1386','0');
INSERT INTO [t151] VALUES (1388,'دلالة السنة على ذلك','2930','1386','0');
INSERT INTO [t151] VALUES (1389,'إجماع الصحابة على ذلك','2931','1386','0');
INSERT INTO [t151] VALUES (1390,'الفصل الثاني: أن ما تقدم فيه حكم حاكمين من أصحاب رسول الله صلى الله عليه وسلم فهو على ما حكما، لا يحتاج إلى استئناف حكم ثان','2932','1385','0');
INSERT INTO [t151] VALUES (1391,'يجوز أن يكون أحد الحكمين هو القاتل للصيد','2934','1390','0');
INSERT INTO [t151] VALUES (1392,'الفصل الثالث فيما قد مضى فيه الحكم واستقر أمره','2936','1385','0');
INSERT INTO [t151] VALUES (1393,'الضبع، والظبي','2938','1392','0');
INSERT INTO [t151] VALUES (1394,'الثعلب، والأرنب','2941','1392','0');
INSERT INTO [t151] VALUES (1395,'اليربوع','2942','1392','0');
INSERT INTO [t151] VALUES (1396,'الضب، والوبر، والسنور','2944','1392','0');
INSERT INTO [t151] VALUES (1397,'فصل (وأما الطير فثلاثة أنواع؛ أحدها: الحمام وما أشبهه، وفيه شاة)','2945','1385','0');
INSERT INTO [t151] VALUES (1398,'الثاني: ما كان أصغر من الحمام، ففيه القيمة','2948','1385','0');
INSERT INTO [t151] VALUES (1399,'الثالث: ما كان أكبر من الحمام، ففيه شاة على أصح الوجهين','2949','1385','0');
INSERT INTO [t151] VALUES (1400,'فصل (يضمن الصيد بمثله، سواء كان المثل مما يجزئ في الهدايا والضحايا المطلقة أو لا، فيجب في الصغير صغير، وفي الكبير كبير، وهكذا)','2950','1385','0');
INSERT INTO [t151] VALUES (1401,'فصل (في كيفية الجزاء إذا أتلف بعض الصيد، مثل أن جرحه)','2953','1385','0');
INSERT INTO [t151] VALUES (1402,'فصل (يضمن بيض الصيد بقيمته)','2954','1385','0');
INSERT INTO [t151] VALUES (1403,'وفي جنين الصيد القيمة أيضا؛ وهو أرش ما نقصته الجناية','2958','1385','0');
INSERT INTO [t151] VALUES (1404,'وهل يباح البيض بعد كسره؟ وجهان','2959','1385','0');
INSERT INTO [t151] VALUES (1405,'حكم بيض النمل والقمل','2961','1385','0');
INSERT INTO [t151] VALUES (1406,'فصل (لا يجوز أخذ لبن الصيد، فإن أخذه ضمنه بقيمته)','2961','1385','0');
INSERT INTO [t151] VALUES (1407,'مسألة: (ويتخير بين إخراج المثل أو تقويمه بطعام، فيطعم كل مسكين مدا، أو يصوم عن كل مد يوما)','2962','1378','0');
INSERT INTO [t151] VALUES (1408,'رواية أخرى: أن بدل الصيد على الترتيب، إذا كان موسرا ووجد الهدي لم يجزئه غيره، وإن كان موسرا ولم يجده اشترى طعاما، فإن كان معسرا صام','2963','1407','0');
INSERT INTO [t151] VALUES (1409,'فصل (إذا كفر بالطعام وكان للصيد مثل، فهل يقوم المثل أو الصيد؟ روايتان)','2969','1407','0');
INSERT INTO [t151] VALUES (1410,'فصل (إذا قوم الصيد أو بدله فإنه يشتري بالقيمة طعاما مما يجزئ إخراجه في الكفارات)','2971','1407','0');
INSERT INTO [t151] VALUES (1411,'فصل (عدل الصدقة من الصيام في كتاب الله أن يصام عن طعام كل مسكين يوم)','2971','1407','0');
INSERT INTO [t151] VALUES (1412,'فصل (ما لا مثل له إذا أراد أن يخرج قيمته لم يجزئه)','2974','1407','0');
INSERT INTO [t151] VALUES (1413,'فصل (له أن يخرج الجزاء بعد انعقاد سببه قبل الوجوب)','2974','1407','0');
INSERT INTO [t151] VALUES (1414,'مسألة: (الضرب الثاني: على الترتيب، وهو هدي التمتع، يلزمه شاة، فإن لم يجد فصيام ثلاثة أيام في الحج وسبعة إذا رجع)','2975','1378','0');
INSERT INTO [t151] VALUES (1415,'الفصل الأول في الهدي','2975','1414','0');
INSERT INTO [t151] VALUES (1416,'هل يجب الهدي والصوم عنه بعد الوقوف بعرفة، أو إذا أحرم؟ روايتان','2977','1415','0');
INSERT INTO [t151] VALUES (1417,'ليس المراد من قولنا: «يجب الهدي بالإحرام» أن ينحر حينئذ','2980','1415','0');
INSERT INTO [t151] VALUES (1418,'الفصل الثاني: أنه إذا لم يجد الهدي فعليه صيام ثلاثة أيام في الحج وسبعة إذا رجع','2983','1414','0');
INSERT INTO [t151] VALUES (1419,'يجوز الصوم من حين يحرم بعمرة التمتع بلا تردد','2985','1418','0');
INSERT INTO [t151] VALUES (1420,'فصل (صيام السبعة يكون إذا رجع إلى أهله، فإن صامها في طريقه أو في مكة بعد أيام منى وبعد التحلل الثاني جاز)','2990','1418','0');
INSERT INTO [t151] VALUES (1421,'صوم السبعة قد وجب في ذمته بمكة، فإذا مات ولم يصم يطعم عنه بمكة موضع وجب عليه','2995','1418','0');
INSERT INTO [t151] VALUES (1422,'فصل (يجوز أن يصوم كل واحد من الثلاثة والسبعة متفرقا)','2995','1418','0');
INSERT INTO [t151] VALUES (1423,'فصل (إنما يجوز أن يصوم من حين الإحرام بالعمرة، إذا لم يجد هديا حينئذ، ويغلب على ظنه أنه لا يجده إلى يوم النحر)','2996','1418','0');
INSERT INTO [t151] VALUES (1424,'إذا صام المتمع أياما، ثم أيسر، يجزئه الصيام ويمضي فيه','2996','1418','0');
INSERT INTO [t151] VALUES (1425,'فصل (إذا وجب عليه الهدي فلم يهد حتى خرجت أيام الذبح، ففيه ثلاث روايات منصوصات)','2999','1418','0');
INSERT INTO [t151] VALUES (1426,'إحداهن: عليه هديان: هدي متعته، وهدي آخر لتفريطه','2999','1425','0');
INSERT INTO [t151] VALUES (1427,'والرواية الثانية: ليس عليه إلا هدي التمتع فقط','3001','1425','0');
INSERT INTO [t151] VALUES (1428,'والرواية الثالثة: إن أخره لعذر لم يلزمه إلا هدي واحد، وإن أخره عمدا فعليه هديان','3002','1425','0');
INSERT INTO [t151] VALUES (1429,'أما إذا فوت صوم الثلاثة بعد وجوبه، فيتعين عليه الهدي ولا يجزئه الصوم بحال','3004','1418','0');
INSERT INTO [t151] VALUES (1430,'وفي رواية: أنه يقضيها. وهل عليه دم مع القضاء؟ ثلاث روايات','3007','1418','0');
INSERT INTO [t151] VALUES (1431,'إذا مات ولم يصم السبعة أيام يطعم عنه بمكة موضع وجب عليه','3009','1418','0');
INSERT INTO [t151] VALUES (1432,'الفصل الثالث في الشروط العشرة التي بها يكون متمتعا','3010','1414','0');
INSERT INTO [t151] VALUES (1433,'أحدها: أن يعتمر في أشهر الحج','3010','1432','0');
INSERT INTO [t151] VALUES (1434,'الشرط الثاني: أن يحج من عامه ذلك','3012','1432','0');
INSERT INTO [t151] VALUES (1435,'الشرط الثالث: أن لا يسافر بعد العمرة','3012','1432','0');
INSERT INTO [t151] VALUES (1436,'الشرط الرابع: أن لا يكون من حاضري المسجد الحرام','3017','1432','0');
INSERT INTO [t151] VALUES (1437,'فصل (هل لحاضري المسجد الحرام أن يتمتعوا؟)','3018','1414','0');
INSERT INTO [t151] VALUES (1438,'مسألة: (وفدية الجماع بدنة، فإن لم يجد فصيام كصيام التمتع، وكذلك الحكم في البدنة الواجبة بالمباشرة ودم الفوات)','3018','1378','0');
INSERT INTO [t151] VALUES (1439,'مسألة: (والمحصر يلزمه دم، فإن لم يجد فصيام عشرة أيام)','3018','1378','0');
INSERT INTO [t151] VALUES (1440,'قوله تعالى: {فإن أحصرتم فما استيسر من الهدي} يدل على وجوب الهدي من وجوه','3019','1439','0');
INSERT INTO [t151] VALUES (1441,'فصل (ينحر الهدي في موضع حصره حيث كان من حل أو حرم)','3021','1439','0');
INSERT INTO [t151] VALUES (1442,'وأما وقت الذبح والإحلال ففيه روايتان: إحداهما: أنه يذبحه وقت الإحصار، والثانية: لا يذبح ويحل إلى يوم النحر','3023','1439','0');
INSERT INTO [t151] VALUES (1443,'فصل (فإن لم يجد هديا، صام عشرة أيام قبل أن يحل)','3025','1439','0');
INSERT INTO [t151] VALUES (1444,'فصل (إذا أحصر عن البيت بعد الوقوف بعرفة فهو محصر عند أصحابنا)','3027','1439','0');
INSERT INTO [t151] VALUES (1445,'فصل (المحصر في العمرة كالمحصر في الحج سواء)','3027','1439','0');
INSERT INTO [t151] VALUES (1446,'فصل (إذا كان للمحصر طريق لزمه قصدها، وإن خلي عن طريقه قبل التحلل لزمه السعي وإن خشي الفوات)','3028','1439','0');
INSERT INTO [t151] VALUES (1447,'فصل (لا يجب قضاء النسك الذي أحصر عنه في إحدى الروايتين، إلا أن يكون رجلا لم يحج قط)','3029','1439','0');
INSERT INTO [t151] VALUES (1448,'مسألة: (ومن كرر محظورا من جنس غير قتل الصيد فكفارة واحدة، إلا أن يكون قد كفر عن الأول، فعليه للثاني كفارة، وإن فعل محظورا من أجناس فلكل واحد كفارة)','3031','1378','0');
INSERT INTO [t151] VALUES (1449,'الفصل الأول: إذا كرر محظورا من جنس واحد غير قتل الصيد، فعليه كفارة واحدة ما لم يكن كفر عن الأول','3031','1448','0');
INSERT INTO [t151] VALUES (1450,'الفصل الثاني: أن الصيد تتعدد كفارته بتعدد قتله','3035','1448','0');
INSERT INTO [t151] VALUES (1451,'ما روي أن من عاد متعمدا، لا جزاء عليه، وإنما ينتقم الله منه','3036','1450','0');
INSERT INTO [t151] VALUES (1452,'تحرير أن قوله تعالى: {ومن عاد فينتقم الله منه} لا يمنع وجوب الجزاء عليه','3039','1450','0');
INSERT INTO [t151] VALUES (1453,'الفصل الثالث (إذا فعل محظورات من أجناس، فعليه لكل جنس كفارة)','3040','1448','0');
INSERT INTO [t151] VALUES (1454,'فصل (أما صفة الأجناس: فإن الطيب كله جنس واحد، واللباس كله جنس واحد ويدخل فيه تظليل المحمل)','3041','1453','0');
INSERT INTO [t151] VALUES (1455,'هل شعر الرأس وشعر البدن جنس أو جنسان؟ على روايتين منصوصتين','3042','1453','0');
INSERT INTO [t151] VALUES (1456,'مسألة: (والحلق والتقليم والوطء وقتل الصيد يستوي عمده وسهوه، وسائر المحظورات لا شيء في سهوه)','3045','1378','0');
INSERT INTO [t151] VALUES (1457,'الفصل الأول: أن المحظور الذي يمكن تداركه وإزالته عند الذكر إذا فعله ناسيا، أو جاهلا، فلا كفارة عليه في إحدى الروايتين','3046','1456','0');
INSERT INTO [t151] VALUES (1458,'الفصل الثاني: أنه إذا قتل الصيد ناسيا أو جاهلا فعليه الكفارة، كما على العامد','3049','1456','0');
INSERT INTO [t151] VALUES (1459,'دلالة قوله تعالى: {ومن قتله منكم متعمدا فجزاء مثل ما قتل من النعم} على أنه لا جزاء في الخطأ من وجوه','3049','1458','0');
INSERT INTO [t151] VALUES (1460,'دلالة السنة وإجماع التابعين على وجوب الكفارة في الخطأ','3051','1458','0');
INSERT INTO [t151] VALUES (1461,'سبب تخصيص المتعمد بالذكر في الآية','3053','1458','0');
INSERT INTO [t151] VALUES (1462,'الفصل الثالث: إذا حلق شعرا وقلم ظفرا ناسيا أو مخطئا أو جاهلا، فالمنصوص عنه أن فيه الكفارة','3054','1456','0');
INSERT INTO [t151] VALUES (1463,'فصل (إن حلق حلال رأس محرم وهو نائم، أو أكرهه على ذلك)','3056','1462','0');
INSERT INTO [t151] VALUES (1464,'مسألة: (وكل هدي أو إطعام فهو لمساكين الحرم، إلا فدية الأذى يفرقها في الموضع الذي حلق، وهدي المحصر ينحره في موضعه، وأما الصيام فيجزئه بكل مكان)','3056','1378','0');
INSERT INTO [t151] VALUES (1465,'الفصل الأول: أن الهدي عشرة أنواع، وكلها لا تذبح إلا بالحرم، إلا هدي الإحصار','3056','1464','0');
INSERT INTO [t151] VALUES (1466,'الفصل الثاني: أن الإطعام الواجب حيث يجب الهدي حكمه حكم ذلك الهدي','3058','1464','0');
INSERT INTO [t151] VALUES (1467,'الفصل الثالث: أن الصوم يجزئ بكل مكان','3058','1464','0');
INSERT INTO [t151] VALUES (1468,'باب دخول مكة','3059','1073','0');
INSERT INTO [t151] VALUES (1469,'مسألة: (يستحب أن يدخل مكة من أعلاها)','3059','1468','0');
INSERT INTO [t151] VALUES (1470,'فصل (ما يستحب من الدعاء والاغتسال عند دخول الحرم)','3061','1469','0');
INSERT INTO [t151] VALUES (1471,'مسألة: (ويدخل المسجد من باب بني شيبة اقتداء برسول الله صلى الله عليه وسلم)','3062','1468','0');
INSERT INTO [t151] VALUES (1472,'مسألة: (فإذا رأى البيت رفع يديه وكبر الله وحمده ودعا)','3063','1468','0');
INSERT INTO [t151] VALUES (1473,'مسألة: (ثم يبتدئ بطواف العمرة إن كان معتمرا، وبطواف القدوم إن كان مفردا أو قارنا)','3065','1468','0');
INSERT INTO [t151] VALUES (1474,'فصل (وإن أدرك مكتوبة في جماعة، أو تذكر فائتة، بدأ بها قبل الطواف)','3067','1473','0');
INSERT INTO [t151] VALUES (1475,'مسألة: (ويضطبع بردائه، فيجعل وسطه تحت عاتقه الأيمن، وطرفيه على الأيسر)','3068','1468','0');
INSERT INTO [t151] VALUES (1476,'لا يضطبع في ركعتي الطواف؛ لأن الاضطباع في الصلاة مكروه','3070','1475','0');
INSERT INTO [t151] VALUES (1477,'مسألة: (ويبدأ بالحجر الأسود، فيستلمه ويقبله، ويقول: بسم الله والله أكبر، اللهم إيمانا بك، وتصديقا بكتابك، ووفاء بعهدك، واتباعا لسنة نبيك محمد صلى الله عليه وسلم)','3071','1468','0');
INSERT INTO [t151] VALUES (1478,'فإن لم يمكنه تقبيله استلمه بيده أو بعصاه، وقبلهما','3073','1477','0');
INSERT INTO [t151] VALUES (1479,'فإن لم يمكنه التقبيل ولا الاستلام بيده ولا بشيء في يده، أشار إليه بيده','3075','1477','0');
INSERT INTO [t151] VALUES (1480,'ما ورد في السجود على الحجر الأسود','3077','1477','0');
INSERT INTO [t151] VALUES (1481,'فصل (في فضل الحجر الأسود واستلامه وتقبيله ومعنى ذلك)','3080','1477','0');
INSERT INTO [t151] VALUES (1482,'الركن الأسود يمين الله عز وجل في الأرض','3082','1477','0');
INSERT INTO [t151] VALUES (1483,'فصل (السنة أن يبتدئ بالحجر فيستقبله في أول الطواف)','3083','1477','0');
INSERT INTO [t151] VALUES (1484,'مسألة: (ثم يأخذ على يمينه، ويجعل البيت على يساره، فيطوف سبعا)','3085','1468','0');
INSERT INTO [t151] VALUES (1485,'مسألة: (يرمل في الثلاثة الأول من الحجر إلى الحجر، ويمشي في الأربعة)','3086','1468','0');
INSERT INTO [t151] VALUES (1486,'فصل (يستحب للطائف الدنو من البيت، إلا أن يؤذي غيره أو يتأذى بنفسه)','3089','1485','0');
INSERT INTO [t151] VALUES (1487,'مسألة: (وكلما حاذى الركن اليماني والحجر استلمهما، وكبر وهلل، ويقول بين الركنين: {ربنا آتنا في الدنيا حسنة وفي الآخرة حسنة وقنا عذاب النار}، ويدعو في سائره بما أحب)','3090','1468','0');
INSERT INTO [t151] VALUES (1488,'الفصل الأول: أنه يستلم الركنين اليمانيين خاصة','3091','1487','0');
INSERT INTO [t151] VALUES (1489,'المنصوص عن أحمد لا يقبل الركن اليماني','3093','1488','0');
INSERT INTO [t151] VALUES (1490,'الفصل الثاني: ما يقوله إذا استلم الركنين','3095','1487','0');
INSERT INTO [t151] VALUES (1491,'مسألة: (ثم يصلي ركعتين خلف المقام)','3095','1468','0');
INSERT INTO [t151] VALUES (1492,'مسألة: (ويعود إلى الركن فيستلمه، ويخرج إلى الصفا من بابه)','3096','1468','0');
INSERT INTO [t151] VALUES (1493,'مسألة: (ثم يخرج إلى الصفا من بابه، فيأتيه فيرقى عليه، ويكبر الله ويهلله ويدعوه، ثم ينزل فيمشي إلى العلم، ثم يسعى إلى العلم الآخر، ثم يمشي إلى المروة، فيفعل كفعله على الصفا)','3097','1468','0');
INSERT INTO [t151] VALUES (1494,'يستحب أن يرفع يديه، وأن يستقبل البيت في حال وقوفه على الصفا والمروة','3098','1493','0');
INSERT INTO [t151] VALUES (1495,'استحباب دعاء ابن عمر على الصفا والمروة','3105','1493','0');
INSERT INTO [t151] VALUES (1496,'السنة رفع الصوت بالتكبير دون الدعاء','3107','1493','0');
INSERT INTO [t151] VALUES (1497,'الخلاف في مشروعية التلبية على الصفا والمروة','3107','1493','0');
INSERT INTO [t151] VALUES (1498,'فصل (صفة السعي بين الصفا والمروة)','3110','1493','0');
INSERT INTO [t151] VALUES (1499,'فصل (يستحب أن يذكر الله في السعي بين الصفا والمروة)','3112','1493','0');
INSERT INTO [t151] VALUES (1500,'فصل (ليس على النساء سعي بين العلمين، ولا صعود على الصفا والمروة)','3113','1493','0');
INSERT INTO [t151] VALUES (1501,'مسألة: (ثم يقصر من شعره إن كان معتمرا، وقد حل إلا المتمتع إن كان معه هدي، والقارن والمفرد فإنه لا يحل)','3114','1468','0');
INSERT INTO [t151] VALUES (1502,'فصل (وأما من ساق الهدي ففيه ثلاث روايات)','3116','1501','0');
INSERT INTO [t151] VALUES (1503,'إحداهن: لا ينحر هديه، ولا يحل من إحرامه إلى يوم النحر','3116','1502','0');
INSERT INTO [t151] VALUES (1504,'الرواية الثانية: أن سائق الهدي يحل ليقصر من شعر رأسه إن شاء، دون غيره من محظورات الإحرام','3119','1502','0');
INSERT INTO [t151] VALUES (1505,'الرواية الثالثة: إن قدم في العشر لم ينحر ولم يحل، وإن قدم قبل العشر نحر وحل إن شاء','3121','1502','0');
INSERT INTO [t151] VALUES (1506,'فصل (إن أراد المعتمر في أشهر الحج أن يرجع إلى مسافة القصر، جاز له النحر والتحلل)','3125','1501','0');
INSERT INTO [t151] VALUES (1507,'فصل (من ساق الهدي وقدم في العشر، فهو ممنوع من نحره إلى يوم النحر)','3125','1501','0');
INSERT INTO [t151] VALUES (1508,'مسألة: (والمرأة كالرجل إلا أنها لا ترمل في طواف ولا سعي)','3126','1468','0');
INSERT INTO [t151] VALUES (1509,'باب صفة الحج','3127','1073','0');
INSERT INTO [t151] VALUES (1510,'مسألة: (وإذا كان يوم التروية فمن كان حلالا أحرم من مكة، وخرج إلى عرفات)','3127','1509','0');
INSERT INTO [t151] VALUES (1511,'الفصل الأول: أن السنة أن يخرج الناس إلى عرفات يوم التروية من أول النهار','3127','1510','0');
INSERT INTO [t151] VALUES (1512,'الفصل الثاني: أنه من كان مقيما على إحرامه خرج إلى منى، ومن كان حلالا أحرم قبل التوجه','3129','1510','0');
INSERT INTO [t151] VALUES (1513,'لا يشرع تقدم الإحرام على يوم التروية لمن لم يجد الهدي','3130','1512','0');
INSERT INTO [t151] VALUES (1514,'أما مكان الإحرام فالمستحب أن يحرم من المسجد الحرام','3132','1512','0');
INSERT INTO [t151] VALUES (1515,'هل يهل المكي بالحج يوم التروية، أو من أول الشهر؟ روايتان','3135','1512','0');
INSERT INTO [t151] VALUES (1516,'الفصل الثالث: أنهم يبيتون بمنى حتى تطلع الشمس على ثبير','3138','1510','0');
INSERT INTO [t151] VALUES (1517,'الفصل الرابع: أنهم يسيرون من منى، فلا يقفون عند المشعر الحرام كما كانت الجاهلية تفعل، فينزلون قبل الزوال بنمرة','3138','1510','0');
INSERT INTO [t151] VALUES (1518,'مسألة: (فإذا زالت الشمس يوم عرفة صلى الظهر والعصر يجمع بينهما)','3143','1509','0');
INSERT INTO [t151] VALUES (1519,'وقد أعرض جمهور الناس عن النزول بنمرة أول النهار، ثم الصلاة ببطن عرنة قبل إتيان الموقف، بل يوافون عرفة من أول النهار خلافا للسنة','3145','1518','0');
INSERT INTO [t151] VALUES (1520,'فصل (السنة أن يخطب بهم الإمام ببطن عرنة قبل الوقوف، يخطب ثم يصلي)','3146','1518','0');
INSERT INTO [t151] VALUES (1521,'مسألة: (ويستقبل القبلة)','3150','1509','0');
INSERT INTO [t151] VALUES (1522,'مسألة: (ويكون راكبا)','3150','1509','0');
INSERT INTO [t151] VALUES (1523,'مسألة: (ويكثر من قول: لا إله إلا الله وحده لا شريك له، له الملك وله الحمد، بيده الخير، وهو على كل شيء قدير، ويجتهد في الدعاء والرغبة إلى الله عز وجل إلى غروب الشمس)','3152','1509','0');
INSERT INTO [t151] VALUES (1524,'فضل يوم عرفة، وبيان أنه ليس في الدنيا مشهد أعظم منه','3152','1523','0');
INSERT INTO [t151] VALUES (1525,'ما روي في فضل التهليل يوم عرفة','3154','1523','0');
INSERT INTO [t151] VALUES (1526,'ذكر الأدعية المأثورة في الموقف عشية عرفة','3155','1523','0');
INSERT INTO [t151] VALUES (1527,'مسألة: (ثم يدفع مع الإمام إلى مزدلفة على طريق المأزمين وعليه السكينة والوقار، ويكون ملبيا ذاكرا لله عز وجل)','3158','1509','0');
INSERT INTO [t151] VALUES (1528,'مسألة: (فإذا وصل إلى مزدلفة صلى المغرب والعشاء قبل حط الرحال، يجمع بينهما)','3160','1509','0');
INSERT INTO [t151] VALUES (1529,'هذا الجمع مسنون لكل حاج من المكيين وغيرهم','3162','1528','0');
INSERT INTO [t151] VALUES (1530,'مسألة: (ثم يبيت بها)','3163','1509','0');
INSERT INTO [t151] VALUES (1531,'مسألة: (ثم يصلي الفجر بغلس)','3163','1509','0');
INSERT INTO [t151] VALUES (1532,'مسألة: (ويأتي المشعر الحرام فيقف عنده، ويدعو، ويكون من دعائه: اللهم كما وقفتنا فيه، وأريتنا إياه، فوفقنا لذكرك كما هديتنا، واغفر لنا، وارحمنا)','3165','1509','0');
INSERT INTO [t151] VALUES (1533,'المشعر الحرام في الأصل اسم للمزدلفة كلها، ثم خص بهذا الاسم قزح','3165','1532','0');
INSERT INTO [t151] VALUES (1534,'فصل (لا يفيض الإمام من جمع حتى يسفر النهار، فيفيض قبل طلوع الشمس)','3169','1532','0');
INSERT INTO [t151] VALUES (1535,'المعذور الذي يريد التعجل يذكر الله عند المشعر الحرام بليل','3170','1532','0');
INSERT INTO [t151] VALUES (1536,'فصل (الجبل الذي يستحب الوقوف عنده بالمزدلفة له ثلاثة أسماء: قزح، والمشعر الحرام، والميقدة)','3172','1532','0');
INSERT INTO [t151] VALUES (1537,'مسألة: (ثم يدفع قبل طلوع الشمس، فإذا بلغ محسرا أسرع قدر رمية بحجر حتى يأتي منى)','3172','1509','0');
INSERT INTO [t151] VALUES (1538,'مسألة: (حتى يأتي منى فيبدأ بجمرة العقبة، فيرميها بسبع حصيات كحصى الخذف، يكبر مع كل حصاة)','3174','1509','0');
INSERT INTO [t151] VALUES (1539,'الفصل الأول: أول شيء يصنعه إذا قدم منى أن يؤم جمرة العقبة ليرميها)','3174','1538','0');
INSERT INTO [t151] VALUES (1540,'الفصل الثاني: أن يرميها بسبع حصيات','3175','1538','0');
INSERT INTO [t151] VALUES (1541,'الفصل الثالث: أنه يستحب أن يكون الحصى كحصى الخذف','3175','1538','0');
INSERT INTO [t151] VALUES (1542,'الفصل الرابع: أنه يكبر مع كل حصاة، ويرفع يده في الرمي','3176','1538','0');
INSERT INTO [t151] VALUES (1543,'الفصل الخامس: أنه يقطع التلبية مع ابتداء الرمي','3176','1538','0');
INSERT INTO [t151] VALUES (1544,'الفصل السادس: أن السنة أن يرميها من بطن الوادي','3177','1538','0');
INSERT INTO [t151] VALUES (1545,'الفصل السابع: أنه يستقبل القبلة، فيجعل الجمرة عن يمينه ومنى وراءه','3179','1538','0');
INSERT INTO [t151] VALUES (1546,'الفصل الثامن: أنه لا يقف عندها','3180','1538','0');
INSERT INTO [t151] VALUES (1547,'مسألة: (ثم ينحر هديه)','3181','1509','0');
INSERT INTO [t151] VALUES (1548,'مسألة: (ثم يحلق ويقصر)','3181','1509','0');
INSERT INTO [t151] VALUES (1549,'مسألة: (ثم قد حل له كل شيء إلا النساء)','3182','1509','0');
INSERT INTO [t151] VALUES (1550,'هل يباح له وجوه الاستماع بالنساء غير الوطء؟ روايتان','3185','1549','0');
INSERT INTO [t151] VALUES (1551,'فصل (هل يحصل التحلل الأول بمجرد الرمي، أو بالرمي والحلاق؟ روايتان)','3186','1549','0');
INSERT INTO [t151] VALUES (1552,'تقرير أن الحلق والتقصير جزء من النسك من وجوه','3189','1549','0');
INSERT INTO [t151] VALUES (1553,'فصل (هل نحر الهدي يقوم مقام الحلاق في التحلل الأول؟)','3192','1549','0');
INSERT INTO [t151] VALUES (1554,'مسألة: (ثم يفيض إلى مكة فيطوف للزيارة؛ وهو الطواف الذي به تمام الحج)','3192','1509','0');
INSERT INTO [t151] VALUES (1555,'ويسميه العراقيون طواف الزيارة. ويسمى الطواف الفرض، وطواف الصدر عن منى','3193','1554','0');
INSERT INTO [t151] VALUES (1556,'مسألة: (ثم يسعى بين الصفا والمروة إن كان متمتعا، أو ممن لم يسع مع طواف القدوم)','3194','1509','0');
INSERT INTO [t151] VALUES (1557,'من قال إن المتمتع يجزئه سعي واحد عن حجه وعمرته','3196','1556','0');
INSERT INTO [t151] VALUES (1558,'مسألة: (ثم قد حل من كل شيء)','3197','1509','0');
INSERT INTO [t151] VALUES (1559,'مسألة: (ويستحب أن يشرب من ماء زمزم لما أحب، ويتضلع منه ثم يقول: اللهم اجعله لنا علما نافعا، ورزقا واسعا، وريا وشبعا)','3198','1509','0');
INSERT INTO [t151] VALUES (1560,'فصل (يستحب الشرب من شراب السقاية)','3201','1559','0');
INSERT INTO [t151] VALUES (1561,'باب ما يفعله بعد الحل','3202','1073','0');
INSERT INTO [t151] VALUES (1562,'مسألة: (ثم يرجع إلى منى، ولا يبيت لياليها إلا بها)','3202','1561','0');
INSERT INTO [t151] VALUES (1563,'مسألة: (فيرمي بها الجمار بعد الزوال من أيامها، كل جمرة بسبع حصيات، يبتدئ بالجمرة الأولى)','3203','1561','0');
INSERT INTO [t151] VALUES (1564,'الفصل الأول: أن الحاج يرمي الجمرات الثلاث أيام منى الثلاثة بعد الزوال','3203','1563','0');
INSERT INTO [t151] VALUES (1565,'الفصل الثاني: أنه يرمي كل جمرة بسبع حصيات','3205','1563','0');
INSERT INTO [t151] VALUES (1566,'الفصل الثالث: أن يبتدئ بالجمرة الأولى، ثم بالجمرة الوسطى، ثم بجمرة العقبة','3205','1563','0');
INSERT INTO [t151] VALUES (1567,'الفصل الرابع: أنه يستقبل القبلة عند رمي الأوليين','3206','1563','0');
INSERT INTO [t151] VALUES (1568,'الفصل الخامس: أنه إذا رمى الأولى والثانية تقدم قليلا إلى ناحية الكعبة، فاستقبل القبلة، ووقف يدعو الله سبحانه','3206','1563','0');
INSERT INTO [t151] VALUES (1569,'فصل (السنة أن يمشي من منزله إلى الجمار ويرميها واقفا)','3208','1568','0');
INSERT INTO [t151] VALUES (1570,'مسألة: (وليس في عمل القارن زيادة على عمل المفرد)','3209','1561','0');
INSERT INTO [t151] VALUES (1571,'يجزئ القارن طواف واحد وسعي واحد','3210','1570','0');
INSERT INTO [t151] VALUES (1572,'فصل (هل على المتمتع سعي آخر للحج؟ على روايتين منصوصتين)','3211','1570','0');
INSERT INTO [t151] VALUES (1573,'مسألة: (لكن عليه وعلى المتمتع دم؛ لقوله تعالى: {فمن تمتع بالعمرة إلى الحج فما استيسر من الهدي})','3214','1561','0');
INSERT INTO [t151] VALUES (1574,'مسألة: (وإذا أراد القفول لم يخرج حتى يودع البيت بطواف عند فراغه من جميع أموره، حتى يكون آخر عهده بالبيت)','3214','1561','0');
INSERT INTO [t151] VALUES (1575,'مسألة: (فإن اشتغل بعده بتجارة أعاده)','3215','1561','0');
INSERT INTO [t151] VALUES (1576,'مسألة: (ويستحب له إذا طاف أن يقف في الملتزم بين الركن والباب، فيلتزم البيت ويقول: «اللهم هذا بيتك، وأنا عبدك، وابن عبدك)','3215','1561','0');
INSERT INTO [t151] VALUES (1577,'مسألة: (ومن خرج قبل الوداع رجع إن كان قريبا، وإن أبعد بعث بدم)','3216','1561','0');
INSERT INTO [t151] VALUES (1578,'مسألة: (إلا الحائض والنفساء فلا وداع عليهما، ويستحب لهما الوقوف عند باب المسجد والدعاء بهذا)','3216','1561','0');
INSERT INTO [t151] VALUES (1579,'باب أركان الحج والعمرة','3219','1073','0');
INSERT INTO [t151] VALUES (1580,'مسألة: (أركان الحج: الوقوف بعرفة، وطواف الزيارة)','3219','1579','0');
INSERT INTO [t151] VALUES (1581,'الفصل الأول: أن الوقوف بعرفة لا يتم الحج إلا به','3219','1580','0');
INSERT INTO [t151] VALUES (1582,'دلالة القرآن على ذلك','3219','1581','0');
INSERT INTO [t151] VALUES (1583,'دلالة السنة على ذلك','3222','1581','0');
INSERT INTO [t151] VALUES (1584,'فصل (وللوقوف بعرفة مكان وزمان، لا يصح إلا فيها)','3224','1580','0');
INSERT INTO [t151] VALUES (1585,'أما إن وقف قبل الزوال ففيه روايتان','3226','1580','0');
INSERT INTO [t151] VALUES (1586,'فصل (يشترط لصحة كل طواف عشرة أشياء)','3230','1580','0');
INSERT INTO [t151] VALUES (1587,'الشرط الأول: النية. الشرط الثاني: الطهارة من الحدث','3231','1586','0');
INSERT INTO [t151] VALUES (1588,'فصل (فإن طاف على غير طهارة، ففيه روايتان)','3235','1587','0');
INSERT INTO [t151] VALUES (1589,'الشرط الثالث: أن يكون طاهرا من الخبث','3238','1586','0');
INSERT INTO [t151] VALUES (1590,'الشرط الرابع: السترة','3238','1586','0');
INSERT INTO [t151] VALUES (1591,'الشرط الخامس: أن يطوف سبعة أطواف','3240','1586','0');
INSERT INTO [t151] VALUES (1592,'الشرط السادس: أن يبتدئ بالحجر الأسود','3240','1586','0');
INSERT INTO [t151] VALUES (1593,'الشرط السابع: أن يبتدئ بعد الحجر الأسود بناحية الباب، فيجعل البيت عن يساره','3241','1586','0');
INSERT INTO [t151] VALUES (1594,'الشرط الثامن: الموالاة','3241','1586','0');
INSERT INTO [t151] VALUES (1595,'الشرط التاسع: أن يطوف بالبيت جميعه، فلا يطوف في شيء منه','3243','1586','0');
INSERT INTO [t151] VALUES (1596,'بيان أن قدر ستة أذرع من الحجر جزء من البيت على قواعد إبراهيم','3244','1595','0');
INSERT INTO [t151] VALUES (1597,'الشرط العاشر: أن يطوف في المسجد الحرام، فإن طاف خارج المسجد لم يصح','3247','1586','0');
INSERT INTO [t151] VALUES (1598,'الفصل الثالث: أنه لا ركن إلا الوقوف بعرفة، والطواف طواف الزيارة','3248','1580','0');
INSERT INTO [t151] VALUES (1599,'هل السعي بين الصفا والمروة ركن؟ روايتان','3249','1598','0');
INSERT INTO [t151] VALUES (1600,'هل الإحرام شرط للحج أو ركن فيه؟ خلاف في العبارة','3250','1598','0');
INSERT INTO [t151] VALUES (1601,'مسألة: (وواجباته: الإحرام من الميقات)','3251','1579','0');
INSERT INTO [t151] VALUES (1602,'مسألة: (والوقوف بعرفة إلى الليل)','3252','1579','0');
INSERT INTO [t151] VALUES (1603,'فصل (لا يجوز له أن يفيض من عرفات قبل غروب الشمس)','3253','1602','0');
INSERT INTO [t151] VALUES (1604,'فصل (لو وقف قبل الزوال أو بعده ثم خرج، ثم رجع لا دم عليه)','3256','1602','0');
INSERT INTO [t151] VALUES (1605,'مسألة: (والمبيت بمزدلفة إلى نصف الليل)','3256','1579','0');
INSERT INTO [t151] VALUES (1606,'وأحكام جمع مضطربة تتلخص في مسائل','3259','1605','0');
INSERT INTO [t151] VALUES (1607,'الأولى والثانية: أن الوقوف بها واجب في الجملة، وليس بركن','3259','1606','0');
INSERT INTO [t151] VALUES (1608,'الثالثة: أن من فاته الوقوف بها والمبيت فعليه دم','3261','1606','0');
INSERT INTO [t151] VALUES (1609,'الرابعة: أن الصواب أن وقت الوقوف لا يفوت إلى طلوع الشمس','3261','1606','0');
INSERT INTO [t151] VALUES (1610,'إيجاب المبيت بمزدلفة لم ينطق [به] كتاب ولا سنة، وإنما ورد الأمر بذكر الله تعالى عندها، وذلك يمكن بعد الفجر','3264','1609','0');
INSERT INTO [t151] VALUES (1611,'الخامسة: من وافاها أول الليل فعليه أن يبيت بها، بمعنى أن يقيم بها، لا يجوز له الخروج منها إلى آخر الليل','3267','1606','0');
INSERT INTO [t151] VALUES (1612,'في الوقت الذي يجوز فيه الدفع روايتان: إحداهما: يجوز الدفع بعد نصف الليل','3267','1611','0');
INSERT INTO [t151] VALUES (1613,'الرواية الثانية: لا تجوز الإفاضة قبل مغيب القمر','3269','1611','0');
INSERT INTO [t151] VALUES (1614,'فصل (هل يجب هذا المبيت على أهل السقاية والرعاء؟)','3271','1611','0');
INSERT INTO [t151] VALUES (1615,'المسألة السادسة: أن من وافاها بعد جواز الإفاضة منها، أجزأه ذلك ولا دم عليه','3271','1606','0');
INSERT INTO [t151] VALUES (1616,'المسألة السابعة: من لا عذر له فإنه يجوز أن يخرج منها قبل طلوع الفجر، والمستحب وقوفه عند قزح قبل ذلك','3272','1606','0');
INSERT INTO [t151] VALUES (1617,'يتوجه وجوب الوقوف بعد الفجر لغير أهل الأعذار','3274','1616','0');
INSERT INTO [t151] VALUES (1618,'مسألة: (والسعي)','3276','1579','0');
INSERT INTO [t151] VALUES (1619,'وروي عنه أنه ركن لا يتم الحج والعمرة إلا به، وروي عنه أنه تطوع','3276','1618','0');
INSERT INTO [t151] VALUES (1620,'حجة من قال إنه تطوع','3277','1618','0');
INSERT INTO [t151] VALUES (1621,'حجة من قال إنه واجب في الجملة','3279','1618','0');
INSERT INTO [t151] VALUES (1622,'حجة من قال إنه واجب يجب بتركه هدي','3287','1618','0');
INSERT INTO [t151] VALUES (1623,'حجة من قال إنه ركن','3289','1618','0');
INSERT INTO [t151] VALUES (1624,'فصل (يشترط له ستة أشياء)','3290','1618','0');
INSERT INTO [t151] VALUES (1625,'أما الطهارة فتسن له، ولا تشترط','3292','1618','0');
INSERT INTO [t151] VALUES (1626,'فصل (يجوز الطواف راكبا، لعذر من مرض أو كبر)','3293','1618','0');
INSERT INTO [t151] VALUES (1627,'مسألة: (والمبيت بمنى)','3293','1579','0');
INSERT INTO [t151] VALUES (1628,'إن ترك المبيت بمنى، فثلاث روايات: عليه الدم. يتصدق بشي. لا شيء عليه','3295','1627','0');
INSERT INTO [t151] VALUES (1629,'مسألة: (والرمي)','3300','1579','0');
INSERT INTO [t151] VALUES (1630,'من رمى بحجر قد رمي به لم يجزئه، ومن رمى بذهب أو فضة لم يجزئه، وفي غير الحصى روايتان','3302','1629','0');
INSERT INTO [t151] VALUES (1631,'فصل (وأما الأدعية المشروعة في الحج، مثل التلبية وغيرها، فهي سنة عند أصحابنا)','3302','1629','0');
INSERT INTO [t151] VALUES (1632,'فصل (وأما ركعتا الطواف.)','3302','1629','0');
INSERT INTO [t151] VALUES (1633,'مسألة: (والحلق)','3302','1579','0');
INSERT INTO [t151] VALUES (1634,'مسألة: (وطواف الوداع)','3303','1579','0');
INSERT INTO [t151] VALUES (1635,'فصل (المشهور في المذهب أن طواف القدوم سنة، وثم رواية أنه واجب','3304','1634','0');
INSERT INTO [t151] VALUES (1636,'مسألة: (وأركان العمرة: الطواف، وواجباتها: الإحرام والسعي والحلق)','3305','1579','0');
INSERT INTO [t151] VALUES (1637,'مسألة: (فمن ترك ركنا لم يتم نسكه إلا به، ومن ترك واجبا جبره بدم، ومن ترك سنة فلا شيء عليه)','3307','1579','0');
INSERT INTO [t151] VALUES (1638,'مسألة: (ومن لم يقف بعرفة حتى طلع الفجر يوم النحر فقد فاته الحج، فيتحلل بطواف وسعي، وينحر هديا إن كان معه، وعليه القضاء)','3308','1579','0');
INSERT INTO [t151] VALUES (1639,'الفصل الأول: يجب على المحرم بالحج أن يقصد الوقوف بعرفة في وقته، فإذا طلع الفجر ولم يواف عرفة، فقد فاته الحج، وعليه أن يتحلل بعمرة','3308','1638','0');
INSERT INTO [t151] VALUES (1640,'فصل (هل يخرج من إحرام الحج إلى إحرام العمرة، أو إحرام الحج باق عليه؟ اختلفت عبارتهم مع اتفاقهم على أنه يتحلل بطواف وسعي)','3313','1638','0');
INSERT INTO [t151] VALUES (1641,'فصل (عليه القضاء من العام المقبل في أصح الروايتين)','3318','1638','0');
