INSERT INTO [t1578] VALUES (1,'باب الألف','2','0','0');
INSERT INTO [t1578] VALUES (2,'*','2','1','0');
INSERT INTO [t1578] VALUES (3,'- «أبغض الكلام إلى الله عز وجل الفارسية، وكلام الشياطين الخوزية، وكلام أهل النار البخارية،','2','2','0');
INSERT INTO [t1578] VALUES (4,'- «أبى الله أن يرزق عبده إلا من حيث لا يحتسب» . في حديث اجتمع علي وأبو بكر يأتي بعد إن شاء','3','2','0');
INSERT INTO [t1578] VALUES (5,'باب التاء بعد الألف','3','1','0');
INSERT INTO [t1578] VALUES (6,'- «أتاني ربي البارحة في منامي في أحسن صورة حتى وضع يده بين كتفي» الحديث. رواه يوسف بن عطية','3','5','0');
INSERT INTO [t1578] VALUES (7,'- " أتاني جبريل بهريسة فقال: كل هذه تشد ظهرك لقيام الليل ". رواه محمد بن الحجاج الواسطي اللخمي،','4','5','0');
INSERT INTO [t1578] VALUES (8,'- «أتاني جبريل وعليه قباء أسود. . .» الحديث في ولد العباس. رواه الشاه بن شير باميان','4','5','0');
INSERT INTO [t1578] VALUES (9,'- " أتاني جبريل، فقال: يا محمد أحب من شئت فإنك مفارقه، واجمع ما شئت فإنك تاركه، واعمل ما شئت','4','5','0');
INSERT INTO [t1578] VALUES (10,'- " أتاني جبريل حين طلع الفجر، فقال: قم فصل، فصليت الفجر ركعتين. . . " الحديث بطوله نحو حديث','5','5','0');
INSERT INTO [t1578] VALUES (11,'أجعل شطر صلاتي دعاء لك؟ قال: «نعم» الحديث. رواه عمر بن محمد بن صهبان، عن زيد بن أسلم، عن','6','5','0');
INSERT INTO [t1578] VALUES (12,'العمرة أواجبة هي؟ . . . . " الحديث. رواه الحجاج بن أرطاة، عن محمد بن المنكدر، عن جابر.','6','5','0');
INSERT INTO [t1578] VALUES (13,'ألك زوجة؟ . . . .» الحديث. رواه معاوية بن يحيى الأطرابلسي، عن سليمان بن موسى، عن مكحول، عن','7','5','0');
INSERT INTO [t1578] VALUES (14,'خاتم من ذهب فقال لي: «أتؤدي زكاته؟» فقلت: وهل فيه زكاة؟ فقال رسول الله صلى الله عليه وسلم:','7','5','0');
INSERT INTO [t1578] VALUES (15,'- «اتخذوا السودان، فإن ثلاثة منهم سادات أهل الجنة لقمان وبلال والنجاشي» . رواه أبين بن سفيان','7','5','0');
INSERT INTO [t1578] VALUES (16,'- «اتخذوا الحمام المقاصيص، فإنها تلهي الجن عن صبيانكم» . رواه محمد بن زياد الجزري الحنفي، عن','8','5','0');
INSERT INTO [t1578] VALUES (17,'«أتربوا الكتاب واسخو من أسفله» .','8','5','0');
INSERT INTO [t1578] VALUES (18,'- «اتقوا القدر فإنه شعبة من النصرانية» . رواه نزار، عن عكرمة. ونزار يروي المناكير عن عكرمة لا','10','5','0');
INSERT INTO [t1578] VALUES (19,'- «أرأيتم رفعكم أيديكم في الصلاة إنها لبدعة» . الحديث رواه بشر بن حرب الندبي، عن عبد الله بن','10','5','0');
INSERT INTO [t1578] VALUES (20,'باب الجيم بعد الألف','11','1','0');
INSERT INTO [t1578] VALUES (21,'«جئتم لتسألوني» وفيه «وجهاد المرأة حسن التبعل» وفيه «أبى الله أن يرزق عبده إلا من حيث لا','11','20','0');
INSERT INTO [t1578] VALUES (22,'باب الحاء بعد الألف','11','1','0');
INSERT INTO [t1578] VALUES (23,'- «أحب العباد إلى الله أنفع الناس للناس» . رواه عبد الحميد بن بحر الكوفي، عن مالك، عن عبد الله','11','22','0');
INSERT INTO [t1578] VALUES (24,'- «أحبب حبيبك هونا ما عسى أن يكون بغيضك يوما ما» الحديث. رواه سويد بن عمرو الكلبي، عن حماد بن','11','22','0');
INSERT INTO [t1578] VALUES (25,'- «احذروا دعوة المؤمن وفراسته، فإنه ينظر بنور الله، وينطق بتوفيق الله» . رواه سلمة بن سليمان','12','22','0');
INSERT INTO [t1578] VALUES (26,'- أحضروا موائدكم البقل، فإنه مطردة للشيطان مع التسمية ". رواه العلاء بن مسلمة الرواس','13','22','0');
INSERT INTO [t1578] VALUES (27,'- «أحسني جوار نعمة الله عليك، فبالخبز أنزل الله المطر. . .» الحديث. رواه أبو أشرس الكوفي، عن','13','22','0');
INSERT INTO [t1578] VALUES (28,'- «أحل لكم ميتتان ودمان» ، رواه عبد الرحمن بن زيد بن أسلم، وهو ليس بشيء','13','22','0');
INSERT INTO [t1578] VALUES (29,'- «احتجم رسول الله صلى الله عليه وسلم فأعطاني دمه» الحديث. رواه إبراهيم بن عمر بن سفينة، عن','14','22','0');
INSERT INTO [t1578] VALUES (30,'باب الخاء بعد الألف','15','1','0');
INSERT INTO [t1578] VALUES (31,'- «آخر جنازة صلى عليها رسول الله صلى الله عليه وسلم كبر عليها أربعا» . رواه محمد بن معاوية','15','30','0');
INSERT INTO [t1578] VALUES (32,'- «أخر الكلام في القدر بشرار أمتي» . رواه عنبسة بن مهران الحداد، عن الزهري، عن سعيد بن المسيب،','16','30','0');
INSERT INTO [t1578] VALUES (33,'- «أخرجوا المخنثين من بيوتكم» . رواه الوازع بن نافع العقيلي، عن سالم بن عبد الله بن عمر، عن','16','30','0');
INSERT INTO [t1578] VALUES (34,'- «أخذ رسول الله صلى الله عليه وسلم بيد عبد الرحمن بن عوف، فانطلق به إلى النخل الذي فيه ابنه','16','30','0');
INSERT INTO [t1578] VALUES (35,'أنتما وزيراي. . . " الحديث. رواه زكريا بن دويد، عن حميد، عن أنس. وزكريا كذاب، له نسخة بهذا','17','30','0');
INSERT INTO [t1578] VALUES (36,': «لا يصيبك السوء يا أبا أيوب» . رواه أبو عمرو البجلي عبيدة بن عبد الرحمن وقيل عبيدة، عن يحيى','17','30','0');
INSERT INTO [t1578] VALUES (37,'باب الدال بعد الألف','17','1','0');
INSERT INTO [t1578] VALUES (38,'- «دخلت الجنة، فناولني جبريل عليه السلام تفاحة، فانفلقت في يدي فخرجت منها جارية. . . .» وفيه','18','37','0');
INSERT INTO [t1578] VALUES (39,'- «ادفنوا شعوركم وأظفاركم ودماءكم لا يلعب بها سحرة بني آدم» . رواه اليمان بن عدي أبو عدي','18','37','0');
INSERT INTO [t1578] VALUES (40,'- «ادهنوا بالبنفسج، فإنه حار في الشتاء بارد في الصيف» . رواه علي بن موسى الرضا عن آبائه، وكان','19','37','0');
INSERT INTO [t1578] VALUES (41,'باب الذال بعد الألف','19','1','0');
INSERT INTO [t1578] VALUES (42,'- «أذن بلال للنبي صلى الله عليه وسلم مثنى مثنى، وأقام مثل ذلك» . رواه زياد بن عبد الله بن','19','41','0');
INSERT INTO [t1578] VALUES (43,'- «أذيبوا طعامكم بذكر الله، لا تناموا عنه فتقسو قلوبكم» . رواه بزيغ بن حسان، عن هشام بن عروة،','19','41','0');
INSERT INTO [t1578] VALUES (44,'باب إذا على الترتيب','20','1','0');
INSERT INTO [t1578] VALUES (45,'- «إذا أقيمت الصلاة فلا صلاة إلا المكتوبة» . رواه أحمد بن محمد بن عمر بن يونس اليمامي أبو سهل،','20','44','0');
INSERT INTO [t1578] VALUES (46,'- «إذا أقيمت الصلاة فتحت أبواب الجنان وغلقت أبواب النيران. . .» الحديث. رواه محمد بن محصن، عن','21','44','0');
INSERT INTO [t1578] VALUES (47,'- «إذا أطاق الغلام صوم ثلاثة أيام وجب عليه صوم رمضان» . رواه يحيى بن العلاء الرازي، عن يحيى بن','21','44','0');
INSERT INTO [t1578] VALUES (48,'- «إذا بعثتم إلي بريدا فابعثوه حسن الاسم حسن الوجه» . رواه عمر بن راشد اليمامي، عن يحيى بن أبي','21','44','0');
INSERT INTO [t1578] VALUES (49,'- «إذا أتى علي يوم ولم أزدد فيه خيرا يقربني إلى ربي فلا بورك لي في ذلك اليوم» . رواه سليمان بن','22','44','0');
INSERT INTO [t1578] VALUES (50,'- «إذا تناول العبد كأس الخمر ناشده الإيمان بالله لا تدخله علي» . رواه محمد بن أيوب بن سويد','22','44','0');
INSERT INTO [t1578] VALUES (51,'- «إذا أتاكم من ترضون خلقه ودينه فأنكحوه» . الحديث. رواه عبد الحميد بن سليمان، عن محمد بن','23','44','0');
INSERT INTO [t1578] VALUES (52,'- «إذا أراد الله أن يبعث إلى أهل بيت ضيفا بعث قبل ذلك بأربعين صباحا طيرا أبيض» ثم ذكر حديثا','23','44','0');
INSERT INTO [t1578] VALUES (53,'- «إذا أراد أحدكم الخلاء فلا يستقبل الريح» . رواه يزيد بن سنان، عن يحيى بن أبي كثير، عن خلاد','24','44','0');
INSERT INTO [t1578] VALUES (54,'- «إذا اشتد الحر فاستعينوا بالحجامة لا يتبيغ الدم بأحدكم فيقتله» . رواه محمد بن القاسم الأسدي،','24','44','0');
INSERT INTO [t1578] VALUES (55,'- «إذا توضأتم فلا تنفضوا أيديكم، فإنها مراوح الشيطان» . رواه البختري بن عبيد الطائي، عن أبيه،','25','44','0');
INSERT INTO [t1578] VALUES (56,'- «إذا جاء الرطب فهنئيني» . رواه حسان بن سياه البصري، عن ثابت، عن أنس، أن النبي صلى الله عليه','25','44','0');
INSERT INTO [t1578] VALUES (57,'- «إذا جامع الرجل زوجته أو جاريته فلا ينظر إلى فرجها، فإن ذلك يورث العمى» . رواه بقية، عن ابن','25','44','0');
INSERT INTO [t1578] VALUES (58,'- «إذا حضرتم موتاكم فأغمضوا البصر فإن البصر يتبع الروح، وقولوا خيرا فإنه يؤمن على ما قال أهل','26','44','0');
INSERT INTO [t1578] VALUES (59,'- «إذا حملت المرأة فلها أجر الصائم القائم. . .» الحديث بطوله في هذا المعنى. رواه الحسن بن','26','44','0');
INSERT INTO [t1578] VALUES (60,'- «إذا خلعت المرأة ثيابها في غير بيت زوجها هتكت كل ستر بينها وبين الله تعالى» . رواه معاوية بن','27','44','0');
INSERT INTO [t1578] VALUES (61,'- «إذا دعوت الله فادع الله ببطن كفيك ولا تدع بظهورهما، فإذا فرغت فامسح بهما وجهك» . رواه صالح','27','44','0');
INSERT INTO [t1578] VALUES (62,'- " إذا دعوتم لأحد من اليهود والنصارى فقولوا: أكثر الله في عددك " الحديث. رواه عبد الله بن جعفر','28','44','0');
INSERT INTO [t1578] VALUES (63,'- «إذا دعي أحدكم إلى طعام فليخلع نعليه» . رواه محمد بن خليد الحنفي، عن داود بن الزبرقان، عن','28','44','0');
INSERT INTO [t1578] VALUES (64,'- «إذا ذكر القدر فأمسكوا، وإذا ذكر النجوم فأمسكوا، فإنه يدعو إلى الكهانة، وإذا ذكر أصحابي','28','44','0');
INSERT INTO [t1578] VALUES (65,'- «إذا رأيتم آية فاسجدوا» . رواه إبراهيم بن الحكم بن أبان العدني، عن أبيه، عن عكرمة، عن ابن','29','44','0');
INSERT INTO [t1578] VALUES (66,'- «إذا رأيتم رياض الجنة فارتعوا» قيل: وما رياض الجنة؟ قال: «حلق الذكر» . رواه محمد بن ثابت','29','44','0');
INSERT INTO [t1578] VALUES (67,'- «إذا رجع أحدكم من سفر فليرجع إلى أهله بهدية، فإن لم يجد إلا أن يلقي في مخلاته حجرا أو حزمة','31','44','0');
INSERT INTO [t1578] VALUES (68,'- «إذا سلم رمضان سلمت السنة، وإذا سلمت الجمعة سلمت الأيام» . رواه عبد العزيز بن أبان، عن','31','44','0');
INSERT INTO [t1578] VALUES (69,'- «إذا سقط الهلال قبل الشفق فهو لليلته، وإذا سقط بعد الشفق فهو لليلتين» . رواه رشدين بن سعد،','31','44','0');
INSERT INTO [t1578] VALUES (70,'- «إذا ضحك أحدكم في صلاته فعليه الوضوء والصلاة، وإذا تبسم فلا شيء عليه» . رواه محمد بن عبد','32','44','0');
INSERT INTO [t1578] VALUES (71,'- «إذا طنت أذن أحدكم فليذكرني وليصل علي وليقل ذكر الله بخير من ذكرني» . رواه محمد بن عبيد الله','32','44','0');
INSERT INTO [t1578] VALUES (72,'- «إذا عملت أمتي خمس عشرة خصلة حل بها البلاء» . رواه فرج بن فضالة، عن يحيى بن سعيد، عن محمد بن','33','44','0');
INSERT INTO [t1578] VALUES (73,'- «إذا عرف الغلام يمينه من شماله فمروه بالصلاة» . رواه هشام بن سعد، عن معاذ بن عبد الله بن','33','44','0');
INSERT INTO [t1578] VALUES (74,'- «إذا غاب الهلال قبل الشفق فهو لليلته، وإذا غاب بعد الشفق فهو لليلتين» . رواه الوليد بن سلمة','33','44','0');
INSERT INTO [t1578] VALUES (75,'- «إذا كان يوم القيامة دعا الله عبدا من عباده فيسأله عن جاهه كما يسأله عن ماله» . رواه يوسف بن','34','44','0');
INSERT INTO [t1578] VALUES (76,'- «إذا كان يوم القيامة بعث الله قوما عليهم ثياب خضر بأجنحة خضر. .» الحديث. رواه حميد بن علي','35','44','0');
INSERT INTO [t1578] VALUES (77,'- «إذا كان يوم القيامة نادى مناد من تحت العرش. . .» الحديث وفيه ذكر لأبي بكر «أدخل من شئت» وقد','36','44','0');
INSERT INTO [t1578] VALUES (78,'- " إذا كان يوم القيامة نادى مناد من وراء الحجاب: يأهل الجمع غضوا أبصاركم عن فاطمة بنت محمد','36','44','0');
INSERT INTO [t1578] VALUES (79,'- «إذا كان يوم القيامة يدعى فسقة العلماء، فيؤمر بهم إلى النار قبل عبدة الأوثان» الحديث. رواه','37','44','0');
INSERT INTO [t1578] VALUES (80,'- " إذا كان يوم عرفة غفر الله للحاج، الحديث إلى قوله: إذا كان يوم جمرة العقبة غفر الله','37','44','0');
INSERT INTO [t1578] VALUES (81,'- «إذا كان العبد بين نفر فأعتق» . الحديث وفيه «ألا يستسعى» الحديث. رواه الحجاج بن أرطاة، عن','38','44','0');
INSERT INTO [t1578] VALUES (82,'- «إذا كان الفيء ذراعا ونصفا إلى ذراعين فصلوا الظهر» رواه أصرم بن حوشب، عن زياد بن سعد، عن','38','44','0');
INSERT INTO [t1578] VALUES (83,'- «إذا كان آخر الزمان اختلفت الأهواء فعليكم بدين البادية والنساء» . رواه محمد بن عبد الرحمن بن','38','44','0');
INSERT INTO [t1578] VALUES (84,'- «إذا كان أول ليلة من شهر رمضان نادى الجليل عز وجل رضوان. . .» الحديث بطوله. رواه أصرم بن','39','44','0');
INSERT INTO [t1578] VALUES (85,'- " إذا كان سنة ستين ومائة كان الغرباء في الدنيا أربعة: قرآن في جوف ظالم، ومصحف في بيت لا','39','44','0');
INSERT INTO [t1578] VALUES (86,'- «إذا فرغ أحدكم من كتابه، فلا يكتب عليه» بلغ «فإنه اسم الشيطان، ولكن يكتب عليه» الله ". رواه','40','44','0');
INSERT INTO [t1578] VALUES (87,'- " إذا قال الرجل للرجل: يا يهودي فاجلدوه عشرين. . . " الحديث. رواه إبراهيم بن إسماعيل بن أبي','40','44','0');
INSERT INTO [t1578] VALUES (88,'- «إذا قاء أحدكم أو رعف في الصلاة أو أحدث فلينصرف فليتوضأ ثم ليجئ فليبن على ما مضى» . رواه أبو','41','44','0');
INSERT INTO [t1578] VALUES (89,'- «إذا قدم أحدكم من سفر فليهد لأهله وليطرفهم ولو بحجارة» . رواه محمد بن المنذر بن عبيد الله،','41','44','0');
INSERT INTO [t1578] VALUES (90,'- «إذا لقي أحدكم أخاه فليسلم عليه، فإن حالت بينهما شجرة أو جدار ولقيه فليسلم عليه» . رواه عبد','41','44','0');
INSERT INTO [t1578] VALUES (91,'- «إذا لقي أحدكم أخاه في النهار مرارا فليسلم عليه، فإن النعمة ربما حدثت في الساعة» . رواه يحيى','42','44','0');
INSERT INTO [t1578] VALUES (92,'- «إذا لقيت الحاج فسلم عليه وصافحه ومره أن يستغفر لك قبل أن يدخل بيته» . رواه محمد بن عبد','42','44','0');
INSERT INTO [t1578] VALUES (93,'- «إذا مرض العبد ثم برأ من مرضه كان كالبردة البيضاء» . رواه سفيان بن محمد الفزاري، عن ابن وهب،','42','44','0');
INSERT INTO [t1578] VALUES (94,'- «إذا مشت أمتي المطيطاء وخدمتها أبناء الملوك أبناء فارس والروم سلط الله شرارها على خيارها» .','43','44','0');
INSERT INTO [t1578] VALUES (95,'- «إذا مدح الفاسق اهتز العرش» . رواه أبو خلف الأعمى واسمه حازم، عن أنس. وهو منكر','43','44','0');
INSERT INTO [t1578] VALUES (96,'- «إذا نام أحدكم وفي نفسه أن يصلي من الليل. . .» الحديث. رواه أيوب بن عتبة، عن يحيى بن أبي','44','44','0');
INSERT INTO [t1578] VALUES (97,'- «إذا نزل على قوم ضيف فلا يصوم إلا بإذنهم» . رواه أبو بكر الداهري عبد الله بن الحكم، عن هشام','44','44','0');
INSERT INTO [t1578] VALUES (98,'- «إذا وقعت كبيرة أو هاجت ريح مظلمة فعليكم بالتكبير، فإنه يجلي العجاج» . رواه عنبسة بن عبد','45','44','0');
INSERT INTO [t1578] VALUES (99,'- «إذا وضعت الحلوى بين يدي أحدكم فليصب منها ولا يردها» . رواه فضالة بن حصين، عن محمد بن عمرو،','45','44','0');
INSERT INTO [t1578] VALUES (100,'- «إذا وضعت المائدة فليأكل الرجل مما يليه. .» الحديث بتمامه. رواه عبد الأعلى بن أعين، عن يحيى','45','44','0');
INSERT INTO [t1578] VALUES (101,'باب الراء بعد الألف','46','1','0');
INSERT INTO [t1578] VALUES (102,'- " أربع لا يشبعن من أربع: أرض من مطر، وعين من نظر، وأنثى من ذكر، وطالب علم من علم ". رواه حسين','46','101','0');
INSERT INTO [t1578] VALUES (103,'- " أربع لا تقبل من أربع: نفقة من خيانة أو سرقة أو غلول، أو مال يتيم لا يقبل في حج ولا عمرة','47','101','0');
INSERT INTO [t1578] VALUES (104,'- " أربع لا يصبن إلا بعجب: الصمت، وهو أول العبادة، والتواضع وذكر الله وقلة الشيء ". رواه العوام','47','101','0');
INSERT INTO [t1578] VALUES (105,'- «أربع خصال واحدة منهن لي، وواحدة لك، وواحدة فيما بيني وبينك» الحديث. رواه صالح بن بشير، عن','48','101','0');
INSERT INTO [t1578] VALUES (106,'- " أربعة أبواب من أبواب الجنة مفتحة في الدنيا: أولهن إسكندرية، وعسقلان، وقزوين، وعبادان،','48','101','0');
INSERT INTO [t1578] VALUES (107,'- «أربعة لعنتهم وكل نبي مجاب. . .» الحديث. رواه أحمد بن العباس بن عيسى بن هارون بن سليمان','48','101','0');
INSERT INTO [t1578] VALUES (108,'- " ارحموا ثلاثة: عزيز قوم ذل. . . " الحديث. رواه أبو البختري بن وهب، عن ابن جريج، عن عطاء، عن','49','101','0');
INSERT INTO [t1578] VALUES (109,'باب السين بعد الألف','49','1','0');
INSERT INTO [t1578] VALUES (110,'- «أسبغ الوضوء يزد في عمرك. . .» الحديث. رواه عويد بن أبي عمران الجوني، عن أبيه، عن أنس.','49','109','0');
INSERT INTO [t1578] VALUES (111,'- «أسفروا بالصبح، فإنه أعظم للأجر» . رواه سعيد بن أوس أبو زيد الأنصاري، عن ابن عون، عن ابن','50','109','0');
INSERT INTO [t1578] VALUES (112,'- «استعينوا على إنجاح الحوائج بكتمانها، فإن كل ذي نعمة محسود» . رواه طاهر بن الفضل الحلبي، عن','50','109','0');
INSERT INTO [t1578] VALUES (113,'ههنا تسكب العبرات» . رواه محمد بن عون الخراساني، عن نافع، عن ابن عمر. ومحمد هذا كان ممن','51','109','0');
INSERT INTO [t1578] VALUES (114,'- «استوصوا بالغوغاء، فإنهم يسدون البثوق، ويحفرون الخنادق، ويطفئون الحريق» . رواه محمد بن','52','109','0');
INSERT INTO [t1578] VALUES (115,'باب الشين بعد الألف','52','1','0');
INSERT INTO [t1578] VALUES (116,'- «اشربوا على الطعام تشبعوا» . رواه عبد الله بن ميمون القداح المكي، عن طلحة بن عمرو، عن عطاء،','52','115','0');
INSERT INTO [t1578] VALUES (117,'- «أشكم درد» رواه ذواد بن علبة، عن ليث، عن مجاهد، عن أبي هريرة، وذواد هذا من أهل الكوفة متروك','52','115','0');
INSERT INTO [t1578] VALUES (118,'باب الصاد بعد الألف','53','1','0');
INSERT INTO [t1578] VALUES (119,'زوجتك سيدا في الدنيا وأنت في الآخرة من الصالحين. . .» الحديث بطوله. رواه مخلد بن عمرو','53','118','0');
INSERT INTO [t1578] VALUES (120,': أصبت امرأتي وهي حائض، فأمره رسول الله صلى الله عليه وسلم أن يعتق نسمة ". رواه عبد الرحمن بن','53','118','0');
INSERT INTO [t1578] VALUES (121,'- «أصل كل داء البردة» . رواه تمام بن نجيح الملطي الأسدي، عن الحسن، عن أنس. وتمام هذا يروي','53','118','0');
INSERT INTO [t1578] VALUES (122,': «اصبروا على الصلوات الخمس، وصابروا على قتال عدوكم، ورابطوا في سبيل الله لعلكم تفلحون» . رواه','54','118','0');
INSERT INTO [t1578] VALUES (123,'باب الطاء بعد الألف','54','1','0');
INSERT INTO [t1578] VALUES (124,'- «اطلبوا الخير عند حسان الوجوه» . رواه الحكم بن عبد الله الأيلي، عن الزهري، عن سعيد بن','54','123','0');
INSERT INTO [t1578] VALUES (125,'- «اطلبوا الرزق في خبايا الأرض» . رواه هشام بن عبد الله بن عكرمة بن عبد الرحمن المخزومي، عن','55','123','0');
INSERT INTO [t1578] VALUES (126,'- «اطلبوا العلم ولو بالصين» . رواه أبو عاتكة طريف بن سلمان، عن أنس. وأبو عاتكة منكر الحديث','56','123','0');
INSERT INTO [t1578] VALUES (127,'- «اطلبوا الفضل من الرحماء، من عبادي تعيشوا في أكنافهم، فإن فيهم رحمتي، ولا تطلبوا من','56','123','0');
INSERT INTO [t1578] VALUES (128,'- «أطعموا نساءكم اللوز والرطب، فإن لم يكن الرطب فالتمر. . .» الحديث. رواه مسرور بن سعيد','57','123','0');
INSERT INTO [t1578] VALUES (129,'- «اطلع في القبور واعتبر بيوم النشور» . رواه محمد بن يونس الكديمي، عن مكي بن إبراهيم، عن جعفر','57','123','0');
INSERT INTO [t1578] VALUES (130,'باب العين بعد الألف','57','1','0');
INSERT INTO [t1578] VALUES (131,'- «اعملوا بالقرآن أحلوا حلاله. . .» الحديث. وفيه: ما تشابه عليكم منه فردوه إلى الله وإلى أولي','58','130','0');
INSERT INTO [t1578] VALUES (132,'- «اعتموا تزدادوا حلما» . رواه عبيد الله بن أبي حميد الهذلي، عن أبي المليح، عن ابن عباس. وعبيد','58','130','0');
INSERT INTO [t1578] VALUES (133,'باب الغين بعد الألف','59','1','0');
INSERT INTO [t1578] VALUES (134,'- «أغبوا العيادة وأربعوا إلا أن يكون مغلوبا» . رواه موسى بن محمد بن إبراهيم بن الحارث التيمي،','59','133','0');
INSERT INTO [t1578] VALUES (135,'- «اغتسلوا يوم الجمعة ولو كأسا بدينار» . رواه حفص بن عمر الأيلي، عن عبد الله بن المثنى، عن','59','133','0');
INSERT INTO [t1578] VALUES (136,'باب الفاء بعد الألف','59','1','0');
INSERT INTO [t1578] VALUES (137,'- «أفطر الحاجم والمحجوم» . رواه أحمد بن إسماعيل أبو حذافة، عن مالك، عن نافع، عن ابن عمر، عن','59','136','0');
INSERT INTO [t1578] VALUES (138,'- «أفطر عندكم الصائمون، وأكل طعامكم الأبرار. . . .» الحديث. رواه الخليل بن مرة، عن يحيى بن','60','136','0');
INSERT INTO [t1578] VALUES (139,'- «افتح له وبشره بالجنة. . .» الحديث. رواه بكر بن المختار بن فلفل، عن أبيه، عن أنس، وفيه ذكر','60','136','0');
INSERT INTO [t1578] VALUES (140,'باب القاف بعد الألف','61','1','0');
INSERT INTO [t1578] VALUES (141,'- " أقاتل بمن أقبل من قومي من أدبر منهم؟ قال: «نعم. . .» الحديث. رواه يحيى بن أبي حية، عن يحيى،','61','140','0');
INSERT INTO [t1578] VALUES (142,'- «أقم مكانك الذي أنت فيه. . .» . وفيه ذكر العباس لما أراد أن ينتقل. رواه إسماعيل بن قيس بن','61','140','0');
INSERT INTO [t1578] VALUES (143,'- «إقامة حد من حدود الله أحب إلى الله من أن ينزل غيث أربعين ليلة. . .» الحديث. رواه سعيد بن','62','140','0');
INSERT INTO [t1578] VALUES (144,'باب الكاف بعد الألف','62','1','0');
INSERT INTO [t1578] VALUES (145,'- «أكرموا عمتكم النخلة، فإنها خلقت من الطين الذي خلق منه آدم، وليس من الشجر شجر يلقح غيرها» .','62','144','0');
INSERT INTO [t1578] VALUES (146,'- «أكذب الناس الصباغون والصواغون» . رواه فرقد السبخي، عن يزيد بن عبد الله بن الشخير، عن أبي','64','144','0');
INSERT INTO [t1578] VALUES (147,'- «اكفلوا لي بست أكفل لكم بالجنة. . .» الحديث. رواه فضال بن جبير، عن أبي أمامة. وفضال هذا لم','65','144','0');
INSERT INTO [t1578] VALUES (148,'- «أكثر الحيض عشرة، وأقله ثلاث» . رواه الحسين بن علوان، عن هشام بن عروة، عن أبيه، عن عائشة رضي','65','144','0');
INSERT INTO [t1578] VALUES (149,'- «أكلت مع النبي صلى الله عليه وسلم لحم حبارى. . .» الحديث. رواه إبراهيم بن عمر بن سفينة، عن','65','144','0');
INSERT INTO [t1578] VALUES (150,'باب اللام بعد الألف','66','1','0');
INSERT INTO [t1578] VALUES (151,'- «الله في عون المسلم ما دام المسلم في عون أخيه، ومن أغاث ملهوفا غفر له اثنان وسبعون مغفرة. . .»','66','150','0');
INSERT INTO [t1578] VALUES (152,'- «اللهم بارك لأمتي في بكورها يوم خميسها» . رواه أحمد بن محمد بن الفضل القيسي، عن نصر بن علي،','66','150','0');
INSERT INTO [t1578] VALUES (153,'باب الميم بعد الألف','66','1','0');
INSERT INTO [t1578] VALUES (154,'- " أمرني جبريل فقال: يا محمد إذا توضأت فانضح ". رواه الحسن بن علي الهاشمي المدني، عن الأعرج،','67','153','0');
INSERT INTO [t1578] VALUES (155,'- «أمرني ربي عز وجل أن أقضي باليمين مع الشاهد» . رواه إبراهيم بن أبي حية المكي، عن جعفر بن','67','153','0');
INSERT INTO [t1578] VALUES (156,'- «أمتي خمس طبقات، كل طبقة أربعون عاما. . .» الحديث. رواه عباد بن عبد الصمد أبو معمر، عن أنس.','68','153','0');
INSERT INTO [t1578] VALUES (157,'أن نعرض أولادنا على حب علي بن أبي طالب» . رواه الحسن بن علي العدوي، عن أحمد بن عبدة، عن','68','153','0');
INSERT INTO [t1578] VALUES (158,'- «أمان أهل الأرض من الغرق القوس، وأمان أهل الأرض من الاختلاف والفتن الولاة من قريش، إذا','68','153','0');
INSERT INTO [t1578] VALUES (159,'- «أمارة المسلمين والصديقين والشهداء والصالحين البشاشة إذا تزاوروا والمصافحة والترحيب إذا','69','153','0');
INSERT INTO [t1578] VALUES (160,'- «أما يخشى الذي يرفع رأسه قبل الإمام. . .» الحديث. معروف رواه عبد الله بن عبد الرحمن الجزري،','69','153','0');
INSERT INTO [t1578] VALUES (161,'باب النون بعد الألف','70','1','0');
INSERT INTO [t1578] VALUES (162,'الله اتخذني خليلا كما اتخذ إبراهيم خليلا، فمنزلي ومنزل إبراهيم في الجنة تجاهين، والعباس','70','161','0');
INSERT INTO [t1578] VALUES (163,'الله إذا أطعم نبيا طعمة ثم قبضه كانت للذي يلي الأمر بعده» . رواه سيف بن مسكين السلمي','70','161','0');
INSERT INTO [t1578] VALUES (164,'الله تعالى إذا غضب انتفخ على العرش حتى يثقل على حملته» . رواه حماد بن سلمة، عن أيوب بن عبد','71','161','0');
INSERT INTO [t1578] VALUES (165,'الله تعالى لما خلق الجنة قال لها: تزيني، فتزينت، فقال لها: تكلمي، فقالت: طوبى لمن رضيت','71','161','0');
INSERT INTO [t1578] VALUES (166,'الله اختار أصحابي على جميع العالمين. . .» الحديث وفيه ذكر أبي بكر وعمر. رواه عبد الله كاتب','72','161','0');
INSERT INTO [t1578] VALUES (167,'الله إذا أحب عبدا ابتلاه ليسمع تضرعه» . رواه يحيى بن عبيد الله بن الموهب، عن أبيه، عن أبي','73','161','0');
INSERT INTO [t1578] VALUES (168,'الله طيب يحب الطيب، نظيف يحب النظافة، كريم يحب الكرم» . رواه خالد بن إلياس العدوي، عن عامر','73','161','0');
INSERT INTO [t1578] VALUES (169,'الله اتخذ لي أصحابا وأصهارا. . .» الحديث. رواه بشر بن عبيد الله، عن أنس. قال ابن حبان:','73','161','0');
INSERT INTO [t1578] VALUES (170,'الله كتب الغيرة على النساء، وكتب الجهاد على الرجال. . .» الحديث. رواه كامل بن العلاء أبو','74','161','0');
INSERT INTO [t1578] VALUES (171,'الله جعل قرة عيني في الصلاة، وحبب إلي الطيب. . .» الحديث. رواه يوسف بن عطية الصفار السعدي،','75','161','0');
INSERT INTO [t1578] VALUES (172,'الله يحب إغاثة اللهفان» . رواه محمد بن يونس الكديمي، عن أزهر السمان، عن ابن عون، عن محمد بن','75','161','0');
INSERT INTO [t1578] VALUES (173,'الله أوحى إلى أن من سلك مسلكا في طلب العلم سهل الله له طريقا إلى الجنة» . رواه محمد بن عبد','75','161','0');
INSERT INTO [t1578] VALUES (174,'الله لعن الخمر وعاصرها ومعتصرها والجالب والمجلوب إليه والبائع والمشتري والساقي والشارب،','76','161','0');
INSERT INTO [t1578] VALUES (175,'الله قرأ: طه ويس قبل أن يخلق الخلق. . . ". رواه إبراهيم بن المهاجر المدني، عن عمر بن حفص','76','161','0');
INSERT INTO [t1578] VALUES (176,'الله يباهي بالطائفين» . رواه عائذ بن نسير، عن عطاء، عن عائشة رضي الله تعالى عنها. وعائذ','77','161','0');
INSERT INTO [t1578] VALUES (177,'باب','77','1','0');
INSERT INTO [t1578] VALUES (178,'لله عز وجل في كل يوم وليلة عشرين ومائة رحمة ينزل على البيت» . رواه سعيد بن سالم القداح، عن','77','177','0');
INSERT INTO [t1578] VALUES (179,'لله من خلقه وجوها خلقهم لحوائج الناس يرغبون في الأجر، ويعدون الجود مجدا، والله يحب مكارم','78','177','0');
INSERT INTO [t1578] VALUES (180,'لله في كل يوم وليلة مائة ألف عتيق» . رواه الأزور بن غالب، عن سليمان التيمي، وثابت، عن أنس.','78','177','0');
INSERT INTO [t1578] VALUES (181,'لله ديكا عنقه منطو تحت العرش ورجلاه في النجوم. . .» الحديث. رواه علي بن أبي علي اللهبي، عن','79','177','0');
INSERT INTO [t1578] VALUES (182,'لله سرايا من الملائكة على رفف في مجالس الذكر. . .» الحديث. رواه عمر بن عبد الله مولى غفرة،','79','177','0');
INSERT INTO [t1578] VALUES (183,'باب','79','1','0');
INSERT INTO [t1578] VALUES (184,'توضأ مرة مرة، وقال: «هذا صفة الوضوء، لا يقبل الله الصلاة إلا به» ثم توضأ مرتين. . . " الحديث.','80','183','0');
INSERT INTO [t1578] VALUES (185,'كان يقبلها ويمص لسانها. . .» الحديث. رواه محمد بن دينار الطاحي، عن سعد بن أوس، عن مصدع أبي','80','183','0');
INSERT INTO [t1578] VALUES (186,'صلى على أهل بدر تسع تكبيرات وعلى بني هاشم سبع تكبيرات» . رواه نافع أبو هرمز، عن أنس. ونافع','81','183','0');
INSERT INTO [t1578] VALUES (187,'لم يكن يعود مريضا إلا بعد ثلاث» . رواه مسلمة بن علي الخشني، عن ابن جريج، عن حميد، عن أنس.','81','183','0');
INSERT INTO [t1578] VALUES (188,': {فشاربون شرب الهيم} [الواقعة: ٥٥] في أشياء من هذا النوع ". رواه سلام بن سليمان، عن أبي عمرو','82','183','0');
INSERT INTO [t1578] VALUES (189,'بعث رجلا إلى الجن فقال: «سر ثلاثا ملسا حتى إذا لم تر شمسا، فأعلف بعيرا وأشبع نفسا. . .»','82','183','0');
INSERT INTO [t1578] VALUES (190,'استقبله نساء وهن جائين من عرس لهم من الأنصار، فسلم عليهن، فقال: «والله إني لأحبكن» . رواه','83','183','0');
INSERT INTO [t1578] VALUES (191,'قضى باليمين مع الشاهد» . رواه خالد بن عثمان العثماني، عن مالك، عن جعفر بن محمد، عن أبيه، عن','83','183','0');
INSERT INTO [t1578] VALUES (192,'قضى في الماشية المسروحة يسرق منها السارق قبل أن يبلغ المراح. . .» الحديث. رواه سليمان بن','84','183','0');
INSERT INTO [t1578] VALUES (193,'كان يرفع يديه في كل خفض ورفع. . .» الحديث. رواه رفدة بن قضاعة الغساني، عن الأوزاعي، عن','84','183','0');
INSERT INTO [t1578] VALUES (194,'أسهم للفارس سهمين وللراجل سهما» . رواه عبد الله بن عمر العمري، عن نافع، عن ابن عمر. عبد','85','183','0');
INSERT INTO [t1578] VALUES (195,'إذا حضر رمضان أطلق كل أسير وأعطى كل سائل» . رواه أبو بكر الهذلي واسمه: سلمى بن عبد الله بن','85','183','0');
INSERT INTO [t1578] VALUES (196,'نهى أن يقد السير بين إصبعين» . رواه قريش بن أنس الأنصاري، عن أشعث، عن الحسن، عن أنس. وقريش','85','183','0');
INSERT INTO [t1578] VALUES (197,'زار قبر أمه في ألف مقنع فما رئي باكيا أكثر من يومئذ» . رواه محمد بن يونس الكديمي، عن قبيصة','86','183','0');
INSERT INTO [t1578] VALUES (198,'كان يصلي في موضع يبول فيه الحسن والحسين، فقالت له عائشة. . .» الحديث. رواه بزيغ بن حسان،','86','183','0');
INSERT INTO [t1578] VALUES (199,'كان له مكحلة يكتحل منها كل ليلة ثلاثا في هذه، وثلاثا في هذه» . رواه عباد بن منصور التاجي،','86','183','0');
INSERT INTO [t1578] VALUES (200,'مر على صبيان فسلم عليهم» . رواه محمد بن سليمان الجوهري البصري، عن حفص بن عمر الحوضي، عن','87','183','0');
INSERT INTO [t1578] VALUES (201,'صلى على قتلى أحد وكبر عليهم أربع تكبيرات» . رواه صبيح بن سعيد النجاشي، عن عائشة رضي الله','87','183','0');
INSERT INTO [t1578] VALUES (202,'اجتلى عائشة رضي الله تعالى عنها عند أبويها قبل أن يبني بها» . رواه القاسم بن عبد الله بن','87','183','0');
INSERT INTO [t1578] VALUES (203,'مر بجدار مائل. . .» الحديث. رواه إبراهيم بن الفضل المخرومي، عن سعيد المقبري، عن أبي هريرة.','88','183','0');
INSERT INTO [t1578] VALUES (204,': «وكيف لا أهتم ورفع أحدكم بين أطرافه» . رواه الضحاك بن زيد الأهوازي، عن إسماعيل بن أبي خالد،','88','183','0');
INSERT INTO [t1578] VALUES (205,': من أحب أن يتقوى على الصوم فليتسحر وليمس طيبا ولا يفطر على ماء ". رواه شعيب بن مبشر الكلبي،','89','183','0');
INSERT INTO [t1578] VALUES (206,'، فوجد الناس قد صلوا، فذهب إلى منزله، فجمع أهله فصلى بهم» . رواه معاوية بن يحيى الصدفي، عن','89','183','0');
INSERT INTO [t1578] VALUES (207,'خرج من غائط أو بول، فسلم عليه رجل فلم يرد عليه» ، وفيه ذكر التيمم. رواه محمد بن ثابت','90','183','0');
INSERT INTO [t1578] VALUES (208,'كان يدهن بالزيت غير المقتت عند الإحرام» . رواه فرقد بن يعقوب السبخي، عن سعيد بن جبير، عن','90','183','0');
INSERT INTO [t1578] VALUES (209,'أتي بنبيذ الجر ينش. . .» الحديث. رواه إبراهيم بن إسحاق بن إبراهيم البغدادي، عن بندار، عن','90','183','0');
INSERT INTO [t1578] VALUES (210,'دخل غيضة، فاجتنى منه سواكين. . .» الحديث في حق الصاحب. رواه أحمد بن محمد بن عمرو بن يونس','91','183','0');
INSERT INTO [t1578] VALUES (211,'نهى عن كسر سكة المسلمين بينهم إلا من بأس» . رواه محمد بن فضاء الجهضمي، عن أبيه، عن علقمة بن','91','183','0');
INSERT INTO [t1578] VALUES (212,'قوما يتعاطون سيفا بينهم في المسجد، فقال: «لعن الله من فعل، ألم أنهه عن هذا؟» . رواه محمد بن','92','183','0');
INSERT INTO [t1578] VALUES (213,'قوم يبنون حائطا فقال: «نيك نيك تكنيت» . رواه ذواد بن علبة، عن ليث، عن مجاهد، عن أبي هريرة.','92','183','0');
INSERT INTO [t1578] VALUES (214,': «ادعوا لي أخي» فدعي له أبو بكر، فأعرض عنه، ثم عمر ثم عثمان ثم علي، فستره بثوبه وأكب عليه،','92','183','0');
INSERT INTO [t1578] VALUES (215,'سقط عن فرس فجحش شقه الأيمن» . رواه سويد بن عبد العزيز الدمشقي، عن مالك، عن الزهري، عن','93','183','0');
INSERT INTO [t1578] VALUES (216,'بقوم يلعبون بالشطرنج فقال: «ما هذه الكوبة؟ . . .» الحديث. وفيه: لعن الله من لعب بها ". رواه','93','183','0');
INSERT INTO [t1578] VALUES (217,'توضأ فخلل لحيته» . رواه عبد الله بن حسين بن جابر البغدادي، عن محمد بن المبارك الصوري، عن','94','183','0');
INSERT INTO [t1578] VALUES (218,'دخل مكة وعليه عمامة سوداء» . رواه أحمد بن طاهر بن حرملة بن يحيى المصري، عن جده، عن عبد','95','183','0');
INSERT INTO [t1578] VALUES (219,'صلى العصر خمسا، فسجد سجدتي السهو وهو جالس» . رواه سعيد بن بشير، عن منصور، عن الحكم بن','95','183','0');
INSERT INTO [t1578] VALUES (220,'يقبل نحر فاطمة. . . الحديث. وفيه «لما أسري بي إلى السماء، أمر جبريل فأدخلني الجنة، فأوقفني','95','183','0');
INSERT INTO [t1578] VALUES (221,'غزى رجلا مسلما برجل ذمي. . .» الحديث. رواه إسماعيل بن يحيى بن عبد الله التيمي، عن ابن أبي','96','183','0');
INSERT INTO [t1578] VALUES (222,'إذا اشتكى تقمح كف شونيز وشرب عليه ماء وعسلا» . رواه سعيد بن ميسرة البكري، عن أنس. وسعيد','96','183','0');
INSERT INTO [t1578] VALUES (223,'عق عن نفسه بعدما بعثه الله عز وجل» . رواه عبد الله بن محرر، عن قتادة، عن أنس. وعبد الله','96','183','0');
INSERT INTO [t1578] VALUES (224,'كان يمس لحيته في الصلاة» . رواه المنذر بن زياد الطائي، عن الوليد بن سريع، عن ابن أبي أوفى.','97','183','0');
INSERT INTO [t1578] VALUES (225,'دخل قبرا ليلا. . .» الحديث. رواه الحجاج بن أرطاة، عن عطاء، عن ابن عباس. والحجاج متروك','97','183','0');
INSERT INTO [t1578] VALUES (226,': «هذا وصيي وموضع سري وخير من أترك بعدي» . رواه خالد بن عبيد العتكي البصري أبو عصام، عن أنس.','97','183','0');
INSERT INTO [t1578] VALUES (227,': {والعين بالعين} [المائدة: ٤٥] ". رواه طاهر بن الفضل الحلبي، عن سفيان بن عيينة، عن الزهري،','98','183','0');
INSERT INTO [t1578] VALUES (228,'لعن النائحة والمستمعة والحالقة والصالقة. . .» الحديث. رواه عفير بن معدان اليحصبي، عن عطاء،','98','183','0');
INSERT INTO [t1578] VALUES (229,': «تزوجت؟» قال: لا. وفيه ذكر قل هو الله أحد وقل يأيها الكافرون وإذا زلزلت ". رواه سلمة بن','99','183','0');
INSERT INTO [t1578] VALUES (230,'كحل عين علي ببزاقه» . رواه معلى بن عرفان بن سلمة ابن أخي شقيق ابن سلمة، عن عمه أبي وائل، عن','99','183','0');
INSERT INTO [t1578] VALUES (231,'نهى عن ذبائح الزنج» . رواه عبد الله بن أذينة، عن ثور بن يزيد، عن الزهري، عن حميد بن عبد','99','183','0');
INSERT INTO [t1578] VALUES (232,'نهى عن ذبائح الجن» . والقول فيه كالقول في الذي قبله.','100','183','0');
INSERT INTO [t1578] VALUES (233,'أعطى معاوية سهما وقال: «هاك حتى تلقاني به في الجنة» . رواه القاسم بن بهرام أبو حمدان، عن أبي','100','183','0');
INSERT INTO [t1578] VALUES (234,'نهى أن يبال في الماء الراكد ثم يتوضأ منه» . رواه السري بن عاصم، عن ابن علية، عن يحيى بن','100','183','0');
INSERT INTO [t1578] VALUES (235,'أفرد الحج» . رواه عبد الله بن عيسى الفروي أبو علقمة، عن ابن نافع، عن مالك، عن نافع، عن ابن','101','183','0');
INSERT INTO [t1578] VALUES (236,'احتجم وهو صائم» . رواه الأحوص بن حكيم، عن أبي الزاهرية، عن جبير بن نفير، عن معاذ. والأحوص','101','183','0');
INSERT INTO [t1578] VALUES (237,'دخل مكة وعلى رأسه مغفر» . رواه عبد السلام بن أبي فروة النصيبي، عن عبيد الله بن موسى، عن','101','183','0');
INSERT INTO [t1578] VALUES (238,'الشيطان حين أخرج من الجنة رفع يديه فوق رأسه» . رواه سعيد بن ميسرة، عن أنس. وسعيد هذا متروك','102','183','0');
INSERT INTO [t1578] VALUES (239,'صلى الفجر فقرأ فيه: إذا زلزلت مرتين ". رواه عبيد بن القاسم، عن هشام، عن أبيه، عن عائشة.','102','183','0');
INSERT INTO [t1578] VALUES (240,'أقاد من خدش» . رواه العطاف بن خالد بن عبد الله القرشي أبو صفوان، عن نافع، عن ابن عمر.','102','183','0');
INSERT INTO [t1578] VALUES (241,'أتى رجلا يعوده على أتان ليس عليها سرج ولا لجام، فسلم ثلاثا كل ذلك يرد عليه الرجل ولا','103','183','0');
INSERT INTO [t1578] VALUES (242,'نهى عن الحجامة يوم السبت ويوم الأربعاء» . رواه عباد بن راشد التميمي، عن الحسن، عن سبعة من','103','183','0');
INSERT INTO [t1578] VALUES (243,'إذا دخل المسجد يوم الجمعة سلم على من عند المنبر، فإذا صعد المنبر توجه إلى الناس فسلم','103','183','0');
INSERT INTO [t1578] VALUES (244,'يختم وتره بهذا الدعاء. . .» الحديث في الدعاء بطوله. رواه الحسن بن عمار، عن داود بن علي، عن','104','183','0');
INSERT INTO [t1578] VALUES (245,'نهى عن المراجيح وأمر بقطعها» . رواه عمرو بن محمد الأعشم وإسماعيل بن عياش، عن يحيى بن سعيد','104','183','0');
INSERT INTO [t1578] VALUES (246,': «من يبغض عثمان أبغضه الله» . رواه محمد بن زياد الجزري، عن ابن عجلان، عن أبي الزبير، عن جابر.','105','183','0');
INSERT INTO [t1578] VALUES (247,'لم يأكل على خوان حتى مات، ولم يأكل خبزا مرققا» . رواه الخليل بن سلم أبو مسلم البزاز، عن عبد','105','183','0');
INSERT INTO [t1578] VALUES (248,'إذا مشى لا يلتفت. . .» الحديث. وفيه لأنهم كانوا يمزحون ويضحكون وقد أمنوا التفاته. رواه عبد','105','183','0');
INSERT INTO [t1578] VALUES (249,'إذا ضحى اشترى كبشين سمينين أقرنين أملحين، فإذا صلى خطب. . . .» الحديث. رواه عبد الله بن','106','183','0');
INSERT INTO [t1578] VALUES (250,'لمس صنما فتوضأ» . رواه صالح بن حيان القرشي الكوفي، عن بن بريدة، عن أبيه. وصالح هذا','106','183','0');
INSERT INTO [t1578] VALUES (251,'أخي ووزيري وخليفتي في أهلي وخير من أترك بعدي، يقضي ديني وينجز موعدي علي بن أبي طالب» . رواه','107','183','0');
INSERT INTO [t1578] VALUES (252,'أبا بكر وعمر من الإسلام بمنزلة السمع والبصر من الإنسان» . رواه الوليد بن الفضل العنزي، عن','107','183','0');
INSERT INTO [t1578] VALUES (253,'أهل الجنة ليرون أهل عليين كما يرون الكوكب الدري في السماء، وإن أبا بكر وعمر منهم وأنعما» .','108','183','0');
INSERT INTO [t1578] VALUES (254,'أهل السماء لا يسمعون من أهل الأرض إلا الأذان» . رواه عبيد الله بن الوليد الوصافي، عن محارب','108','183','0');
INSERT INTO [t1578] VALUES (255,'أتحبان أن يسوركما الله عز وجل سوارين من نار؟ " قالتا: لا، قال: «فأديا زكاته» . رواه عمرو بن','109','183','0');
INSERT INTO [t1578] VALUES (256,'أخوف ما أخاف على أمتي من بعدي لعمل قوم لوط» . رواه عبد الله بن محمد بن عقيل، عن جابر نفسه.','109','183','0');
INSERT INTO [t1578] VALUES (257,'أعجل الطاعات ثوابا؛ صلة الرحم. . .» الحديث. وفيه: «وإن أعجل المعصية عقوبة؛ البغي والخيانة','109','183','0');
INSERT INTO [t1578] VALUES (258,'ما من الأنصار رجل ولا امرأة إلا وقد أتحفك بشيء غيري، وليس لي إلا ولدي هذا، فأحب أن تقبله','110','183','0');
INSERT INTO [t1578] VALUES (259,'- «إن أحببت أن تكون من القانتين فلا تعرفن من عن يمينك وعن شمالك في الصلاة المكتوبة» . رواه','110','183','0');
INSERT INTO [t1578] VALUES (260,'علمني دعاء أدعو به، ويرد الله علي بصري. . " الحديث. رواه عون بن عمارة، عن روح بن القاسم، عن','110','183','0');
INSERT INTO [t1578] VALUES (261,'بقرة انقلبت على خمر فشربت، فخافوا عليها، فأتوا النبي صلى الله عليه وسلم فقال: «لا بأس بها','111','183','0');
INSERT INTO [t1578] VALUES (262,'- «إن تؤمروا أبا بكر تجدوه أمينا مسلما. . .» الحديث. وفيه ذكر عمر وعلي. رواه فضيل بن مرزوق،','111','183','0');
INSERT INTO [t1578] VALUES (263,'جبريل أتى النبي صلى الله عليه وسلم بقطف. . .» الحديث. رواه حفص بن عمر بن أبي العطاف','112','183','0');
INSERT INTO [t1578] VALUES (264,'الحكمة تزيد الشرف شرفا وترفع العبد المملوك حتى تجلسه مجالس الملوك» . رواه صالح بن بشر','112','183','0');
INSERT INTO [t1578] VALUES (265,'خيار الناس أحسنهم قضاء» . رواه يحيى بن محمد بن قيس أبو زكير، عن زيد بن أسلم، عن أبيه، عن','112','183','0');
INSERT INTO [t1578] VALUES (266,'خرافة كان رجلا من بني عذرة. . .» الحديث بطوله. رواه عثمان بن معاوية، عن ثابت، عن أنس.','113','183','0');
INSERT INTO [t1578] VALUES (267,'ربكم عز وجل يقول: أنا ملك الملوك، قلوب الملوك بيدي ". رواه وهب بن راشد، عن مالك بن دينار،','113','183','0');
INSERT INTO [t1578] VALUES (268,'فشكا إليه فقرا أو حاجة، فقال: «أين أنت من صلاة الملائكة. . .» الحديث. رواه إسحاق بن إبراهيم','113','183','0');
INSERT INTO [t1578] VALUES (269,': «آمروا النساء في أنفسهن» . رواه يحيى بن أبي أنيسة، عن نافع، عن ابن عمر. ويحيى هذا كذبه أخوه','114','183','0');
INSERT INTO [t1578] VALUES (270,'رجلا أتى النبي صلى الله عليه وسلم مع امرأته وابن له. رواه إسماعيل بن عباد المزني، عن سعيد','114','183','0');
INSERT INTO [t1578] VALUES (271,'إنك تبشر الرجل بكل خير. . . " الحديث. رواه عمرو بن سعد الخولاني، عن أنس بن مالك. وعمرو هذا','115','183','0');
INSERT INTO [t1578] VALUES (272,'- «إن صليت الضحى ركعتين لم تكتب من الغافلين. . .» الحديث. رواه حسين بن عطاء المدني، عن زيد بن','115','183','0');
INSERT INTO [t1578] VALUES (273,'عيسى ابن مريم أسلمته أمه الكتاب. . .» الحديث. وفيه تفسير {بسم الله الرحمن الرحيم}','116','183','0');
INSERT INTO [t1578] VALUES (274,'أشركنا في صالح دعائك» . رواه عاصم بن عبيد الله العمري، عن سالم، عن ابن عمر. وعاصم هذا ضعيف،','117','183','0');
INSERT INTO [t1578] VALUES (275,'أهدى إلى رسول الله سفرجلة.','117','183','0');
INSERT INTO [t1578] VALUES (276,'يتخذ ثنية من ذهب. رواه أبان بن سفيان المقدسي، عن الفضيل بن عياض، عن هشام بن عروة، عن','117','183','0');
INSERT INTO [t1578] VALUES (277,'عبدا سينادي في جهنم يا حنان يا منان. . .» الحديث بطوله. رواه أبو ظلال القسملي هلال بن أبي','117','183','0');
INSERT INTO [t1578] VALUES (278,'كنيف أن تبنيها بمنى، فلم يأذن لها» . رواه إبراهيم بن أبي حية، عن هشام بن عروة، عن أبيه، عن','118','183','0');
INSERT INTO [t1578] VALUES (279,'فاطمة أحصنت فرجها، فحرم الله ذريتها على النار» . رواه عمر بن الغياث وقيل: عمرو، من أهل','118','183','0');
INSERT INTO [t1578] VALUES (280,'في الجنة نهرا يقال له رجب، أشد بياضا من اللبن وأحلى من العسل. . .» الحديث. رواه موسى بن','119','183','0');
INSERT INTO [t1578] VALUES (281,'في الجنة غرفا إذ كان ساكنها فيها لم يخف عليه ما خلفها. . .» الحديث بطوله. رواه حفص بن عمر','119','183','0');
INSERT INTO [t1578] VALUES (282,'في جهنم واديا، وفي الوادي بيت يقال له: هبهب. . . " الحديث بطوله. رواه الأزهر بن سنان، عن','119','183','0');
INSERT INTO [t1578] VALUES (283,'لكل أمة مفتاحا ومفتاح الجنة حب المساكين» . رواه أحمد بن داود بن عبد الغفار، عن أبي مصعب، عن','120','183','0');
INSERT INTO [t1578] VALUES (284,'للقلب فرحة عند أكل اللحم. . .» الحديث. رواه أحمد بن عيسى الخشاب التنيسي، عن مصعب بن ماهان،','120','183','0');
INSERT INTO [t1578] VALUES (285,'لجهنم سبعة أبواب، باب منها لمن سل سيفه على أمتي. . .» الحديث. رواه جنيد بن العلاء بن أبي','121','183','0');
INSERT INTO [t1578] VALUES (286,'لي جارين هما في الجوار سواء، أحدهما بابه قبالة بابي والآخر عن يمين بابي» الحديث. رواه عامر','121','183','0');
INSERT INTO [t1578] VALUES (287,'من أبغض الحلال إلى الله عز وجل الطلاق» . رواه عبيد الله بن الوليد الوصافي، عن محارب بن','121','183','0');
INSERT INTO [t1578] VALUES (288,'من السنة أن تشيع مع الضيف إلى باب الدار» . رواه سلم بن سالم البلخي، عن ابن جريج، عن عطاء،','122','183','0');
INSERT INTO [t1578] VALUES (289,'من السرف أن تأكل ما اشتهيت» . رواه نوح بن ذكوان، عن الحسن، عن أنس. ونوح منكر','122','183','0');
INSERT INTO [t1578] VALUES (290,'من حق جلال الله على العبد إكرام ذي الشيبة المسلم ورعاية القرآن لمن استرعاه إياه، وطاعة','123','183','0');
INSERT INTO [t1578] VALUES (291,'مكارم الأخلاق من أعمال أهل الجنة» . رواه سليمان بن يسار، عن سفيان بن عيينة، عن حميد، عن','123','183','0');
INSERT INTO [t1578] VALUES (292,'معاوية بن معاوية الليثي مات بالمدينة، فبعث الله إليه سبعين ألف ملك يصلون عليه. . .»','124','183','0');
INSERT INTO [t1578] VALUES (293,'الإيمان أيزيد وينقص. . الحديث. رواه الحكم بن عبد الله أبو مطيع البلخي، عن حماد بن سلمة،','124','183','0');
INSERT INTO [t1578] VALUES (294,'هذا القرآن مأدبة الله عز وجل، فتعلموا مأدبة الله عز وجل» . رواه إبراهيم بن مسلم الهجري أبو','125','183','0');
INSERT INTO [t1578] VALUES (295,'هذه الحشوش محتضرة. . .» الحديث. رواه أحمد بن العباس الهاشمي البصري، عن يحيى بن حبيب، عن','125','183','0');
INSERT INTO [t1578] VALUES (296,'- {إن الإنسان لربه لكنود} [العاديات: ٦] ، الكنود: الذي يأكل وحده ويمنع رفده. . . " الحديث.','126','183','0');
INSERT INTO [t1578] VALUES (297,'الملائكة حول العرش تتكلم بالفارسية الدرية. . .» الحديث. رواه الحسن بن دينار، عن جعفر بن','126','183','0');
INSERT INTO [t1578] VALUES (298,'المؤمن الذي يعمل الطاعات ليحفظه الله عز وجل في سبع قرون من ذريته» . رواه محمد بن عبد الرحمن','127','183','0');
INSERT INTO [t1578] VALUES (299,'الأذان سمح سهل. . .» الحديث. رواه إسحاق بن أبي يحيى الكعبي، عن ابن جريج، عن عطاء، عن ابن','127','183','0');
INSERT INTO [t1578] VALUES (300,'العبد إذا كذب تباعد عنه الملك ميلا من نتن ما جاء به ". رواه عبد العزيز بن أبي رواد، عن','128','183','0');
INSERT INTO [t1578] VALUES (301,'الخلق الحسن طوق من رضوان الله في عين صاحبه. . .» الحديث. رواه عبد الرحمن بن محمد بن الحسن','128','183','0');
INSERT INTO [t1578] VALUES (302,'المرض ليتبع الذنوب في المفاصل. . .» الحديث. رواه إسحاق بن بشر الكاهلي، عن الثوري، عن هشام،','129','183','0');
INSERT INTO [t1578] VALUES (303,'العين لتدخل الجمل القدر والرجل القبر» . رواه علي بن أبي علي اللهبي، عن محمد بن المنكدر، عن','129','183','0');
INSERT INTO [t1578] VALUES (304,'الرجل ليكون من أهل الصلاة والزكاة والصوم والعمرة والجهاد. . .» الحديث. وفيه: «وما يجزى يوم','130','183','0');
INSERT INTO [t1578] VALUES (305,'الأعراب يأتوننا باللحمان، لا ندري أذكر اسم الله عليها أو لا؟ ، رواه إبراهيم الخوزي، عن','131','183','0');
INSERT INTO [t1578] VALUES (306,'- «أنا مدينة العلم وعلي بابها، فمن أراد المدينة فليأتها من قبل الباب» . وفي رواية: «أنا دار','131','183','0');
INSERT INTO [t1578] VALUES (307,'- «أنا زعيم بقصر في أعلى الجنة وقصر في وسط الجنة وقصر في ربض الجنة لمن ترك المراء، وإن كان','132','183','0');
INSERT INTO [t1578] VALUES (308,'- «إني لأستحيي من عبدي وأمتي، يشيب رأس عبدي وأمتي في الإسلام. . .» الحديث. رواه أيوب بن','133','183','0');
INSERT INTO [t1578] VALUES (309,'- «أنزل الله عز وجل من الجنة إلى الأرض خمسة أنهار سيحون وجيحون وهو نهر بلخ، والدجلة والفرات','133','183','0');
INSERT INTO [t1578] VALUES (310,'مثلي ومثل الدنيا كراكب. . .» الحديث. رواه الحسن بن الحسين الكوفي، عن جرير بن عبد الحميد،','134','183','0');
INSERT INTO [t1578] VALUES (311,'أتقبل الصلاة لمن تواضع لعظمتي وقطع نهاره بذكري. .» الحديث. رواه عبد الله بن واقد الحراني','135','183','0');
INSERT INTO [t1578] VALUES (312,'سمي الدرهم لأنه دار هم، وإنما سمي الدينار لأنه دار نار. . .» الحديث. رواه عبد الله بن أبي','135','183','0');
INSERT INTO [t1578] VALUES (313,'مثلك يا علي في هذه الأمة كمثل عيسى ابن مريم أحبه قوم فأفرطوا فيه، وأبغضه قوم فأفرطوا فيه. . .»','136','183','0');
INSERT INTO [t1578] VALUES (314,'لم يعط العباد شيئا، خيرا من العافية» . رواه عبد الله بن الحسين بن جابر البغدادي، عن محمد بن','136','183','0');
INSERT INTO [t1578] VALUES (315,'سيخرج في أمتك رجل يشفعه الله في عدد ربيعة ومضر. . .» الحديث. وفيه ذكر أويس. رواه محمد بن','136','183','0');
INSERT INTO [t1578] VALUES (316,'- «أنكحوا من فتيانكم أصاغر النساء. . .» الحديث. رواه إبراهيم بن البراء، عن حماد بن سلمة، عن','137','183','0');
INSERT INTO [t1578] VALUES (317,'فرد نكاحها. رواه زيد بن حبان المدني، عن أيوب، عن عكرمة، عن ابن عباس. وزيد هذا أنكر عليه','137','183','0');
INSERT INTO [t1578] VALUES (318,'- «انصر أخاك ظالما أو مظلوما. . .» الحديث. رواه طاهر بن الفضل الحلبي، عن سفيان بن عيينة، عن','137','183','0');
INSERT INTO [t1578] VALUES (319,'باب الواو بعد الألف','138','1','0');
INSERT INTO [t1578] VALUES (320,'- «أول الآيات طلوع الشمس من مغربها» . رواه فضال بن جبير، عن أبي أمامة، عن النبي صلى الله عليه','138','319','0');
INSERT INTO [t1578] VALUES (321,'- «أول رحمة ترفع من الأرض الطاعون، وأول نعمة ترفع من الأرض العسل» . رواه علي بن عروة، عن عبد','138','319','0');
INSERT INTO [t1578] VALUES (322,'- «أول ما يجازى به المؤمن يوم القيامة أن يغفر لجميع من يشيع جنازته» . رواه مروان بن سالم','139','319','0');
INSERT INTO [t1578] VALUES (323,'- «أول الوقت رضوان الله، وآخره عفو الله» . رواه يعقوب بن الوليد المدني، عن عبيد الله بن عمر،','139','319','0');
INSERT INTO [t1578] VALUES (324,'باب الهاء بعد الألف','140','1','0');
INSERT INTO [t1578] VALUES (325,'، فنعم الطعام الزبيب، يشد العصب، ويذهب الوصب، ويطفئ الغضب، ويطيب النكهة، ويذهب بالبلغم،','140','324','0');
INSERT INTO [t1578] VALUES (326,'- أهدي لرسول الله صلى الله عليه وسلم ثلاث طوائر. . . " الحديث. رواه أبو ظلال القسملي واسمه','140','324','0');
INSERT INTO [t1578] VALUES (327,'- أهدي إلى رسول الله صلى الله عليه وسلم سفرجلة. . . وفيه ذكر معاوية. رواه إبراهيم بن زكريا','141','324','0');
INSERT INTO [t1578] VALUES (328,'باب لام ألف بعد الألف','141','1','0');
INSERT INTO [t1578] VALUES (329,'- «ألا أخبركم بأجود الأجودين؟» . رواه أيوب بن ذكوان، عن الحسن، عن أنس. وأيوب','142','328','0');
INSERT INTO [t1578] VALUES (330,'- «ألا أخبركم بأشقى الأشقياء؟ من جمع الله عليه عذاب الآخرة وفقر الدنيا» . رواه أحمد بن إبراهيم','142','328','0');
INSERT INTO [t1578] VALUES (331,'- " ألا أخبركم بشيء أمر به نوح ابنه؟ إن نوحا قال لابنه: يا بني آمرك بأمرين وأنهاك عن أمرين. . . "','142','328','0');
INSERT INTO [t1578] VALUES (332,'- «ألا أنبئكم بأفضل الملائكة؟ أفضل الملائكة جبريل. . .» الحديث بطوله. رواه نافع أبو هرمز، عن','143','328','0');
INSERT INTO [t1578] VALUES (333,'- «ألا لا يصلين أحد إلى أحد ولا قبر» . رواه رشدين بن كريب، عن أبيه، عن ابن عباس. ورشدين','143','328','0');
INSERT INTO [t1578] VALUES (334,'- «ألا إن بني آدم خلقوا على طبقات شتى، منهم من يولد مؤمنا ويحيى مؤمنا. . .» الحديث. رواه علي','143','328','0');
INSERT INTO [t1578] VALUES (335,'شاهد الزور مع العشار في النار» . رواه محمد بن حذيفة الأسدي، عن سفيان بن عيينة، عن زيادة بن','144','328','0');
INSERT INTO [t1578] VALUES (336,'باب الياء بعد الألف','144','1','0');
INSERT INTO [t1578] VALUES (337,'كان الحق فيها على غيرنا وجب. . .» الحديث إلى قوله: «ولم يعدها إلى بدعة» . رواه أبان بن أبي','144','336','0');
INSERT INTO [t1578] VALUES (338,'ربكم قد تطاول عليكم في يومكم هذا، فوهب مسيئكم لمحسنكم. . .» الحديث بطوله. رواه يحيى بن','145','336','0');
INSERT INTO [t1578] VALUES (339,'الله عز وجل قد فرض عليكم الجمعة في ساعتك هذه. . .» الحديث. رواه خالد بن عبد الدائم المصري،','145','336','0');
INSERT INTO [t1578] VALUES (340,'- أي الناس أعلم؟ قال: «من يجمع علم الناس إلى علمه، وكل صاحب علم غرثان» . رواه مسعدة بن اليسع،','146','336','0');
INSERT INTO [t1578] VALUES (341,'- أي الناس أجوع؟ قال: «الذي لا يشبع من العلم» . رواه محمد بن عبد الرحمن بن البيلماني، عن أبيه،','146','336','0');
INSERT INTO [t1578] VALUES (342,'- أي المسلمين أفضل؟ قال: «أحسنهم خلقا» قال: أي المؤمنين أكيس؟ قال: «أكثرهم للموت ذكرا» . . . "','146','336','0');
INSERT INTO [t1578] VALUES (343,'- أي الخلق أول دخولا الجنة؟ قال: «الأنبياء. . .» الحديث. رواه محمد بن عيسى الهلالي، عن محمد','147','336','0');
INSERT INTO [t1578] VALUES (344,'- «أيما رجل عهر بحرة أو أمة قوم فالولد زنا لا يرث ولا يورث» . رواه عمرو بن شعيب، عن أبيه، عن','147','336','0');
INSERT INTO [t1578] VALUES (345,'- «أيما شاب تزوج في حداثة سنه. . .» الحديث. رواه خالد بن إسماعيل المخزومي، عن عبيد الله بن','147','336','0');
INSERT INTO [t1578] VALUES (346,'- «أيما مسلم اشتهى شهوته فرد شهوته. . .» الحديث. رواه عمرو بن خالد الواسطي، عن حبيب بن أبي','148','336','0');
INSERT INTO [t1578] VALUES (347,'- «أيما امرأة تطيبت للمسجد لا تطيب إلا له لم تقبل لها صلاة حتى تغتسل من طيبها كاغتسالها من','148','336','0');
INSERT INTO [t1578] VALUES (348,'- «أيما نائحة ماتت قبل أن تتوب. . .» الحديث. رواه عبيس بن ميمون، عن يحيى بن أبي كثير، عن أبي','149','336','0');
INSERT INTO [t1578] VALUES (349,'- «إياكم والزنا، فإن فيه ست خصال. . .» الحديث. رواه أبان بن نهشل أبو الوليد البصري، عن','149','336','0');
INSERT INTO [t1578] VALUES (350,'- «إياكم والبطنة من الطعام، فإنها مكسلة عن الصلاة. . .» الحديث. رواه عبد الله بن عبد الرحمن','150','336','0');
INSERT INTO [t1578] VALUES (351,'- «إياكم وتشبيك الأصابع في الصلاة، فإنه يورث النسيان» . رواه عنبسة بن عبد الرحمن القرشي، عن','150','336','0');
INSERT INTO [t1578] VALUES (352,'- «إياكم والسكنى في السواد فإن من يسكن في السواد يصدأ قلبه» . رواه إسماعيل بن عباد أبو محمد','150','336','0');
INSERT INTO [t1578] VALUES (353,'- أيمنع سواد نفسي ودمامتي من دخول الجنة؟ فقال: «لا ما اتقيت الله. . .» . الحديث. رواه محمد بن','151','336','0');
INSERT INTO [t1578] VALUES (354,'- أيستاك الصائم؟ قال: «نعم. . .» إلى قول: «في أول النهار وآخره» ، الحديث. رواه إبراهيم بن بيطار','151','336','0');
INSERT INTO [t1578] VALUES (355,'- «آية الكرسي» ، {ويشهد الله} [البقرة: ٢٠٤] وفاتحة الكتاب معلقات. . . " الحديث. رواه الحارث بن','151','336','0');
INSERT INTO [t1578] VALUES (356,'- «الإيمان قول والعمل شرائع» . رواه أحمد بن عبد الله أبو علي الجوباري الهروي، عن ابن عيينة، عن','152','336','0');
INSERT INTO [t1578] VALUES (357,'- «الأنبياء سادات أهل الجنة، والشهداء القواد، وحملة القرآن عرفاء أهل الجنة» . رواه مجاشع بن','153','336','0');
INSERT INTO [t1578] VALUES (358,'- «الأمناء عند الله عز وجل ثلاثة أنا وجبريل ومعاوية» . رواه أحمد بن عيسى الخشاب التنيسي، عن','154','336','0');
INSERT INTO [t1578] VALUES (359,'- «الأرواح جنود مجندة. . .» الحديث. رواه علي بن سعيد بن شهريار الرقي، عن محمد بن عبد الله','154','336','0');
INSERT INTO [t1578] VALUES (360,'- «الأذان والإقامة مثنى مثنى، اللهم فأرشد الأئمة واغفر للمؤذنين» . رواه حميد بن علي بن هارون','154','336','0');
INSERT INTO [t1578] VALUES (361,'- «الأسير ما كان في الأسر فصلاته ركعتان. . .» الحديث. رواه أبان المحبر، عن إسماعيل العبدي، عن','155','336','0');
INSERT INTO [t1578] VALUES (362,'باب الباء','155','0','0');
INSERT INTO [t1578] VALUES (363,'- " قال الله: بعزتي وعظمتي وجلالي وارتفاعي لا يؤثر عبدي هواي على هواه إلا أقللت همومه. . . "','155','362','0');
INSERT INTO [t1578] VALUES (364,'- بينما رسول الله صلى الله عليه وسلم جالس وعائشة وراءه إذ استأذن أبو بكر. . . " الحديث. وفيه:','155','363','0');
INSERT INTO [t1578] VALUES (365,'- بينما أنا مع النبي صلى الله عليه وسلم على جبل من جبال تهامة. . . " الحديث. وفيه ذكر الهام','156','362','0');
INSERT INTO [t1578] VALUES (366,'- «بينما النبي صلى الله عليه وسلم جالس وعنده أبو بكر وعليه عباءة قد خللها. . .» الحديث بطوله.','156','362','0');
INSERT INTO [t1578] VALUES (367,'- بينما جبريل جالس مع النبي صلى الله عليه وسلم إذ مر أبو بكر. . . " الحديث في الفضائل. رواه','157','362','0');
INSERT INTO [t1578] VALUES (368,'- «بئس الشعب جياد. . .» الحديث. رواه رباح بن عبيد الله العمري، عن سهيل، عن أبيه، عن أبي','157','362','0');
INSERT INTO [t1578] VALUES (369,'- بعثتني مرة في صدقات أموالهم إلى رسول الله صلى الله عليه وسلم، وفيه حديث الرطب، وذكر. . .','157','362','0');
INSERT INTO [t1578] VALUES (370,'- بايع أعرابي النبي صلى الله عليه وسلم إلى أجل. . . الحديث في البيوع. وفيه ذكر أبي بكر وعمر','158','362','0');
INSERT INTO [t1578] VALUES (371,'- «باكروا طلب الرزق. . . .» الحديث. رواه إسماعيل بن قيس بن سعد بن زيد بن ثابت، عن هشام بن','158','362','0');
INSERT INTO [t1578] VALUES (372,'- «بجلوا المشايخ فإن تبجيل المشايخ من تبجيل الله عز وجل» . رواه صخر بن محمد المنقري الحاجبي،','159','362','0');
INSERT INTO [t1578] VALUES (373,'- «بسم الله خير الأسماء» . رواه ثابت بن زهير، عن نافع، عن ابن عمر، عن النبي صلى الله عليه وسلم','159','362','0');
INSERT INTO [t1578] VALUES (374,'- «بعثت داعيا، وليس إلي من الهدى شيء، وبعث إبليس مزينا، وليس إليه من الضلالة شيء» . رواه خالد','160','362','0');
INSERT INTO [t1578] VALUES (375,'باب التاء','160','0','0');
INSERT INTO [t1578] VALUES (376,'- «تابعوا بين الحج والعمرة» . رواه إبراهيم بن يزيد الخوزي، عن أيوب السختياني، عن نافع، عن ابن','160','375','0');
INSERT INTO [t1578] VALUES (377,'- «تصافحوا فإن المصافحة تذهب السخيمة، وتهادوا فإن الهدية تذهب الغل» . رواه محمد بن أبي','161','375','0');
INSERT INTO [t1578] VALUES (378,'- «تسحروا فإن في السحور بركة» . رواه نائل بن نجيح، عن الثوري، عن ابن المنكدر، عن','162','375','0');
INSERT INTO [t1578] VALUES (379,'- «تربوا الكتاب واسحوه من أسفله فإنه أنجح للحاجة» . رواه بقية بن الوليد، عن ابن جريج، عن عطاء،','162','375','0');
INSERT INTO [t1578] VALUES (380,'- «تخيروا لنطفكم» . رواه الحارث بن عمران الجعفري، عن هشام بن عروة، عن أبيه، عن','163','375','0');
INSERT INTO [t1578] VALUES (381,'- «تختموا بالعقيق، فإنه مبارك» . رواه يعقوب بن الوليد المديني، عن هشام بن عروة، عن أبيه، عن','164','375','0');
INSERT INTO [t1578] VALUES (382,'- «تعلموا الفرائض وعلموها الناس، وهذا أول علم ينزع من أمتي» ، رواه حفص بن عمر بن أبي العطاف،','164','375','0');
INSERT INTO [t1578] VALUES (383,'- «تسليم اليهود إشارة بالأصابع، وتسليم النصارى إشارة بالكف، لا تشبهوا بأهل الكتاب، قصوا','165','375','0');
INSERT INTO [t1578] VALUES (384,'- «تعاد الصلاة من قدر الدرهم من الدم» . رواه روح بن غطيف بن أبي سفيان الثقفي، عن الزهري، عن','165','375','0');
INSERT INTO [t1578] VALUES (385,'- «ترك الشر صدقة» . رواه أحمد بن محمد بن الفضل القيسي، عن سفيان بن عيينة، عن الزهري، عن أنس.','166','375','0');
INSERT INTO [t1578] VALUES (386,'- «تهادوا فإن الهدية تذهب الغل» .','167','375','0');
INSERT INTO [t1578] VALUES (387,'- «تعوذوا بالله من فخر القراء، فإنهم أشد فخرا من الجبابرة» . رواه عباد بن كثير الثقفي، عن','167','375','0');
INSERT INTO [t1578] VALUES (388,'- «تعوذوا بالله من إمارة الصبيان» . رواه يحيى بن عبيد بن موهب، عن أبيه، عن أبي هريرة. فيه يحيى','167','375','0');
INSERT INTO [t1578] VALUES (389,'باب الثاء','167','0','0');
INSERT INTO [t1578] VALUES (390,'- «ثلاث من كن فيه آواه الله إلى كنفه ونشر عليه رحمته» . رواه عمر بن راشد الجاري، عن ابن أبي','167','389','0');
INSERT INTO [t1578] VALUES (391,'- " ثلاث يفرح لهن البدن ويربو: اللبن والطيب وشرب العسل ". رواه يونس بن هارون الأردني، عن مالك','168','389','0');
INSERT INTO [t1578] VALUES (392,'- " ثلاث لا يفطرن الصائم: الحجامة والقيء والاحتلام ". رواه عبد الرحمن بن زيد بن أسلم، عن أبيه،','168','389','0');
INSERT INTO [t1578] VALUES (393,'- " ثلاث منجيات، وثلاث مهلكات: شح مطاع ". رواه حميد بن الحكم القرشي، عن الحسن، عن أنس. فيه حميد','169','389','0');
INSERT INTO [t1578] VALUES (394,'- " ثلاث لا يقبل الله منهم شهادة أن لا إله إلا الله: الراكب والمركوب، والراكبة والمركوبة ".','169','389','0');
INSERT INTO [t1578] VALUES (395,'- " ثلاث لا ترد: اللبن والوسائد والدهن ". رواه عبد الله بن مسلم بن هرمز، عن أبيه، عن ابن عمر.','169','389','0');
INSERT INTO [t1578] VALUES (396,'- " ثلاث يا علي لا تؤخرهن: الصلاة والجنازة. . . " الحديث. رواه سعيد بن عبد الرحمن الجمحي، عن','170','389','0');
INSERT INTO [t1578] VALUES (397,'باب الجيم','171','0','0');
INSERT INTO [t1578] VALUES (398,': «فضلتم علينا بالصوم. . .» الحديث بطوله. رواه أيوب بن عتبة، عن عطاء، عن ابن عمر. وأيوب هذا','171','397','0');
INSERT INTO [t1578] VALUES (399,'طفت أسبوعين فقرنت بينهما، وركعت أربع ركعات؟ فقال: «أحسنت» . رواه ياسين الزيات، عن عمرو بن','172','397','0');
INSERT INTO [t1578] VALUES (400,'فشكا إليه قلة الولد، فأمره بأكل البيض والبصل. رواه محمد بن يحيى بن ضرار المازني من أهل','172','397','0');
INSERT INTO [t1578] VALUES (401,': وعزتي وجلالي ووحدانيتي وارتفاع مكاني. . . " الحديث. رواه محمد بن عبد الله الأنصاري، عن مالك','173','397','0');
INSERT INTO [t1578] VALUES (402,'وأبي يموت، فتفل من قرني إلى قدمي. رواه محمد بن مصعب القرقساني، عن حماد بن سلمة، عن أبي','173','397','0');
INSERT INTO [t1578] VALUES (403,'ذهب الرجال بالفضل. . . " الحديث. رواه روح بن المسيب، عن ثابت، عن أنس. وروح هذا متروك','174','397','0');
INSERT INTO [t1578] VALUES (404,'- جاع النبي صلى الله عليه وسلم جوعا شديدا، فنزل جبريل عليه بلوزة فإذا فيها فريدة خضراء','174','397','0');
INSERT INTO [t1578] VALUES (405,'باب الحاء','174','0','0');
INSERT INTO [t1578] VALUES (406,'- «حق الجوار أربعون جارا هكذا وهكذا يمينا وشمالا وقدام وخلف» . رواه عبد السلام بن أبي الجنوب،','175','405','0');
INSERT INTO [t1578] VALUES (407,'- «حجة لمن لم يحج خير من عشر غزوات، وغزوة لمن لم يغز خير من عشر حجج. . .» الحديث. رواه عبد','175','405','0');
INSERT INTO [t1578] VALUES (408,'- «حبذا المتخللون في الوضوء بين الأصابع والأظافر» . رواه واصل بن السائب، عن أبي سورة، عن أبي','176','405','0');
INSERT INTO [t1578] VALUES (409,'باب الخاء','176','0','0');
INSERT INTO [t1578] VALUES (410,'- «خير الأسماء عند الله عبد الله وعبد الرحمن، وأصدقها الحارث وهمام، وشرها حرب ومرة» . رواه فرج','176','409','0');
INSERT INTO [t1578] VALUES (411,'- «خير المجالس أوسعها» . رواه مصعب بن ثابت، عن عبد الله بن أبي طلحة، عن أنس، ومصعب منكر','177','409','0');
INSERT INTO [t1578] VALUES (412,'- «خير الناس أنفعهم للناس» . رواه عمرو بن مالك السكسكي، عن ابن جريج، عن عطاء، عن جابر. وعمرو','178','409','0');
INSERT INTO [t1578] VALUES (413,'- «خير الرزق ما كفى» . رواه أحمد بن محمد بن الفضل القيسي الأبلي، عن نصر بن علي، عن سفيان بن','178','409','0');
INSERT INTO [t1578] VALUES (414,'- «خلق الله عز وجل ألف أمة، ست مائة في البحر، وأربع مائة في البر، أول شيء يهلك من هذه الأمة','178','409','0');
INSERT INTO [t1578] VALUES (415,'- «خلق الله عز وجل حجارة قبل أن يخلق السموات والأرض، ثم أمر أن يوقد عليها، أعدها لإبليس','179','409','0');
INSERT INTO [t1578] VALUES (416,'- «خلق الله الجن على ثلاثة أصناف. . .» الحديث بطوله. رواه يزيد بن سنان بن يزيد الجزري أبو','179','409','0');
INSERT INTO [t1578] VALUES (417,'- «خرج ثلاثة ممن كان قبلكم. . .» فذكر حديث الغار. رواه محمد بن سليمان الجوهري البصري، عن عبيد','179','409','0');
INSERT INTO [t1578] VALUES (418,': «لا تقوموا كما تقوم الأعاجم بعضها لبعض» . رواه أبو مرزوق، عن أبي غالب، عن أبي أمامة. وأبو','180','409','0');
INSERT INTO [t1578] VALUES (419,': «هكذا نبعث يوم القيامة» . رواه سعيد بن مسلمة بن هشام بن عبد الملك، عن إسماعيل بن أمية، عن','180','409','0');
INSERT INTO [t1578] VALUES (420,': «لا تكفرن أحدا من أهل التوحيد بذنب» . رواه كثير بن مروان السلمي الفلسطيني، عن عبد الله بن','181','409','0');
INSERT INTO [t1578] VALUES (421,'ننازع في القدر , فغضب حتى احمر وجهه، الحديث. رواه صالح بن بشر المري، عن هشام، عن ابن','181','409','0');
INSERT INTO [t1578] VALUES (422,'- خمس لم يكن النبي صلى الله عليه وسلم يتركهن في حضر ولا سفر المرآة والمكحلة والمشط والمدرى','181','409','0');
INSERT INTO [t1578] VALUES (423,': صلوا في نعالكم ". رواه عباد بن مصعب بن جويرية، عن الأوزاعي، عن قتادة، عن أنس. وعباد هذا من','182','409','0');
INSERT INTO [t1578] VALUES (424,'- «خصال لا تنبغي في المساجد، لا يتخذ طرقا، ولا يشهر فيها سلاح، ولا ينثر فيها نبل، ولا يمر','182','409','0');
INSERT INTO [t1578] VALUES (425,'باب الدال','183','0','0');
INSERT INTO [t1578] VALUES (426,'- " دخلت الجنة فرأيت فيها ذئبا، فقلت: أذئب في الجنة؟ فقال: إني أكلت ابن شرطي ". رواه عمرو بن','183','425','0');
INSERT INTO [t1578] VALUES (427,'يحتجم يوم الثلاثاء. . . الحديث. وفيه قصة نافع مع ابن عمر. رواه نافع أبو هرمز، عن عطاء، عن','183','425','0');
INSERT INTO [t1578] VALUES (428,'ادن مني أعلمك مقادير الصلاة. . .» الحديث. وفيه ذكر التسمية. رواه عباد بن صهيب، عن حميد','183','425','0');
INSERT INTO [t1578] VALUES (429,'يشتكي بطنه. . . " الحديث. رواه عنبسة بن عبد الرحمن القرشي، عن محمد بن زياد، عن أم سعد قالت:','184','425','0');
INSERT INTO [t1578] VALUES (430,': «نعم الجمل جملكما، ونعم العدلان أنتما» . رواه مسروح أبو شهاب، عن الثوري، عن أبي الزبير، عن','184','425','0');
INSERT INTO [t1578] VALUES (431,': «دونكها أبا محمد، فإنها تجم الفؤاد» . رواه عبد الرحمن بن حماد الطلحي من ولد طلحة بن عبيد','185','425','0');
INSERT INTO [t1578] VALUES (432,': لو أن أحدهما ينظر إلى قدميه. . . الحديث. رواه أحمد بن محمد بن مالك بن أنس، عن إسماعيل بن','186','425','0');
INSERT INTO [t1578] VALUES (433,': «مالك يا أبا هريرة؟» قلت: الجوع. رواه عبد الله بن عبد الرحمن الجزري، عن الثوري، عن إبراهيم','186','425','0');
INSERT INTO [t1578] VALUES (434,'- «داوموا على الصلوات الخمس، فإن الله عز وجل افترضهن عليكم. . .» الحديث بطوله. رواه مروان بن','187','425','0');
INSERT INTO [t1578] VALUES (435,'باب الذال.','187','0','0');
INSERT INTO [t1578] VALUES (436,'- «ذكاة الجنين ذكاة أمه إذا أشعر، ولكنه يذبح حتى ينصب ما فيه من الدم» . رواه محمد بن الحسن','187','435','0');
INSERT INTO [t1578] VALUES (437,'- «ذنب لا يغفر، وذنب لا يترك، وذنب يغفر. . .» الحديث. رواه يزيد بن سفيان أبو خالد، عن سليمان','188','435','0');
INSERT INTO [t1578] VALUES (438,'الله لا يؤخر نفسا إذا جاء أجلها، وإنما زيادة العمر ذرية صالحة يرزقها الله العبد فيدعون','188','435','0');
INSERT INTO [t1578] VALUES (439,'- ذروا الحسناء العقيم، وعليكم بالسوداء الولود ". رواه حسان بن سياه البصري، عن ثابت، عن أنس.','189','435','0');
INSERT INTO [t1578] VALUES (440,'باب الراء','189','0','0');
INSERT INTO [t1578] VALUES (441,'- " رأيت ليلة أسري بي مكتوبا على باب الجنة: الحسنة بعشر أمثالها، والقرض بثمانية عشر ". رواه','189','440','0');
INSERT INTO [t1578] VALUES (442,'- «رأيت ليلة أسري بي حول العرش فريدة خضراء مكتوب فيها بقلم من نور أبيض لا إله إلا الله محمد','189','440','0');
INSERT INTO [t1578] VALUES (443,'يشتد بين يدي رسول الله صلى الله عليه وسلم والحجارة تنكب رجليه، الحديث. رواه إسماعيل بن','190','440','0');
INSERT INTO [t1578] VALUES (444,'- «رأيت على رسول الله قلنسوة شامية طويلة» . رواه الضحاك بن حجوة المنبجي، عن أبي قتادة، عن أبي','190','440','0');
INSERT INTO [t1578] VALUES (445,'إذا افتتح الصلاة رفع يديه» . . . الحديث. رواه يزيد بن أبي زياد، عن عبد الرحمن بن أبي ليلى،','190','440','0');
INSERT INTO [t1578] VALUES (446,'إذا سجد سجدة الأولى، فرفع رأسه منها رفع يديه تلقاء وجهه، وفيه رأيت أبي يفعله، وقال: رأيت','191','440','0');
INSERT INTO [t1578] VALUES (447,'وبيده كتف شاة، وهو متكئ، وهو يجتز بالسكين، ثم أقيمت الصلاة، فصلى ولم يتوضأ. رواه عبد الله','191','440','0');
INSERT INTO [t1578] VALUES (448,'أذن في أذن الحسن حين ولدته فاطمة. رواه عاصم بن عبيد الله بن عاصم بن عمر بن الخطاب، عن','192','440','0');
INSERT INTO [t1578] VALUES (449,'يستقي ماء لوضوئه من زمزم فقلت: أنا أكفيك. . . الحديث. رواه نصر بن منصور العنزي، عن عقبة','192','440','0');
INSERT INTO [t1578] VALUES (450,'- «رأيت على باب الجنة مكتوبا لا إله إلا الله محمد رسول الله علي أخو رسول الله» . رواه كادح بن','193','440','0');
INSERT INTO [t1578] VALUES (451,'يخضب بصفرة. . . الحديث. رواه خالد بن عثمان العثماني، عن مالك، عن نافع، عن ابن عمر. وخالد','193','440','0');
INSERT INTO [t1578] VALUES (452,'- «رحم الله أبا بكر زوجني ابنته، وصحبني إلى دار الهجرة، وأعتق بلالا من ماله، رحم الله عمر. . .»','193','440','0');
INSERT INTO [t1578] VALUES (453,'- «رحم الله عبدا سمع مقالتي، فوعاها وأداها، فرب حامل فقه إلى من هو أفقه منه، ورب حامل فقه','194','440','0');
INSERT INTO [t1578] VALUES (454,': «أين أبو الورد» . رواه جبارة بن مغلس، عن عبد الله بن المبارك، عن حميد بن أبي الورد، عن أبيه','194','440','0');
INSERT INTO [t1578] VALUES (455,'- رخص في الكذب في ثلاث: الرجل يقول القول يريد به الإصلاح. . . " الحديث. رواه عبد الوهاب بن','195','440','0');
INSERT INTO [t1578] VALUES (456,'- ريح الولد من ريح الجنة. رواه مندل بن علي، عن عبد الحميد بن سهيل، عن عبيد الله بن عبد','195','440','0');
INSERT INTO [t1578] VALUES (457,'أبا الدرداء يمشي أمام أبي بكر. . . الحديث. رواه إسماعيل بن يحيى بن عبيد الله التيمي، عن','195','440','0');
INSERT INTO [t1578] VALUES (458,'- «ركعتان بسواك أفضل من سبعين ركعة من غير سواك» . رواه مسلمة بن علي الخشني، عن الأوزاعي، عن','196','440','0');
INSERT INTO [t1578] VALUES (459,'- رعفت عند النبي صلى الله عليه وسلم، فأمرني أن أحدث وضوءا. رواه أبو خالد الدالاني الواسطي','196','440','0');
INSERT INTO [t1578] VALUES (460,'باب الزاي','197','0','0');
INSERT INTO [t1578] VALUES (461,'- «زر غبا تزدد حبا» . رواه طلحة بن عمرو الحضرمي، عن عطاء، عن أبي هريرة. وطلحة بن عمرو ليس','197','460','0');
INSERT INTO [t1578] VALUES (462,'- «زوجوا الأكفاء، وتزوجوا إليهم، وتخيروا لنطفكم، وإياكم والزنج، فإنه خلق مشوه» . رواه محمد بن','198','460','0');
INSERT INTO [t1578] VALUES (463,'- «زن وأرجح. . .» الحديث. رواه عبد الرحمن بن زياد الإفريقي، عن الأغر أبي مسلم، عن أبي هريرة.','198','460','0');
INSERT INTO [t1578] VALUES (464,'باب السين','199','0','0');
INSERT INTO [t1578] VALUES (465,'تفسير سبحان الله؟ فقال: «هو تنزيه الله عن السوء. . .» الحديث. رواه عبد الرحمن بن حماد','199','464','0');
INSERT INTO [t1578] VALUES (466,': «صل في القوس واطرح القرن» . رواه موسى بن إبراهيم بن الحارث التيمي، عن أبيه، عن سلمة بن','199','464','0');
INSERT INTO [t1578] VALUES (467,'قول الناس في العيد: تقبل الله منا ومنكم؟ قال: «ذلك فعل أهل الكتابين» وكرهه. رواه عبد الخالق','200','464','0');
INSERT INTO [t1578] VALUES (468,'السمن والجبن والفراء؟ فقال: «الحلال. . .» الحديث. رواه سيف بن هرمز البرجمي، عن سليمان','200','464','0');
INSERT INTO [t1578] VALUES (469,'الرجل يتخذ الحمام في القرية؟ فقال: «إن كان له زرع كما يزرعون، وإلا فلا» . رواه عبد الله بن','200','464','0');
INSERT INTO [t1578] VALUES (470,'الإيمان؟ فقال: «الصبر والسماحة» . رواه يوسف بن محمد بن المنكدر، عن أبيه، عن جابر. ويوسف يروي','201','464','0');
INSERT INTO [t1578] VALUES (471,'أقرأ خلف الإمام أو أنصت؟ قال: «لا، بل أنصت» . رواه محمد بن سالم الكوفي، عن الشعبي، عن','201','464','0');
INSERT INTO [t1578] VALUES (472,'تسألني عن عسقلان؟ فقالت: كان رسول الله عندي. . . . الحديث، وفيه ذكر مقبرة عسقلان. رواه','201','464','0');
INSERT INTO [t1578] VALUES (473,'- «سرعة المشي تذهب ببهاء المؤمن» . رواه الوليد بن سلمة الطبراني، عن محمد بن عبد الرحمن بن أبي','202','464','0');
INSERT INTO [t1578] VALUES (474,'- «سيد طعام أهل الجنة اللحم» . رواه سليمان بن عطاء، عن مسلمة بن عبد الله الأشجعي، عن أبي','202','464','0');
INSERT INTO [t1578] VALUES (475,'- «ساقي القوم آخرهم شربا» . رواه أحمد بن محمد بن الفضل، عن نصر بن علي، عن سفيان بن عيينة، عن','203','464','0');
INSERT INTO [t1578] VALUES (476,'- " ستبعث بعدي بعوث، فكونوا في بعث يقال لها: خراسان " وفيه ذكر مرو. . . الحديث. رواه سهل بن عبد','203','464','0');
INSERT INTO [t1578] VALUES (477,'- «سوء الخلق يفسد العمل كما يفسد الخل العسل» . رواه النضر بن معبد أبو قحذم، عن محمد بن سيرين،','203','464','0');
INSERT INTO [t1578] VALUES (478,'- «سوداء ولود خير من حسناء لا تلد» . رواه علي بن الربيع، عن بهز بن حكيم، عن أبيه، عن جده، وفيه "','204','464','0');
INSERT INTO [t1578] VALUES (479,'- «سبع يجري للعبد أجرهن بعد موته. . .» الحديث. رواه محمد بن عبيد الله العرزمي، عن قتادة، عن','204','464','0');
INSERT INTO [t1578] VALUES (480,': «انظروا ما هذا» فنظرت فإذا معاوية وعمرو يتغنيان. . . الحديث. وفيه: «اللهم أركسهما في الفتنة» .','205','464','0');
INSERT INTO [t1578] VALUES (481,'باب الشين','205','0','0');
INSERT INTO [t1578] VALUES (482,'- «شفاعتي لأهل الكبائر من أمتي» . رواه محمد بن ثابت البناني، عن جعفر بن محمد، عن أبيه، عن','205','481','0');
INSERT INTO [t1578] VALUES (483,'- «شراركم عزابكم. . .» الحديث. رواه خالد بن إسماعيل المخزومي، عن عبيد الله بن عمر، عن صالح','205','481','0');
INSERT INTO [t1578] VALUES (484,'- «شر الناس من نزل وحده، وجلد عبده، ومنع رفده. . .» الحديث. رواه إسحاق بن وهب الطهرمسي، عن','206','481','0');
INSERT INTO [t1578] VALUES (485,'- «شرب الماء على الريق يعقد الشحم» . رواه عاصم بن سليمان الكوزي، عن هشام بن حسان، عن ابن','206','481','0');
INSERT INTO [t1578] VALUES (486,'- «شاهد الزور لا تزول قدماه حتى تجب له النار» . رواه محمد بن الفرات الكوفي، عن محارب بن دثار،','206','481','0');
INSERT INTO [t1578] VALUES (487,'- «شعار المسلمين على الصراط يوم القيامة سلم سلم» . رواه عبد الرحمن بن إسحاق الواسطي وهو الذي','207','481','0');
INSERT INTO [t1578] VALUES (488,'أرقا أصابني، فقال: " قل: اللهم غارت النجوم وهدأت العيون. . . " الحديث. رواه محمد بن عبد الله','207','481','0');
INSERT INTO [t1578] VALUES (489,'باب الصاد','208','0','0');
INSERT INTO [t1578] VALUES (490,'- «صلة الرحم وحسن الخلق وحسن الجوار تعمر الديار وتزيد في الأعمار» . رواه محمد بن سليمان بن','208','489','0');
INSERT INTO [t1578] VALUES (491,'- " صنفان من أمتي ليس في الإسلام سهم: المرجئة والقدرية ". رواه سلام بن أبي عمرو الخراساني، عن','208','489','0');
INSERT INTO [t1578] VALUES (492,'- صلوا خلف من قال «لا إله إلا الله» وصلوا على من مات من أهل «لا إله إلا الله» . رواه عثمان بن','209','489','0');
INSERT INTO [t1578] VALUES (493,'يرفعون أيديهم في أول الصلاة ثم لا يعودون» . رواه محمد بن جابر اليمامي، عن حماد، عن إبراهيم،','209','489','0');
INSERT INTO [t1578] VALUES (494,'- «صوامع المؤمنين بيوتهم» . رواه محمد بن سليمان بن هشام ابن بنت مطر الوراق، عن ابن أبي عدي، عن','210','489','0');
INSERT INTO [t1578] VALUES (495,'- «صومكم يوم تصومون، وفطركم يوم تفطرون» . رواه محمد بن عمر الواقدي، عن مالك، وابن أبي الرجال،','210','489','0');
INSERT INTO [t1578] VALUES (496,'باب الضاد','210','0','0');
INSERT INTO [t1578] VALUES (497,'- ضحى النبي صلى الله عليه وسلم بكبشين أملحين، وقال: «بسم الله» وفيه «وهذا عني وعن أمتي. . .»','211','496','0');
INSERT INTO [t1578] VALUES (498,'باب الطاء','211','0','0');
INSERT INTO [t1578] VALUES (499,'- «طلب العلم فريضة على كل مسلم» . رواه أحمد بن إبراهيم بن موسى، عن مالك، عن نافع، عن ابن عمر.','211','498','0');
INSERT INTO [t1578] VALUES (500,'- «طلب الحلال فريضة بعد الفريضة» . رواه عباد بن كثير الرملي، عن سفيان الثوري، عن منصور، عن','212','498','0');
INSERT INTO [t1578] VALUES (501,'- «طوبى لمن رآني ثلاث مرات» . رواه طلحة بن عمرو، عن نافع، عن ابن عمر. وطلحة ليس بشيء. ولهذا','212','498','0');
INSERT INTO [t1578] VALUES (502,'باب العين','213','0','0');
INSERT INTO [t1578] VALUES (503,'- «عليكم بالبغيض النافع التلبينة. . .» الحديث. رواه أيمن بن نابل أبو عمران، عن فاطمة، عن أم','213','502','0');
INSERT INTO [t1578] VALUES (504,'- «عليكم بغسل الدبر، فإنه يذهب الباسور» . رواه عثمان بن مطر الشيباني، عن الحسن بن أبي جعفر، عن','214','502','0');
INSERT INTO [t1578] VALUES (505,'- «عليكم بدين العجائز» . قال المقدسي: هذا اللفظ تداوله العامة عنهم، ولم أقف على أصل يرجع إليه','214','502','0');
INSERT INTO [t1578] VALUES (506,'- «علي خير البرية» . رواه أحمد بن سمرة من ولد سمرة، عن شريك بن عبد الله، عن الأعمش، عن عطية،','215','502','0');
INSERT INTO [t1578] VALUES (507,'- «علموا أولادكم أبناء سبع سنين الصلاة، واضربوهم عليها أبناء اثنتي عشرة سنة. . .» الحديث.','215','502','0');
INSERT INTO [t1578] VALUES (508,'- علمت ناسا من أهل الصفة الكتابة والقرآن، فأهدى إلي رجل منهم قوسا. . . الحديث. رواه المغيرة','215','502','0');
INSERT INTO [t1578] VALUES (509,'- «عذاب أمتي في دنياها. . .» الحديث. رواه الحسن بن الحكم النخعي، عن أبي بردة بن أبي موسى، عن','216','502','0');
INSERT INTO [t1578] VALUES (510,'- «عند كل ختمة دعوة مستجابة» . رواه يحيى بن هاشم السمسار أبو زكريا البغدادي، عن مسعر، عن','216','502','0');
INSERT INTO [t1578] VALUES (511,'باب الغين','217','0','0');
INSERT INTO [t1578] VALUES (512,'- «غنيمتان غنمهما كثير من الناس. . .» الحديث. رواه حميد بن الحكم الحوشي، عن الحسن، عن أنس.','217','511','0');
INSERT INTO [t1578] VALUES (513,'- «غزوة في البحر كعشر غزوات في البر» . رواه خالد بن يزيد العمري، عن الثوري، عن يحيى بن سعيد،','217','511','0');
INSERT INTO [t1578] VALUES (514,'- غزونا مع رسول الله صلى الله عليه وسلم في زمن قيظ، وفيه ذكر العباس. رواه إسماعيل بن قيس بن','217','511','0');
INSERT INTO [t1578] VALUES (515,'باب الفاء','218','0','0');
INSERT INTO [t1578] VALUES (516,'- «فضل المؤمن العالم على المؤمن العابد سبعون درجة، بين كل درجتين حفر الفرس السريع المضمر','218','515','0');
INSERT INTO [t1578] VALUES (517,'- «فضل دهن البنفسج على سائر الأدهان كفضلي على سائر الخلق، بارد في الصيف حار في الشتاء» . رواه','218','515','0');
INSERT INTO [t1578] VALUES (518,'- «فضل العالم على العابد كفضلي على أمتي» . رواه سلام بن سليم الطويل، عن زيد العمي، عن جعفر','218','515','0');
INSERT INTO [t1578] VALUES (519,'- «فروا من المجذومين فراركم من الأسد» . رواه إبراهيم الخوزي، عن سعيد بن ميناء، عن أبي هريرة.','219','515','0');
INSERT INTO [t1578] VALUES (520,'- «فقيه واحد أشد على الشيطان من ألف عابد» . رواه روح بن جناح الشامي، عن مجاهد، عن ابن عباس.','219','515','0');
INSERT INTO [t1578] VALUES (521,'- «فصل بين الحرام والحلال الدف والصوت» . رواه يحيى بن أبي سليم أبو بلج، عن محمد بن حاطب. قال','219','515','0');
INSERT INTO [t1578] VALUES (522,'- «في العسل في كل عشرة أزق زق» . رواه إسماعيل بن محمد بن يوسف من أهل بيت جبرين، عن عمرو بن أبي','220','515','0');
INSERT INTO [t1578] VALUES (523,': «يطعم عنه لكل يوم نصف صاع من بر» . رواه محمد بن عبد الرحمن بن أبي ليلى، عن نافع، عن ابن عمر.','221','515','0');
INSERT INTO [t1578] VALUES (524,'- «فيما سقت السماء والعيون العشور، وفيما سقي بالنضح نصف العشور» . رواه عاصم بن عبد العزيز بن','221','515','0');
INSERT INTO [t1578] VALUES (525,'- {فمحونا آية الليل} [الإسراء: ١٢] قال: «هو هذا السواد الذي يكون في القمر» . رواه العلاء بن','221','515','0');
INSERT INTO [t1578] VALUES (526,'باب القاف','222','0','0');
INSERT INTO [t1578] VALUES (527,': يا داود ابن لي بيتا، فبنى بيتا لنفسه قبل البيت الذي أمر به. . . " الحديث بطوله. رواه محمد','222','526','0');
INSERT INTO [t1578] VALUES (528,'الخمير يقترض؟ قال: «لا بأس به» . رواه أبو البختري وهب بن وهب، عن هشام بن عروة، عن أبيه، عن','222','526','0');
INSERT INTO [t1578] VALUES (529,'- «قيلوا فإن الشياطين لا تقيل» . رواه عباد بن كثير الكاهلي الثقفي، عن أبي خالد الدالاني، عن','222','526','0');
INSERT INTO [t1578] VALUES (530,'- " قالت أم سليمان بن داود لابنها: يا بني لا تكثر النوم، فإن كثرة النوم تدع الرجل فقيرا يوم','223','526','0');
INSERT INTO [t1578] VALUES (531,'- " قراء القرآن ثلاثة: رجل اتخذه بضاعة. . . " الحديث. رواه أحمد بن ميثم بن أبي نعيم، عن علي بن','223','526','0');
INSERT INTO [t1578] VALUES (532,'- «قرآن في صلاة خير من قرآن في غير صلاة. . .» الحديث. وفيه «لا قول إلا بعمل، ولا عمل إلا بنية،','224','526','0');
INSERT INTO [t1578] VALUES (533,'ما هذه الأضاحي؟ قال: «سنة أبيكم إبراهيم» . رواه نفيع بن الحارث أبو داود الأعمى، عن زيد بن','224','526','0');
INSERT INTO [t1578] VALUES (534,'- «قتل الصبر لا يمر بذنب إلا محاه» . رواه عنبسة بن سعيد، عن هشام، عن أبيه، عن عائشة رضي الله','224','526','0');
INSERT INTO [t1578] VALUES (535,'بجميع ميراث ولد الملاعنة لها، لما أصابها فيه من التعب» . رواه الوزير بن عبد الله الحراني،','225','526','0');
INSERT INTO [t1578] VALUES (536,'- «قدس العدس على لسان سبعين نبيا. . .» الحديث. رواه عيسى بن شعيب البصري، عن الحجاج بن منهال،','225','526','0');
INSERT INTO [t1578] VALUES (537,'باب الكاف','225','0','0');
INSERT INTO [t1578] VALUES (538,'*','226','537','0');
INSERT INTO [t1578] VALUES (539,'إذا أراد أن يقوم لحاجة، وأراد أن يرجع وضع نعليه في مجلسه أو بعض ما يكون» . رواه عمام بن','226','538','0');
INSERT INTO [t1578] VALUES (540,'إذا اهتم أخذ بلحيته فنظر فيها. رواه سهل مولى المغيرة، عن الزهري، عن أبي سلمة، عن أبي','227','538','0');
INSERT INTO [t1578] VALUES (541,'يستاك آخر النهار وهو صائم. رواه أحمد بن عبد الله بن ميسرة الحراني أبو ميسرة، عن يحيى بن','227','538','0');
INSERT INTO [t1578] VALUES (542,'- كان لرسول الله صلى الله عليه وسلم سيف محلى قائمته من فضة. . . الحديث بطوله. رواه علي بن','228','538','0');
INSERT INTO [t1578] VALUES (543,'إذا دخل الخلاء دخلت بعده فلا أرى شيئا. . . الحديث. رواه الحسين بن علوان، عن هشام بن عروة،','228','538','0');
INSERT INTO [t1578] VALUES (544,'يبيت الليالي المتتابعة طاويا وأهله. . . الحديث. رواه هلال بن خباب أبو العلاء، عن عكرمة،','228','538','0');
INSERT INTO [t1578] VALUES (545,'يقبل ولا يعيد الوضوء. رواه غالب بن عبيد الله العقيلي، عن نافع، عن ابن عمر. وغالب هذا يتهم','229','538','0');
INSERT INTO [t1578] VALUES (546,'- كان لرسول الله قدح قوارير يشرب منه. رواه مندل بن علي، عن محمد بن إسحاق، عن الزهري، عن','229','538','0');
INSERT INTO [t1578] VALUES (547,'إذا خرج ثلاثة أميال من المدينة يريد سفرا قصر الصلاة وأفطر. وهذا حديث رواه صبيح بن سعيد','229','538','0');
INSERT INTO [t1578] VALUES (548,'يعجبه النظر إلى الحمام الأحمر والأترج. رواه عيسى بن عبد الله بن محمد بن عمر بن علي بن أبي','230','538','0');
INSERT INTO [t1578] VALUES (549,'إذا غدا إلى العيد غدا ماشيا، وإذا رجع رجع راكبا. رواه محمد بن عبد الله بن عمر العمري أخو','230','538','0');
INSERT INTO [t1578] VALUES (550,': هل في الجنة من سماع؟ قال: «نعم» الحديث. رواه سليمان بن عطاء، عن مسلمة بن عبد الله الجهني،','230','538','0');
INSERT INTO [t1578] VALUES (551,'يكبر من غداة عرفة إلى صلاة العصر من آخر أيام التشريق. رواه بشر بن إبراهيم البصري، عن عمرو','231','538','0');
INSERT INTO [t1578] VALUES (552,'إذا صلى الصبح قال وهو ثان رجليه: «سبحان الله وبحمده. . .» الحديث بطوله. رواه سليمان بن','231','538','0');
INSERT INTO [t1578] VALUES (553,'أذان رسول الله صلى الله عليه وسلم شفعا شفعا والإقامة شفعا شفعا مرتين مرتين. رواه محمد بن','231','538','0');
INSERT INTO [t1578] VALUES (554,'أحب الشاة إلى رسول الله الذراع. رواه عيسى بن عبد الله بن محمد بن عمر بن علي بن أبي طالب،','231','538','0');
INSERT INTO [t1578] VALUES (555,'إذا انتبه من منامه خر ساجدا، وإذا رأى القرد خر ساجدا. رواه يوسف بن محمد بن المنكدر، عن','232','538','0');
INSERT INTO [t1578] VALUES (556,'يقوم حتى تفطرت قدماه. . الحديث. رواه أبو قتادة الحراني عبد الله بن واقد، عن مسعر بن كدام،','232','538','0');
INSERT INTO [t1578] VALUES (557,'لا يقعد في بيت مظلم حتى يضاء له السراج. رواه أبو محمد، عن عائشة، وهو ممن يروي عنها ما لم','233','538','0');
INSERT INTO [t1578] VALUES (558,'إذا استوى على المنبر استقبلناه بوجوهنا. رواه محمد بن الفضل بن عطية المروزي، عن منصور، عن','233','538','0');
INSERT INTO [t1578] VALUES (559,'أحب الفاكهة إلى رسول الله الرطب والبطيخ، وكان يأكل القثاء إذا أكل بالملح، وكان يأكل التمر','233','538','0');
INSERT INTO [t1578] VALUES (560,'يرتاد لبوله كما يرتاد أحدكم لصلاته. رواه عمر بن هارون البلخي، عن الأوزاعي، عن يحيى بن أبي','234','538','0');
INSERT INTO [t1578] VALUES (561,'يأكل الطعام مما يليه، فإذا وضع التمر جالت يده في الإناء. رواه عبيد بن القاسم، عن هشام بن','234','538','0');
INSERT INTO [t1578] VALUES (562,'إذا أشفق عن الحاجة أن ينساها ربط في أصبعه أو خاتمه خيطا. رواه سالم بن عبد الأعلى أبو','234','538','0');
INSERT INTO [t1578] VALUES (563,'- كان لنعل رسول الله صلى الله عليه وسلم قبالان. رواه أسيد بن زيد الحمال مولى صالح بن علي،','235','538','0');
INSERT INTO [t1578] VALUES (564,'إذا ادهن جعل في راحته اليسرى وبدأ براحتيه، ثم بشاربيه. . . الحديث. رواه حسين بن علوان، عن','235','538','0');
INSERT INTO [t1578] VALUES (565,'يستاك عرضا، ويشرب مصا. . . الحديث. رواه ثبيت بن كثير الضبي البصري، عن يحيى بن سعيد','235','538','0');
INSERT INTO [t1578] VALUES (566,'يسجد على أعلى جبهته مع قصاص الشعر. رواه أبو بكر بن أبي مريم، عن حكيم بن عمير، عن جابر بن','236','538','0');
INSERT INTO [t1578] VALUES (567,'إذا قام إلى الصلاة من الليل كبر، ثم يقول: «سبحانك اللهم وبحمدك. . .» الحديث. رواه علي بن','236','538','0');
INSERT INTO [t1578] VALUES (568,'إذا استفتح الصلاة، قال: «وجهت وجهي. . .» الحديث. رواه عبد الله بن عامر الأسلمي، عن محمد بن','236','538','0');
INSERT INTO [t1578] VALUES (569,'إذا أتي بالباكورة من الفاكهة قبلها ووضعها على عينيه. . . الحديث. رواه علي بن الحسن السامي','237','538','0');
INSERT INTO [t1578] VALUES (570,'- كان باب النبي صلى الله عليه وسلم يقرع بالأظافير. رواه عمير بن سويد، عن أنس. وعمير عن أنس','237','538','0');
INSERT INTO [t1578] VALUES (571,'يكتحل بالإثمد وهو صائم. رواه محمد بن عبيد الله بن أبي رافع، عن أبيه، عن جده. ومحمد هذا','237','538','0');
INSERT INTO [t1578] VALUES (572,'إذا عطس قال له علي: رفع الله ذكرك، وإذا عطس علي قال له النبي صلى الله عليه وسلم: «أعلى','238','538','0');
INSERT INTO [t1578] VALUES (573,'يوتر بـ سبح اسم ربك الأعلى وقل هو الله أحد وقل يأيها الكافرون. رواه أيوب بن جابر، عن','238','538','0');
INSERT INTO [t1578] VALUES (574,'يأمر بتأخير العصر. رواه عبد الواحد بن نافع الكلاعي، عن عبيد الله بن عبد الله بن رافع بن','238','538','0');
INSERT INTO [t1578] VALUES (575,'إذا كتبت كتابا فضع القلم في أذنك، فإنه أذكر لك» . رواه يونس بن عطاء المدائني، عن حميد بن','239','538','0');
INSERT INTO [t1578] VALUES (576,'باب حديث كنا','239','537','0');
INSERT INTO [t1578] VALUES (577,'فدعا بهذه الدعوات، وذكر دعاء طويلا. رواه إبراهيم بن زيد الأسلمي، عن مالك، عن أبي الزناد،','239','576','0');
INSERT INTO [t1578] VALUES (578,'فأتي بقدح فيه شراب، فقربه إلى فيه، ثم رده، فقال له بعض جلسائه: أحرام هو؟ . . . الحديث.','240','576','0');
INSERT INTO [t1578] VALUES (579,'فمن خلق الله؟ فقال عليه السلام: «هذا إبليس جاء يشكك الناس في دينهم» . رواه عبد الله بن جعفر','240','576','0');
INSERT INTO [t1578] VALUES (580,'لا نقتل تجار المشركين على عهد رسول الله. رواه الحجاج بن أرطاة، عن عمرو بن شعيب، عن أبيه،','241','576','0');
INSERT INTO [t1578] VALUES (581,'، فدخل حجرة حفصة فصلى ركعتين، ثم خرج فدخل مع الناس فصلى بهم. رواه علي بن الحسن النسوي، عن','241','576','0');
INSERT INTO [t1578] VALUES (582,'فعطس النبي صلى الله عليه وسلم فقال: يرحمك الله يا محمد، فقال عليه الصلاة والسلام: «يهديك','241','576','0');
INSERT INTO [t1578] VALUES (583,'باب كل','242','537','0');
INSERT INTO [t1578] VALUES (584,'- «كل بني آدم يخطئ، وخير الخطائين التوابون» . رواه علي بن مسعدة الباهلي، عن قتادة، عن أنس.','242','583','0');
INSERT INTO [t1578] VALUES (585,'- «كل مشكل حرام، وليس في الدين إشكال» . رواه حسين بن عبد الله بن ضميرة، عن أبيه، عن جده، عن','242','583','0');
INSERT INTO [t1578] VALUES (586,'- «كل البلاد فتحت بالسيف والرمح، وفتحت المدينة بالقرآن، وفيها قبري ومهاجري، وحق على كل مسلم','243','583','0');
INSERT INTO [t1578] VALUES (587,'- «كل لهو مكروه إلا ملاعبة الرجل أهله، ومشيه بين الهدفين أو تعلقه قوسه» . رواه المنذر بن زياد','244','583','0');
INSERT INTO [t1578] VALUES (588,'باب كانت','244','537','0');
INSERT INTO [t1578] VALUES (589,'- كانت النفساء على عهد رسول الله صلى الله عليه وسلم تقعد بعد نفاسها أربعين يوما وأربعين','244','588','0');
INSERT INTO [t1578] VALUES (590,'باب كنت','245','537','0');
INSERT INTO [t1578] VALUES (591,'لله تعالى سبعة أملاك في كل سماء. . . " الحديث. رواه القاسم بن عبد الله المكفوف، عن سلم','245','590','0');
INSERT INTO [t1578] VALUES (592,'باب كيف','246','537','0');
INSERT INTO [t1578] VALUES (593,'- «كيف أصبحت يا حارثة؟» قال: أصبحت مؤمنا حقا. . . " الحديث. رواه أحمد بن الحسين بن أبان المضري','246','592','0');
INSERT INTO [t1578] VALUES (594,'- «كلم الله البحر الشامي. . .» الحديث. رواه عبد الرحمن بن عبد الله بن عمر العمري، عن سهل، عن','247','592','0');
INSERT INTO [t1578] VALUES (595,'- «كلام أهل الجنة العربية. . .» الحديث. رواه عثمان بن فائدة أبو لبابة القرشي، عن جعفر بن','247','592','0');
INSERT INTO [t1578] VALUES (596,'- «كبرت الملائكة على آدم أربع تكبيرات، وكبر أبو بكر على فاطمة أربعا، وكبر عمر على أبي بكر','247','592','0');
INSERT INTO [t1578] VALUES (597,'لا يجتنى من الشوك العنب، كذلك لا ينال الفجار منازل الأبرار» . رواه مكبر بن عثمان التنوخي،','248','592','0');
INSERT INTO [t1578] VALUES (598,'- " كل ما في السموات والأرض وما بينهما فهو مخلوق غير الله والقرآن، وذلك منه بدأ وإليه يعود،','248','592','0');
INSERT INTO [t1578] VALUES (599,': «أكثر خرز أهل الجنة العقيق» . رواه سلم بن عبد الله الزاهد، عن القاسم بن معن، عن عائشة بنت','249','592','0');
INSERT INTO [t1578] VALUES (600,'- كفن رسول الله صلى الله عليه وسلم في سبعة أثواب. رواه عبد الله بن محمد بن عقيل بن أبي','249','592','0');
INSERT INTO [t1578] VALUES (601,'- كلوا البلح بالتمر، فإن الشيطان إذا أكله ابن آدم غضب. . . ". رواه أبو زكريا يحيى بن محمد بن','250','592','0');
INSERT INTO [t1578] VALUES (602,'باب اللام','250','0','0');
INSERT INTO [t1578] VALUES (603,'- «لله عز وجل ثلاث مائة وستون نظرة، لا ينظر فيها إلى صاحب الشاه، يعني الشطرنج» . رواه محمد بن','250','602','0');
INSERT INTO [t1578] VALUES (604,'- «ليس الخبر كالمعاينة» . رواه أحمد بن الحارث الملحمي أبو الحسن الجرجاني، عن علي بن الجعد، عن','251','602','0');
INSERT INTO [t1578] VALUES (605,'- «ليس على أهل لا إله إلا الله وحشة في قبورهم. . .» الحديث. رواه محمد بن سعيد الطائفي، عن ابن','251','602','0');
INSERT INTO [t1578] VALUES (606,'- «ليس على مداو ضمان، وليس على مسلم جزية» . رواه عمر بن زيد الصنعاني، عن محارب بن دثار، عن ابن','252','602','0');
INSERT INTO [t1578] VALUES (607,'- «ليس أحد يدخل الجنة إلا جرد مرد إلا موسى بن عمران، فإن لحيته إلى سرته، وليس أحد يكنى إلا','252','602','0');
INSERT INTO [t1578] VALUES (608,'- «ليس في الخضروات صدقة، ولا في الجبهة صدقة» . والجبهة الخيل. رواه الصعق بن حبيب، عن أبي رجاء','252','602','0');
INSERT INTO [t1578] VALUES (609,'- «ليس على من أسلف مالا زكاة» . رواه عنبسة بن عبد الرحمن القرشي، عن محمد بن زاذان، عن أم سعد','253','602','0');
INSERT INTO [t1578] VALUES (610,'- «لما أسري بي إلى السماء رأيت فيها أعاجيب من عباد الله وخلقه، ورأيت في السماء الدنيا ديكا','253','602','0');
INSERT INTO [t1578] VALUES (611,'- " لما أسري بي إلى بيت المقدس مر بي جبريل عليه السلام بقبر أبي إبراهيم، فقال: انزل فصل','253','602','0');
INSERT INTO [t1578] VALUES (612,'- «لما أسري بي إلى السماء، سقط من عرقي إلى الأرض، فنبت منه الورد» . رواه علي بن موسى الرضا، عن','254','602','0');
INSERT INTO [t1578] VALUES (613,'- «لما تجلى الله عز وجل للجبل طارت لعظمته ثلاثة أجبل. . .» الحديث. رواه جلد بن أيوب، عن','254','602','0');
INSERT INTO [t1578] VALUES (614,'وقبل بين عينيه. رواه إبراهيم ابن أخي عبد الرزاق، عن عمه، عن معمر، عن إسماعيل بن أبي خالد،','254','602','0');
INSERT INTO [t1578] VALUES (615,'إنا أعطيناك الكوثر. وفيه «ما هذه النحيرة؟» وفيه ذكر رفع اليدين. . . الحديث. رواه إسرائيل بن','255','602','0');
INSERT INTO [t1578] VALUES (616,'«أعتقها ولدها» . رواه حسين بن عبد الله بن عبيد الله بن عباس بن عبد المطلب، عن عكرمة، عن ابن','255','602','0');
INSERT INTO [t1578] VALUES (617,'- لما قدم النبي صلى الله عليه وسلم، وفيه ذكر أبي بكر. رواه أحمد بن محمد بن عمر بن يونس','256','602','0');
INSERT INTO [t1578] VALUES (618,'الليلة التي زفت فيها فاطمة إلى علي كان النبي صلى الله عليه وسلم أمامها وجبريل عن يمينها. . .','256','602','0');
INSERT INTO [t1578] VALUES (619,'- لما كان يوم الخندق. . . وفيه ذكر الحجر والجوع. رواه إسماعيل بن عبد الله بن أبي صعير','256','602','0');
INSERT INTO [t1578] VALUES (620,'- " لما خلق الله عز وجل الفردوس: قالت: رب زيني، فأوحى الله عز وجل إليها: قد زينتك بالحسن','257','602','0');
INSERT INTO [t1578] VALUES (621,': «ما اسمك؟» قال: يزيد بن شهاب، أخرج الله من نسل جدي ستين حمارا، كلهم لم يركبهم إلا نبي، ولم','257','602','0');
INSERT INTO [t1578] VALUES (622,'- «لعن الله سهيلا. . .» الحديث. رواه إبراهيم، عن عمرو بن دينار، عن ابن عمر مسندا. وإبراهيم','258','602','0');
INSERT INTO [t1578] VALUES (623,'- «لو أن الله عز وجل أذن للسموات والأرض أن تتكلما. . .» الحديث في الصوم. رواه أبو هدبة','258','602','0');
INSERT INTO [t1578] VALUES (624,'- «لولا المنابر لهلك أهل القرى» . رواه سعيد بن موسى الأزدي، عن مالك، عن نافع، عن ابن عمر.','259','602','0');
INSERT INTO [t1578] VALUES (625,'- «لو ربى أحدكم بعد سنة ستين ومائة جرو كلب خير له من أن يربي ولد صلبه» . رواه الحكم بن مصعب،','259','602','0');
INSERT INTO [t1578] VALUES (626,'- «لو لم تذنبوا لخشيت عليكم ما هو أشد منه العجب العجب» . رواه سلام بن أبي الصهباء البصري، عن','260','602','0');
INSERT INTO [t1578] VALUES (627,'- «لو تمت سورة البقرة ثلاث مائة آية لتكلمت البقرة مع الناس» . رواه يعقوب بن الوليد المدني، عن','260','602','0');
INSERT INTO [t1578] VALUES (628,'- «لو كان القرآن في إهاب ما مسته النار» . رواه عبد الوهاب بن الضحاك، عن عبد العزيز بن أبي','260','602','0');
INSERT INTO [t1578] VALUES (629,'- «لو بغى جبل على جبل لجعله الله دكا» . رواه أحمد بن محمد بن الفضل، عن نصر بن علي، عن ابن','261','602','0');
INSERT INTO [t1578] VALUES (630,'- «لقنوا موتاكم لا إله إلا الله» ، رواه أبو حرب مولى الزهري، عن الزهري، عن أبي سلمة، عن أبي','261','602','0');
INSERT INTO [t1578] VALUES (631,'«لا ينبغي لأمتي أن يؤمهم إمام وفيهم أبو بكر» . رواه موسى بن دينار المكي، عن موسى بن طلحة، عن','262','602','0');
INSERT INTO [t1578] VALUES (632,'- «ليصل أحدكم في المسجد الذي يليه، ولا يتتبع المساجد» . رواه عبيس بن مرحوم، عن بكر بن عبد الله','262','602','0');
INSERT INTO [t1578] VALUES (633,'- «لينهض كل رجل إلى كفؤه، ونهض النبي صلى الله عليه وسلم إلى عثمان» . رواه طلحة بن زيد الرقي،','262','602','0');
INSERT INTO [t1578] VALUES (634,'- «لن تخلو الأرض من ثلاثين مثل إبراهيم خليل الرحمن، بهم تغاثون، وبهم ترزقون، وبهم تمطرون» .','263','602','0');
INSERT INTO [t1578] VALUES (635,'- «للمؤمن أكرم على الله من الملائكة الذي عنده» . رواه يزيد بن سفيان أبو المهزم، عن أبي هريرة.','263','602','0');
INSERT INTO [t1578] VALUES (636,'- «لسقط أقدمه بين يدي أحب إلي من فارس أخلفه ورائي» . رواه يزيد بن عبد الملك النوفلي، عن سهل،','263','602','0');
INSERT INTO [t1578] VALUES (637,'- «ليؤمكم أقرؤكم وإن كان ولد زنا» . رواه صالح بن حسان الأنصاري، عن نافع، عن ابن عمر. وصالح هذا','264','602','0');
INSERT INTO [t1578] VALUES (638,'- " ليلة أسري بي دخلت الجنة فإذا أنا بتفاحة تفلقت عن حوراء مزينة، فقلت: لمن أنت يا جارية؟','264','602','0');
INSERT INTO [t1578] VALUES (639,'- «ليلة أسري بي ما مررت بملأ من الملائكة إلا أمروني بالحجامة» . رواه نافع أبو هرمز، عن عطاء،','264','602','0');
INSERT INTO [t1578] VALUES (640,'حين ينهض من صلاته: «اللهم بك انتشرت، وإليك توجهت، وبك اعتصمت. . .» الحديث. رواه عمر بن','265','602','0');
INSERT INTO [t1578] VALUES (641,'- «لرد دانق من الحرام يعدل عند الله سبعين ألف حجة مبرورة» . رواه إسحاق بن وهب الطهرمسي، عن عبد','265','602','0');
INSERT INTO [t1578] VALUES (642,'- «لقد رأيت البارحة عجبا، رأيت رجلا من أمتي جاءه ملك الموت ليقبض روحه، فجاءه بره بوالديه،','266','602','0');
INSERT INTO [t1578] VALUES (643,'- لقيت رسول الله يوم العيد، فقلت: تقبل الله منا ومنك. رواه محمد بن إبراهيم الشامي، عن بقية،','266','602','0');
INSERT INTO [t1578] VALUES (644,'باب الميم','266','0','0');
INSERT INTO [t1578] VALUES (645,'*','266','644','0');
INSERT INTO [t1578] VALUES (646,'- «ما من نبي يموت ويقيم في قبره إلا أربعين صباحا. . .» الحديث. رواه الحسن بن يحيى الخشني، عن','267','645','0');
INSERT INTO [t1578] VALUES (647,'- «ما أنفقت الورق في شيء أحب إلى الله من نحيرة تنحر في يوم عيد» . رواه إبراهيم الخوزي، عن عمرو','268','645','0');
INSERT INTO [t1578] VALUES (648,'- «ما وقى به المرء عرضه صدقة، وما أنفق الرجل على أهله صدقة. . .» الحديث. رواه مسور بن الصلت،','268','645','0');
INSERT INTO [t1578] VALUES (649,'- «ما السحت؟ . . .» الحديث. رواه بشر بن إبراهيم البصري المفلوج، عن الأوزاعي، عن عبد الرحمن بن','269','645','0');
INSERT INTO [t1578] VALUES (650,'- «ما أوذي أحد ما أوذيت» . رواه محمد بن سليمان بن هشام الخزاز ابن بنت مطر، عن وكيع، عن مالك،','269','645','0');
INSERT INTO [t1578] VALUES (651,'يستفتح دعاء إلا استفتحه بسبحان ربي الأعلى. رواه عمر بن راشد اليمامي، عن إياس بن سلمة بن','269','645','0');
INSERT INTO [t1578] VALUES (652,'- «ما الذي يعطي من سعة بأعظم أجرا من الذي يأخذ إذا كان محتاجا» . رواه عائذ بن شريح، عن أنس.','270','645','0');
INSERT INTO [t1578] VALUES (653,'- «ما حسدونا اليهود على شيء ما حسدونا على السلام وآمين» . رواه عبد الله بن ميسرة أبو إسحاق، عن','270','645','0');
INSERT INTO [t1578] VALUES (654,'- «ما عمل عبد ذنبا فساءه إلا غفر له. . .» الحديث رواه بشر بن إبراهيم البصري، عن الأوزاعي، عن','271','645','0');
INSERT INTO [t1578] VALUES (655,'- ما ينبغي للعرب من التجارة، قال: «الغنم والسمن والإبل. . .» الحديث. رواه عمران بن أبي الفضل،','271','645','0');
INSERT INTO [t1578] VALUES (656,'- «ما من يوم يصبح فيه إنسان إلا استقبل الروح الجسد. . .» الحديث. رواه أبو هدبة إبراهيم بن','271','645','0');
INSERT INTO [t1578] VALUES (657,'- «ما أسكر كثيره فقليله حرام» . رواه سفيان الفزاري، عن سفيان بن عيينة، عن محمد بن المنكدر، عن','272','645','0');
INSERT INTO [t1578] VALUES (658,'- «ما خلا يهودي قط بمسلم إلى حدثت نفسه بقتله» . رواه يحيى بن عبيد الله بن موهب، عن أبيه، عن','272','645','0');
INSERT INTO [t1578] VALUES (659,'- «ما من حافظين يرفعان إلى الله عز وجل ما حفظا» . رواه تمام بن نجيح الملطي، عن الحسن، عن أنس.','272','645','0');
INSERT INTO [t1578] VALUES (660,'- «ما رأيت للروم مثل مدينة أنطاكية. . .» الحديث. وفيه «إن فيها التوراة وعصا موسى ورضراض','273','645','0');
INSERT INTO [t1578] VALUES (661,'- «ما عظمت نعمة الله على عبد إلا عظمت مؤنة الناس عليه» . رواه محمد بن عبد الله بن علاثة','273','645','0');
INSERT INTO [t1578] VALUES (662,'- ما صليت وراء أحد أشبه صلاة برسول الله صلى الله عليه وسلم من هذا الفتى - يعني عمر بن عبد','274','645','0');
INSERT INTO [t1578] VALUES (663,'- «ما في الجنة شجرة أو قال ورقة إلا عليها مكتوب لا إله إلا الله محمد رسول الله، أبو بكر','274','645','0');
INSERT INTO [t1578] VALUES (664,'- «ما من معمر يعمر في الإسلام أربعين سنة. . .» الحديث. رواه يوسف بن أبي ذرة، عن جعفر بن عمرو','275','645','0');
INSERT INTO [t1578] VALUES (665,'- «ما ابتعث الله نبيا قط إلا كان في أمته مرجئة وقدرية، يشوشون عليه أمر أمته، ألا وإن القدرية','275','645','0');
INSERT INTO [t1578] VALUES (666,'- «ما من رجل يمر بقبر رجل كان يعرفه في الدنيا فيسلم عليه إلا عرفه ورد عليه السلام» . رواه عبد','276','645','0');
INSERT INTO [t1578] VALUES (667,'- «ما قعد يتيم على قصعة قوم فيقرب قصعتهم الشيطان» . رواه الحسن بن دينار، عن الأسود بن عبد','276','645','0');
INSERT INTO [t1578] VALUES (668,'- «ما زال جبريل يوصيني بالمملوك حتى ظننت أنه يضرب له أجلا» . رواه الحسن بن علي الهاشمي، عن','277','645','0');
INSERT INTO [t1578] VALUES (669,'- "ما من دعاء أحب إلى الله عز وجل من قول العبد: اللهم اغفر لأمة محمد رحمة عامة ". رواه عمرو بن','277','645','0');
INSERT INTO [t1578] VALUES (670,'- «ما استودع الله عبدا عقلا إلا استنقذه به يوما ما» . رواه أبو حذافة، عن حاتم بن إسماعيل، عن','278','645','0');
INSERT INTO [t1578] VALUES (671,'- ما حق الزوج على زوجته؟ قال «لا تمنعه نفسها. . .» الحديث. رواه ليث بن أبي سليم، عن عبد','278','645','0');
INSERT INTO [t1578] VALUES (672,'- «ما صبر أهل بيت على ضيق إلا أتاهم الله برزق» . رواه أبو رجاء الجزري، عن فرات بن سلمان، عن','278','645','0');
INSERT INTO [t1578] VALUES (673,'- «ما منكم أحد غني ولا فقير إلا يود يوم القيامة أنه أوتي في الدنيا قوتا» . رواه نفيع بن الحارث','279','645','0');
INSERT INTO [t1578] VALUES (674,'- «ما طاب رائحة عبد قط إلا قل همه. . .» الحديث. رواه دينار بن عبد الله، عن أنس. ودينار كذاب','279','645','0');
INSERT INTO [t1578] VALUES (675,'- «ما مسخت أمة قط فتكون لها ناسلة» . رواه مسلمة بن يحيى الخشني الدمشقي، عن الأوزاعي، عن','279','645','0');
INSERT INTO [t1578] VALUES (676,'-: ما من مسلمين يلتقيان يتصافحان. . . " الحديث. رواه درست، عن زياد، عن قتادة، عن أنس. ودرست','280','645','0');
INSERT INTO [t1578] VALUES (677,'- «ما عمل ابن آدم من عمل يوم النحر أحب إلى الله عز وجل من هراقة دم. . .» الحديث. رواه أبو','280','645','0');
INSERT INTO [t1578] VALUES (678,'باب مثل','281','644','0');
INSERT INTO [t1578] VALUES (679,'- «مثل المريض إذا برأ أو صح من مرضه مثل البردة. . .» . الحديث. رواه الوليد بن محمد الموقري،','281','678','0');
INSERT INTO [t1578] VALUES (680,'- «مثل أمتي مثل المطر، لا يدرى أوله خير أو آخره» . رواه هشام بن عبيد الله الرازي، عن مالك، عن','281','678','0');
INSERT INTO [t1578] VALUES (681,'- «مثل الصلوات الخمس» . رواه أحمد بن محمد الأنصاري أبو عقبة، عن عبد الأعلى بن عبد الأعلى، عن','282','678','0');
INSERT INTO [t1578] VALUES (682,'باب الأفراد','282','644','0');
INSERT INTO [t1578] VALUES (683,'- «مفتاح الصلاة الطهور. . .» . رواه محمد بن موسى بن مسكين قاضي المدينة أبو غزية، عن فليح بن','283','682','0');
INSERT INTO [t1578] VALUES (684,'- «مرد دانق من حرام أفضل عند الله من سبعين حجة» . رواه أحمد بن محمد بن الصلت أبو العباس','283','682','0');
INSERT INTO [t1578] VALUES (685,'- «مضغتان لا تموتان الإنفحة والبيض» . رواه بشر بن إبراهيم البصري المفلوج، عن الأوزاعي، عن يحيى','284','682','0');
INSERT INTO [t1578] VALUES (686,'«الدنيا أهون على الله من هذه. . .» الحديث. رواه محمد بن مصعب القرقساني، عن الأوزاعي، عن','284','682','0');
INSERT INTO [t1578] VALUES (687,'- «مرض يوم يكفر سنة» رواه إسحاق بن بشر الكاهلي أبو حذيفة، أصله من بلخ عن الثوري، عن هشام، عن','285','682','0');
INSERT INTO [t1578] VALUES (688,'- «مكارم الأخلاق عشرة تكون في الرجل ولا تكون في أبيه. . .» الحديث. رواه الوليد بن الوليد','285','682','0');
INSERT INTO [t1578] VALUES (689,'- «مروا نساءكم بالغزل، فإنه أزين لهن وخير» . رواه عنبسة بن عبد الرحمن القرشي، عن عبد الرحمن بن','285','682','0');
INSERT INTO [t1578] VALUES (690,'- «منهومان لا يشبعان طالب علم وطالب دنيا» . رواه أبو بكر الداهري عبد الله بن حكيم، عن إسماعيل','286','682','0');
INSERT INTO [t1578] VALUES (691,'- «مروا صبيانكم بالصلاة إذا بلغوا سبعا، واضربوهم عليها إذا بلغوا عشرا» . رواه داود بن سوار','286','682','0');
INSERT INTO [t1578] VALUES (692,'وقد أسس قباء وما معه إلا هؤلاء الثلاثة أبو بكر وعمر وعثمان. رواه محمد بن الفضل بن عطية','287','682','0');
INSERT INTO [t1578] VALUES (693,'في كل أربعين فما فوقها جمعة. . . ". الحديث. رواه عبد العزيز بن عبد الرحمن الجزري، عن غضيف،','287','682','0');
INSERT INTO [t1578] VALUES (694,'- «موت الفجأة أخذة أسف» . في حديث رواه درست بن زياد، عن يزيد الرقاشي، عن أنس. ودرست متروك','287','682','0');
INSERT INTO [t1578] VALUES (695,'- «مهور الحور العين قبضات التمر وفلق الخبز» . رواه عمر بن صبح، عن مقاتل بن حيان، عن الأعرج، عن','288','682','0');
INSERT INTO [t1578] VALUES (696,'- «معلمو صبيانكم شراركم، أقلهم رحمة لليتيم، وأغلظهم على المساكين» . رواه سعد بن طريف بن بركات','288','682','0');
INSERT INTO [t1578] VALUES (697,'باب من المكسورة','289','644','0');
INSERT INTO [t1578] VALUES (698,'- «من كرامة المؤمن على الله أن يغفر لمشيعيه» . رواه عبد الرحمن بن قيس الزعفراني، عن محمد بن','289','697','0');
INSERT INTO [t1578] VALUES (699,'- «من حق الولد على الوالد أن يعلمه كتاب الله والسباحة والرمي» . رواه أبو العطوف الجراح بن','289','697','0');
