INSERT INTO [t1578] VALUES (899,'أن ينام الرجل على أجار ليس بمحجور عليه. رواه عبد الجبار بن عمرو الأبلي، عن ابن المنكدر،','360','893','0');
INSERT INTO [t1578] VALUES (900,'عن حلق القفا إلا للحجامة. رواه سعيد بن بشير، عن قتادة، عن أنس، عن عمر بن الخطاب. وسعيد','361','893','0');
INSERT INTO [t1578] VALUES (901,'أن يكون الإمام مؤذنا. رواه معلى بن هلال الطحان، عن محمد بن سوقة، عن محمد بن المنكدر، عن','361','893','0');
INSERT INTO [t1578] VALUES (902,'أن يقطع اللحم بالسكين. رواه نوح بن أبي مريم، عن يحيى بن سعيد الأنصاري، عن سعيد بن المسيب،','361','893','0');
INSERT INTO [t1578] VALUES (903,'عن إخصاء الخيل والبقر والغنم وقال: «النماء في الخيل» . رواه عبد الله بن نافع، عن أبيه، عن','362','893','0');
INSERT INTO [t1578] VALUES (904,'أن تتبع جنازة فيها صارخة. رواه حماد بن قيراط، عن عبيد الله بن عمر، عن نافع، عن ابن عمر.','362','893','0');
INSERT INTO [t1578] VALUES (905,'- نهى عن ضرب البهائم. . . الحديث. رواه إبراهيم الخوزي، عن عمرو بن دينار، عن ابن عمر.','362','893','0');
INSERT INTO [t1578] VALUES (906,'رجل لا يقيم صلبه في الركوع. . . الحديث. رواه أحمد بن محمد الأنصاري، عن عبد الأعلى بن عبد','363','893','0');
INSERT INTO [t1578] VALUES (907,': «هذا في الجنة، وإن من شيعته قوم يسمون الرافضة. . .» الحديث. رواه تليد بن سليمان المحاربي،','363','893','0');
INSERT INTO [t1578] VALUES (908,'رجل ساجد ذي جمة وهو يرفع شعره بيده لا يصيبه التراب. . . الحديث. رواه عبد الله بن محرر، عن','364','893','0');
INSERT INTO [t1578] VALUES (909,'- «نظر الرجل إلى عورة أخيه كنظره إلى الفرج الحرام» . رواه زيد بن جبيرة الأوسي، عن داود بن','364','893','0');
INSERT INTO [t1578] VALUES (910,'- «نعم المفتاح الهدية أمام الحاجة» . رواه عمرو بن خالد الأعشى، عن هشام بن عروة، عن أبيه، عن','364','893','0');
INSERT INTO [t1578] VALUES (911,': «نعم الإدام الخل» . وهذا الحديث ليس عند هؤلاء عن ابن عمر، وإنما يعرف من حديث أبو الزبير وأبي','365','893','0');
INSERT INTO [t1578] VALUES (912,'الله عز وجل قتل بيحيى بن زكريا سبعين ألفا، فإنه قاتل بابن ابنتك الحسين بن علي سبعين ألفا','365','893','0');
INSERT INTO [t1578] VALUES (913,'- «نبات الشعر في الأنف أمان من الجذام» . رواه يحيى بن هاشم السمسار، عن أبيه، عن عائشة. ويحيى','366','893','0');
INSERT INTO [t1578] VALUES (914,'- نفلنا رسول الله نفلا سوى نصيبنا. . . الحديث. رواه إسحاق بن إدريس الأسواري من أهل البصرة،','366','893','0');
INSERT INTO [t1578] VALUES (915,'- نادى النبي صلى الله عليه وسلم رجلا فقال: لبيك. . . الحديث. رواه جبارة بن مغلس، عن حماد بن','366','893','0');
INSERT INTO [t1578] VALUES (916,'باب الواو','367','0','0');
INSERT INTO [t1578] VALUES (917,'- «والله لأغزون قريشا» ثم قال عند الثالثة: «إن شاء الله» . رواه محمد بن إسحاق البلخي، عن ابن','367','916','0');
INSERT INTO [t1578] VALUES (918,'- وقت رسول للنفساء أربعين يوما. . . الحديث. رواه عطاء بن عجلان العطار البصري، عن ابن أبي','367','916','0');
INSERT INTO [t1578] VALUES (919,'- {ونفضل بعضها على بعض في الأكل} [الرعد: ٤] قال: «الدقل والفارسي والحلو والحامض» . رواه سيف بن','368','916','0');
INSERT INTO [t1578] VALUES (920,'يخلل لحيته بالماء بأصابعه من تحت حنكه. رواه عمر بن حفص العبدي، عن ثابت، عن أنس. وعمر هذا','368','916','0');
INSERT INTO [t1578] VALUES (921,'- " ولد نوح ثلاثة: حام وسام ويافث، فولد سام. . . " الحديث. رواه يزيد بن سنان أبو فروة، عن يحيى','369','916','0');
INSERT INTO [t1578] VALUES (922,'- «ويل للذين يمسون فروجهم ثم يصلون. . .» الحديث. رواه عبد الرحمن بن عبد الله بن عمر العمري،','369','916','0');
INSERT INTO [t1578] VALUES (923,'- «ولد الزنا لا يرث ولا يورث» . رواه محمد بن عبد الرحمن البيلماني، عن أبيه، عن ابن عمر. ومحمد','369','916','0');
INSERT INTO [t1578] VALUES (924,'باب الهاء','369','0','0');
INSERT INTO [t1578] VALUES (925,'- «هو الطهور ماؤه الحل ميتته» . رواه عبد الله بن محمد بن ربيعة القدامي، عن إبراهيم بن سعيد، عن','369','924','0');
INSERT INTO [t1578] VALUES (926,'- «هذا العباس عمي ووارثي. . .» الحديث. رواه محمد بن الضوء، عن أبيه الضوء، عن جده الصلصال.','371','924','0');
INSERT INTO [t1578] VALUES (927,'- «هل تدري كيف حكم الله فيمن بغى من هذه الأمة؟» . رواه كوثر بن حكيم، عن نافع، عن ابن عمر.','371','924','0');
INSERT INTO [t1578] VALUES (928,'في الجنة من سماع؟ قال: نعم، ثنا أعرابي. . . رواه سليمان بن عطاء، عن مسلمة بن عبد الله، عن','371','924','0');
INSERT INTO [t1578] VALUES (929,'- «هدية الله إلى المؤمن السائل على بابه» . رواه سعيد بن موسى الأزدي، عن مالك، عن نافع، عن ابن','371','924','0');
INSERT INTO [t1578] VALUES (930,'باب لا','372','0','0');
INSERT INTO [t1578] VALUES (931,'- «لا إله إلا الله كلامي، وأيما مؤمن قالها مخلصا دخل الجنة حصني. . .» الحديث. رواه أبو أشرس','372','930','0');
INSERT INTO [t1578] VALUES (932,'- «لا بأس بأكل كل طير ما خلا البوم والرحمة» ، رواه عبد الله بن زياد بن سمعان، عن نافع، عن ابن','373','930','0');
INSERT INTO [t1578] VALUES (933,'- «لا بأس أن يقلب الرجل الجارية إذا أراد أن يشتريها وينظر إليها. . .» الحديث. رواه صالح بن','373','930','0');
INSERT INTO [t1578] VALUES (934,'- «لا بأس بذبيحة العود والحجر والقصبة إذا أهرق الدم» . رواه يحيى بن أبي أنيسة، عن أبي الزبير،','373','930','0');
INSERT INTO [t1578] VALUES (935,'- «لا تأخذوا العلم إلا ممن تجيزون شهادتهم» . رواه حفص بن عمر قاضي حلب، عن هشام بن حسان، عن','374','930','0');
INSERT INTO [t1578] VALUES (936,'- «لا تظهر الشماتة لأخيك فيعافه الله ويبتليك. . .» الحديث. رواه القاسم بن أمية الحذاء، عن حفص','374','930','0');
INSERT INTO [t1578] VALUES (937,'- «لا تذهب الدنيا حتى يستغني الرجال بالرجال والنساء بالنساء» . رواه ابن عوف القرشي، عن بكار بن','375','930','0');
INSERT INTO [t1578] VALUES (938,'- " لا تزول قدما عبد من بين يدي الرحمن حتى يسأل عن أربع: شبابك فيما أبليت. . . " الحديث. رواه','375','930','0');
INSERT INTO [t1578] VALUES (939,'- «لا تكرهوا مرضاكم على الطعام» . رواه محمد بن عمر بن الوليد، عن مالك، عن نافع، عن ابن عمر.','376','930','0');
INSERT INTO [t1578] VALUES (940,'- «لا تقوم الساعة على مؤمن حتى يبعث الله بين الساعة ريحا طيبة. . .» الحديث. رواه موسى بن مطير','376','930','0');
INSERT INTO [t1578] VALUES (941,'- «لا تستخدموا أرقاءكم بالليل، فإن الليل لهم والنهار لكم» . رواه يحيى بن هشام السمسار، عن هشام','377','930','0');
INSERT INTO [t1578] VALUES (942,'- «لا تنزلوهن الغرف، ولا تعلموهن الكتاب، وعلموهن المغزل وسورة النور» . رواه محمد بن إبراهيم','377','930','0');
INSERT INTO [t1578] VALUES (943,'- «لا تلقوا الدر في أفواه الكلاب» . رواه علي بن سعيد الرقي، عن يزيد بن هارون، عن شعبة، عن محمد','377','930','0');
INSERT INTO [t1578] VALUES (944,'- " لا تجوز شهادة خائن ولا خائنة ولا مجلود في حد ولا مجرب في شهادة ولا ظنين ولا ذي غمر على','378','930','0');
INSERT INTO [t1578] VALUES (945,'- «لا تكلموا في القدر، فإنه سر الله. . .» الحديث. رواه الهيثم بن جماز الحنفي، عن عمران','378','930','0');
INSERT INTO [t1578] VALUES (946,'- «لا تقربوا اليهود والنصارى في أعيادهم. . .» الحديث. رواه أحمد بن إبراهيم المزني، عن محمد بن','379','930','0');
INSERT INTO [t1578] VALUES (947,'- «لا تجعلوني كقدح الراكب، فإن الراكب يملأ قدحه. . .» الحديث. رواه موسى بن عبيدة الربذي، عن','379','930','0');
INSERT INTO [t1578] VALUES (948,'- " لا تقطعوا اللحم بالسكين. . . الحديث. رواه نجيح السندي، عن هشام، عن أبيه، عن عائشة رضي','379','930','0');
INSERT INTO [t1578] VALUES (949,'- «لا تشتر من الإماء إلا صناعة اليدين» . رواه مالك بن سليمان أبو غسان النهشلي، عن يزيد بن','380','930','0');
INSERT INTO [t1578] VALUES (950,'- «لا تأكلن متكئا ولا على غربال» . رواه رزيق بن عبد الله الألهاني الشامي، عن عمرو بن الأسود،','380','930','0');
INSERT INTO [t1578] VALUES (951,'- «لا تغالوا بالشاء، فإنما هو سقيا من الله، وإذا حلبتم فادعوا للبن داعيا. . .» الحديث. رواه','380','930','0');
INSERT INTO [t1578] VALUES (952,'- «لا تفوت صلاة ليل في ليل ولا نهار في نهار، ولكن التضييع فيما بين ذلك» . رواه محمد بن عبد','381','930','0');
INSERT INTO [t1578] VALUES (953,'- «لا تكن أول من يدخل السوق، ولا آخر من يخرج منها، فإنها معركة الشيطان. . .» الحديث. رواه','381','930','0');
INSERT INTO [t1578] VALUES (954,'- «لا تستأذنوا لمن لا يبدأ بالسلام» . رواه إبراهيم بن يزيد الخوزي، عن أبي الزبير، عن جابر.','382','930','0');
INSERT INTO [t1578] VALUES (955,'- " لا تبكين إلا لأحد رجلين: فاجر مكمل فجوره وبار مكمل بره ". رواه راشد بن سعد، عن يونس بن','382','930','0');
INSERT INTO [t1578] VALUES (956,'- «لا تدخل الملائكة بيتا فيه جلد نمر» . رواه سعيد بن بشير، عن أبي الزبير، عن جابر. وهذا أحد ما','382','930','0');
INSERT INTO [t1578] VALUES (957,'- «لا حول ولا قوة إلا بالله دواء من تسعة وتسعين داء أيسرها الهم» . رواه بشر بن رافع النجراني،','383','930','0');
INSERT INTO [t1578] VALUES (958,'- «لا حسد ولا ملق إلا في طلب العلم» . رواه محمد بن علاثة، عن الأوزاعي، عن الزهري، عن أبي سلمة،','383','930','0');
INSERT INTO [t1578] VALUES (959,'- «لا خير فيمن لا يجمع المال، يصل به رحمه، ويؤدي به عن أمانته، ويستعين به» . رواه العلاء بن','384','930','0');
INSERT INTO [t1578] VALUES (960,'- «لا خير في صحبة من لا يرى لك من الحق. . .» الحديث. رواه بكار بن شعيب، عن أبي حازم، عن أبيه،','384','930','0');
INSERT INTO [t1578] VALUES (961,'- «لا خير في التجارة إلا كسب تاجر إن باع. . .» . الحديث. رواه بشر بن رافع النجراني، عن يحيى','385','930','0');
INSERT INTO [t1578] VALUES (962,'- «لا شفعة لصغير ولا لغالب، وإذا سبق الشريك شريكه بالشفعة فلا شفعة، والشفعة كحل العقال» . رواه','385','930','0');
INSERT INTO [t1578] VALUES (963,'- «لا صلاة لجار المسجد إلا في المسجد» . رواه عمر بن راشد، عن ابن ذئب، عن الزهري، عن عروة، عن','385','930','0');
INSERT INTO [t1578] VALUES (964,'- «لا فقر أشد من الجهل، ولا مال أعود من العقل. . .» الحديث بطوله. رواه محمد بن عبد الله','386','930','0');
INSERT INTO [t1578] VALUES (965,'- «لا طلاق قبل نكاح، ولا عتق لمن لا يملك، ولا صمت يوما إلى الليل، ولا رهبانية فينا» . رواه','386','930','0');
INSERT INTO [t1578] VALUES (966,'«لا تنكحوا النساء إلا من الأكفاء» عن أبي الزبير، عن جابر، وهذا التخليط من','387','930','0');
INSERT INTO [t1578] VALUES (967,'- «لا نكاح إلا بولي وشاهدي عدل» . رواه عبد الله بن محرر، عن قتادة، عن الحسن، عن عمران بن حصين.','387','930','0');
INSERT INTO [t1578] VALUES (968,'- «لا هم إلا هم الدين، ولا وجع إلا وجع العين» . رواه سهل بن قرين، عن ابن أبي ذئب، عن محمد بن','388','930','0');
INSERT INTO [t1578] VALUES (969,'- «لا تقطع الصلاة امرأة ولا كلب. . .» الحديث. رواه إسحاق بن أبي فروة، عن زيد بن أسلم، عن','389','930','0');
INSERT INTO [t1578] VALUES (970,'- «لا يقبل الله قولا إلا بعمل، ولا يقبل قولا وعملا إلا بالنية. . .» الحديث. رواه أحمد بن','389','930','0');
INSERT INTO [t1578] VALUES (971,'- «لا يجتمع على مسلم خراج وعشر» . رواه يحيى بن عنبسة، عن أبي حنيفة، عن حماد، عن إبراهيم، عن','390','930','0');
INSERT INTO [t1578] VALUES (972,'- «لا يزال أربعون رجلا يحفظ الله بهم الأرض، كلما مات منهم رجل أبدل الله مكانه آخر، هم في','390','930','0');
INSERT INTO [t1578] VALUES (973,'- «لا يبغي على الناس إلا ابن بغية أو فيه عرق منها» . رواه سهل الأعرابي، عن بلال بن أبي بردة،','391','930','0');
INSERT INTO [t1578] VALUES (974,'- «لا يركب البحر إلا حاج أو معتمر أو غاز» . رواه ليث بن أبي سليم، عن نافع، عن ابن عمر. وليث','391','930','0');
INSERT INTO [t1578] VALUES (975,'- «لا يلدغ المؤمن من جحر مرتين» ، رواه عبد السلام بن أبي فروة النصيبي، عن ابن عيينة، عن أبي','391','930','0');
INSERT INTO [t1578] VALUES (976,'- «لا يقطع الصلاة شيء وادرأ ما استطعت» . رواه إبراهيم الخوزي، عن سالم بن عبد الله، عن ابن عمر.','392','930','0');
INSERT INTO [t1578] VALUES (977,'- «لا يحل لامرأة أن تبيت ليلة حتى تعرض نفسها على زوجها» . رواه جعفر بن ميسرة، عن أبيه، عن ابن','392','930','0');
INSERT INTO [t1578] VALUES (978,'- «لا يؤذن لكم من يدغم الهاء» . رواه علي بن جميل الرقي، عن عيسى بن يونس، عن الأعمش، عن أبي','392','930','0');
INSERT INTO [t1578] VALUES (979,'- «لا يدخل ولد الزنا ولا شيء من نسله إلى سبعة آباء الجنة» . رواه إبراهيم بن مهاجر البجلي','393','930','0');
INSERT INTO [t1578] VALUES (980,'- «لا يعجبنكم جمع مال من غير حله. . .» الحديث. رواه الحسين بن قيس الرحبي - ولقبه حنش - عن','393','930','0');
INSERT INTO [t1578] VALUES (981,'- «لا يحرم الحلال الحرام، وإنما يحرم الحرام ما كان من نكاح حلال» . رواه عثمان بن عبد الرحمن','393','930','0');
INSERT INTO [t1578] VALUES (982,'- «لا يعجبنكم إسلام امرئ حتى تعلموا ما عقدة عقله» . رواه إسحاق بن أبي فروة، عن نافع، عن ابن','394','930','0');
INSERT INTO [t1578] VALUES (983,'- «لا يمسح الرجل جبهته من التراب حتى يفرغ من الصلاة. . .» الحديث. رواه عثمان بن عبد الرحمن','394','930','0');
INSERT INTO [t1578] VALUES (984,'- " لا يبيت أحدكم وعند رأسه طعام، فإني لا آمن عليه الهوام. . . الحديث. رواه يحيى بن هاشم، عن','394','930','0');
INSERT INTO [t1578] VALUES (985,'- «لا يحبس إنسان في الدين أكثر من أربعين يوما» . رواه يونس بن عطاء الصدائي، عن حميد، عن أنس.','395','930','0');
INSERT INTO [t1578] VALUES (986,'- «لا يحرم من الرضاع إلا عشر رضعات» . رواه محمد بن عمر الواقدي، عن هشام بن سعد، عن زيد بن','395','930','0');
INSERT INTO [t1578] VALUES (987,'باب الياء','395','0','0');
INSERT INTO [t1578] VALUES (988,'أرأيت الأرض على ما هي؟ قال: «على الماء. . .» الحديث بطوله. رواه سعيد بن سنان الكندي، عن أبي','395','987','0');
INSERT INTO [t1578] VALUES (989,'المدينة لا تصلح لا بي وبك، وأنت مني بمنزلة هارون من موسى» .','396','987','0');
INSERT INTO [t1578] VALUES (990,': «ارم فداك أبي وأمي» فحمل حفص متن يزيد بن عياض على مالك عن الزهري عن سعيد متوهما، وقرن إليه','396','987','0');
INSERT INTO [t1578] VALUES (991,'كذب من زعم أنه يحبني ويبغضك. . .» الحديث. رواه محمد بن الضوء بن الصلصال، عن أبيه، عن جده','397','987','0');
INSERT INTO [t1578] VALUES (992,'خصلتين هما أخف على الظهر وأثقل في الميزان؟ . . .» الحديث. رواه بشار بن الحكم الضبي، عن','397','987','0');
INSERT INTO [t1578] VALUES (993,'للمسجد تحية فقم فاركعهما. . .» ثم ذكر الحديث بطوله في وصية أبي ذر. رواه يحيى بن سعد','397','987','0');
INSERT INTO [t1578] VALUES (994,'أصبح بالفجر، فإنه أعظم للأجر» . رواه أيوب بن سيار الزهري، عن محمد بن المنكدر، عن جابر، عن','398','987','0');
INSERT INTO [t1578] VALUES (995,'إذا ركبت دابة يقال لها: الهملاج. . . الحديث. وفيه ذكر بيت المقدس. رواه الفرات بن سليم، عن','398','987','0');
INSERT INTO [t1578] VALUES (996,'زيدي على البيتين قالهما اليهودي: ارفع ضعيفك. . . البيتين ". رواه سهل مولى المغيرة، عن حسين','398','987','0');
