INSERT INTO [t1613] VALUES (1,'كان يرفع يديه إذا كبر للصلاة حذو منكبيه , وإذا أراد أن يركع , وإذا رفع رأسه من الركوع ,','3','0','0');
INSERT INTO [t1613] VALUES (2,'يرفع يديه إذا كبر , وإذا ركع , وإذا رفع رأسه من الركوع , ولا يفعل ذلك بين','4','0','0');
INSERT INTO [t1613] VALUES (3,'إذا قام إلى الصلاة رفع يديه , وإذا ركع , وإذا رفع رأسه من الركوع , وإذا قام من الركعتين','5','0','0');
INSERT INTO [t1613] VALUES (4,'قام فكبر فرفع يديه، ثم رفع يديه حين كبر للركوع , ثم ركع فوضع يديه على','6','0','0');
