INSERT INTO [t1613] VALUES (11,'يرفع يديه حذو منكبيه إذا افتتح الصلاة , وإذا كبر للركوع , وإذا رفع رأسه من الركوع رفعهما','13','0','0');
INSERT INTO [t1613] VALUES (12,'إذا رفع رأسه من السجود فأراد أن يقوم رفع يديه','14','0','0');
