INSERT INTO [t1613] VALUES (15,'كان عبد الله بن عامر يسألني أن أستأذن له على عمر بن عبد العزيز فاستأذنت له عليه فقال:','17','0','0');
INSERT INTO [t1613] VALUES (16,'رأيت ابن عباس , وابن الزبير , وأبا سعيد , وجابرا يرفعون أيديهم إذا افتتحوا الصلاة , وإذا','18','0','0');
INSERT INTO [t1613] VALUES (17,'إذا كبر رفع يديه , وإذا ركع , وإذا رفع رأسه من الركوع','19','0','0');
