INSERT INTO [t16562] VALUES (394,'المسألة الثالثة يشترط أن يكون الراوي مسلما، فمن لا يكون كذلك ولم يكن من أهل قبلتنا كأهل الكتاب وغيرهم فإنه لا تقبل روايته إجماعا','2642','391','0');
