INSERT INTO [t1714] VALUES (63,'أحمد بن عبد الله بن أيوب بن زياد بن عطاش أبو بكر الأصبهاني قديم الموت، يروي عن أبي عاصم النبيل، وأبي عمر الضرير','355','30','0');
