INSERT INTO [t1714] VALUES (66,'أحمد بن يزيد أبو جعفر القطان من الحفاظ، كثير الرواية عن أبي داود، وقتيبة بن مهران، حدث عنه جعفر بن أحمد بن فارس، وإسماعيل بن عبد الله سمويه ذكره أبو عبد الله الغزال، روى عن أبي داود عن الحسن بن دينار، وقال: أبوه واصل، ودينار زوج أمه','360','30','0');
INSERT INTO [t1714] VALUES (67,'أحمد بن مهران بن خالد الأصبهاني أبو جعفر يروي عن إسماعيل بن عمرو البجلي، وخالد بن مخلد، والحسن بن قتيبة، كان لا يخرج من بيته إلا إلى الصلاة توفي بيزد سنة أربع وثمانين ومائتين','362','30','0');
