INSERT INTO [t1714] VALUES (75,'أحمد بن إبراهيم بن عبد الله المؤدب سكن قرية هفشور، روى عن محمد بن المثنى، وسلمة بن شبيب، والمقرئ','381','30','0');
INSERT INTO [t1714] VALUES (76,'أحمد بن يحيى بن حمزة بن زكرياء بن موسى بن المغيرة الثقفي يعرف بوشجة، وقيل: خشجة توفي سنة إحدى أو اثنتين وثمانين، روى عن الحسين بن حفص ومحمد بن أبان العنبري','383','30','0');
