INSERT INTO [t1714] VALUES (120,'أحمد بن المساور بن سهيل بن المساور بن موسى بن المساور الضبي أبو جعفر شيخ ثقة، يروي عن سعدويه، وسهل بن عثمان، وعلي بن بشر، وعن موسى بن المساور','503','30','0');
INSERT INTO [t1714] VALUES (121,'أحمد بن إبراهيم بن أبي يحيى واسم أبي يحيى يزيد بن عبد الله الباهلي أبو العباس المكتب من أهل المدينة، توفي سنة أربع وثلاثمائة في ربيع الأول، يروي عن نصر بن علي، وعبد الله بن محمد الزهري، والناس، كثير الحديث','506','30','0');
