INSERT INTO [t1714] VALUES (289,'أحمد بن علي بن بسام البغدادي أبو الحسين يعرف بابن سبك الديناري، قدم أصبهان سنة أربعين وثلاثمائة، كتب بالعراق وبخراسان. يروي عن عبد الله بن إسحاق المدائني وطبقته','883','30','0');
INSERT INTO [t1714] VALUES (290,'أحمد بن إبراهيم الكيال المؤدب أبو عبد الله سمع بخراسان من عبد الله بن محمود المروزي وغيره، وسمع بإصبهان، توفي سنة أربع وأربعين وثلاثمائة','884','30','0');
INSERT INTO [t1714] VALUES (291,'أحمد بن المؤمل بن أحمد بن خالد النيسابوري أبو العباس قدم أصبهان سنة تسع وثلاثين وثلاثمائة، حدث عن محمد بن إسحاق السراج','886','30','0');
INSERT INTO [t1714] VALUES (292,'أحمد بن محمد بن الحسين بن حمك الرازي أبو بكر قدم أصبهان يكتب سنة ست وثلاثين. يروي عن محمد بن جعفر البغدادي , عن أبي حذافة , عن مالك , عن نافع , عن ابن عمر: " أن النبي صلى الله عليه وسلم قضى باليمين مع الشاهد "','887','30','0');
INSERT INTO [t1714] VALUES (293,'أحمد بن محمد أبو الفضل الجارودي الهروي قدم أصبهان، يعرف الحديث ويذاكر به سنة إحدى وستين وثلاثمائة، كان يصحبنا','888','30','0');
INSERT INTO [t1714] VALUES (294,'أحمد بن محمد بن محمد بن يوسف السمسمي أبو الفضل البلخي قدم أصبهان سنة ثمان وخمسين وثلاثمائة، يكتب الحديث','890','30','0');
INSERT INTO [t1714] VALUES (295,'أحمد بن محمد بن علي بن أحمد أبو الحسن النسائي قدم أصبهان','892','30','0');
INSERT INTO [t1714] VALUES (296,'أحمد بن علي بن محمد بن عبد الرحيم أبو علي الرازي الحافظ قدم أصبهان في غيبتي عنها , كثير الحديث , روى عن أحمد بن محمد بن معاوية الرازي، وعلي بن مهرويه القزويني','894','30','0');
INSERT INTO [t1714] VALUES (297,'أحمد بن يوسف بن بشر المروزي قدم أصبهان وحدث بها','895','30','0');
INSERT INTO [t1714] VALUES (298,'أحمد بن محمد بن العلاء أبو الفرج الصوفي شيرازي، سكن بغداد. قدم أصبهان سنة اثنتين وخمسين، يروي عن ابن صاعد وغيره','897','30','0');
INSERT INTO [t1714] VALUES (299,'أحمد بن الحسين الأسدي أبو العلاء الشاعر بغدادي , قدم أصبهان وسكنها حتى مات','898','30','0');
INSERT INTO [t1714] VALUES (300,'أحمد بن محمد بن جعفر الخفاف أبو بكر روى عن محمد بن عيسى المقرئ، روى عنه أبو إسحاق السريجاني المديني','899','30','0');
INSERT INTO [t1714] VALUES (301,'أحمد بن مالك بن يحيى بن الأحنف بن قيس الواذناني روى عنه أبو إسحاق السريجاني','900','30','0');
INSERT INTO [t1714] VALUES (302,'أحمد بن بشر بن الفاخر الأصبهاني روى عن أبي مسعود','901','30','0');
INSERT INTO [t1714] VALUES (303,'أحمد بن سفيان بن معاوية العبدي روى عن هارون بن سليمان، وأحمد بن يونس','903','30','0');
INSERT INTO [t1714] VALUES (304,'أحمد بن شعيب بن عيسى أبو محمد المذكر روى عن ابن أبي مسرة، وأحمد بن مهدي','904','30','0');
INSERT INTO [t1714] VALUES (305,'أحمد بن الحسين بن فارس حدث عن محمد بن عاصم، روى عنه السريجاني','906','30','0');
INSERT INTO [t1714] VALUES (306,'أحمد بن موسى بن مردويه الحافظ توفي في شهر رمضان لست بقين منه من سنة عشر وأربعمائة، جمع حديث الأئمة والشيوخ والتفسير، وله المصنفات','907','30','0');
INSERT INTO [t1714] VALUES (307,'أحمد بن محمد بن مهران روى عنه أبو بكر بن عبد الوهاب المقرئ','908','30','0');
INSERT INTO [t1714] VALUES (308,'أحمد بن جعفر بن ممية المؤدب حدث عن إبراهيم بن نائلة، روى عنه السريجاني','909','30','0');
INSERT INTO [t1714] VALUES (309,'أحمد بن عبد الله بن سهيل الجرواءاني أبو بكر','910','30','0');
INSERT INTO [t1714] VALUES (310,'أحمد بن جعفر الخشاب أبو العباس روى عن محمد بن عاصم، حدث عنه السريجاني','912','30','0');
INSERT INTO [t1714] VALUES (311,'أحمد بن الحسين بن محمود أبو بكر البزار بن أبي علي الخرجاني الواعظ يروي عن ابن أخي أبي زرعة، وأبي الأسود وغيرهما','913','30','0');
INSERT INTO [t1714] VALUES (312,'أحمد بن محمد بن عبد الرحمن بن سهلان أبو علي الكاتب جارنا، كان يكتب لعلي بن تمة، حدث عن أبي مسعود العسكري. حدثنا أبو علي، ثنا أبو مسعود','914','30','0');
INSERT INTO [t1714] VALUES (313,'أحمد بن إبراهيم القصار أبو العباس يعرف بالفاتني سمع من محمد بن محمد بن يونس الأبهري، وأبي عمرو بن حكيم، والصحاف، وكان يختلف معنا إلى أن توفي','915','30','0');
INSERT INTO [t1714] VALUES (314,'أحمد بن محمد بن أحمد بن جعفر أبو بكر القصار الفقيه توفي سنة تسع وتسعين وثلاثمائة , يتفقه للشافعي , روى عن أبي علي بن عاصم، وعبد الله بن خالد الراراني، وعبد الله بن جعفر، ومحمد بن إسحاق بن عباد البصري','916','30','0');
INSERT INTO [t1714] VALUES (315,'آدم بن سعيد المديني يروي عن وكيع بن الجراح، ذكر أحمد بن محمد بن نصير المديني قال: سمعت أبي يقول: سمعت القاسم بن محمد بن موسى الفقيه يعرف بقاسويه يقول: سمعت آدم بن سعيد، يقول: دخلت على وكيع بن الجراح في الطواف، فسألته عن أربعمائة مسألة، وحفظت عنه','917','30','0');
INSERT INTO [t1714] VALUES (316,'آدم بن بشر بن آدم بن سعيد المديني يكنى أبا بشر حدث عن محمد بن بكير الحضرمي، لا أعلم أحدا روى عنه إلا أحمد بن محمد بن نصير فيما ذكره بعض شيوخنا','919','30','0');
INSERT INTO [t1714] VALUES (317,'باب من اسمه إبراهيم','921','30','0');
INSERT INTO [t1714] VALUES (318,'إبراهيم بن محمد ابن الحنيفة روى عنه سالم بن أبي الجعد، وياسين العجلي، ومحمد بن إسحاق، ويعقوب القمي , قيل إنه قدم عليهم بقم وسمع منه يعقوب مقامه بقم','922','30','0');
INSERT INTO [t1714] VALUES (319,'إبراهيم بن هدبة أبو هدبة روى عنه الحجاج بن يوسف، ورستة، وحاملة بن مسور، والخضر بن أبان، قدم أصبهان فحدث على المنبر عن أنس بن مالك، فرفع ذلك إلى جرير بن عبد الحميد فصدقه، وكان المأمون أيضا يصدقه','926','30','0');
INSERT INTO [t1714] VALUES (320,'إبراهيم بن محمد بن أبي يحيى الأسلمي أخو سحبل، في حديثه نكارة، وفي مذهبه فساد، توفي بالمدينة سنة أربع وثمانين ومائة , حدث عنه ابن جريج، ومحمد بن إدريس الشافعي، والحسن بن حفص','931','30','0');
INSERT INTO [t1714] VALUES (321,'إبراهيم بن ميمون الصائغ انتقل إلى خراسان , قتله أبو مسلم مظلوما شهيدا سنة إحدى وثلاثين ومائة','935','30','0');
INSERT INTO [t1714] VALUES (322,'إبراهيم بن قرة الأسدي القاساني يعرف بالأصم صحب الثوري وصنف له الجامع , وقرأه عليه في إذنه , روى عنه إبراهيم بن أيوب، ومحمد بن حميد، وأبو حجر عمرو بن رافع. توفي سنة عشر ومائتين , وابنه إسحاق خرج إلى مصر وحدث بها عن عمرو بن علي','940','30','0');
INSERT INTO [t1714] VALUES (323,'إبراهيم بن أيوب العنبري أبو إسحاق الفرساني سمع من الثوري، والمبارك بن فضالة، وأبي هانئ، والأسود بن رزين، وكان صاحب تهجد وعبادة، لم يعرف له فراش أربعين سنة، كان يخضب رأسه ولحيته','942','30','0');
INSERT INTO [t1714] VALUES (324,'إبراهيم بن عون بن راشد السعدي المديني أبو إسحاق من أهل المدينة، روى عن ابن عيينة، ووكيع، وعبيد الله بن موسى، وقبيصة وغيرهم، كان من خيار الناس. توفي بعد سنة أربع وأربعين ومائتين','947','30','0');
INSERT INTO [t1714] VALUES (325,'إبراهيم بن عامر بن إبراهيم بن واقد بن عبد الله أبو إسحاق المؤذن الأشعري كان خيرا فاضلا , توفي سنة ستين ومائتين , وصلى عليه أخوه محمد , يروي عن أبي هانئ وعن مسدد، وأحمد بن عبد الله بن يونس، وسعيد بن منصور، والحسين بن حفص','950','30','0');
INSERT INTO [t1714] VALUES (326,'إبراهيم بن فرقد الأصبهاني المعدل أبو إسحاق كان ممن يشهد عند مبشر بن ورقاء , روى عن روح بن مسافر، روى عنه عامر بن إبراهيم','954','30','0');
INSERT INTO [t1714] VALUES (327,'إبراهيم بن أبي يحيى المكتب يعرف بأفرجة من أهل المدينة , قرأ على يعقوب الحضرمي، وسمع من يحيى القطان، روى عنه أسيد بن عاصم , واسم أبي يحيى يزيد بن عبد الله الباهلي','956','30','0');
INSERT INTO [t1714] VALUES (328,'إبراهيم بن محمد لا تعرف في نسبه زيادة','959','30','0');
INSERT INTO [t1714] VALUES (329,'إبراهيم بن عبد العزيز بن الضحاك بن عمر بن قيس بن الزبير يعرف بشاذة بن عبد كويه الحبال سكن المدينة، أبو إسحاق , يروي عن ابن علية، ويزيد بن هارون، وأبي داود , روى عنه يونس بن حبيب في المسند من حديث أبي الزبير عن جابر. قعد يحدث فأملى فضائل أبي بكر،','961','30','0');
INSERT INTO [t1714] VALUES (330,'إبراهيم بن يحيى بن الحكم بن الحزور والد أبي جعفر الحزوري مولى السائب بن الأقرع. روى عن أبي داود، وبكر بن بكار، يروي عنه ابنه','966','30','0');
INSERT INTO [t1714] VALUES (331,'إبراهيم بن محمد أبو إسحاق يعرف بقرابة ابن شريك، يروي عن سعيد بن سلام، يروي عنه أسيد بن عاصم، ومحمد بن أبان المديني، ذكره المتأخر','968','30','0');
INSERT INTO [t1714] VALUES (332,'إبراهيم بن الحجاج الأبهري أبو إسحاق جد محمد بن يونس الغزال المديني , توفي قبل الخمسين والمائتين , روى عن أبي داود لم يخرج حديثه. حدثنا عبد الله بن محمد بن جعفر، قال محمد بن يونس: قرأت في كتاب جدي إبراهيم بن الحجاج , ثنا أبو داود، ثنا شعبة، عن منصور','970','30','0');
INSERT INTO [t1714] VALUES (333,'إبراهيم بن جرير بن يزيد يروي عن داود بن سليمان، روى عنه ابن أخيه أحمد بن سعيد بن جرير , ذكره أبو عبد الله بن مخلد ولم يخرج عنه','971','30','0');
INSERT INTO [t1714] VALUES (334,'إبراهيم بن كوفي الحبال','973','30','0');
INSERT INTO [t1714] VALUES (335,'إبراهيم بن الوليد بن سندة بن بطة بن أستندار أبو إسحاق يلقب بمنده جد محمد بن يحيى بن منده , ذكر حفيده محمد بن يحيى بن منده قال: وجدت في كتاب جدي يحيى بن منده أبي زكرياء بخطه , عن أبيه , عن النعمان قال: كنت إذا رأيت سلام بن مسكين كأنه رجل من أهل الجنة "','976','30','0');
INSERT INTO [t1714] VALUES (336,'إبراهيم بن ناصح بن المعلى بن حماد الأصبهاني أبو بشر صاحب مناكير، متروك الحديث , ولقب ناصح فورويه , يروي عن ابن عيينة','977','30','0');
INSERT INTO [t1714] VALUES (337,'إبراهيم بن عبد الله بن الحارث الجمحي قدم أصبهان , كان صدوقا , يروي عن يعلى بن عبيد، وحفص بن غياث، وجعفر بن عون، وغيرهم , وهو إبراهيم بن عبد الله بن الحارث بن علي بن محمد بن حاطب بن نعيم بن حبيب بن وهب بن حذافة بن جمح , كوفي قدم أصبهان، سكن المدينة','981','30','0');
INSERT INTO [t1714] VALUES (338,'إبراهيم بن رستم القرشي أبو بكر يروي عن أبي عصمة، روى عنه مسعود بن يزيد , ذكره أبو عبد الله الغزال ولم يخرج عنه','983','30','0');
INSERT INTO [t1714] VALUES (339,'إبراهيم بن بوبة واسمه عبد العزيز بن كوفي سكن جرواءان، أبو إسحاق، ثقة مأمون، روايته عن عبد الوهاب بن عطاء، وأبي النضر، وأبي أحمد الزبيري، وشجاع , وكان صدوقا','987','30','0');
INSERT INTO [t1714] VALUES (340,'إبراهيم بن عبد الله يروي عن الوليد بن مسلم، كتب عنه أبو مسعود , وروى عنه محمد بن إبراهيم بن شبيب , ذكره الغزال , وذكره العقيلي في كتابه ولم يخرج له شيئا , وقيل إبراهيم بن محمد','990','30','0');
INSERT INTO [t1714] VALUES (341,'إبراهيم بن عيسى الزاهد أبو إسحاق كان من العباد والفضلاء، لم يخرج حديثه لإقباله على التعبد , صحب معروفا الكرخي , توفي سنة سبع وأربعين ومائتين , سمع من أبي داود، وشبابة بن سوار، ومروان بن محمد المقرئ','991','30','0');
INSERT INTO [t1714] VALUES (342,'إبراهيم بن عبد الواحد الأشعري المديني','994','30','0');
INSERT INTO [t1714] VALUES (343,'إبراهيم بن إسحاق بن إبراهيم بن صالح بن زياد بن إسحاق العقيلي أبو إسحاق توفي سنة ثمان وأربعين ومائتين , حدث عنه أبو مسعود، وأسيد بن عاصم، توفي قبلهما بزمان , روى عن الوليد بن مسلم','996','30','0');
INSERT INTO [t1714] VALUES (344,'إبراهيم بن عمر بن حفص بن معدان الحافظ الجرواءاني ثقة , توفي سنة إحدى وخمسين , روى عن بكر بن بكار، وسليمان بن حرب، وعلي بن عبد الحميد , ومحمد بن أبان، وحسين بن حفص , روى عنه هارون بن سليمان، ومحمد بن يحيى بن منده','998','30','0');
INSERT INTO [t1714] VALUES (345,'إبراهيم بن سعيد بن يحيى الأصبهاني الطويل حدث عنه محمد بن يحيى بن منده , قال أبو عبد الله الغزال: حدث عنه رستة وحدث عن أبيه، وحجاج بن منهال، والحوضي، وعمرو بن مرزوق','1001','30','0');
INSERT INTO [t1714] VALUES (346,'إبراهيم بن خليد أبو إسحاق القواس مؤذن مسجد حفص بن معدان بن أبي سليمان، ثقة، قديم، حدث عن النعمان','1003','30','0');
INSERT INTO [t1714] VALUES (347,'إبراهيم بن محمد الأصبهاني','1005','30','0');
INSERT INTO [t1714] VALUES (348,'إبراهيم بن أحمد بن المنخل أبو إسحاق النخعي كان من رفقاء محمد بن يوسف , وروى عن بكر بن بكار، ومحمد بن بكير الحضرمي، وسعيد بن الطويل. حدث عنه من المتأخرين أبو الحسن المظالمي، وأحمد بن محمد بن نصير','1007','30','0');
INSERT INTO [t1714] VALUES (349,'إبراهيم بن عاصم أبو إسحاق الفرساني حدث عن خلاد بن يحيى، ذكره المتأخر','1009','30','0');
INSERT INTO [t1714] VALUES (350,'إبراهيم بن أحمد الخطابي القاضي من ولد زيد بن الخطاب , ولي قضاء أصبهان , وكان أحمد بن أبي عاصم من أمنائه , ثم خلفه قاضيا , كان سمع من أبي الوليد، وعمرو بن مرزوق وغيرهما , لم يحدث بأصبهان , لم يخرج له أبو محمد بن حيان شيئا , وكان مهدي بن حكيم على محاضره،','1010','30','0');
INSERT INTO [t1714] VALUES (351,'إبراهيم بن حيان بن حكيم بن حنظلة بن سويد بن علقمة بن سعد بن معاذ الأشهلي كان يسكن فرسان، يروي عن آبائه، وشريك بن عبد الله','1013','30','0');
INSERT INTO [t1714] VALUES (352,'إبراهيم بن يوسف الفقيه توفي سنة سبع وستين ومائتين , ذكره أبو عبد الله بن مخلد ولم يزد عليه','1017','30','0');
INSERT INTO [t1714] VALUES (353,'إبراهيم بن أورمة بن سياوش بن فروخ أبو إسحاق الحافظ المفيد الأصبهاني فاق أهل عصره في الحفظ والمعرفة , أقام بالعراق يكتب أهل العراق والغرباء بفائدته , مشهور مذكور , توفي بعد السبعين والمائتين بأصبهان , وقيل ببغداد , سنة إحدى وأربعين ومائتين , يروي عن عاصم','1018','30','0');
INSERT INTO [t1714] VALUES (354,'إبراهيم بن معمر بن شريس أبو إسحاق الجوزداني توفي سنة أربع وستين , كانوا أخوة ثلاثة لم يحدث منهم إلا إبراهيم , حدث عنه من المتأخرين عبد الله بن جعفر بن أحمد , يروي عن الشاميين: هشام بن عمار، وسليمان بن عبد الرحمن، والحوطي وطبقتهم: سهل بن عثمان،','1022','30','0');
INSERT INTO [t1714] VALUES (355,'إبراهيم بن شذرة أبو إسحاق المديني حدث عن أبيه شذرة عن معمر، وحدث شذرة بن إبراهيم عن أبيه، عن جده إبراهيم بن شذرة حدث عن شذرة، أبو عمرو بن حكيم','1026','30','0');
INSERT INTO [t1714] VALUES (356,'إبراهيم بن بسطام أبو إسحاق الأصبهاني نزل هو وأخوه أحمد البصرة وتوفي بها , ذكره المتأخر يروي عن أبي داود','1028','30','0');
INSERT INTO [t1714] VALUES (357,'إبراهيم بن فهد بن حكيم بن ماهان البصري أبو إسحاق قدم أصبهان وحدث بها، توفي سنة اثنتين وثمانين ومائتين , وقيل توفي سنة خمس وسبعين ومائتين , ضعفه البرذعي , ذهب كتبه وكثر خطأه لرداءة حفظه , حدث عنه إسحاق بن إبراهيم بن جميل','1030','30','0');
INSERT INTO [t1714] VALUES (358,'إبراهيم بن سعدان بن إبراهيم المديني أبو سعيد الكاتب توفي سنة أربع وثمانين , ثقة , صاحب كتاب , سكن المدينة، يروي عن بكر , آخر من مات من أصحاب بكر بن بكار، وسمع من هريم بن عبد الأعلى','1032','30','0');
INSERT INTO [t1714] VALUES (359,'إبراهيم بن عثمان بن عمير الأبهري روى عن التبوذكي','1036','30','0');
INSERT INTO [t1714] VALUES (360,'إبراهيم بن صالح الأنجذاني ثقة توفي سنة ست وثمانين ومائتين , حدث عن محمد بن منصور الكرماني','1038','30','0');
INSERT INTO [t1714] VALUES (361,'إبراهيم بن محمد بن سعيد الثقفي أخو علي , كان غاليا في الرفض، يروي عن إسماعيل بن أبان وغيره، ترك حديثه','1040','30','0');
INSERT INTO [t1714] VALUES (362,'إبراهيم بن أحمد النقاش المقرئ أبو إسحاق توفي سنة إحدى وثمانين ومائتين , قرأ على أبي عبد الله محمد بن عيسى , يروي عن أبي الوليد، والحوضي وطبقتهما','1041','30','0');
INSERT INTO [t1714] VALUES (363,'إبراهيم بن إدريس بن المنذر الحنظلي أخو أبي حاتم الرازي أصبهاني الأصل , يروي عن محمد بن كثير والطبقة، روى عنه محمد بن قارن الرازي','1042','30','0');
INSERT INTO [t1714] VALUES (364,'إبراهيم بن يزداد بغدادي , قدم أصبهان , حدث عنه محمد بن يحيى بن منده','1043','30','0');
INSERT INTO [t1714] VALUES (365,'إبراهيم بن بندار بن عبدة القطان يروي عن ابن أبي عمر، حدث عنه سليمان','1045','30','0');
INSERT INTO [t1714] VALUES (366,'إبراهيم بن محمد بن الحارث بن ميمون أبو إسحاق يعرف بابن نائلة من أهل المدينة , توفي سنة إحدى وتسعين , حدث عنه أبو بكر البرذعي، ومحمد بن يحيى بن منده , ونائلة اسم أمه , سمع من سعيد بن منصور , وذهب سماعه , كان عنده كتب النعمان عن محمد بن المغيرة','1048','30','0');
INSERT INTO [t1714] VALUES (367,'إبراهيم بن محمد بن الحسن بن أبي الحسن أبو إسحاق الإمام يعرف بابن متويه توفي سنة اثنتين وثلاثمائة في جمادى الآخرة , روى عن محمد بن أبي عمر العدني، وعبد الجبار بن العلاء، وسعيد المخزومي، وابن أبي الشوارب , وعن الشاميين والمصريين وأهل العراقين , كان من','1055','30','0');
INSERT INTO [t1714] VALUES (368,'إبراهيم بن عبد الله بن معدان المديني أبو إسحاق توفي سنة أربع وتسعين , روى عن المصريين، وسمع من محمد بن حميد والرازيين','1060','30','0');
INSERT INTO [t1714] VALUES (369,'إبراهيم بن محمد بن مالك القطان أبو إسحاق، يعرف بابن ماهويه صاحب كتاب، فقيه , توفي سنة أربع وثلاثمائة , يروي عن السمتي، وحسين بن مهدي، وإسماعيل بن يزيد، ومحمد بن عمرو بن العباس','1063','30','0');
INSERT INTO [t1714] VALUES (370,'إبراهيم بن ميمون الأسدي أبو إسحاق روى عن لوين','1067','30','0');
INSERT INTO [t1714] VALUES (371,'إبراهيم بن محمد بن برزج ثقة، صاحب أصول، يروي عن لوين، ونصر بن علي، وعمرو بن علي، وموسى بن عبد الرحمن','1069','30','0');
INSERT INTO [t1714] VALUES (372,'إبراهيم بن حرة الأصبهاني روى عن إسماعيل بن يزيد القطان','1071','30','0');
INSERT INTO [t1714] VALUES (373,'إبراهيم بن محمد الفابزاني النفيلي','1072','30','0');
INSERT INTO [t1714] VALUES (374,'إبراهيم بن محمد بن إبراهيم','1074','30','0');
INSERT INTO [t1714] VALUES (375,'إبراهيم بن جعفر الأشعري توفي في طريق الحج سنة أربع وتسعين , يروي عن حميد بن مسعدة والعراقيين حديثا كثيرا','1076','30','0');
INSERT INTO [t1714] VALUES (376,'إبراهيم بن السندي بن علي بن بهرام أبو إسحاق كان يخضب بالحمرة , توفي سنة ثلاث عشرة وثلاثمائة , صاحب أصول , يروي عن محمد بن أبي عبد الرحمن المقرئ، ومحمد بن زياد الزيادي','1080','30','0');
INSERT INTO [t1714] VALUES (377,'إبراهيم بن سفيان أبو بكر الطهراني كثير الحديث روى عن العراقيين، والأصبهانيين','1084','30','0');
INSERT INTO [t1714] VALUES (378,'إبراهيم بن عبد الله القاساني','1087','30','0');
INSERT INTO [t1714] VALUES (379,'إبراهيم بن محمد بن عبيد الله بن عمر أبو إسماعيل يروي عن أسيد بن عاصم وطبقته','1089','30','0');
INSERT INTO [t1714] VALUES (380,'إبراهيم بن محمد بن خليد الكرماني أبو إسحاق قدم أصبهان سنة تسعين ومائتين , ونزل دار الخصيب بن سلم , ذكر محمد بن أحمد بن يعقوب , ثنا إبراهيم بن محمد بن خليد الكرماني أبو إسحاق , قدم علينا سنة تسعين ومائتين ونزل دار الخصيب بن سلم','1091','30','0');
INSERT INTO [t1714] VALUES (381,'إبراهيم بن علي بن عاصم المقرئ والد أبي بكر، يروي عن محمد أخي رستة، ذكر عنه ابنه وجودا في كتابه','1093','30','0');
INSERT INTO [t1714] VALUES (382,'إبراهيم بن عبد الله أبو إسحاق الشاهين القاضي ورد أصبهان، ذكره المتأخر','1096','30','0');
INSERT INTO [t1714] VALUES (383,'إبراهيم بن أحمد بن نوح المؤدب روى الجامع في القراءات عن أبي عبد الله المقرئ','1098','30','0');
INSERT INTO [t1714] VALUES (384,'إبراهيم بن القاسم بن يونس بن عبد الملك الباطرقاني الشيباني الوراق ثقة، يروي عن أبي مسعود','1102','30','0');
INSERT INTO [t1714] VALUES (385,'إبراهيم بن الحسن بن الحكم العسال حدث عن هارون بن سليمان','1107','30','0');
INSERT INTO [t1714] VALUES (386,'إبراهيم بن أحمد الخلنجي','1109','30','0');
INSERT INTO [t1714] VALUES (387,'إبراهيم بن أحمد بن معدان يروي عن هارون بن سليمان، حدث عنه أحمد بن محمد بن سندة الخباز','1111','30','0');
INSERT INTO [t1714] VALUES (388,'إبراهيم بن عمار بن زيد حدث عن سلمة بن شبيب، حدث عنه أبو علي بن عاصم','1113','30','0');
INSERT INTO [t1714] VALUES (389,'إبراهيم بن محمد بن يحيى بن منده ومنده اسمه إبراهيم أبو إسحاق الحافظ توفي في شهر رمضان سنة عشرين وثلاثمائة , صنف الشيوخ , تام العناية , يروي عن أحمد بن خشنام، وأبي بكر بن النعمان، والثقفي','1114','30','0');
INSERT INTO [t1714] VALUES (390,'إبراهيم بن أحمد بن الفضل أبو أحمد الأصبهاني','1119','30','0');
INSERT INTO [t1714] VALUES (391,'إبراهيم بن محمد بن إبراهيم أبو إسحاق الرفاعي أخو عمرو','1122','30','0');
INSERT INTO [t1714] VALUES (392,'إبراهيم بن محمد البراد','1124','30','0');
INSERT INTO [t1714] VALUES (393,'إبراهيم بن عبد الله بن مصعب يروي عن أبي بكر بن النعمان','1126','30','0');
INSERT INTO [t1714] VALUES (394,'إبراهيم بن الحسن بن يزداد يروي عن أسيد بن عاصم','1128','30','0');
INSERT INTO [t1714] VALUES (395,'إبراهيم بن محمد بن حمزة بن عمارة أبو إسحاق الحافظ واحد زمانه في الحفظ , لم ير بعد ابن مظاهر مثله في الحفظ , جمع الشيوخ وصنف المسند , توفي في شهر رمضان لسبع خلون منه سنة ثلاث وخمسين وثلاثمائة , وهو إبراهيم بن محمد بن حمزة بن عمارة بن حمزة بن يسار بن عبد','1129','30','0');
INSERT INTO [t1714] VALUES (396,'إبراهيم بن محمد بن الخصيب أبو إسحاق العسال روى عن يوسف القاضي ومحمد بن يحيى المروزي، وأحمد بن يحيى الحلواني','1133','30','0');
INSERT INTO [t1714] VALUES (397,'إبراهيم بن محمد بن إبراهيم أبو إسحاق الوراق، يعرف بالقراء روى عن الأخرم وطبقته، توفي قبل الستين','1137','30','0');
INSERT INTO [t1714] VALUES (398,'إبراهيم بن عبد الله بن إسحاق بن جعفر بن زكرياء بن يحيى بن شهمردان المعدل الأصبهاني سكن نيسابور , سمع بأصبهان من إبراهيم بن محمد بن الحسن، ونيسابور من أبي بكر بن خزيمة، وأبي العباس السراج وبالجزيرة والسامرة من صالح بن أبي الأصبغ وطبقته','1140','30','0');
INSERT INTO [t1714] VALUES (399,'إبراهيم بن محمد بن قحطبة يروي عن عبدان','1142','30','0');
INSERT INTO [t1714] VALUES (400,'إبراهيم بن أبان بن رستة المديني توفي سنة تسع وثلاثين وثلاثمائة , روى عن أبي مسلم الكشي، ومحمد بن عثمان بن أبي شيبة , ويوسف القاضي، وأحمد بن يحيى بن خالد الرقي، ومحمد بن المغيرة الصائغ','1144','30','0');
INSERT INTO [t1714] VALUES (401,'إبراهيم بن محمد بن يوسف بن مسعدة بن جناب الفزاري الأصبهاني سكن نهاوند , ثقة , قدم أصبهان سنة ثلاث وأربعين وثلاثمائة , وحدث بها عن إبراهيم بن الحسين بن ديزيل، وعمير بن مرداس، ومحمد بن أيوب، وابن أبي عاصم','1146','30','0');
INSERT INTO [t1714] VALUES (402,'إبراهيم بن إسحاق بن إبراهيم أبو إسحاق الخشاب يروي عن أبي خليفة، وابن أبي داود البغوي','1147','30','0');
INSERT INTO [t1714] VALUES (403,'إبراهيم بن محمد بن موسى الوكيل أبو إسحاق يعرف بالباقلاني , كان إليه استغلال وقوف الجامع، توفي بعد ستين وثلاثمائة , حدث عن الحسن بن علي الطوسي، وعلي بن سعيد العسكري','1149','30','0');
INSERT INTO [t1714] VALUES (404,'إبراهيم بن إسحاق بن موسى أبو إسحاق الصفار التاجر يروي عن محمد بن إسحاق بن خزيمة، ومحمد بن إسحاق السراج','1150','30','0');
INSERT INTO [t1714] VALUES (405,'إبراهيم بن محمد بن الفاخر بن محمد بن يحيى أبو إسحاق الفقيه السريجاني المديني توفي في ربيع الآخر سنة ثمان وخمسين وثلاثمائة , يروي عن المنيعي، وابن أبي داود، ومحمد بن هارون بن حميد المجدر والبغداديين والكوفيين والبصريين','1153','30','0');
INSERT INTO [t1714] VALUES (406,'إبراهيم بن محمد بن إبراهيم أبو إسحاق الرفاعي عن محمد بن سليمان بن الحارث الباغندي الكبير، توفي سنة سبع وأربعين وثلاثمائة , أدركته ولم أرزق منه','1155','30','0');
INSERT INTO [t1714] VALUES (407,'إبراهيم بن أبحة بن أعصر أبو إسحاق المنجاني روى عن محمد بن عاصم، وإبراهيم بن محمد بن مصعب أبو إسحاق، روى عن علي بن جبلة، حدث عنهما أبو إسحاق السريجاني','1156','30','0');
INSERT INTO [t1714] VALUES (408,'إبراهيم بن يحيى بن آدم المديني حدث عن أبي صالح الجلاب، ومحمد بن عمر بن حفص، وعبد الله بن إبراهيم بن الصباح المتأخرين','1157','30','0');
INSERT INTO [t1714] VALUES (409,'إبراهيم بن علي أبو إسحاق القاساني المؤدب حدث عن أبي يعلى الموصلي، وأحمد بن الحسين الجرادي الموصلي، أدركته ولم أسمع منه','1158','30','0');
INSERT INTO [t1714] VALUES (410,'إبراهيم بن أحمد بن يوسف أبو إسحاق الجلاب من باب كوشك، سمع من ابن صاعد وطبقته','1159','30','0');
INSERT INTO [t1714] VALUES (411,'إبراهيم بن علي بن الجسري يروي عن أبي عمرو بن حكيم وطبقته','1160','30','0');
INSERT INTO [t1714] VALUES (412,'إبراهيم بن محمد بن عامر أبو إسحاق التاجر سكن طرسوس مدة متعاطيا للفروسية، توفي سنة تسع وثمانين وثلاثمائة، يروي عن الحسين بن إسماعيل المحاملي','1161','30','0');
INSERT INTO [t1714] VALUES (413,'إبراهيم بن عبد الله بن محمد بن خرشيد قوله التاجر أبو إسحاق خال عبد الله ومحمود ابني أحمد بن محمود، يروي عن المحاملي، وابن مخلد، وابن عقدة، توفي سنة أربعمائة في صفر','1162','30','0');
INSERT INTO [t1714] VALUES (414,'إبراهيم بن قسانشاه بن ززا من أهل نائين','1163','30','0');
INSERT INTO [t1714] VALUES (415,'باب من اسمه إسماعيل','1166','30','0');
INSERT INTO [t1714] VALUES (416,'إسماعيل بن عبد الرحمن أبو محمد الأعور، يعرف بالسدي صاحب التفسير , كان أبوه عبد الرحمن يكنى أبا خزيمة، من عظماء أهل أصبهان , توفي في ولاية مروان سنة سبع وعشرين ومائة، كان عريض اللحية، إذا جلس غطى لحيته صدره , وسمي السدي لأنه نزل بالسدة , قيل إنه رأى','1167','30','0');
INSERT INTO [t1714] VALUES (417,'إسماعيل بن حماد بن أبي سليمان الفقيه الأشعري أصبهاني الأصل، كان جده أبو سليمان من سبي أصبهان , واسم أبي سليمان مسلم , روى عن أبي إسحاق السبيعي، والأعمش، حدث عنه معتمر بن سليمان، وخالد بن عبد الله , كان أبو سليمان من أصبهان من رستاق برخوار , ذكره محمد','1177','30','0');
INSERT INTO [t1714] VALUES (418,'إسماعيل بن خليفة القاضي أبو هانئ الكوفي كان من موالي سعد بن عبادة , ولاه المنصور القضاء بأصبهان , توفي في ولاية المهدي , حدث عن إسماعيل بن أبي خالد، ومجالد، والثوري، ومبارك بن فضالة , روى عنه الحسين بن حفص، وعامر بن إبراهيم، وإبراهيم بن أيوب، وأبو','1182','30','0');
INSERT INTO [t1714] VALUES (419,'إسماعيل بن عمرو بن نجيح أبو إسحاق البجلي مولى لهم كوفي , قدم أصبهان وسكن المدينة. توفي سنة سبع وعشرين ومائتين حدث عن مسعر، والثوري، وشيبان، والحسن، وعلي ابني صالح بن حي، وعثمان بن غالب , حدث عنه أسيد بن عاصم , روى عنه عبيد الله بن محمد ابن عائشة،','1187','30','0');
INSERT INTO [t1714] VALUES (420,'إسماعيل بن موسى ابن بنت السدي روى عن مالك بن أنس، وشريك، وروى عن عمر بن شاكر عن أنس، توفي سنة خمس وأربعين بالكوفة، ذكره العقيلي في جملة الأصبهانيين في كتاب أصبهان','1195','30','0');
INSERT INTO [t1714] VALUES (421,'إسماعيل بن يزيد بن حريث بن مردانبة القطان أبو أحمد توفي سنة الستين والمائتين أو قبله، اختلط عليه بعض حديثه في آخر أيامه، يذكر بالزهد والعبادة. حسن الحديث، كثير الغرائب والفوائد، صنف المسند والتفسير، روى عنه محمد بن حميد الرازي. روى عن سفيان بن','1196','30','0');
INSERT INTO [t1714] VALUES (422,'إسماعيل بن محمد بن عصام بن يزيد أبو مالك يروي عن أبيه، وعمه، وعن جده بغرائب من حديث الثوري','1200','30','0');
INSERT INTO [t1714] VALUES (423,'إسماعيل بن يوسف بن محمد الزاهد يعرف بسمويه كان يصحب عبد الرحمن بن يوسف المعداني، حدث عن يحيى القطان، وعبد الرحمن بن مهدي، ونائل بن نجيح وكان ابنه أبو إبراهيم يقرئ الناس، وكان له فضل وعبادة، قال أبو محمد بن حيان: أدركته أنا','1203','30','0');
INSERT INTO [t1714] VALUES (424,'إسماعيل بن عبد الله بن مسعود بن جبير بن عبد الله بن كيسان العبدي الفقيه الحافظ أبو بشر يعرف بسمويه، كان من الحفاظ والفقهاء، حدث عن الحسين بن حفص، وبكر بن بكار، ومن العراقيين والشاميين والمصريين أبو نعيم، وأبو مسهر، وأبو اليمان، وسعيد بن أبي مريم.','1205','30','0');
INSERT INTO [t1714] VALUES (425,'أبو علي إسماعيل بن بحر الزعفراني العسكري قدم أصبهان يعرف بسمعان، توفي سنة ثمان وسبعين، يروي عن البصريين: ابن عائشة وغيرهم','1212','30','0');
INSERT INTO [t1714] VALUES (426,'إسماعيل بن أحمد بن أسيد الثقفي أبو إسحاق صنف التفسير والمسند، أخو عبد الله حدث عن المكيين، والبصريين والكوفيين، أبي كريب وطبقته، كان على المسائل، توفي في ربيع الآخر سنة اثنتين وثمانين ومائتين','1214','30','0');
INSERT INTO [t1714] VALUES (427,'إسماعيل بن داود البزار','1216','30','0');
INSERT INTO [t1714] VALUES (428,'إسماعيل بن زياد بن عبيد الخزاعي يعرف بابن زراة توفي سنة ثلاث وسبعين ومائتين، وكان من الكتبة الرحالة الحفظة، يذاكر، توفي شابا، ولم يخرج حديثه','1218','30','0');
INSERT INTO [t1714] VALUES (429,'إسماعيل بن محمد الأصبهاني حدث ببغداد','1221','30','0');
INSERT INTO [t1714] VALUES (430,'إسماعيل بن عبد الله بن محمد بن عبدة بن زياد الضبي أبو الحسن توفي في رجب سنة تسع وتسعين ومائتين. حدثنا عنه القاضي والجماعة، روى عن إسحاق الخطمي، والرازيين، زنيج، وسهل بن زياد، كان جده محمد بن عبدة يروي عن الزحاف عن ابن جريج','1223','30','0');
INSERT INTO [t1714] VALUES (431,'إسماعيل بن إبراهيم بن صبيح الثقفي أبو إسحاق يروي عن سعدويه، وسهل بن عثمان، والشاذكوني، ذكره أبو عبد الله الغزال، ولم يخرج له، كان له ابن يقال له: أبو يعقوب الحذاء توفي شابا في طريق مكة كان يكتب الكثير، آخر من حدث عنه أبو عثمان إسحاق بن إبراهيم بن','1229','30','0');
INSERT INTO [t1714] VALUES (432,'إسماعيل بن عباد بن العباس الطالقاني أبو القاسم الوزير الملقب بالصاحب سمع بالعراق، والري، وأصبهان الكثير، مولده سنة ست وعشرين وثلاثمائة، وتوفي في سنة خمس وثمانين وثلاثمائة','1230','30','0');
INSERT INTO [t1714] VALUES (433,'باب من اسمه إسحاق','1232','30','0');
INSERT INTO [t1714] VALUES (434,'إسحاق بن الفيض بن محمد بن سليمان أبو يعقوب مولى عتاب بن أسيد بن أبي العيص توفي بعد الخمسين والمائتين. روى عن أبي زهير عبد الرحمن بن مغراء، ومهران، وسلمة بن حفص، والوليد بن مسلم، وابن عيينة، وعبد المجيد بن عبد العزيز','1233','30','0');
INSERT INTO [t1714] VALUES (435,'إسحاق بن إبراهيم بن قرة القاساني خرج إلى مصر وحدث بها، سمع من عمرو بن علي وغيره','1237','30','0');
INSERT INTO [t1714] VALUES (436,'إسحاق بن مهران بن عبد الرحمن مولى قريش والد يعقوب بن أبي يعقوب، وقيل إسحاق بن إبراهيم يروي عن ابن مهدي، ويحيى القطان، وغندر، وعبد الوهاب الثقفي روى عنه أبو مسعود، وسمويه وابنه يعقوب','1238','30','0');
INSERT INTO [t1714] VALUES (437,'إسحاق بن إبراهيم بن صالح بن زياد العقيلي سكن طرسوس، وتوفي بها سنة أربعين ومائتين، يكنى أبا يعقوب، ذكره أبو عبد الله بن مخلد، يروي عن ابن عيينة، والشافعي، وابن المبارك لم يخرج حديثه، روى عنه أحمد بن إبراهيم الدورقي، وأبو مسعود، وأسيد، وكان بينه','1240','30','0');
INSERT INTO [t1714] VALUES (438,'إسحاق بن إسماعيل بن السكين الفلفلاني أبو يعقوب توفي بعد الستين والمائتين. روى عن إسحاق بن سليمان الرازي، وكان أخوه محمد بن إسماعيل أحد الثقات','1242','30','0');
INSERT INTO [t1714] VALUES (439,'إسحاق بن يوسف الجرجاني الديلماني توفي سنة خمس وأربعين ومائتين، من الثقات، سمع من سفيان بن عيينة، وحفص بن عمر العدني، وطارق بن عبد العزيز المكي روى عنه عقيل بن يحيى، وابنه عبد الله','1245','30','0');
INSERT INTO [t1714] VALUES (440,'إسحاق بن عبد الله الشيباني الزعفراني الأصبهاني روى عنه محمد بن إبراهيم الكناني، ذكره المتأخر','1247','30','0');
INSERT INTO [t1714] VALUES (441,'إسحاق بن إسماعيل بن موسى بن مهران الجلكي شيخ ثقة، توفي سنة تسع وسبعين ومائتين، أبو يعقوب التاجر روى عن البصريين أبي الوليد، وسهل بن بكار، ومعاذ بن أسد، وأبي الربيع، وإسماعيل بن أبي أويس، وسعيد بن منصور وغيرهم','1248','30','0');
INSERT INTO [t1714] VALUES (442,'إسحاق بن إسماعيل بن عبد الله بن زكرياء المذحجي الرملي أبو يعقوب قدم أصبهان سنة ثمان وثمانين ومائتين، كان يخضب بالحمرة، نزل سكة القصارين، كان نحاسا. يروي عن آدم بن أبي إياس، ومحمد بن رمح، حدث بأحاديث من حفظه فأخطأ فيها','1250','30','0');
INSERT INTO [t1714] VALUES (443,'إسحاق بن إبراهيم بن أحمد بن عبد الله بن يعيش الهمذاني يعرف بابن النابتي قدم أصبهان، كان أبوه على القضاء بهمذان','1253','30','0');
INSERT INTO [t1714] VALUES (444,'إسحاق بن إبراهيم بن محمد بن جميل أبو يعقوب توفي سنة عشر وثلاثمائة، كان من المعمرين، جاوز المائة، حدث عن أبي كريب، والكوفيين، وأحمد بن منيع، توفي وله من السن مائة وسبع عشرة سنة','1256','30','0');
INSERT INTO [t1714] VALUES (445,'إسحاق بن محمد بن علي بن سعيد المديني من موالي الأنصار أبو يعقوب: توفي آخر يوم من رمضان سنة إحدى عشرة وثلاثمائة. يروي عن حميد بن مسعدة، وعمرو بن علي، سمع كتاب العلل ليحيى بن معين عن عباس الدوري','1258','30','0');
INSERT INTO [t1714] VALUES (446,'إسحاق بن محمد الأصبهاني مولى عبد الله بن جعفر الهاشمي، يروي عنه سليمان بن أحمد','1261','30','0');
INSERT INTO [t1714] VALUES (447,'إسحاق بن عبد الله الزعفراني الأصبهاني روى عنه محمد بن إبراهيم الكناني، ذكره بعض شيوخنا ولم يزد عليه','1263','30','0');
INSERT INTO [t1714] VALUES (448,'إسحاق بن محمد بن إبراهيم بن حكيم بن أسيد أبو الحسن توفي في رمضان سنة اثنتي عشرة وثلاثمائة، شيخ ثبت صدوق عارف بالحديث أديب، ولا يحدث إلا من كتابه، كتب بالشأم والحجاز، وبالعراق، صنف الشيوخ','1264','30','0');
INSERT INTO [t1714] VALUES (449,'إسحاق بن إبراهيم بن داود المكتب أبو يعقوب يروي عن حميد بن مسعدة، حدث عنه أبو أحمد','1268','30','0');
INSERT INTO [t1714] VALUES (450,'إسحاق بن إبراهيم بن قران المؤدب','1272','30','0');
INSERT INTO [t1714] VALUES (451,'إسحاق بن إبراهيم بن زيد بن سلمة بن الربيع بن جابر التيمي أبو عثمان المعدل توفي سنة أربعين وثلاثمائة، ثقة مأمون. حدثنا عنه أبو إسحاق بن حمزة، روى عن أبي النعمان، وعمران بن عبد الرحيم، وإسماعيل بن بحر سمعان، وعن الحضرمي وغيره من العراقيين','1275','30','0');
INSERT INTO [t1714] VALUES (452,'إسحاق بن أحمد بن علي بن إبراهيم بن قولويه أبو يعقوب التاجر سمع من الرازيين توفي لخمس خلون من ربيع الأول سنة ثمان وستين','1277','30','0');
INSERT INTO [t1714] VALUES (453,'إسحاق بن محمد بن يحيى بن منده أبو يعقوب كان من أهل بيت الحديث والرواية، توفي في رمضان سنة إحدى وأربعين، سمع عبد الله بن محمد بن النعمان، وابن أبي عاصم، والبزار، قد رأيته وشاهدته، ولم أرزق منه سماع حديثه','1281','30','0');
INSERT INTO [t1714] VALUES (454,'إسحاق بن محمد بن إسحاق الزجاج كان من المتعبدين، قديم الموت، سمع من محمود بن الفرج وطبقته','1283','30','0');
INSERT INTO [t1714] VALUES (455,'أيوب بن معمر بن شريس الأصبهاني كان من الحفاظ، من تيم أصبهان، خرج منها وهو صغير، وكان حافظا، جليل القدر صاحب غرائب. حدث بالعراق. يروي عن مخلد الشعيري، عن إبراهيم بن خالد الصنعاني، عن ابن المبارك، عن الثوري، عن شعبة، عن قتادة، عن أنس قال: كان','1285','30','0');
INSERT INTO [t1714] VALUES (456,'أيوب بن حماد من فقهاء أصبهان، قال أبو محمد بن حيان: له حديث كثير لم يقع إلينا','1288','30','0');
INSERT INTO [t1714] VALUES (457,'أيوب بن موسى بن زكرياء القرشي المكتب يروي عن محمد بن بكير','1289','30','0');
INSERT INTO [t1714] VALUES (458,'أيوب بن عمران بن أيوب بن عمران بن أبي سليمان عم عبد الله بن محمد بن عمران المعدل','1293','30','0');
INSERT INTO [t1714] VALUES (459,'أسباط بن إبراهيم المعدل المديني توفي سنة أربعين وثلاثمائة، روى عن أحمد بن خشنام، وإبراهيم بن سعدان، وابن أبي عاصم أخو إسماعيل','1295','30','0');
INSERT INTO [t1714] VALUES (460,'أسباط بن عبد الله روى عنه إسماعيل بن عبد الله بن سمويه. يروي عن أبي داود','1297','30','0');
INSERT INTO [t1714] VALUES (461,'إدريس بن العباس الكرجي أبو شجاع قدم علينا، سكن خشينان','1299','30','0');
INSERT INTO [t1714] VALUES (462,'الأحنف بن قيس بن معاوية بن حصين بن عباد بن النزال السعدي التميمي أبو بحر أدرك عهد النبي صلى الله عليه وسلم، ولم يصحبه، مختلف في اسمه، فقيل صخر، وقيل الضحاك، كان ممن شهد فتح التيمرة، وقاسان عنوة سمع عمر بن الخطاب، وقدم المدينة، فحبسه عمر عنده سنة','1301','30','0');
INSERT INTO [t1714] VALUES (463,'الأحنف بن حكيم بن عمران أبو بحر وقيل أبو محمد، كان ينزل عبادان، ومات بأصبهان، وحدث بها، يروي عن حماد بن سلمة، وجرير بن حازم، وابن ثعلبة العابد، سمع منه يونس بن حبيب، وكنية حكيم أبو الأحنف','1305','30','0');
INSERT INTO [t1714] VALUES (464,'من اسمه أسيد','1309','30','0');
INSERT INTO [t1714] VALUES (465,'أسيد بن المتشمس بن معاوية قدم مع أبي موسى الأشعري غازيا، وهو ابن عم الأحنف بن قيس، حدث عنه الحسن بن أبي الحسن','1310','30','0');
INSERT INTO [t1714] VALUES (466,'أسيد بن عاصم بن عبد الله مولى ثقيف، أبو الحسين، صنف المسند، كانوا إخوة: محمد، وعلي، والنعمان، وأسيد، يروي عن سعيد بن عامر، وبشر بن عمر، وعبد الصمد بن عبد الوارث، والحسين بن حفص، وبكر بن بكار توفي سنة سبعين ومائتين، وصلى عليه إسماعيل بن أحمد','1313','30','0');
INSERT INTO [t1714] VALUES (467,'أسيد بن عبد الله بن أحمد بن أسيد المعدل المديني توفي دون الخمسين والثلاثمائة، روى عن محمد بن نصير وطبقته','1317','30','0');
INSERT INTO [t1714] VALUES (468,'أشعث بن إسحاق بن سعد بن عامر بن مالك الأشعري أحد الثقات، نزل قم، روى عن جعفر بن أبي المغيرة، حدث عنه جرير بن عبد الحميد','1318','30','0');
INSERT INTO [t1714] VALUES (469,'أشعث بن شداد بن إبراهيم الربعي أبو عبد الله الخراساني سجستاني قدم أصبهان، يروي عن يحيى بن يحيى، وسعد بن يزيد الفراء وغيرهما','1320','30','0');
INSERT INTO [t1714] VALUES (470,'أزهر بن رستة بن عبد الله أبو محمد المكتب توفي سنة ست وثمانين ومائتين، يروي عن محمد بن بكير، وسهل بن عثمان، وسعدويه','1323','30','0');
INSERT INTO [t1714] VALUES (471,'أعين بن محمد الجرواءاني وهو أعين بن محمد بن مندويه بن حماد بن زهير بن عطية، مولى العباس بن مرداس، يكنى أبا سعيد، توفي سنة سبعين ومائتين في آخرها، يروي عن أبي الوليد الطيالسي، وأبي حذيفة، وانتقل حماد من الكوفة إلى أصبهان','1326','30','0');
INSERT INTO [t1714] VALUES (472,'أسلم مولى عمر بن الخطاب','1328','30','0');
INSERT INTO [t1714] VALUES (473,'أنيس بن أبي يحيى أخو محمد بن أبي يحيى، أبو يونس، توفي سنة ست وأربعين ومائة، حدث عن أبيه واسم أبيه أبي يحيى سمعان، روى عنه يحيى القطان، وسعد بن الصلت','1331','30','0');
INSERT INTO [t1714] VALUES (474,'أبان بن أبي الخصيب واسم أبي الخصيب شهاب، أبو أحمد كان من بني حنيفة بن لجيم، توفي سنة ثمان وخمسين ومائتين، يروي عن الحسين بن حفص، ويحيى بن عبد الله بن بكير، وعيسى بن جعفر، وأبي عبد الرحمن المقرئ، وأحمد بن يزيد الحراني، وأحمد بن يونس، وسليم بن','1333','30','0');
INSERT INTO [t1714] VALUES (475,'أبان بن مخلد بن أبان أبو الحسن البزاز توفي قبل الثلاثمائة، وقيل سنة تسع وتسعين ومائتين، شيخ لا بأس به، يروي عن محمد بن أبان البلخي، وعبد الله بن عمران، ومحمد بن مهران','1337','30','0');
INSERT INTO [t1714] VALUES (476,'أشعث بن إسحاق بن سعد بن عامر بن مالك القمي يروي عن جعفر بن أبي المغيرة، ذكره أبو عبد الله بن مخلد روى عنه جرير بن عبد الحميد، وعبد الرحمن الدشتكي، ويحيى بن يمان','1340','30','0');
INSERT INTO [t1714] VALUES (477,'أشعث بن الأصبهاني حدث عن خارجة بن مصعب، عن زيد بن أسلم، عن علي بن أبي طالب في صفة المؤمن، ذكره ابن منده، وقال: روى عنه الحسن بن عاصم الرازي، ثنا محمد بن أبي حامد، عن ابن موسى عنه. أسد بن حكيم الأصبهاني روى عن أبي عاصم، والمقرئ، والحسين بن حفص،','1343','30','0');
INSERT INTO [t1714] VALUES (478,'الأسود بن رزين يروي عن الحسن بن سلم، ووقاء الأسدي، روى عنه إبراهيم بن أيوب، ذكره أبو عبد الله الغزال','1344','30','0');
INSERT INTO [t1714] VALUES (479,'أزديار بن سليمان بن داود أبو محمد الفارسي كان يتفقه على مذهب الحجازيين، من المتصوفة، قدم علينا سنة سبع وستين وثلاثمائة','1346','30','0');
INSERT INTO [t1714] VALUES (480,'باب الباء','1348','0','0');
INSERT INTO [t1714] VALUES (481,'بشر بن الحسين الأصبهاني الهلالي حدث عن الزبير بن عدي، وعبد الرحمن بن عبد الله بن دينار، توفي بعد المائتين من أهل المدينة، وجاء إلى أبي داود، فقال: حدثني الزبير بن عدي، فكذبه أبو داود، وقال: ما نعرف للزبير بن عدي عن أنس إلا حديثا واحدا، وكتب عنه','1349','480','0');
INSERT INTO [t1714] VALUES (482,'بشر بن حيان بن بشر الأسدي','1356','480','0');
INSERT INTO [t1714] VALUES (483,'بشر بن أبي السري أبو أحمد من أهل رويدشت، توفي قبل الثلاثمائة، شيخ ثقة','1358','480','0');
INSERT INTO [t1714] VALUES (484,'بشر بن الأزهر أبو الأزهر المديني روى عن حميد بن مسعدة، وعمرو بن علي','1361','480','0');
INSERT INTO [t1714] VALUES (485,'بشر بن فورك واسم فورك يحيى حدث عن الشاذكوني، ومحمد بن بكير، ثقة','1363','480','0');
INSERT INTO [t1714] VALUES (486,'من اسمه بكر','1365','480','0');
INSERT INTO [t1714] VALUES (487,'بكر بن بكار بن الخصيب أبوعمرو القيسي البصري قدم أصبهان سنة ست ومائتين، روى عن ابن عون، وشعبة، والثوري، ومسعر، وفطر بن خليفة، وحمزة الزيات، وقرة بن خالد، وعمر بن ذر، وعيسى بن المسيب، وحدث عنه أبو داود الطيالسي، والحسن بن علي الحلواني، والمقدمي','1366','480','0');
INSERT INTO [t1714] VALUES (488,'بكر بن سليمة بن واقد التيمي، تيم الرباب، المعدل روى عن محمد بن حرب المكي، والمقرئ، توفي قبل الخمسين والمائتين','1371','480','0');
INSERT INTO [t1714] VALUES (489,'بكر بن أحمد، وقيل ابن علي الحبطي يحدث عن محمد بن سعيد بن سابق، وإبراهيم بن موسى الفراء، توفي سنة ثمان وثمانين ومائتين، لم يخرج عنه أبو محمد شيئا، وقال ابن مخلد: بكر بن علي، ولم يخرج عنه شيئا','1373','480','0');
INSERT INTO [t1714] VALUES (490,'بكر بن محمد بن بكر بن محمد بن يحيى بن أبان بن الحكم العنبري أبو محمد ولد سنة ثمان وخمسين','1374','480','0');
INSERT INTO [t1714] VALUES (491,'بزيع مولى عبد المجيد الأنصاري أصبهاني','1376','480','0');
INSERT INTO [t1714] VALUES (492,'بكر بن عبد الله بن واضح الصوفي أبو محمد','1378','480','0');
