INSERT INTO [t1714] VALUES (521,'جعفر بن محمد بن أحمد بن شريك أبو الفضل حدثنا عنه القاضي والجماعة، توفي سنة ثمان وثمانين ومائتين، نزل عليه محمد بن سليمان لوين وخصه بغير حديث، تولى غسله أبو جعفر الأخرم، وصلى عليه، كان صاحب سنة، يروي عن الحسين بن الفرج، وعبد الله بن عمران','1442','510','0');
INSERT INTO [t1714] VALUES (522,'جعفر بن أحمد بن فارس أبو الفضل يروي عن سهل بن عثمان، وعبدان العسكري، وإسحاق بن أبي إسرائيل، وأبي مصعب، كتب الكثير بالبصرة، ومكة، سمع الموطأ من أبي مصعب عن مالك، توفي بالكرج، له مصنفات، توفي سنة تسع وثمانين ومائتين','1448','510','0');
