INSERT INTO [t1714] VALUES (544,'جنيد بن كوفي بن الجنيد أبو محمد حدث عنه أبو حامد الأشعري، يروي عن الكوفيين، والبصريين، وعن أحمد بن عبدة، توفي سنة ثلاث وسبعين ومائتين، وكان الحسن بن علي بن يونس كثير الثناء عليه والذكر لفضله','1498','510','0');
INSERT INTO [t1714] VALUES (545,'جبير بن حية بن مسعود بن قعنب بن مالك بن عمرو بن سعد بن عوف بن ثقيف يكنى أبا فرس الثقفي كان يسكن الطائف، وكان معلم كتاب، ثم قدم العراق، وصار من كتبة الديوان، وولاه زياد أصبهان، ورفع من شأنه، روى عنه ابنه زياد، والزبير بن مسلم الصواف، عقبه بالبصرة','1500','510','0');
