INSERT INTO [t1714] VALUES (558,'الحسن بن عبد الرحمن بن عمر بن يزيد الزهري أبو سعيد، وعبد الرحمن، يعرف برستة، توفي سنة ثلاث وستين ومائتين في المحرم، يروي عن عثمان بن الهيثم، وعلي بن المديني، وحاتم بن عبيد الله. أخبرنا عبد الله بن جعفر إجازة، ثنا الحسن بن عبد الرحمن بغير حديث','1536','547','0');
INSERT INTO [t1714] VALUES (559,'الحسن بن شاذان أبو عطية الخراساني قدم أصبهان وكتبوا عنه، يروي عن سويد بن نصر وغيره','1539','547','0');
