INSERT INTO [t1714] VALUES (561,'الحسن بن الفضل بن سمح الزعفراني البوصرائي قرية من قرى بغداد، أبو علي، يحدث عن أبي نعيم والكوفيين والبصريين','1543','547','0');
INSERT INTO [t1714] VALUES (562,'الحسن بن محمد بن جميل المروزي قدم أصبهان، روى عن جرير، وابن المبارك، وابن مغراء','1548','547','0');
INSERT INTO [t1714] VALUES (563,'الحسن بن محمد بن مزيد أبو سعيد يروي عن الشأميين، والمصريين، وهو أول من حمل علم الشافعي إلى أصبهان، يروي عن أهل مصر توفي قبل الثمانين، حدث عن حامد بن يحيى، وموسى بن مروان، وهشام بن عمار، وعثمان بن عيسى','1550','547','0');
