INSERT INTO [t1714] VALUES (565,'الحسن بن الجهم بن جبلة بن مصقلة الواذاري أبو علي التيمي سمع كتاب المغازي من الحسين بن الفرج، ومن إسماعيل بن عمرو، وحيان بن بشر توفي سنة تسعين ومائتين في رجب','1557','547','0');
