INSERT INTO [t1714] VALUES (731,'الزحاف بن أبي الزحاف الأصبهاني أبو محمد يروي عن ابن جريج، وهشام القردوسي، والمثنى بن الصباح، ومسلم بن خالد، وله بأصبهان عقب، روى عنه عقيل بن يحيى، وابنه جعفر بن الزحاف قال أبو محمد بن حيان ذكر محمد بن عاصم أنه حدثه من كتب عن أبيه جعفر بن محمد بن','1981','717','0');
