INSERT INTO [t1714] VALUES (875,'علي بن محمد بن الحسن بن أبي الحسن نصر بن عثمان بن زيد بن مزيد مولى الأنصار , يعرف بعلي بن متويه العابد , توفي سنة ثلاث وثمانين ومائتين','2324','818','0');
