INSERT INTO [t1714] VALUES (878,'علي بن الحسن بن سلم خرج إلى الري ومات بها , حدثنا عنه القاضي، روى عن العراقيين، والأصبهانيين، يرجع إلى معرفة وكثرة حديث','2330','818','0');
INSERT INTO [t1714] VALUES (879,'علي بن نمراذ أبو علي كتب الكثير توفي بالبادية سنة ثلاث وتسعين ومائتين','2333','818','0');
INSERT INTO [t1714] VALUES (880,'علي بن الصباح بن علي المعروف بابن ريذوس، يروي عن جبر، يكنى أبا الحسن , كان من الحفاظ , حدثنا عنه القاضي، والجماعة','2336','818','0');
INSERT INTO [t1714] VALUES (881,'علي بن رستم بن المطيار الطهراني أبو الحسن عم أبي علي بن رستم , توفي سنة ثلاث وثلاثمائة , يروي عن لوين، وأحمد بن معاوية، وجبر، وعبد الله بن عمر، أخي رستة , وعقيل بن يحيى , والطبقة إبراهيم بن معمر , ويحيى بن واقد , ومحمد بن محمد , والحسن بن علي بن عفان','2339','818','0');
