INSERT INTO [t1714] VALUES (884,'علي بن أبي علي الأصبهاني حدث عنه القاضي أبو أحمد، ولم ينسبه','2345','818','0');
INSERT INTO [t1714] VALUES (885,'علي بن إسحاق بن إبراهيم الوزير أبو الحسن توفي سنة سبع وتسعين ومائتين , روى عن حسين المروزي، وعبد الجبار بن العلاء، ومحمد بن يزيد الآدمي، وأبي كريب , والحسن بن قزعة , وغيرهم، كان يقوم بحوائج أبي مسعود الرازي , يلقب بالوزير','2347','818','0');
INSERT INTO [t1714] VALUES (886,'علي بن سعيد أبو الحسن العسكري عسكر سرمرى: قدم أصبهان سنة ثمان وتسعين ومائتين وخرج منها إلى نيسابور , وتوفي بها سنة ثلاثمائة , كان من الحفاظ , صنف الشيوخ والمسند , حدثنا عنه القاضي والطبقة.','2351','818','0');
INSERT INTO [t1714] VALUES (887,'علي بن جعفر الملحمي الأصبهاني كتب عن العراقيين، كثير الحديث , حسن التصنيف , ثقة , أخو أبي حامد ومحمد.','2356','818','0');
