INSERT INTO [t1714] VALUES (891,'علي بن سهل بن محمد بن الأزهر أبو الحسن الصوفي أحد أعلام المتصوفة من أهل أصبهان كان من أصحاب جدي محمد بن يوسف البناء , ثم بلغ من شأنه أنه كان يكاتب الجنيد بن محمد وأقرانه , توفي سنة سبع وثلاثمائة , سمع من يونس بن حبيب.','2364','818','0');
INSERT INTO [t1714] VALUES (892,'علي بن خشنام بن معدان أبو الحسن روى عن الحسين بن معدان الفسوي، وأبي حاتم وغيرهم , شيخ ثقة.','2366','818','0');
INSERT INTO [t1714] VALUES (893,'علي بن الحسن بن علي المظالمي أبو الحسن ولي القضاء بأصبهان , يروي عن العراقيين والرازيين: أبي حاتم وطبقته، توفي سنة ست وثلاثين وثلاثمائة.','2370','818','0');
INSERT INTO [t1714] VALUES (894,'علي بن محمد بن المرزبان أبو الحسن الأسواري أحد القراء والعباد , صحب أبا عبد الله الخشوعي , توفي سنة ثلاث وثلاثين وثلاثمائة , سمع من أحمد بن مهدي , وأبي بكر بن النعمان , ولم يخرج حديثه , وذكر المتأخر أنه توفي سنة ثمان وعشرين وثلاثمائة في صفر.','2373','818','0');
