INSERT INTO [t1714] VALUES (896,'علي بن عبد الله بن إبراهيم الحبال أبو الحسن روى عن محمد بن أيوب الرازي.','2377','818','0');
INSERT INTO [t1714] VALUES (897,'علي بن محمد بن إبراهيم بن شاذويه البيع يعرف بعلي بن أبي عيسى , روى عن عبد الله بن محمد بن زكرياء، وأبي بكر البزار وغيرهما.','2378','818','0');
INSERT INTO [t1714] VALUES (898,'علي بن أحمد بن صالح أبو الحسن البصري قدم أصبهان , روى عن علي بن مبشر الواسطي، قال: سمعت أحمد بن سنان، يقول: كتب علي بن عاصم إلى أهله: تصدقوا عني اليوم بعشرة آلاف درهم فقد قبلت شهادتي على حديث رسول الله صلى الله عليه وسلم','2380','818','0');
INSERT INTO [t1714] VALUES (899,'علي بن محمد بن عمر بن أبان بن الوليد بن الوليد بن الحسن بن طيفور بن محمد أبو الحسن القاضي الطبري كان رأسا في الفقه والحديث والتصوف , ولي القضاء بأصبهان سنين , ثم خرج وتولى ببلاد الجبل , روى عن أبي خليفة , والفريابي , وعمران بن موسى , والفرهاذاني , والحسن','2381','818','0');
