INSERT INTO [t1714] VALUES (902,'علي بن محمد بن جعفر الطبري الحصيري قدم أصبهان , من الحفاظ , يروي عن محمد بن أيوب الرازي والفريابي وعبدان.','2388','818','0');
INSERT INTO [t1714] VALUES (903,'علي بن يعقوب بن إسحاق بن البختيار المؤذن أبو الحسن كان يؤذن في الجامع , توفي بعد الخمسين , روى عن الأخرم وأحمد بن علي بن الجارود وإبراهيم بن محمد بن الحسن وطبقتهم , والحسن بن هارون بن سليمان.','2390','818','0');
INSERT INTO [t1714] VALUES (904,'علي بن الفضل بن شهريار المعدل أبو الحسن التاجر صاحب أصول , ثقة , يروي عن محمد بن أيوب.','2392','818','0');
