INSERT INTO [t1714] VALUES (906,'علي بن عبد الله بن إبراهيم الحبال أبو الحسن يروي عن محمد بن أيوب.','2398','818','0');
INSERT INTO [t1714] VALUES (907,'علي بن محمد بن إبراهيم بن الحسن أبو الحسن الإمام المعدل المعروف بأبي الحسن بن أبي عبد الله بن متويه توفي سنة اثنتين وستين , يروي عن عبد الله بن محمد بن عمران وغيره.','2401','818','0');
