INSERT INTO [t1714] VALUES (929,'علي بن إسحاق بن ماقوله أبو الحسن من قرية سين , سمع الكثير، حدث عن أحمد بن موسى بن إسحاق الأنصاري.','2444','818','0');
INSERT INTO [t1714] VALUES (930,'عكرمة مولى عبد الله بن عباس كان كثير الجولان والتطواف في البلدان , قدم على الوالي بأصبهان , فأجازه بثلاثة آلاف درهم فيما حكاه أبو النضر عن عبد الحميد بن بهرام روى عنه من الأصبهانيين حبيب بن الزبير , وإبراهيم الصائغ , وعنبسة بن أبي حفص , وجعفر بن حميد ,','2445','818','0');
INSERT INTO [t1714] VALUES (931,'عمرو بن عبد الله أبو إسحاق الهمداني السبيعي الكوفي قدم أصبهان في اجتيازه إلى خراسان , من كبار تابعي أهل الكوفة , روى عن أربعة وثلاثين نفسا من الصحابة , وكان مولده لسنتين بقيتا من خلافة عثمان بن عفان رضي الله عنه , ووفاته سنة سبع , وقيل: ثمان , وقيل:','2450','818','0');
