INSERT INTO [t1714] VALUES (934,'عمرو بن صالح الثقفي أبو عثمان بصري الأصل , قدم أصبهان , روى عنه عامر بن إبراهيم , يروي عن البصريين: أبي حرة، وإسماعيل بن مسلم، وأشعث الحمراني، وابن عون.','2464','818','0');
INSERT INTO [t1714] VALUES (935,'عمرو بن علي بن بحر بن كنيز الصيرفي أبو حفص قدم أصبهان قد مات سنة ست عشرة وسنة أربع وعشرين وسنة ست وثلاثين , سئل عنه أبو زرعة الرازي فقال: ذاك من فرسان الحديث , حكى ابن مكرم بالبصرة قال: ما قدم علينا بعد علي بن المديني مثل عمرو بن علي , حدث عنه عفان بن','2468','818','0');
INSERT INTO [t1714] VALUES (936,'عمرو بن سعيد بن علي روى عن وهب بن جرير، وأبي عامر العقدي , توفي سنة تسع وستين ومائتين.','2472','818','0');
INSERT INTO [t1714] VALUES (937,'عمرو بن سليمان بن محمد بن الزبير القرشي البصري أبو عثمان كان أديبا من سمار المستعين بالله , قدم أصبهان , روى عنه ابن الجارود , روى عن أبي حذيفة النهدي , وعثمان بن عمر , وأبي الوليد , وعبد الله بن رجاء , وغيرهم من البصريين.','2475','818','0');
