INSERT INTO [t1714] VALUES (939,'عمرو بن سعيد بن سنان أبو عثمان العسكري حدث عن الحسن بن عمرو، وعياد بن صهيب، والمقرئ، ذكره المتأخر.','2480','818','0');
INSERT INTO [t1714] VALUES (940,'عمرو بن سعيد الجمال أبو حفص توفي سنة تسع وستين ومائتين , يروي عن الحسين بن حفص , وأبي داود , ومحمد بن الصلت , وعبد العزيز بن الخطاب يعرف بعمرويه بن سندة ثقة صدوق.','2482','818','0');
