INSERT INTO [t1714] VALUES (942,'عمرو بن نصير بن ثابت القرشي أبو نصر حدث بأصبهان، ثم خرج إلى طرسوس سنة ثمان وثمانين ومائتين , ومات بها.','2489','818','0');
INSERT INTO [t1714] VALUES (943,'عمرو بن سلم بن محمد بن الزبير البصري الأديب أبو عمرو كان من سمار المستعين.','2491','818','0');
