INSERT INTO [t1714] VALUES (948,'عمرو بن محمد بن إبراهيم أبو حفص الرفاعي ثقة مأمون: كتب عن أبي داود السجستاني، وأبي خالد القرشي , وأحمد بن عصام , وعبيد بن شريك , ومحمد بن سليمان الباغندي.','2507','818','0');
INSERT INTO [t1714] VALUES (949,'عمير بن سعيد النخعي قدم أصبهان , حكى عنه الحجاج بن أرطأة وروى عنه.','2511','818','0');
INSERT INTO [t1714] VALUES (950,'عامر بن ناجية الأصبهاني روى عنه صالح بن مهران أبو سفيان , كان من الفضلاء والعباد , لقي مالك بن مغول , وكان يتسمت لسمته وسمت أقرانه من العلماء المتنسكين.','2514','818','0');
INSERT INTO [t1714] VALUES (951,'عامر بن إبراهيم بن واقد بن عبد الله مولى أبي موسى الأشعري خرج إلى يعقوب القمي فكتب عامة كتبه , وأقام عنده في داره شهرا , سمع مالك بن أنس وحماد بن سلمة ومبارك بن فضالة , ويعقوب القمي , توفي سنة إحدى أو اثنتين ومائتين , كان يبيع الخشب , وقيل له: لم تكتب','2517','818','0');
INSERT INTO [t1714] VALUES (952,'عامر بن حمدويه العابد سكن مشتلة , صحب الثوري وروى عنه وسمع من ابن المبارك، وشعبة، وعامر بن يساف.','2520','818','0');
INSERT INTO [t1714] VALUES (953,'عامر بن عامر بن عثمان بن سالم بن مسلم بن عبد الله أبو يحيى , يعرف بحنك مولى نصر بن مالك الهمداني , صاحب غرائب وفوائد , يروي عن سليمان بن حرب، ومسلم بن إبراهيم، وأبي الوليد.','2524','818','0');
INSERT INTO [t1714] VALUES (954,'عامر بن أسيد بن واضح الواضحي أبو عمر من أهل سنبلان , إمام مسجد أيوب بن زياد , يروي عن وكيع , وابن عيينة , ويحيى القطان , وابن مهدي , والمعتمر.','2529','818','0');
INSERT INTO [t1714] VALUES (955,'عامر بن إبراهيم بن عامر أبو محمد المؤذن ثقة , توفي سنة ست وثلاثمائة , يروي عن أبيه، وعن إبراهيم بن محمد بن مروان العتيق.','2533','818','0');
INSERT INTO [t1714] VALUES (956,'عامر بن عقبة بن خالد بن عامر بن ثعلبة بن أبي برزة أبو الحسن ثقة صدوق يروي عن أبيه، وعن سلمة بن شبيب، وحميد بن مسعدة.','2537','818','0');
INSERT INTO [t1714] VALUES (957,'عامر بن أحمد بن محمد بن عامر أبو الحسن الشونيزي شافعي المذهب , توفي سنة إحدى وثلاثمائة , يروي عن أحمد بن عبد الجبار.','2539','818','0');
