INSERT INTO [t1714] VALUES (968,'عبد الله بن معاوية بن عبد الله بن جعفر بن أبي طالب رضي الله عنهم صاحب الميدان قدمها متغلبا عليها أيام مروان سنة ثمان وعشرين ومائة ومعه المنصور أبو جعفر إلى انقضاء سنة تسع وعشرين ومائة , ثم خرج منها هاربا إلى خراسان فحبسه أبو مسلم صاحب الدولة في سجنه ومات','2564','818','0');
INSERT INTO [t1714] VALUES (969,'عبد الله بن أبي بكرة الثقفي ولي أصبهان , ذكر النسابة , قال: دخلت سنة عشرين ومائة فولي عبد الله بن عبيد الله بن أبي بكرة.','2567','818','0');
INSERT INTO [t1714] VALUES (970,'عبد الله بن محمد بن علي بن عبد الله بن العباس بن عبد المطلب أبو جعفر المنصور وكان يقال له: عبد الله الطويل , قدمها مع عبد الله بن معاوية , وخرج منها إلى فارس , سمعت أبا بكر الجعابي يقول: كان المنصور يلقب في أيام أبيه بمدرك التراث , أتته البيعة بمكة','2569','818','0');
INSERT INTO [t1714] VALUES (971,'عبد الله بن يزيد الأصبهاني وهو الراوي عنه جرير بن عبد الحميد.','2576','818','0');
INSERT INTO [t1714] VALUES (972,'عبد الله بن أبي مريم الأموي ولي القضاء بأصبهان أيام الحجاج , وكانوا إخوة ثلاثة: عبد الله , وعبيد الله , ويزيد , من ولده محمد بن المغيرة بن سلم بن عبد الله , وعلي بن بشر أيضا من ولد عبيد الله , وهو علي بن عبد الملك بن عبيد الله بن أبي مريم , وقد حدث','2578','818','0');
INSERT INTO [t1714] VALUES (973,'عبد الله بن محمد بن أبي يحيى أخو محمد وأنيس , يعرف بسحبل , واسم أبي يحيى سمعان , روى عنه ابن أبي فديك، وقتيبة، يروي عن سعيد بن أبي هند، وأبي صالح السمان , وعوف بن الحارث بن الطفيل','2580','818','0');
INSERT INTO [t1714] VALUES (974,'عبد الله بن عبد الرحمن الأصبهاني روى عن عطاء.','2583','818','0');
